      .collection("products")
      .findOne({ _id: new ObjectId(id) });

    return NextResponse.json({
      message: "Product updated successfully",
      product: updatedProduct,
    });
  } catch (error) {
    console.error("Error updating product:", error);
    return NextResponse.json(
      { error: "Failed to update product" },
      { status: 500 },
    );
  }
}

// DELETE - Delete a product
export async function DELETE(request, { params }) {
  try {
    const client = await clientPromise;
    const db = client.db("farmfresh");
    const { id } = params;

    if (!ObjectId.isValid(id)) {
      return NextResponse.json(
        { error: "Invalid product ID" },
        { status: 400 },
      );
    }

    // First check if product exists
    const product = await db
      .collection("products")
      .findOne({ _id: new ObjectId(id) });

    if (!product) {
      return NextResponse.json({ error: "Product not found" }, { status: 404 });
    }

    // Optional: Check if there are any pending orders with this product
    const pendingOrders = await db.collection("orders").findOne({
      "items.productId": id,
      status: { $in: ["pending", "confirmed", "shipped"] },
    });

    if (pendingOrders) {
      return NextResponse.json(
        {
          error:
            "Cannot delete product with pending orders. Please wait for all orders to be completed or cancelled.",
          hasPendingOrders: true,
        },
        { status: 409 },
      );
    }

    // Delete the product
    const result = await db
      .collection("products")
      .deleteOne({ _id: new ObjectId(id) });

    if (result.deletedCount === 0) {
      return NextResponse.json(
        { error: "Failed to delete product" },
        { status: 500 },
      );
    }

    // Optional: Remove product from any user favorites
    await db
      .collection("users")
      .updateMany({ favorites: id }, { $pull: { favorites: id } });

    return NextResponse.json({
      message: "Product deleted successfully",
      deletedProductId: id,
    });
  } catch (error) {
    console.error("Error deleting product:", error);
    return NextResponse.json(
      { error: "Failed to delete product" },
      { status: 500 },
    );
  }
}

// Helper function to calculate real product performance metrics from orders and reviews
async function calculateProductPerformance(db, productId) {
  try {
    // Handle both string IDs and ObjectIds
    let productIdQuery = productId;
    if (ObjectId.isValid(productId)) {
      productIdQuery = new ObjectId(productId);
    }

    // Calculate sales metrics from completed orders
    const salesData = await db.collection("orders").aggregate([
      { 
        $match: { 
          status: "completed" 
        } 
      },
      { $unwind: "$items" },
      { 
        $match: { 
          $or: [
            { "items.productId": productId },
            { "items.productId": productIdQuery }
          ]
        } 
      },
      {
        $group: {
          _id: null,
          totalSales: { $sum: "$items.quantity" },
          totalRevenue: { $sum: { $multiply: ["$items.quantity", "$items.price"] } },
          totalOrders: { $sum: 1 }
        }
      }
    ]).toArray();

    // Calculate review metrics
    const reviewsData = await db.collection("reviews").aggregate([
      { 
        $match: { 
          $or: [
            { productId: productId },
            { productId: productIdQuery }
          ]
        } 
      },
      {
        $group: {
          _id: null,
          averageRating: { $avg: "$rating" },
          totalReviews: { $sum: 1 }
        }
      }
    ]).toArray();

    // Get recent orders count (last 30 days)
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const recentOrdersData = await db.collection("orders").aggregate([
      { 
        $match: { 
          createdAt: { $gte: thirtyDaysAgo.toISOString() },
          status: { $in: ["completed", "shipped", "delivered"] }
        } 
      },
      { $unwind: "$items" },
      { 
        $match: { 
          $or: [
            { "items.productId": productId },
            { "items.productId": productIdQuery }
          ]
        } 
      },
      {
        $group: {
          _id: null,
          recentSales: { $sum: "$items.quantity" },
          recentRevenue: { $sum: { $multiply: ["$items.quantity", "$items.price"] } }
        }
      }
    ]).toArray();

    const salesMetrics = salesData[0] || {};
    const reviewsMetrics = reviewsData[0] || {};
    const recentMetrics = recentOrdersData[0] || {};

    return {
      totalSales: salesMetrics.totalSales || 0,
      totalRevenue: salesMetrics.totalRevenue || 0,
      totalOrders: salesMetrics.totalOrders || 0,
      averageRating: reviewsMetrics.averageRating || 0,
      totalReviews: reviewsMetrics.totalReviews || 0,
      recentSales: recentMetrics.recentSales || 0,
      recentRevenue: recentMetrics.recentRevenue || 0,
      // Add some calculated metrics
      averageOrderValue: salesMetrics.totalOrders > 0 ? (salesMetrics.totalRevenue / salesMetrics.totalOrders) : 0,
      salesTrend: recentMetrics.recentSales > 0 ? "up" : "stable"
    };
  } catch (error) {
    console.error("Error calculating product performance:", error);
    return {
      totalSales: 0,
      totalRevenue: 0,
      totalOrders: 0,
      averageRating: 0,
      totalReviews: 0,
      recentSales: 0,
      recentRevenue: 0,
      averageOrderValue: 0,
      salesTrend: "stable"
    };
  }
}

"use client";

import Link from "next/link";
import Image from "next/image";
import { useState, useEffect } from "react";
import { useSearchParams } from "next/navigation";
import { useSession } from "next-auth/react";
import { useCart } from "@/contexts/CartContext";
import ProductCard from "@/components/ProductCard";
import StarRating from "@/components/StarRating";
import Footer from "@/components/Footer";

export default function ProductDetails() {
  const searchParams = useSearchParams();
  const productId = searchParams.get("id");
  const viewMode = searchParams.get("view"); // Check for view parameter
  const { data: session } = useSession();
  const { addToCart } = useCart();

  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState("description");
  const [reviewsPage, setReviewsPage] = useState(1);
  const [hasMoreReviews, setHasMoreReviews] = useState(false);
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isOwner, setIsOwner] = useState(false);

  // Review form state
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({ rating: 5, comment: "" });
  const [isSubmittingReview, setIsSubmittingReview] = useState(false);

  // Farmer-specific states
  const [editMode, setEditMode] = useState(false);
  const [stockUpdate, setStockUpdate] = useState("");
  const [priceUpdate, setPriceUpdate] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  useEffect(() => {
    if (productId) {
      fetchProductDetails();
      fetchReviews();
    }
  }, [productId]);

  const fetchProductDetails = async () => {
    console.log(" FETCHING PRODUCT DETAILS - START");
    console.log("Product ID from URL:", productId);
    console.log("View Mode:", viewMode);

    try {
      const response = await fetch(`/api/products/${productId}`);
      console.log("API Response Status:", response.status);

      if (response.ok) {
        const data = await response.json();
        console.log(" RAW API RESPONSE:", data);
        console.log(" PRODUCT DATA:", data.product);
        console.log(" PRODUCT IMAGES:", data.product?.images);
        console.log(" IMAGES TYPE:", typeof data.product?.images);
        console.log(
          " IMAGES ARRAY CHECK:",
          Array.isArray(data.product?.images),
        );

        setProduct(data.product);
        setRelatedProducts(data.relatedProducts);

        // Check ownership only if not forcing customer view
        if (viewMode !== "customer") {
          setIsOwner(checkOwnership(data.product));
        } else {
          setIsOwner(false); // Force customer view
        }
      } else {
        console.error(
          "API Response Error:",
          response.status,
          response.statusText,
        );
        const errorData = await response.text();
        console.error("Error Response Body:", errorData);
      }
    } catch (error) {
      console.error("Error fetching product details:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchReviews = async (page = 1, append = false) => {
    try {
      const response = await fetch(
        `/api/products/${productId}/reviews?page=${page}`,
      );
      if (response.ok) {
        const data = await response.json();

        if (append) {
          setReviews((prev) => [...prev, ...data.reviews]);
        } else {
          setReviews(data.reviews);
        }
        setHasMoreReviews(data.hasMore);
        setReviewsPage(page);
      }
    } catch (error) {
      console.error("Error fetching reviews:", error);
    }
  };

  const handleAddToCart = async () => {
    if (!session?.user) {
      // Redirect to login if not authenticated
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Use consistent user ID format
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;

      const response = await fetch("/api/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          productId: productId,
          quantity: quantity,
          userId: userId,
        }),
      });

      if (response.ok) {
        alert("Product added to cart successfully!");
      } else {
        const errorData = await response.json();
        alert(errorData.error || "Failed to add to cart");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add product to cart. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleBuyNow = async () => {
    if (!session?.user) {
      // Redirect to login if not authenticated
      window.location.href = "/login";
      return;
    }

    // First add to cart, then navigate to payment
    setIsAddingToCart(true);
    try {
      // Use consistent user ID format
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;

      const response = await fetch("/api/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
