          {/* Show farmer view only if isOwner is true AND viewMode is not "customer" */}
          {isOwner && viewMode !== "customer" ? (
            <>
              {/* Farmer Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/manage" className="hover:text-primary-600">
                      <i className="fas fa-tachometer-alt mr-1"></i>
                      Dashboard
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name} - Management
                    </span>
                  </li>
                </ol>
              </nav>

              {/* Add Customer View Notice */}
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <i className="fas fa-info-circle text-blue-600 dark:text-blue-400 mr-2"></i>
                    <span className="text-blue-800 dark:text-blue-200">
                      You are viewing this as the product owner.
                    </span>
                  </div>
                  <Link
                    href={`/details?id=${productId}&view=customer`}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                  >
                    <i className="fas fa-eye mr-1"></i>
                    View as Customer
                  </Link>
                </div>
              </div>

              {/* Farmer Product Management Header */}
              <div className="bg-gradient-to-r from-green-600 to-emerald-600 rounded-2xl shadow-lg p-8 mb-8 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-3xl font-bold mb-2">
                      Product Management
                    </h1>
                    <p className="text-green-100">
                      Manage your product listing and inventory
                    </p>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-green-100 mb-1">
                      Product Status
                    </div>
                    <div
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                        product.status === "active"
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      <i
                        className={`fas ${product.status === "active" ? "fa-check-circle" : "fa-pause-circle"} mr-1`}
                      ></i>
                      {product.status === "active" ? "Active" : "Inactive"}
                    </div>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Product Images & Info */}
                <div className="lg:col-span-2 space-y-6">
                  {/* Image Management */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Images ({product.images?.length || 0})
                      </h3>
                      <button className="text-blue-600 hover:text-blue-700 text-sm font-medium">
                        <i className="fas fa-plus mr-1"></i>
                        Add Images
                      </button>
                    </div>

                    {/* Current Images Display */}
                    {(() => {
                      // Debug logging for farmer view
                      console.log("=== FARMER VIEW IMAGE DEBUG ===");
                      console.log("Product object:", product);
                      console.log("Product images array:", product.images);
                      console.log("Product single image:", product.image);
                      console.log("Images length:", product.images?.length);
                      console.log("Has single image:", !!product.image);

                      // Combine both image sources
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      console.log("Combined images:", allImages);
                      console.log("Total image count:", allImages.length);
                      console.log("=== END DEBUG ===");

                      return null;
                    })()}

                    {(() => {
                      // Combine both image sources
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      return allImages.length > 0 ? (
                        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                          {allImages.map((image, index) => {
                            console.log(
                              `Rendering farmer view image ${index + 1}:`,
                              image,
                            );
                            return (
                              <div key={index} className="relative group">
                                <div className="aspect-square bg-gray-100 dark:bg-gray-700 rounded-lg overflow-hidden">
                                  <Image
                                    src={image}
                                    alt={`${product.name} ${index + 1}`}
                                    width={200}
                                    height={200}
                                    className="w-full h-full object-cover transition-transform group-hover:scale-105"
                                    onError={(e) => {
                                      console.log(
                                        "Farmer view image load ERROR:",
                                        {
                                          index,
                                          originalSrc: image,
                                          errorEvent: e,
                                        },
                                      );
                                      e.target.src =
                                        "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=200&h=200&fit=crop";
                                    }}
                                    onLoad={() => {
                                      console.log(
                                        `Farmer view image ${index + 1} loaded successfully:`,
                                        image,
                                      );
                                    }}
                                  />
                                </div>
                                <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
                                  <button className="bg-red-500 hover:bg-red-600 text-white p-1 rounded text-xs">
                                    <i className="fas fa-trash"></i>
                                  </button>
                                </div>
                                {index === 0 && (
                                  <div className="absolute bottom-2 left-2">
                                    <span className="bg-blue-500 text-white px-2 py-1 rounded text-xs font-medium">
                                      Primary
                                    </span>
                                  </div>
                                )}
                                <div className="absolute bottom-2 right-2">
                                  <span className="bg-black bg-opacity-50 text-white px-1 py-0.5 rounded text-xs">
                                    {index + 1}
                                  </span>
                                </div>
                              </div>
                            );
                          })}

                          {/* Add Image Placeholder */}
                          <div className="aspect-square border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg flex items-center justify-center hover:border-blue-500 hover:bg-blue-50 dark:hover:bg-blue-900/20 transition cursor-pointer">
                            <div className="text-center">
                              <i className="fas fa-plus text-2xl text-gray-400 mb-2"></i>
                              <p className="text-sm text-gray-500">Add Image</p>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <div className="w-16 h-16 bg-gray-100 dark:bg-gray-700 rounded-full flex items-center justify-center mx-auto mb-4">
                            <i className="fas fa-image text-2xl text-gray-400"></i>
                          </div>
                          <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                            No Images Uploaded
                          </h4>
                          <p className="text-gray-600 dark:text-gray-400 mb-4">
                            Add product images to make your listing more
                            attractive
                          </p>
                          <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition">
                            <i className="fas fa-upload mr-1"></i>
                            Upload Images
                          </button>
                        </div>
                      );
                    })()}

                    {/* Enhanced Debug info for farmer view */}
                    <div className="mt-4 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-3">
                      <p className="text-xs font-semibold text-yellow-800 dark:text-yellow-200 mb-1">
                         DEBUG - Farmer View Images:
                      </p>
                      <div className="text-xs text-yellow-700 dark:text-yellow-300 space-y-1">
                        <p>
                          <strong>Images Array Length:</strong>{" "}
                          {product.images?.length || 0}
                        </p>
                        <p>
                          <strong>Single Image Field:</strong>{" "}
                          {product.image ? "Yes" : "No"}
                        </p>
                        <p>
                          <strong>Total Images:</strong>{" "}
                          {(() => {
                            const allImages = [];
                            if (product.image) allImages.push(product.image);
                            if (product.images && product.images.length > 0)
                              allImages.push(...product.images);
                            return allImages.length;
                          })()}
                        </p>
                        <p>
                          <strong>Product ID:</strong> {productId}
                        </p>
                        {product.image && (
                          <div className="ml-2 p-1 bg-yellow-100 dark:bg-yellow-900 rounded text-xs break-all">
                            <strong>Single Image:</strong>{" "}
                            {product.image.substring(0, 50)}...
                          </div>
                        )}
                        {product.images?.map((img, idx) => (
                          <div
                            key={idx}
                            className="ml-2 p-1 bg-yellow-100 dark:bg-yellow-900 rounded text-xs break-all"
                          >
                            <strong>Array Image {idx + 1}:</strong> {img}
                          </div>
                        ))}
                        <p>
                          <strong>API Response Check:</strong> Open browser
                          console for detailed logs
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Product Information */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Information
                      </h3>
                      <Link
                        href={`/create?edit=${productId}`}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-edit mr-1"></i>
                        Edit Details
                      </Link>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h4 className="text-2xl font-bold text-gray-900 dark:text-white">
                          {product.name}
                        </h4>
                        <p className="text-gray-600 dark:text-gray-400">
                          {product.category}
                        </p>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-1">
                            Current Price
                          </div>
                          <div className="text-2xl font-bold text-green-600">
                            {product.price}
                          </div>
                          <div className="text-sm text-gray-500">
                            per {product.unit || "kg"}
                          </div>
                        </div>

                        <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-1">
                            Current Stock
                          </div>
                          <div className="text-2xl font-bold text-blue-600">
                            {product.stock}
                          </div>
                          <div className="text-sm text-gray-500">
                            {product.unit || "kg"} available
                          </div>
                        </div>
                      </div>

                      <div>
                        <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                          Description
                        </div>
                        <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
                          {product.description || "No description available"}
                        </p>
                      </div>

                      {product.features && (
                        <div>
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                            Features
                          </div>
                          <div className="flex flex-wrap gap-2">
                            {product.features.map((feature, index) => (
                              <span
                                key={index}
                                className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded text-sm"
                              >
                                {feature}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Quick Actions */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
                      Quick Updates
                    </h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      {/* Stock Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Stock
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={stockUpdate}
                            onChange={(e) => setStockUpdate(e.target.value)}
                            placeholder={product.stock.toString()}
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 dark:bg-gray-700 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={!stockUpdate || isUpdating}
                            className="bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            {isUpdating ? "Updating..." : "Update"}
                          </button>
                        </div>
                      </div>

                      {/* Price Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Price ()
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={priceUpdate}
                            onChange={(e) => setPriceUpdate(e.target.value)}
                            placeholder={product.price.toString()}
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 dark:bg-gray-700 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={!priceUpdate || isUpdating}
                            className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            {isUpdating ? "Updating..." : "Update"}
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Sidebar - Analytics & Actions */}
                <div className="space-y-6">
                  {/* Performance Stats */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Performance
                    </h3>

                    <div className="space-y-4">
                      <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-blue-600">
                          {product.purchaseCount || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Sales
                        </div>
                      </div>

                      <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-yellow-600">
                          {product.averageRating
                            ? product.averageRating.toFixed(1)
                            : "0.0"}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Average Rating
                        </div>
                      </div>

                      <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-green-600">
                          {reviews?.length || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Reviews
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Actions
                    </h3>

                    <div className="space-y-3">
                      <Link
                        href={`/create?edit=${productId}`}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-edit mr-2"></i>
                        Edit Product
                      </Link>

                      <Link
                        href={`/details?id=${productId}&view=customer`}
                        className="w-full bg-gray-600 hover:bg-gray-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-eye mr-2"></i>
                        View as Customer
                      </Link>

                      <button
                        onClick={handleToggleStatus}
                        disabled={isUpdating}
                        className={`w-full py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          product.status === "active"
                            ? "bg-yellow-600 hover:bg-yellow-700 text-white"
                            : "bg-green-600 hover:bg-green-700 text-white"
                        } ${isUpdating ? "opacity-50 cursor-not-allowed" : ""}`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i
                            className={`fas ${product.status === "active" ? "fa-pause" : "fa-play"} mr-2`}
                          ></i>
                        )}
                        {isUpdating
                          ? "Updating..."
                          : product.status === "active"
                            ? "Deactivate"
                            : "Activate"}
                      </button>

                      <button
                        onClick={handleDeleteProduct}
                        disabled={isUpdating}
                        className={`w-full bg-red-600 hover:bg-red-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          isUpdating ? "opacity-50 cursor-not-allowed" : ""
                        }`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i className="fas fa-trash mr-2"></i>
                        )}
                        {isUpdating ? "Deleting..." : "Delete Product"}
                      </button>
                    </div>
                  </div>

                  {/* Recent Orders */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Recent Orders
                    </h3>

                    <div className="space-y-3 text-sm">
                      <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                        <i className="fas fa-shopping-bag text-2xl mb-2"></i>
                        <p>No recent orders</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            /* Regular Customer View */
            <>
              {/* Regular breadcrumb and customer interface here */}
              {/* Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/" className="hover:text-primary-600">
                      Home
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <Link href="/products" className="hover:text-primary-600">
                      Products
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name}
                    </span>
                  </li>
                </ol>
              </nav>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                {/* Product Images */}
                <div className="space-y-4">
                  <div className="aspect-square bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg">
                    {(() => {
                      // Combine both image sources for customer view
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      console.log(
                        "Customer view - Combined images:",
                        allImages,
                      );
                      console.log(
                        "Customer view - Selected image index:",
                        selectedImage,
                      );
                      console.log(
                        "Customer view - Displaying image:",
                        allImages[selectedImage],
                      );

                      return (
                        <Image
                          src={
                            allImages[selectedImage] ||
                            "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=600&h=600&fit=crop"
                          }
                          alt={product.name}
                          width={600}
                          height={600}
                          className="w-full h-full object-cover"
                          onError={(e) => {
                            console.log(
                              "Customer view main image load ERROR:",
                              e.target.src,
                            );
                            e.target.src =
                              "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=600&h=600&fit=crop";
                          }}
                          onLoad={(e) => {
                            console.log(
                              "Customer view main image loaded successfully:",
                              e.target.src,
                            );
                          }}
                        />
                      );
                    })()}
                  </div>

                  {/* Thumbnail Images */}
                  {(() => {
                    // Combine both image sources for thumbnails
                    const allImages = [];
                    if (product.image) {
                      allImages.push(product.image);
                    }
                    if (product.images && product.images.length > 0) {
                      allImages.push(...product.images);
                    }

                    return allImages.length > 1 ? (
                      <div className="grid grid-cols-5 gap-2">
                        {allImages.map((image, index) => (
                          <button
                            key={index}
                            onClick={() => setSelectedImage(index)}
                            className={`aspect-square bg-white dark:bg-gray-800 rounded-lg overflow-hidden border-2 transition ${
                              selectedImage === index
                                ? "border-primary-500"
                                : "border-transparent hover:border-primary-300"
                            }`}
                          >
                            <Image
                              src={image}
                              alt={`${product.name} ${index + 1}`}
                              width={100}
                              height={100}
                              className="w-full h-full object-cover"
                              onError={(e) => {
                                console.log(
                                  "Customer view thumbnail load ERROR:",
                                  e.target.src,
                                );
                                e.target.src =
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=100&h=100&fit=crop";
                              }}
                              onLoad={() => {
                                console.log(
                                  `Customer view thumbnail ${index + 1} loaded successfully`,
                                );
                              }}
                            />
                          </button>
                        ))}
                      </div>
                    ) : null;
                  })()}
                </div>

                {/* Product Info */}
                <div className="space-y-6">
                  {/* Category and Features */}
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-3 py-1 rounded-full text-sm font-medium">
                      {product.category}
                    </span>
                    {product.isOrganic && (
                      <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                        Organic
                      </span>
                    )}
                    {product.isFresh && (
                      <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                        Fresh
                      </span>
                    )}
                  </div>

                  {/* Product Name and Farmer */}
                  <div>
                    <div className="flex items-center space-x-2 mb-2">
                      {product.isOrganic && (
                        <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                          Organic
                        </span>
                      )}
                      {product.isFresh && (
                        <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                          Fresh
                        </span>
                      )}
                    </div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                      {product.name}
                    </h1>
                    <p className="text-lg text-gray-600 dark:text-gray-400">
                      Produced by{" "}
                      <span className="font-semibold text-primary-600 dark:text-primary-400">
                        {product.farmer?.farmName ||
                          product.farmer?.name ||
                          "Unknown Farm"}
                      </span>
                    </p>
                  </div>

                  {/* Rating and Reviews */}
                  <div className="flex items-center space-x-4">
                    <StarRating
                      rating={product.averageRating || 0}
                      showValue={true}
                    />
                    <span className="text-gray-500 dark:text-gray-400">
                      ({product.reviewCount || product.totalReviews || 0}{" "}
                      reviews)
                    </span>
                    <button
                      onClick={() => setActiveTab("reviews")}
                      className="text-primary-600 dark:text-primary-400 hover:underline"
                    >
                      Write a review
                    </button>
                  </div>

                  {/* Price and Stock */}
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-6">
                    <div className="flex items-center justify-between mb-4">
                      <div>
                        <span className="text-3xl font-bold text-primary-600 dark:text-primary-400">
                          {product.price}
                        </span>
                        <span className="text-lg text-gray-500 dark:text-gray-400">
                          /{product.unit || "kg"}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500 dark:text-gray-400">
                          Available Stock
                        </p>
                        <p className="text-lg font-semibold text-gray-900 dark:text-white">
                          {product.stock} {product.unit || "kg"}
                        </p>
                      </div>
                    </div>

                    {/* Location */}
                    <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                      <i className="fas fa-map-marker-alt mr-2"></i>
                      <span>
                        {product.farmer?.location || "Location not specified"}
                      </span>
                    </div>
                  </div>

                  {/* Quantity Selection */}
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Quantity ({product.unit || "kg"})
                      </label>
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={() => setQuantity(Math.max(1, quantity - 1))}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-minus text-sm"></i>
                        </button>
                        <input
                          type="number"
                          value={quantity}
                          onChange={(e) =>
                            setQuantity(
                              Math.max(1, parseInt(e.target.value) || 1),
                            )
                          }
                          min="1"
                          max={product.stock}
                          className="w-20 text-center py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                        />
                        <button
                          onClick={() =>
                            setQuantity(Math.min(product.stock, quantity + 1))
                          }
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-plus text-sm"></i>
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    <button
                      onClick={handleBuyNow}
                      disabled={product.stock <= 0}
                      className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                    >
                      <i className="fas fa-bolt mr-2"></i>
                      Buy Now
                    </button>
                    <button
                      onClick={handleAddToCart}
                      disabled={isAddingToCart || product.stock <= 0}
                      className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                    >
                      <i className="fas fa-shopping-cart mr-2"></i>
                      {isAddingToCart ? "Adding..." : "Add to Cart"}
                    </button>
                    <button
                      onClick={handleFavoriteToggle}
                      className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                    >
                      <i
                        className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                      ></i>
                      Add to Favorite
                    </button>
                  </div>

                  {/* Farmer Contact */}
                  <div className="bg-primary-50 dark:bg-primary-900 rounded-xl p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-800 flex items-center justify-center">
                        <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                      </div>
                      <div>
                        <h4 className="font-semibold text-gray-900 dark:text-white">
                          {product.farmer?.name || "Unknown Farmer"}
                        </h4>
                        <p className="text-sm text-gray-600 dark:text-gray-400">
                          Farmer since{" "}
                          {new Date().getFullYear() -
                            (product.farmer?.experience || 5)}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Tabs Section */}
              <div className="mt-16">
                <div className="border-b border-gray-200 dark:border-gray-700">
                  <nav className="flex space-x-8">
                    {[
                      "description",
                      "nutrition",
                      "storage",
                      "reviews",
                      "farmer",
                    ].map((tab) => (
                      <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`py-4 px-1 border-b-2 font-medium text-sm transition ${
                          activeTab === tab
                            ? "border-primary-500 text-primary-600 dark:text-primary-400"
                            : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300"
                        }`}
                      >
                        {tab === "nutrition"
                          ? "Nutrition"
                          : tab === "storage"
                            ? "Storage"
                            : tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    ))}
                  </nav>
                </div>

                <div className="py-8">
                  {/* Description Tab */}
                  {activeTab === "description" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-4">
                        Product Description
                      </h3>
                      <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                        {product.description ||
                          "No description available for this product."}
                      </p>

                      {product.features && product.features.length > 0 && (
                        <div className="mt-6">
                          <h4 className="text-lg font-medium mb-3">Features</h4>
                          <ul className="list-disc list-inside space-y-1">
                            {product.features.map((feature, index) => (
                              <li
                                key={index}
                                className="text-gray-600 dark:text-gray-400"
                              >
                                {feature}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Nutrition Tab */}
                  {activeTab === "nutrition" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Nutritional Information
                      </h3>

                      {product.nutritionalInformation ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
                          <div className="px-6 py-4 bg-gray-50 dark:bg-gray-700 border-b border-gray-200 dark:border-gray-600">
                            <h4 className="text-lg font-medium text-gray-900 dark:text-white">
                              Per{" "}
                              {product.nutritionalInformation.servingSize ||
                                "100g"}{" "}
                              serving
                            </h4>
                          </div>

                          <div className="p-6">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                              {/* Main nutrients */}
                              <div className="space-y-3">
                                {product.nutritionalInformation.calories && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Calories
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.calories}{" "}
                                      kcal
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.protein && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Protein</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.protein}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation
                                  .carbohydrates && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Carbohydrates
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {
                                        product.nutritionalInformation
                                          .carbohydrates
                                      }
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.fiber && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Fiber</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.fiber}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.fat && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Fat</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.fat}
                                    </span>
                                  </div>
                                )}
                              </div>

                              {/* Vitamins and Minerals */}
                              <div className="space-y-3">
                                {product.nutritionalInformation.iron && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Iron</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.iron}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.vitaminB1 && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Vitamin B1
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.vitaminB1}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.vitaminC && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Vitamin C
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.vitaminC}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.gluten && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium text-orange-600">
                                      Allergen Info
                                    </span>
                                    <span className="text-orange-600 dark:text-orange-400">
                                      {product.nutritionalInformation.gluten}
                                    </span>
                                  </div>
                                )}
                              </div>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <p className="text-gray-600 dark:text-gray-400">
                            Nutritional information is not available for this
                            product.
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Storage Instructions Tab */}
                  {activeTab === "storage" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Storage Instructions
                      </h3>

                      {product.storageInstructions ? (
                        <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-6">
                          <div className="flex items-start gap-3">
                            <div className="flex-shrink-0 mt-1">
                              <i className="fas fa-snowflake text-blue-600 dark:text-blue-400 text-xl"></i>
                            </div>
                            <div className="flex-1">
                              <h4 className="text-lg font-medium text-blue-900 dark:text-blue-100 mb-3">
                                How to Store This Product
                              </h4>
                              <p className="text-blue-800 dark:text-blue-200 leading-relaxed">
                                {product.storageInstructions}
                              </p>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <p className="text-gray-600 dark:text-gray-400">
                            Storage instructions are not available for this
                            product.
                          </p>
                        </div>
                      )}

                      {/* General storage tips */}
                      <div className="mt-8">
                        <h4 className="text-lg font-medium mb-4">
                          General Storage Tips
                        </h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-thermometer-half text-green-600"></i>
                              <span className="font-medium">Temperature</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Keep at recommended temperature to maintain
                              freshness
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-tint text-blue-600"></i>
                              <span className="font-medium">Moisture</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Control humidity levels to prevent spoilage
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-sun text-yellow-600"></i>
                              <span className="font-medium">Light</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Store away from direct sunlight when specified
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-clock text-purple-600"></i>
                              <span className="font-medium">Freshness</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Consume within recommended timeframe
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Reviews Tab */}
                  {activeTab === "reviews" && (
                    <div>
                      <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          Customer Reviews
                        </h2>
                        {session && (
                          <button
                            onClick={() => setShowReviewForm(true)}
                            className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            Write a Review
                          </button>
                        )}
                      </div>

                      {/* Review Summary */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-6 mb-8">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                          <div>
                            <div className="flex items-center space-x-2 mb-4">
                              <span className="text-4xl font-bold text-gray-900 dark:text-white">
                                {(() => {
                                  // Use reviews from API call, which should contain the product's reviews array
                                  const allReviews = reviews || [];
                                  if (allReviews.length === 0) {
                                    // Fallback to product's original rating if no reviews from API
                                    return (
                                      product.averageRating ||
                                      product.rating ||
                                      0
                                    ).toFixed(1);
                                  }
                                  const totalRating = allReviews.reduce(
                                    (sum, review) =>
                                      sum + Number(review.rating || 0),
                                    0,
                                  );
                                  return (
                                    totalRating / allReviews.length
                                  ).toFixed(1);
                                })()}
                              </span>
                              <div>
                                <div className="flex text-yellow-400 mb-1">
                                  {[...Array(5)].map((_, i) => {
                                    // Calculate average rating from API reviews or fallback to product rating
                                    const allReviews = reviews || [];
                                    let avgRating = 0;
                                    if (allReviews.length > 0) {
                                      const totalRating = allReviews.reduce(
                                        (sum, review) =>
                                          sum + Number(review.rating || 0),
                                        0,
                                      );
                                      avgRating =
                                        totalRating / allReviews.length;
                                    } else {
                                      avgRating =
                                        product.averageRating ||
                                        product.rating ||
                                        0;
                                    }

                                    console.log(
                                      "Star rendering - Average rating:",
                                      avgRating,
                                      "Star index:",
                                      i,
                                    );

                                    return (
                                      <i
                                        key={i}
                                        className={`fas fa-star ${
                                          i < Math.round(avgRating)
                                            ? "text-yellow-400"
                                            : "text-gray-300"
                                        }`}
                                      ></i>
                                    );
                                  })}
                                </div>
                                <p className="text-sm text-gray-500 dark:text-gray-400">
                                  Based on{" "}
                                  {reviews?.length ||
                                    product.totalReviews ||
                                    product.reviewCount ||
                                    0}{" "}
                                  reviews
                                </p>
                              </div>
                            </div>
                          </div>
                          <div className="space-y-2">
                            {/* Dynamic Rating breakdown using API reviews */}
                            {[5, 4, 3, 2, 1].map((star) => {
                              const allReviews = reviews || [];
                              const totalReviews = allReviews.length;

                              // Count reviews that fall within the star range (e.g., 4.0-4.9 for 4 stars)
                              const starCount = allReviews.filter((review) => {
                                const rating = Number(review.rating || 0);
                                return rating >= star && rating < star + 1;
                              }).length;

                              const percentage =
                                totalReviews > 0
                                  ? (starCount / totalReviews) * 100
                                  : 0;

                              return (
                                <div
                                  key={star}
                                  className="flex items-center space-x-2"
                                >
                                  <span className="text-sm w-8">{star}</span>
                                  <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                                    <div
                                      className="bg-yellow-400 h-2 rounded-full transition-all duration-300"
                                      style={{ width: `${percentage}%` }}
                                    ></div>
                                  </div>
                                  <span className="text-sm text-gray-500 dark:text-gray-400 w-8">
                                    {starCount}
                                  </span>
                                </div>
                              );
                            })}
                          </div>
                        </div>
                      </div>

                      {/* Review Form Modal */}
                      {showReviewForm && (
                        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                          <div className="bg-white dark:bg-gray-800 rounded-lg max-w-md w-full p-6">
                            <h4 className="text-lg font-semibold mb-4">
                              Write a Review
                            </h4>
                            <form onSubmit={handleSubmitReview}>
                              <div className="mb-4">
                                <label className="block text-sm font-medium mb-2">
                                  Rating
                                </label>
                                <div className="flex gap-1">
                                  {[1, 2, 3, 4, 5].map((star) => (
                                    <button
                                      key={star}
                                      type="button"
                                      onClick={() =>
                                        setReviewForm({
                                          ...reviewForm,
                                          rating: star,
                                        })
                                      }
                                      className={`text-2xl transition-colors ${
                                        star <= reviewForm.rating
                                          ? "text-yellow-400 hover:text-yellow-500"
                                          : "text-gray-300 hover:text-gray-400"
                                      }`}
                                    >

                                    </button>
                                  ))}
                                </div>
                              </div>

                              <div className="mb-4">
                                <label className="block text-sm font-medium mb-2">
                                  Comment
                                </label>
                                <textarea
                                  value={reviewForm.comment}
                                  onChange={(e) =>
                                    setReviewForm({
                                      ...reviewForm,
                                      comment: e.target.value,
                                    })
                                  }
                                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 dark:bg-gray-700"
                                  rows="4"
                                  placeholder="Share your experience with this product..."
                                  required
                                />
                              </div>

                              <div className="flex gap-3">
                                <button
                                  type="button"
                                  onClick={() => setShowReviewForm(false)}
                                  className="flex-1 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 py-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition"
                                >
                                  Cancel
                                </button>
                                <button
                                  type="submit"
                                  disabled={
                                    isSubmittingReview ||
                                    !reviewForm.comment.trim()
                                  }
                                  className="flex-1 bg-primary-600 hover:bg-primary-700 text-white py-2 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed transition"
                                >
                                  {isSubmittingReview
                                    ? "Submitting..."
                                    : "Submit Review"}
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      )}

                      {/* Individual Reviews */}
                      <div className="space-y-6">
                        {/* Only display reviews from API call */}
                        {(() => {
                          const allReviews = reviews || [];

                          if (allReviews.length === 0) {
                            return (
                              <div className="text-center py-12">
                                <div className="w-16 h-16 bg-gray-100 dark:bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4">
                                  <i className="fas fa-comment-alt text-2xl text-gray-400"></i>
                                </div>
                                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                                  No reviews yet
                                </h3>
                                <p className="text-gray-600 dark:text-gray-400 mb-6">
                                  Be the first to share your thoughts about this
                                  product.
                                </p>
                                {session && (
                                  <button
                                    onClick={() => setShowReviewForm(true)}
                                    className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition"
                                  >
                                    Write the First Review
                                  </button>
                                )}
                              </div>
                            );
                          }

                          return allReviews.map((review, index) => (
                            <div
                              key={review._id || index}
                              className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-sm"
                            >
                              <div className="flex items-start space-x-4">
                                <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-900 flex items-center justify-center">
                                  <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                                </div>
                                <div className="flex-1">
                                  <div className="flex items-center justify-between mb-2">
                                    <div>
                                      <div className="flex items-center gap-2">
                                        <h4 className="font-semibold text-gray-900 dark:text-white">
                                          {review.userName || "Anonymous"}
                                        </h4>
                                        {review.isCurrentUser && (
                                          <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-2 py-1 rounded text-xs">
                                            Your Review
                                          </span>
                                        )}
                                        <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded text-xs">
                                          Verified Purchase
                                        </span>
                                      </div>
                                      <div className="flex items-center space-x-2">
                                        <div className="flex text-yellow-400 text-sm">
                                          {[...Array(5)].map((_, i) => {
                                            const reviewRating = Number(
                                              review.rating || 0,
                                            );
                                            console.log(
                                              `Review by ${review.userName} - Rating: ${reviewRating}, Star ${i + 1}: ${i < Math.floor(reviewRating) ? "filled" : "empty"}`,
                                            );

                                            return (
                                              <i
                                                key={i}
                                                className={`fas fa-star ${
                                                  i < Math.floor(reviewRating)
                                                    ? "text-yellow-400"
                                                    : "text-gray-300"
                                                }`}
                                              ></i>
                                            );
                                          })}
                                        </div>
                                        <span className="text-sm text-gray-500 dark:text-gray-400">
                                          {review.createdAt
                                            ? new Date(
                                                review.createdAt,
                                              ).toLocaleDateString()
                                            : "Unknown date"}
                                        </span>
                                      </div>
                                    </div>
                                    <button className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition">
                                      <i className="fas fa-ellipsis-v"></i>
                                    </button>
                                  </div>
                                  <p className="text-gray-700 dark:text-gray-300 mb-3 leading-relaxed">
                                    {review.comment}
                                  </p>
                                  <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
                                    <button className="hover:text-primary-600 dark:hover:text-primary-400 transition">
                                      <i className="far fa-thumbs-up mr-1"></i>
                                      Helpful (
                                      {Math.floor(Math.random() * 15) + 1})
                                    </button>
                                    <button className="hover:text-primary-600 dark:hover:text-primary-400 transition">
                                      Reply
                                    </button>
                                    {review.isCurrentUser && (
                                      <button className="hover:text-red-600 dark:hover:text-red-400 transition">
                                        Edit
                                      </button>
                                    )}
                                  </div>
                                </div>
                              </div>
                            </div>
                          ));
                        })()}

                        {/* Load More Reviews Button */}
                        {hasMoreReviews && (
                          <div className="text-center mt-8">
                            <button
                              onClick={loadMoreReviews}
                              className="bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white px-6 py-3 rounded-lg font-medium transition"
                            >
                              Load More Reviews
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Farmer Info Tab */}
                  {activeTab === "farmer" && (
                    <div>
                      <h3 className="text-xl font-semibold mb-6">
                        About the Farmer
                      </h3>
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow-lg">
                        <div className="flex items-start gap-4">
                          <div className="w-16 h-16 bg-primary-100 dark:bg-primary-900 rounded-full flex items-center justify-center">
                            <i className="fas fa-user text-2xl text-primary-600 dark:text-primary-400"></i>
                          </div>
                          <div className="flex-1">
                            <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-1">
                              {product.farmer?.name || "Unknown Farmer"}
                            </h4>
                            <p className="text-gray-600 dark:text-gray-400 mb-3">
                              <i className="fas fa-map-marker-alt mr-2"></i>
                              {product.farmer?.location ||
                                "Location not specified"}
                            </p>
                            <p className="text-gray-600 dark:text-gray-400">
                              {product.farmer?.bio ||
                                "No farmer information available."}
                            </p>

                            {product.farmer?.experience && (
                              <div className="mt-4">
                                <span className="text-sm text-gray-500">
                                  Experience:{" "}
                                </span>
                                <span className="font-medium">
                                  {product.farmer.experience} years
                                </span>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Related Products */}
              {relatedProducts.length > 0 && (
                <div className="mt-16">
                  <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">
                    Related Products
                  </h3>
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {relatedProducts.map((relatedProduct) => (
                      <ProductCard
                        key={relatedProduct._id}
                        product={relatedProduct}
                      />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import Link from "next/link";
import Image from "next/image";
import { useState, useEffect } from "react";
import { useSearchParams } from "next/navigation";
import { useSession } from "next-auth/react";
import { useCart } from "@/contexts/CartContext";
import ProductCard from "@/components/ProductCard";
import StarRating from "@/components/StarRating";
import Footer from "@/components/Footer";

export default function ProductDetails() {
  const searchParams = useSearchParams();
  const productId = searchParams.get("id");
  const viewMode = searchParams.get("view"); // Check for view parameter
  const { data: session } = useSession();
  const { addToCart } = useCart();

  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState("description");
  const [reviewsPage, setReviewsPage] = useState(1);
  const [hasMoreReviews, setHasMoreReviews] = useState(false);
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isOwner, setIsOwner] = useState(false);

  // Review form state
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({ rating: 5, comment: "" });
  const [isSubmittingReview, setIsSubmittingReview] = useState(false);

  // Farmer-specific states
  const [editMode, setEditMode] = useState(false);
  const [stockUpdate, setStockUpdate] = useState("");
  const [priceUpdate, setPriceUpdate] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  useEffect(() => {
    if (productId) {
      fetchProductDetails();
      fetchReviews();
    }
  }, [productId]);

  const fetchProductDetails = async () => {
    console.log(" FETCHING PRODUCT DETAILS - START");
    console.log("Product ID from URL:", productId);
    console.log("View Mode:", viewMode);

    try {
      const response = await fetch(`/api/products/${productId}`);
      console.log("API Response Status:", response.status);

      if (response.ok) {
        const data = await response.json();
        console.log(" RAW API RESPONSE:", data);
        console.log(" PRODUCT DATA:", data.product);
        console.log(" PRODUCT IMAGES:", data.product?.images);
        console.log(" IMAGES TYPE:", typeof data.product?.images);
        console.log(
          " IMAGES ARRAY CHECK:",
          Array.isArray(data.product?.images),
        );

        setProduct(data.product);
        setRelatedProducts(data.relatedProducts);

        // Check ownership only if not forcing customer view
        if (viewMode !== "customer") {
          setIsOwner(checkOwnership(data.product));
        } else {
          setIsOwner(false); // Force customer view
        }
      } else {
        console.error(
          "API Response Error:",
          response.status,
          response.statusText,
        );
        const errorData = await response.text();
        console.error("Error Response Body:", errorData);
      }
    } catch (error) {
      console.error("Error fetching product details:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchReviews = async (page = 1, append = false) => {
    try {
      const response = await fetch(
        `/api/products/${productId}/reviews?page=${page}`,
      );
      if (response.ok) {
        const data = await response.json();

        if (append) {
          setReviews((prev) => [...prev, ...data.reviews]);
        } else {
          setReviews(data.reviews);
        }
        setHasMoreReviews(data.hasMore);
        setReviewsPage(page);
      }
    } catch (error) {
      console.error("Error fetching reviews:", error);
    }
  };

  const handleAddToCart = async () => {
    if (!session?.user) {
      // Redirect to login if not authenticated
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Use consistent user ID format
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;

      const response = await fetch("/api/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          productId: productId,
          quantity: quantity,
          userId: userId,
        }),
      });

      if (response.ok) {
        alert("Product added to cart successfully!");
      } else {
        const errorData = await response.json();
        alert(errorData.error || "Failed to add to cart");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add product to cart. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleBuyNow = async () => {
    if (!session?.user) {
      // Redirect to login if not authenticated
      window.location.href = "/login";
      return;
    }

    // First add to cart, then navigate to payment
    setIsAddingToCart(true);
    try {
      // Use consistent user ID format
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;

      const response = await fetch("/api/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          productId: productId,
          quantity: quantity,
          userId: userId,
        }),
      });

      if (response.ok) {
        // Navigate to payment page
        window.location.href = "/payment";
      } else {
        const errorData = await response.json();
        alert(errorData.error || "Failed to add to cart");
      }
    } catch (error) {
      console.error("Error processing buy now:", error);
      alert("Failed to process order. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleFavoriteToggle = async () => {
    setIsFavorite(!isFavorite);
    // TODO: Implement favorite API call
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (!session) {
      alert("Please login to submit a review");
      return;
    }

    setIsSubmittingReview(true);
    try {
      const response = await fetch(`/api/products/${productId}/reviews`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reviewForm),
      });

      if (response.ok) {
        setShowReviewForm(false);
        setReviewForm({ rating: 5, comment: "" });
        fetchReviews(); // Refresh reviews
        fetchProductDetails(); // Refresh product rating
      } else {
        const error = await response.json();
        alert(error.error || "Failed to submit review");
      }
    } catch (error) {
      console.error("Error submitting review:", error);
      alert("Failed to submit review");
    } finally {
      setIsSubmittingReview(false);
    }
  };

  const loadMoreReviews = () => {
    fetchReviews(reviewsPage + 1, true);
  };

  // Check if current user owns this product
  const checkOwnership = (productData) => {
    if (!session?.user || !productData) return false;

    const userId = session.user.userId || session.user.id || session.user._id;
    const userEmail = session.user.email;

    return (
      productData.farmerId === userId ||
      productData.farmerId === String(userId) ||
      productData.farmerEmail === userEmail ||
      productData.farmer?.email === userEmail ||
      productData.farmer?.id === userId
    );
  };

  const handleUpdateProduct = async () => {
    if (!isOwner) return;

    // Validate input
    if (!stockUpdate && !priceUpdate) {
      alert("Please enter a value to update");
      return;
    }

    setIsUpdating(true);
    try {
      const updateData = {};

      // Only include fields that have values
      if (stockUpdate && stockUpdate.trim() !== "") {
        const stockValue = parseInt(stockUpdate);
        if (isNaN(stockValue) || stockValue < 0) {
          alert("Please enter a valid stock number");
          setIsUpdating(false);
          return;
        }
        updateData.stock = stockValue;
      }

      if (priceUpdate && priceUpdate.trim() !== "") {
        const priceValue = parseFloat(priceUpdate);
        if (isNaN(priceValue) || priceValue <= 0) {
          alert("Please enter a valid price");
          setIsUpdating(false);
          return;
        }
        updateData.price = priceValue;
      }

      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        alert("Product updated successfully!");
        fetchProductDetails(); // Refresh product details
        setStockUpdate(""); // Clear input
        setPriceUpdate(""); // Clear input
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update product");
      }
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleStockUpdate = async () => {
    if (!stockUpdate || !stockUpdate.trim()) {
      alert("Please enter a stock value");
      return;
    }

    const stockValue = parseInt(stockUpdate);
    if (isNaN(stockValue) || stockValue < 0) {
      alert("Please enter a valid stock number");
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ stock: stockValue }),
      });

      if (response.ok) {
        alert("Stock updated successfully!");
        fetchProductDetails();
        setStockUpdate("");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update stock");
      }
    } catch (error) {
      console.error("Error updating stock:", error);
      alert("Failed to update stock. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handlePriceUpdate = async () => {
    if (!priceUpdate || !priceUpdate.trim()) {
      alert("Please enter a price value");
      return;
    }

    const priceValue = parseFloat(priceUpdate);
    if (isNaN(priceValue) || priceValue <= 0) {
      alert("Please enter a valid price");
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ price: priceValue }),
      });

      if (response.ok) {
        alert("Price updated successfully!");
        fetchProductDetails();
        setPriceUpdate("");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update price");
      }
    } catch (error) {
      console.error("Error updating price:", error);
      alert("Failed to update price. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleToggleStatus = async () => {
    if (!isOwner) return;

    const newStatus = product.status === "active" ? "inactive" : "active";
    const actionText = newStatus === "active" ? "activate" : "deactivate";

    if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        alert(`Product ${actionText}d successfully!`);
        fetchProductDetails();
      } else {
        const error = await response.json();
        alert(error.error || `Failed to ${actionText} product`);
      }
    } catch (error) {
      console.error(`Error ${actionText}ing product:`, error);
      alert(`Failed to ${actionText} product. Please try again.`);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleDeleteProduct = async () => {
    if (!isOwner) return;

    if (
      !confirm(
        " Are you sure you want to delete this product?\n\nThis action cannot be undone and will remove:\n The product listing\n All associated data\n Product from any pending orders",
      )
    ) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Product deleted successfully!");
        window.location.href = "/manage"; // Redirect to dashboard
      } else {
        const error = await response.json();
        if (response.status === 409) {
          alert(
            " Cannot Delete Product\n\nThis product has pending orders and cannot be deleted.\nPlease wait for all orders to be completed or cancelled before deleting this product.\n\nYou can temporarily deactivate the product instead.",
          );
        } else {
          alert(error.error || "Failed to delete product");
        }
      }
    } catch (error) {
      console.error("Error deleting product:", error);
      alert("Failed to delete product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="animate-pulse">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
              <div className="space-y-4">
                <div className="aspect-square bg-gray-300 dark:bg-gray-600 rounded-2xl"></div>
                <div className="grid grid-cols-5 gap-2">
                  {[...Array(5)].map((_, i) => (
                    <div
                      key={i}
                      className="aspect-square bg-gray-300 dark:bg-gray-600 rounded-lg"
                    ></div>
                  ))}
                </div>
              </div>
              <div className="space-y-6">
                <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded w-3/4"></div>
                <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-1/2"></div>
                <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-1/4"></div>
                <div className="h-10 bg-gray-300 dark:bg-gray-600 rounded"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
            Product not found
          </h1>
          <Link
            href="/products"
            className="text-primary-600 hover:text-primary-700"
          >
            Browse all products
          </Link>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Show farmer view only if isOwner is true AND viewMode is not "customer" */}
          {isOwner && viewMode !== "customer" ? (
            <>
              {/* Farmer Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/manage" className="hover:text-primary-600">
                      <i className="fas fa-tachometer-alt mr-1"></i>
                      Dashboard
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name} - Management
                    </span>
                  </li>
                </ol>
              </nav>

              {/* Add Customer View Notice */}
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <i className="fas fa-info-circle text-blue-600 dark:text-blue-400 mr-2"></i>
                    <span className="text-blue-800 dark:text-blue-200">
                      You are viewing this as the product owner.
                    </span>
                  </div>
                  <Link
                    href={`/details?id=${productId}&view=customer`}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                  >
                    <i className="fas fa-eye mr-1"></i>
                    View as Customer
                  </Link>
                </div>
              </div>

              {/* Farmer Product Management Header */}
              <div className="bg-gradient-to-r from-green-600 to-emerald-600 rounded-2xl shadow-lg p-8 mb-8 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-3xl font-bold mb-2">
                      Product Management
                    </h1>
                    <p className="text-green-100">
                      Manage your product listing and inventory
                    </p>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-green-100 mb-1">
                      Product Status
                    </div>
                    <div
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                        product.status === "active"
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      <i
                        className={`fas ${product.status === "active" ? "fa-check-circle" : "fa-pause-circle"} mr-1`}
                      ></i>
                      {product.status === "active" ? "Active" : "Inactive"}
                    </div>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Product Images & Info */}
                <div className="lg:col-span-2 space-y-6">
                  {/* Image Management */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Images ({product.images?.length || 0})
                      </h3>
                      <button className="text-blue-600 hover:text-blue-700 text-sm font-medium">
                        <i className="fas fa-plus mr-1"></i>
                        Add Images
                      </button>
                    </div>

                    {/* Current Images Display */}
                    {(() => {
                      // Debug logging for farmer view
                      console.log("=== FARMER VIEW IMAGE DEBUG ===");
                      console.log("Product object:", product);
                      console.log("Product images array:", product.images);
                      console.log("Product single image:", product.image);
                      console.log("Images length:", product.images?.length);
                      console.log("Has single image:", !!product.image);

                      // Combine both image sources
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      console.log("Combined images:", allImages);
                      console.log("Total image count:", allImages.length);
                      console.log("=== END DEBUG ===");

                      return null;
                    })()}

                    {(() => {
                      // Combine both image sources
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      return allImages.length > 0 ? (
                        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                          {allImages.map((image, index) => {
                            console.log(
                              `Rendering farmer view image ${index + 1}:`,
                              image,
                            );
                            return (
                              <div key={index} className="relative group">
                                <div className="aspect-square bg-gray-100 dark:bg-gray-700 rounded-lg overflow-hidden">
                                  <Image
                                    src={image}
                                    alt={`${product.name} ${index + 1}`}
                                    width={200}
                                    height={200}
                                    className="w-full h-full object-cover transition-transform group-hover:scale-105"
                                    onError={(e) => {
                                      console.log(
                                        "Farmer view image load ERROR:",
                                        {
                                          index,
                                          originalSrc: image,
                                          errorEvent: e,
                                        },
                                      );
                                      e.target.src =
                                        "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=200&h=200&fit=crop";
                                    }}
                                    onLoad={() => {
                                      console.log(
                                        `Farmer view image ${index + 1} loaded successfully:`,
                                        image,
                                      );
                                    }}
                                  />
                                </div>
                                <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
                                  <button className="bg-red-500 hover:bg-red-600 text-white p-1 rounded text-xs">
                                    <i className="fas fa-trash"></i>
                                  </button>
                                </div>
                                {index === 0 && (
                                  <div className="absolute bottom-2 left-2">
                                    <span className="bg-blue-500 text-white px-2 py-1 rounded text-xs font-medium">
                                      Primary
                                    </span>
                                  </div>
                                )}
                                <div className="absolute bottom-2 right-2">
                                  <span className="bg-black bg-opacity-50 text-white px-1 py-0.5 rounded text-xs">
                                    {index + 1}
                                  </span>
                                </div>
                              </div>
                            );
                          })}

                          {/* Add Image Placeholder */}
                          <div className="aspect-square border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg flex items-center justify-center hover:border-blue-500 hover:bg-blue-50 dark:hover:bg-blue-900/20 transition cursor-pointer">
                            <div className="text-center">
                              <i className="fas fa-plus text-2xl text-gray-400 mb-2"></i>
                              <p className="text-sm text-gray-500">Add Image</p>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <div className="w-16 h-16 bg-gray-100 dark:bg-gray-700 rounded-full flex items-center justify-center mx-auto mb-4">
                            <i className="fas fa-image text-2xl text-gray-400"></i>
                          </div>
                          <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                            No Images Uploaded
                          </h4>
                          <p className="text-gray-600 dark:text-gray-400 mb-4">
                            Add product images to make your listing more
                            attractive
                          </p>
                          <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition">
                            <i className="fas fa-upload mr-1"></i>
                            Upload Images
                          </button>
                        </div>
                      );
                    })()}

                    {/* Enhanced Debug info for farmer view */}
                    <div className="mt-4 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-3">
                      <p className="text-xs font-semibold text-yellow-800 dark:text-yellow-200 mb-1">
                         DEBUG - Farmer View Images:
                      </p>
                      <div className="text-xs text-yellow-700 dark:text-yellow-300 space-y-1">
                        <p>
                          <strong>Images Array Length:</strong>{" "}
                          {product.images?.length || 0}
                        </p>
                        <p>
                          <strong>Single Image Field:</strong>{" "}
                          {product.image ? "Yes" : "No"}
                        </p>
                        <p>
                          <strong>Total Images:</strong>{" "}
                          {(() => {
                            const allImages = [];
                            if (product.image) allImages.push(product.image);
                            if (product.images && product.images.length > 0)
                              allImages.push(...product.images);
                            return allImages.length;
                          })()}
                        </p>
                        <p>
                          <strong>Product ID:</strong> {productId}
                        </p>
                        {product.image && (
                          <div className="ml-2 p-1 bg-yellow-100 dark:bg-yellow-900 rounded text-xs break-all">
                            <strong>Single Image:</strong>{" "}
                            {product.image.substring(0, 50)}...
                          </div>
                        )}
                        {product.images?.map((img, idx) => (
                          <div
                            key={idx}
                            className="ml-2 p-1 bg-yellow-100 dark:bg-yellow-900 rounded text-xs break-all"
                          >
                            <strong>Array Image {idx + 1}:</strong> {img}
                          </div>
                        ))}
                        <p>
                          <strong>API Response Check:</strong> Open browser
                          console for detailed logs
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Product Information */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Information
                      </h3>
                      <Link
                        href={`/create?edit=${productId}`}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-edit mr-1"></i>
                        Edit Details
                      </Link>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h4 className="text-2xl font-bold text-gray-900 dark:text-white">
                          {product.name}
                        </h4>
                        <p className="text-gray-600 dark:text-gray-400">
                          {product.category}
                        </p>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-1">
                            Current Price
                          </div>
                          <div className="text-2xl font-bold text-green-600">
                            {product.price}
                          </div>
                          <div className="text-sm text-gray-500">
                            per {product.unit || "kg"}
                          </div>
                        </div>

                        <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-1">
                            Current Stock
                          </div>
                          <div className="text-2xl font-bold text-blue-600">
                            {product.stock}
                          </div>
                          <div className="text-sm text-gray-500">
                            {product.unit || "kg"} available
                          </div>
                        </div>
                      </div>

                      <div>
                        <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                          Description
                        </div>
                        <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
                          {product.description || "No description available"}
                        </p>
                      </div>

                      {product.features && (
                        <div>
                          <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                            Features
                          </div>
                          <div className="flex flex-wrap gap-2">
                            {product.features.map((feature, index) => (
                              <span
                                key={index}
                                className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded text-sm"
                              >
                                {feature}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Quick Actions */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
                      Quick Updates
                    </h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      {/* Stock Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Stock
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={stockUpdate}
                            onChange={(e) => setStockUpdate(e.target.value)}
                            placeholder={product.stock.toString()}
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 dark:bg-gray-700 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={!stockUpdate || isUpdating}
                            className="bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            {isUpdating ? "Updating..." : "Update"}
                          </button>
                        </div>
                      </div>

                      {/* Price Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Price ()
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={priceUpdate}
                            onChange={(e) => setPriceUpdate(e.target.value)}
                            placeholder={product.price.toString()}
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 dark:bg-gray-700 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={!priceUpdate || isUpdating}
                            className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            {isUpdating ? "Updating..." : "Update"}
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Sidebar - Analytics & Actions */}
                <div className="space-y-6">
                  {/* Performance Stats */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                        Performance
                      </h3>
                      <div className="text-xs text-gray-500 dark:text-gray-400">
                        Real-time data
                      </div>
                    </div>

                    <div className="space-y-4">
                      {/* Total Sales */}
                      <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                        <div className="flex items-center justify-center gap-2 mb-1">
                          <div className="text-2xl font-bold text-blue-600">
                            {product.performanceMetrics?.totalSales || 0}
                          </div>
                          {product.performanceMetrics?.salesTrend === "up" && (
                            <i className="fas fa-arrow-up text-green-500 text-sm"></i>
                          )}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Sales (Units)
                        </div>
                        {product.performanceMetrics?.recentSales > 0 && (
                          <div className="text-xs text-green-600 mt-1">
                            +{product.performanceMetrics.recentSales} this month
                          </div>
                        )}
                      </div>

                      {/* Total Revenue */}
                      <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-green-600">
                          {product.performanceMetrics?.totalRevenue?.toLocaleString() || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Revenue
                        </div>
                        {product.performanceMetrics?.recentRevenue > 0 && (
                          <div className="text-xs text-green-600 mt-1">
                            +{product.performanceMetrics.recentRevenue.toLocaleString()} this month
                          </div>
                        )}
                      </div>

                      {/* Average Rating */}
                      <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-yellow-600">
                          {product.performanceMetrics?.averageRating 
                            ? product.performanceMetrics.averageRating.toFixed(1)
                            : "0.0"}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Average Rating
                        </div>
                        <div className="flex justify-center mt-1">
                          {[...Array(5)].map((_, i) => (
                            <i
                              key={i}
                              className={`fas fa-star text-xs ${
                                i < Math.round(product.performanceMetrics?.averageRating || 0)
                                  ? "text-yellow-400"
                                  : "text-gray-300"
                              }`}
                            ></i>
                          ))}
                        </div>
                      </div>

                      {/* Total Reviews */}
                      <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-purple-600">
                          {product.performanceMetrics?.totalReviews || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Reviews
                        </div>
                      </div>

                      {/* Average Order Value */}
                      {product.performanceMetrics?.averageOrderValue > 0 && (
                        <div className="text-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-orange-600">
                            {product.performanceMetrics.averageOrderValue.toFixed(0)}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Avg. Order Value
                          </div>
                        </div>
                      )}

                      {/* Total Orders */}
                      {product.performanceMetrics?.totalOrders > 0 && (
                        <div className="text-center p-4 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-indigo-600">
                            {product.performanceMetrics.totalOrders}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Total Orders
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Performance Summary */}
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                      <div className="text-xs text-gray-500 dark:text-gray-400 text-center">
                        {product.performanceMetrics?.totalSales > 0 
                          ? " Active product with sales history"
                          : " New product - building performance data"
                        }
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Actions
                    </h3>

                    <div className="space-y-3">
                      <Link
                        href={`/create?edit=${productId}`}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-edit mr-2"></i>
                        Edit Product
                      </Link>

                      <Link
                        href={`/details?id=${productId}&view=customer`}
                        className="w-full bg-gray-600 hover:bg-gray-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-eye mr-2"></i>
                        View as Customer
                      </Link>

                      <button
                        onClick={handleToggleStatus}
                        disabled={isUpdating}
                        className={`w-full py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          product.status === "active"
                            ? "bg-yellow-600 hover:bg-yellow-700 text-white"
                            : "bg-green-600 hover:bg-green-700 text-white"
                        } ${isUpdating ? "opacity-50 cursor-not-allowed" : ""}`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i
                            className={`fas ${product.status === "active" ? "fa-pause" : "fa-play"} mr-2`}
                          ></i>
                        )}
                        {isUpdating
                          ? "Updating..."
                          : product.status === "active"
                            ? "Deactivate"
                            : "Activate"}
                      </button>

                      <button
                        onClick={handleDeleteProduct}
                        disabled={isUpdating}
                        className={`w-full bg-red-600 hover:bg-red-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          isUpdating ? "opacity-50 cursor-not-allowed" : ""
                        }`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i className="fas fa-trash mr-2"></i>
                        )}
                        {isUpdating ? "Deleting..." : "Delete Product"}
                      </button>
                    </div>
                  </div>

                  {/* Recent Orders */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Recent Orders
                    </h3>

                    <div className="space-y-3 text-sm">
                      <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                        <i className="fas fa-shopping-bag text-2xl mb-2"></i>
                        <p>No recent orders</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            /* Regular Customer View */
            <>
              {/* Regular breadcrumb and customer interface here */}
              {/* Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/" className="hover:text-primary-600">
                      Home
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <Link href="/products" className="hover:text-primary-600">
                      Products
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name}
                    </span>
                  </li>
                </ol>
              </nav>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                {/* Product Images */}
                <div className="space-y-4">
                  <div className="aspect-square bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg">
                    {(() => {
                      // Combine both image sources for customer view
                      const allImages = [];
                      if (product.image) {
                        allImages.push(product.image);
                      }
                      if (product.images && product.images.length > 0) {
                        allImages.push(...product.images);
                      }

                      console.log(
                        "Customer view - Combined images:",
                        allImages,
                      );
                      console.log(
                        "Customer view - Selected image index:",
                        selectedImage,
                      );
                      console.log(
                        "Customer view - Displaying image:",
                        allImages[selectedImage],
                      );

                      return (
                        <Image
                          src={
                            allImages[selectedImage] ||
                            "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=600&h=600&fit=crop"
                          }
                          alt={product.name}
                          width={600}
                          height={600}
                          className="w-full h-full object-cover"
                          onError={(e) => {
                            console.log(
                              "Customer view main image load ERROR:",
                              e.target.src,
                            );
                            e.target.src =
                              "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=600&h=600&fit=crop";
                          }}
                          onLoad={(e) => {
                            console.log(
                              "Customer view main image loaded successfully:",
                              e.target.src,
                            );
                          }}
                        />
                      );
                    })()}
                  </div>

                  {/* Thumbnail Images */}
                  {(() => {
                    // Combine both image sources for thumbnails
                    const allImages = [];
                    if (product.image) {
                      allImages.push(product.image);
                    }
                    if (product.images && product.images.length > 0) {
                      allImages.push(...product.images);
                    }

                    return allImages.length > 1 ? (
                      <div className="grid grid-cols-5 gap-2">
                        {allImages.map((image, index) => (
                          <button
                            key={index}
                            onClick={() => setSelectedImage(index)}
                            className={`aspect-square bg-white dark:bg-gray-800 rounded-lg overflow-hidden border-2 transition ${
                              selectedImage === index
                                ? "border-primary-500"
                                : "border-transparent hover:border-primary-300"
                            }`}
                          >
                            <Image
                              src={image}
                              alt={`${product.name} ${index + 1}`}
                              width={100}
                              height={100}
                              className="w-full h-full object-cover"
                              onError={(e) => {
                                console.log(
                                  "Customer view thumbnail load ERROR:",
                                  e.target.src,
                                );
                                e.target.src =
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=100&h=100&fit=crop";
                              }}
                              onLoad={() => {
                                console.log(
                                  `Customer view thumbnail ${index + 1} loaded successfully`,
                                );
                              }}
                            />
                          </button>
                        ))}
                      </div>
                    ) : null;
                  })()}
                </div>

                {/* Product Info */}
                <div className="space-y-6">
                  {/* Category and Features */}
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-3 py-1 rounded-full text-sm font-medium">
                      {product.category}
                    </span>
                    {product.isOrganic && (
                      <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                        Organic
                      </span>
                    )}
                    {product.isFresh && (
                      <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                        Fresh
                      </span>
                    )}
                  </div>

                  {/* Product Name and Farmer */}
                  <div>
                    <div className="flex items-center space-x-2 mb-2">
                      {product.isOrganic && (
                        <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                          Organic
                        </span>
                      )}
                      {product.isFresh && (
                        <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                          Fresh
                        </span>
                      )}
                    </div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                      {product.name}
                    </h1>
                    <p className="text-lg text-gray-600 dark:text-gray-400">
                      Produced by{" "}
                      <span className="font-semibold text-primary-600 dark:text-primary-400">
                        {product.farmer?.farmName ||
                          product.farmer?.name ||
                          "Unknown Farm"}
                      </span>
                    </p>
                  </div>

                  {/* Rating and Reviews */}
                  <div className="flex items-center space-x-4">
                    <StarRating
                      rating={product.averageRating || 0}
                      showValue={true}
                    />
                    <span className="text-gray-500 dark:text-gray-400">
                      ({product.reviewCount || product.totalReviews || 0}{" "}
                      reviews)
                    </span>
                    <button
                      onClick={() => setActiveTab("reviews")}
                      className="text-primary-600 dark:text-primary-400 hover:underline"
                    >
                      Write a review
                    </button>
                  </div>

                  {/* Price and Stock */}
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-6">
                    <div className="flex items-center justify-between mb-4">
                      <div>
                        <span className="text-3xl font-bold text-primary-600 dark:text-primary-400">
                          {product.price}
                        </span>
                        <span className="text-lg text-gray-500 dark:text-gray-400">
                          /{product.unit || "kg"}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500 dark:text-gray-400">
                          Available Stock
                        </p>
                        <p className="text-lg font-semibold text-gray-900 dark:text-white">
                          {product.stock} {product.unit || "kg"}
                        </p>
                      </div>
                    </div>

                    {/* Location */}
                    <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                      <i className="fas fa-map-marker-alt mr-2"></i>
                      <span>
                        {product.farmer?.location || "Location not specified"}
                      </span>
                    </div>
                  </div>

                  {/* Quantity Selection */}
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Quantity ({product.unit || "kg"})
                      </label>
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={() => setQuantity(Math.max(1, quantity - 1))}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-minus text-sm"></i>
                        </button>
                        <input
                          type="number"
                          value={quantity}
                          onChange={(e) =>
                            setQuantity(
                              Math.max(1, parseInt(e.target.value) || 1),
                            )
                          }
                          min="1"
                          max={product.stock}
                          className="w-20 text-center py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                        />
                        <button
                          onClick={() =>
                            setQuantity(Math.min(product.stock, quantity + 1))
                          }
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-plus text-sm"></i>
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    <button
                      onClick={handleBuyNow}
                      disabled={product.stock <= 0}
                      className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                    >
                      <i className="fas fa-bolt mr-2"></i>
                      Buy Now
                    </button>
                    <button
                      onClick={handleAddToCart}
                      disabled={isAddingToCart || product.stock <= 0}
                      className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                    >
                      <i className="fas fa-shopping-cart mr-2"></i>
                      {isAddingToCart ? "Adding..." : "Add to Cart"}
                    </button>
                    <button
                      onClick={handleFavoriteToggle}
                      className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                    >
                      <i
                        className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                      ></i>
                      Add to Favorite
                    </button>
                  </div>

                  {/* Farmer Contact */}
                  <div className="bg-primary-50 dark:bg-primary-900 rounded-xl p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-800 flex items-center justify-center">
                        <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                      </div>
                      <div>
                        <h4 className="font-semibold text-gray-900 dark:text-white">
                          {product.farmer?.name || "Unknown Farmer"}
                        </h4>
                        <p className="text-sm text-gray-600 dark:text-gray-400">
                          Farmer since{" "}
                          {new Date().getFullYear() -
                            (product.farmer?.experience || 5)}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Tabs Section */}
              <div className="mt-16">
                <div className="border-b border-gray-200 dark:border-gray-700">
                  <nav className="flex space-x-8">
                    {[
                      "description",
                      "nutrition",
                      "storage",
                      "reviews",
                      "farmer",
                    ].map((tab) => (
                      <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`py-4 px-1 border-b-2 font-medium text-sm transition ${
                          activeTab === tab
                            ? "border-primary-500 text-primary-600 dark:text-primary-400"
                            : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300"
                        }`}
                      >
                        {tab === "nutrition"
                          ? "Nutrition"
                          : tab === "storage"
                            ? "Storage"
                            : tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    ))}
                  </nav>
                </div>

                <div className="py-8">
                  {/* Description Tab */}
                  {activeTab === "description" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-4">
                        Product Description
                      </h3>
                      <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                        {product.description ||
                          "No description available for this product."}
                      </p>

                      {product.features && product.features.length > 0 && (
                        <div className="mt-6">
                          <h4 className="text-lg font-medium mb-3">Features</h4>
                          <ul className="list-disc list-inside space-y-1">
                            {product.features.map((feature, index) => (
                              <li
                                key={index}
                                className="text-gray-600 dark:text-gray-400"
                              >
                                {feature}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Nutrition Tab */}
                  {activeTab === "nutrition" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Nutritional Information
                      </h3>

                      {product.nutritionalInformation ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
                          <div className="px-6 py-4 bg-gray-50 dark:bg-gray-700 border-b border-gray-200 dark:border-gray-600">
                            <h4 className="text-lg font-medium text-gray-900 dark:text-white">
                              Per{" "}
                              {product.nutritionalInformation.servingSize ||
                                "100g"}{" "}
                              serving
                            </h4>
                          </div>

                          <div className="p-6">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                              {/* Main nutrients */}
                              <div className="space-y-3">
                                {product.nutritionalInformation.calories && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Calories
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.calories}{" "}
                                      kcal
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.protein && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Protein</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.protein}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation
                                  .carbohydrates && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Carbohydrates
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {
                                        product.nutritionalInformation
                                          .carbohydrates
                                      }
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.fiber && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Fiber</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.fiber}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.fat && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Fat</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.fat}
                                    </span>
                                  </div>
                                )}
                              </div>

                              {/* Vitamins and Minerals */}
                              <div className="space-y-3">
                                {product.nutritionalInformation.iron && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">Iron</span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.iron}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.vitaminB1 && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Vitamin B1
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.vitaminB1}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.vitaminC && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium">
                                      Vitamin C
                                    </span>
                                    <span className="text-gray-600 dark:text-gray-400">
                                      {product.nutritionalInformation.vitaminC}
                                    </span>
                                  </div>
                                )}
                                {product.nutritionalInformation.gluten && (
                                  <div className="flex justify-between py-2 border-b border-gray-100 dark:border-gray-600">
                                    <span className="font-medium text-orange-600">
                                      Allergen Info
                                    </span>
                                    <span className="text-orange-600 dark:text-orange-400">
                                      {product.nutritionalInformation.gluten}
                                    </span>
                                  </div>
                                )}
                              </div>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <p className="text-gray-600 dark:text-gray-400">
                            Nutritional information is not available for this
                            product.
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Storage Instructions Tab */}
                  {activeTab === "storage" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Storage Instructions
                      </h3>

                      {product.storageInstructions ? (
                        <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-6">
                          <div className="flex items-start gap-3">
                            <div className="flex-shrink-0 mt-1">
                              <i className="fas fa-snowflake text-blue-600 dark:text-blue-400 text-xl"></i>
                            </div>
                            <div className="flex-1">
                              <h4 className="text-lg font-medium text-blue-900 dark:text-blue-100 mb-3">
                                How to Store This Product
                              </h4>
                              <p className="text-blue-800 dark:text-blue-200 leading-relaxed">
                                {product.storageInstructions}
                              </p>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <p className="text-gray-600 dark:text-gray-400">
                            Storage instructions are not available for this
                            product.
                          </p>
                        </div>
                      )}

                      {/* General storage tips */}
                      <div className="mt-8">
                        <h4 className="text-lg font-medium mb-4">
                          General Storage Tips
                        </h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-thermometer-half text-green-600"></i>
                              <span className="font-medium">Temperature</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Keep at recommended temperature to maintain
                              freshness
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-tint text-blue-600"></i>
                              <span className="font-medium">Moisture</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Control humidity levels to prevent spoilage
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-sun text-yellow-600"></i>
                              <span className="font-medium">Light</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Store away from direct sunlight when specified
                            </p>
                          </div>

                          <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
                            <div className="flex items-center gap-2 mb-2">
                              <i className="fas fa-clock text-purple-600"></i>
                              <span className="font-medium">Freshness</span>
                            </div>
                            <p className="text-sm text-gray-600 dark:text-gray-400">
                              Consume within recommended timeframe
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Reviews Tab */}
                  {activeTab === "reviews" && (
                    <div>
                      <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          Customer Reviews
                        </h2>
                        {session && (
                          <button
                            onClick={() => setShowReviewForm(true)}
                            className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            Write a Review
                          </button>
                        )}
                      </div>

                      {/* Review Summary */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-6 mb-8">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                          <div>
                            <div className="flex items-center space-x-2 mb-4">
                              <span className="text-4xl font-bold text-gray-900 dark:text-white">
                                {(() => {
                                  // Use reviews from API call, which should contain the product's reviews array
                                  const allReviews = reviews || [];
                                  if (allReviews.length === 0) {
                                    // Fallback to product's original rating if no reviews from API
                                    return (
                                      product.averageRating ||
                                      product.rating ||
                                      0
                                    ).toFixed(1);
                                  }
                                  const totalRating = allReviews.reduce(
                                    (sum, review) =>
                                      sum + Number(review.rating || 0),
                                    0,
                                  );
                                  return (
                                    totalRating / allReviews.length
                                  ).toFixed(1);
                                })()}
                              </span>
                              <div>
                                <div className="flex text-yellow-400 mb-1">
                                  {[...Array(5)].map((_, i) => {
                                    // Calculate average rating from API reviews or fallback to product rating
                                    const allReviews = reviews || [];
                                    let avgRating = 0;
                                    if (allReviews.length > 0) {
                                      const totalRating = allReviews.reduce(
                                        (sum, review) =>
                                          sum + Number(review.rating || 0),
                                        0,
                                      );
                                      avgRating =
                                        totalRating / allReviews.length;
                                    } else {
                                      avgRating =
                                        product.averageRating ||
                                        product.rating ||
                                        0;
                                    }

                                    console.log(
                                      "Star rendering - Average rating:",
                                      avgRating,
                                      "Star index:",
                                      i,
                                    );

                                    return (
                                      <i
                                        key={i}
                                        className={`fas fa-star ${
                                          i < Math.round(avgRating)
                                            ? "text-yellow-400"
                                            : "text-gray-300"
                                        }`}
                                      ></i>
                                    );
                                  })}
                                </div>
                                <p className="text-sm text-gray-500 dark:text-gray-400">
                                  Based on{" "}
                                  {reviews?.length ||
                                    product.totalReviews ||
                                    product.reviewCount ||
                                    0}{" "}
                                  reviews
                                </p>
                              </div>
                            </div>
                          </div>
                          <div className="space-y-2">
                            {/* Dynamic Rating breakdown using API reviews */}
                            {[5, 4, 3, 2, 1].map((star) => {
                              const allReviews = reviews || [];
                              const totalReviews = allReviews.length;

                              // Count reviews that fall within the star range (e.g., 4.0-4.9 for 4 stars)
                              const starCount = allReviews.filter((review) => {
                                const rating = Number(review.rating || 0);
                                return rating >= star && rating < star + 1;
                              }).length;

                              const percentage =
                                totalReviews > 0
                                  ? (starCount / totalReviews) * 100
                                  : 0;

                              return (
                                <div
                                  key={star}
                                  className="flex items-center space-x-2"
                                >
                                  <span className="text-sm w-8">{star}</span>
                                  <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                                    <div
                                      className="bg-yellow-400 h-2 rounded-full transition-all duration-300"
                                      style={{ width: `${percentage}%` }}
                                    ></div>
                                  </div>
                                  <span className="text-sm text-gray-500 dark:text-gray-400 w-8">
                                    {starCount}
                                  </span>
                                </div>
                              );
                            })}
                          </div>
                        </div>
                      </div>

                      {/* Review Form Modal */}
                      {showReviewForm && (
                        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                          <div className="bg-white dark:bg-gray-800 rounded-lg max-w-md w-full p-6">
                            <h4 className="text-lg font-semibold mb-4">
                              Write a Review
                            </h4>
                            <form onSubmit={handleSubmitReview}>
                              <div className="mb-4">
                                <label className="block text-sm font-medium mb-2">
                                  Rating
                                </label>
                                <div className="flex gap-1">
                                  {[1, 2, 3, 4, 5].map((star) => (
                                    <button
                                      key={star}
                                      type="button"
                                      onClick={() =>
                                        setReviewForm({
                                          ...reviewForm,
                                          rating: star,
                                        })
                                      }
                                      className={`text-2xl transition-colors ${
                                        star <= reviewForm.rating
                                          ? "text-yellow-400 hover:text-yellow-500"
                                          : "text-gray-300 hover:text-gray-400"
                                      }`}
                                    >

                                    </button>
                                  ))}
                                </div>
                              </div>

                              <div className="mb-4">
                                <label className="block text-sm font-medium mb-2">
                                  Comment
                                </label>
                                <textarea
                                  value={reviewForm.comment}
                                  onChange={(e) =>
                                    setReviewForm({
                                      ...reviewForm,
                                      comment: e.target.value,
                                    })
                                  }
                                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 dark:bg-gray-700"
                                  rows="4"
                                  placeholder="Share your experience with this product..."
                                  required
                                />
                              </div>

                              <div className="flex gap-3">
                                <button
                                  type="button"
                                  onClick={() => setShowReviewForm(false)}
                                  className="flex-1 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 py-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition"
                                >
                                  Cancel
                                </button>
                                <button
                                  type="submit"
                                  disabled={
                                    isSubmittingReview ||
                                    !reviewForm.comment.trim()
                                  }
                                  className="flex-1 bg-primary-600 hover:bg-primary-700 text-white py-2 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed transition"
                                >
                                  {isSubmittingReview
                                    ? "Submitting..."
                                    : "Submit Review"}
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      )}

                      {/* Individual Reviews */}
                      <div className="space-y-6">
                        {/* Only display reviews from API call */}
                        {(() => {
                          const allReviews = reviews || [];

                          if (allReviews.length === 0) {
                            return (
                              <div className="text-center py-12">
                                <div className="w-16 h-16 bg-gray-100 dark:bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4">
                                  <i className="fas fa-comment-alt text-2xl text-gray-400"></i>
                                </div>
                                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                                  No reviews yet
                                </h3>
                                <p className="text-gray-600 dark:text-gray-400 mb-6">
                                  Be the first to share your thoughts about this
                                  product.
                                </p>
                                {session && (
                                  <button
                                    onClick={() => setShowReviewForm(true)}
                                    className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition"
                                  >
                                    Write the First Review
                                  </button>
                                )}
                              </div>
                            );
                          }

                          return allReviews.map((review, index) => (
                            <div
                              key={review._id || index}
                              className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-sm"
                            >
                              <div className="flex items-start space-x-4">
                                <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-900 flex items-center justify-center">
                                  <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                                </div>
                                <div className="flex-1">
                                  <div className="flex items-center justify-between mb-2">
                                    <div>
                                      <div className="flex items-center gap-2">
                                        <h4 className="font-semibold text-gray-900 dark:text-white">
                                          {review.userName || "Anonymous"}
                                        </h4>
                                        {review.isCurrentUser && (
                                          <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-2 py-1 rounded text-xs">
                                            Your Review
                                          </span>
                                        )}
                                        <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded text-xs">
                                          Verified Purchase
                                        </span>
                                      </div>
                                      <div className="flex items-center space-x-2">
                                        <div className="flex text-yellow-400 text-sm">
                                          {[...Array(5)].map((_, i) => {
                                            const reviewRating = Number(
                                              review.rating || 0,
                                            );
                                            console.log(
                                              `Review by ${review.userName} - Rating: ${reviewRating}, Star ${i + 1}: ${i < Math.floor(reviewRating) ? "filled" : "empty"}`,
                                            );

                                            return (
                                              <i
                                                key={i}
                                                className={`fas fa-star ${
                                                  i < Math.floor(reviewRating)
                                                    ? "text-yellow-400"
                                                    : "text-gray-300"
                                                }`}
                                              ></i>
                                            );
                                          })}
                                        </div>
                                        <span className="text-sm text-gray-500 dark:text-gray-400">
                                          {review.createdAt
                                            ? new Date(
                                                review.createdAt,
                                              ).toLocaleDateString()
                                            : "Unknown date"}
                                        </span>
                                      </div>
                                    </div>
                                    <button className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition">
                                      <i className="fas fa-ellipsis-v"></i>
                                    </button>
                                  </div>
                                  <p className="text-gray-700 dark:text-gray-300 mb-3 leading-relaxed">
                                    {review.comment}
                                  </p>
                                  <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
                                    <button className="hover:text-primary-600 dark:hover:text-primary-400 transition">
                                      <i className="far fa-thumbs-up mr-1"></i>
                                      Helpful (
                                      {Math.floor(Math.random() * 15) + 1})
                                    </button>
                                    <button className="hover:text-primary-600 dark:hover:text-primary-400 transition">
                                      Reply
                                    </button>
                                    {review.isCurrentUser && (
                                      <button className="hover:text-red-600 dark:hover:text-red-400 transition">
                                        Edit
                                      </button>
                                    )}
                                  </div>
                                </div>
                              </div>
                            </div>
                          ));
                        })()}

                        {/* Load More Reviews Button */}
                        {hasMoreReviews && (
                          <div className="text-center mt-8">
                            <button
                              onClick={loadMoreReviews}
                              className="bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white px-6 py-3 rounded-lg font-medium transition"
                            >
                              Load More Reviews
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Farmer Info Tab */}
                  {activeTab === "farmer" && (
                    <div>
                      <h3 className="text-xl font-semibold mb-6">
                        About the Farmer
                      </h3>
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow-lg">
                        <div className="flex items-start gap-4">
                          <div className="w-16 h-16 bg-primary-100 dark:bg-primary-900 rounded-full flex items-center justify-center">
                            <i className="fas fa-user text-2xl text-primary-600 dark:text-primary-400"></i>
                          </div>
                          <div className="flex-1">
                            <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-1">
                              {product.farmer?.name || "Unknown Farmer"}
                            </h4>
                            <p className="text-gray-600 dark:text-gray-400 mb-3">
                              <i className="fas fa-map-marker-alt mr-2"></i>
                              {product.farmer?.location ||
                                "Location not specified"}
                            </p>
                            <p className="text-gray-600 dark:text-gray-400">
                              {product.farmer?.bio ||
                                "No farmer information available."}
                            </p>

                            {product.farmer?.experience && (
                              <div className="mt-4">
                                <span className="text-sm text-gray-500">
                                  Experience:{" "}
                                </span>
                                <span className="font-medium">
                                  {product.farmer.experience} years
                                </span>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Related Products */}
              {relatedProducts.length > 0 && (
                <div className="mt-16">
                  <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">
                    Related Products
                  </h3>
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                    {relatedProducts.map((relatedProduct) => (
                      <ProductCard
                        key={relatedProduct._id}
                        product={relatedProduct}
                      />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import Link from "next/link";
import { useState, useEffect } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import ProductCard from "@/components/ProductCard";
import Footer from "@/components/Footer";

export default function Products() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [pagination, setPagination] = useState({});

  // Search and filter states
  const [searchTerm, setSearchTerm] = useState(
    searchParams.get("search") || "",
  );
  const [selectedCategory, setSelectedCategory] = useState(
    searchParams.get("category") || "All Categories",
  );
  const [selectedPriceRanges, setSelectedPriceRanges] = useState([]);
  const [selectedRatings, setSelectedRatings] = useState([]);
  const [selectedFarmers, setSelectedFarmers] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [priceRangeSlider, setPriceRangeSlider] = useState([0, 10000]); // Increased default max to 10000
  const [sortBy, setSortBy] = useState("newest");
  const [currentPage, setCurrentPage] = useState(1);
  const [showMobileFilters, setShowMobileFilters] = useState(false);
  const [availableFarmers, setAvailableFarmers] = useState([]);

  const categoryOptions = [
    "All Categories",
    "Vegetables",
    "Fruits",
    "Grains",
    "Dairy",
    "Honey",
    "Herbs",
  ];

  const priceRangeOptions = [
    { label: "Under 50", min: 0, max: 49 },
    { label: "50 - 100", min: 50, max: 100 },
    { label: "100 - 200", min: 101, max: 200 },
    { label: "200 - 500", min: 201, max: 500 },
    { label: "Above 500", min: 501, max: 9999 },
  ];

  const tagOptions = [
    "Organic",
    "Fresh",
    "Local",
    "Premium",
    "Seasonal",
    "Limited Stock",
  ];

  useEffect(() => {
    console.log(" Products useEffect triggered:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });
    fetchProducts();
    // Remove fetchAvailableFarmers from here to prevent unnecessary calls
  }, [
    searchTerm,
    selectedCategory,
    selectedPriceRanges,
    selectedRatings,
    selectedFarmers,
    selectedTags,
    priceRangeSlider,
    sortBy,
    currentPage,
  ]);

  // Separate useEffect for fetching farmers (only once)
  useEffect(() => {
    fetchAvailableFarmers();
  }, []); // Empty dependency array - only run once

  // Update URL when category changes
  useEffect(() => {
    console.log(" Category changed, updating URL:", selectedCategory);
    updateURLWithFilters({
      selectedCategory: selectedCategory,
    });
  }, [selectedCategory]);

  useEffect(() => {
    console.log(" URL params changed, updating state:", {
      searchParams: Object.fromEntries(searchParams.entries()),
    });

    // Update states from URL params
    const newSearchTerm = searchParams.get("search") || "";
    const newCategory = searchParams.get("category") || "All Categories";

    console.log(" Setting search term:", newSearchTerm);
    console.log(" Setting category:", newCategory);

    setSearchTerm(newSearchTerm);
    setSelectedCategory(newCategory);

    // Restore price ranges from URL
    const priceRanges = searchParams.get("priceRanges");
    if (priceRanges) {
      const newPriceRanges = priceRanges.split(",");
      console.log(" Setting price ranges:", newPriceRanges);
      setSelectedPriceRanges(newPriceRanges);
    } else {
      console.log(" Clearing price ranges");
      setSelectedPriceRanges([]);
    }

    // Restore ratings from URL
    const ratings = searchParams.get("ratings");
    if (ratings) {
      const newRatings = ratings.split(",").map(Number);
      console.log(" Setting ratings:", newRatings);
      setSelectedRatings(newRatings);
    } else {
      console.log(" Clearing ratings");
      setSelectedRatings([]);
    }

    // Restore farmers from URL
    const farmers = searchParams.get("farmers");
    if (farmers) {
      const newFarmers = farmers.split(",");
      console.log(" Setting farmers:", newFarmers);
      setSelectedFarmers(newFarmers);
    } else {
      console.log(" Clearing farmers");
      setSelectedFarmers([]);
    }

    // Restore tags from URL
    const tags = searchParams.get("tags");
    if (tags) {
      const newTags = tags.split(",");
      console.log(" Setting tags:", newTags);
      setSelectedTags(newTags);
    } else {
      console.log(" Clearing tags");
      setSelectedTags([]);
    }

    // Restore price range slider from URL
    const minPrice = searchParams.get("minPrice");
    const maxPrice = searchParams.get("maxPrice");
    if (minPrice && maxPrice) {
      const newPriceSlider = [Number(minPrice), Number(maxPrice)];
      console.log(" Setting price slider:", newPriceSlider);
      setPriceRangeSlider(newPriceSlider);
    } else {
      console.log(" Resetting price slider to default");
      setPriceRangeSlider([0, 10000]);
    }

    // Restore sort option from URL
    const newSort = searchParams.get("sort") || "newest";
    console.log(" Setting sort:", newSort);
    setSortBy(newSort);

    // Restore page from URL
    const newPage = Number(searchParams.get("page")) || 1;
    console.log(" Setting page:", newPage);
    setCurrentPage(newPage);
  }, [searchParams]);

  const fetchAvailableFarmers = async () => {
    try {
      console.log(" Starting comprehensive farmer fetch...");
      const farmers = [];

      // 1. Fetch from dedicated farmers API
      try {
        const response = await fetch("/api/farmers?limit=1000");
        if (response.ok) {
          const data = await response.json();
          console.log(" Fetched farmers from farmers API:", data);

          if (data.farmers && Array.isArray(data.farmers)) {
            data.farmers.forEach((farmer) => {
              if (farmer.name && !farmers.includes(farmer.name)) {
                farmers.push(farmer.name);
              }
            });
          }
        }
      } catch (error) {
        console.warn(" Farmers API failed:", error);
      }

      // 2. Fetch newly registered farmers from users collection
      try {
        const usersResponse = await fetch(
          "/api/auth/users?userType=farmer&limit=1000",
        );
        if (usersResponse.ok) {
          const usersData = await usersResponse.json();
          console.log(" Fetched farmers from users API:", usersData);

          if (usersData.users && Array.isArray(usersData.users)) {
            usersData.users.forEach((user) => {
              const farmerName =
                user.farmDetails?.farmName ||
                user.name ||
                `${user.firstName} ${user.lastName}`;
              if (farmerName && !farmers.includes(farmerName)) {
                farmers.push(farmerName);
              }
            });
          }
        } else {
          console.warn(
            " Users API not available, trying alternative approach",
          );
        }
      } catch (error) {
        console.warn(" Users API failed:", error);
      }

      // 3. Backup: get farmers from products API
      try {
        const productsResponse = await fetch("/api/products?limit=1000");
        if (productsResponse.ok) {
          const productsData = await productsResponse.json();
          console.log(
            " Fetched farmers from products API:",
            productsData.products?.length || 0,
            "products",
          );

          const productFarmers = [
            ...new Set(
              productsData.products.map((p) => p.farmer?.name).filter(Boolean),
            ),
          ];

          // Merge farmers from products
          productFarmers.forEach((farmerName) => {
            if (!farmers.includes(farmerName)) {
              farmers.push(farmerName);
            }
          });
        }
      } catch (error) {
        console.warn(" Products API failed:", error);
      }

      // Sort farmers alphabetically and update state
      farmers.sort();

      console.log(" Final comprehensive farmers list:", farmers);
      console.log(" Total farmers found:", farmers.length);
      setAvailableFarmers(farmers);
    } catch (error) {
      console.error(" Error in comprehensive farmer fetch:", error);
      // Ultimate fallback - at least try products API
      try {
        const response = await fetch("/api/products?limit=1000");
        if (response.ok) {
          const data = await response.json();
          const farmers = [
            ...new Set(
              data.products.map((p) => p.farmer?.name).filter(Boolean),
            ),
          ];
          farmers.sort();
          setAvailableFarmers(farmers);
          console.log(" Fallback farmers loaded:", farmers.length);
        }
      } catch (fallbackError) {
        console.error(" Complete failure in farmer fetch:", fallbackError);
        setAvailableFarmers([]);
      }
    }
  };

  const fetchProducts = async () => {
    console.log(" fetchProducts called with current state:", {
      searchTerm,
      selectedCategory,
      sortBy,
      currentPage,
    });

    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (searchTerm) params.append("search", searchTerm);
      if (selectedCategory !== "All Categories")
        params.append("category", selectedCategory);
      // Add high limit to fetch all products for client-side pagination
      params.append("limit", "1000");
      params.append("sortBy", sortBy);

      const apiUrl = `/api/products?${params}`;
      console.log(" API call URL:", apiUrl);

      const response = await fetch(apiUrl);
      if (response.ok) {
        let data = await response.json();
        let allProducts = data.products;

        console.log(" Raw products from API:", allProducts.length);
        console.log(
          " Sample of all products:",
          allProducts.slice(0, 3).map((p) => ({
            name: p.name,
            _id: p._id,
            farmerId: p.farmerId,
            farmerEmail: p.farmerEmail,
            category: p.category,
            price: p.price,
          })),
        );

        // Apply client-side filters to all products
        let filteredProducts = applyFilters(allProducts);

        console.log(" Products after filtering:", filteredProducts.length);

        // Always apply pagination regardless of filters
        const itemsPerPage = 12;
        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        const paginatedProducts = filteredProducts.slice(startIndex, endIndex);

        console.log(" Paginated products:", {
          startIndex,
          endIndex,
          paginatedCount: paginatedProducts.length,
          totalFiltered: filteredProducts.length,
        });

        setProducts(paginatedProducts);
        setPagination({
          currentPage: currentPage,
          totalPages: Math.ceil(filteredProducts.length / itemsPerPage),
          totalProducts: filteredProducts.length,
          hasNextPage:
            currentPage < Math.ceil(filteredProducts.length / itemsPerPage),
          hasPrevPage: currentPage > 1,
        });
      }
    } catch (error) {
      console.error(" Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = (products) => {
    console.log(" applyFilters called with:", {
      totalProducts: products.length,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
    });

    let filtered = [...products];
    const originalCount = filtered.length;

    // Price range filters
    if (selectedPriceRanges.length > 0) {
      const beforeFilter = filtered.length;
      filtered = filtered.filter((product) => {
        return selectedPriceRanges.some((range) => {
          const option = priceRangeOptions.find((opt) => opt.label === range);
          return product.price >= option.min && product.price <= option.max;
        });
      });
      console.log(" Price range filter:", {
        before: beforeFilter,
        after: filtered.length,
        ranges: selectedPriceRanges,
      });
    }

    // Custom price range slider
    const beforeSlider = filtered.length;
    const filteredOutProducts = [];

    filtered = filtered.filter((product) => {
      const withinRange =
        product.price >= priceRangeSlider[0] &&
        product.price <= priceRangeSlider[1];

      if (!withinRange) {
        filteredOutProducts.push({
          name: product.name,
          price: product.price,
          priceType: typeof product.price,
        });
      }

      return withinRange;
    });

    console.log(" Price slider filter:", {
      before: beforeSlider,
      after: filtered.length,
      range: priceRangeSlider,
      filteredOut: filteredOutProducts,
    });

    // Rating filters
    if (selectedRatings.length > 0) {
      const beforeRating = filtered.length;
      filtered = filtered.filter((product) => {
        const productRating = product.averageRating || 0;
        return selectedRatings.some((rating) => productRating >= rating);
      });
      console.log(" Rating filter:", {
        before: beforeRating,
        after: filtered.length,
        ratings: selectedRatings,
      });
    }

    // Farmer filters
    if (selectedFarmers.length > 0) {
      const beforeFarmer = filtered.length;
      filtered = filtered.filter((product) =>
        selectedFarmers.includes(product.farmer?.name),
      );
      console.log(" Farmer filter:", {
        before: beforeFarmer,
        after: filtered.length,
        farmers: selectedFarmers,
      });
    }

    // Tag filters
    if (selectedTags.length > 0) {
      const beforeTags = filtered.length;
      filtered = filtered.filter((product) => {
        return selectedTags.every((tag) => {
          switch (tag) {
            case "Organic":
              return product.isOrganic;
            case "Fresh":
              return product.isFresh;
            case "Local":
              return true; // Assuming all products are local
            case "Premium":
              return product.price > 200;
            case "Seasonal":
              return product.stock < 100;
            case "Limited Stock":
              return product.stock < 50;
            default:
              return false;
          }
        });
      });
      console.log(" Tag filter:", {
        before: beforeTags,
        after: filtered.length,
        tags: selectedTags,
      });
    }

    // Apply sorting
    const beforeSort = filtered.length;
    switch (sortBy) {
      case "price-low":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort(
          (a, b) => (b.averageRating || 0) - (a.averageRating || 0),
        );
        filtered.sort(
          (a, b) => (b.purchaseCount || 0) - (a.purchaseCount || 0),
        );
        break;
      case "newest":
        filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case "oldest":
        filtered.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
        break;
    }
    console.log(" Sorting applied:", { sortBy, count: filtered.length });

    console.log(" Final filtering result:", {
      original: originalCount,
      final: filtered.length,
      reduction: originalCount - filtered.length,
    });

    return filtered;
  };

  // Handle search functionality
  const handleSearch = () => {
    const params = new URLSearchParams();
    if (searchTerm) params.append("search", searchTerm);
    if (selectedCategory !== "All Categories")
      params.append("category", selectedCategory);
    router.push(`/products?${params.toString()}`);
    setCurrentPage(1);
    fetchProducts();
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  const handleCategoryChange = (category) => {
    console.log(" Category change clicked:", category);
    setSelectedCategory(category);
    setCurrentPage(1);
    // Don't call updateURL here - let the useEffect handle it
  };

  const handlePriceRangeChange = (range) => {
    console.log(" Price range clicked:", range);
    console.log(" Current selected price ranges:", selectedPriceRanges);

    const newPriceRanges = selectedPriceRanges.includes(range)
      ? selectedPriceRanges.filter((r) => r !== range)
      : [...selectedPriceRanges, range];

    console.log(" New price ranges:", newPriceRanges);

    setSelectedPriceRanges(newPriceRanges);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new price ranges:", newPriceRanges);
    updateURLWithFilters({
      selectedPriceRanges: newPriceRanges,
    });
  };

  const handleRatingChange = (rating) => {
    console.log(" Rating clicked:", rating);
    console.log(" Current selected ratings:", selectedRatings);

    const newRatings = selectedRatings.includes(rating)
      ? selectedRatings.filter((r) => r !== rating)
      : [...selectedRatings, rating];

    console.log(" New ratings:", newRatings);

    setSelectedRatings(newRatings);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new ratings:", newRatings);
    updateURLWithFilters({
      selectedRatings: newRatings,
    });
  };

  const handleFarmerChange = (farmer) => {
    console.log(" Farmer clicked:", farmer);
    console.log(" Current selected farmers:", selectedFarmers);

    const newFarmers = selectedFarmers.includes(farmer)
      ? selectedFarmers.filter((f) => f !== farmer)
      : [...selectedFarmers, farmer];

    console.log(" New farmers:", newFarmers);

    setSelectedFarmers(newFarmers);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new farmers:", newFarmers);
    updateURLWithFilters({
      selectedFarmers: newFarmers,
    });
  };

  const handleTagChange = (tag) => {
    console.log(" Tag clicked:", tag);
    console.log(" Current selected tags:", selectedTags);

    const newTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];

    console.log(" New tags:", newTags);

    setSelectedTags(newTags);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new tags:", newTags);
    updateURLWithFilters({
      selectedTags: newTags,
    });
  };

  const handlePriceSliderChange = (newPriceRange) => {
    console.log(" Price slider changed:", newPriceRange);
    console.log(" Current price slider:", priceRangeSlider);

    setPriceRangeSlider(newPriceRange);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new price slider:", newPriceRange);
    updateURLWithFilters({
      priceRangeSlider: newPriceRange,
    });
  };

  const handleSortChange = (newSort) => {
    console.log(" Sort changed:", newSort);
    console.log(" Current sort:", sortBy);

    setSortBy(newSort);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new sort:", newSort);
    updateURLWithFilters({
      sortBy: newSort,
    });
  };

  const clearAllFilters = () => {
    setSelectedCategory("All Categories");
    setSearchTerm("");
    setSelectedPriceRanges([]);
    setSelectedRatings([]);
    setSelectedFarmers([]);
    setSelectedTags([]);
    setPriceRangeSlider([0, 10000]);
    setCurrentPage(1);
    updateURL();
  };

  const updateURL = () => {
    console.log(" updateURL called with current state:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });

    const params = new URLSearchParams();

    // Add search term
    if (searchTerm) params.append("search", searchTerm);

    // Add category
    if (selectedCategory !== "All Categories")
      params.append("category", selectedCategory);

    // Add price ranges
    if (selectedPriceRanges.length > 0) {
      params.append("priceRanges", selectedPriceRanges.join(","));
    }

    // Add ratings
    if (selectedRatings.length > 0) {
      params.append("ratings", selectedRatings.join(","));
    }

    // Add farmers
    if (selectedFarmers.length > 0) {
      params.append("farmers", selectedFarmers.join(","));
    }

    // Add tags
    if (selectedTags.length > 0) {
      params.append("tags", selectedTags.join(","));
    }

    // Add price range slider
    if (priceRangeSlider[0] !== 0 || priceRangeSlider[1] !== 10000) {
      params.append("minPrice", priceRangeSlider[0]);
      params.append("maxPrice", priceRangeSlider[1]);
    }

    // Add sort option
    if (sortBy !== "newest") {
      params.append("sort", sortBy);
    }

    // Add current page if not first page
    if (currentPage > 1) {
      params.append("page", currentPage);
    }

    const newURL = `/products${params.toString() ? `?${params.toString()}` : ""}`;
    console.log(" Generated URL:", newURL);
    router.push(newURL, { shallow: true });
  };

  const updateURLWithFilters = (overrides = {}) => {
    console.log(" updateURLWithFilters called with overrides:", overrides);
    console.log(" Current state before overrides:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });

    const params = new URLSearchParams();

    // Use current state values or overrides
    const currentSearchTerm =
      overrides.searchTerm !== undefined ? overrides.searchTerm : searchTerm;
    const currentCategory =
      overrides.selectedCategory !== undefined
        ? overrides.selectedCategory
        : selectedCategory;
    const currentPriceRanges =
      overrides.selectedPriceRanges !== undefined
        ? overrides.selectedPriceRanges
        : selectedPriceRanges;
    const currentRatings =
      overrides.selectedRatings !== undefined
        ? overrides.selectedRatings
        : selectedRatings;
    const currentFarmers =
      overrides.selectedFarmers !== undefined
        ? overrides.selectedFarmers
        : selectedFarmers;
    const currentTags =
      overrides.selectedTags !== undefined
        ? overrides.selectedTags
        : selectedTags;
    const currentPriceSlider =
      overrides.priceRangeSlider !== undefined
        ? overrides.priceRangeSlider
        : priceRangeSlider;
    const currentSort =
      overrides.sortBy !== undefined ? overrides.sortBy : sortBy;
    const currentPageNumber =
      overrides.currentPage !== undefined ? overrides.currentPage : 1; // Reset to page 1 for filters

    console.log(" Final values to use:", {
      currentSearchTerm,
      currentCategory,
      currentPriceRanges,
      currentRatings,
      currentFarmers,
      currentTags,
      currentPriceSlider,
      currentSort,
      currentPageNumber,
    });

    // Add search term
    if (currentSearchTerm) params.append("search", currentSearchTerm);

    // Add category
    if (currentCategory !== "All Categories")
      params.append("category", currentCategory);

    // Add price ranges
    if (currentPriceRanges.length > 0) {
      params.append("priceRanges", currentPriceRanges.join(","));
    }

    // Add ratings
    if (currentRatings.length > 0) {
      params.append("ratings", currentRatings.join(","));
    }

    // Add farmers
    if (currentFarmers.length > 0) {
      params.append("farmers", currentFarmers.join(","));
    }

    // Add tags
    if (currentTags.length > 0) {
      params.append("tags", currentTags.join(","));
    }

    // Add price range slider
    if (currentPriceSlider[0] !== 0 || currentPriceSlider[1] !== 10000) {
      params.append("minPrice", currentPriceSlider[0]);
      params.append("maxPrice", currentPriceSlider[1]);
    }

    // Add sort option
    if (currentSort !== "newest") {
      params.append("sort", currentSort);
    }

    // Add current page if not first page
    if (currentPageNumber > 1) {
      params.append("page", currentPageNumber);
    }

    const newURL = `/products${params.toString() ? `?${params.toString()}` : ""}`;
    console.log(" Generated URL with filters:", newURL);
    router.push(newURL, { shallow: true });
  };

  const handlePageChange = (page) => {
    console.log(" Page change clicked:", page);
    setCurrentPage(page);
    // Update URL immediately for page changes
    updateURLWithFilters({
      currentPage: page,
    });
  };

  const getActiveFilterCount = () => {
    let count = 0;
    if (selectedCategory !== "All Categories") count++;
    if (selectedPriceRanges.length > 0) count++;
    if (selectedRatings.length > 0) count++;
    if (selectedFarmers.length > 0) count++;
    if (selectedTags.length > 0) count++;
    if (priceRangeSlider[0] !== 0 || priceRangeSlider[1] !== 10000) count++;
    return count;
  };

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Products Header */}
        <div className="bg-primary-600 text-white py-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h1 className="text-4xl font-bold mb-4">Fresh Products</h1>
            <p className="text-xl text-primary-100">
              Discover fresh, locally-sourced produce from our trusted farmers
            </p>

            {/* Search Results Info */}
            {(searchTerm || selectedCategory !== "All Categories") && (
              <div className="mt-6 p-4 bg-primary-700 rounded-lg">
                <div className="flex items-center justify-between flex-wrap gap-2">
                  <div>
                    {searchTerm && (
                      <p className="text-primary-100">
                        Search results for:{" "}
                        <span className="font-semibold text-white">
                          "{searchTerm}"
                        </span>
                      </p>
                    )}
                    {selectedCategory !== "All Categories" && (
                      <p className="text-primary-100">
                        Category:{" "}
                        <span className="font-semibold text-white">
                          {selectedCategory}
                        </span>
                      </p>
                    )}
                    <p className="text-sm text-primary-200">
                      {pagination.totalProducts} products found
                    </p>
                  </div>

                  {/* Search Bar in Results */}
                  <div className="flex gap-2">
                    <input
                      type="text"
                      placeholder="Search products..."
                      className="px-4 py-2 rounded-lg text-gray-900 focus:outline-none"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                    />
                    <button
                      onClick={handleSearch}
                      className="px-4 py-2 bg-primary-500 hover:bg-primary-400 rounded-lg transition"
                    >
                      <i className="fas fa-search"></i>
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Filters and Products */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Mobile Filter Toggle */}
          <div className="lg:hidden mb-6">
            <button
              onClick={() => setShowMobileFilters(!showMobileFilters)}
              className="w-full bg-white dark:bg-gray-800 text-gray-900 dark:text-white px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-between"
            >
              <span className="flex items-center">
                <i className="fas fa-filter mr-2"></i>
                Filters
                {getActiveFilterCount() > 0 && (
                  <span className="ml-2 bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                    {getActiveFilterCount()}
                  </span>
                )}
              </span>
              <i
                className={`fas fa-chevron-${showMobileFilters ? "up" : "down"}`}
              ></i>
            </button>
          </div>

          {/* Active Filters Summary */}
          {getActiveFilterCount() > 0 && (
            <div className="mb-6 p-4 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-medium text-gray-900 dark:text-white">
                  Active Filters ({getActiveFilterCount()})
                </h4>
                <button
                  onClick={clearAllFilters}
                  className="text-sm text-primary-600 hover:text-primary-700 dark:text-primary-400"
                >
                  Clear All
                </button>
              </div>
              <div className="flex flex-wrap gap-2">
                {selectedCategory !== "All Categories" && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-primary-100 text-primary-800 dark:bg-primary-900 dark:text-primary-200">
                    Category: {selectedCategory}
                    <button
                      onClick={() => handleCategoryChange("All Categories")}
                      className="ml-2 text-primary-600 hover:text-primary-800"
                    >

                    </button>
                  </span>
                )}
                {selectedPriceRanges.map((range) => (
                  <span
                    key={range}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                  >
                    {range}
                    <button
                      onClick={() => handlePriceRangeChange(range)}
                      className="ml-2 text-green-600 hover:text-green-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedRatings.map((rating) => (
                  <span
                    key={rating}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
                  >
                    {rating}+ Stars
                    <button
                      onClick={() => handleRatingChange(rating)}
                      className="ml-2 text-yellow-600 hover:text-yellow-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedFarmers.map((farmer) => (
                  <span
                    key={farmer}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
                  >
                    {farmer}
                    <button
                      onClick={() => handleFarmerChange(farmer)}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedTags.map((tag) => (
                  <span
                    key={tag}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200"
                  >
                    {tag}
                    <button
                      onClick={() => handleTagChange(tag)}
                      className="ml-2 text-purple-600 hover:text-purple-800"
                    >

                    </button>
                  </span>
                ))}
                {(priceRangeSlider[0] !== 0 ||
                  priceRangeSlider[1] !== 10000) && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
                    {priceRangeSlider[0]} - {priceRangeSlider[1]}
                    <button
                      onClick={() => setPriceRangeSlider([0, 10000])}
                      className="ml-2 text-orange-600 hover:text-orange-800"
                    >

                    </button>
                  </span>
                )}
              </div>
            </div>
          )}

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            {/* Filters Sidebar */}
            <div
              className={`lg:col-span-1 ${showMobileFilters ? "block" : "hidden lg:block"}`}
            >
              <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 sticky top-24">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    Filters
                  </h3>
                  {getActiveFilterCount() > 0 && (
                    <span className="bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                      {getActiveFilterCount()}
                    </span>
                  )}
                </div>

                {/* Category Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-th-large mr-2 text-primary-600"></i>
                    Category
                  </h4>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {categoryOptions.map((category) => (
                      <label
                        key={category}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="radio"
                          name="category"
                          checked={selectedCategory === category}
                          onChange={() => handleCategoryChange(category)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {category}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Price Range */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-dollar-sign mr-2 text-primary-600"></i>
                    Price Range ()
                  </h4>

                  {/* Custom Price Range Slider */}
                  <div className="mb-4">
                    <div className="relative">
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={priceRangeSlider[0]}
                        onChange={(e) =>
                          handlePriceSliderChange([
                            +e.target.value,
                            priceRangeSlider[1],
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={priceRangeSlider[1]}
                        onChange={(e) =>
                          handlePriceSliderChange([
                            priceRangeSlider[0],
                            +e.target.value,
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <div className="relative h-2 bg-gray-200 rounded-lg">
                        <div
                          className="absolute h-2 bg-primary-600 rounded-lg"
                          style={{
                            left: `${(priceRangeSlider[0] / 10000) * 100}%`,
                            width: `${((priceRangeSlider[1] - priceRangeSlider[0]) / 10000) * 100}%`,
                          }}
                        ></div>
                      </div>
                    </div>
                    <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mt-2">
                      <span>{priceRangeSlider[0]}</span>
                      <span>{priceRangeSlider[1]}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    {priceRangeOptions.map((option) => (
                      <label
                        key={option.label}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedPriceRanges.includes(option.label)}
                          onChange={() => handlePriceRangeChange(option.label)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {option.label}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Rating Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-star mr-2 text-primary-600"></i>
                    Rating
                  </h4>
                  <div className="space-y-2">
                    {[5, 4, 3, 2, 1].map((rating) => (
                      <label
                        key={rating}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedRatings.includes(rating)}
                          onChange={() => handleRatingChange(rating)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 flex items-center">
                          {[...Array(rating)].map((_, i) => (
                            <i
                              key={i}
                              className="fas fa-star text-yellow-400 text-xs mr-1"
                            ></i>
                          ))}
                          {rating < 5 && <span className="ml-1">& Up</span>}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Farmer Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-user mr-2 text-primary-600"></i>
                    Farmer
                  </h4>
                  <div className="space-y-2 max-h-32 overflow-y-auto">
                    {availableFarmers.length > 0 ? (
                      availableFarmers.map((farmer) => (
                        <label
                          key={farmer}
                          className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                        >
                          <input
                            type="checkbox"
                            checked={selectedFarmers.includes(farmer)}
                            onChange={() => handleFarmerChange(farmer)}
                            className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                          />
                          <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 truncate">
                            {farmer}
                          </span>
                        </label>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500 dark:text-gray-400 p-2">
                        Loading farmers...
                      </p>
                    )}
                  </div>
                </div>

                {/* Tag Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-tags mr-2 text-primary-600"></i>
                    Tags
                  </h4>
                  <div className="space-y-2">
                    {tagOptions.map((tag) => (
                      <label
                        key={tag}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedTags.includes(tag)}
                          onChange={() => handleTagChange(tag)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {tag}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Clear Filters */}
                <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
                  <button
                    onClick={clearAllFilters}
                    disabled={getActiveFilterCount() === 0}
                    className="w-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 py-2 rounded-lg font-medium hover:bg-gray-300 dark:hover:bg-gray-600 transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <i className="fas fa-times mr-2"></i>
                    Clear All Filters
                  </button>
                </div>
              </div>
            </div>

            {/* Products Grid */}
            <div className="lg:col-span-3">
              {/* Sort and View Options */}
              <div className="flex items-center justify-between mb-6 bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
                <div className="flex items-center space-x-4">
                  <span className="text-gray-700 dark:text-gray-300 font-medium">
                    <i className="fas fa-box mr-2 text-primary-600"></i>
                    {pagination.totalProducts} products
                  </span>
                  {loading && (
                    <div className="flex items-center text-primary-600">
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      <span className="text-sm">Loading...</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center space-x-4">
                  <label className="text-sm text-gray-700 dark:text-gray-300">
                    Sort by:
                  </label>
                  <select
                    value={sortBy}
                    onChange={(e) => handleSortChange(e.target.value)}
                    className="border border-gray-300 dark:border-gray-600 rounded-lg px-3 py-2 bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-primary-500 text-sm"
                  >
                    <option value="newest">Newest First</option>
                    <option value="oldest">Oldest First</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                    <option value="rating">Highest Rated</option>
                    <option value="popular">Most Popular</option>
                  </select>
                </div>
              </div>

              {/* Products Grid */}
              {loading ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(9)].map((_, index) => (
                    <div
                      key={index}
                      className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden animate-pulse"
                    >
                      <div className="w-full h-48 bg-gray-300 dark:bg-gray-600"></div>
                      <div className="p-6">
                        <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded mb-2"></div>
                        <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded mb-3 w-3/4"></div>
                        <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded mb-4 w-1/2"></div>
                        <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded"></div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : products.length > 0 ? (
                <>
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                    {products.map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>

                  {/* Pagination */}
                  {pagination.totalPages > 1 && (
                    <div className="mt-8 flex justify-center">
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handlePageChange(currentPage - 1)}
                          disabled={!pagination.hasPrevPage}
                          className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          Previous
                        </button>

                        {[...Array(Math.min(5, pagination.totalPages))].map(
                          (_, index) => {
                            const pageNum = index + 1;
                            return (
                              <button
                                key={pageNum}
                                onClick={() => handlePageChange(pageNum)}
                                className={`px-3 py-2 border rounded-lg ${
                                  currentPage === pageNum
                                    ? "bg-primary-600 text-white border-primary-600"
                                    : "border-gray-300 dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700"
                                }`}
                              >
                                {pageNum}
                              </button>
                            );
                          },
                        )}

                        <button
                          onClick={() => handlePageChange(currentPage + 1)}
                          disabled={!pagination.hasNextPage}
                          className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          Next
                        </button>
                      </div>
                    </div>
                  )}
                </>
              ) : (
                <div className="text-center py-12">
                  <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                  <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                    No products found
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400 mb-4">
                    Try adjusting your search criteria or browse all products
                  </p>
                  <Link
                    href="/products"
                    className="inline-block bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition"
                  >
                    Browse All Products
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import Link from "next/link";
import { useState, useEffect } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import ProductCard from "@/components/ProductCard";
import Footer from "@/components/Footer";

export default function Products() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [pagination, setPagination] = useState({});

  // Search and filter states
  const [searchTerm, setSearchTerm] = useState(
    searchParams.get("search") || "",
  );
  const [selectedCategory, setSelectedCategory] = useState(
    searchParams.get("category") || "All Categories",
  );
  const [selectedPriceRanges, setSelectedPriceRanges] = useState([]);
  const [selectedRatings, setSelectedRatings] = useState([]);
  const [selectedFarmers, setSelectedFarmers] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [priceRangeSlider, setPriceRangeSlider] = useState([0, 10000]); // Increased default max to 10000
  const [sortBy, setSortBy] = useState("newest");
  const [currentPage, setCurrentPage] = useState(1);
  const [showMobileFilters, setShowMobileFilters] = useState(false);
  const [availableFarmers, setAvailableFarmers] = useState([]);

  const categoryOptions = [
    "All Categories",
    "Vegetables",
    "Fruits",
    "Grains",
    "Dairy",
    "Honey",
    "Herbs",
  ];

  const priceRangeOptions = [
    { label: "Under 50", min: 0, max: 49 },
    { label: "50 - 100", min: 50, max: 100 },
    { label: "100 - 200", min: 101, max: 200 },
    { label: "200 - 500", min: 201, max: 500 },
    { label: "Above 500", min: 501, max: 9999 },
  ];

  const tagOptions = [
    "Organic",
    "Fresh",
    "Local",
    "Premium",
    "Seasonal",
    "Limited Stock",
  ];

  useEffect(() => {
    console.log(" Products useEffect triggered:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });
    fetchProducts();
    // Remove fetchAvailableFarmers from here to prevent unnecessary calls
  }, [
    searchTerm,
    selectedCategory,
    selectedPriceRanges,
    selectedRatings,
    selectedFarmers,
    selectedTags,
    priceRangeSlider,
    sortBy,
    currentPage,
  ]);

  // Separate useEffect for fetching farmers (only once)
  useEffect(() => {
    fetchAvailableFarmers();
  }, []); // Empty dependency array - only run once

  // Update URL when category changes
  useEffect(() => {
    console.log(" Category changed, updating URL:", selectedCategory);
    updateURLWithFilters({
      selectedCategory: selectedCategory,
    });
  }, [selectedCategory]);

  useEffect(() => {
    console.log(" URL params changed, updating state:", {
      searchParams: Object.fromEntries(searchParams.entries()),
    });

    // Update states from URL params
    const newSearchTerm = searchParams.get("search") || "";
    const newCategory = searchParams.get("category") || "All Categories";

    console.log(" Setting search term:", newSearchTerm);
    console.log(" Setting category:", newCategory);

    setSearchTerm(newSearchTerm);
    setSelectedCategory(newCategory);

    // Restore price ranges from URL
    const priceRanges = searchParams.get("priceRanges");
    if (priceRanges) {
      const newPriceRanges = priceRanges.split(",");
      console.log(" Setting price ranges:", newPriceRanges);
      setSelectedPriceRanges(newPriceRanges);
    } else {
      console.log(" Clearing price ranges");
      setSelectedPriceRanges([]);
    }

    // Restore ratings from URL
    const ratings = searchParams.get("ratings");
    if (ratings) {
      const newRatings = ratings.split(",").map(Number);
      console.log(" Setting ratings:", newRatings);
      setSelectedRatings(newRatings);
    } else {
      console.log(" Clearing ratings");
      setSelectedRatings([]);
    }

    // Restore farmers from URL
    const farmers = searchParams.get("farmers");
    if (farmers) {
      const newFarmers = farmers.split(",");
      console.log(" Setting farmers:", newFarmers);
      setSelectedFarmers(newFarmers);
    } else {
      console.log(" Clearing farmers");
      setSelectedFarmers([]);
    }

    // Restore tags from URL
    const tags = searchParams.get("tags");
    if (tags) {
      const newTags = tags.split(",");
      console.log(" Setting tags:", newTags);
      setSelectedTags(newTags);
    } else {
      console.log(" Clearing tags");
      setSelectedTags([]);
    }

    // Restore price range slider from URL
    const minPrice = searchParams.get("minPrice");
    const maxPrice = searchParams.get("maxPrice");
    if (minPrice && maxPrice) {
      const newPriceSlider = [Number(minPrice), Number(maxPrice)];
      console.log(" Setting price slider:", newPriceSlider);
      setPriceRangeSlider(newPriceSlider);
    } else {
      console.log(" Resetting price slider to default");
      setPriceRangeSlider([0, 10000]);
    }

    // Restore sort option from URL
    const newSort = searchParams.get("sort") || "newest";
    console.log(" Setting sort:", newSort);
    setSortBy(newSort);

    // Restore page from URL
    const newPage = Number(searchParams.get("page")) || 1;
    console.log(" Setting page:", newPage);
    setCurrentPage(newPage);
  }, [searchParams]);

  const fetchAvailableFarmers = async () => {
    try {
      console.log(" Starting comprehensive farmer fetch...");
      const farmers = [];

      // 1. Fetch from dedicated farmers API
      try {
        const response = await fetch("/api/farmers?limit=1000");
        if (response.ok) {
          const data = await response.json();
          console.log(" Fetched farmers from farmers API:", data);

          if (data.farmers && Array.isArray(data.farmers)) {
            data.farmers.forEach((farmer) => {
              if (farmer.name && !farmers.includes(farmer.name)) {
                farmers.push(farmer.name);
              }
            });
          }
        }
      } catch (error) {
        console.warn(" Farmers API failed:", error);
      }

      // 2. Fetch newly registered farmers from users collection
      try {
        const usersResponse = await fetch(
          "/api/auth/users?userType=farmer&limit=1000",
        );
        if (usersResponse.ok) {
          const usersData = await usersResponse.json();
          console.log(" Fetched farmers from users API:", usersData);

          if (usersData.users && Array.isArray(usersData.users)) {
            usersData.users.forEach((user) => {
              // Prioritize farmer's personal name over farm name
              const farmerName =
                user.name || `${user.firstName} ${user.lastName}` || user.farmDetails?.farmName;
              if (farmerName && !farmers.includes(farmerName)) {
                farmers.push(farmerName);
                console.log(" Added farmer from users:", farmerName);
              }
            });
          }
        } else {
          console.warn(
            " Users API not available, trying alternative approach",
          );
        }
      } catch (error) {
        console.warn(" Users API failed:", error);
      }

      // 3. Backup: get farmers from products API
      try {
        const productsResponse = await fetch("/api/products?limit=1000");
        if (productsResponse.ok) {
          const productsData = await productsResponse.json();
          console.log(
            " Fetched farmers from products API:",
            productsData.products?.length || 0,
            "products",
          );

          const productFarmers = [
            ...new Set(
              productsData.products.map((p) => p.farmer?.name).filter(Boolean),
            ),
          ];

          // Merge farmers from products
          productFarmers.forEach((farmerName) => {
            if (!farmers.includes(farmerName)) {
              farmers.push(farmerName);
            }
          });
        }
      } catch (error) {
        console.warn(" Products API failed:", error);
      }

      // Sort farmers alphabetically and update state
      farmers.sort();

      console.log(" Final comprehensive farmers list:", farmers);
      console.log(" Total farmers found:", farmers.length);
      setAvailableFarmers(farmers);
    } catch (error) {
      console.error(" Error in comprehensive farmer fetch:", error);
      // Ultimate fallback - at least try products API
      try {
        const response = await fetch("/api/products?limit=1000");
        if (response.ok) {
          const data = await response.json();
          const farmers = [
            ...new Set(
              data.products.map((p) => p.farmer?.name).filter(Boolean),
            ),
          ];
          farmers.sort();
          setAvailableFarmers(farmers);
          console.log(" Fallback farmers loaded:", farmers.length);
        }
      } catch (fallbackError) {
        console.error(" Complete failure in farmer fetch:", fallbackError);
        setAvailableFarmers([]);
      }
    }
  };

  const fetchProducts = async () => {
    console.log(" fetchProducts called with current state:", {
      searchTerm,
      selectedCategory,
      sortBy,
      currentPage,
    });

    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (searchTerm) params.append("search", searchTerm);
      if (selectedCategory !== "All Categories")
        params.append("category", selectedCategory);
      // Add high limit to fetch all products for client-side pagination
      params.append("limit", "1000");
      params.append("sortBy", sortBy);

      const apiUrl = `/api/products?${params}`;
      console.log(" API call URL:", apiUrl);

      const response = await fetch(apiUrl);
      if (response.ok) {
        let data = await response.json();
        let allProducts = data.products;

        console.log(" Raw products from API:", allProducts.length);
        console.log(
          " Sample of all products:",
          allProducts.slice(0, 3).map((p) => ({
            name: p.name,
            _id: p._id,
            farmerId: p.farmerId,
            farmerEmail: p.farmerEmail,
            category: p.category,
            price: p.price,
          })),
        );

        // Apply client-side filters to all products
        let filteredProducts = applyFilters(allProducts);

        console.log(" Products after filtering:", filteredProducts.length);

        // Always apply pagination regardless of filters
        const itemsPerPage = 12;
        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        const paginatedProducts = filteredProducts.slice(startIndex, endIndex);

        console.log(" Paginated products:", {
          startIndex,
          endIndex,
          paginatedCount: paginatedProducts.length,
          totalFiltered: filteredProducts.length,
        });

        setProducts(paginatedProducts);
        setPagination({
          currentPage: currentPage,
          totalPages: Math.ceil(filteredProducts.length / itemsPerPage),
          totalProducts: filteredProducts.length,
          hasNextPage:
            currentPage < Math.ceil(filteredProducts.length / itemsPerPage),
          hasPrevPage: currentPage > 1,
        });
      }
    } catch (error) {
      console.error(" Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = (products) => {
    console.log(" applyFilters called with:", {
      totalProducts: products.length,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
    });

    let filtered = [...products];
    const originalCount = filtered.length;

    // Price range filters
    if (selectedPriceRanges.length > 0) {
      const beforeFilter = filtered.length;
      filtered = filtered.filter((product) => {
        return selectedPriceRanges.some((range) => {
          const option = priceRangeOptions.find((opt) => opt.label === range);
          return product.price >= option.min && product.price <= option.max;
        });
      });
      console.log(" Price range filter:", {
        before: beforeFilter,
        after: filtered.length,
        ranges: selectedPriceRanges,
      });
    }

    // Custom price range slider
    const beforeSlider = filtered.length;
    const filteredOutProducts = [];

    filtered = filtered.filter((product) => {
      const withinRange =
        product.price >= priceRangeSlider[0] &&
        product.price <= priceRangeSlider[1];

      if (!withinRange) {
        filteredOutProducts.push({
          name: product.name,
          price: product.price,
          priceType: typeof product.price,
        });
      }

      return withinRange;
    });

    console.log(" Price slider filter:", {
      before: beforeSlider,
      after: filtered.length,
      range: priceRangeSlider,
      filteredOut: filteredOutProducts,
    });

    // Rating filters
    if (selectedRatings.length > 0) {
      const beforeRating = filtered.length;
      filtered = filtered.filter((product) => {
        const productRating = product.averageRating || 0;
        return selectedRatings.some((rating) => productRating >= rating);
      });
      console.log(" Rating filter:", {
        before: beforeRating,
        after: filtered.length,
        ratings: selectedRatings,
      });
    }

    // Farmer filters
    if (selectedFarmers.length > 0) {
      const beforeFarmer = filtered.length;
      filtered = filtered.filter((product) =>
        selectedFarmers.includes(product.farmer?.name),
      );
      console.log(" Farmer filter:", {
        before: beforeFarmer,
        after: filtered.length,
        farmers: selectedFarmers,
      });
    }

    // Tag filters
    if (selectedTags.length > 0) {
      const beforeTags = filtered.length;
      filtered = filtered.filter((product) => {
        return selectedTags.every((tag) => {
          switch (tag) {
            case "Organic":
              return product.isOrganic;
            case "Fresh":
              return product.isFresh;
            case "Local":
              return true; // Assuming all products are local
            case "Premium":
              return product.price > 200;
            case "Seasonal":
              return product.stock < 100;
            case "Limited Stock":
              return product.stock < 50;
            default:
              return false;
          }
        });
      });
      console.log(" Tag filter:", {
        before: beforeTags,
        after: filtered.length,
        tags: selectedTags,
      });
    }

    // Apply sorting
    const beforeSort = filtered.length;
    switch (sortBy) {
      case "price-low":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort(
          (a, b) => (b.averageRating || 0) - (a.averageRating || 0),
        );
        filtered.sort(
          (a, b) => (b.purchaseCount || 0) - (a.purchaseCount || 0),
        );
        break;
      case "newest":
        filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case "oldest":
        filtered.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
        break;
    }
    console.log(" Sorting applied:", { sortBy, count: filtered.length });

    console.log(" Final filtering result:", {
      original: originalCount,
      final: filtered.length,
      reduction: originalCount - filtered.length,
    });

    return filtered;
  };

  // Handle search functionality
  const handleSearch = () => {
    const params = new URLSearchParams();
    if (searchTerm) params.append("search", searchTerm);
    if (selectedCategory !== "All Categories")
      params.append("category", selectedCategory);
    router.push(`/products?${params.toString()}`);
    setCurrentPage(1);
    fetchProducts();
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  const handleCategoryChange = (category) => {
    console.log(" Category change clicked:", category);
    setSelectedCategory(category);
    setCurrentPage(1);
    // Don't call updateURL here - let the useEffect handle it
  };

  const handlePriceRangeChange = (range) => {
    console.log(" Price range clicked:", range);
    console.log(" Current selected price ranges:", selectedPriceRanges);

    const newPriceRanges = selectedPriceRanges.includes(range)
      ? selectedPriceRanges.filter((r) => r !== range)
      : [...selectedPriceRanges, range];

    console.log(" New price ranges:", newPriceRanges);

    setSelectedPriceRanges(newPriceRanges);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new price ranges:", newPriceRanges);
    updateURLWithFilters({
      selectedPriceRanges: newPriceRanges,
    });
  };

  const handleRatingChange = (rating) => {
    console.log(" Rating clicked:", rating);
    console.log(" Current selected ratings:", selectedRatings);

    const newRatings = selectedRatings.includes(rating)
      ? selectedRatings.filter((r) => r !== rating)
      : [...selectedRatings, rating];

    console.log(" New ratings:", newRatings);

    setSelectedRatings(newRatings);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new ratings:", newRatings);
    updateURLWithFilters({
      selectedRatings: newRatings,
    });
  };

  const handleFarmerChange = (farmer) => {
    console.log(" Farmer clicked:", farmer);
    console.log(" Current selected farmers:", selectedFarmers);

    const newFarmers = selectedFarmers.includes(farmer)
      ? selectedFarmers.filter((f) => f !== farmer)
      : [...selectedFarmers, farmer];

    console.log(" New farmers:", newFarmers);

    setSelectedFarmers(newFarmers);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new farmers:", newFarmers);
    updateURLWithFilters({
      selectedFarmers: newFarmers,
    });
  };

  const handleTagChange = (tag) => {
    console.log(" Tag clicked:", tag);
    console.log(" Current selected tags:", selectedTags);

    const newTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];

    console.log(" New tags:", newTags);

    setSelectedTags(newTags);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new tags:", newTags);
    updateURLWithFilters({
      selectedTags: newTags,
    });
  };

  const handlePriceSliderChange = (newPriceRange) => {
    console.log(" Price slider changed:", newPriceRange);
    console.log(" Current price slider:", priceRangeSlider);

    setPriceRangeSlider(newPriceRange);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new price slider:", newPriceRange);
    updateURLWithFilters({
      priceRangeSlider: newPriceRange,
    });
  };

  const handleSortChange = (newSort) => {
    console.log(" Sort changed:", newSort);
    console.log(" Current sort:", sortBy);

    setSortBy(newSort);
    setCurrentPage(1);

    // Immediate URL update
    console.log(" Updating URL with new sort:", newSort);
    updateURLWithFilters({
      sortBy: newSort,
    });
  };

  const clearAllFilters = () => {
    setSelectedCategory("All Categories");
    setSearchTerm("");
    setSelectedPriceRanges([]);
    setSelectedRatings([]);
    setSelectedFarmers([]);
    setSelectedTags([]);
    setPriceRangeSlider([0, 10000]);
    setCurrentPage(1);
    updateURL();
  };

  const updateURL = () => {
    console.log(" updateURL called with current state:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });

    const params = new URLSearchParams();

    // Add search term
    if (searchTerm) params.append("search", searchTerm);

    // Add category
    if (selectedCategory !== "All Categories")
      params.append("category", selectedCategory);

    // Add price ranges
    if (selectedPriceRanges.length > 0) {
      params.append("priceRanges", selectedPriceRanges.join(","));
    }

    // Add ratings
    if (selectedRatings.length > 0) {
      params.append("ratings", selectedRatings.join(","));
    }

    // Add farmers
    if (selectedFarmers.length > 0) {
      params.append("farmers", selectedFarmers.join(","));
    }

    // Add tags
    if (selectedTags.length > 0) {
      params.append("tags", selectedTags.join(","));
    }

    // Add price range slider
    if (priceRangeSlider[0] !== 0 || priceRangeSlider[1] !== 10000) {
      params.append("minPrice", priceRangeSlider[0]);
      params.append("maxPrice", priceRangeSlider[1]);
    }

    // Add sort option
    if (sortBy !== "newest") {
      params.append("sort", sortBy);
    }

    // Add current page if not first page
    if (currentPage > 1) {
      params.append("page", currentPage);
    }

    const newURL = `/products${params.toString() ? `?${params.toString()}` : ""}`;
    console.log(" Generated URL:", newURL);
    router.push(newURL, { shallow: true });
  };

  const updateURLWithFilters = (overrides = {}) => {
    console.log(" updateURLWithFilters called with overrides:", overrides);
    console.log(" Current state before overrides:", {
      searchTerm,
      selectedCategory,
      selectedPriceRanges,
      selectedRatings,
      selectedFarmers,
      selectedTags,
      priceRangeSlider,
      sortBy,
      currentPage,
    });

    const params = new URLSearchParams();

    // Use current state values or overrides
    const currentSearchTerm =
      overrides.searchTerm !== undefined ? overrides.searchTerm : searchTerm;
    const currentCategory =
      overrides.selectedCategory !== undefined
        ? overrides.selectedCategory
        : selectedCategory;
    const currentPriceRanges =
      overrides.selectedPriceRanges !== undefined
        ? overrides.selectedPriceRanges
        : selectedPriceRanges;
    const currentRatings =
      overrides.selectedRatings !== undefined
        ? overrides.selectedRatings
        : selectedRatings;
    const currentFarmers =
      overrides.selectedFarmers !== undefined
        ? overrides.selectedFarmers
        : selectedFarmers;
    const currentTags =
      overrides.selectedTags !== undefined
        ? overrides.selectedTags
        : selectedTags;
    const currentPriceSlider =
      overrides.priceRangeSlider !== undefined
        ? overrides.priceRangeSlider
        : priceRangeSlider;
    const currentSort =
      overrides.sortBy !== undefined ? overrides.sortBy : sortBy;
    const currentPageNumber =
      overrides.currentPage !== undefined ? overrides.currentPage : 1; // Reset to page 1 for filters

    console.log(" Final values to use:", {
      currentSearchTerm,
      currentCategory,
      currentPriceRanges,
      currentRatings,
      currentFarmers,
      currentTags,
      currentPriceSlider,
      currentSort,
      currentPageNumber,
    });

    // Add search term
    if (currentSearchTerm) params.append("search", currentSearchTerm);

    // Add category
    if (currentCategory !== "All Categories")
      params.append("category", currentCategory);

    // Add price ranges
    if (currentPriceRanges.length > 0) {
      params.append("priceRanges", currentPriceRanges.join(","));
    }

    // Add ratings
    if (currentRatings.length > 0) {
      params.append("ratings", currentRatings.join(","));
    }

    // Add farmers
    if (currentFarmers.length > 0) {
      params.append("farmers", currentFarmers.join(","));
    }

    // Add tags
    if (currentTags.length > 0) {
      params.append("tags", currentTags.join(","));
    }

    // Add price range slider
    if (currentPriceSlider[0] !== 0 || currentPriceSlider[1] !== 10000) {
      params.append("minPrice", currentPriceSlider[0]);
      params.append("maxPrice", currentPriceSlider[1]);
    }

    // Add sort option
    if (currentSort !== "newest") {
      params.append("sort", currentSort);
    }

    // Add current page if not first page
    if (currentPageNumber > 1) {
      params.append("page", currentPageNumber);
    }

    const newURL = `/products${params.toString() ? `?${params.toString()}` : ""}`;
    console.log(" Generated URL with filters:", newURL);
    router.push(newURL, { shallow: true });
  };

  const handlePageChange = (page) => {
    console.log(" Page change clicked:", page);
    setCurrentPage(page);
    // Update URL immediately for page changes
    updateURLWithFilters({
      currentPage: page,
    });
  };

  const getActiveFilterCount = () => {
    let count = 0;
    if (selectedCategory !== "All Categories") count++;
    if (selectedPriceRanges.length > 0) count++;
    if (selectedRatings.length > 0) count++;
    if (selectedFarmers.length > 0) count++;
    if (selectedTags.length > 0) count++;
    if (priceRangeSlider[0] !== 0 || priceRangeSlider[1] !== 10000) count++;
    return count;
  };

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Products Header */}
        <div className="bg-primary-600 text-white py-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h1 className="text-4xl font-bold mb-4">Fresh Products</h1>
            <p className="text-xl text-primary-100">
              Discover fresh, locally-sourced produce from our trusted farmers
            </p>

            {/* Search Results Info */}
            {(searchTerm || selectedCategory !== "All Categories") && (
              <div className="mt-6 p-4 bg-primary-700 rounded-lg">
                <div className="flex items-center justify-between flex-wrap gap-2">
                  <div>
                    {searchTerm && (
                      <p className="text-primary-100">
                        Search results for:{" "}
                        <span className="font-semibold text-white">
                          "{searchTerm}"
                        </span>
                      </p>
                    )}
                    {selectedCategory !== "All Categories" && (
                      <p className="text-primary-100">
                        Category:{" "}
                        <span className="font-semibold text-white">
                          {selectedCategory}
                        </span>
                      </p>
                    )}
                    <p className="text-sm text-primary-200">
                      {pagination.totalProducts} products found
                    </p>
                  </div>

                  {/* Search Bar in Results */}
                  <div className="flex gap-2">
                    <input
                      type="text"
                      placeholder="Search products..."
                      className="px-4 py-2 rounded-lg text-gray-900 focus:outline-none"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                    />
                    <button
                      onClick={handleSearch}
                      className="px-4 py-2 bg-primary-500 hover:bg-primary-400 rounded-lg transition"
                    >
                      <i className="fas fa-search"></i>
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Filters and Products */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Mobile Filter Toggle */}
          <div className="lg:hidden mb-6">
            <button
              onClick={() => setShowMobileFilters(!showMobileFilters)}
              className="w-full bg-white dark:bg-gray-800 text-gray-900 dark:text-white px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-between"
            >
              <span className="flex items-center">
                <i className="fas fa-filter mr-2"></i>
                Filters
                {getActiveFilterCount() > 0 && (
                  <span className="ml-2 bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                    {getActiveFilterCount()}
                  </span>
                )}
              </span>
              <i
                className={`fas fa-chevron-${showMobileFilters ? "up" : "down"}`}
              ></i>
            </button>
          </div>

          {/* Active Filters Summary */}
          {getActiveFilterCount() > 0 && (
            <div className="mb-6 p-4 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-medium text-gray-900 dark:text-white">
                  Active Filters ({getActiveFilterCount()})
                </h4>
                <button
                  onClick={clearAllFilters}
                  className="text-sm text-primary-600 hover:text-primary-700 dark:text-primary-400"
                >
                  Clear All
                </button>
              </div>
              <div className="flex flex-wrap gap-2">
                {selectedCategory !== "All Categories" && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-primary-100 text-primary-800 dark:bg-primary-900 dark:text-primary-200">
                    Category: {selectedCategory}
                    <button
                      onClick={() => handleCategoryChange("All Categories")}
                      className="ml-2 text-primary-600 hover:text-primary-800"
                    >

                    </button>
                  </span>
                )}
                {selectedPriceRanges.map((range) => (
                  <span
                    key={range}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                  >
                    {range}
                    <button
                      onClick={() => handlePriceRangeChange(range)}
                      className="ml-2 text-green-600 hover:text-green-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedRatings.map((rating) => (
                  <span
                    key={rating}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
                  >
                    {rating}+ Stars
                    <button
                      onClick={() => handleRatingChange(rating)}
                      className="ml-2 text-yellow-600 hover:text-yellow-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedFarmers.map((farmer) => (
                  <span
                    key={farmer}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
                  >
                    {farmer}
                    <button
                      onClick={() => handleFarmerChange(farmer)}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >

                    </button>
                  </span>
                ))}
                {selectedTags.map((tag) => (
                  <span
                    key={tag}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200"
                  >
                    {tag}
                    <button
                      onClick={() => handleTagChange(tag)}
                      className="ml-2 text-purple-600 hover:text-purple-800"
                    >

                    </button>
                  </span>
                ))}
                {(priceRangeSlider[0] !== 0 ||
                  priceRangeSlider[1] !== 10000) && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
                    {priceRangeSlider[0]} - {priceRangeSlider[1]}
                    <button
                      onClick={() => setPriceRangeSlider([0, 10000])}
                      className="ml-2 text-orange-600 hover:text-orange-800"
                    >

                    </button>
                  </span>
                )}
              </div>
            </div>
          )}

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            {/* Filters Sidebar */}
            <div
              className={`lg:col-span-1 ${showMobileFilters ? "block" : "hidden lg:block"}`}
            >
              <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 sticky top-24">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    Filters
                  </h3>
                  {getActiveFilterCount() > 0 && (
                    <span className="bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                      {getActiveFilterCount()}
                    </span>
                  )}
                </div>

                {/* Category Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-th-large mr-2 text-primary-600"></i>
                    Category
                  </h4>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {categoryOptions.map((category) => (
                      <label
                        key={category}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="radio"
                          name="category"
                          checked={selectedCategory === category}
                          onChange={() => handleCategoryChange(category)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {category}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Price Range */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-dollar-sign mr-2 text-primary-600"></i>
                    Price Range ()
                  </h4>

                  {/* Custom Price Range Slider */}
                  <div className="mb-4">
                    <div className="relative">
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={priceRangeSlider[0]}
                        onChange={(e) =>
                          handlePriceSliderChange([
                            +e.target.value,
                            priceRangeSlider[1],
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={priceRangeSlider[1]}
                        onChange={(e) =>
                          handlePriceSliderChange([
                            priceRangeSlider[0],
                            +e.target.value,
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <div className="relative h-2 bg-gray-200 rounded-lg">
                        <div
                          className="absolute h-2 bg-primary-600 rounded-lg"
                          style={{
                            left: `${(priceRangeSlider[0] / 10000) * 100}%`,
                            width: `${((priceRangeSlider[1] - priceRangeSlider[0]) / 10000) * 100}%`,
                          }}
                        ></div>
                      </div>
                    </div>
                    <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mt-2">
                      <span>{priceRangeSlider[0]}</span>
                      <span>{priceRangeSlider[1]}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    {priceRangeOptions.map((option) => (
                      <label
                        key={option.label}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedPriceRanges.includes(option.label)}
                          onChange={() => handlePriceRangeChange(option.label)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {option.label}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Rating Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-star mr-2 text-primary-600"></i>
                    Rating
                  </h4>
                  <div className="space-y-2">
                    {[5, 4, 3, 2, 1].map((rating) => (
                      <label
                        key={rating}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedRatings.includes(rating)}
                          onChange={() => handleRatingChange(rating)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 flex items-center">
                          {[...Array(rating)].map((_, i) => (
                            <i
                              key={i}
                              className="fas fa-star text-yellow-400 text-xs mr-1"
                            ></i>
                          ))}
                          {rating < 5 && <span className="ml-1">& Up</span>}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Farmer Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-user mr-2 text-primary-600"></i>
                    Farmer
                  </h4>
                  <div className="space-y-2 max-h-32 overflow-y-auto">
                    {availableFarmers.length > 0 ? (
                      availableFarmers.map((farmer) => (
                        <label
                          key={farmer}
                          className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                        >
                          <input
                            type="checkbox"
                            checked={selectedFarmers.includes(farmer)}
                            onChange={() => handleFarmerChange(farmer)}
                            className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                          />
                          <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 truncate">
                            {farmer}
                          </span>
                        </label>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500 dark:text-gray-400 p-2">
                        Loading farmers...
                      </p>
                    )}
                  </div>
                </div>

                {/* Tag Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-tags mr-2 text-primary-600"></i>
                    Tags
                  </h4>
                  <div className="space-y-2">
                    {tagOptions.map((tag) => (
                      <label
                        key={tag}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={selectedTags.includes(tag)}
                          onChange={() => handleTagChange(tag)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {tag}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Clear Filters */}
                <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
                  <button
                    onClick={clearAllFilters}
                    disabled={getActiveFilterCount() === 0}
                    className="w-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 py-2 rounded-lg font-medium hover:bg-gray-300 dark:hover:bg-gray-600 transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <i className="fas fa-times mr-2"></i>
                    Clear All Filters
                  </button>
                </div>
              </div>
            </div>

            {/* Products Grid */}
            <div className="lg:col-span-3">
              {/* Sort and View Options */}
              <div className="flex items-center justify-between mb-6 bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
                <div className="flex items-center space-x-4">
                  <span className="text-gray-700 dark:text-gray-300 font-medium">
                    <i className="fas fa-box mr-2 text-primary-600"></i>
                    {pagination.totalProducts} products
                  </span>
                  {loading && (
                    <div className="flex items-center text-primary-600">
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      <span className="text-sm">Loading...</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center space-x-4">
                  <label className="text-sm text-gray-700 dark:text-gray-300">
                    Sort by:
                  </label>
                  <select
                    value={sortBy}
                    onChange={(e) => handleSortChange(e.target.value)}
                    className="border border-gray-300 dark:border-gray-600 rounded-lg px-3 py-2 bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-primary-500 text-sm"
                  >
                    <option value="newest">Newest First</option>
                    <option value="oldest">Oldest First</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                    <option value="rating">Highest Rated</option>
                    <option value="popular">Most Popular</option>
                  </select>
                </div>
              </div>

              {/* Products Grid */}
              {loading ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(9)].map((_, index) => (
                    <div
                      key={index}
                      className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden animate-pulse"
                    >
                      <div className="w-full h-48 bg-gray-300 dark:bg-gray-600"></div>
                      <div className="p-6">
                        <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded mb-2"></div>
                        <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded mb-3 w-3/4"></div>
                        <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded mb-4 w-1/2"></div>
                        <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded"></div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : products.length > 0 ? (
                <>
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                    {products.map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>

                  {/* Pagination */}
                  {pagination.totalPages > 1 && (
                    <div className="mt-8 flex justify-center">
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handlePageChange(currentPage - 1)}
                          disabled={!pagination.hasPrevPage}
                          className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          Previous
                        </button>

                        {[...Array(Math.min(5, pagination.totalPages))].map(
                          (_, index) => {
                            const pageNum = index + 1;
                            return (
                              <button
                                key={pageNum}
                                onClick={() => handlePageChange(pageNum)}
                                className={`px-3 py-2 border rounded-lg ${
                                  currentPage === pageNum
                                    ? "bg-primary-600 text-white border-primary-600"
                                    : "border-gray-300 dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700"
                                }`}
                              >
                                {pageNum}
                              </button>
                            );
                          },
                        )}

                        <button
                          onClick={() => handlePageChange(currentPage + 1)}
                          disabled={!pagination.hasNextPage}
                          className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 dark:hover:bg-gray-700"
                        >
                          Next
                        </button>
                      </div>
                    </div>
                  )}
                </>
              ) : (
                <div className="text-center py-12">
                  <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                  <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                    No products found
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400 mb-4">
                    Try adjusting your search criteria or browse all products
                  </p>
                  <Link
                    href="/products"
                    className="inline-block bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition"
                  >
                    Browse All Products
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default function AuthLayout({ children, modal }) {
  return (
    <>
      {children}
      {modal}
    </>
  );
}

export default function Default() {
  return null;
}

import NextAuth from "next-auth";
import GoogleProvider from "next-auth/providers/google";
import CredentialsProvider from "next-auth/providers/credentials";
import clientPromise from "@/lib/mongodb";
import bcrypt from "bcryptjs";
import { generateTokens } from "@/lib/jwt";

export const authOptions = {
  providers: [
    GoogleProvider({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
      allowDangerousEmailAccountLinking: true,

import NextAuth from "next-auth";
import GoogleProvider from "next-auth/providers/google";
import CredentialsProvider from "next-auth/providers/credentials";
import clientPromise from "@/lib/mongodb";
import bcrypt from "bcryptjs";
import { generateTokens } from "@/lib/jwt";

export const authOptions = {
  providers: [
    GoogleProvider({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
      allowDangerousEmailAccountLinking: true,
      authorization: {
        params: {
          prompt: "select_account",
        },
      },
    }),
    CredentialsProvider({
      name: "credentials",
      credentials: {
        email: { label: "Email", type: "email" },
        password: { label: "Password", type: "password" },
      },
      async authorize(credentials) {
        try {
          const client = await clientPromise;
          const db = client.db("farmfresh");
          const users = db.collection("users");

          const user = await users.findOne({ email: credentials.email });

          if (
            user &&
            (await bcrypt.compare(credentials.password, user.password))
          ) {
            return {
              id: user._id,
              email: user.email,
              name: user.name,
              userType: user.userType,
              image: user.image,
            };
          }
          return null;
        } catch (error) {
          return null;
        }
      },
    }),
  ],
  session: {
    strategy: "jwt",
    maxAge: 30 * 24 * 60 * 60, // 30 days
  },
  callbacks: {
    async jwt({ token, user, account }) {
      if (user) {
        token.userType = user.userType;
        token.userId = user.id;

        const tokens = generateTokens(user);
        token.accessToken = tokens.accessToken;
        token.refreshToken = tokens.refreshToken;
      }
      return token;
    },
    async session({ session, token }) {
      session.user.userType = token.userType;
      session.user.userId = token.userId;
      session.accessToken = token.accessToken;
      session.refreshToken = token.refreshToken;
      return session;
    },
    async signIn({ user, account, profile }) {
      if (account.provider === "google") {
        try {
          const client = await clientPromise;
          const db = client.db("farmfresh");
          const users = db.collection("users");

          const existingUser = await users.findOne({ email: user.email });

          if (!existingUser) {
            const newUser = {
              email: user.email,
              name: user.name,
              firstName: user.name?.split(" ")[0] || "",
              lastName: user.name?.split(" ").slice(1).join(" ") || "",
              image: user.image,
              userType: "customer",
              provider: "google",
              googleId: profile.sub,
              phone: "",
              address: "",
              bio: "",
              emailVerified: true,
              createdAt: new Date(),
              updatedAt: new Date(),
            };

            const result = await users.insertOne(newUser);
            user.id = result.insertedId.toString();
            user.userType = "customer";
          } else {
            if (!existingUser.googleId) {
              await users.updateOne(
                { email: user.email },
                {
                  $set: {
                    googleId: profile.sub,
                    provider: "google",
                    image: user.image || existingUser.image,
                    emailVerified: true,
                    updatedAt: new Date(),
                  },
                },
              );
            }
            user.id = existingUser._id.toString();
            user.userType = existingUser.userType;
          }
        } catch (error) {
          return false;
        }
      }
      return true;
    },
  },
  pages: {
    signIn: "/login",
    signUp: "/register",
    error: "/login",
  },
  secret: process.env.NEXTAUTH_SECRET,
};

const handler = NextAuth(authOptions);
export { handler as GET, handler as POST };

"use client";

import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useRouter, useSearchParams } from "next/navigation";
import Link from "next/link";
import Image from "next/image";

export default function CreateProduct() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const searchParams = useSearchParams();
  const [loading, setLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editProductId, setEditProductId] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
    price: "",
    stock: "",
    unit: "",
    features: [],
    image: "",
  });

  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      // Check if user is a farmer
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }

      // Check if we're in edit mode
      const editId = searchParams.get("edit");
      if (editId) {
        setIsEditMode(true);
        setEditProductId(editId);
        fetchProductForEdit(editId);
      }
    }
  }, [session, status, router, searchParams]);

  const fetchProductForEdit = async (productId) => {
    try {
      setLoading(true);
      const response = await fetch(`/api/products/${productId}`);

      if (response.ok) {
        const data = await response.json();
        const product = data.product;

        // Pre-populate form with existing product data
        setFormData({
          name: product.name || "",
          category: product.category || "",
          description: product.description || "",
          price: product.price?.toString() || "",
          stock: product.stock?.toString() || "",
          unit: product.unit || "",
          features: product.features || [],
        });
      } else {
        console.error("Failed to fetch product for editing");
        alert(
          "Failed to load product data. Redirecting to create new product.",
        );
        // Reset to create mode
        setIsEditMode(false);
        setEditProductId(null);
      }
    } catch (error) {
      console.error("Error fetching product for edit:", error);
      alert("Error loading product data. Redirecting to create new product.");
      setIsEditMode(false);
      setEditProductId(null);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      features: checked
        ? [...prev.features, value]
        : prev.features.filter((feature) => feature !== value),
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      alert("Product name is required");
      return false;
    }
    if (!formData.category) {
      alert("Category is required");
      return false;
    }
    if (!formData.description.trim()) {
      alert("Description is required");
      return false;
    }
    if (!formData.price || parseFloat(formData.price) <= 0) {
      alert("Valid price is required");
      return false;
    }
    if (!formData.stock || parseInt(formData.stock) < 0) {
      alert("Valid stock quantity is required");
      return false;
    }
    if (!formData.unit) {
      alert("Unit is required");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      // Use the same user ID logic as in manage page
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        farmerId: userId,
        farmerEmail: userEmail,
        farmer: {
          id: userId,
          email: userEmail,
          name: session.user.name,
        },
        status: "active",
        updatedAt: new Date().toISOString(),
      };

      // Only add createdAt for new products
      if (!isEditMode) {
        productData.createdAt = new Date().toISOString();
      }

      console.log(
        isEditMode ? "Updating product:" : "Creating product:",
        productData,
      );

      const url = isEditMode
        ? `/api/products/${editProductId}`
        : "/api/products";
      const method = isEditMode ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const result = await response.json();
        console.log(
          isEditMode
            ? "Product updated successfully:"
            : "Product created successfully:",
          result,
        );

        alert(
          isEditMode
            ? "Product updated successfully!"
            : "Product added successfully!",
        );

        // Reset form only for new products
        if (!isEditMode) {
          setFormData({
            name: "",
            category: "",
            description: "",
            price: "",
            stock: "",
            unit: "",
            features: [],
          });
        }

        // Redirect to manage products page
        router.push("/manage");
      } else {
        const errorData = await response.json();
        console.error(
          isEditMode
            ? "Failed to update product:"
            : "Failed to create product:",
          errorData,
        );
        alert(
          `Failed to ${isEditMode ? "update" : "add"} product: ${
            errorData.error || "Unknown error"
          }`,
        );
      }
    } catch (error) {
      console.error(
        isEditMode ? "Error updating product:" : "Error creating product:",
        error,
      );
      alert(
        `Failed to ${isEditMode ? "update" : "add"} product. Please try again.`,
      );
    } finally {
      setLoading(false);
    }
  };

  if (status === "loading") {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-primary-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      {/* Navigation */}
      <nav className="bg-white dark:bg-gray-800 shadow-lg sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <Link href="/" className="flex items-center space-x-3">
                <div className="bg-green-500 p-2 rounded-lg">
                  <i className="fas fa-seedling text-white text-xl"></i>
                </div>
                <div>
                  <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                    FarmFresh
                  </h1>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    Local Farmer Booking
                  </p>
                </div>
              </Link>
            </div>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-8">
              <Link
                href="/"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Home
              </Link>
              <Link
                href="/products"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Products
              </Link>
              <Link
                href="/farmers"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Farmers
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">
              {isEditMode ? "Edit Product" : "Add New Product"}
            </h1>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Product Name */}
              <div>
                <label
                  htmlFor="name"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Product Name *
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  placeholder="Enter product name"
                />
              </div>

              {/* Category */}
              <div>
                <label
                  htmlFor="category"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Category *
                </label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="">Select Category</option>
                  <option value="Vegetables">Vegetables</option>
                  <option value="Fruits">Fruits</option>
                  <option value="Grains">Grains</option>
                  <option value="Dairy">Dairy</option>
                  <option value="Honey">Honey</option>
                  <option value="Herbs">Herbs</option>
                </select>
              </div>

              {/* Description */}
              <div>
                <label
                  htmlFor="description"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Description *
                </label>
                <textarea
                  id="description"
                  name="description"
                  rows={4}
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  placeholder="Describe your product..."
                />
              </div>

              {/* Price and Stock */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="price"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Price () *
                  </label>
                  <input
                    type="number"
                    id="price"
                    name="price"
                    min="0"
                    step="0.01"
                    value={formData.price}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="0.00"
                  />
                </div>

                <div>
                  <label
                    htmlFor="stock"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Stock Quantity *
                  </label>
                  <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    value={formData.stock}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="0"
                  />
                </div>
              </div>

              {/* Unit */}
              <div>
                <label
                  htmlFor="unit"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Unit *
                </label>
                <select
                  id="unit"
                  name="unit"
                  value={formData.unit}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="">Select Unit</option>
                  <option value="kg">Kilogram (kg)</option>
                  <option value="g">Gram (g)</option>
                  <option value="piece">Piece</option>
                  <option value="bunch">Bunch</option>
                  <option value="liter">Liter</option>
                  <option value="ml">Milliliter (ml)</option>
                </select>
              </div>

              {/* Image Upload */}
              <div>
                <label
                  htmlFor="images"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Product Images
                </label>
                <input
                  type="file"
                  id="images"
                  name="images"
                  multiple
                  accept="image/*"
                  onChange={handleImageChange}
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
                <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                  Upload up to 5 images (JPG, PNG, WebP)
                </p>

                {/* Image Preview */}
                {imagePreview && (
                  <div className="mt-4">
                    <Image
                      src={imagePreview}
                      alt="Image Preview"
                      width={500}
                      height={300}
                      className="rounded-lg object-cover"
                    />
                  </div>
                )}
              </div>

              {/* Features */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Features
                </label>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  {["organic", "fresh", "local", "seasonal", "premium"].map(
                    (feature) => (
                      <label key={feature} className="flex items-center">
                        <input
                          type="checkbox"
                          name="features"
                          value={feature}
                          checked={formData.features.includes(feature)}
                          onChange={handleFeatureChange}
                          className="mr-2 text-green-600 focus:ring-green-500"
                        />
                        <span className="text-gray-700 dark:text-gray-300 capitalize">
                          {feature}
                        </span>
                      </label>
                    ),
                  )}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-4 pt-6">
                <button
                  type="submit"
                  disabled={loading}
                  className={`flex-1 py-3 px-6 rounded-lg font-medium transition ${
                    loading
                      ? "bg-gray-400 cursor-not-allowed text-white"
                      : "bg-green-600 hover:bg-green-700 text-white"
                  }`}
                >
                  {loading ? (
                    <>
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      {isEditMode ? "Updating Product..." : "Adding Product..."}
                    </>
                  ) : (
                    <>
                      <i className="fas fa-plus mr-2"></i>
                      {isEditMode ? "Update Product" : "Add Product"}
                    </>
                  )}
                </button>

                <Link
                  href="/manage"
                  className="flex-1 bg-gray-600 hover:bg-gray-700 text-white py-3 px-6 rounded-lg font-medium text-center transition"
                >
                  <i className="fas fa-list mr-2"></i>
                  Manage Products
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useRouter, useSearchParams } from "next/navigation";
import Link from "next/link";
import Image from "next/image";

export default function CreateProduct() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const searchParams = useSearchParams();
  const [loading, setLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editProductId, setEditProductId] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
    price: "",
    stock: "",
    unit: "",
    features: [],
    image: "",
  });

  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      // Check if user is a farmer
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }

      // Check if we're in edit mode
      const editId = searchParams.get("edit");
      if (editId) {
        setIsEditMode(true);
        setEditProductId(editId);
        fetchProductForEdit(editId);
      }
    }
  }, [session, status, router, searchParams]);

  const fetchProductForEdit = async (productId) => {
    try {
      setLoading(true);
      const response = await fetch(`/api/products/${productId}`);

      if (response.ok) {
        const data = await response.json();
        const product = data.product;

        // Pre-populate form with existing product data
        setFormData({
          name: product.name || "",
          category: product.category || "",
          description: product.description || "",
          price: product.price?.toString() || "",
          stock: product.stock?.toString() || "",
          unit: product.unit || "",
          features: product.features || [],
          image: product.image || "",
        });

        // Set image preview if product has an image
        if (product.image) {
          setImagePreview(product.image);
        }
      } else {
        console.error("Failed to fetch product for editing");
        alert(
          "Failed to load product data. Redirecting to create new product.",
        );
        // Reset to create mode
        setIsEditMode(false);
        setEditProductId(null);
      }
    } catch (error) {
      console.error("Error fetching product for edit:", error);
      alert("Error loading product data. Redirecting to create new product.");
      setIsEditMode(false);
      setEditProductId(null);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      features: checked
        ? [...prev.features, value]
        : prev.features.filter((feature) => feature !== value),
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert("Image size must be less than 5MB");
        return;
      }

      // Validate file type
      if (!file.type.startsWith("image/")) {
        alert("Please select a valid image file");
        return;
      }

      setImageFile(file);
      setImagePreview(URL.createObjectURL(file));

      // Convert image to base64 for storage
      const reader = new FileReader();
      reader.onload = (event) => {
        const base64String = event.target.result;
        setFormData((prev) => ({
          ...prev,
          image: base64String,
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      alert("Product name is required");
      return false;
    }
    if (!formData.category) {
      alert("Category is required");
      return false;
    }
    if (!formData.description.trim()) {
      alert("Description is required");
      return false;
    }
    if (!formData.price || parseFloat(formData.price) <= 0) {
      alert("Valid price is required");
      return false;
    }
    if (!formData.stock || parseInt(formData.stock) < 0) {
      alert("Valid stock quantity is required");
      return false;
    }
    if (!formData.unit) {
      alert("Unit is required");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      // Use the same user ID logic as in manage page
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        farmerId: userId,
        farmerEmail: userEmail,
        farmer: {
          id: userId,
          email: userEmail,
          name: session.user.name,
        },
        status: "active",
        updatedAt: new Date().toISOString(),
      };

      // Only add createdAt for new products
      if (!isEditMode) {
        productData.createdAt = new Date().toISOString();
      }

      console.log(
        isEditMode ? "Updating product:" : "Creating product:",
        productData,
      );

      const url = isEditMode
        ? `/api/products/${editProductId}`
        : "/api/products";
      const method = isEditMode ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const result = await response.json();
        console.log(
          isEditMode
            ? "Product updated successfully:"
            : "Product created successfully:",
          result,
        );

        alert(
          isEditMode
            ? "Product updated successfully!"
            : "Product added successfully!",
        );

        // Reset form only for new products
        if (!isEditMode) {
          setFormData({
            name: "",
            category: "",
            description: "",
            price: "",
            stock: "",
            unit: "",
            features: [],
            image: "",
          });
          setImagePreview(null);
          setImageFile(null);
        }

        // Redirect to manage products page
        router.push("/manage");
      } else {
        const errorData = await response.json();
        console.error(
          isEditMode
            ? "Failed to update product:"
            : "Failed to create product:",
          errorData,
        );
        alert(
          `Failed to ${isEditMode ? "update" : "add"} product: ${
            errorData.error || "Unknown error"
          }`,
        );
      }
    } catch (error) {
      console.error(
        isEditMode ? "Error updating product:" : "Error creating product:",
        error,
      );
      alert(
        `Failed to ${isEditMode ? "update" : "add"} product. Please try again.`,
      );
    } finally {
      setLoading(false);
    }
  };

  if (status === "loading") {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-primary-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      {/* Navigation */}
      <nav className="bg-white dark:bg-gray-800 shadow-lg sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <Link href="/" className="flex items-center space-x-3">
                <div className="bg-green-500 p-2 rounded-lg">
                  <i className="fas fa-seedling text-white text-xl"></i>
                </div>
                <div>
                  <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                    FarmFresh
                  </h1>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    Local Farmer Booking
                  </p>
                </div>
              </Link>
            </div>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-8">
              <Link
                href="/"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Home
              </Link>
              <Link
                href="/products"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Products
              </Link>
              <Link
                href="/farmers"
                className="text-gray-700 dark:text-gray-300 hover:text-green-600 dark:hover:text-green-400 transition"
              >
                Farmers
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-8">
              {isEditMode ? "Edit Product" : "Add New Product"}
            </h1>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Product Name */}
              <div>
                <label
                  htmlFor="name"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Product Name *
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  placeholder="Enter product name"
                />
              </div>

              {/* Category */}
              <div>
                <label
                  htmlFor="category"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Category *
                </label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="">Select Category</option>
                  <option value="Vegetables">Vegetables</option>
                  <option value="Fruits">Fruits</option>
                  <option value="Grains">Grains</option>
                  <option value="Dairy">Dairy</option>
                  <option value="Honey">Honey</option>
                  <option value="Herbs">Herbs</option>
                </select>
              </div>

              {/* Description */}
              <div>
                <label
                  htmlFor="description"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Description *
                </label>
                <textarea
                  id="description"
                  name="description"
                  rows={4}
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  placeholder="Describe your product..."
                />
              </div>

              {/* Price and Stock */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="price"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Price () *
                  </label>
                  <input
                    type="number"
                    id="price"
                    name="price"
                    min="0"
                    step="0.01"
                    value={formData.price}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="0.00"
                  />
                </div>

                <div>
                  <label
                    htmlFor="stock"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Stock Quantity *
                  </label>
                  <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    value={formData.stock}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="0"
                  />
                </div>
              </div>

              {/* Unit */}
              <div>
                <label
                  htmlFor="unit"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Unit *
                </label>
                <select
                  id="unit"
                  name="unit"
                  value={formData.unit}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="">Select Unit</option>
                  <option value="kg">Kilogram (kg)</option>
                  <option value="g">Gram (g)</option>
                  <option value="piece">Piece</option>
                  <option value="bunch">Bunch</option>
                  <option value="liter">Liter</option>
                  <option value="ml">Milliliter (ml)</option>
                </select>
              </div>

              {/* Image Upload */}
              <div>
                <label
                  htmlFor="images"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Product Images
                </label>
                <input
                  type="file"
                  id="images"
                  name="images"
                  multiple
                  accept="image/*"
                  onChange={handleImageChange}
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
                <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                  Upload up to 5 images (JPG, PNG, WebP)
                </p>

                {/* Image Preview */}
                {imagePreview && (
                  <div className="mt-4">
                    <Image
                      src={imagePreview}
                      alt="Image Preview"
                      width={500}
                      height={300}
                      className="rounded-lg object-cover"
                    />
                  </div>
                )}
              </div>

              {/* Features */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Features
                </label>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  {["organic", "fresh", "local", "seasonal", "premium"].map(
                    (feature) => (
                      <label key={feature} className="flex items-center">
                        <input
                          type="checkbox"
                          name="features"
                          value={feature}
                          checked={formData.features.includes(feature)}
                          onChange={handleFeatureChange}
                          className="mr-2 text-green-600 focus:ring-green-500"
                        />
                        <span className="text-gray-700 dark:text-gray-300 capitalize">
                          {feature}
                        </span>
                      </label>
                    ),
                  )}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-4 pt-6">
                <button
                  type="submit"
                  disabled={loading}
                  className={`flex-1 py-3 px-6 rounded-lg font-medium transition ${
                    loading
                      ? "bg-gray-400 cursor-not-allowed text-white"
                      : "bg-green-600 hover:bg-green-700 text-white"
                  }`}
                >
                  {loading ? (
                    <>
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      {isEditMode ? "Updating Product..." : "Adding Product..."}
                    </>
                  ) : (
                    <>
                      <i className="fas fa-plus mr-2"></i>
                      {isEditMode ? "Update Product" : "Add Product"}
                    </>
                  )}
                </button>

                <Link
                  href="/manage"
                  className="flex-1 bg-gray-600 hover:bg-gray-700 text-white py-3 px-6 rounded-lg font-medium text-center transition"
                >
                  <i className="fas fa-list mr-2"></i>
                  Manage Products
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Footer from "@/components/Footer";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);
  const ordersPerPage = 10;

  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      // Check if user is a farmer
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }
      fetchOrders();
    }
  }, [session, status, router]);

  useEffect(() => {
    filterOrders();
  }, [orders, statusFilter, searchTerm]);

  const fetchOrders = async () => {
    setLoading(true);
    try {
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      if (!userId && !userEmail) {
        console.error("No user ID or email found in session");
        setLoading(false);
        return;
      }

      // Fetch orders for this farmer using farmerId and farmerEmail parameters
      const params = new URLSearchParams();
      if (userId) params.append("farmerId", userId);
      if (userEmail) params.append("farmerEmail", userEmail);

      console.log("Farmer orders page - fetching with params:", {
        farmerId: userId,
        farmerEmail: userEmail,
        url: `/api/orders?${params.toString()}`,
      });

      const response = await fetch(`/api/orders?${params.toString()}`, {
        cache: "no-store",
        headers: {
          "Cache-Control": "no-cache",
          Pragma: "no-cache",
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Farmer orders API response:", data);
        console.log("Farmer orders fetched:", data.orders?.length || 0);
        console.log("Sample orders:", data.orders?.slice(0, 2));

        // Log the structure of orders to see what data we have
        if (data.orders && data.orders.length > 0) {
          console.log("First order structure:", {
            _id: data.orders[0]._id,
            customerName: data.orders[0].customerName,
            customerEmail: data.orders[0].customerEmail,
            status: data.orders[0].status,
            items: data.orders[0].items?.length || 0,
            farmerIds: data.orders[0].farmerIds,
            farmerEmails: data.orders[0].farmerEmails,
            createdAt: data.orders[0].createdAt,
          });
        }

        setOrders(data.orders || []);
      } else {
        console.error("Failed to fetch orders");
        setOrders([]);
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  const filterOrders = () => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter
    if (searchTerm) {
      const searchRegex = new RegExp(searchTerm, "i");
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) || searchRegex.test(item.productName),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    setFilteredOrders(filtered);
    setCurrentPage(1);
  };

  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    // More descriptive confirmation messages based on status transition
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      const updateData = {
        status: newStatus,
        statusHistory: {
          status: newStatus,
          timestamp: new Date().toISOString(),
          updatedBy: session.user.email || session.user.name,
        },
      };

      // Add estimated delivery date for shipped status
      if (newStatus === "shipped") {
        const estimatedDelivery = new Date();
        estimatedDelivery.setDate(estimatedDelivery.getDate() + 3); // 3 days from ship date
        updateData.estimatedDeliveryDate = estimatedDelivery.toISOString();
      }

      const response = await fetch(`/api/orders/${orderId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        setOrders((prev) =>
          prev.map((order) =>
            order._id === orderId
              ? {
                  ...order,
                  status: newStatus,
                  estimatedDeliveryDate:
                    updateData.estimatedDeliveryDate ||
                    order.estimatedDeliveryDate,
                  statusHistory: [
                    ...(Array.isArray(order.statusHistory)
                      ? order.statusHistory
                      : []),
                    updateData.statusHistory,
                  ],
                }
              : order,
          ),
        );

        // More specific success messages
        const successMessages = {
          confirmed: "Order confirmed! Customer has been notified.",
          shipped:
            "Order marked as shipped! Customer has been notified with tracking information.",
          delivered:
            "Order completed! Customer has been notified of successful delivery.",
          cancelled: "Order cancelled. Customer has been notified.",
        };

        alert(
          successMessages[newStatus] ||
            `Order status updated to ${newStatus} successfully!`,
        );
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to update order status");
      }
    } catch (error) {
      console.error("Error updating order status:", error);
      alert(`Failed to update order status: ${error.message}`);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchOrders();
    setRefreshing(false);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-green-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">
            Loading your orders...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Breadcrumb */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <nav className="flex" aria-label="Breadcrumb">
            <ol className="flex items-center space-x-2 text-sm">
              <li>
                <Link href="/" className="text-gray-500 hover:text-green-600">
                  Home
                </Link>
              </li>
              <li>
                <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
              </li>
              <li>
                <Link
                  href="/manage"
                  className="text-gray-500 hover:text-green-600"
                >
                  Manage
                </Link>
              </li>
              <li>
                <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
              </li>
              <li className="text-gray-900 dark:text-white">
                Order Management
              </li>
            </ol>
          </nav>
        </div>

        {/* Page Header */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                Order Management
              </h1>
              <p className="text-gray-600 dark:text-gray-400 mt-2">
                Manage and track all orders for your products
              </p>
            </div>
            <div className="mt-4 sm:mt-0 flex space-x-3">
              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "fa-spin" : ""}`}
                ></i>
                Refresh
              </button>
              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Order Summary Cards */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-gray-100 dark:bg-gray-700 rounded-lg">
                  <i className="fas fa-shopping-cart text-gray-600 dark:text-gray-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Total
                  </p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {orderSummary.total}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-yellow-100 dark:bg-yellow-900 rounded-lg">
                  <i className="fas fa-clock text-yellow-600 dark:text-yellow-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Pending
                  </p>
                  <p className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">
                    {orderSummary.pending}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-blue-100 dark:bg-blue-900 rounded-lg">
                  <i className="fas fa-check text-blue-600 dark:text-blue-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Confirmed
                  </p>
                  <p className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                    {orderSummary.confirmed}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-purple-100 dark:bg-purple-900 rounded-lg">
                  <i className="fas fa-truck text-purple-600 dark:text-purple-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Shipped
                  </p>
                  <p className="text-2xl font-bold text-purple-600 dark:text-purple-400">
                    {orderSummary.shipped}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 dark:bg-green-900 rounded-lg">
                  <i className="fas fa-check-circle text-green-600 dark:text-green-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Delivered
                  </p>
                  <p className="text-2xl font-bold text-green-600 dark:text-green-400">
                    {orderSummary.delivered}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-red-100 dark:bg-red-900 rounded-lg">
                  <i className="fas fa-times-circle text-red-600 dark:text-red-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Cancelled
                  </p>
                  <p className="text-2xl font-bold text-red-600 dark:text-red-400">
                    {orderSummary.cancelled}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Filters and Search */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label
                  htmlFor="search"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    id="search"
                    placeholder="Search by customer name, email, or order ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>
              <div>
                <label
                  htmlFor="status-filter"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Filter by Status
                </label>
                <select
                  id="status-filter"
                  value={statusFilter}
                  onChange={handleStatusChange}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  Clear Filters
                </button>
              </div>
            </div>
          </div>

          {/* Orders List */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <i className="fas fa-shopping-bag text-6xl text-gray-400 mb-4"></i>
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                No orders found
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-6">
                {statusFilter === "All Orders"
                  ? "You haven't received any orders yet."
                  : `No ${statusFilter.toLowerCase()} orders found.`}
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden"
                >
                  <div className="p-6">
                    {/* Order Header */}
                    <div className="flex flex-col lg:flex-row lg:items-center justify-between mb-6">
                      <div className="flex items-center space-x-4 mb-4 lg:mb-0">
                        <div>
                          <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Footer from "@/components/Footer";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);
  const ordersPerPage = 10;

  // Memoize the filterOrders function to prevent infinite re-renders
  const filterOrders = useCallback(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter
    if (searchTerm) {
      const searchRegex = new RegExp(searchTerm, "i");
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) || searchRegex.test(item.productName),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    setFilteredOrders(filtered);
    setCurrentPage(1);
  }, [orders, statusFilter, searchTerm]);

  // Apply filters whenever dependencies change
  useEffect(() => {
    filterOrders();
  }, [filterOrders]);

  const fetchOrders = useCallback(async () => {
    if (!session?.user) return;

    try {
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      // Fetch orders for this farmer using farmerId and farmerEmail parameters
      const params = new URLSearchParams();
      if (userId) params.append("farmerId", userId);
      if (userEmail) params.append("farmerEmail", userEmail);

      console.log("Farmer orders page - fetching with params:", {
        farmerId: userId,
        farmerEmail: userEmail,
        url: `/api/orders?${params.toString()}`,
      });

      const response = await fetch(`/api/orders?${params.toString()}`, {
        cache: "no-store",
        headers: {
          "Cache-Control": "no-cache",
          Pragma: "no-cache",
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Farmer orders API response:", data);
        console.log("Farmer orders fetched:", data.orders?.length || 0);
        console.log("Sample orders:", data.orders?.slice(0, 2));

        // Log the structure of orders to see what data we have
        if (data.orders && data.orders.length > 0) {
          console.log("First order structure:", {
            _id: data.orders[0]._id,
            customerName: data.orders[0].customerName,
            customerEmail: data.orders[0].customerEmail,
            status: data.orders[0].status,
            items: data.orders[0].items?.length || 0,
            farmerIds: data.orders[0].farmerIds,
            farmerEmails: data.orders[0].farmerEmails,
            createdAt: data.orders[0].createdAt,
          });
        }

        setOrders(data.orders || []);
      } else {
        console.error("Failed to fetch orders");
        setOrders([]);
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  }, [session]);

  useEffect(() => {
    fetchOrders();
  }, [fetchOrders]);

  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    // More descriptive confirmation messages based on status transition
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      const updateData = {
        status: newStatus,
        statusHistory: {
          status: newStatus,
          timestamp: new Date().toISOString(),
          updatedBy: session.user.email || session.user.name,
        },
      };

      // Add estimated delivery date for shipped status
      if (newStatus === "shipped") {
        const estimatedDelivery = new Date();
        estimatedDelivery.setDate(estimatedDelivery.getDate() + 3); // 3 days from ship date
        updateData.estimatedDeliveryDate = estimatedDelivery.toISOString();
      }

      const response = await fetch(`/api/orders/${orderId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        setOrders((prev) =>
          prev.map((order) =>
            order._id === orderId
              ? {
                  ...order,
                  status: newStatus,
                  estimatedDeliveryDate:
                    updateData.estimatedDeliveryDate ||
                    order.estimatedDeliveryDate,
                  statusHistory: [
                    ...(Array.isArray(order.statusHistory)
                      ? order.statusHistory
                      : []),
                    updateData.statusHistory,
                  ],
                }
              : order,
          ),
        );

        // More specific success messages
        const successMessages = {
          confirmed: "Order confirmed! Customer has been notified.",
          shipped:
            "Order marked as shipped! Customer has been notified with tracking information.",
          delivered:
            "Order completed! Customer has been notified of successful delivery.",
          cancelled: "Order cancelled. Customer has been notified.",
        };

        alert(
          successMessages[newStatus] ||
            `Order status updated to ${newStatus} successfully!`,
        );
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to update order status");
      }
    } catch (error) {
      console.error("Error updating order status:", error);
      alert(`Failed to update order status: ${error.message}`);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchOrders();
    setRefreshing(false);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-green-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">
            Loading your orders...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Breadcrumb */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <nav className="flex" aria-label="Breadcrumb">
            <ol className="flex items-center space-x-2 text-sm">
              <li>
                <Link href="/" className="text-gray-500 hover:text-green-600">
                  Home
                </Link>
              </li>
              <li>
                <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
              </li>
              <li>
                <Link
                  href="/manage"
                  className="text-gray-500 hover:text-green-600"
                >
                  Manage
                </Link>
              </li>
              <li>
                <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
              </li>
              <li className="text-gray-900 dark:text-white">
                Order Management
              </li>
            </ol>
          </nav>
        </div>

        {/* Page Header */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                Order Management
              </h1>
              <p className="text-gray-600 dark:text-gray-400 mt-2">
                Manage and track all orders for your products
              </p>
            </div>
            <div className="mt-4 sm:mt-0 flex space-x-3">
              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "fa-spin" : ""}`}
                ></i>
                Refresh
              </button>
              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Order Summary Cards */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-gray-100 dark:bg-gray-700 rounded-lg">
                  <i className="fas fa-shopping-cart text-gray-600 dark:text-gray-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Total
                  </p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {orderSummary.total}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-yellow-100 dark:bg-yellow-900 rounded-lg">
                  <i className="fas fa-clock text-yellow-600 dark:text-yellow-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Pending
                  </p>
                  <p className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">
                    {orderSummary.pending}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-blue-100 dark:bg-blue-900 rounded-lg">
                  <i className="fas fa-check text-blue-600 dark:text-blue-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Confirmed
                  </p>
                  <p className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                    {orderSummary.confirmed}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-purple-100 dark:bg-purple-900 rounded-lg">
                  <i className="fas fa-truck text-purple-600 dark:text-purple-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Shipped
                  </p>
                  <p className="text-2xl font-bold text-purple-600 dark:text-purple-400">
                    {orderSummary.shipped}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 dark:bg-green-900 rounded-lg">
                  <i className="fas fa-check-circle text-green-600 dark:text-green-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Delivered
                  </p>
                  <p className="text-2xl font-bold text-green-600 dark:text-green-400">
                    {orderSummary.delivered}
                  </p>
                </div>
              </div>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4">
              <div className="flex items-center">
                <div className="p-2 bg-red-100 dark:bg-red-900 rounded-lg">
                  <i className="fas fa-times-circle text-red-600 dark:text-red-300"></i>
                </div>
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                    Cancelled
                  </p>
                  <p className="text-2xl font-bold text-red-600 dark:text-red-400">
                    {orderSummary.cancelled}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Filters and Search */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label
                  htmlFor="search"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    id="search"
                    placeholder="Search by customer name, email, or order ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>
              <div>
                <label
                  htmlFor="status-filter"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                >
                  Filter by Status
                </label>
                <select
                  id="status-filter"
                  value={statusFilter}
                  onChange={handleStatusChange}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  Clear Filters
                </button>
              </div>
            </div>
          </div>

          {/* Orders List */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <i className="fas fa-shopping-bag text-6xl text-gray-400 mb-4"></i>
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                No orders found
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-6">
                {statusFilter === "All Orders"
                  ? "You haven't received any orders yet."
                  : `No ${statusFilter.toLowerCase()} orders found.`}
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden"
                >
                  <div className="p-6">
                    {/* Order Header */}
                    <div className="flex flex-col lg:flex-row lg:items-center justify-between mb-6">
                      <div className="flex items-center space-x-4 mb-4 lg:mb-0">
                        <div>
                          <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

import localFont from "next/font/local";
          rel="stylesheet"
        />
      </head>
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-300`}
      >
        <Providers>
          {children}
          {modal}
        </Providers>
      </body>
    </html>
  );
}

// Import script to upload products to MongoDB
const { MongoClient } = require('mongodb');
const fs = require('fs');
const path = require('path');

// Read your connection string from environment or replace with your actual connection string
const MONGODB_URI = process.env.MONGODB_URI || 'your-mongodb-connection-string-here';

async function importProducts() {
  try {
    // Read the products JSON file
    const productsPath = path.join(__dirname, '..', 'products.json');
    const productsData = JSON.parse(fs.readFileSync(productsPath, 'utf8'));

    console.log(`Found ${productsData.length} products to import`);

    // Connect to MongoDB
    const client = new MongoClient(MONGODB_URI);
    await client.connect();

    const db = client.db('farmfresh');
    const collection = db.collection('products');

    // Clear existing products (optional - remove this line if you want to keep existing data)
    await collection.deleteMany({});
    console.log('Cleared existing products');

    // Insert all products
    const result = await collection.insertMany(productsData);
    console.log(`Successfully imported ${result.insertedCount} products`);

    await client.close();
    console.log('Import completed successfully!');

  } catch (error) {
    console.error('Error importing products:', error);
  }
}

importProducts();
