        {/* Favorite Button */}
        <div className="absolute top-3 right-3">
          <button
            onClick={handleFavoriteToggle}
            className="bg-white dark:bg-gray-800 p-2 rounded-full shadow-md hover:bg-gray-100 dark:hover:bg-gray-700 transition"
          >
            <i
              className={`${isFavorite ? "fas fa-heart text-red-500" : "far fa-heart text-gray-600 dark:text-gray-400"}`}
            ></i>
          </button>
        </div>
      </div>

      <div className="p-6">
        <div className="flex items-center justify-between mb-2">
          <Link href={`/details?id=${product._id}`}>
            <h3 className="font-semibold text-gray-900 dark:text-white hover:text-primary-600 cursor-pointer">
              {product.name}
            </h3>
          </Link>

          {product.averageRating && (
            <StarRating rating={product.averageRating} />
          )}
        </div>

        <p className="text-sm text-gray-600 dark:text-gray-400 mb-3">
          By {product.farmer?.name || "Unknown Farmer"} •{" "}
          {product.farmer?.location || "Location N/A"}
        </p>

        <div className="flex items-center justify-between mb-4">
          <div>
            <span className="text-2xl font-bold text-primary-600 dark:text-primary-400">
              ৳{product.price}
            </span>
            <span className="text-sm text-gray-500 dark:text-gray-400">
              /{product.unit || "kg"}
            </span>
          </div>
          <span className="text-sm text-gray-500 dark:text-gray-400">
            Stock: {product.stock}
            {product.unit || "kg"}
          </span>
        </div>

        {showAddToCart && (
          <button
            onClick={handleAddToCart}
            disabled={isAddingToCart || product.stock <= 0}
            className="w-full bg-primary-600 hover:bg-primary-700 disabled:bg-gray-400 text-white py-2 rounded-lg font-medium transition"
          >
            {isAddingToCart
              ? "Adding..."
              : product.stock <= 0
                ? "Out of Stock"
                : "Add to Cart"}
          </button>
        )}
      </div>
    </div>
  );
}

"use client";

export default function StarRating({ rating, size = "text-sm", showValue = true }) {
  // Handle the fractional star logic according to your requirements
  const getStarDisplay = (rating) => {
    const fullStars = Math.floor(rating);
    const decimal = rating - fullStars;

    let hasHalfStar = false;
    let totalFullStars = fullStars;

    // Handle fractional part logic
    if (decimal >= 0.5) {
      if (decimal > 0.5) {
        // If decimal > 0.5, round up to next full star
        totalFullStars = fullStars + 1;
      } else {
        // If decimal = 0.5, show half star
        hasHalfStar = true;
      }
    } else if (decimal >= 0.4) {
      // If decimal is 0.4-0.49, show as half star
      hasHalfStar = true;
    }
    // If decimal < 0.4, just show full stars (no change needed)

    return {
      fullStars: totalFullStars,
      hasHalfStar,
      emptyStars: 5 - totalFullStars - (hasHalfStar ? 1 : 0)
    };
  };

  const { fullStars, hasHalfStar, emptyStars } = getStarDisplay(rating);

  return (
    <div className="flex items-center text-yellow-400">
      {/* Render full stars */}
      {[...Array(fullStars)].map((_, index) => (
        <i key={`full-${index}`} className={`fas fa-star ${size}`}></i>
      ))}

      {/* Render half star if needed */}
      {hasHalfStar && (
        <i className={`fas fa-star-half-alt ${size}`}></i>
      )}

      {/* Render empty stars */}
      {[...Array(emptyStars)].map((_, index) => (
        <i key={`empty-${index}`} className={`far fa-star ${size} text-gray-300`}></i>
      ))}

      {/* Show numeric value if requested */}
      {showValue && (
        <span className="text-sm text-gray-600 dark:text-gray-400 ml-1">
          {rating.toFixed(1)}
        </span>
      )}
    </div>
  );
}

