  console.log('✅ Sample reviews added successfully!');
  await client.close();
}

addSampleReviews().catch(console.error);

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";

export async function POST(request) {
  try {
    const client = await clientPromise;
    const db = client.db("farmfresh");
    const usersCollection = db.collection("users");
    const farmersCollection = db.collection("farmers");

    // Find all users with userType "farmer"
    const farmerUsers = await usersCollection.find({ userType: "farmer" }).toArray();

    console.log(`Found ${farmerUsers.length} farmer users to migrate`);

    let migratedCount = 0;
    let skippedCount = 0;

    for (const user of farmerUsers) {
      // Check if farmer already exists in farmers collection
      const existingFarmer = await farmersCollection.findOne({ _id: user._id });

      if (!existingFarmer) {
        // Create farmer document
        const farmerData = {
          _id: user._id,
          name: user.name || `${user.firstName} ${user.lastName}`,
          email: user.email,
          phone: user.phone || "",
          location: user.address || "",
          farmName: user.farmDetails?.farmName || `${user.name}'s Farm`,
          specializations: user.farmDetails?.specialization ? 
            Array.isArray(user.farmDetails.specialization) ? 
              user.farmDetails.specialization : 
              [user.farmDetails.specialization] : 
            ["General Farming"],
          farmSize: user.farmDetails?.farmSize || null,
          farmSizeUnit: user.farmDetails?.farmSizeUnit || "acres",
          bio: user.bio || "",
          profilePicture: user.profilePicture || null,
          rating: 0,
          totalReviews: 0,
          verified: false,
          joinedDate: user.createdAt || new Date(),
          products: [],
          orders: [],
          availability: {
            status: "available",
            schedule: {
              monday: { start: "09:00", end: "17:00", available: true },
              tuesday: { start: "09:00", end: "17:00", available: true },
              wednesday: { start: "09:00", end: "17:00", available: true },
              thursday: { start: "09:00", end: "17:00", available: true },
              friday: { start: "09:00", end: "17:00", available: true },
              saturday: { start: "09:00", end: "15:00", available: true },
              sunday: { start: "10:00", end: "14:00", available: false }
            }
          },
          socialMedia: {
            facebook: "",
            instagram: "",
            twitter: "",
            website: ""
          },
          certifications: [],
          deliveryOptions: {
            farmPickup: true,
            localDelivery: false,
            shipping: false,
            deliveryRadius: 0
          },
          createdAt: user.createdAt || new Date(),
          updatedAt: new Date()
        };

        // Insert farmer
        await farmersCollection.insertOne(farmerData);
        console.log(`✅ Migrated farmer: ${farmerData.name}`);
        migratedCount++;
      } else {
        console.log(`⚠️  Farmer ${user.name} already exists in farmers collection`);
        skippedCount++;
      }
    }

    // Get final count
    const totalFarmers = await farmersCollection.countDocuments();

    return NextResponse.json({
      success: true,
      message: "Farmer migration completed",
      results: {
        farmersFound: farmerUsers.length,
        migrated: migratedCount,
        skipped: skippedCount,
        totalFarmersInCollection: totalFarmers
      }
    });

  } catch (error) {
    console.error("Migration error:", error);
    return NextResponse.json(
      { 
        success: false, 
        error: "Failed to migrate farmers",
        message: error.message 
      },
      { status: 500 }
    );
  }
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import ManagePageLoadingSkeleton from "@/components/ManagePageLoadingSkeleton";
import { useDashboardData } from "@/hooks/useDashboardData";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateProductInCache,
    updateBulkProductsInCache,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        router.push("/");
        return;
      }
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization
  const analytics = useMemo(() => {
    const validOrders = orders.filter(
      (order) =>
        order.status !== ORDER_STATUS.CANCELLED &&
        order.status !== ORDER_STATUS.RETURNED,
    );

    const totalProducts = products.length;
    const activeProducts = products.filter(
      (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
    ).length;

    const totalOrders = orders.length;
    const pendingOrders = orders.filter(
      (o) => o.status === ORDER_STATUS.PENDING,
    ).length;

    const totalRevenue = validOrders.reduce(
      (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
      0,
    );

    const now = new Date();
    const thisMonthValidOrders = validOrders.filter((order) => {
      const orderDate = new Date(order.createdAt);
      return (
        orderDate.getMonth() === now.getMonth() &&
        orderDate.getFullYear() === now.getFullYear()
      );
    });

    const averageOrderValue =
      validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

    const recentOrders = orders.slice(0, 5).map((order) => ({
      _id: order._id,
      id: order.id,
      customerName: order.customerName,
      total: order.total || order.farmerSubtotal,
      createdAt: order.createdAt,
      status: order.status,
      items: order.items?.length || 0,
    }));

    return {
      totalProducts,
      activeProducts,
      totalOrders,
      pendingOrders,
      totalRevenue,
      thisMonthOrders: thisMonthValidOrders.length,
      averageOrderValue,
      lowStockProducts: products.filter((p) => p.stock <= 10).length,
      recentOrders,
    };
  }, [products.length, orders.length]);

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = [...products];

    if (filters.search) {
      const searchRegex = new RegExp(filters.search.trim(), "i");
      filtered = filtered.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description || "") ||
          searchRegex.test(product.category || ""),
      );
    }

    if (filters.category) {
      filtered = filtered.filter(
        (product) =>
          product.category?.toLowerCase() === filters.category.toLowerCase(),
      );
    }

    if (filters.status) {
      switch (filters.status) {
        case PRODUCT_STATUS.ACTIVE:
          filtered = filtered.filter(
            (product) =>
              product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.INACTIVE:
          filtered = filtered.filter(
            (product) => product.status === PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.OUT_OF_STOCK:
          filtered = filtered.filter((product) => product.stock === 0);
          break;
        case PRODUCT_STATUS.LOW_STOCK:
          filtered = filtered.filter(
            (product) => product.stock > 0 && product.stock <= 5,
          );
          break;
      }
    }

    if (filters.sort) {
      const [key, order] = filters.sort.split("-");
      filtered.sort((a, b) => {
        let valueA, valueB;

        switch (key) {
          case "price":
            valueA = a.price || 0;
            valueB = b.price || 0;
            break;
          case "name":
            valueA = a.name || "";
            valueB = b.name || "";
            return order === "asc"
              ? valueA.localeCompare(valueB)
              : valueB.localeCompare(valueA);
          case "stock":
            valueA = a.stock || 0;
            valueB = b.stock || 0;
            break;
          case "date":
            valueA = new Date(a.createdAt || 0);
            valueB = new Date(b.createdAt || 0);
            break;
          default:
            return 0;
        }

        return order === "asc" ? valueA - valueB : valueB - valueA;
      });
    }

    return filtered;
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      console.log("handleStatusToggle called with:", {
        productId,
        currentStatus,
        type: typeof currentStatus,
      });

      // Normalize the current status to lowercase for comparison
      const normalizedCurrentStatus = currentStatus?.toLowerCase();
      const normalizedActiveStatus = PRODUCT_STATUS.ACTIVE.toLowerCase();

      const newStatus =
        normalizedCurrentStatus === normalizedActiveStatus
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;

      console.log("Status toggle:", {
        currentStatus,
        normalizedCurrentStatus,
        newStatus,
        comparison: normalizedCurrentStatus === normalizedActiveStatus,
      });

      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        console.log("Sending status update:", { productId, status: newStatus });

        // Use the same API service pattern as bulk update for consistency
        const { apiService } = await import("@/lib/api-service");
        const result = await apiService.bulkUpdateProducts(
          [productId], // Single product as array
          { status: newStatus },
        );

        console.log("API response:", result);

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        // Update the React Query cache using the same pattern as bulk update
        if (updateBulkProductsInCache) {
          updateBulkProductsInCache([productId], { status: newStatus });
        }

        // Dispatch custom event to notify products page of status change
        window.dispatchEvent(
          new CustomEvent("productStatusUpdated", {
            detail: {
              productId: productId,
              newStatus: newStatus,
              timestamp: Date.now(),
            },
          }),
        );

        // Also set localStorage flag for cross-tab communication
        localStorage.setItem(
          "productStatusUpdated",
          JSON.stringify({
            productId: productId,
            newStatus: newStatus,
            timestamp: Date.now(),
          }),
        );

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [updateBulkProductsInCache],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        "⚠️ Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        "• The product listing\n" +
        "• All associated data\n" +
        "• Product from any pending orders";

      if (!confirm(confirmMessage)) return;

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              "❌ Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert("❌ Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        await refetchDashboard();
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(`❌ Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update functions
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const commonProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
    updateBulkProductsInCache,
  };

  const productProps = {
    ...commonProps,
    paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
  };

  // Loading state - Use dedicated manage page skeleton
  if (isLoading || status === "loading") {
    return <ManagePageLoadingSkeleton />;
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            {error.message}
          </p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <DashboardHeader {...commonProps} />

        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...commonProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...commonProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...commonProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...commonProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import ManagePageLoadingSkeleton from "@/components/ManagePageLoadingSkeleton";
import { useDashboardData } from "@/hooks/useDashboardData";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateProductInCache,
    updateBulkProductsInCache,
    bulkUpdateProducts,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        router.push("/");
        return;
      }
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization
  const analytics = useMemo(() => {
    const validOrders = orders.filter(
      (order) =>
        order.status !== ORDER_STATUS.CANCELLED &&
        order.status !== ORDER_STATUS.RETURNED,
    );

    const totalProducts = products.length;
    const activeProducts = products.filter(
      (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
    ).length;

    const totalOrders = orders.length;
    const pendingOrders = orders.filter(
      (o) => o.status === ORDER_STATUS.PENDING,
    ).length;

    const totalRevenue = validOrders.reduce(
      (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
      0,
    );

    const now = new Date();
    const thisMonthValidOrders = validOrders.filter((order) => {
      const orderDate = new Date(order.createdAt);
      return (
        orderDate.getMonth() === now.getMonth() &&
        orderDate.getFullYear() === now.getFullYear()
      );
    });

    const averageOrderValue =
      validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

    const recentOrders = orders.slice(0, 5).map((order) => ({
      _id: order._id,
      id: order.id,
      customerName: order.customerName,
      total: order.total || order.farmerSubtotal,
      createdAt: order.createdAt,
      status: order.status,
      items: order.items?.length || 0,
    }));

    return {
      totalProducts,
      activeProducts,
      totalOrders,
      pendingOrders,
      totalRevenue,
      thisMonthOrders: thisMonthValidOrders.length,
      averageOrderValue,
      lowStockProducts: products.filter((p) => p.stock <= 10).length,
      recentOrders,
    };
  }, [products.length, orders.length]);

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = [...products];

    if (filters.search) {
      const searchRegex = new RegExp(filters.search.trim(), "i");
      filtered = filtered.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description || "") ||
          searchRegex.test(product.category || ""),
      );
    }

    if (filters.category) {
      filtered = filtered.filter(
        (product) =>
          product.category?.toLowerCase() === filters.category.toLowerCase(),
      );
    }

    if (filters.status) {
      switch (filters.status) {
        case PRODUCT_STATUS.ACTIVE:
          filtered = filtered.filter(
            (product) =>
              product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.INACTIVE:
          filtered = filtered.filter(
            (product) => product.status === PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.OUT_OF_STOCK:
          filtered = filtered.filter((product) => product.stock === 0);
          break;
        case PRODUCT_STATUS.LOW_STOCK:
          filtered = filtered.filter(
            (product) => product.stock > 0 && product.stock <= 5,
          );
          break;
      }
    }

    if (filters.sort) {
      const [key, order] = filters.sort.split("-");
      filtered.sort((a, b) => {
        let valueA, valueB;

        switch (key) {
          case "price":
            valueA = a.price || 0;
            valueB = b.price || 0;
            break;
          case "name":
            valueA = a.name || "";
            valueB = b.name || "";
            return order === "asc"
              ? valueA.localeCompare(valueB)
              : valueB.localeCompare(valueA);
          case "stock":
            valueA = a.stock || 0;
            valueB = b.stock || 0;
            break;
          case "date":
            valueA = new Date(a.createdAt || 0);
            valueB = new Date(b.createdAt || 0);
            break;
          default:
            return 0;
        }

        return order === "asc" ? valueA - valueB : valueB - valueA;
      });
    }

    return filtered;
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      console.log("handleStatusToggle called with:", {
        productId,
        currentStatus,
        type: typeof currentStatus,
      });

      // Normalize the current status to lowercase for comparison
      const normalizedCurrentStatus = currentStatus?.toLowerCase();
      const normalizedActiveStatus = PRODUCT_STATUS.ACTIVE.toLowerCase();

      const newStatus =
        normalizedCurrentStatus === normalizedActiveStatus
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;

      console.log("Status toggle:", {
        currentStatus,
        normalizedCurrentStatus,
        newStatus,
        comparison: normalizedCurrentStatus === normalizedActiveStatus,
      });

      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        console.log("Sending status update:", { productId, status: newStatus });

        // Use the bulkUpdateProducts from hook (includes cache invalidation like farmer updates)
        const result = await bulkUpdateProducts(
          [productId], // Single product as array
          { status: newStatus },
        );

        console.log("API response:", result);

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        // No need for manual cache update - bulkUpdateProducts handles it automatically
        // if (updateBulkProductsInCache) {
        //   updateBulkProductsInCache([productId], { status: newStatus });
        // }

        // Dispatch custom event to notify products page of status change
        window.dispatchEvent(
          new CustomEvent("productStatusUpdated", {
            detail: {
              productId: productId,
              newStatus: newStatus,
              timestamp: Date.now(),
            },
          }),
        );

        // Also set localStorage flag for cross-tab communication
        localStorage.setItem(
          "productStatusUpdated",
          JSON.stringify({
            productId: productId,
            newStatus: newStatus,
            timestamp: Date.now(),
          }),
        );

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [bulkUpdateProducts],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        "⚠️ Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        "• The product listing\n" +
        "• All associated data\n" +
        "• Product from any pending orders";

      if (!confirm(confirmMessage)) return;

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              "❌ Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert("❌ Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        await refetchDashboard();
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(`❌ Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update functions
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const commonProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
    updateBulkProductsInCache,
    bulkUpdateProducts, // Add this prop for DashboardTab
  };

  const productProps = {
    ...commonProps,
    paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
    // Add missing bulk update functionality for ProductsTab
    updateBulkProductsInCache,
    bulkUpdateProducts,
  };

  // Loading state - Use dedicated manage page skeleton
  if (isLoading || status === "loading") {
    return <ManagePageLoadingSkeleton />;
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            {error.message}
          </p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <DashboardHeader {...commonProps} />

        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...commonProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...commonProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...commonProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...commonProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useAuth } from "../../contexts/AuthContext";
import { useRouter } from "next/navigation";
import { useTheme } from "../../contexts/ThemeContext";

export default function FarmerProfilePage() {
  const { user, isAuthenticated, updateUser } = useAuth();
  const { isDarkMode } = useTheme();
  const router = useRouter();

  const [activeTab, setActiveTab] = useState("personal");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const [profileData, setProfileData] = useState({
    name: "",
    email: "",
    phone: "",
    farmInfo: {
      farmName: "",
      farmDescription: "",
      farmSize: "",
      farmType: "",
      certifications: [],
      establishedYear: "",
      farmingMethods: [],
    },
    address: {
      street: "",
      city: "",
      state: "",
      zipCode: "",
      country: "",
    },
    businessInfo: {
      businessLicense: "",
      taxId: "",
      deliveryRadius: "",
      minimumOrder: "",
      website: "",
      socialMedia: {
        facebook: "",
        instagram: "",
        twitter: "",
      },
    },
    preferences: {
      notifications: {
        email: true,
        sms: false,
        orderUpdates: true,
        lowStockAlerts: true,
        customerMessages: true,
        marketingUpdates: false,
      },
      language: "en",
      currency: "USD",
      autoAcceptOrders: false,
      showFarmLocation: true,
    },
    security: {
      currentPassword: "",
      newPassword: "",
      confirmPassword: "",
    },
  });

  // Redirect if not authenticated or not a farmer
  useEffect(() => {
    if (!isAuthenticated) {
      router.push("/login");
      return;
    }

    // Check if user is a farmer, if not redirect to regular profile
    if (user && user.role !== "farmer") {
      router.push("/profile");
      return;
    }

    // Initialize profile data with user data
    if (user) {
      setProfileData((prev) => ({
        ...prev,
        name: user.name || "",
        email: user.email || "",
        phone: user.phone || "",
        address: user.address || prev.address,
        farmInfo: user.farmInfo || prev.farmInfo,
        businessInfo: user.businessInfo || prev.businessInfo,
        preferences: user.preferences || prev.preferences,
      }));
    }
  }, [user, isAuthenticated, router]);

  const handleInputChange = (field, value) => {
    setProfileData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleNestedChange = (section, subsection, field, value) => {
    if (subsection) {
      setProfileData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [subsection]: {
            ...prev[section][subsection],
            [field]: value,
          },
        },
      }));
    } else {
      setProfileData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: value,
        },
      }));
    }
  };

  const handleArrayChange = (section, field, value) => {
    const arrayValue = value.split(",").map(item => item.trim()).filter(item => item);
    setProfileData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: arrayValue,
      },
    }));
  };

  const handlePersonalInfoUpdate = async () => {
    try {
      setLoading(true);
      setMessage({ type: "", text: "" });

      const response = await fetch("/api/auth/users", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: profileData.name,
          phone: profileData.phone,
          address: profileData.address,
          farmInfo: profileData.farmInfo,
          businessInfo: profileData.businessInfo,
          preferences: profileData.preferences,
        }),
      });

      if (response.ok) {
        const updatedUser = await response.json();
        updateUser(updatedUser.user);
        setMessage({ type: "success", text: "Profile updated successfully!" });

        // Also update localStorage to persist data
        localStorage.setItem(
          "farmfresh_user",
          JSON.stringify(updatedUser.user),
        );
      } else {
        const errorData = await response.json();
        setMessage({
          type: "error",
          text: errorData.error || "Failed to update profile",
        });
      }
    } catch (error) {
      setMessage({
        type: "error",
        text: "An error occurred while updating profile",
      });
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordChange = async () => {
    try {
      setLoading(true);
      setMessage({ type: "", text: "" });

      if (
        profileData.security.newPassword !==
        profileData.security.confirmPassword
      ) {
        setMessage({ type: "error", text: "New passwords don't match" });
        return;
      }

      if (profileData.security.newPassword.length < 6) {
        setMessage({
          type: "error",
          text: "Password must be at least 6 characters long",
        });
        return;
      }

      const response = await fetch("/api/auth/users", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          currentPassword: profileData.security.currentPassword,
          newPassword: profileData.security.newPassword,
        }),
      });

      if (response.ok) {
        setMessage({ type: "success", text: "Password updated successfully!" });
        setProfileData((prev) => ({
          ...prev,
          security: {
            currentPassword: "",
            newPassword: "",
            confirmPassword: "",
          },
        }));
      } else {
        const errorData = await response.json();
        setMessage({
          type: "error",
          text: errorData.error || "Failed to update password",
        });
      }
    } catch (error) {
      setMessage({
        type: "error",
        text: "An error occurred while updating password",
      });
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { id: "personal", label: "Personal Info", icon: "fas fa-user" },
    { id: "farm", label: "Farm Info", icon: "fas fa-seedling" },
    { id: "business", label: "Business", icon: "fas fa-building" },
    { id: "address", label: "Address", icon: "fas fa-map-marker-alt" },
    { id: "preferences", label: "Preferences", icon: "fas fa-cog" },
    { id: "security", label: "Security", icon: "fas fa-shield-alt" },
  ];

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            Farmer Profile Settings
          </h1>
          <p className="mt-2 text-gray-600 dark:text-gray-400">
            Manage your farmer account settings and farm information
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm">
          {/* Tab Navigation */}
          <div className="border-b border-gray-200 dark:border-gray-700">
            <nav className="flex space-x-8 px-6 overflow-x-auto">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 whitespace-nowrap ${
                    activeTab === tab.id
                      ? "border-primary-500 text-primary-600 dark:text-primary-400"
                      : "border-transparent text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300"
                  }`}
                >
                  <i className={tab.icon}></i>
                  <span>{tab.label}</span>
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="p-6">
            {message.text && (
              <div
                className={`mb-4 p-4 rounded-md ${
                  message.type === "success"
                    ? "bg-green-50 dark:bg-green-900 text-green-700 dark:text-green-300 border border-green-200 dark:border-green-800"
                    : "bg-red-50 dark:bg-red-900 text-red-700 dark:text-red-300 border border-red-200 dark:border-red-800"
                }`}
              >
                {message.text}
              </div>
            )}

            {/* Personal Information Tab */}
            {activeTab === "personal" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Personal Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Full Name
                    </label>
                    <input
                      type="text"
                      value={profileData.name}
                      onChange={(e) =>
                        handleInputChange("name", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Email Address
                    </label>
                    <input
                      type="email"
                      value={profileData.email}
                      disabled
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm bg-gray-50 dark:bg-gray-600 text-gray-500 dark:text-gray-400"
                    />
                    <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">
                      Email cannot be changed
                    </p>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      value={profileData.phone}
                      onChange={(e) =>
                        handleInputChange("phone", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Changes"}
                </button>
              </div>
            )}

            {/* Farm Information Tab */}
            {activeTab === "farm" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Farm Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Name *
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.farmName}
                      onChange={(e) =>
                        handleNestedChange("farmInfo", "", "farmName", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Green Valley Farm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Type
                    </label>
                    <select
                      value={profileData.farmInfo.farmType}
                      onChange={(e) =>
                        handleNestedChange("farmInfo", "", "farmType", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    >
                      <option value="">Select Farm Type</option>
                      <option value="organic">Organic Farm</option>
                      <option value="conventional">Conventional Farm</option>
                      <option value="hydroponic">Hydroponic Farm</option>
                      <option value="greenhouse">Greenhouse Farm</option>
                      <option value="mixed">Mixed Farm</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Size (acres)
                    </label>
                    <input
                      type="number"
                      value={profileData.farmInfo.farmSize}
                      onChange={(e) =>
                        handleNestedChange("farmInfo", "", "farmSize", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="10"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Established Year
                    </label>
                    <input
                      type="number"
                      value={profileData.farmInfo.establishedYear}
                      onChange={(e) =>
                        handleNestedChange("farmInfo", "", "establishedYear", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="2020"
                    />
                  </div>

                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Description
                    </label>
                    <textarea
                      value={profileData.farmInfo.farmDescription}
                      onChange={(e) =>
                        handleNestedChange("farmInfo", "", "farmDescription", e.target.value)
                      }
                      rows={4}
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Tell customers about your farm, your farming philosophy, and what makes your products special..."
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Certifications (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.certifications.join(", ")}
                      onChange={(e) =>
                        handleArrayChange("farmInfo", "certifications", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Organic Certified, Non-GMO, USDA Organic"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farming Methods (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.farmingMethods.join(", ")}
                      onChange={(e) =>
                        handleArrayChange("farmInfo", "farmingMethods", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Sustainable, Pesticide-free, Crop Rotation"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Farm Information"}
                </button>
              </div>
            )}

            {/* Business Information Tab */}
            {activeTab === "business" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Business Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Business License
                    </label>
                    <input
                      type="text"
                      value={profileData.businessInfo.businessLicense}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "", "businessLicense", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Tax ID
                    </label>
                    <input
                      type="text"
                      value={profileData.businessInfo.taxId}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "", "taxId", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Delivery Radius (miles)
                    </label>
                    <input
                      type="number"
                      value={profileData.businessInfo.deliveryRadius}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "", "deliveryRadius", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="25"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Minimum Order ($)
                    </label>
                    <input
                      type="number"
                      value={profileData.businessInfo.minimumOrder}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "", "minimumOrder", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="25"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Website
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.website}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "", "website", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://yourfarm.com"
                    />
                  </div>

                  {/* Social Media */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Facebook
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.facebook}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "socialMedia", "facebook", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://facebook.com/yourfarm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Instagram
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.instagram}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "socialMedia", "instagram", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://instagram.com/yourfarm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Twitter
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.twitter}
                      onChange={(e) =>
                        handleNestedChange("businessInfo", "socialMedia", "twitter", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://twitter.com/yourfarm"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Business Information"}
                </button>
              </div>
            )}

            {/* Address Tab */}
            {activeTab === "address" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Farm Address
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Street Address
                    </label>
                    <input
                      type="text"
                      value={profileData.address.street}
                      onChange={(e) =>
                        handleNestedChange("address", "", "street", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      City
                    </label>
                    <input
                      type="text"
                      value={profileData.address.city}
                      onChange={(e) =>
                        handleNestedChange("address", "", "city", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      State/Province
                    </label>
                    <input
                      type="text"
                      value={profileData.address.state}
                      onChange={(e) =>
                        handleNestedChange("address", "", "state", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      ZIP/Postal Code
                    </label>
                    <input
                      type="text"
                      value={profileData.address.zipCode}
                      onChange={(e) =>
                        handleNestedChange("address", "", "zipCode", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Country
                    </label>
                    <select
                      value={profileData.address.country}
                      onChange={(e) =>
                        handleNestedChange("address", "", "country", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    >
                      <option value="">Select Country</option>
                      <option value="US">United States</option>
                      <option value="CA">Canada</option>
                      <option value="UK">United Kingdom</option>
                      <option value="AU">Australia</option>
                      <option value="DE">Germany</option>
                      <option value="FR">France</option>
                    </select>
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Address"}
                </button>
              </div>
            )}

            {/* Preferences Tab */}
            {activeTab === "preferences" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Preferences
                </h3>

                <div className="space-y-6">
                  <div>
                    <h4 className="text-md font-medium text-gray-900 dark:text-white mb-4">
                      Notification Preferences
                    </h4>
                    <div className="space-y-3">
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.email}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "email", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Email notifications
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.sms}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "sms", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          SMS notifications
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.orderUpdates}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "orderUpdates", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Order status updates
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.lowStockAlerts}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "lowStockAlerts", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Low stock alerts
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.customerMessages}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "customerMessages", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Customer messages
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.notifications.marketingUpdates}
                          onChange={(e) =>
                            handleNestedChange("preferences", "notifications", "marketingUpdates", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Marketing updates
                        </span>
                      </label>
                    </div>
                  </div>

                  <div>
                    <h4 className="text-md font-medium text-gray-900 dark:text-white mb-4">
                      Business Preferences
                    </h4>
                    <div className="space-y-3">
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.autoAcceptOrders}
                          onChange={(e) =>
                            handleNestedChange("preferences", "", "autoAcceptOrders", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Auto-accept orders
                        </span>
                      </label>

                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          checked={profileData.preferences.showFarmLocation}
                          onChange={(e) =>
                            handleNestedChange("preferences", "", "showFarmLocation", e.target.checked)
                          }
                          className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          Show farm location to customers
                        </span>
                      </label>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Language
                      </label>
                      <select
                        value={profileData.preferences.language}
                        onChange={(e) =>
                          handleNestedChange("preferences", "", "language", e.target.value)
                        }
                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      >
                        <option value="en">English</option>
                        <option value="es">Spanish</option>
                        <option value="fr">French</option>
                        <option value="de">German</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Currency
                      </label>
                      <select
                        value={profileData.preferences.currency}
                        onChange={(e) =>
                          handleNestedChange("preferences", "", "currency", e.target.value)
                        }
                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      >
                        <option value="USD">USD ($)</option>
                        <option value="EUR">EUR (€)</option>
                        <option value="GBP">GBP (£)</option>
                        <option value="CAD">CAD ($)</option>
                      </select>
                    </div>
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Preferences"}
                </button>
              </div>
            )}

            {/* Security Tab */}
            {activeTab === "security" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Security Settings
                </h3>

                <div className="max-w-md space-y-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Current Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.currentPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "currentPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      New Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.newPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "newPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Confirm New Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.confirmPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "confirmPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <button
                    onClick={handlePasswordChange}
                    disabled={loading}
                    className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? "Updating..." : "Update Password"}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Simple script to check the review-product mismatch
const { MongoClient } = require('mongodb');

async function checkMismatch() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Checking product-review mismatch...\n');

  // Get all products
  const products = await db.collection('products').find({}).toArray();
  console.log(` Total products: ${products.length}`);
  console.log('Product IDs:', products.slice(0, 5).map(p => p._id.toString()));

  // Get all reviews  
  const reviews = await db.collection('reviews').find({}).toArray();
  console.log(`\n Total reviews: ${reviews.length}`);
  console.log('Review product IDs:', reviews.map(r => r.productId));

  // Check for matches
  const productIds = products.map(p => p._id.toString());
  const reviewProductIds = reviews.map(r => r.productId);

  const matches = productIds.filter(pid => reviewProductIds.includes(pid));
  console.log(`\n✅ Matching product-review pairs: ${matches.length}`);

  if (matches.length > 0) {
    console.log('Matched IDs:', matches);

    // Show which products have reviews
    for (const matchId of matches) {
      const product = products.find(p => p._id.toString() === matchId);
      const productReviews = reviews.filter(r => r.productId === matchId);
      console.log(`- ${product.name}: ${productReviews.length} reviews`);
    }
  } else {
    console.log('\n❌ No matches found!');
    console.log('This means the existing reviews belong to products that no longer exist');
    console.log('or have different IDs than the current products in the database');
  }

  await client.close();
}

checkMismatch().catch(console.error);

// Debug why old products with reviews aren't showing ratings on products page
const { MongoClient, ObjectId } = require('mongodb');

async function debugOldProductReviews() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Debugging old product reviews...\n');

  // Get all reviews
  const allReviews = await db.collection('reviews').find({}).toArray();
  console.log(` Total reviews in database: ${allReviews.length}`);

  // Get sample of old products (first 5)
  const oldProducts = await db.collection('products').find({}).limit(5).toArray();
  console.log(` Checking first 5 products:\n`);

  for (const product of oldProducts) {
    const productId = product._id.toString();
    const productReviews = allReviews.filter(review => review.productId === productId);

    console.log(`Product: ${product.name} (ID: ${productId})`);
    console.log(`  Reviews found: ${productReviews.length}`);
    if (productReviews.length > 0) {
      console.log(`  Sample review: ${productReviews[0].comment?.substring(0, 50)}...`);
      console.log(`  Review rating: ${productReviews[0].rating}`);
    }
    console.log('');
  }

  // Check the structure of all reviews
  console.log(' All review product IDs:');
  const reviewProductIds = [...new Set(allReviews.map(r => r.productId))];
  reviewProductIds.forEach(id => {
    const count = allReviews.filter(r => r.productId === id).length;
    console.log(`  ${id}: ${count} reviews`);
  });

  await client.close();
}

debugOldProductReviews().catch(console.error);

