option.value:
import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

// Track if indexes have been initialized to avoid repeated calls
let indexesInitialized = false;

// Initialize indexes optimized for MongoDB Atlas performance
async function initializeOrderIndexes(db) {
  // Only initialize once per application lifecycle
  if (indexesInitialized) {
    return;
  }

  try {
    const ordersCollection = db.collection("orders");
    const productsCollection = db.collection("products");

    // Check if indexes already exist before creating them
    const existingIndexes = await ordersCollection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Atlas-optimized compound indexes for better performance
    const indexesToCreate = [
      // Core user queries
      {
        key: { userId: 1, createdAt: -1 },
        name: "userId_createdAt_idx",
        options: { background: true },
      },
      // Farmer queries - optimized for Atlas
      {
        key: { "items.farmerId": 1, status: 1, createdAt: -1 },
        name: "items_farmerId_status_date_idx",
        options: { background: true },
      },
      {
        key: { "items.farmerEmail": 1, status: 1, createdAt: -1 },
        name: "items_farmerEmail_status_date_idx",
        options: { background: true },
      },
      // Alternative farmer fields
      {
        key: { farmerIds: 1, createdAt: -1 },
        name: "farmerIds_createdAt_idx",
        options: { background: true },
      },
      {
        key: { farmerEmails: 1, createdAt: -1 },
        name: "farmerEmails_createdAt_idx",
        options: { background: true },
      },
      // Product queries
      {
        key: { "items.productId": 1, createdAt: -1 },
        name: "items_productId_createdAt_idx",
        options: { background: true },
      },
      // Status and date queries
      {
        key: { status: 1, createdAt: -1 },
        name: "status_createdAt_idx",
        options: { background: true },
      },
      // General date sorting
      {
        key: { createdAt: -1 },
        name: "createdAt_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await ordersCollection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    // Products collection indexes for order operations
    const productIndexes = await productsCollection.listIndexes().toArray();
    const productIndexNames = productIndexes.map((index) => index.name);

    if (!productIndexNames.includes("stock_status_idx")) {
      await productsCollection.createIndex(
        { stock: 1, status: 1 },
        { name: "stock_status_idx", background: true },
      );
    }

    indexesInitialized = true;
    console.log("Atlas-optimized order indexes initialized successfully");
  } catch (error) {
    console.log("Order index initialization note:", error.message);
  }
}

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);
    const userId = searchParams.get("userId");
    const orderId = searchParams.get("orderId");
    const farmerId = searchParams.get("farmerId");
    const farmerEmail = searchParams.get("farmerEmail");
    const productId = searchParams.get("productId");
    const limit = parseInt(searchParams.get("limit")) || null;

    if (!userId && !orderId && !farmerId && !farmerEmail && !productId) {
      return NextResponse.json(
        {
          error:
            "User ID, Order ID, Farmer ID, Farmer Email, or Product ID is required",
        },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Initialize indexes only once - optimized to prevent repeated calls
    await initializeOrderIndexes(db);

    if (orderId) {
      // Get specific order using optimized query
      const orderPipeline = [
        { $match: { _id: new ObjectId(orderId) } },
        { $limit: 1 },
      ];

      const [order] = await db
        .collection("orders")
        .aggregate(orderPipeline)
        .toArray();

      if (!order) {
        return NextResponse.json({ error: "Order not found" }, { status: 404 });
      }

      return NextResponse.json({ order });
    } else if (productId) {
      // Get orders containing a specific product using aggregation pipeline
      const productOrdersPipeline = [
        {
          $match: {
            $or: [
              { "items.productId": productId },
              { "items._id": productId },
              { "products.productId": productId },
              { "products._id": productId },
              ...(ObjectId.isValid(productId)
                ? [
                    { "items.productId": new ObjectId(productId) },
                    { "items._id": new ObjectId(productId) },
                    { "products.productId": new ObjectId(productId) },
                    { "products._id": new ObjectId(productId) },
                  ]
                : []),
            ],
          },
        },
        {
          $addFields: {
            matchingItems: {
              $filter: {
                input: { $ifNull: ["$items", "$products"] },
                cond: {
                  $or: [
                    { $eq: ["$$this.productId", productId] },
                    { $eq: ["$$this._id", productId] },
                    ...(ObjectId.isValid(productId)
                      ? [
                          {
                            $eq: ["$$this.productId", new ObjectId(productId)],
                          },
                          { $eq: ["$$this._id", new ObjectId(productId)] },
                        ]
                      : []),
                  ],
                },
              },
            },
          },
        },
        {
          $addFields: {
            items: "$matchingItems",
            products: "$matchingItems",
            matchingItemsCount: { $size: "$matchingItems" },
          },
        },
        { $match: { matchingItemsCount: { $gt: 0 } } },
        { $sort: { createdAt: -1 } },
        ...(limit ? [{ $limit: limit }] : []),
      ];

      const orders = await db
        .collection("orders")
        .aggregate(productOrdersPipeline)
        .toArray();

      return NextResponse.json({
        orders,
        total: orders.length,
      });
    } else if (farmerId || farmerEmail) {
      // Get orders for farmer using optimized aggregation pipeline
      let farmerMatchCriteria = { $or: [] };

      // Build safer match criteria for farmer queries
      if (farmerId) {
        // Handle both string and ObjectId formats for farmerId
        if (ObjectId.isValid(farmerId)) {
          farmerMatchCriteria.$or.push(
            { "items.farmerId": farmerId },
            { "items.farmerId": new ObjectId(farmerId) },
            { farmerIds: farmerId },
            { farmerIds: new ObjectId(farmerId) },
          );
        } else {
          farmerMatchCriteria.$or.push(
            { "items.farmerId": farmerId },
            { farmerIds: farmerId },
          );
        }
      }

      if (farmerEmail) {
        farmerMatchCriteria.$or.push(
          { "items.farmerEmail": farmerEmail },
          { farmerEmails: farmerEmail },
        );
      }

      // If no valid criteria, return empty result
      if (farmerMatchCriteria.$or.length === 0) {
        return NextResponse.json({
          orders: [],
          message: "No valid farmer criteria provided",
        });
      }

      const farmerOrdersPipeline = [
        { $match: farmerMatchCriteria },
        // Add allowDiskUse option and optimize the pipeline order
        {
          $addFields: {
            farmerItems: {
              $filter: {
                input: { $ifNull: ["$items", []] },
                cond: {
                  $or: [
                    ...(farmerId
                      ? [
                          { $eq: ["$$this.farmerId", farmerId] },
                          ...(ObjectId.isValid(farmerId)
                            ? [
                                {
                                  $eq: [
                                    "$$this.farmerId",
                                    new ObjectId(farmerId),
                                  ],
                                },
                              ]
                            : []),
                        ]
                      : []),
                    ...(farmerEmail
                      ? [{ $eq: ["$$this.farmerEmail", farmerEmail] }]
                      : []),
                  ],
                },
              },
            },
          },
        },
        // Filter out orders with no matching items early to reduce dataset size
        {
          $match: {
            $expr: { $gt: [{ $size: { $ifNull: ["$farmerItems", []] } }, 0] },
          },
        },
        {
          $addFields: {
            items: "$farmerItems",
            farmerSubtotal: {
              $sum: {
                $map: {
                  input: "$farmerItems",
                  as: "item",
                  in: {
                    $multiply: [
                      { $ifNull: ["$$item.price", 0] },
                      { $ifNull: ["$$item.quantity", 0] },
                    ],
                  },
                },
              },
            },
          },
        },
        // Sort after filtering to reduce memory usage
        { $sort: { createdAt: -1 } },
        ...(limit ? [{ $limit: limit }] : [{ $limit: 100 }]), // Always limit to prevent memory issues
      ];

      try {
        const orders = await db
          .collection("orders")
          .aggregate(farmerOrdersPipeline, {
            allowDiskUse: true, // Enable disk-based sorting
            maxTimeMS: 30000, // 30 second timeout
          })
          .toArray();

        return NextResponse.json({
          orders,
          total: orders.length,
          message: `Found ${orders.length} orders for farmer`,
        });
      } catch (aggregationError) {
        console.error("Farmer orders aggregation error:", aggregationError);

        // Fallback to simpler query if aggregation fails
        const simpleQuery = {};
        if (farmerId) {
          simpleQuery["items.farmerId"] = farmerId;
        }
        if (farmerEmail) {
          simpleQuery["items.farmerEmail"] = farmerEmail;
        }

        const fallbackOrders = await db
          .collection("orders")
          .find(simpleQuery)
          .sort({ createdAt: -1 })
          .limit(limit || 100)
          .toArray();

        return NextResponse.json({
          orders: fallbackOrders,
          total: fallbackOrders.length,
          message: `Found ${fallbackOrders.length} orders for farmer (fallback query)`,
        });
      }
    } else {
      // Get orders for customer using optimized query
      const customerOrdersPipeline = [
        {
          $match: {
            $or: [
              { userId: userId },
              ...(ObjectId.isValid(userId)
                ? [{ userId: new ObjectId(userId) }]
                : []),
            ],
          },
        },
        { $sort: { createdAt: -1 } },
      ];

      const orders = await db
        .collection("orders")
        .aggregate(customerOrdersPipeline)
        .toArray();

      return NextResponse.json({
        orders,
        message: `Found ${orders.length} orders for customer`,
      });
    }
  } catch (error) {
    console.error("Orders API error:", error);
    return NextResponse.json(
      { error: "Internal server error", details: error.message },
      { status: 500 },
    );
  }
}

export async function POST(request) {
  try {
    const orderData = await request.json();

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Initialize indexes for optimal performance
    await initializeOrderIndexes(db);

    // Optimize stock validation using aggregation pipeline
    const productIds = orderData.items.map(
      (item) => new ObjectId(item.productId),
    );

    const stockValidationPipeline = [
      { $match: { _id: { $in: productIds } } },
      {
        $project: {
          _id: 1,
          stock: 1,
          name: 1,
        },
      },
    ];

    const products = await db
      .collection("products")
      .aggregate(stockValidationPipeline)
      .toArray();

    // Create lookup map for faster validation
    const productMap = new Map(products.map((p) => [p._id.toString(), p]));

    // Validate stock availability
    const stockUpdates = [];
    for (const item of orderData.items) {
      const product = productMap.get(item.productId);

      if (!product) {
        throw new Error(`Product ${item.name} not found`);
      }

      if (product.stock < item.quantity) {
        throw new Error(
          `Insufficient stock for ${item.name}. Available: ${product.stock}, Requested: ${item.quantity}`,
        );
      }

      stockUpdates.push({
        updateOne: {
          filter: { _id: new ObjectId(item.productId) },
          update: {
            $inc: { stock: -item.quantity },
            $set: { updatedAt: new Date() },
          },
        },
      });
    }

    // Perform bulk stock updates for better performance
    if (stockUpdates.length > 0) {
      await db.collection("products").bulkWrite(stockUpdates);
    }

    // Add timestamps to order
    const newOrder = {
      ...orderData,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    // Create the order
    const result = await db.collection("orders").insertOne(newOrder);

    return NextResponse.json({
      message: "Order created successfully",
      orderId: result.insertedId,
      order: { ...newOrder, _id: result.insertedId },
    });
  } catch (error) {
    console.error("Create order error:", error);
    return NextResponse.json(
      { error: "Failed to create order", details: error.message },
      { status: 500 },
    );
  }
}

export async function PATCH(request) {
  try {
    const { orderId, ...updateData } = await request.json();

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Get the current order to check status changes (without transactions)
    const currentOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    if (!currentOrder) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Check if order status is being changed to cancelled/returned
    const isBeingCancelled =
      updateData.status &&
      (updateData.status === "cancelled" || updateData.status === "returned") &&
      currentOrder.status !== "cancelled" &&
      currentOrder.status !== "returned";

    // If order is being cancelled, restore stock
    if (isBeingCancelled && currentOrder.items) {
      console.log(
        `Order ${orderId} is being cancelled/returned, restoring stock...`,
      );

      for (const item of currentOrder.items) {
        const productId = item.productId;
        const orderQuantity = item.quantity;

        // Get current product
        const product = await db
          .collection("products")
          .findOne({ _id: new ObjectId(productId) });

        if (product) {
          // Restore product stock
          const newStock = product.stock + orderQuantity;
          await db.collection("products").updateOne(
            { _id: new ObjectId(productId) },
            {
              $set: {
                stock: newStock,
                updatedAt: new Date(),
              },
            },
          );
        } else {
          console.warn(`Product ${productId} not found when restoring stock`);
        }
      }
    }

    // Update the order
    const result = await db.collection("orders").updateOne(
      { _id: new ObjectId(orderId) },
      {
        $set: {
          ...updateData,
          updatedAt: new Date(),
        },
      },
    );

    if (result.matchedCount === 0) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Get the updated order
    const updatedOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    return NextResponse.json({
      message: "Order updated successfully",
      order: updatedOrder,
    });
  } catch (error) {
    console.error("Update order error:", error);
    return NextResponse.json(
      { error: "Failed to update order", details: error.message },
      { status: 500 },
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

// Track if indexes have been initialized to avoid repeated calls
let indexesInitialized = false;

// Initialize indexes optimized for MongoDB Atlas performance
async function initializeOrderIndexes(db) {
  // Only initialize once per application lifecycle
  if (indexesInitialized) {
    return;
  }

  try {
    const ordersCollection = db.collection("orders");
    const productsCollection = db.collection("products");

    // Check if indexes already exist before creating them
    const existingIndexes = await ordersCollection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Atlas-optimized compound indexes for better performance
    const indexesToCreate = [
      // Core user queries
      {
        key: { userId: 1, createdAt: -1 },
        name: "userId_createdAt_idx",
        options: { background: true },
      },
      // Farmer queries - optimized for Atlas
      {
        key: { "items.farmerId": 1, status: 1, createdAt: -1 },
        name: "items_farmerId_status_date_idx",
        options: { background: true },
      },
      {
        key: { "items.farmerEmail": 1, status: 1, createdAt: -1 },
        name: "items_farmerEmail_status_date_idx",
        options: { background: true },
      },
      // Alternative farmer fields
      {
        key: { farmerIds: 1, createdAt: -1 },
        name: "farmerIds_createdAt_idx",
        options: { background: true },
      },
      {
        key: { farmerEmails: 1, createdAt: -1 },
        name: "farmerEmails_createdAt_idx",
        options: { background: true },
      },
      // Product queries
      {
        key: { "items.productId": 1, createdAt: -1 },
        name: "items_productId_createdAt_idx",
        options: { background: true },
      },
      // Status and date queries
      {
        key: { status: 1, createdAt: -1 },
        name: "status_createdAt_idx",
        options: { background: true },
      },
      // General date sorting
      {
        key: { createdAt: -1 },
        name: "createdAt_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await ordersCollection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    // Products collection indexes for order operations
    const productIndexes = await productsCollection.listIndexes().toArray();
    const productIndexNames = productIndexes.map((index) => index.name);

    if (!productIndexNames.includes("stock_status_idx")) {
      await productsCollection.createIndex(
        { stock: 1, status: 1 },
        { name: "stock_status_idx", background: true },
      );
    }

    indexesInitialized = true;
    console.log("Atlas-optimized order indexes initialized successfully");
  } catch (error) {
    console.log("Order index initialization note:", error.message);
  }
}

// Cache for database connection and collections
let cachedDb = null;
let cachedOrdersCollection = null;

// Response cache for identical requests (3 minutes for orders - shorter than products)
const responseCache = new Map();
const CACHE_TTL = 3 * 60 * 1000;

// Generate cache key for request
function generateCacheKey(searchParams) {
  const params = {};
  searchParams.forEach((value, key) => {
    params[key] = value;
  });
  return JSON.stringify(params);
}

// Get cached response if available and not expired
function getCachedResponse(cacheKey) {
  const cached = responseCache.get(cacheKey);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }
  responseCache.delete(cacheKey);
  return null;
}

// Set response in cache
function setCachedResponse(cacheKey, data) {
  responseCache.set(cacheKey, {
    data,
    timestamp: Date.now(),
  });

  // Clean up expired entries
  if (responseCache.size > 50) {
    const now = Date.now();
    for (const [key, value] of responseCache.entries()) {
      if (now - value.timestamp >= CACHE_TTL) {
        responseCache.delete(key);
      }
    }
  }
}

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);

    // Check cache first
    const cacheKey = generateCacheKey(searchParams);
    const cachedResponse = getCachedResponse(cacheKey);
    if (cachedResponse) {
      return NextResponse.json(cachedResponse);
    }

    const userId = searchParams.get("userId");
    const farmerId = searchParams.get("farmerId");
    const farmerEmail = searchParams.get("farmerEmail");
    const status = searchParams.get("status");
    const limit = parseInt(searchParams.get("limit")) || 50;
    const page = parseInt(searchParams.get("page")) || 1;

    // Reuse database connection
    if (!cachedDb) {
      const client = await clientPromise;
      cachedDb = client.db("farmfresh");
      cachedOrdersCollection = cachedDb.collection("orders");
    }

    // Initialize indexes
    await initializeOrderIndexes(cachedDb);

    // Build optimized query
    const query = {};

    if (userId) {
      query.userId = userId;
    }

    if (status) {
      query.status = status;
    }

    // Optimized farmer filtering for better Atlas performance
    if (farmerId || farmerEmail) {
      const farmerConditions = [];

      if (farmerId) {
        farmerConditions.push(
          { "items.farmerId": farmerId },
          { "items.farmer.id": farmerId },
          { "items.farmer._id": farmerId },
          { farmerIds: farmerId },
        );
      }

      if (farmerEmail) {
        farmerConditions.push(
          { "items.farmerEmail": farmerEmail },
          { "items.farmer.email": farmerEmail },
          { farmerEmails: farmerEmail },
        );
      }

      query.$or = farmerConditions;
    }

    // Optimized projection - reduce data transfer
    const projection = {
      _id: 1,
      userId: 1,
      status: 1,
      total: 1,
      farmerSubtotal: 1,
      shippingAddress: 1,
      paymentMethod: 1,
      createdAt: 1,
      updatedAt: 1,
      // Include essential item fields only
      "items._id": 1,
      "items.productId": 1,
      "items.name": 1,
      "items.price": 1,
      "items.quantity": 1,
      "items.subtotal": 1,
      "items.farmerId": 1,
      "items.farmerEmail": 1,
      "items.farmerName": 1,
      "items.farmer": 1,
      // Exclude heavy fields like detailed product data, full user info, etc.
    };

    // Use aggregation pipeline for better Atlas performance
    const pipeline = [
      { $match: query },
      { $project: projection },
      { $sort: { createdAt: -1 } },
    ];

    // Add pagination
    if (limit < 1000) {
      pipeline.push(
        { $skip: (page - 1) * limit },
        { $limit: limit },
      );
    }

    // Execute optimized query
    const startTime = Date.now();
    const [orders, totalCount] = await Promise.all([
      cachedOrdersCollection.aggregate(pipeline).toArray(),
      // Only count if we need pagination
      limit < 1000
        ? cachedOrdersCollection.countDocuments(query)
        : Promise.resolve(orders.length),
    ]);

    const queryTime = Date.now() - startTime;
    console.log(
      `Atlas orders query executed in ${queryTime}ms for ${orders.length} orders`,
    );

    // Build response
    const response = {
      orders,
      pagination: {
        page,
        limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / limit),
        hasNext: page * limit < totalCount,
        hasPrev: page > 1,
      },
      meta: {
        queryTime,
        cached: false,
      },
    };

    // Cache the response
    setCachedResponse(cacheKey, response);

    return NextResponse.json(response);
  } catch (error) {
    console.error("Orders API Error:", error);
    return NextResponse.json(
      {
        error: "Failed to fetch orders",
        details: error.message,
        orders: [],
        pagination: {
          page: 1,
          limit: 50,
          total: 0,
          totalPages: 0,
          hasNext: false,
          hasPrev: false,
        },
      },
      { status: 500 },
    );
  }
}

export async function POST(request) {
  try {
    const orderData = await request.json();

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Initialize indexes for optimal performance
    await initializeOrderIndexes(db);

    // Optimize stock validation using aggregation pipeline
    const productIds = orderData.items.map(
      (item) => new ObjectId(item.productId),
    );

    const stockValidationPipeline = [
      { $match: { _id: { $in: productIds } } },
      {
        $project: {
          _id: 1,
          stock: 1,
          name: 1,
        },
      },
    ];

    const products = await db
      .collection("products")
      .aggregate(stockValidationPipeline)
      .toArray();

    // Create lookup map for faster validation
    const productMap = new Map(products.map((p) => [p._id.toString(), p]));

    // Validate stock availability
    const stockUpdates = [];
    for (const item of orderData.items) {
      const product = productMap.get(item.productId);

      if (!product) {
        throw new Error(`Product ${item.name} not found`);
      }

      if (product.stock < item.quantity) {
        throw new Error(
          `Insufficient stock for ${item.name}. Available: ${product.stock}, Requested: ${item.quantity}`,
        );
      }

      stockUpdates.push({
        updateOne: {
          filter: { _id: new ObjectId(item.productId) },
          update: {
            $inc: { stock: -item.quantity },
            $set: { updatedAt: new Date() },
          },
        },
      });
    }

    // Perform bulk stock updates for better performance
    if (stockUpdates.length > 0) {
      await db.collection("products").bulkWrite(stockUpdates);
    }

    // Add timestamps to order
    const newOrder = {
      ...orderData,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    // Create the order
    const result = await db.collection("orders").insertOne(newOrder);

    return NextResponse.json({
      message: "Order created successfully",
      orderId: result.insertedId,
      order: { ...newOrder, _id: result.insertedId },
    });
  } catch (error) {
    console.error("Create order error:", error);
    return NextResponse.json(
      { error: "Failed to create order", details: error.message },
      { status: 500 },
    );
  }
}

export async function PATCH(request) {
  try {
    const { orderId, ...updateData } = await request.json();

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Get the current order to check status changes (without transactions)
    const currentOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    if (!currentOrder) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Check if order status is being changed to cancelled/returned
    const isBeingCancelled =
      updateData.status &&
      (updateData.status === "cancelled" || updateData.status === "returned") &&
      currentOrder.status !== "cancelled" &&
      currentOrder.status !== "returned";

    // If order is being cancelled, restore stock
    if (isBeingCancelled && currentOrder.items) {
      console.log(
        `Order ${orderId} is being cancelled/returned, restoring stock...`,
      );

      for (const item of currentOrder.items) {
        const productId = item.productId;
        const orderQuantity = item.quantity;

        // Get current product
        const product = await db
          .collection("products")
          .findOne({ _id: new ObjectId(productId) });

        if (product) {
          // Restore product stock
          const newStock = product.stock + orderQuantity;
          await db.collection("products").updateOne(
            { _id: new ObjectId(productId) },
            {
              $set: {
                stock: newStock,
                updatedAt: new Date(),
              },
            },
          );
        } else {
          console.warn(`Product ${productId} not found when restoring stock`);
        }
      }
    }

    // Update the order
    const result = await db.collection("orders").updateOne(
      { _id: new ObjectId(orderId) },
      {
        $set: {
          ...updateData,
          updatedAt: new Date(),
        },
      },
    );

    if (result.matchedCount === 0) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Get the updated order
    const updatedOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    return NextResponse.json({
      message: "Order updated successfully",
      order: updatedOrder,
    });
  } catch (error) {
    console.error("Update order error:", error);
    return NextResponse.json(
      { error: "Failed to update order", details: error.message },
      { status: 500 },
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { enhanceProductsWithRatings } from "@/lib/reviewUtils";

// Track if indexes have been initialized to avoid repeated calls
let productIndexesInitialized = false;
// Cache for database connection and collections
let cachedDb = null;
let cachedCollection = null;

// Response cache for identical requests (5 minutes)
const responseCache = new Map();
const CACHE_TTL = 5 * 60 * 1000;

// Initialize indexes optimized for MongoDB Atlas performance
async function initializeProductIndexes(db) {
  // Only initialize once per application lifecycle
  if (productIndexesInitialized) {
    return;
  }

  try {
    const collection = db.collection("products");

    // Check if indexes already exist before creating them
    const existingIndexes = await collection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Simplified Atlas-optimized compound indexes for better performance
    const indexesToCreate = [
      // Primary query index - most common pattern
      {
        key: { status: 1, category: 1, createdAt: -1 },
        name: "primary_query_idx",
        options: { background: true },
      },
      // Search index
      {
        key: { name: "text", description: "text", category: "text" },
        name: "products_text_search_idx",
        options: {
          background: true,
          weights: { name: 10, category: 5, description: 1 },
        },
      },
      // Price and rating filters
      {
        key: { status: 1, price: 1, averageRating: -1 },
        name: "price_rating_idx",
        options: { background: true },
      },
      // Farmer queries
      {
        key: { "farmer._id": 1, status: 1 },
        name: "farmer_status_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await collection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    productIndexesInitialized = true;
    console.log("Atlas-optimized product indexes initialized successfully");
  } catch (error) {
    console.log("Product index initialization note:", error.message);
  }
}

// Generate cache key for request
function generateCacheKey(searchParams) {
  const params = {};
  searchParams.forEach((value, key) => {
    params[key] = value;
  });
  return JSON.stringify(params);
}

// Get cached response if available and not expired
function getCachedResponse(cacheKey) {
  const cached = responseCache.get(cacheKey);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }
  responseCache.delete(cacheKey);
  return null;
}

// Set response in cache
function setCachedResponse(cacheKey, data) {
  responseCache.set(cacheKey, {
    data,
    timestamp: Date.now(),
  });

  // Clean up expired entries if cache gets too large
  if (responseCache.size > 100) {
    const now = Date.now();
    for (const [key, value] of responseCache.entries()) {
      if (now - value.timestamp >= CACHE_TTL) {
        responseCache.delete(key);
      }
    }
  }
}

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);

    // Check cache first
    const cacheKey = generateCacheKey(searchParams);
    const cachedResponse = getCachedResponse(cacheKey);
    if (cachedResponse) {
      return NextResponse.json(cachedResponse);
    }

    const search = searchParams.get("search");
    const category = searchParams.get("category");
    const featured = searchParams.get("featured");
    const sortBy = searchParams.get("sortBy");
    const farmerId = searchParams.get("farmerId");
    const farmerEmail = searchParams.get("farmerEmail");
    const limit = searchParams.get("limit")
      ? parseInt(searchParams.get("limit"))
      : 12; // Default to 12 for pagination
    const page = parseInt(searchParams.get("page")) || 1;

    // Simplified filtering parameters
    const minPrice = searchParams.get("minPrice")
      ? parseFloat(searchParams.get("minPrice"))
      : null;
    const maxPrice = searchParams.get("maxPrice")
      ? parseFloat(searchParams.get("maxPrice"))
      : null;
    const minRating = searchParams.get("minRating")
      ? parseFloat(searchParams.get("minRating"))
      : null;

    // Reuse database connection and collection
    if (!cachedDb) {
      const client = await clientPromise;
      cachedDb = client.db("farmfresh");
      cachedCollection = cachedDb.collection("products");
    }

    // Initialize indexes only once
    await initializeProductIndexes(cachedDb);

    // Build optimized query for Atlas
    const query = { status: { $ne: "deleted" } };

    // Add search filter using text index for better performance
    if (search) {
      query.$text = { $search: search };
    }

    // Add category filter
    if (category && category !== "All Categories") {
      query.category = { $regex: new RegExp(category, "i") };
    }

    // Add featured filter
    if (featured === "true") {
      query.featured = true;
    }

    // Add farmer filters for dashboard
    if (farmerId || farmerEmail) {
      query.$or = [];
      if (farmerId) {
        query.$or.push(
          { farmerId: farmerId },
          { farmerId: { $eq: farmerId } },
          { "farmer.id": farmerId },
          { "farmer._id": farmerId },
        );
      }
      if (farmerEmail) {
        query.$or.push(
          { farmerEmail: farmerEmail },
          { "farmer.email": farmerEmail },
        );
      }
    }

    // Add price range filter
    if (minPrice !== null || maxPrice !== null) {
      query.price = {};
      if (minPrice !== null) query.price.$gte = minPrice;
      if (maxPrice !== null) query.price.$lte = maxPrice;
    }

    // Add rating filter
    if (minRating !== null) {
      query.averageRating = { $gte: minRating };
    }

    // Optimized projection - only select needed fields to reduce data transfer
    const projection = {
      _id: 1,
      name: 1,
      description: 1,
      price: 1,
      stock: 1,
      images: 1,
      category: 1,
      averageRating: 1,
      totalReviews: 1,
      featured: 1,
      status: 1,
      createdAt: 1,
      updatedAt: 1,
      farmer: 1,
      farmerId: 1,
      farmerEmail: 1,
      farmerName: 1,
      tags: 1,
      isOrganic: 1,
      isFresh: 1,
      purchaseCount: 1,
      // Exclude heavy fields like detailed descriptions, reviews array, etc.
    };

    // Build sort options for better Atlas performance
    let sortOptions = {};
    if (search) {
      // Text search score for relevance
      sortOptions = { score: { $meta: "textScore" }, createdAt: -1 };
    } else {
      switch (sortBy) {
        case "price-low":
          sortOptions = { price: 1 };
          break;
        case "price-high":
          sortOptions = { price: -1 };
          break;
        case "rating":
          sortOptions = { averageRating: -1, totalReviews: -1 };
          break;
        case "popular":
          sortOptions = { purchaseCount: -1, averageRating: -1 };
          break;
        case "oldest":
          sortOptions = { createdAt: 1 };
          break;
        case "newest":
        default:
          sortOptions = { createdAt: -1 };
          break;
      }
    }

    // Use aggregation pipeline for better Atlas performance
    const pipeline = [
      { $match: query },
      { $project: projection },
      { $sort: sortOptions },
    ];

    // Add pagination only if needed
    if (limit < 1000) {
      pipeline.push({ $skip: (page - 1) * limit }, { $limit: limit });
    }

    // Execute optimized query
    const startTime = Date.now();
    const [products, totalCount] = await Promise.all([
      cachedCollection.aggregate(pipeline).toArray(),
      // Only count if we need pagination
      limit < 1000
        ? cachedCollection.countDocuments(query)
        : Promise.resolve(products.length),
    ]);

    const queryTime = Date.now() - startTime;
    console.log(
      `Atlas query executed in ${queryTime}ms for ${products.length} products`,
    );

    // Enhance with ratings only if needed (for detailed views)
    const enhancedProducts =
      search || limit <= 50
        ? await enhanceProductsWithRatings(products, cachedDb)
        : products; // Skip rating enhancement for large datasets

    // Build response
    const response = {
      products: enhancedProducts,
      pagination: {
        page,
        limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / limit),
        hasNext: page * limit < totalCount,
        hasPrev: page > 1,
      },
      meta: {
        queryTime,
        cached: false,
      },
    };

    // Cache the response
    setCachedResponse(cacheKey, response);

    return NextResponse.json(response);
  } catch (error) {
    console.error("Products API Error:", error);
    return NextResponse.json(
      {
        error: "Failed to fetch products",
        details: error.message,
        products: [],
        pagination: {
          page: 1,
          limit: 12,
          total: 0,
          totalPages: 0,
          hasNext: false,
          hasPrev: false,
        },
      },
      { status: 500 },
    );
  }
}

export async function POST(request) {
  try {
    const productData = await request.json();

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Clear cache when new product is added
    responseCache.clear();

    const result = await db.collection("products").insertOne({
      ...productData,
      createdAt: new Date(),
      updatedAt: new Date(),
    });

    return NextResponse.json(
      { message: "Product created successfully", id: result.insertedId },
      { status: 201 },
    );
  } catch (error) {
    console.error("Error creating product:", error);
    return NextResponse.json(
      { error: "Failed to create product" },
      { status: 500 },
    );
  }
}

