export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);

  // Enhanced state for new features
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed"); // 'detailed', 'compact'
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Performance optimizations
  const [requestInProgress, setRequestInProgress] = useState(false);

  const intervalRef = useRef(null);
  const abortControllerRef = useRef(null);
  const cacheRef = useRef(new Map());

  const ordersPerPage = viewMode === "compact" ? 20 : 10;
  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes
  const MAX_RETRY_ATTEMPTS = 3;
  const RETRY_DELAY = 1000; // 1 second

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Optimized cache management
  const getCacheKey = useCallback((userId, userEmail) => {
    return `farmer-orders-${userId || userEmail}`;
  }, []);

  const getCachedData = useCallback(
    (cacheKey) => {
      const cached = cacheRef.current.get(cacheKey);
      if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
        return cached.data;
      }
      return null;
    },
    [CACHE_DURATION],
  );

  const setCachedData = useCallback(
    (cacheKey, data) => {
      cacheRef.current.set(cacheKey, {
        data,
        timestamp: Date.now(),
      });
      // Clean up old cache entries
      for (const [key, value] of cacheRef.current.entries()) {
        if (Date.now() - value.timestamp > CACHE_DURATION) {
          cacheRef.current.delete(key);
        }
      }
    },
    [CACHE_DURATION],
  );

  // Enhanced file download with better error handling
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion with better data handling
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email",
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Enhanced fetch function with retry logic
  const fetchOrdersWithRetry = useCallback(
    async (showLoading = true, retryAttempt = 0) => {
      if (!session?.user || requestInProgress) return;

      try {
        if (showLoading) setLoading(true);
        setRequestInProgress(true);

        const userId =
          session.user.userId || session.user.id || session.user._id;
        const userEmail = session.user.email;
        const cacheKey = getCacheKey(userId, userEmail);

        // Check cache first
        if (!showLoading && retryAttempt === 0) {
          const cachedData = getCachedData(cacheKey);
          if (cachedData) {
            setOrders(cachedData);
            setRequestInProgress(false);
            return;
          }
        }

        // Cancel previous request
        if (abortControllerRef.current) {
          abortControllerRef.current.abort();
        }
        abortControllerRef.current = new AbortController();

        const params = new URLSearchParams();
        if (userId) params.append("farmerId", userId);
        if (userEmail) params.append("farmerEmail", userEmail);

        const response = await fetch(`/api/orders?${params.toString()}`, {
          cache: "no-store",
          signal: abortControllerRef.current.signal,
          headers: {
            "Cache-Control": "no-cache",
            Pragma: "no-cache",
            "X-Requested-With": "XMLHttpRequest",
          },
        });

        if (!response.ok) {
          const error = new Error(
            `HTTP ${response.status}: ${response.statusText}`,
          );
          console.error("Response error:", error);
          throw error;
        }

        const data = await response.json();
        const ordersData = data.orders || [];

        // Check for new orders for notifications (only if not initial load)
        if (
          !showLoading &&
          orders.length > 0 &&
          ordersData.length > orders.length
        ) {
          const newOrdersCount = ordersData.length - orders.length;
          addNotification(
            `${newOrdersCount} new order(s) received!`,
            "success",
          );
        }

        setOrders(ordersData);
        setCachedData(cacheKey, ordersData);
      } catch (error) {
        if (error.name === "AbortError") {
          return; // Request was cancelled, don't treat as error
        }

        console.error("Error fetching orders:", error);

        // Retry logic
        if (retryAttempt < MAX_RETRY_ATTEMPTS) {
          addNotification(
            `Retrying to fetch orders... (${retryAttempt + 1}/${MAX_RETRY_ATTEMPTS})`,
            "warning",
          );

          setTimeout(
            () => {
              fetchOrdersWithRetry(showLoading, retryAttempt + 1);
            },
            RETRY_DELAY * Math.pow(2, retryAttempt),
          ); // Exponential backoff

          return;
        }

        setOrders([]);
        addNotification("Failed to fetch orders. Please try again.", "error");
      } finally {
        if (showLoading) setLoading(false);
        setRequestInProgress(false);
      }
    },
    [
      session,
      orders.length,
      getCacheKey,
      getCachedData,
      setCachedData,
      requestInProgress,
      addNotification,
      MAX_RETRY_ATTEMPTS,
      RETRY_DELAY,
    ],
  );

  // Memoized filtered orders with performance optimization
  const memoizedFilteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter with improved regex
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999); // Include entire end date
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting with improved performance
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Initial fetch
  useEffect(() => {
    fetchOrdersWithRetry();
  }, [session?.user, fetchOrdersWithRetry]);

  // Optimized bulk operations with better error handling
  const handleBulkStatusUpdate = async (newStatus) => {
      for (let i = 0; i < selectedOrders.length; i += batchSize) {
        batches.push(selectedOrders.slice(i, i + batchSize));
                  updatedBy: session.user.email || session.user.name,
                },
              }),
            });

            if (!response.ok) {
          }
        });
      }

      if (successCount > 0) {
        // Update local state optimistically
        setOrders((prev) =>
          prev.map((order) =>
            selectedOrders.includes(order._id)
              ? { ...order, status: newStatus }
              : order,
          ),
        );
        setSelectedOrders([]);

        // Clear cache to force refresh
        cacheRef.current.clear();
      }

      if (errorCount === 0) {
        addNotification(
          `${successCount} orders updated successfully!`,
          "success",
        );
      } else {
        addNotification(
          `${successCount} orders updated, ${errorCount} failed`,
          "warning",
        );
      }
    } catch (error) {
      console.error("Bulk update error:", error);
      addNotification("Failed to update orders", "error");
    } finally {
      setRequestInProgress(false);
    }
  };

  // Enhanced single order status update
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      setRequestInProgress(true);

      const updateData = {
        status: newStatus,
        statusHistory: {
          status: newStatus,
          timestamp: new Date().toISOString(),
          updatedBy: session.user.email || session.user.name,
        },
      };

      // Add estimated delivery date for shipped status
      if (newStatus === "shipped") {
        const estimatedDelivery = new Date();
        estimatedDelivery.setDate(estimatedDelivery.getDate() + 3);
        updateData.estimatedDeliveryDate = estimatedDelivery.toISOString();
      }

      const response = await fetch(`/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          "X-Requested-With": "XMLHttpRequest",
        },
        body: JSON.stringify(updateData),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        const error = new Error(
          errorData.error || `HTTP ${response.status}: ${response.statusText}`,
        );
        console.error("Order update error:", error);
        throw error;
      }

      // Optimistic update
      setOrders((prev) =>
        prev.map((order) =>
          order._id === orderId
            ? {
                ...order,
                status: newStatus,
                estimatedDeliveryDate:
                  updateData.estimatedDeliveryDate ||
                  order.estimatedDeliveryDate,
                statusHistory: [
                  ...(Array.isArray(order.statusHistory)
                    ? order.statusHistory
                    : []),
                  updateData.statusHistory,
                ],
              }
            : order,
        ),
      );

      // Clear cache
      cacheRef.current.clear();

      const successMessages = {
        confirmed: "Order confirmed! Customer has been notified.",
        shipped:
          "Order marked as shipped! Customer has been notified with tracking information.",
        delivered:
          "Order completed! Customer has been notified of successful delivery.",
        cancelled: "Order cancelled. Customer has been notified.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully!`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    } finally {
      setRequestInProgress(false);
    }
  };

  // Enhanced refresh with better UX
  const handleRefresh = async () => {
    setRefreshing(true);
    cacheRef.current.clear(); // Clear cache to force fresh data
    await fetchOrdersWithRetry(false);
    setRefreshing(false);
    addNotification("Orders refreshed successfully!", "success");
  };

  // Optimized search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Enhanced status filter handler
  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  // Optimized export functionality with better performance
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400 text-lg">
            Loading your enhanced orders dashboard...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      {/* Notification System */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`px-6 py-4 rounded-lg shadow-lg text-white transform transition-all duration-500 ${
              notification.type === "success"
                ? "bg-green-600"
                : notification.type === "error"
                  ? "bg-red-600"
                  : notification.type === "warning"
                    ? "bg-yellow-600"
                    : "bg-blue-600"
            }`}
          >
            <div className="flex items-center">
              <i
                className={`fas ${
                  notification.type === "success"
                    ? "fa-check-circle"
                    : notification.type === "error"
                      ? "fa-exclamation-circle"
                      : notification.type === "warning"
                        ? "fa-exclamation-triangle"
                        : "fa-info-circle"
                } mr-2`}
              ></i>
              {notification.message}
            </div>
          </div>
        ))}
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center">
          <div className="bg-white dark:bg-gray-800 rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Export Orders
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              {selectedOrders.length > 0
                ? `Export ${selectedOrders.length} selected orders`
                : `Export all ${filteredOrders.length} filtered orders`}
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => exportOrders("csv")}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-csv mr-2"></i>
                Export as CSV
              </button>
              <button
                onClick={() => exportOrders("json")}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-code mr-2"></i>
                Export as JSON
              </button>
            </div>
            <button
              onClick={() => setShowExportModal(false)}
              className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg font-medium transition"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Enhanced Breadcrumb with Real-time Indicators */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <nav className="flex" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-home mr-1"></i>Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li>
                  <Link
                    href="/manage"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-cog mr-1"></i>Manage
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  <i className="fas fa-chart-line mr-1"></i>Advanced Order
                  Management
                </li>
              </ol>
            </nav>

            {/* Real-time Status Indicator */}
            <div className="flex items-center space-x-2">
              <div
                className={`w-3 h-3 rounded-full ${autoRefresh ? "bg-green-500 animate-pulse" : "bg-gray-400"}`}
              ></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {autoRefresh ? "Live" : "Static"}
              </span>
            </div>
          </div>
        </div>

        {/* Enhanced Page Header with Quick Actions */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-green-600 to-blue-600 p-4 rounded-2xl">
                <i className="fas fa-chart-line text-white text-2xl"></i>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Advanced Order Management
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Comprehensive order tracking and analytics dashboard
                </p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className="text-sm text-gray-500">
                    Last updated: {new Date().toLocaleTimeString()}
                  </span>
                  {autoRefresh && (
                    <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                      Auto-refresh enabled
                    </span>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6 lg:mt-0 flex flex-wrap gap-3">
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`inline-flex items-center px-4 py-3 rounded-lg font-medium transition ${
                  autoRefresh
                    ? "bg-green-600 hover:bg-green-700 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600"
                }`}
              >
                <i
                  className={`fas ${autoRefresh ? "fa-pause" : "fa-play"} mr-2`}
                ></i>
                {autoRefresh ? "Pause" : "Auto"} Refresh
              </button>

              <button
                onClick={() => setShowExportModal(true)}
                className="inline-flex items-center px-4 py-3 bg-orange-600 hover:bg-orange-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-download mr-2"></i>
                Export
              </button>

              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                ></i>
                Refresh
              </button>

              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Enhanced Order Summary Cards with Animations */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            {Object.entries(orderSummary).map(([key, value], index) => {
              const config = {
                total: {
                  icon: "fa-shopping-cart",
                  color: "gray",
                  label: "Total",
                },
                pending: {
                  icon: "fa-clock",
                  color: "yellow",
                  label: "Pending",
                },
                confirmed: {
                  icon: "fa-check",
                  color: "blue",
                  label: "Confirmed",
                },
                shipped: {
                  icon: "fa-truck",
                  color: "purple",
                  label: "Shipped",
                },
                delivered: {
                  icon: "fa-check-circle",
                  color: "green",
                  label: "Delivered",
                },
                cancelled: {
                  icon: "fa-times-circle",
                  color: "red",
                  label: "Cancelled",
                },
              }[key];

              return (
                <div
                  key={key}
                  className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 transform hover:scale-105 transition-transform duration-200 cursor-pointer"
                  onClick={() =>
                    setStatusFilter(
                      key === "total"
                        ? "All Orders"
                        : config.label.toLowerCase(),
                    )
                  }
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <div className="flex items-center">
                    <div
                      className={`p-2 bg-${config.color}-100 dark:bg-${config.color}-900 rounded-lg`}
                    >
                      <i
                        className={`fas ${config.icon} text-${config.color}-600 dark:text-${config.color}-300`}
                      ></i>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                        {config.label}
                      </p>
                      <p
                        className={`text-2xl font-bold text-${config.color}-600 dark:text-${config.color}-400`}
                      >
                        {value}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Enhanced Filters and Controls */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 lg:mb-0">
                <i className="fas fa-filter mr-2"></i>
                Advanced Filters & Controls
              </h3>

              {/* View Mode Toggle */}
              <div className="flex space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {["detailed", "compact"].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-4 py-2 rounded-md text-sm font-medium transition ${
                      viewMode === mode
                        ? "bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow"
                        : "text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white"
                    }`}
                  >
                    <i
                      className={`fas ${
                        mode === "detailed"
                          ? "fa-list"
                          : mode === "compact"
                            ? "fa-th-list"
                            : ""
                      } mr-1`}
                    ></i>
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4">
              {/* Search */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    placeholder="Search by customer, email, or ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Status Filter
                </label>
                <select
                  value={statusFilter}
                  onChange={handleStatusChange}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useRef, useMemo } from "react";
import { useSession } from "next-auth/react";
import Link from "next/link";
import Footer from "@/components/Footer";
import { debounce } from "@/utils/debounce";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);

  // Enhanced state for new features
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed"); // 'detailed', 'compact'
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Performance optimizations
  const [requestInProgress, setRequestInProgress] = useState(false);

  const intervalRef = useRef(null);
  const abortControllerRef = useRef(null);
  const cacheRef = useRef(new Map());

  const ordersPerPage = viewMode === "compact" ? 20 : 10;
  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes
  const MAX_RETRY_ATTEMPTS = 3;
  const RETRY_DELAY = 1000; // 1 second

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Optimized cache management
  const getCacheKey = useCallback((userId, userEmail) => {
    return `farmer-orders-${userId || userEmail}`;
  }, []);

  const getCachedData = useCallback(
    (cacheKey) => {
      const cached = cacheRef.current.get(cacheKey);
      if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
        return cached.data;
      }
      return null;
    },
    [CACHE_DURATION],
  );

  const setCachedData = useCallback(
    (cacheKey, data) => {
      cacheRef.current.set(cacheKey, {
        data,
        timestamp: Date.now(),
      });
      // Clean up old cache entries
      for (const [key, value] of cacheRef.current.entries()) {
        if (Date.now() - value.timestamp > CACHE_DURATION) {
          cacheRef.current.delete(key);
        }
      }
    },
    [CACHE_DURATION],
  );

  // Enhanced file download with better error handling
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion with better data handling
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email",
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Enhanced fetch function with retry logic
  const fetchOrdersWithRetry = useCallback(
    async (showLoading = true, retryAttempt = 0) => {
      if (!session?.user || requestInProgress) return;

      try {
        if (showLoading) setLoading(true);
        setRequestInProgress(true);

        const userId =
          session.user.userId || session.user.id || session.user._id;
        const userEmail = session.user.email;
        const cacheKey = getCacheKey(userId, userEmail);

        // Check cache first
        if (!showLoading && retryAttempt === 0) {
          const cachedData = getCachedData(cacheKey);
          if (cachedData) {
            setOrders(cachedData);
            setRequestInProgress(false);
            return;
          }
        }

        // Cancel previous request
        if (abortControllerRef.current) {
          abortControllerRef.current.abort();
        }
        abortControllerRef.current = new AbortController();

        const params = new URLSearchParams();
        if (userId) params.append("farmerId", userId);
        if (userEmail) params.append("farmerEmail", userEmail);

        const response = await fetch(`/api/orders?${params.toString()}`, {
          cache: "no-store",
          signal: abortControllerRef.current.signal,
          headers: {
            "Cache-Control": "no-cache",
            Pragma: "no-cache",
            "X-Requested-With": "XMLHttpRequest",
          },
        });

        if (!response.ok) {
          const error = new Error(
            `HTTP ${response.status}: ${response.statusText}`,
          );
          console.error("Response error:", error);
          throw error;
        }

        const data = await response.json();
        const ordersData = data.orders || [];

        // Check for new orders for notifications (only if not initial load)
        if (
          !showLoading &&
          orders.length > 0 &&
          ordersData.length > orders.length
        ) {
          const newOrdersCount = ordersData.length - orders.length;
          addNotification(
            `${newOrdersCount} new order(s) received!`,
            "success",
          );
        }

        setOrders(ordersData);
        setCachedData(cacheKey, ordersData);
      } catch (error) {
        if (error.name === "AbortError") {
          return; // Request was cancelled, don't treat as error
        }

        console.error("Error fetching orders:", error);

        // Retry logic
        if (retryAttempt < MAX_RETRY_ATTEMPTS) {
          addNotification(
            `Retrying to fetch orders... (${retryAttempt + 1}/${MAX_RETRY_ATTEMPTS})`,
            "warning",
          );

          setTimeout(
            () => {
              fetchOrdersWithRetry(showLoading, retryAttempt + 1);
            },
            RETRY_DELAY * Math.pow(2, retryAttempt),
          ); // Exponential backoff

          return;
        }

        setOrders([]);
        addNotification("Failed to fetch orders. Please try again.", "error");
      } finally {
        if (showLoading) setLoading(false);
        setRequestInProgress(false);
      }
    },
    [
      session,
      orders.length,
      getCacheKey,
      getCachedData,
      setCachedData,
      requestInProgress,
      addNotification,
      MAX_RETRY_ATTEMPTS,
      RETRY_DELAY,
    ],
  );

  // Memoized filtered orders with performance optimization
  const memoizedFilteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter with improved regex
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999); // Include entire end date
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting with improved performance
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Initial fetch
  useEffect(() => {
    if (session?.user && !requestInProgress) {
      fetchOrdersWithRetry();
    }
  }, [session?.user]); // Removed fetchOrdersWithRetry dependency

  // Update filtered orders when memoized value changes
  useEffect(() => {
    setFilteredOrders(memoizedFilteredOrders);
    setCurrentPage(1);
  }, [memoizedFilteredOrders]);

  // Auto-refresh functionality with better management
  useEffect(() => {
    if (autoRefresh && !loading && !requestInProgress) {
      intervalRef.current = setInterval(() => {
        fetchOrdersWithRetry(false);
      }, 30000); // Refresh every 30 seconds
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    }
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [autoRefresh, loading, requestInProgress]); // Removed fetchOrdersWithRetry dependency

  // Optimized bulk operations with better error handling
  const handleBulkStatusUpdate = async (newStatus) => {
    if (selectedOrders.length === 0) {
      addNotification("Please select orders to update", "warning");
      return;
    }

    if (
      !confirm(
        `Are you sure you want to mark ${selectedOrders.length} orders as ${newStatus}?`,
      )
    ) {
      return;
    }

    try {
      setRequestInProgress(true);

      // Process in batches for better performance
      const batchSize = 5;
      const batches = [];
      for (let i = 0; i < selectedOrders.length; i += batchSize) {
        batches.push(selectedOrders.slice(i, i + batchSize));
      }

      let successCount = 0;
      let errorCount = 0;

      for (const batch of batches) {
        const updatePromises = batch.map(async (orderId) => {
          try {
            const response = await fetch(`/api/orders/${orderId}`, {
              method: "PATCH",
              headers: {
                "Content-Type": "application/json",
                "X-Requested-With": "XMLHttpRequest",
              },
              body: JSON.stringify({
                status: newStatus,
                statusHistory: {
                  status: newStatus,
                  timestamp: new Date().toISOString(),
                  updatedBy: session.user.email || session.user.name,
                },
              }),
            });

            if (!response.ok) {
              const error = new Error(`Failed to update order ${orderId}`);
              console.error("Bulk update error:", error);
              throw error;
            }

            return { success: true, orderId };
          } catch (error) {
            console.error(`Error updating order ${orderId}:`, error);
            return { success: false, orderId, error: error.message };
          }
        });

        const results = await Promise.allSettled(updatePromises);

        results.forEach((result) => {
          if (result.status === "fulfilled" && result.value.success) {
            successCount++;
          } else {
            errorCount++;
          }
        });
      }

      if (successCount > 0) {
        // Update local state optimistically
        setOrders((prev) =>
          prev.map((order) =>
            selectedOrders.includes(order._id)
              ? { ...order, status: newStatus }
              : order,
          ),
        );
        setSelectedOrders([]);

        // Clear cache to force refresh
        cacheRef.current.clear();
      }

      if (errorCount === 0) {
        addNotification(
          `${successCount} orders updated successfully!`,
          "success",
        );
      } else {
        addNotification(
          `${successCount} orders updated, ${errorCount} failed`,
          "warning",
        );
      }
    } catch (error) {
      console.error("Bulk update error:", error);
      addNotification("Failed to update orders", "error");
    } finally {
      setRequestInProgress(false);
    }
  };

  // Enhanced single order status update
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      setRequestInProgress(true);

      const updateData = {
        status: newStatus,
        statusHistory: {
          status: newStatus,
          timestamp: new Date().toISOString(),
          updatedBy: session.user.email || session.user.name,
        },
      };

      // Add estimated delivery date for shipped status
      if (newStatus === "shipped") {
        const estimatedDelivery = new Date();
        estimatedDelivery.setDate(estimatedDelivery.getDate() + 3);
        updateData.estimatedDeliveryDate = estimatedDelivery.toISOString();
      }

      const response = await fetch(`/api/orders/${orderId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          "X-Requested-With": "XMLHttpRequest",
        },
        body: JSON.stringify(updateData),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        const error = new Error(
          errorData.error || `HTTP ${response.status}: ${response.statusText}`,
        );
        console.error("Order update error:", error);
        throw error;
      }

      // Optimistic update
      setOrders((prev) =>
        prev.map((order) =>
          order._id === orderId
            ? {
                ...order,
                status: newStatus,
                estimatedDeliveryDate:
                  updateData.estimatedDeliveryDate ||
                  order.estimatedDeliveryDate,
                statusHistory: [
                  ...(Array.isArray(order.statusHistory)
                    ? order.statusHistory
                    : []),
                  updateData.statusHistory,
                ],
              }
            : order,
        ),
      );

      // Clear cache
      cacheRef.current.clear();

      const successMessages = {
        confirmed: "Order confirmed! Customer has been notified.",
        shipped:
          "Order marked as shipped! Customer has been notified with tracking information.",
        delivered:
          "Order completed! Customer has been notified of successful delivery.",
        cancelled: "Order cancelled. Customer has been notified.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully!`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    } finally {
      setRequestInProgress(false);
    }
  };

  // Enhanced refresh with better UX
  const handleRefresh = async () => {
    setRefreshing(true);
    cacheRef.current.clear(); // Clear cache to force fresh data
    await fetchOrdersWithRetry(false);
    setRefreshing(false);
    addNotification("Orders refreshed successfully!", "success");
  };

  // Optimized search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Enhanced status filter handler
  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  // Optimized export functionality with better performance
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400 text-lg">
            Loading your enhanced orders dashboard...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      {/* Notification System */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`px-6 py-4 rounded-lg shadow-lg text-white transform transition-all duration-500 ${
              notification.type === "success"
                ? "bg-green-600"
                : notification.type === "error"
                  ? "bg-red-600"
                  : notification.type === "warning"
                    ? "bg-yellow-600"
                    : "bg-blue-600"
            }`}
          >
            <div className="flex items-center">
              <i
                className={`fas ${
                  notification.type === "success"
                    ? "fa-check-circle"
                    : notification.type === "error"
                      ? "fa-exclamation-circle"
                      : notification.type === "warning"
                        ? "fa-exclamation-triangle"
                        : "fa-info-circle"
                } mr-2`}
              ></i>
              {notification.message}
            </div>
          </div>
        ))}
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center">
          <div className="bg-white dark:bg-gray-800 rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Export Orders
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              {selectedOrders.length > 0
                ? `Export ${selectedOrders.length} selected orders`
                : `Export all ${filteredOrders.length} filtered orders`}
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => exportOrders("csv")}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-csv mr-2"></i>
                Export as CSV
              </button>
              <button
                onClick={() => exportOrders("json")}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-code mr-2"></i>
                Export as JSON
              </button>
            </div>
            <button
              onClick={() => setShowExportModal(false)}
              className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg font-medium transition"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Enhanced Breadcrumb with Real-time Indicators */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <nav className="flex" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-home mr-1"></i>Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li>
                  <Link
                    href="/manage"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-cog mr-1"></i>Manage
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  <i className="fas fa-chart-line mr-1"></i>Advanced Order
                  Management
                </li>
              </ol>
            </nav>

            {/* Real-time Status Indicator */}
            <div className="flex items-center space-x-2">
              <div
                className={`w-3 h-3 rounded-full ${autoRefresh ? "bg-green-500 animate-pulse" : "bg-gray-400"}`}
              ></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {autoRefresh ? "Live" : "Static"}
              </span>
            </div>
          </div>
        </div>

        {/* Enhanced Page Header with Quick Actions */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-green-600 to-blue-600 p-4 rounded-2xl">
                <i className="fas fa-chart-line text-white text-2xl"></i>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Advanced Order Management
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Comprehensive order tracking and analytics dashboard
                </p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className="text-sm text-gray-500">
                    Last updated: {new Date().toLocaleTimeString()}
                  </span>
                  {autoRefresh && (
                    <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                      Auto-refresh enabled
                    </span>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6 lg:mt-0 flex flex-wrap gap-3">
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`inline-flex items-center px-4 py-3 rounded-lg font-medium transition ${
                  autoRefresh
                    ? "bg-green-600 hover:bg-green-700 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600"
                }`}
              >
                <i
                  className={`fas ${autoRefresh ? "fa-pause" : "fa-play"} mr-2`}
                ></i>
                {autoRefresh ? "Pause" : "Auto"} Refresh
              </button>

              <button
                onClick={() => setShowExportModal(true)}
                className="inline-flex items-center px-4 py-3 bg-orange-600 hover:bg-orange-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-download mr-2"></i>
                Export
              </button>

              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                ></i>
                Refresh
              </button>

              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Enhanced Order Summary Cards with Animations */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            {Object.entries(orderSummary).map(([key, value], index) => {
              const config = {
                total: {
                  icon: "fa-shopping-cart",
                  color: "gray",
                  label: "Total",
                },
                pending: {
                  icon: "fa-clock",
                  color: "yellow",
                  label: "Pending",
                },
                confirmed: {
                  icon: "fa-check",
                  color: "blue",
                  label: "Confirmed",
                },
                shipped: {
                  icon: "fa-truck",
                  color: "purple",
                  label: "Shipped",
                },
                delivered: {
                  icon: "fa-check-circle",
                  color: "green",
                  label: "Delivered",
                },
                cancelled: {
                  icon: "fa-times-circle",
                  color: "red",
                  label: "Cancelled",
                },
              }[key];

              return (
                <div
                  key={key}
                  className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 transform hover:scale-105 transition-transform duration-200 cursor-pointer"
                  onClick={() =>
                    setStatusFilter(
                      key === "total"
                        ? "All Orders"
                        : config.label.toLowerCase(),
                    )
                  }
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <div className="flex items-center">
                    <div
                      className={`p-2 bg-${config.color}-100 dark:bg-${config.color}-900 rounded-lg`}
                    >
                      <i
                        className={`fas ${config.icon} text-${config.color}-600 dark:text-${config.color}-300`}
                      ></i>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                        {config.label}
                      </p>
                      <p
                        className={`text-2xl font-bold text-${config.color}-600 dark:text-${config.color}-400`}
                      >
                        {value}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Enhanced Filters and Controls */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 lg:mb-0">
                <i className="fas fa-filter mr-2"></i>
                Advanced Filters & Controls
              </h3>

              {/* View Mode Toggle */}
              <div className="flex space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {["detailed", "compact"].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-4 py-2 rounded-md text-sm font-medium transition ${
                      viewMode === mode
                        ? "bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow"
                        : "text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white"
                    }`}
                  >
                    <i
                      className={`fas ${
                        mode === "detailed"
                          ? "fa-list"
                          : mode === "compact"
                            ? "fa-th-list"
                            : ""
                      } mr-1`}
                    ></i>
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4">
              {/* Search */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    placeholder="Search by customer, email, or ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Status Filter
                </label>
                <select
                  value={statusFilter}
                  onChange={handleStatusChange}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import { apiService } from "@/lib/api-service";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Core state
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [refreshing, setRefreshing] = useState(false);
  const [actionLoading, setActionLoading] = useState({});

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // User identification helper
  const getUserIdentifiers = useCallback(() => {
    if (!session?.user) return {};

    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  }, [session?.user]);

  // Fetch products with caching
  const fetchProducts = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      try {
        const { userId, userEmail } = getUserIdentifiers();

        if (!userId && !userEmail) {
          throw new Error("No user identification found");
        }

        // Use cached API service
        const data = await apiService.getProducts(
          {
            limit: 1000,
            farmerId: userId,
            farmerEmail: userEmail,
          },
          { forceRefresh },
        );

        const products = data.products || [];

        // Filter farmer's products on client side as fallback
        const farmerProducts = products.filter((product) => {
          return (
            product.farmerId === userId ||
            product.farmerId === String(userId) ||
            product.farmerEmail === userEmail ||
            product.farmer?.email === userEmail ||
            product.farmer?.id === userId
          );
        });

        setProducts(farmerProducts);
      } catch (error) {
        console.error("Error fetching products:", error);
        setError("Failed to load products. Please try again.");
      }
    },
    [session?.user, getUserIdentifiers],
  );

  // Fetch orders with caching
  const fetchOrders = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      try {
        const { userId, userEmail } = getUserIdentifiers();

        const params = {
          farmerId: userId,
          farmerEmail: userEmail,
          limit: 1000,
        };

        // Use cached API service for orders
        const data = await apiService.getOrders(params, { forceRefresh });
        setOrders(data.orders || []);
      } catch (error) {
        console.error("Error fetching orders:", error);
        setError("Failed to load orders. Please try again.");
      }
    },
    [session?.user, getUserIdentifiers],
  );

  // Initialize dashboard data with caching
  const initializeDashboard = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      // Only show loading for initial load or force refresh
      if (forceRefresh || products.length === 0) {
        setLoading(true);
      }
      setError(null);

      try {
        await Promise.all([
          fetchProducts(forceRefresh),
          fetchOrders(forceRefresh),
        ]);
      } catch (err) {
        console.error("Dashboard initialization error:", err);
        setError(
          "Failed to load dashboard data. Please try refreshing the page.",
        );
      } finally {
        setLoading(false);
      }
    },
    [fetchProducts, fetchOrders, products.length, session?.user],
  );

  // Authentication and authorization
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }
      initializeDashboard();
    }
  }, [session, status, router, initializeDashboard]);

  // Handle tab changes without refetching data
  const handleTabChange = useCallback((newTab) => {
    setActiveTab(newTab);
    // Data is already cached, no need to refetch
  }, []);

  // Refresh data manually
  const handleRefresh = useCallback(async () => {
    setRefreshing(true);
    await initializeDashboard(true); // Force refresh
    setRefreshing(false);
  }, [initializeDashboard]);

  // Calculate analytics with memoization
  const calculatedAnalytics = useMemo(() => {
    try {
      // Filter valid orders (exclude cancelled/returned for revenue)
      const validOrders = orders.filter(
        (order) =>
          order.status !== ORDER_STATUS.CANCELLED &&
          order.status !== ORDER_STATUS.RETURNED,
      );

      const totalProducts = products.length;
      const activeProducts = products.filter(
        (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
      ).length;

      const totalOrders = orders.length;
      const pendingOrders = orders.filter(
        (o) => o.status === ORDER_STATUS.PENDING,
      ).length;

      // Calculate revenue from valid orders only
      const totalRevenue = validOrders.reduce(
        (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
        0,
      );

      // This month's orders (valid only)
      const now = new Date();
      const thisMonthValidOrders = validOrders.filter((order) => {
        const orderDate = new Date(order.createdAt);
        return (
          orderDate.getMonth() === now.getMonth() &&
          orderDate.getFullYear() === now.getFullYear()
        );
      });

      const averageOrderValue =
        validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

      return {
        totalProducts,
        activeProducts,
        totalOrders,
        pendingOrders,
        totalRevenue,
        thisMonthOrders: thisMonthValidOrders.length,
        averageOrderValue,
        lowStockProducts: products.filter((p) => p.stock <= 10).length,
        recentOrders: orders.slice(0, 5),
      };
    } catch (error) {
      console.error("Error calculating analytics:", error);
      return {
        totalProducts: 0,
        activeProducts: 0,
        totalOrders: 0,
        pendingOrders: 0,
        totalRevenue: 0,
        thisMonthOrders: 0,
        averageOrderValue: 0,
        lowStockProducts: 0,
        recentOrders: [],
      };
    }
  }, [products, orders]);

  // Update analytics when calculated values change
  useEffect(() => {
    setAnalytics(calculatedAnalytics);
  }, [calculatedAnalytics]);

  // Filter and sort products with better performance
  const filteredAndSortedProducts = useMemo(() => {
    try {
      let filtered = [...products];

      // Apply search filter
      if (filters.search) {
        const searchRegex = new RegExp(filters.search.trim(), "i");
        filtered = filtered.filter(
          (product) =>
            searchRegex.test(product.name) ||
            searchRegex.test(product.description || "") ||
            searchRegex.test(product.category || ""),
        );
      }

      // Apply category filter
      if (filters.category) {
        filtered = filtered.filter(
          (product) =>
            product.category?.toLowerCase() === filters.category.toLowerCase(),
        );
      }

      // Apply status filter
      if (filters.status) {
        switch (filters.status) {
          case PRODUCT_STATUS.ACTIVE:
            filtered = filtered.filter(
              (product) =>
                product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.INACTIVE:
            filtered = filtered.filter(
              (product) => product.status === PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.OUT_OF_STOCK:
            filtered = filtered.filter((product) => product.stock === 0);
            break;
          case PRODUCT_STATUS.LOW_STOCK:
            filtered = filtered.filter(
              (product) => product.stock > 0 && product.stock <= 5,
            );
            break;
        }
      }

      // Apply sorting
      if (filters.sort) {
        const [key, order] = filters.sort.split("-");
        filtered.sort((a, b) => {
          let valueA, valueB;

          switch (key) {
            case "price":
              valueA = a.price || 0;
              valueB = b.price || 0;
              break;
            case "name":
              valueA = a.name || "";
              valueB = b.name || "";
              return order === "asc"
                ? valueA.localeCompare(valueB)
                : valueB.localeCompare(valueA);
            case "stock":
              valueA = a.stock || 0;
              valueB = b.stock || 0;
              break;
            case "date":
              valueA = new Date(a.createdAt || 0);
              valueB = new Date(b.createdAt || 0);
              break;
            default:
              return 0;
          }

          return order === "asc" ? valueA - valueB : valueB - valueA;
        });
      }

      return filtered;
    } catch (error) {
      console.error("Error filtering products:", error);
      return products;
    }
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(async (productId, currentStatus) => {
    const newStatus =
      currentStatus === PRODUCT_STATUS.ACTIVE
        ? PRODUCT_STATUS.INACTIVE
        : PRODUCT_STATUS.ACTIVE;
    const actionText =
      newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

    if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
      return;
    }

    setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Failed to ${actionText} product`);
      }

      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || `Failed to ${actionText} product`);
      }

      // Update local state
      setProducts((prev) =>
        prev.map((product) =>
          product._id === productId
            ? { ...product, status: newStatus }
            : product,
        ),
      );

      // Show success message
      const successMsg =
        newStatus === PRODUCT_STATUS.ACTIVE
          ? "Product activated successfully!"
          : "Product deactivated successfully!";

      // Consider using a toast notification instead of alert
      alert(successMsg);
    } catch (error) {
      console.error("Error updating product status:", error);
      alert(`Failed to ${actionText} product: ${error.message}`);
    } finally {
      setActionLoading((prev) => ({ ...prev, [productId]: null }));
    }
  }, []);

  const handleDeleteProduct = useCallback(async (productId) => {
    const confirmMessage =
      " Are you sure you want to delete this product?\n\n" +
      "This action cannot be undone and will remove:\n" +
      " The product listing\n" +
      " All associated data\n" +
      " Product from any pending orders";

    if (!confirm(confirmMessage)) {
      return;
    }

    setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        const errorData = await response.json();

        if (response.status === 409) {
          alert(
            " Cannot Delete Product\n\n" +
              "This product has pending orders and cannot be deleted.\n" +
              "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
              "You can temporarily deactivate the product instead by clicking the pause button.",
          );
          return;
        } else if (response.status === 404) {
          alert(" Product not found. It may have already been deleted.");
          return;
        } else {
          throw new Error(errorData.error || "Failed to delete product");
        }
      }

      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || "Failed to delete product");
      }

      // Remove from local state
      setProducts((prev) =>
        prev.filter((product) => product._id !== productId),
      );

      alert("Product deleted successfully!");
    } catch (error) {
      console.error("Error deleting product:", error);
      alert(` Failed to delete product: ${error.message}`);
    } finally {
      setActionLoading((prev) => ({ ...prev, [productId]: null }));
    }
  }, []);

  // Update filters
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  // Update pagination
  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const dashboardProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const productProps = {
    products: products,
    paginatedProducts: paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const orderProps = {
    orders,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const analyticsProps = {
    analytics,
    products,
    orders,
    formatPrice,
    loading,
    error,
  };

  const settingsProps = {
    session,
    handleRefresh,
    refreshing,
    loading,
    error,
  };

  // Loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            Loading dashboard...
          </p>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">{error}</p>
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {refreshing ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Dashboard Header */}
        <DashboardHeader {...dashboardProps} />

        {/* Error Alert */}
        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">{error}</span>
                <button
                  onClick={() => setError(null)}
                  className="ml-auto text-red-500 hover:text-red-700"
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Navigation Tabs */}
        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Tab Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...dashboardProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...orderProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...analyticsProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...settingsProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import { apiService } from "@/lib/api-service";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Core state
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [refreshing, setRefreshing] = useState(false);
  const [actionLoading, setActionLoading] = useState({});

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // User identification helper
  const getUserIdentifiers = useCallback(() => {
    if (!session?.user) return {};

    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  }, [session?.user]);

  // Fetch products with caching
  const fetchProducts = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      try {
        const { userId, userEmail } = getUserIdentifiers();

        if (!userId && !userEmail) {
          throw new Error("No user identification found");
        }

        // Use cached API service
        const data = await apiService.getProducts(
          {
            limit: 1000,
            farmerId: userId,
            farmerEmail: userEmail,
          },
          { forceRefresh },
        );

        const products = data.products || [];

        // Filter farmer's products on client side as fallback
        const farmerProducts = products.filter((product) => {
          return (
            product.farmerId === userId ||
            product.farmerId === String(userId) ||
            product.farmerEmail === userEmail ||
            product.farmer?.email === userEmail ||
            product.farmer?.id === userId
          );
        });

        setProducts(farmerProducts);
      } catch (error) {
        console.error("Error fetching products:", error);
        setError("Failed to load products. Please try again.");
      }
    },
    [session?.user, getUserIdentifiers],
  );

  // Fetch orders with caching
  const fetchOrders = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      try {
        const { userId, userEmail } = getUserIdentifiers();

        const params = {
          farmerId: userId,
          farmerEmail: userEmail,
          limit: 1000,
        };

        // Use cached API service for orders
        const data = await apiService.getOrders(params, { forceRefresh });
        setOrders(data.orders || []);
      } catch (error) {
        console.error("Error fetching orders:", error);
        setError("Failed to load orders. Please try again.");
      }
    },
    [session?.user, getUserIdentifiers],
  );

  // Initialize dashboard data with caching and progressive loading
  const initializeDashboard = useCallback(
    async (forceRefresh = false) => {
      if (!session?.user) return;

      const { userId, userEmail } = getUserIdentifiers();
      if (!userId && !userEmail) return;

      // Only show loading for initial load or force refresh
      if (forceRefresh || products.length === 0) {
        setLoading(true);
      }
      setError(null);

      try {
        // Use the optimized dashboard data method
        const dashboardData = await apiService.getDashboardData(
          userId, 
          userEmail, 
          { forceRefresh }
        );

        setProducts(dashboardData.products);
        setOrders(dashboardData.orders);

        console.log(`Dashboard loaded: ${dashboardData.meta.productsCount} products, ${dashboardData.meta.ordersCount} orders (cached: ${dashboardData.meta.fromCache})`);
      } catch (err) {
        console.error("Dashboard initialization error:", err);
        setError(
          "Failed to load dashboard data. Please try refreshing the page.",
        );
      } finally {
        setLoading(false);
      }
    },
    [getUserIdentifiers, products.length, session?.user],
  );

  // Authentication and authorization
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }
      initializeDashboard();
    }
  }, [session, status, router, initializeDashboard]);

  // Handle tab changes without refetching data
  const handleTabChange = useCallback((newTab) => {
    setActiveTab(newTab);
    // Data is already cached, no need to refetch
  }, []);

  // Refresh data manually
  const handleRefresh = useCallback(async () => {
    setRefreshing(true);
    await initializeDashboard(true); // Force refresh
    setRefreshing(false);
  }, [initializeDashboard]);

  // Calculate analytics with memoization
  const calculatedAnalytics = useMemo(() => {
    try {
      // Filter valid orders (exclude cancelled/returned for revenue)
      const validOrders = orders.filter(
        (order) =>
          order.status !== ORDER_STATUS.CANCELLED &&
          order.status !== ORDER_STATUS.RETURNED,
      );

      const totalProducts = products.length;
      const activeProducts = products.filter(
        (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
      ).length;

      const totalOrders = orders.length;
      const pendingOrders = orders.filter(
        (o) => o.status === ORDER_STATUS.PENDING,
      ).length;

      // Calculate revenue from valid orders only
      const totalRevenue = validOrders.reduce(
        (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
        0,
      );

      // This month's orders (valid only)
      const now = new Date();
      const thisMonthValidOrders = validOrders.filter((order) => {
        const orderDate = new Date(order.createdAt);
        return (
          orderDate.getMonth() === now.getMonth() &&
          orderDate.getFullYear() === now.getFullYear()
        );
      });

      const averageOrderValue =
        validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

      return {
        totalProducts,
        activeProducts,
        totalOrders,
        pendingOrders,
        totalRevenue,
        thisMonthOrders: thisMonthValidOrders.length,
        averageOrderValue,
        lowStockProducts: products.filter((p) => p.stock <= 10).length,
        recentOrders: orders.slice(0, 5),
      };
    } catch (error) {
      console.error("Error calculating analytics:", error);
      return {
        totalProducts: 0,
        activeProducts: 0,
        totalOrders: 0,
        pendingOrders: 0,
        totalRevenue: 0,
        thisMonthOrders: 0,
        averageOrderValue: 0,
        lowStockProducts: 0,
        recentOrders: [],
      };
    }
  }, [products, orders]);

  // Update analytics when calculated values change
  useEffect(() => {
    setAnalytics(calculatedAnalytics);
  }, [calculatedAnalytics]);

  // Filter and sort products with better performance
  const filteredAndSortedProducts = useMemo(() => {
    try {
      let filtered = [...products];

      // Apply search filter
      if (filters.search) {
        const searchRegex = new RegExp(filters.search.trim(), "i");
        filtered = filtered.filter(
          (product) =>
            searchRegex.test(product.name) ||
            searchRegex.test(product.description || "") ||
            searchRegex.test(product.category || ""),
        );
      }

      // Apply category filter
      if (filters.category) {
        filtered = filtered.filter(
          (product) =>
            product.category?.toLowerCase() === filters.category.toLowerCase(),
        );
      }

      // Apply status filter
      if (filters.status) {
        switch (filters.status) {
          case PRODUCT_STATUS.ACTIVE:
            filtered = filtered.filter(
              (product) =>
                product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.INACTIVE:
            filtered = filtered.filter(
              (product) => product.status === PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.OUT_OF_STOCK:
            filtered = filtered.filter((product) => product.stock === 0);
            break;
          case PRODUCT_STATUS.LOW_STOCK:
            filtered = filtered.filter(
              (product) => product.stock > 0 && product.stock <= 5,
            );
            break;
        }
      }

      // Apply sorting
      if (filters.sort) {
        const [key, order] = filters.sort.split("-");
        filtered.sort((a, b) => {
          let valueA, valueB;

          switch (key) {
            case "price":
              valueA = a.price || 0;
              valueB = b.price || 0;
              break;
            case "name":
              valueA = a.name || "";
              valueB = b.name || "";
              return order === "asc"
                ? valueA.localeCompare(valueB)
                : valueB.localeCompare(valueA);
            case "stock":
              valueA = a.stock || 0;
              valueB = b.stock || 0;
              break;
            case "date":
              valueA = new Date(a.createdAt || 0);
              valueB = new Date(b.createdAt || 0);
              break;
            default:
              return 0;
          }

          return order === "asc" ? valueA - valueB : valueB - valueA;
        });
      }

      return filtered;
    } catch (error) {
      console.error("Error filtering products:", error);
      return products;
    }
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(async (productId, currentStatus) => {
    const newStatus =
      currentStatus === PRODUCT_STATUS.ACTIVE
        ? PRODUCT_STATUS.INACTIVE
        : PRODUCT_STATUS.ACTIVE;
    const actionText =
      newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

    if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
      return;
    }

    setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Failed to ${actionText} product`);
      }

      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || `Failed to ${actionText} product`);
      }

      // Update local state
      setProducts((prev) =>
        prev.map((product) =>
          product._id === productId
            ? { ...product, status: newStatus }
            : product,
        ),
      );

      // Show success message
      const successMsg =
        newStatus === PRODUCT_STATUS.ACTIVE
          ? "Product activated successfully!"
          : "Product deactivated successfully!";

      // Consider using a toast notification instead of alert
      alert(successMsg);
    } catch (error) {
      console.error("Error updating product status:", error);
      alert(`Failed to ${actionText} product: ${error.message}`);
    } finally {
      setActionLoading((prev) => ({ ...prev, [productId]: null }));
    }
  }, []);

  const handleDeleteProduct = useCallback(async (productId) => {
    const confirmMessage =
      " Are you sure you want to delete this product?\n\n" +
      "This action cannot be undone and will remove:\n" +
      " The product listing\n" +
      " All associated data\n" +
      " Product from any pending orders";

    if (!confirm(confirmMessage)) {
      return;
    }

    setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        const errorData = await response.json();

        if (response.status === 409) {
          alert(
            " Cannot Delete Product\n\n" +
              "This product has pending orders and cannot be deleted.\n" +
              "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
              "You can temporarily deactivate the product instead by clicking the pause button.",
          );
          return;
        } else if (response.status === 404) {
          alert(" Product not found. It may have already been deleted.");
          return;
        } else {
          throw new Error(errorData.error || "Failed to delete product");
        }
      }

      const result = await response.json();

      if (!result.success) {
        throw new Error(result.error || "Failed to delete product");
      }

      // Remove from local state
      setProducts((prev) =>
        prev.filter((product) => product._id !== productId),
      );

      alert("Product deleted successfully!");
    } catch (error) {
      console.error("Error deleting product:", error);
      alert(` Failed to delete product: ${error.message}`);
    } finally {
      setActionLoading((prev) => ({ ...prev, [productId]: null }));
    }
  }, []);

  // Update filters
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  // Update pagination
  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const dashboardProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const productProps = {
    products: products,
    paginatedProducts: paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const orderProps = {
    orders,
    handleRefresh,
    refreshing,
    formatPrice,
    formatDate,
    loading,
    error,
  };

  const analyticsProps = {
    analytics,
    products,
    orders,
    formatPrice,
    loading,
    error,
  };

  const settingsProps = {
    session,
    handleRefresh,
    refreshing,
    loading,
    error,
  };

  // Loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            Loading dashboard...
          </p>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">{error}</p>
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {refreshing ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Dashboard Header */}
        <DashboardHeader {...dashboardProps} />

        {/* Error Alert */}
        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">{error}</span>
                <button
                  onClick={() => setError(null)}
                  className="ml-auto text-red-500 hover:text-red-700"
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Navigation Tabs */}
        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Tab Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...dashboardProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...orderProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...analyticsProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...settingsProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import Link from "next/link";
import { useState, useEffect, useCallback, useMemo } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import ProductCard from "@/components/ProductCard";
import Footer from "@/components/Footer";
import { debounce } from "@/utils/debounce";
import { apiService } from "@/lib/api-service";

// Move constants outside component to prevent recreations
const CATEGORY_OPTIONS = [
  "All Categories",
  "Vegetables",
  "Fruits",
  "Grains",
  "Dairy",
  "Honey",
  "Herbs",
];

const PRICE_RANGE_OPTIONS = [
  { label: "Under 50", min: 0, max: 49 },
  { label: "50 - 100", min: 50, max: 100 },
  { label: "100 - 200", min: 101, max: 200 },
  { label: "200 - 500", min: 201, max: 500 },
  { label: "Above 500", min: 501, max: 9999 },
];

const TAG_OPTIONS = [
  "Organic",
  "Fresh",
  "Local",
  "Premium",
  "Seasonal",
  "Limited Stock",
];

const ITEMS_PER_PAGE = 12;
const DEFAULT_PRICE_RANGE = [0, 10000];

export default function Products() {
  const searchParams = useSearchParams();
  const router = useRouter();

  // Core data states
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [availableFarmers, setAvailableFarmers] = useState([]);

  // Filter states - Initialize from URL params
  const [filters, setFilters] = useState(() => ({
    searchTerm: searchParams.get("search") || "",
    selectedCategory: searchParams.get("category") || "All Categories",
    selectedPriceRanges: searchParams.get("priceRanges")?.split(",") || [],
    selectedRatings: searchParams.get("ratings")?.split(",").map(Number) || [],
    selectedFarmers: searchParams.get("farmers")?.split(",") || [],
    selectedTags: searchParams.get("tags")?.split(",") || [],
    priceRangeSlider: [
      Number(searchParams.get("minPrice")) || 0,
      Number(searchParams.get("maxPrice")) || 10000,
    ],
    sortBy: searchParams.get("sort") || "newest",
  }));

  const [currentPage, setCurrentPage] = useState(
    Number(searchParams.get("page")) || 1,
  );
  const [showMobileFilters, setShowMobileFilters] = useState(false);

  // Memoized filtered and sorted products
  const filteredProducts = useMemo(() => {
    let filtered = [...allProducts];

    // Apply price range checkboxes
    if (filters.selectedPriceRanges.length > 0) {
      filtered = filtered.filter((product) => {
        return filters.selectedPriceRanges.some((range) => {
          const option = PRICE_RANGE_OPTIONS.find((opt) => opt.label === range);
          const price = parseFloat(product.price) || 0;
          return price >= option.min && price <= option.max;
        });
      });
    }

    // Apply price range slider
    filtered = filtered.filter((product) => {
      const price = parseFloat(product.price) || 0;
      return (
        price >= filters.priceRangeSlider[0] &&
        price <= filters.priceRangeSlider[1]
      );
    });

    // Apply rating filters
    if (filters.selectedRatings.length > 0) {
      filtered = filtered.filter((product) => {
        const productRating = parseFloat(product.averageRating) || 0;
        return filters.selectedRatings.some(
          (rating) => productRating >= rating,
        );
      });
    }

    // Apply farmer filters
    if (filters.selectedFarmers.length > 0) {
      filtered = filtered.filter((product) => {
        const farmerName = product.farmer?.name || product.farmerName || "";
        return filters.selectedFarmers.includes(farmerName);
      });
    }

    // Apply tag filters
    if (filters.selectedTags.length > 0) {
      filtered = filtered.filter((product) => {
        return filters.selectedTags.some((tag) => {
          switch (tag) {
            case "Organic":
              return (
                product.isOrganic === true ||
                (product.tags && product.tags.includes("organic"))
              );
            case "Fresh":
              return (
                product.isFresh === true ||
                (product.tags && product.tags.includes("fresh"))
              );
            case "Local":
              return true;
            case "Premium":
              return parseFloat(product.price) > 200;
            case "Seasonal":
              return parseInt(product.stock) < 100;
            case "Limited Stock":
              return parseInt(product.stock) < 50;
            default:
              return product.tags && product.tags.includes(tag.toLowerCase());
          }
        });
      });
    }

    // Apply sorting
    switch (filters.sortBy) {
      case "price-low":
        filtered.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
        break;
      case "price-high":
        filtered.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
        break;
      case "rating":
        filtered.sort(
          (a, b) =>
            (parseFloat(b.averageRating) || 0) -
            (parseFloat(a.averageRating) || 0),
        );
        break;
      case "popular":
        filtered.sort(
          (a, b) =>
            (parseInt(b.purchaseCount) || 0) - (parseInt(a.purchaseCount) || 0),
        );
        break;
      case "newest":
        filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case "oldest":
        filtered.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
        break;
      default:
        break;
    }

    return filtered;
  }, [allProducts, filters]);

  // Memoized pagination data
  const paginationData = useMemo(() => {
    const totalProducts = filteredProducts.length;
    const totalPages = Math.ceil(totalProducts / ITEMS_PER_PAGE);
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    const paginatedProducts = filteredProducts.slice(startIndex, endIndex);

    return {
      products: paginatedProducts,
      pagination: {
        currentPage,
        totalPages,
        totalProducts,
        hasNextPage: currentPage < totalPages,
        hasPrevPage: currentPage > 1,
      },
    };
  }, [filteredProducts, currentPage]);

  // ONLY CHANGE: Use optimized API service for faster MongoDB Atlas performance
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      // Use the new optimized API service with caching
      const params = {
        limit: 1000, // Fetch more products for client-side filtering
      };

      // Add search/category filters for server-side optimization
      if (filters.searchTerm) params.search = filters.searchTerm;
      if (filters.selectedCategory !== "All Categories") {
        params.category = filters.selectedCategory;
      }

      const data = await apiService.getProducts(params);
      setAllProducts(data.products || []);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError("Failed to load products. Please try again.");
      setAllProducts([]);
    } finally {
      setLoading(false);
    }
  }, [filters.searchTerm, filters.selectedCategory]);

  // ONLY CHANGE: Use optimized API service for farmers
  const fetchAvailableFarmers = useCallback(async () => {
    try {
      const data = await apiService.getProducts({ limit: 1000 });
      const uniqueFarmers = [
        ...new Set(
          data.products
            .map((p) => p.farmer?.name || p.farmerName)
            .filter(Boolean),
        ),
      ].sort();
      setAvailableFarmers(uniqueFarmers);
    } catch (error) {
      console.error("Error fetching farmers:", error);
      setAvailableFarmers([]);
    }
  }, []);

  // Initial data fetch
  useEffect(() => {
    fetchProducts();
    fetchAvailableFarmers();
  }, [fetchProducts, fetchAvailableFarmers]);

  // Update URL when filters change (with debouncing)
  const updateURL = useCallback(
    debounce(() => {
      const params = new URLSearchParams();

      // Only add non-default parameters to keep URLs clean
      if (filters.searchTerm) params.set("search", filters.searchTerm);
      if (filters.selectedCategory !== "All Categories")
        params.set("category", filters.selectedCategory);
      if (filters.selectedPriceRanges.length > 0)
        params.set("priceRanges", filters.selectedPriceRanges.join(","));
      if (filters.selectedRatings.length > 0)
        params.set("ratings", filters.selectedRatings.join(","));
      if (filters.selectedFarmers.length > 0)
        params.set("farmers", filters.selectedFarmers.join(","));
      if (filters.selectedTags.length > 0)
        params.set("tags", filters.selectedTags.join(","));
      if (filters.priceRangeSlider[0] > 0)
        params.set("minPrice", filters.priceRangeSlider[0]);
      if (filters.priceRangeSlider[1] < 10000)
        params.set("maxPrice", filters.priceRangeSlider[1]);
      if (filters.sortBy !== "newest") params.set("sort", filters.sortBy);
      if (currentPage > 1) params.set("page", currentPage);

      const newURL = params.toString() ? `?${params.toString()}` : "";
      router.push(`/products${newURL}`, { scroll: false });
    }, 300),
    [filters, currentPage, router],
  );

  useEffect(() => {
    updateURL();
  }, [updateURL]);

  // Handle filter changes
  const handleFilterChange = useCallback((filterType, value) => {
    setFilters((prev) => {
      const newFilters = { ...prev };

      switch (filterType) {
        case "searchTerm":
          newFilters.searchTerm = value;
          break;
        case "category":
          newFilters.selectedCategory = value;
          break;
        case "priceRange":
          if (newFilters.selectedPriceRanges.includes(value)) {
            newFilters.selectedPriceRanges =
              newFilters.selectedPriceRanges.filter((range) => range !== value);
          } else {
            newFilters.selectedPriceRanges = [
              ...newFilters.selectedPriceRanges,
              value,
            ];
          }
          break;
        case "rating":
          if (newFilters.selectedRatings.includes(value)) {
            newFilters.selectedRatings = newFilters.selectedRatings.filter(
              (rating) => rating !== value,
            );
          } else {
            newFilters.selectedRatings = [...newFilters.selectedRatings, value];
          }
          break;
        case "farmer":
          if (newFilters.selectedFarmers.includes(value)) {
            newFilters.selectedFarmers = newFilters.selectedFarmers.filter(
              (farmer) => farmer !== value,
            );
          } else {
            newFilters.selectedFarmers = [...newFilters.selectedFarmers, value];
          }
          break;
        case "tag":
          if (newFilters.selectedTags.includes(value)) {
            newFilters.selectedTags = newFilters.selectedTags.filter(
              (tag) => tag !== value,
            );
          } else {
            newFilters.selectedTags = [...newFilters.selectedTags, value];
          }
          break;
        case "priceSlider":
          newFilters.priceRangeSlider = value;
          break;
        case "sortBy":
          newFilters.sortBy = value;
          break;
      }

      return newFilters;
    });

    // Reset to first page when filters change
    if (filterType !== "sortBy") {
      setCurrentPage(1);
    }
  }, []);

  // Clear all filters
  const clearAllFilters = useCallback(() => {
    setFilters({
      searchTerm: "",
      selectedCategory: "All Categories",
      selectedPriceRanges: [],
      selectedRatings: [],
      selectedFarmers: [],
      selectedTags: [],
      priceRangeSlider: [0, 10000],
      sortBy: "newest",
    });
    setCurrentPage(1);
  }, []);

  // Handle page changes
  const handlePageChange = useCallback((page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, []);

  // Refresh data
  const refreshData = useCallback(() => {
    fetchProducts();
  }, [fetchProducts]);

  // Handle browser back/forward navigation
  useEffect(() => {
    const handlePopState = () => {
      fetchProducts();
    };

    window.addEventListener("popstate", handlePopState);
    return () => window.removeEventListener("popstate", handlePopState);
  }, [fetchProducts]);

  // Memoized pagination component
  const PaginationComponent = useMemo(() => {
    if (paginationData.pagination.totalPages <= 1) return null;

    return (
      <div className="flex justify-center items-center space-x-2 mt-8">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={!paginationData.pagination.hasPrevPage}
          className="px-4 py-2 border rounded-lg disabled:opacity-50 disabled:cursor-not-allowed
                     hover:bg-gray-50 transition-colors duration-200"
        >
          Previous
        </button>

        <div className="flex space-x-1">
          {Array.from(
            { length: Math.min(5, paginationData.pagination.totalPages) },
            (_, i) => {
              const page = Math.max(1, currentPage - 2) + i;
              if

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import ProductCard from "@/components/ProductCard";
import Footer from "@/components/Footer";
import { debounce } from "@/utils/debounce";
import { apiService } from "@/lib/api-service";

// Move constants outside component to prevent recreations
const CATEGORY_OPTIONS = [
  "All Categories",
  "Vegetables",
  "Fruits",
  "Grains",
  "Dairy",
  "Honey",
  "Herbs",
];

const PRICE_RANGE_OPTIONS = [
  { label: "Under 50", min: 0, max: 49 },
  { label: "50 - 100", min: 50, max: 100 },
  { label: "100 - 200", min: 101, max: 200 },
  { label: "200 - 500", min: 201, max: 500 },
  { label: "Above 500", min: 501, max: 9999 },
];

const TAG_OPTIONS = [
  "Organic",
  "Fresh",
  "Local",
  "Premium",
  "Seasonal",
  "Limited Stock",
];

const ITEMS_PER_PAGE = 12;

export default function Products() {
  const searchParams = useSearchParams();
  const router = useRouter();

  // Core data states
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [availableFarmers, setAvailableFarmers] = useState([]);

  // Filter states - Initialize from URL params
  const [filters, setFilters] = useState(() => ({
    searchTerm: searchParams.get("search") || "",
    selectedCategory: searchParams.get("category") || "All Categories",
    selectedPriceRanges: searchParams.get("priceRanges")?.split(",") || [],
    selectedRatings: searchParams.get("ratings")?.split(",").map(Number) || [],
    selectedFarmers: searchParams.get("farmers")?.split(",") || [],
    selectedTags: searchParams.get("tags")?.split(",") || [],
    priceRangeSlider: [
      Number(searchParams.get("minPrice")) || 0,
      Number(searchParams.get("maxPrice")) || 10000,
    ],
    sortBy: searchParams.get("sort") || "newest",
  }));

  const [currentPage, setCurrentPage] = useState(
    Number(searchParams.get("page")) || 1,
  );
  const [showMobileFilters, setShowMobileFilters] = useState(false);

  // Memoized filtered and sorted products
  const filteredProducts = useMemo(() => {
    let filtered = [...allProducts];

    // Apply price range checkboxes
    if (filters.selectedPriceRanges.length > 0) {
      filtered = filtered.filter((product) => {
        return filters.selectedPriceRanges.some((range) => {
          const option = PRICE_RANGE_OPTIONS.find((opt) => opt.label === range);
          const price = parseFloat(product.price) || 0;
          return price >= option.min && price <= option.max;
        });
      });
    }

    // Apply price range slider
    filtered = filtered.filter((product) => {
      const price = parseFloat(product.price) || 0;
      return (
        price >= filters.priceRangeSlider[0] &&
        price <= filters.priceRangeSlider[1]
      );
    });

    // Apply rating filters
    if (filters.selectedRatings.length > 0) {
      filtered = filtered.filter((product) => {
        const productRating = parseFloat(product.averageRating) || 0;
        return filters.selectedRatings.some(
          (rating) => productRating >= rating,
        );
      });
    }

    // Apply farmer filters
    if (filters.selectedFarmers.length > 0) {
      filtered = filtered.filter((product) => {
        const farmerName = product.farmer?.name || product.farmerName || "";
        return filters.selectedFarmers.includes(farmerName);
      });
    }

    // Apply tag filters
    if (filters.selectedTags.length > 0) {
      filtered = filtered.filter((product) => {
        return filters.selectedTags.some((tag) => {
          switch (tag) {
            case "Organic":
              return (
                product.isOrganic === true ||
                (product.tags && product.tags.includes("organic"))
              );
            case "Fresh":
              return (
                product.isFresh === true ||
                (product.tags && product.tags.includes("fresh"))
              );
            case "Local":
              return true;
            case "Premium":
              return parseFloat(product.price) > 200;
            case "Seasonal":
              return parseInt(product.stock) < 100;
            case "Limited Stock":
              return parseInt(product.stock) < 50;
            default:
              return product.tags && product.tags.includes(tag.toLowerCase());
          }
        });
      });
    }

    // Apply sorting
    switch (filters.sortBy) {
      case "price-low":
        filtered.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
        break;
      case "price-high":
        filtered.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
        break;
      case "rating":
        filtered.sort(
          (a, b) =>
            (parseFloat(b.averageRating) || 0) -
            (parseFloat(a.averageRating) || 0),
        );
        break;
      case "popular":
        filtered.sort(
          (a, b) =>
            (parseInt(b.purchaseCount) || 0) - (parseInt(a.purchaseCount) || 0),
        );
        break;
      case "newest":
        filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case "oldest":
        filtered.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
        break;
      default:
        break;
    }

    return filtered;
  }, [allProducts, filters]);

  // Memoized pagination data
  const paginationData = useMemo(() => {
    const totalProducts = filteredProducts.length;
    const totalPages = Math.ceil(totalProducts / ITEMS_PER_PAGE);
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;
    const paginatedProducts = filteredProducts.slice(startIndex, endIndex);

    return {
      products: paginatedProducts,
      pagination: {
        currentPage,
        totalPages,
        totalProducts,
        hasNextPage: currentPage < totalPages,
        hasPrevPage: currentPage > 1,
      },
    };
  }, [filteredProducts, currentPage]);

  // Use optimized API service with caching
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const params = {
        limit: 1000, // Fetch more products for client-side filtering
      };

      // Add search/category filters for server-side optimization
      if (filters.searchTerm) params.search = filters.searchTerm;
      if (filters.selectedCategory !== "All Categories") {
        params.category = filters.selectedCategory;
      }

      const data = await apiService.getProducts(params);
      setAllProducts(data.products || []);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError("Failed to load products. Please try again.");
      setAllProducts([]);
    } finally {
      setLoading(false);
    }
  }, [filters.searchTerm, filters.selectedCategory]);

  // Fetch available farmers using cached API
  const fetchAvailableFarmers = useCallback(async () => {
    try {
      const data = await apiService.getProducts({ limit: 1000 });
      const uniqueFarmers = [
        ...new Set(
          data.products
            .map((p) => p.farmer?.name || p.farmerName)
            .filter(Boolean),
        ),
      ].sort();
      setAvailableFarmers(uniqueFarmers);
    } catch (error) {
      console.error("Error fetching farmers:", error);
      setAvailableFarmers([]);
    }
  }, []);

  // Initial data fetch
  useEffect(() => {
    fetchProducts();
    fetchAvailableFarmers();
  }, [fetchProducts, fetchAvailableFarmers]);

  // Update URL when filters change (with debouncing)
  const updateURL = useCallback(
    debounce(() => {
      const params = new URLSearchParams();

      // Only add non-default parameters to keep URLs clean
      if (filters.searchTerm) params.set("search", filters.searchTerm);
      if (filters.selectedCategory !== "All Categories")
        params.set("category", filters.selectedCategory);
      if (filters.selectedPriceRanges.length > 0)
        params.set("priceRanges", filters.selectedPriceRanges.join(","));
      if (filters.selectedRatings.length > 0)
        params.set("ratings", filters.selectedRatings.join(","));
      if (filters.selectedFarmers.length > 0)
        params.set("farmers", filters.selectedFarmers.join(","));
      if (filters.selectedTags.length > 0)
        params.set("tags", filters.selectedTags.join(","));
      if (filters.priceRangeSlider[0] > 0)
        params.set("minPrice", filters.priceRangeSlider[0]);
      if (filters.priceRangeSlider[1] < 10000)
        params.set("maxPrice", filters.priceRangeSlider[1]);
      if (filters.sortBy !== "newest") params.set("sort", filters.sortBy);
      if (currentPage > 1) params.set("page", currentPage);

      const newURL = params.toString() ? `?${params.toString()}` : "";
      router.push(`/products${newURL}`, { scroll: false });
    }, 300),
    [filters, currentPage, router],
  );

  useEffect(() => {
    updateURL();
  }, [updateURL]);

  // Handle filter changes
  const handleFilterChange = useCallback((filterType, value) => {
    setFilters((prev) => {
      const newFilters = { ...prev };

      switch (filterType) {
        case "searchTerm":
          newFilters.searchTerm = value;
          break;
        case "category":
          newFilters.selectedCategory = value;
          break;
        case "priceRange":
          if (newFilters.selectedPriceRanges.includes(value)) {
            newFilters.selectedPriceRanges =
              newFilters.selectedPriceRanges.filter((range) => range !== value);
          } else {
            newFilters.selectedPriceRanges = [
              ...newFilters.selectedPriceRanges,
              value,
            ];
          }
          break;
        case "rating":
          if (newFilters.selectedRatings.includes(value)) {
            newFilters.selectedRatings = newFilters.selectedRatings.filter(
              (rating) => rating !== value,
            );
          } else {
            newFilters.selectedRatings = [...newFilters.selectedRatings, value];
          }
          break;
        case "farmer":
          if (newFilters.selectedFarmers.includes(value)) {
            newFilters.selectedFarmers = newFilters.selectedFarmers.filter(
              (farmer) => farmer !== value,
            );
          } else {
            newFilters.selectedFarmers = [...newFilters.selectedFarmers, value];
          }
          break;
        case "tag":
          if (newFilters.selectedTags.includes(value)) {
            newFilters.selectedTags = newFilters.selectedTags.filter(
              (tag) => tag !== value,
            );
          } else {
            newFilters.selectedTags = [...newFilters.selectedTags, value];
          }
          break;
        case "priceSlider":
          newFilters.priceRangeSlider = value;
          break;
        case "sortBy":
          newFilters.sortBy = value;
          break;
      }

      return newFilters;
    });

    // Reset to first page when filters change
    if (filterType !== "sortBy") {
      setCurrentPage(1);
    }
  }, []);

  // Clear all filters
  const clearAllFilters = useCallback(() => {
    setFilters({
      searchTerm: "",
      selectedCategory: "All Categories",
      selectedPriceRanges: [],
      selectedRatings: [],
      selectedFarmers: [],
      selectedTags: [],
      priceRangeSlider: [0, 10000],
      sortBy: "newest",
    });
    setCurrentPage(1);
  }, []);

  // Handle page changes
  const handlePageChange = useCallback((page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, []);

  // Refresh data
  const refreshData = useCallback(() => {
    fetchProducts();
  }, [fetchProducts]);

  // Get active filter count
  const getActiveFilterCount = () => {
    let count = 0;
    if (filters.selectedCategory !== "All Categories") count++;
    if (filters.selectedPriceRanges.length > 0) count++;
    if (filters.selectedRatings.length > 0) count++;
    if (filters.selectedFarmers.length > 0) count++;
    if (filters.selectedTags.length > 0) count++;
    if (filters.priceRangeSlider[0] !== 0 || filters.priceRangeSlider[1] !== 10000) count++;
    return count;
  };

  // Pagination Component
  const PaginationComponent = useMemo(() => {
    if (paginationData.pagination.totalPages <= 1) return null;

    return (
      <div className="flex justify-center items-center space-x-2 mt-8">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={!paginationData.pagination.hasPrevPage}
          className="px-4 py-2 border rounded-lg disabled:opacity-50 disabled:cursor-not-allowed
                     hover:bg-gray-50 transition-colors duration-200"
        >
          Previous
        </button>

        <div className="flex space-x-1">
          {Array.from(
            { length: Math.min(5, paginationData.pagination.totalPages) },
            (_, i) => {
              const page = Math.max(1, currentPage - 2) + i;
              if (page > paginationData.pagination.totalPages) return null;

              return (
                <button
                  key={page}
                  onClick={() => handlePageChange(page)}
                  className={`px-3 py-2 rounded-lg transition-colors duration-200 ${
                    page === currentPage
                      ? "bg-green-600 text-white"
                      : "border hover:bg-gray-50"
                  }`}
                >
                  {page}
                </button>
              );
            },
          )}
        </div>

        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={!paginationData.pagination.hasNextPage}
          className="px-4 py-2 border rounded-lg disabled:opacity-50 disabled:cursor-not-allowed
                     hover:bg-gray-50 transition-colors duration-200"
        >
          Next
        </button>
      </div>
    );
  }, [paginationData.pagination, currentPage, handlePageChange]);

  if (loading && allProducts.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading fresh products...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">
            <div className="text-red-600 text-lg mb-4">{error}</div>
            <button
              onClick={refreshData}
              className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Products Header */}
        <div className="bg-primary-600 text-white py-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h1 className="text-4xl font-bold mb-4">Fresh Products</h1>
            <p className="text-xl text-primary-100">
              Discover fresh, locally-sourced produce from our trusted farmers
            </p>

            {/* Search Results Info */}
            {(filters.searchTerm || filters.selectedCategory !== "All Categories") && (
              <div className="mt-6 p-4 bg-primary-700 rounded-lg">
                <div className="flex items-center justify-between flex-wrap gap-2">
                  <div>
                    {filters.searchTerm && (
                      <p className="text-primary-100">
                        Search results for:{" "}
                        <span className="font-semibold text-white">
                          "{filters.searchTerm}"
                        </span>
                      </p>
                    )}
                    {filters.selectedCategory !== "All Categories" && (
                      <p className="text-primary-100">
                        Category:{" "}
                        <span className="font-semibold text-white">
                          {filters.selectedCategory}
                        </span>
                      </p>
                    )}
                    <p className="text-sm text-primary-200">
                      {paginationData.pagination.totalProducts} products found
                    </p>
                  </div>

                  {/* Search Bar in Results */}
                  <div className="flex gap-2">
                    <input
                      type="text"
                      placeholder="Search products..."
                      className="px-4 py-2 rounded-lg text-gray-900 focus:outline-none"
                      value={filters.searchTerm}
                      onChange={(e) => handleFilterChange("searchTerm", e.target.value)}
                      onKeyPress={(e) => e.key === "Enter" && fetchProducts()}
                    />
                    <button
                      onClick={fetchProducts}
                      className="px-4 py-2 bg-primary-500 hover:bg-primary-400 rounded-lg transition"
                    >
                      <i className="fas fa-search"></i>
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Filters and Products */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Mobile Filter Toggle */}
          <div className="lg:hidden mb-6">
            <button
              onClick={() => setShowMobileFilters(!showMobileFilters)}
              className="w-full bg-white dark:bg-gray-800 text-gray-900 dark:text-white px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-between"
            >
              <span className="flex items-center">
                <i className="fas fa-filter mr-2"></i>
                Filters
                {getActiveFilterCount() > 0 && (
                  <span className="ml-2 bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                    {getActiveFilterCount()}
                  </span>
                )}
              </span>
              <i
                className={`fas fa-chevron-${showMobileFilters ? "up" : "down"}`}
              ></i>
            </button>
          </div>

          {/* Active Filters Summary */}
          {getActiveFilterCount() > 0 && (
            <div className="mb-6 p-4 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-medium text-gray-900 dark:text-white">
                  Active Filters ({getActiveFilterCount()})
                </h4>
                <button
                  onClick={clearAllFilters}
                  className="text-sm text-primary-600 hover:text-primary-700 dark:text-primary-400"
                >
                  Clear All
                </button>
              </div>
              <div className="flex flex-wrap gap-2">
                {filters.selectedCategory !== "All Categories" && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-primary-100 text-primary-800 dark:bg-primary-900 dark:text-primary-200">
                    Category: {filters.selectedCategory}
                    <button
                      onClick={() => handleFilterChange("category", "All Categories")}
                      className="ml-2 text-primary-600 hover:text-primary-800"
                    >

                    </button>
                  </span>
                )}
                {filters.selectedPriceRanges.map((range) => (
                  <span
                    key={range}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                  >
                    {range}
                    <button
                      onClick={() => handleFilterChange("priceRange", range)}
                      className="ml-2 text-green-600 hover:text-green-800"
                    >

                    </button>
                  </span>
                ))}
                {filters.selectedRatings.map((rating) => (
                  <span
                    key={rating}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
                  >
                    {rating}+ Stars
                    <button
                      onClick={() => handleFilterChange("rating", rating)}
                      className="ml-2 text-yellow-600 hover:text-yellow-800"
                    >

                    </button>
                  </span>
                ))}
                {filters.selectedFarmers.map((farmer) => (
                  <span
                    key={farmer}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
                  >
                    {farmer}
                    <button
                      onClick={() => handleFilterChange("farmer", farmer)}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >

                    </button>
                  </span>
                ))}
                {filters.selectedTags.map((tag) => (
                  <span
                    key={tag}
                    className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200"
                  >
                    {tag}
                    <button
                      onClick={() => handleFilterChange("tag", tag)}
                      className="ml-2 text-purple-600 hover:text-purple-800"
                    >

                    </button>
                  </span>
                ))}
                {(filters.priceRangeSlider[0] !== 0 ||
                  filters.priceRangeSlider[1] !== 10000) && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
                    {filters.priceRangeSlider[0]} - {filters.priceRangeSlider[1]}
                    <button
                      onClick={() => handleFilterChange("priceSlider", [0, 10000])}
                      className="ml-2 text-orange-600 hover:text-orange-800"
                    >

                    </button>
                  </span>
                )}
              </div>
            </div>
          )}

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            {/* Filters Sidebar */}
            <div
              className={`lg:col-span-1 ${showMobileFilters ? "block" : "hidden lg:block"}`}
            >
              <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 sticky top-24">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    Filters
                  </h3>
                  {getActiveFilterCount() > 0 && (
                    <span className="bg-primary-600 text-white text-xs px-2 py-1 rounded-full">
                      {getActiveFilterCount()}
                    </span>
                  )}
                </div>

                {/* Category Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-th-large mr-2 text-primary-600"></i>
                    Category
                  </h4>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {CATEGORY_OPTIONS.map((category) => (
                      <label
                        key={category}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="radio"
                          name="category"
                          checked={filters.selectedCategory === category}
                          onChange={() => handleFilterChange("category", category)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {category}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Price Range */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-dollar-sign mr-2 text-primary-600"></i>
                    Price Range ()
                  </h4>

                  {/* Custom Price Range Slider */}
                  <div className="mb-4">
                    <div className="relative">
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={filters.priceRangeSlider[0]}
                        onChange={(e) =>
                          handleFilterChange("priceSlider", [
                            +e.target.value,
                            filters.priceRangeSlider[1],
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <input
                        type="range"
                        min="0"
                        max="10000"
                        step="10"
                        value={filters.priceRangeSlider[1]}
                        onChange={(e) =>
                          handleFilterChange("priceSlider", [
                            filters.priceRangeSlider[0],
                            +e.target.value,
                          ])
                        }
                        className="absolute w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                      />
                      <div className="relative h-2 bg-gray-200 rounded-lg">
                        <div
                          className="absolute h-2 bg-primary-600 rounded-lg"
                          style={{
                            left: `${(filters.priceRangeSlider[0] / 10000) * 100}%`,
                            width: `${((filters.priceRangeSlider[1] - filters.priceRangeSlider[0]) / 10000) * 100}%`,
                          }}
                        ></div>
                      </div>
                    </div>
                    <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mt-2">
                      <span>{filters.priceRangeSlider[0]}</span>
                      <span>{filters.priceRangeSlider[1]}</span>
                    </div>
                  </div>

                  <div className="space-y-2">
                    {PRICE_RANGE_OPTIONS.map((option) => (
                      <label
                        key={option.label}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={filters.selectedPriceRanges.includes(option.label)}
                          onChange={() => handleFilterChange("priceRange", option.label)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {option.label}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Rating Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-star mr-2 text-primary-600"></i>
                    Rating
                  </h4>
                  <div className="space-y-2">
                    {[5, 4, 3, 2, 1].map((rating) => (
                      <label
                        key={rating}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={filters.selectedRatings.includes(rating)}
                          onChange={() => handleFilterChange("rating", rating)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 flex items-center">
                          {[...Array(rating)].map((_, i) => (
                            <i
                              key={i}
                              className="fas fa-star text-yellow-400 text-xs mr-1"
                            ></i>
                          ))}
                          {rating < 5 && <span className="ml-1">& Up</span>}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Farmer Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-user mr-2 text-primary-600"></i>
                    Farmer
                  </h4>
                  <div className="space-y-2 max-h-32 overflow-y-auto">
                    {availableFarmers.length > 0 ? (
                      availableFarmers.map((farmer) => (
                        <label
                          key={farmer}
                          className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                        >
                          <input
                            type="checkbox"
                            checked={filters.selectedFarmers.includes(farmer)}
                            onChange={() => handleFilterChange("farmer", farmer)}
                            className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                          />
                          <span className="ml-2 text-sm text-gray-700 dark:text-gray-300 truncate">
                            {farmer}
                          </span>
                        </label>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500 dark:text-gray-400 p-2">
                        Loading farmers...
                      </p>
                    )}
                  </div>
                </div>

                {/* Tag Filter */}
                <div className="mb-6">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-3 flex items-center">
                    <i className="fas fa-tags mr-2 text-primary-600"></i>
                    Tags
                  </h4>
                  <div className="space-y-2">
                    {TAG_OPTIONS.map((tag) => (
                      <label
                        key={tag}
                        className="flex items-center hover:bg-gray-50 dark:hover:bg-gray-700 p-2 rounded cursor-pointer"
                      >
                        <input
                          type="checkbox"
                          checked={filters.selectedTags.includes(tag)}
                          onChange={() => handleFilterChange("tag", tag)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <span className="ml-2 text-sm text-gray-700 dark:text-gray-300">
                          {tag}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Clear Filters */}
                <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
                  <button
                    onClick={clearAllFilters}
                    disabled={getActiveFilterCount() === 0}
                    className="w-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 py-2 rounded-lg font-medium hover:bg-gray-300 dark:hover:bg-gray-600 transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <i className="fas fa-times mr-2"></i>
                    Clear All Filters
                  </button>
                </div>
              </div>
            </div>

            {/* Products Grid */}
            <div className="lg:col-span-3">
              {/* Sort and View Options */}
              <div className="flex items-center justify-between mb-6 bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
                <div className="flex items-center space-x-4">
                  <span className="text-gray-700 dark:text-gray-300 font-medium">
                    <i className="fas fa-box mr-2 text-primary-600"></i>
                    {paginationData.pagination.totalProducts} products
                  </span>
                  {loading && (
                    <div className="flex items-center text-primary-600">
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      <span className="text-sm">Loading...</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center space-x-4">
                  <label className="text-sm text-gray-700 dark:text-gray-300">
                    Sort by:
                  </label>
                  <select
                    value={filters.sortBy}
                    onChange={(e) => handleFilterChange("sortBy", e.target.value)}
                    className="border border-gray-300 dark:border-gray-600 rounded-lg px-3 py-2 bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-primary-500 text-sm"
                  >
                    <option value="newest">Newest First</option>
                    <option value="oldest">Oldest First</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                    <option value="rating">Highest Rated</option>
                    <option value="popular">Most Popular</option>
                  </select>
                </div>
              </div>

              {/* Products Grid */}
              {loading ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(9)].map((_, index) => (
                    <div
                      key={index}
                      className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden animate-pulse"
                    >
                      <div className="w-full h-48 bg-gray-300 dark:bg-gray-600"></div>
                      <div className="p-6">
                        <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded mb-2"></div>
                        <div className="h-3 bg-gray-300 dark:bg-gray-600 rounded mb-3 w-3/4"></div>
                        <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded mb-4 w-1/2"></div>
                        <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded"></div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : paginationData.products.length > 0 ? (
                <>
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                    {paginationData.products.map((product) => (
                      <ProductCard key={product._id} product={product} />
                    ))}
                  </div>

                  {PaginationComponent}
                </>
              ) : (
                <div className="text-center py-12">
                  <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                  <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                    No products found
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400 mb-4">
                    Try adjusting your search criteria or browse all products
                  </p>
                  <button
                    onClick={clearAllFilters}
                    className="inline-block bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition"
                  >
                    Browse All Products
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

import globalCache, { sessionCache } from "./cache";

// Request deduplication to prevent multiple identical API calls
const ongoingRequests = new Map();

// Optimized API service with caching and request deduplication
export class ApiService {
  constructor() {
    this.baseURL = "/api";
  }

  // Generic fetch with caching and deduplication
  async fetchWithCache(endpoint, params = {}, options = {}) {
    const {
      ttl = 5 * 60 * 1000, // 5 minutes default
      useSessionCache = true,
      skipCache = false,
      ...fetchOptions
    } = options;

    // Generate cache key
    const cacheKey = globalCache.generateKey(endpoint, params);
    const sessionKey = sessionCache.generateKey(endpoint, params);

    // Return cached data if available and not skipping cache
    if (!skipCache) {
      // Try memory cache first (fastest)
      const memoryData = globalCache.get(cacheKey);
      if (memoryData) {
        return memoryData;
      }

      // Try session cache (survives page reloads)
      if (useSessionCache) {
        const sessionData = sessionCache.get(sessionKey);
        if (sessionData) {
          // Also store in memory cache for faster subsequent access
          globalCache.set(cacheKey, sessionData, ttl);
          return sessionData;
        }
      }
    }

    // Check if same request is already ongoing
    const requestKey = `${endpoint}_${JSON.stringify(params)}`;
    if (ongoingRequests.has(requestKey)) {
      return ongoingRequests.get(requestKey);
    }

    // Build URL with params
    const url = new URL(endpoint, window.location.origin);
    Object.entries(params).forEach(([key, value]) => {
      if (value !== null && value !== undefined && value !== "") {
        url.searchParams.append(key, value);
      }
    });

    // Create the promise and store it to prevent duplicate requests
    const requestPromise = fetch(url.toString(), {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        ...fetchOptions.headers,
      },
      ...fetchOptions,
    })
      .then(async (response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        // Cache the successful response
        if (!skipCache) {
          globalCache.set(cacheKey, data, ttl);
          if (useSessionCache) {
            sessionCache.set(sessionKey, data, ttl);
          }
        }
        return data;
      })
      .catch((error) => {
        console.error(`API Error for ${endpoint}:`, error);
        throw error;
      })
      .finally(() => {
        // Remove from ongoing requests
        ongoingRequests.delete(requestKey);
      });

    // Store the ongoing request
    ongoingRequests.set(requestKey, requestPromise);

    return requestPromise;
  }

  // Products API methods
  async getProducts(params = {}) {
    return this.fetchWithCache("/api/products", params, {
      ttl: 3 * 60 * 1000, // 3 minutes for products
      useSessionCache: true,
    });
  }

  async getProduct(id) {
    return this.fetchWithCache(
      `/api/products/${id}`,
      {},
      {
        ttl: 10 * 60 * 1000, // 10 minutes for individual products
        useSessionCache: true,
      },
    );
  }

  // Categories API
  async getCategories() {
    return this.fetchWithCache(
      "/api/categories",
      {},
      {
        ttl: 30 * 60 * 1000, // 30 minutes for categories (rarely change)
        useSessionCache: true,
      },
    );
  }

  // Farmers API
  async getFarmers() {
    return this.fetchWithCache(
      "/api/farmers",
      {},
      {
        ttl: 15 * 60 * 1000, // 15 minutes for farmers
        useSessionCache: true,
      },
    );
  }

  // Orders API
  async getOrders(params = {}) {
    return this.fetchWithCache("/api/orders", params, {
      ttl: 2 * 60 * 1000, // 2 minutes for orders (more dynamic)
      useSessionCache: false, // Don't cache orders in session (sensitive data)
    });
  }

  // Refresh specific cache entries
  refreshProductsCache(params = {}) {
    const cacheKey = globalCache.generateKey("/api/products", params);
    const sessionKey = sessionCache.generateKey("/api/products", params);
    globalCache.delete(cacheKey);
    sessionCache.delete(sessionKey);
  }

  // Clear all cache
  clearCache() {
    globalCache.clear();
    sessionCache.cleanup();
  }

  // Preload data for better performance
  async preloadProducts(commonParams = []) {
    const preloadPromises = commonParams.map((params) =>
      this.getProducts(params).catch((err) =>
        console.warn("Preload failed:", err),
      ),
    );
    return Promise.allSettled(preloadPromises);
  }
}

// Global API service instance
export const apiService = new ApiService();

// Hook for React components
export function useApiService() {
  return apiService;
}

import globalCache, { sessionCache } from "./cache";

// Request deduplication to prevent multiple identical API calls
const ongoingRequests = new Map();

// Optimized API service with caching and request deduplication
export class ApiService {
  constructor() {
    this.baseURL = "/api";
  }

  // Generic fetch with caching and deduplication
  async fetchWithCache(endpoint, params = {}, options = {}) {
    const {
      ttl = 5 * 60 * 1000, // 5 minutes default
      useSessionCache = true,
      skipCache = false,
      ...fetchOptions
    } = options;

    // Generate cache key
    const cacheKey = globalCache.generateKey(endpoint, params);
    const sessionKey = sessionCache.generateKey(endpoint, params);

    // Return cached data if available and not skipping cache
    if (!skipCache) {
      // Try memory cache first (fastest)
      const memoryData = globalCache.get(cacheKey);
      if (memoryData) {
        return memoryData;
      }

      // Try session cache (survives page reloads)
      if (useSessionCache) {
        const sessionData = sessionCache.get(sessionKey);
        if (sessionData) {
          // Also store in memory cache for faster subsequent access
          globalCache.set(cacheKey, sessionData, ttl);
          return sessionData;
        }
      }
    }

    // Check if same request is already ongoing
    const requestKey = `${endpoint}_${JSON.stringify(params)}`;
    if (ongoingRequests.has(requestKey)) {
      return ongoingRequests.get(requestKey);
    }

    // Build URL with params
    const url = new URL(endpoint, window.location.origin);
    Object.entries(params).forEach(([key, value]) => {
      if (value !== null && value !== undefined && value !== "") {
        url.searchParams.append(key, value);
      }
    });

    // Create the promise and store it to prevent duplicate requests
    const requestPromise = fetch(url.toString(), {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        ...fetchOptions.headers,
      },
      ...fetchOptions,
    })
      .then(async (response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        // Cache the successful response
        if (!skipCache) {
          globalCache.set(cacheKey, data, ttl);
          if (useSessionCache) {
            sessionCache.set(sessionKey, data, ttl);
          }
        }
        return data;
      })
      .catch((error) => {
        console.error(`API Error for ${endpoint}:`, error);
        throw error;
      })
      .finally(() => {
        // Remove from ongoing requests
        ongoingRequests.delete(requestKey);
      });

    // Store the ongoing request
    ongoingRequests.set(requestKey, requestPromise);

    return requestPromise;
  }

  // Products API methods
  async getProducts(params = {}) {
    return this.fetchWithCache("/api/products", params, {
      ttl: 3 * 60 * 1000, // 3 minutes for products
      useSessionCache: true,
    });
  }

  async getProduct(id) {
    return this.fetchWithCache(
      `/api/products/${id}`,
      {},
      {
        ttl: 10 * 60 * 1000, // 10 minutes for individual products
        useSessionCache: true,
      },
    );
  }

  // Categories API
  async getCategories() {
    return this.fetchWithCache(
      "/api/categories",
      {},
      {
        ttl: 30 * 60 * 1000, // 30 minutes for categories (rarely change)
        useSessionCache: true,
      },
    );
  }

  // Farmers API
  async getFarmers() {
    return this.fetchWithCache(
      "/api/farmers",
      {},
      {
        ttl: 15 * 60 * 1000, // 15 minutes for farmers
        useSessionCache: true,
      },
    );
  }

  // Orders API methods
  async getOrders(params = {}, options = {}) {
    return this.fetchWithCache("/api/orders", params, {
      ttl: 3 * 60 * 1000, // 3 minutes for orders (more dynamic data)
      ...options,
    });
  }

  async getOrderById(orderId, options = {}) {
    return this.fetchWithCache(`/api/orders/${orderId}`, {}, options);
  }

  async updateOrder(orderId, updateData) {
    const response = await fetch(`/api/orders`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ orderId, ...updateData }),
    });

    if (!response.ok) {
      throw new Error(`Failed to update order: ${response.status}`);
    }

    // Clear orders cache when updating
    this.clearOrdersCache();
    return response.json();
  }

  // Cache management for orders
  clearOrdersCache() {
    // Clear all order-related cache entries
    const orderKeys = globalCache.getStats().keys.filter(key => 
      key.includes('/api/orders')
    );
    orderKeys.forEach(key => globalCache.delete(key));

    // Clear session cache
    if (typeof window !== 'undefined') {
      sessionCache.cleanup();
    }
  }

  // Optimized method for dashboard data - fetch both products and orders efficiently
  async getDashboardData(userId, userEmail, options = {}) {
    const { forceRefresh = false } = options;

    try {
      // Fetch products and orders in parallel with optimized parameters
      const [productsData, ordersData] = await Promise.all([
        this.getProducts({
          limit: 200, // Reduced limit for dashboard
          farmerId: userId,
          farmerEmail: userEmail,
        }, { skipCache: forceRefresh }),

        this.getOrders({
          limit: 100, // Reduced limit for dashboard
          farmerId: userId,
          farmerEmail: userEmail,
        }, { skipCache: forceRefresh })
      ]);

      return {
        products: productsData.products || [],
        orders: ordersData.orders || [],
        meta: {
          productsCount: productsData.products?.length || 0,
          ordersCount: ordersData.orders?.length || 0,
          fromCache: !forceRefresh
        }
      };
    } catch (error) {
      console.error('Dashboard data fetch error:', error);
      throw error;
    }
  }
}

// Global API service instance
export const apiService = new ApiService();

// Hook for React components
export function useApiService() {
  return apiService;
}

// Cache utility for client-side data caching
class CacheManager {
  constructor() {
    this.cache = new Map();
    this.timestamps = new Map();
    this.defaultTTL = 5 * 60 * 1000; // 5 minutes default TTL
  }

  // Generate cache key from parameters
  generateKey(baseKey, params = {}) {
    const sortedParams = Object.keys(params)
      .sort()
      .reduce((result, key) => {
        result[key] = params[key];
        return result;
      }, {});
    return `${baseKey}_${JSON.stringify(sortedParams)}`;
  }

  // Set cache entry with TTL
  set(key, data, ttl = this.defaultTTL) {
    this.cache.set(key, data);
    this.timestamps.set(key, Date.now() + ttl);

    // Clean up expired entries periodically
    this.cleanup();
  }

  // Get cache entry if not expired
  get(key) {
    const timestamp = this.timestamps.get(key);
    if (!timestamp || Date.now() > timestamp) {
      this.cache.delete(key);
      this.timestamps.delete(key);
      return null;
    }
    return this.cache.get(key);
  }

  // Check if cache has valid entry
  has(key) {
    const timestamp = this.timestamps.get(key);
    if (!timestamp || Date.now() > timestamp) {
      this.cache.delete(key);
      this.timestamps.delete(key);
      return false;
    }
    return this.cache.has(key);
  }

  // Clear specific entry
  delete(key) {
    this.cache.delete(key);
    this.timestamps.delete(key);
  }

  // Clear all cache
  clear() {
    this.cache.clear();
    this.timestamps.clear();
  }

  // Clean up expired entries
  cleanup() {
    const now = Date.now();
    for (const [key, timestamp] of this.timestamps.entries()) {
      if (now > timestamp) {
        this.cache.delete(key);
        this.timestamps.delete(key);
      }
    }
  }

  // Get cache stats
  getStats() {
    return {
      size: this.cache.size,
      keys: Array.from(this.cache.keys()),
    };
  }
}

// Global cache instance
const globalCache = new CacheManager();

export default globalCache;

// Session storage cache for persistence across page reloads
export class SessionCache {
  constructor(prefix = "farmfresh_") {
    this.prefix = prefix;
  }

  generateKey(baseKey, params = {}) {
    const sortedParams = Object.keys(params)
      .sort()
      .reduce((result, key) => {
        result[key] = params[key];
        return result;
      }, {});
    return `${this.prefix}${baseKey}_${JSON.stringify(sortedParams)}`;
  }

  set(key, data, ttl = 5 * 60 * 1000) {
    if (typeof window === "undefined") return;

    const item = {
      data,
      timestamp: Date.now() + ttl,
    };

    try {
      sessionStorage.setItem(key, JSON.stringify(item));
    } catch (e) {
      console.warn("SessionStorage full, clearing old entries");
      this.cleanup();
      try {
        sessionStorage.setItem(key, JSON.stringify(item));
      } catch (e) {
        console.error("Failed to store in sessionStorage:", e);
      }
    }
  }

  get(key) {
    if (typeof window === "undefined") return null;

    try {
      const item = sessionStorage.getItem(key);
      if (!item) return null;

      const parsed = JSON.parse(item);
      if (Date.now() > parsed.timestamp) {
        sessionStorage.removeItem(key);
        return null;
      }

      return parsed.data;
    } catch (e) {
      sessionStorage.removeItem(key);
      return null;
    }
  }

  has(key) {
    return this.get(key) !== null;
  }

  delete(key) {
    if (typeof window === "undefined") return;
    sessionStorage.removeItem(key);
  }

  cleanup() {
    if (typeof window === "undefined") return;

    const keysToRemove = [];
    for (let i = 0; i < sessionStorage.length; i++) {
      const key = sessionStorage.key(i);
      if (key && key.startsWith(this.prefix)) {
        try {
          const item = JSON.parse(sessionStorage.getItem(key));
          if (Date.now() > item.timestamp) {
            keysToRemove.push(key);
          }
        } catch (e) {
          keysToRemove.push(key);
        }
      }
    }

    keysToRemove.forEach((key) => sessionStorage.removeItem(key));
  }
}

export const sessionCache = new SessionCache();

// Cache utility for client-side data caching
class CacheManager {
  constructor() {
