async function showReviewTimeline() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const reviews = await db
    .collection("reviews")
    .find({})
    .sort({ createdAt: 1 })
    .toArray();

  console.log("\n Review timeline (oldest to newest):");
  reviews.forEach((review, index) => {
    console.log(
      `${index + 1}. ${review.createdAt.toISOString()} - ${review.reviewer} - ${review.comment?.substring(0, 50)}...`,
    );
  });

  await client.close();
}

// Check and fix review mess - remove duplicate reviews added by mistake
const { MongoClient, ObjectId } = require("mongodb");

async function fixReviewMess() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  console.log(" Analyzing review situation...");

  // Get all reviews
  const allReviews = await db.collection("reviews").find({}).toArray();
  console.log(`Total reviews in database: ${allReviews.length}`);

  // Group reviews by product ID
  const reviewsByProduct = {};
  allReviews.forEach((review) => {
    if (!reviewsByProduct[review.productId]) {
      reviewsByProduct[review.productId] = [];
    }
    reviewsByProduct[review.productId].push(review);
  });

  console.log("\n Reviews per product:");
  Object.entries(reviewsByProduct).forEach(([productId, reviews]) => {
    const product = reviews[0]; // Just to get some context
    console.log(`Product ID ${productId}: ${reviews.length} reviews`);

    // Show reviewers to identify duplicates
    const reviewers = reviews.map((r) => r.reviewer).join(", ");
    console.log(`  Reviewers: ${reviewers}`);

    // Check for suspicious patterns (our sample review names)
    const sampleReviewers = [
      "John Doe",
      "Jane Smith",
      "Mike Johnson",
      "Sarah Wilson",
      "David Brown",
    ];
    const hasSampleReviews = reviews.some((r) =>
      sampleReviewers.includes(r.reviewer),
    );
    if (hasSampleReviews) {
      console.log(
        `   This product has sample reviews that might need removal`,
      );
    }
    console.log("");
  });

  // Ask what to do
  console.log("\n What would you like to do?");
  console.log(
    "1. Remove all reviews with sample reviewer names (John Doe, Jane Smith, etc.)",
  );
  console.log("2. Remove reviews created in the last hour (recent additions)");
  console.log(
    "3. Show me the oldest vs newest reviews to identify which are original",
  );
  console.log("\nRun with parameter: node fix-reviews.js [1|2|3]");

  await client.close();
}

// Handle command line arguments
const action = process.argv[2];

if (action === "1") {
  removeSampleReviews();
} else if (action === "2") {
  removeRecentReviews();
} else if (action === "3") {
  showReviewTimeline();
} else {
  fixReviewMess().catch(console.error);
}

async function removeSampleReviews() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const sampleReviewers = [
    "John Doe",
    "Jane Smith",
    "Mike Johnson",
    "Sarah Wilson",
    "David Brown",
  ];

  console.log(" Removing sample reviews...");
  const result = await db.collection("reviews").deleteMany({
    reviewer: { $in: sampleReviewers },
  });

  console.log(` Removed ${result.deletedCount} sample reviews`);

  // Check what's left
  const remainingReviews = await db.collection("reviews").find({}).toArray();
  console.log(` Remaining reviews: ${remainingReviews.length}`);
  console.log("Your original reviews:");
  remainingReviews.forEach((r) =>
    console.log(`- ${r.reviewer || "Anonymous"}: ${r.comment?.substring(0, 50)}...`),
  );

  await client.close();
}

async function removeRecentReviews() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000);

  console.log(" Removing reviews created in the last hour...");
  const result = await db.collection("reviews").deleteMany({
    createdAt: { $gte: oneHourAgo },
  });

  console.log(` Removed ${result.deletedCount} recent reviews`);
  await client.close();
}

async function showReviewTimeline() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const reviews = await db
    .collection("reviews")
    .find({})
    .sort({ createdAt: 1 })
    .toArray();

  console.log("\n Review timeline (oldest to newest):");
  reviews.forEach((review, index) => {
    console.log(
      `${index + 1}. ${review.createdAt.toISOString()} - ${review.reviewer} - ${review.comment?.substring(0, 50)}...`,
    );
  });

  await client.close();
}

/**
 * Calculate average rating and review count from reviews array
 * @param {Array} reviews - Array of review objects with rating property
 * @returns {Object} - Object containing averageRating and reviewCount
 */
export function calculateRatingStats(reviews) {
  if (!reviews || !Array.isArray(reviews) || reviews.length === 0) {
    return {
      averageRating: 0,
      reviewCount: 0,
    };
  }

  const totalRating = reviews.reduce((sum, review) => {
    const rating = Number(review.rating);
    return sum + (isNaN(rating) ? 0 : rating);
  }, 0);

  const averageRating = Math.round((totalRating / reviews.length) * 10) / 10;

  return {
    averageRating: averageRating,
    reviewCount: reviews.length,
  };
}

/**
 * Enhance product data with calculated rating statistics
 * @param {Object} product - Product object
 * @returns {Object} - Enhanced product object with calculated ratings
 */
export function enhanceProductWithRatings(product) {
  const { averageRating, reviewCount } = calculateRatingStats(product.reviews);

  return {
    ...product,
    averageRating,
    reviewCount: reviewCount,
  };
}

/**
 * Enhance multiple products with calculated rating statistics from database
 * @param {Array} products - Array of product objects
 * @param {Object} db - MongoDB database instance
 * @returns {Array} - Array of enhanced product objects
 */
export async function enhanceProductsWithRatings(products, db) {
  if (!Array.isArray(products) || products.length === 0) {
    return [];
  }

  try {
    // Get all reviews from the database first (simpler approach)
    const allReviews = await db.collection("reviews").find({}).toArray();

    // Group reviews by product ID
    const reviewsByProduct = {};
    allReviews.forEach((review) => {
      const productId = review.productId;
      if (!reviewsByProduct[productId]) {
        reviewsByProduct[productId] = [];
      }
      reviewsByProduct[productId].push(review);
    });

    // Enhance each product with its review data
    const enhancedProducts = products.map((product) => {
      const productId = product._id.toString();
      const productReviews = reviewsByProduct[productId] || [];
      const { averageRating, reviewCount } =
        calculateRatingStats(productReviews);

      return {
        ...product,
        averageRating,
        reviewCount,
        totalRatings: reviewCount, // Add totalRatings for compatibility with existing code
        reviews: productReviews, // Include reviews if needed
      };
    });

    return enhancedProducts;
  } catch (error) {
    console.error("Error enhancing products with ratings:", error);
    // Return products with default rating values if enhancement fails
    return products.map((product) => ({
      ...product,
      averageRating: 0,
      reviewCount: 0,
      totalRatings: 0,
    }));
  }
}

/**
 * Calculate average rating and review count from reviews array
 * @param {Array} reviews - Array of review objects with rating property
 * @returns {Object} - Object containing averageRating and reviewCount
 */
export function calculateRatingStats(reviews) {
  if (!reviews || !Array.isArray(reviews) || reviews.length === 0) {
    return {
      averageRating: 0,
      reviewCount: 0,
    };
  }

  const totalRating = reviews.reduce((sum, review) => {
    const rating = Number(review.rating);
    return sum + (isNaN(rating) ? 0 : rating);
  }, 0);

  const averageRating = Math.round((totalRating / reviews.length) * 10) / 10;

  return {
    averageRating: averageRating,
    reviewCount: reviews.length,
  };
}

/**
 * Enhance product data with calculated rating statistics
 * @param {Object} product - Product object
 * @returns {Object} - Enhanced product object with calculated ratings
 */
export function enhanceProductWithRatings(product) {
  const { averageRating, reviewCount } = calculateRatingStats(product.reviews);

  return {
    ...product,
    averageRating,
    reviewCount: reviewCount,
  };
}

/**
 * Enhance multiple products with calculated rating statistics from database
 * @param {Array} products - Array of product objects
 * @param {Object} db - MongoDB database instance
 * @returns {Array} - Array of enhanced product objects
 */
export async function enhanceProductsWithRatings(products, db) {
  if (!Array.isArray(products) || products.length === 0) {
    return [];
  }

  try {
    // Get all reviews from the separate reviews collection
    const separateReviews = await db.collection("reviews").find({}).toArray();

    // Group separate reviews by product ID
    const reviewsByProduct = {};
    separateReviews.forEach((review) => {
      const productId = review.productId;
      if (!reviewsByProduct[productId]) {
        reviewsByProduct[productId] = [];
      }
      reviewsByProduct[productId].push(review);
    });

    // Enhance each product with its review data
    const enhancedProducts = products.map((product) => {
      const productId = product._id.toString();

      // Check for reviews in two places:
      // 1. Reviews stored inside the product document (old products)
      // 2. Reviews in the separate reviews collection (new products)
      let productReviews = [];

      // First, check if reviews are stored in the product document itself
      if (product.reviews && Array.isArray(product.reviews)) {
        productReviews = product.reviews;
      }
      // Second, check the separate reviews collection
      else if (reviewsByProduct[productId]) {
        productReviews = reviewsByProduct[productId];
      }

      const { averageRating, reviewCount } = calculateRatingStats(productReviews);

      return {
        ...product,
        averageRating,
        reviewCount,
        totalRatings: reviewCount, // Add totalRatings for compatibility with existing code
        reviews: productReviews, // Include reviews if needed
      };
    });

    return enhancedProducts;
  } catch (error) {
    console.error("Error enhancing products with ratings:", error);
    // Return products with default rating values if enhancement fails
    return products.map((product) => ({
      ...product,
      averageRating: 0,
      reviewCount: 0,
      totalRatings: 0,
    }));
  }
}

// Make the products with reviews visible by marking them as featured
const { MongoClient, ObjectId } = require('mongodb');

async function makeReviewedProductsVisible() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  const productIdsWithReviews = ['6889cb7ab6d22dd651140bee', '688affa63c35c3c0ff9c4925'];

  console.log(' Making products with reviews visible...\n');

  for (const productId of productIdsWithReviews) {
    try {
      const result = await db.collection('products').updateOne(
        { _id: new ObjectId(productId) },
        { 
          $set: { 
            featured: true,
            status: 'active',
            stock: Math.max(10, 0) // Ensure they have stock
          } 
        }
      );

      if (result.matchedCount > 0) {
        console.log(` Updated product ${productId} to be featured and active`);
      } else {
        console.log(` Product ${productId} not found`);
      }
    } catch (error) {
      console.log(` Error updating product ${productId}: ${error.message}`);
    }
  }

  console.log('\n Now check your homepage - the products with reviews should appear in the featured section with star ratings!');

  await client.close();
}

makeReviewedProductsVisible().catch(console.error);
