    async (reviewData) => {
      console.log(" Starting handleEnhancedReviewSubmit", {
        reviewData,
        editingReview,
      });

      try {
        if (editingReview) {
          console.log(" Updating existing review...");
          // Update existing review using React Query mutation
          await updateReview({
            reviewId: editingReview._id,
            reviewData: {
              rating: reviewData.rating,
              comment: reviewData.comment,
              title: reviewData.title,
              pros: reviewData.pros,
              cons: reviewData.cons,
              wouldRecommend: reviewData.wouldRecommend,
              isAnonymous: reviewData.isAnonymous,
              tags: reviewData.tags,
            },
            userId: reviewData.userId,
          });

          console.log("✅ Review updated successfully!");
          alert("Review updated successfully!");
        } else {
          console.log("➕ Creating new review...");
          // Create new review using React Query mutation
          await submitReview({
            productId,
            reviewData,
          });

          console.log("✅ Review submitted successfully!");

          // Update user review states
          setHasReviewedProduct(true);
          setUserExistingReview(reviewData);

          console.log(" Updated local states - hasReviewedProduct: true");

          alert("Review submitted successfully!");
