                            onClick={handleUpdateProduct}
                            disabled={isUpdating || !priceUpdate}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition disabled:bg-gray-400"
                          >
                            Update
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Recent Orders */}
                  <RecentOrdersSection
                    recentOrders={recentOrders}
                    loadingOrders={loadingOrders}
                    product={product}
                  />
                </div>

                {/* Sidebar - Analytics & Actions */}
                <div className="space-y-6">
                  {/* Performance Stats */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                        Performance
                      </h3>
                      <div className="text-xs text-gray-500 dark:text-gray-400">
                        Real-time data
                      </div>
                    </div>

                    <div className="space-y-4">
                      {/* Total Sales */}
                      <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                        <div className="flex items-center justify-center gap-2 mb-1">
                          <i className="fas fa-chart-line text-blue-600"></i>
                          <span className="text-2xl font-bold text-blue-600">
                            {product.performanceMetrics?.totalSales || 0}
                          </span>
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Sales
                        </div>
                      </div>

                      {/* Total Revenue */}
                      <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-green-600">
                          {formatPrice(
                            product.performanceMetrics?.totalRevenue || 0,
                          )}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Revenue
                        </div>
                      </div>

                      {/* Average Rating */}
                      <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-yellow-600">
                          {(product.averageRating || 0).toFixed(1)}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Average Rating
                        </div>
                        <div className="flex justify-center mt-1">
                          <StarRating
                            rating={product.averageRating || 0}
                            size="sm"
                          />
                        </div>
                      </div>

                      {/* Total Reviews */}
                      <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-purple-600">
                          {product.reviewCount || product.totalReviews || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Reviews
                        </div>
                      </div>

                      {/* Average Order Value */}
                      {product.performanceMetrics?.averageOrderValue > 0 && (
                        <div className="text-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-orange-600">
                            {formatPrice(
                              product.performanceMetrics.averageOrderValue,
                            )}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Avg. Order Value
                          </div>
                        </div>
                      )}

                      {/* Total Orders */}
                      {product.performanceMetrics?.totalOrders > 0 && (
                        <div className="text-center p-4 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-indigo-600">
                            {product.performanceMetrics.totalOrders}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Total Orders
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Performance Summary */}
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                      <div className="text-xs text-gray-500 dark:text-gray-400 text-center">
                        {product.performanceMetrics?.totalSales > 0
                          ? "Your product is performing well!"
                          : "Start promoting your product to get your first sale!"}
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Actions
                    </h3>

                    <div className="space-y-3">
                      {session?.user?.userType === "farmer" ? (
                        // Farmer-specific buttons
                        <>
                          <Link
                            href="/create"
                            className="w-full bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg flex items-center justify-center"
                          >
                            <i className="fas fa-plus mr-2"></i>
                            Add Product
                          </Link>
                          <Link
                            href="/manage"
                            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg flex items-center justify-center"
                          >
                            <i className="fas fa-cog mr-2"></i>
                            Manage Orders
                          </Link>
                          <Link
                            href="/farmer-orders"
                            className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition flex items-center justify-center"
                          >
                            <i className="fas fa-clipboard-list mr-2"></i>
                            View My Orders
                          </Link>
                        </>
                      ) : (
                        // Customer buttons
                        <>
                          <button
                            onClick={handleBuyNow}
                            disabled={
                              product.stock <= 0 ||
                              session?.user?.userType === "farmer"
                            }
                            className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                            title={
                              session?.user?.userType === "farmer"
                                ? "Farmers cannot purchase products"
                                : ""
                            }
                          >
                            <i className="fas fa-bolt mr-2"></i>
                            Buy Now
                          </button>
                          <button
                            onClick={handleAddToCart}
                            disabled={
                              isAddingToCart ||
                              product.stock <= 0 ||
                              session?.user?.userType === "farmer"
                            }
                            className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                            title={
                              session?.user?.userType === "farmer"
                                ? "Farmers cannot purchase products"
                                : ""
                            }
                          >
                            <i className="fas fa-shopping-cart mr-2"></i>
                            {isAddingToCart ? "Adding..." : "Add to Cart"}
                          </button>
                          <button
                            onClick={handleFavoriteToggle}
                            className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                          >
                            <i
                              className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                            ></i>
                            Add to Favorite
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            /* Regular Customer View */
            <>
              {/* Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/" className="hover:text-primary-600">
                      Home
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <Link href="/products" className="hover:text-primary-600">
                      Products
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name}
                    </span>
                  </li>
                </ol>
              </nav>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                {/* Product Images */}
                <div className="space-y-4">
                  <div className="aspect-square bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg">
                    {(() => {
                      const allImages = imageData.allImages;

                      return (
                        <Image
                          src={
                            allImages[selectedImage] || "/placeholder-image.jpg"
                          }
                          alt={product.name}
                          width={600}
                          height={600}
                          className="w-full h-full object-cover"
                        />
                      );
                    })()}
                  </div>

                  {/* Thumbnail Images */}
                  {(() => {
                    const allImages = imageData.allImages;

                    return allImages.length > 1 ? (
                      <div className="grid grid-cols-5 gap-2">
                        {allImages.map((image, index) => (
                          <button
                            key={index}
                            onClick={() => setSelectedImage(index)}
                            className={`aspect-square rounded-lg overflow-hidden border-2 ${
                              selectedImage === index
                                ? "border-primary-500"
                                : "border-gray-300 dark:border-gray-600"
                            }`}
                          >
                            <Image
                              src={image}
                              alt={`${product.name} ${index + 1}`}
                              width={100}
                              height={100}
                              className="w-full h-full object-cover"
                            />
                          </button>
                        ))}
                      </div>
                    ) : null;
                  })()}
                </div>

                {/* Product Info */}
                <div className="space-y-6">
                  {/* Category and Features */}
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-3 py-1 rounded-full text-sm font-medium">
                      {product.category}
                    </span>
                    {product.isOrganic && (
                      <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                        Organic
                      </span>
                    )}
                    {product.isFresh && (
                      <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                        Fresh
                      </span>
                    )}
                  </div>

                  {/* Product Name and Farmer */}
                  <div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                      {product.name}
                    </h1>
                    <p className="text-lg text-gray-600 dark:text-gray-400">
                      Produced by{" "}
                      <span className="font-semibold text-primary-600 dark:text-primary-400">
                        {product.farmer?.farmName ||
                          product.farmer?.name ||
                          "Unknown Farmer"}
                      </span>
                    </p>
                  </div>

                  {/* Rating and Reviews */}
                  <div className="flex items-center space-x-4">
                    {(() => {
                      // Calculate actual average rating and count from reviews if available
                      const actualReviewCount = reviews?.length || 0;
                      let displayRating = product.averageRating || 0;

                      // If we have reviews but no product rating, calculate from reviews
                      if (
                        actualReviewCount > 0 &&
                        (!product.averageRating || product.averageRating === 0)
                      ) {
                        const totalRating = reviews.reduce(
                          (sum, review) => sum + (review.rating || 0),
                          0,
                        );
                        displayRating = totalRating / actualReviewCount;
                      }

                      return (
                        <>
                          <StarRating rating={displayRating} showValue={true} />
                          <span className="text-gray-500 dark:text-gray-400">
                            ({actualReviewCount} reviews)
                          </span>
                        </>
                      );
                    })()}
                  </div>

                  {/* Price and Stock */}
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-6">
                    <div className="flex items-center justify-between mb-4">
                      <div>
                        <span className="text-3xl font-bold text-primary-600 dark:text-primary-400">
                          {formatPrice(product.price)}
                        </span>
                        <span className="text-lg text-gray-500 dark:text-gray-400">
                          /{product.unit || "kg"}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500 dark:text-gray-400">
                          Available Stock
                        </p>
                        <p className="text-lg font-semibold text-gray-900 dark:text-white">
                          {product.stock} {product.unit || "kg"}
                        </p>
                      </div>
                    </div>

                    {/* Location */}
                    <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                      <i className="fas fa-map-marker-alt mr-2"></i>
                      <span>
                        {product.farmer?.location || "Location not specified"}
                      </span>
                    </div>
                  </div>

                  {/* Quantity Selection */}
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Quantity ({product.unit || "kg"})
                      </label>
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={() => setQuantity(Math.max(1, quantity - 1))}
                          disabled={session?.user?.userType === "farmer"}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                          <i className="fas fa-minus"></i>
                        </button>
                        <input
                          type="number"
                          min="1"
                          max={product.stock}
                          value={quantity}
                          onChange={(e) =>
                            setQuantity(
                              Math.max(1, parseInt(e.target.value) || 1),
                            )
                          }
                          disabled={session?.user?.userType === "farmer"}
                          className="w-20 text-center py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white disabled:opacity-50 disabled:cursor-not-allowed"
                        />
                        <button
                          onClick={() =>
                            setQuantity(Math.min(product.stock, quantity + 1))
                          }
                          disabled={session?.user?.userType === "farmer"}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                          <i className="fas fa-plus"></i>
                        </button>
                      </div>
                      {session?.user?.userType === "farmer" && (
                        <p className="text-sm text-gray-500 dark:text-gray-400 mt-2 italic">
                          <i className="fas fa-info-circle mr-1"></i>
                          Farmers can only view product details, not purchase
                        </p>
                      )}
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    {session?.user?.userType === "farmer" ? (
                      // Farmer-specific buttons
                      <>
                        <Link
                          href="/create"
                          className="w-full bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg flex items-center justify-center"
                        >
                          <i className="fas fa-plus mr-2"></i>
                          Add Product
                        </Link>
                        <Link
                          href="/manage"
                          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg flex items-center justify-center"
                        >
                          <i className="fas fa-cog mr-2"></i>
                          Manage Orders
                        </Link>
                        <Link
                          href="/farmer-orders"
                          className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition flex items-center justify-center"
                        >
                          <i className="fas fa-clipboard-list mr-2"></i>
                          View My Orders
                        </Link>
                      </>
                    ) : (
                      // Customer buttons
                      <>
                        <button
                          onClick={handleBuyNow}
                          disabled={
                            product.stock <= 0 ||
                            session?.user?.userType === "farmer"
                          }
                          className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                          title={
                            session?.user?.userType === "farmer"
                              ? "Farmers cannot purchase products"
                              : ""
                          }
                        >
                          <i className="fas fa-bolt mr-2"></i>
                          Buy Now
                        </button>
                        <button
                          onClick={handleAddToCart}
                          disabled={
                            isAddingToCart ||
                            product.stock <= 0 ||
                            session?.user?.userType === "farmer"
                          }
                          className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                          title={
                            session?.user?.userType === "farmer"
                              ? "Farmers cannot purchase products"
                              : ""
                          }
                        >
                          <i className="fas fa-shopping-cart mr-2"></i>
                          {isAddingToCart ? "Adding..." : "Add to Cart"}
                        </button>
                        <button
                          onClick={handleFavoriteToggle}
                          className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                        >
                          <i
                            className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                          ></i>
                          Add to Favorite
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>

              {/* Tabs Section */}
              <div className="mt-16">
                <div className="border-b border-gray-200 dark:border-gray-700">
                  <nav className="flex space-x-8">
                    {TAB_OPTIONS.map((tab) => (
                      <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`py-4 px-1 border-b-2 font-medium text-sm ${
                          activeTab === tab
                            ? "border-primary-500 text-primary-600 dark:text-primary-400"
                            : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
                        }`}
                      >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    ))}
                  </nav>
                </div>

                <div className="py-8">
                  {/* Description Tab */}
                  {activeTab === "description" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-4">
                        Product Description
                      </h3>
                      <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                        {product.description ||
                          "No description available for this product."}
                      </p>

                      {product.features && product.features.length > 0 && (
                        <div className="mt-6">
                          <h4 className="text-lg font-medium mb-3">
                            Key Features
                          </h4>
                          <ul className="list-disc list-inside space-y-1">
                            {product.features.map((feature, index) => (
                              <li
                                key={index}
                                className="text-gray-600 dark:text-gray-400"
                              >
                                {feature}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Nutrition Tab */}
                  {activeTab === "nutrition" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Nutritional Information
                      </h3>

                      {product.nutritionalInformation ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          {typeof product.nutritionalInformation ===
                          "string" ? (
                            <p className="text-gray-600 dark:text-gray-400">
                              {product.nutritionalInformation}
                            </p>
                          ) : (
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                              {/* Display nutritional information as structured data */}
                              {product.nutritionalInformation.servingSize && (
                                <div className="flex justify-between">
                                  <span className="font-medium">
                                    Serving Size:
                                  </span>
                                  <span>
                                    {product.nutritionalInformation.servingSize}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.calories && (
                                <div className="flex justify-between">
                                  <span className="font-medium">Calories:</span>
                                  <span>
                                    {product.nutritionalInformation.calories}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.protein && (
                                <div className="flex justify-between">
                                  <span className="font-medium">Protein:</span>
                                  <span>
                                    {product.nutritionalInformation.protein}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.fat && (
                                <div className="flex justify-between">
                                  <span className="font-medium">Fat:</span>
                                  <span>
                                    {product.nutritionalInformation.fat}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.carbohydrates && (
                                <div className="flex justify-between">
                                  <span className="font-medium">
                                    Carbohydrates:
                                  </span>
                                  <span>
                                    {
                                      product.nutritionalInformation
                                        .carbohydrates
                                    }
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.fiber && (
                                <div className="flex justify-between">
                                  <span className="font-medium">Fiber:</span>
                                  <span>
                                    {product.nutritionalInformation.fiber}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.vitaminC && (
                                <div className="flex justify-between">
                                  <span className="font-medium">
                                    Vitamin C:
                                  </span>
                                  <span>
                                    {product.nutritionalInformation.vitaminC}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.potassium && (
                                <div className="flex justify-between">
                                  <span className="font-medium">
                                    Potassium:
                                  </span>
                                  <span>
                                    {product.nutritionalInformation.potassium}
                                  </span>
                                </div>
                              )}
                              {product.nutritionalInformation.iron && (
                                <div className="flex justify-between">
                                  <span className="font-medium">Iron:</span>
                                  <span>
                                    {product.nutritionalInformation.iron}
                                  </span>
                                </div>
                              )}
                            </div>
                          )}
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-apple-alt text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Nutritional information is not available for this
                            product.
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Storage Instructions Tab */}
                  {activeTab === "storage" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Storage Instructions
                      </h3>

                      {product.storageInstructions ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          <p className="text-gray-600 dark:text-gray-400">
                            {product.storageInstructions}
                          </p>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-warehouse text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Storage instructions are not available for this
                            product.
                          </p>
                        </div>
                      )}

                      {/* General storage tips */}
                      <div className="mt-8">
                        <h4 className="text-lg font-medium mb-4">
                          General Storage Tips
                        </h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div className="bg-blue-50 dark:bg-blue-900 text-blue-900 dark:text-blue-100 rounded-lg p-4">
                            <h5 className="font-medium mb-2">
                              <i className="fas fa-thermometer-half mr-2"></i>
                              Temperature
                            </h5>
                            <p className="text-sm">
                              Store in a cool, dry place away from direct
                              sunlight
                            </p>
                          </div>
                          <div className="bg-green-50 dark:bg-green-900 text-green-900 dark:text-green-100 rounded-lg p-4">
                            <h5 className="font-medium mb-2">
                              <i className="fas fa-tint mr-2"></i>
                              Humidity
                            </h5>
                            <p className="text-sm">
                              Keep in low humidity environment to prevent
                              spoilage
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Reviews Tab */}
                  {activeTab === "reviews" && (
                    <div>
                      <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          Customer Reviews (
                          {product.reviewCount || product.totalReviews || 0})
                        </h2>
                        {/* Show review button only for customers who have purchased and received the product but haven't reviewed yet */}
                        {session &&
                          session?.user?.userType !== "farmer" &&
                          hasPurchasedProduct &&
                          !hasReviewedProduct && (
                            <button
                              onClick={() => setShowReviewForm(true)}
                              className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium transition"
                            >
                              <i className="fas fa-plus mr-2"></i>
                              Write Review
                            </button>
                          )}

                        {/* Show edit button if user has already reviewed */}
                        {session &&
                          session?.user?.userType !== "farmer" &&
                          hasPurchasedProduct &&
                          hasReviewedProduct &&
                          userExistingReview && (
                            <button
                              onClick={() => {
                                setEditingReview(userExistingReview);
                                setReviewForm({
                                  rating: userExistingReview.rating || 5,
                                  comment: userExistingReview.comment || "",
                                });
                                setShowReviewForm(true);
                              }}
                              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-medium transition"
                            >
                              <i className="fas fa-edit mr-2"></i>
                              Edit Your Review
                            </button>
                          )}

                        {session &&
                          session?.user?.userType !== "farmer" &&
                          !hasPurchasedProduct &&
                          !checkingPurchase && (
                            <div className="text-center">
                              <p className="text-gray-500 dark:text-gray-400 text-sm italic mb-4">
                                You need to purchase and receive this product to
                                write a review
                              </p>
                              <div className="inline-flex items-center px-4 py-2 bg-gray-100 dark:bg-gray-800 rounded-lg text-gray-600 dark:text-gray-400 text-sm">
                                <i className="fas fa-shopping-cart mr-2"></i>
                                Purchase required for reviews
                              </div>
                            </div>
                          )}

                        {/* Show message if user has already reviewed */}
                        {session &&
                          session?.user?.userType !== "farmer" &&
                          hasPurchasedProduct &&
                          hasReviewedProduct &&
                          !checkingPurchase && (
                            <div className="text-center">
                              <p className="text-gray-500 dark:text-gray-400 text-sm italic mb-4">
                                You have already reviewed this product
                              </p>
                              <div className="inline-flex items-center px-4 py-2 bg-green-100 dark:bg-green-800 rounded-lg text-green-600 dark:text-green-400 text-sm">
                                <i className="fas fa-check-circle mr-2"></i>
                                Review submitted
                              </div>
                            </div>
                          )}

                        {session &&
                          session?.user?.userType !== "farmer" &&
                          checkingPurchase && (
                            <div className="text-center">
                              <div className="inline-flex items-center text-gray-500 dark:text-gray-400 text-sm">
                                <i className="fas fa-spinner fa-spin mr-2"></i>
                                Checking purchase history...
                              </div>
                            </div>
                          )}
                        {session && session?.user?.userType === "farmer" && (
                          <div className="text-center">
                            <div className="inline-flex items-center px-4 py-2 bg-blue-50 dark:bg-blue-900/20 rounded-lg text-blue-600 dark:text-blue-400 text-sm">
                              <i className="fas fa-info-circle mr-2"></i>
                              Farmers can view reviews but cannot write them
                            </div>
                          </div>
                        )}
                      </div>

                      {/* Review Summary */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-6 mb-8 shadow-lg border border-gray-100 dark:border-gray-700">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                          {/* Overall Rating Section */}
                          <div className="text-center">
                            <div className="mb-4">
                              {(() => {
                                // Calculate actual average rating from reviews if available
                                const actualReviewCount = reviews?.length || 0;
                                let displayRating = product.averageRating || 0;

                                // If we have reviews but no product rating, calculate from reviews
                                if (
                                  actualReviewCount > 0 &&
                                  (!product.averageRating ||
                                    product.averageRating === 0)
                                ) {
                                  const totalRating = reviews.reduce(
                                    (sum, review) => sum + (review.rating || 0),
                                    0,
                                  );
                                  displayRating =
                                    totalRating / actualReviewCount;
                                }

                                return (
                                  <>
                                    <div className="text-5xl font-bold text-primary-600 dark:text-primary-400 mb-2">
                                      {displayRating.toFixed(1)}
                                    </div>
                                    <StarRating
                                      rating={displayRating}
                                      size="lg"
                                    />
                                    <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg font-medium">
                                      Based on{" "}
                                      <span className="text-primary-600 dark:text-primary-400 font-bold">
                                        {actualReviewCount}
                                      </span>{" "}
                                      {actualReviewCount === 1
                                        ? "review"
                                        : "reviews"}
                                    </p>
                                  </>
                                );
                              })()}
                            </div>

                            {/* Review Quality Indicator */}
                            <div className="mt-4 p-3 bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-900/20 dark:to-blue-900/20 rounded-lg">
                              <div className="flex items-center justify-center space-x-2 text-sm">
                                <i className="fas fa-shield-alt text-green-600"></i>
                                <span className="font-medium text-gray-700 dark:text-gray-300">
                                  {(reviews?.length || 0) > 0
                                    ? `${reviews.length} verified review${reviews.length === 1 ? "" : "s"}`
                                    : "No reviews yet"}
                                </span>
                              </div>
                            </div>
                          </div>

                          {/* Rating Distribution */}
                          <div className="space-y-3">
                            <h4 className="font-semibold text-gray-900 dark:text-white mb-4">
                              Rating Breakdown
                            </h4>
                            {(() => {
                              // Calculate rating distribution
                              const ratingDistribution = {
                                5: 0,
                                4: 0,
                                3: 0,
                                2: 0,
                                1: 0,
                              };
                              if (reviews && reviews.length > 0) {
                                reviews.forEach((review) => {
                                  const rating = Math.floor(review.rating);
                                  if (rating >= 1 && rating <= 5) {
                                    ratingDistribution[rating]++;
                                  }
                                });
                              }
                              const totalReviews = reviews?.length || 0;

                              return [5, 4, 3, 2, 1].map((rating) => {
                                const count = ratingDistribution[rating];
                                const percentage =
                                  totalReviews > 0
                                    ? (count / totalReviews) * 100
                                    : 0;

                                return (
                                  <div
                                    key={rating}
                                    className="flex items-center space-x-3"
                                  >
                                    <span className="text-sm font-medium text-gray-600 dark:text-gray-400 w-12">
                                      {rating} star{rating === 1 ? "" : "s"}
                                    </span>
                                    <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-3 overflow-hidden">
                                      <div
                                        className="bg-gradient-to-r from-yellow-400 to-yellow-500 h-3 rounded-full transition-all duration-500 ease-out"
                                        style={{ width: `${percentage}%` }}
                                      ></div>
                                    </div>
                                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300 w-8 text-right">
                                      {count}
                                    </span>
                                    <span className="text-xs text-gray-500 dark:text-gray-400 w-12 text-right">
                                      {percentage.toFixed(0)}%
                                    </span>
                                  </div>
                                );
                              });
                            })()}

                            {/* Total Reviews Summary */}
                            <div className="mt-4 pt-3 border-t border-gray-200 dark:border-gray-600">
                              <div className="text-center text-sm text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  {(reviews?.length || 0) === 0 &&
                                    "Be the first to review this product!"}
                                  {(reviews?.length || 0) === 1 &&
                                    "1 customer has reviewed this product"}
                                  {(reviews?.length || 0) > 1 &&
                                    `${reviews.length} customers have reviewed this product`}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Enhanced Review Modal */}
                      <EnhancedReviewModal
                        isOpen={showReviewForm}
                        onClose={() => {
                          setShowReviewForm(false);
                          setEditingReview(null);
                          setReviewForm(DEFAULT_REVIEW_FORM);
                        }}
                        product={product}
                        user={session?.user}
                        existingReview={editingReview}
                        onSubmit={handleEnhancedReviewSubmit}
                        isSubmitting={isSubmitting || isUpdating}
                      />

                      {/* Individual Reviews */}
                      <div className="space-y-8">
                        {reviews && reviews.length > 0 ? (
                          reviews.map((review, index) => (
                            <div
                              key={`${review._id}-${review.userId}-${index}`}
                              className="group bg-gradient-to-br from-white via-gray-50 to-white dark:from-gray-800 dark:via-gray-850 dark:to-gray-800 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-800"
                              style={{ animationDelay: `${index * 100}ms` }}
                            >
                              <div className="flex items-start justify-between mb-4">
                                <div className="flex items-center space-x-4">
                                  <div className="relative">
                                    <div className="w-12 h-12 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 dark:from-primary-500 dark:to-primary-700 flex items-center justify-center shadow-lg">
                                      <span className="text-white font-bold text-lg">
                                        {(review.reviewer || "Anonymous")
                                          .charAt(0)
                                          .toUpperCase()}
                                      </span>
                                    </div>
                                    <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white dark:border-gray-800 flex items-center justify-center">
                                      <i className="fas fa-check text-white text-xs"></i>
                                    </div>
                                  </div>
                                  <div className="flex-1">
                                    <div className="flex items-center gap-3 mb-1">
                                      <h4 className="font-semibold text-gray-900 dark:text-white text-lg">
                                        {review.reviewer || "Anonymous"}
                                      </h4>
                                      <span className="px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-xs font-medium rounded-full">
                                        Verified Buyer
                                      </span>
                                    </div>
                                    <div className="flex items-center space-x-3">
                                      <div className="flex items-center space-x-1">
                                        {[1, 2, 3, 4, 5].map((star) => (
                                          <span
                                            key={star}
                                            className={`text-lg transition-all duration-200 ${
                                              star <= review.rating
                                                ? "text-yellow-400 drop-shadow-sm"
                                                : "text-gray-300 dark:text-gray-600"
                                            }`}
                                          >
                                            ★
                                          </span>
                                        ))}
                                      </div>
                                      <span className="text-sm font-medium text-yellow-600 dark:text-yellow-400">
                                        {review.rating}/5
                                      </span>
                                      <span className="text-gray-400">•</span>
                                      <span className="text-sm text-gray-500 dark:text-gray-400 font-medium">
                                        {new Date(
                                          review.createdAt || review.date,
                                        ).toLocaleDateString("en-US", {
                                          year: "numeric",
                                          month: "short",
                                          day: "numeric",
                                        })}
                                      </span>
                                    </div>
                                  </div>
                                </div>
                                <div className="flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                  {/* Show edit/delete buttons only for user's own review */}
                                  {session?.user?.userId === review.userId ? (
                                    <>
                                      <button
                                        onClick={() => {
                                          setEditingReview(review);
                                          setReviewForm({
                                            rating: review.rating,
                                            comment: review.comment,
                                          });
                                          setShowReviewForm(true);
                                        }}
                                        className="p-2 hover:bg-blue-100 dark:hover:bg-blue-900/20 rounded-lg transition-colors"
                                        title="Edit Review"
                                      >
                                        <i className="fas fa-edit text-blue-500 hover:text-blue-600"></i>
                                      </button>
                                      <button
                                        onClick={() =>
                                          handleDeleteReview(review._id)
                                        }
                                        disabled={isDeletingReview}
                                        className="p-2 hover:bg-red-100 dark:hover:bg-red-900/20 rounded-lg transition-colors"
                                        title="Delete Review"
                                      >
                                        <i className="fas fa-trash text-red-500 hover:text-red-600"></i>
                                      </button>
                                    </>
                                  ) : (
                                    <>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-thumbs-up text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-share text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                    </>
                                  )}
                                </div>
                              </div>

                              <div className="relative">
                                <div className="absolute left-0 top-0 w-1 h-full bg-gradient-to-b from-primary-400 to-primary-600 rounded-full opacity-20"></div>
                                <p className="text-gray-700 dark:text-gray-300 leading-relaxed pl-6 text-base">
                                  &ldquo;{review.comment}&rdquo;
                                </p>
                              </div>

                              {/* Review actions footer */}
                              <div className="mt-4 pt-4 border-t border-gray-100 dark:border-gray-700 flex items-center justify-between">
                                <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-heart text-red-400"></i>
                                    <span>Helpful</span>
                                  </span>
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-comment text-blue-400"></i>
                                    <span>Reply</span>
                                  </span>
                                </div>
                                <div className="flex items-center space-x-2">
                                  <div className="flex -space-x-1">
                                    {[1, 2, 3].map((i) => (
                                      <div
                                        key={i}
                                        className="w-6 h-6 rounded-full bg-gray-300 dark:bg-gray-600 border-2 border-white dark:border-gray-800"
                                      ></div>
                                    ))}
                                  </div>
                                  <span className="text-xs text-gray-500">
                                    +2 found helpful
                                  </span>
                                </div>
                              </div>
                            </div>
                          ))
                        ) : (
                          <div className="text-center py-16 bg-gradient-to-br from-gray-50 via-white to-gray-50 dark:from-gray-800 dark:via-gray-900 dark:to-gray-800 rounded-2xl border-2 border-dashed border-gray-200 dark:border-gray-700">
                            <div className="relative inline-block mb-6">
                              <div className="w-20 h-20 bg-gradient-to-br from-primary-100 to-primary-200 dark:from-primary-800 dark:to-primary-900 rounded-full flex items-center justify-center mx-auto shadow-lg">
                                <i className="fas fa-star text-3xl text-primary-500 dark:text-primary-400"></i>
                              </div>
                              <div className="absolute -top-2 -right-2 w-8 h-8 bg-yellow-400 rounded-full flex items-center justify-center animate-bounce">
                                <i className="fas fa-plus text-white text-sm"></i>
                              </div>
                            </div>
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-3">
                              No Reviews Yet
                            </h3>
                            <p className="text-gray-600 dark:text-gray-400 mb-6 max-w-md mx-auto">
                              Be the first to share your experience with this
                              amazing product! Your review helps other customers
                              make informed decisions.
                            </p>
                            {session && hasPurchasedProduct && (
                              <button
                                onClick={() => setShowReviewForm(true)}
                                className="inline-flex items-center bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                              >
                                <i className="fas fa-edit mr-2"></i>
                                Write First Review
                              </button>
                            )}
                          </div>
                        )}

                        {/* Load More Reviews Button */}
                        {hasMoreReviews && (
                          <div className="text-center pt-8">
                            <button
                              onClick={loadMoreReviews}
                              className="group inline-flex items-center bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 text-gray-900 dark:text-white px-8 py-4 rounded-xl font-semibold border-2 border-gray-200 dark:border-gray-700 hover:border-primary-300 dark:hover:border-primary-600 shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                            >
                              <i className="fas fa-chevron-down mr-3 group-hover:animate-bounce"></i>
                              Load More Reviews
                              <span className="ml-3 px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-sm rounded-full">
                                +{Math.min(5, reviews?.length || 0)}
                              </span>
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Farmer Tab */}
                  {activeTab === "farmer" && (
                    <div>
                      <h3 className="text-xl font-semibold mb-6">
                        About the Farmer
                      </h3>
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow-lg">
                        <div className="flex items-start gap-4">
                          <div className="w-16 h-16 rounded-full bg-primary-100 dark:bg-primary-900 flex items-center justify-center flex-shrink-0">
                            <i className="fas fa-user text-2xl text-primary-600 dark:text-primary-400"></i>
                          </div>
                          <div className="flex-1">
                            <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                              {product.farmer?.farmName ||
                                product.farmer?.name ||
                                "Farm Name"}
                            </h4>
                            <p className="text-gray-600 dark:text-gray-400 mb-3">
                              {product.farmer?.email ||
                                "Farmer contact not available"}
                            </p>

                            {product.farmer?.location && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-3">
                                <i className="fas fa-map-marker-alt mr-2"></i>
                                <span>{product.farmer.location}</span>
                              </div>
                            )}

                            {product.farmer?.phone && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                                <i className="fas fa-phone mr-2"></i>
                                <span>{product.farmer.phone}</span>
                              </div>
                            )}

                            <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                              {product.farmer?.description ||
                                "A dedicated farmer committed to providing fresh, quality produce to the community."}
                            </p>

                            <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                              <Link
                                href={`/details?id=${product.farmerId || product.farmer?.id}`}
                                className="inline-flex items-center text-primary-600 hover:text-primary-700 font-medium"
                              >
                                <i className="fas fa-external-link-alt mr-2"></i>
                                View All Products from this Farmer
                              </Link>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Related Products Section - Enhanced */}
              {relatedProducts.length > 0 && (
                <div className="mt-16 bg-gradient-to-br from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 rounded-2xl p-8 shadow-lg border border-gray-100 dark:border-gray-700">
                  {/* Section Header */}
                  <div className="text-center mb-10">
                    <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full mb-4 shadow-lg">
                      <i className="fas fa-box-open text-2xl text-white"></i>
                    </div>
                    <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-3">
                      Related Products
                    </h3>
                    <p className="text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
                      Discover similar high-quality products from our trusted
                      farmers. Each item is carefully selected to meet our
                      quality standards.
                    </p>
                    <div className="mt-4 flex items-center justify-center space-x-2 text-sm text-gray-500 dark:text-gray-400">
                      <i className="fas fa-leaf text-green-500"></i>
                      <span>{relatedProducts.length} products found</span>
                      <span>•</span>
                      <i className="fas fa-truck text-blue-500"></i>
                      <span>Fast delivery available</span>
                    </div>
                  </div>

                  {/* Enhanced Product Grid */}
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                    {relatedProducts.map((relatedProduct, index) => (
                      <div
                        key={relatedProduct._id}
                        className="group transform transition-all duration-300 hover:-translate-y-2"
                        style={{ animationDelay: `${index * 100}ms` }}
                      >
                        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-600 overflow-hidden">
                          {/* Product Image */}
                          <div className="relative aspect-square overflow-hidden">
                            <Image
                              src={
                                relatedProduct.image ||
                                relatedProduct.images?.[0] ||
                                "/placeholder-image.jpg"
                              }
                              alt={relatedProduct.name}
                              width={300}
                              height={300}
                              className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                            />

                            {/* Product Badges */}
                            <div className="absolute top-3 left-3 flex flex-col space-y-2">
                              {relatedProduct.isOrganic && (
                                <span className="bg-green-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                  Organic
                                </span>
                              )}
                              {relatedProduct.isFresh && (
                                <span className="bg-blue-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                  Fresh
                                </span>
                              )}
                            </div>

                            {/* Quick Actions Overlay */}
                            <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
                              <Link
                                href={`/details?id=${relatedProduct._id}`}
                                className="bg-white text-gray-900 px-6 py-2 rounded-full font-medium hover:bg-gray-100 transition-colors transform hover:scale-105"
                              >
                                View Details
                              </Link>
                            </div>
                          </div>

                          {/* Product Info */}
                          <div className="p-6">
                            <div className="mb-3">
                              <h4 className="font-bold text-lg text-gray-900 dark:text-white line-clamp-1 group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                                {relatedProduct.name}
                              </h4>
                              <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-1">
                                by{" "}
                                {relatedProduct.farmer?.name ||
                                  relatedProduct.farmer?.farmName ||
                                  "Unknown Farmer"}
                              </p>
                            </div>

                            {/* Rating */}
                            <div className="flex items-center space-x-1 mb-3">
                              <StarRating
                                rating={relatedProduct.averageRating || 0}
                                size="sm"
                              />
                              <span className="text-xs text-gray-500 dark:text-gray-400">
                                (
                                {relatedProduct.reviewCount ||
                                  relatedProduct.totalReviews ||
                                  0}
                                )
                              </span>
                            </div>

                            {/* Price and Stock */}
                            <div className="flex items-center justify-between mb-4">
                              <div>
                                <span className="text-xl font-bold text-primary-600 dark:text-primary-400">
                                  {formatPrice(relatedProduct.price || 0)}
                                </span>
                                <span className="text-sm text-gray-500 dark:text-gray-400">
                                  /{relatedProduct.unit || "kg"}
                                </span>
                              </div>
                              <div className="text-right">
                                <div
                                  className={`text-xs font-medium ${
                                    (relatedProduct.stock || 0) > 0
                                      ? "text-green-600 dark:text-green-400"
                                      : "text-red-600 dark:text-red-400"
                                  }`}
                                >
                                  {(relatedProduct.stock || 0) > 0
                                    ? `${relatedProduct.stock} ${relatedProduct.unit || "kg"} left`
                                    : "Out of stock"}
                                </div>
                              </div>
                            </div>

                            {/* Action Button */}
                            <Link
                              href={`/details?id=${relatedProduct._id}`}
                              className="w-full bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white py-2.5 px-4 rounded-lg font-medium transition-all duration-200 text-center block group-hover:shadow-lg transform group-hover:scale-[1.02]"
                            >
                              <i className="fas fa-eye mr-2"></i>
                              View Product
                            </Link>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Browse More Section */}
                  <div className="mt-12 text-center">
                    <div className="border-t border-gray-200 dark:border-gray-700 pt-8">
                      <p className="text-gray-600 dark:text-gray-400 mb-4">
                        Looking for more products?
                      </p>
                      <Link
                        href="/products"
                        className="inline-flex items-center bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                      >
                        <i className="fas fa-shopping-bag mr-2"></i>
                        Browse All Products
                        <i className="fas fa-arrow-right ml-2"></i>
                      </Link>
                    </div>
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import Link from "next/link";
import Footer from "@/components/Footer";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const { updateOrderStatus, updating } = useOrderStatusUpdate();

  // Use React Query hook for orders data
  const {
    orders,
    isLoading: loading,
    error,
    isRefetching: refreshing,
    refetch: refetchOrders,
    refreshOrders,
  } = useFarmerOrders();

  // Local UI state
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed");
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  const ordersPerPage = viewMode === "compact" ? 20 : 10;

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Enhanced file download
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email",
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Memoized filtered orders
  const filteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Reset page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [filteredOrders]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshOrders();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshOrders]);

  // Enhanced single order status update using the hook
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      await updateOrderStatus(orderId, newStatus, {
        estimatedDeliveryDate:
          newStatus === "shipped"
            ? (() => {
                const date = new Date();
                date.setDate(date.getDate() + 3);
                return date.toISOString();
              })()
            : undefined,
      });

      const successMessages = {
        confirmed: "Order confirmed! Dashboard updated automatically.",
        shipped: "Order marked as shipped! Dashboard updated automatically.",
        delivered: "Order completed! Dashboard updated automatically.",
        cancelled: "Order cancelled! Dashboard updated automatically.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully! Dashboard updated automatically.`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    }
  };

  // Enhanced refresh handler
  const handleRefresh = async () => {
    await refetchOrders();
    addNotification("Orders refreshed successfully!", "success");
  };

  // Search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Export functionality
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  // Enhanced Farmer Orders Loading Skeleton
  // Enhanced Farmer Orders Loading Skeleton
  // Enhanced Farmer Orders Loading Skeleton
  // Enhanced Farmer Orders Loading Skeleton
  const FarmerOrdersLoadingSkeleton = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-green-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900">
      {/* Custom CSS animations for farmer orders */}
      <style jsx>{`
        @keyframes orderPulse {
          0%,
          100% {
            opacity: 0.7;
          }
          50% {
            opacity: 1;
          }
        }

        @keyframes orderBounce {
          0%,
          100% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-5px);
          }
        }

        @keyframes shimmer {
          0% {
            transform: translateX(-100%);
          }
          100% {
            transform: translateX(100%);
          }
        }

        @keyframes statusWave {
          0%,
          100% {
            transform: scale(1);
          }
          50% {
            transform: scale(1.1);
          }
        }

        @keyframes customerFloat {
          0%,
          100% {
            transform: translateY(0) rotate(0deg);
          }
          50% {
            transform: translateY(-8px) rotate(1deg);
          }
        }

        .animate-order-pulse {
          animation: orderPulse 2s ease-in-out infinite;
        }

        .animate-order-bounce {
          animation: orderBounce 2.5s ease-in-out infinite;
        }

        .animate-shimmer {
          animation: shimmer 2s infinite;
        }

        .animate-status-wave {
          animation: statusWave 2s ease-in-out infinite;
        }

        .animate-customer-float {
          animation: customerFloat 3s ease-in-out infinite;
        }
      `}</style>

      <div className="container mx-auto px-4 py-8">
        {/* Header Section */}
        <div className="mb-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6">
            <div>
              <div className="h-10 bg-gradient-to-r from-blue-200 to-green-200 dark:from-blue-700 dark:to-green-700 rounded-xl w-96 animate-order-pulse mb-3"></div>
              <div className="h-6 bg-gray-200 dark:bg-gray-600 rounded-lg w-80 animate-pulse"></div>
            </div>
            <div className="flex gap-3">
              <div className="h-10 bg-green-200 dark:bg-green-700 rounded-lg w-32 animate-pulse"></div>
              <div className="h-10 bg-blue-200 dark:bg-blue-700 rounded-lg w-28 animate-pulse"></div>
            </div>
          </div>
        </div>

        {/* Stats Cards Row */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          {[
            { color: "blue", delay: 0 },
            { color: "green", delay: 100 },
            { color: "yellow", delay: 200 },
            { color: "purple", delay: 300 },
          ].map((stat, i) => (
            <div
              key={i}
              className="relative bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg border border-gray-200 dark:border-gray-700 animate-order-bounce overflow-hidden"
              style={{ animationDelay: `${stat.delay}ms` }}
            >
              {/* Shimmer effect */}
              <div className="absolute inset-0 -translate-x-full bg-gradient-to-r from-transparent via-white/20 dark:via-gray-600/20 to-transparent animate-shimmer"></div>

              {/* Icon */}
              <div
                className={`w-12 h-12 bg-gradient-to-br from-${stat.color}-200 to-${stat.color}-300 dark:from-${stat.color}-700 dark:to-${stat.color}-600 rounded-full animate-status-wave mb-4`}
              ></div>

              {/* Value */}
              <div className="h-8 bg-gradient-to-r from-gray-300 to-gray-400 dark:from-gray-600 dark:to-gray-500 rounded-lg w-16 animate-order-pulse mb-2"></div>

              {/* Label */}
              <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-24 animate-pulse"></div>
            </div>
          ))}
        </div>

        {/* Filters and Controls */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-8 border border-gray-200 dark:border-gray-700">
          <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
            {/* Search Bar */}
            <div className="flex-1 max-w-md">
              <div className="h-10 bg-gray-100 dark:bg-gray-700 rounded-lg animate-pulse"></div>
            </div>

            {/* Filter Controls */}
            <div className="flex flex-wrap gap-3">
              {[...Array(5)].map((_, i) => (
                <div
                  key={i}
                  className="h-10 bg-gray-100 dark:bg-gray-700 rounded-lg w-24 animate-pulse"
                  style={{ animationDelay: `${i * 50}ms` }}
                ></div>
              ))}
            </div>
          </div>
        </div>

        {/* Orders List */}
        <div className="space-y-6">
          {[...Array(8)].map((_, i) => (
            <div
              key={i}
              className="relative bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 overflow-hidden"
              style={{ animationDelay: `${i * 150}ms` }}
            >
              {/* Shimmer effect */}
              <div className="absolute inset-0 -translate-x-full bg-gradient-to-r from-transparent via-white/20 dark:via-gray-600/20 to-transparent animate-shimmer"></div>

              {/* Order Header */}
              <div className="bg-gradient-to-r from-blue-50 via-green-50 to-blue-50 dark:from-gray-700 dark:via-gray-600 dark:to-gray-700 p-6 border-b border-gray-200 dark:border-gray-600">
                <div className="flex flex-col sm:flex-row justify-between items-start gap-4">
                  <div className="flex-1">
                    {/* Order ID and Customer */}
                    <div className="flex items-center space-x-4 mb-4">
                      <div className="w-12 h-12 bg-gradient-to-br from-blue-200 to-green-200 dark:from-blue-700 dark:to-green-700 rounded-full animate-customer-float"></div>
                      <div>
                        <div className="h-6 bg-gradient-to-r from-gray-300 to-gray-400 dark:from-gray-600 dark:to-gray-500 rounded w-32 animate-order-pulse mb-2"></div>
                        <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-48 animate-pulse"></div>
                      </div>
                    </div>

                    {/* Order Meta Info */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                      <div>
                        <div className="h-3 bg-gray-100 dark:bg-gray-700 rounded w-16 animate-pulse mb-1"></div>
                        <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-20 animate-pulse"></div>
                      </div>
                      <div>
                        <div className="h-3 bg-gray-100 dark:bg-gray-700 rounded w-12 animate-pulse mb-1"></div>
                        <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-16 animate-pulse"></div>
                      </div>
                      <div>
                        <div className="h-3 bg-gray-100 dark:bg-gray-700 rounded w-14 animate-pulse mb-1"></div>
                        <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-18 animate-pulse"></div>
                      </div>
                      <div>
                        <div className="h-3 bg-gray-100 dark:bg-gray-700 rounded w-10 animate-pulse mb-1"></div>
                        <div className="h-4 bg-gray-200 dark:bg-gray-600 rounded w-14 animate-pulse"></div>
                      </div>
                    </div>
                  </div>

                  {/* Order Status and Actions */}
                  <div className="flex flex-col items-end gap-3">
                    <div className="h-8 bg-gradient-to-r from-green-200 to-blue-200 dark:from-green-700 dark:to-blue-700 rounded-full w-28 animate-status-wave"></div>
                    <div className="h-8 bg-gradient-to-r from-gray-300 to-gray-400 dark:from-gray-600 dark:to-gray-500 rounded-lg w-20 animate-order-pulse"></div>
                  </div>
                </div>
              </div>

              {/* Order Content */}
              <div className="p-6">
                {/* Order Items Preview */}
                <div className="mb-6">
                  <div className="h-5 bg-gradient-to-r from-gray-300 to-gray-400 dark:from-gray-600 dark:to-gray-500 rounded w-32 animate-pulse mb-4"></div>

                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {[...Array(3)].map((_, itemIndex) => (
                      <div
                        key={itemIndex}
                        className="bg-gray-50 dark:bg-gray-700 rounded-lg p-3 border border-gray-200 dark:border-gray-600"
