                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import Link from "next/link";
import Footer from "@/components/Footer";
import FarmerOrdersLoadingSkeleton from "@/components/FarmerOrdersLoadingSkeleton";
import { debounce } from "@/utils/debounce";
import { useOrderStatusUpdate } from "@/hooks/useOrderStatusUpdate";
import { useFarmerOrders } from "@/hooks/useFarmerOrders";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const { updateOrderStatus, updating } = useOrderStatusUpdate();

  // Use React Query hook for orders data
  const {
    orders,
    isLoading: loading,
    error,
    isRefetching: refreshing,
    refetch: refetchOrders,
    refreshOrders,
  } = useFarmerOrders();

  // Local UI state
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed");
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  const ordersPerPage = viewMode === "compact" ? 20 : 10;

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Enhanced file download
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email",
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Memoized filtered orders
  const filteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Reset page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [filteredOrders]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshOrders();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshOrders]);

  // Enhanced single order status update using the hook
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      await updateOrderStatus(orderId, newStatus, {
        estimatedDeliveryDate:
          newStatus === "shipped"
            ? (() => {
                const date = new Date();
                date.setDate(date.getDate() + 3);
                return date.toISOString();
              })()
            : undefined,
      });

      const successMessages = {
        confirmed: "Order confirmed! Dashboard updated automatically.",
        shipped: "Order marked as shipped! Dashboard updated automatically.",
        delivered: "Order completed! Dashboard updated automatically.",
        cancelled: "Order cancelled! Dashboard updated automatically.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully! Dashboard updated automatically.`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    }
  };

  // Enhanced refresh handler
  const handleRefresh = async () => {
    await refetchOrders();
    addNotification("Orders refreshed successfully!", "success");
  };

  // Search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Export functionality
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return <FarmerOrdersLoadingSkeleton />;
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      {/* Notification System */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`px-6 py-4 rounded-lg shadow-lg text-white transform transition-all duration-500 ${
              notification.type === "success"
                ? "bg-green-600"
                : notification.type === "error"
                  ? "bg-red-600"
                  : notification.type === "warning"
                    ? "bg-yellow-600"
                    : "bg-blue-600"
            }`}
          >
            <div className="flex items-center">
              <i
                className={`fas ${
                  notification.type === "success"
                    ? "fa-check-circle"
                    : notification.type === "error"
                      ? "fa-exclamation-circle"
                      : notification.type === "warning"
                        ? "fa-exclamation-triangle"
                        : "fa-info-circle"
                } mr-2`}
              ></i>
              {notification.message}
            </div>
          </div>
        ))}
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center">
          <div className="bg-white dark:bg-gray-800 rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Export Orders
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              {selectedOrders.length > 0
                ? `Export ${selectedOrders.length} selected orders`
                : `Export all ${filteredOrders.length} filtered orders`}
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => exportOrders("csv")}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-csv mr-2"></i>
                Export as CSV
              </button>
              <button
                onClick={() => exportOrders("json")}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-code mr-2"></i>
                Export as JSON
              </button>
            </div>
            <button
              onClick={() => setShowExportModal(false)}
              className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg font-medium transition"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Enhanced Breadcrumb with Real-time Indicators */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <nav className="flex" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-home mr-1"></i>Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li>
                  <Link
                    href="/manage"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-cog mr-1"></i>Manage
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  <i className="fas fa-chart-line mr-1"></i>Advanced Order
                  Management
                </li>
              </ol>
            </nav>

            {/* Real-time Status Indicator */}
            <div className="flex items-center space-x-2">
              <div
                className={`w-3 h-3 rounded-full ${autoRefresh ? "bg-green-500 animate-pulse" : "bg-gray-400"}`}
              ></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {autoRefresh ? "Live" : "Static"}
              </span>
            </div>
          </div>
        </div>

        {/* Enhanced Page Header with Quick Actions */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-green-600 to-blue-600 p-4 rounded-2xl">
                <i className="fas fa-chart-line text-white text-2xl"></i>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Advanced Order Management
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Comprehensive order tracking and analytics dashboard
                </p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className="text-sm text-gray-500">
                    Last updated: {new Date().toLocaleTimeString()}
                  </span>
                  {autoRefresh && (
                    <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                      Auto-refresh enabled
                    </span>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6 lg:mt-0 flex flex-wrap gap-3">
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`inline-flex items-center px-4 py-3 rounded-lg font-medium transition ${
                  autoRefresh
                    ? "bg-green-600 hover:bg-green-700 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600"
                }`}
              >
                <i
                  className={`fas ${autoRefresh ? "fa-pause" : "fa-play"} mr-2`}
                ></i>
                {autoRefresh ? "Pause" : "Auto"} Refresh
              </button>

              <button
                onClick={() => setShowExportModal(true)}
                className="inline-flex items-center px-4 py-3 bg-orange-600 hover:bg-orange-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-download mr-2"></i>
                Export
              </button>

              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                ></i>
                Refresh
              </button>

              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Enhanced Order Summary Cards with Animations */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            {Object.entries(orderSummary).map(([key, value], index) => {
              const config = {
                total: {
                  icon: "fa-shopping-cart",
                  color: "gray",
                  label: "Total",
                },
                pending: {
                  icon: "fa-clock",
                  color: "yellow",
                  label: "Pending",
                },
                confirmed: {
                  icon: "fa-check",
                  color: "blue",
                  label: "Confirmed",
                },
                shipped: {
                  icon: "fa-truck",
                  color: "purple",
                  label: "Shipped",
                },
                delivered: {
                  icon: "fa-check-circle",
                  color: "green",
                  label: "Delivered",
                },
                cancelled: {
                  icon: "fa-times-circle",
                  color: "red",
                  label: "Cancelled",
                },
              }[key];

              return (
                <div
                  key={key}
                  className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 transform hover:scale-105 transition-transform duration-200 cursor-pointer"
                  onClick={() =>
                    setStatusFilter(
                      key === "total"
                        ? "All Orders"
                        : config.label.toLowerCase(),
                    )
                  }
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <div className="flex items-center">
                    <div
                      className={`p-2 bg-${config.color}-100 dark:bg-${config.color}-900 rounded-lg`}
                    >
                      <i
                        className={`fas ${config.icon} text-${config.color}-600 dark:text-${config.color}-300`}
                      ></i>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                        {config.label}
                      </p>
                      <p
                        className={`text-2xl font-bold text-${config.color}-600 dark:text-${config.color}-400`}
                      >
                        {value}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Enhanced Filters and Controls */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 lg:mb-0">
                <i className="fas fa-filter mr-2"></i>
                Advanced Filters & Controls
              </h3>

              {/* View Mode Toggle */}
              <div className="flex space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {["detailed", "compact"].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-4 py-2 rounded-md text-sm font-medium transition ${
                      viewMode === mode
                        ? "bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow"
                        : "text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white"
                    }`}
                  >
                    <i
                      className={`fas ${
                        mode === "detailed"
                          ? "fa-list"
                          : mode === "compact"
                            ? "fa-th-list"
                            : ""
                      } mr-1`}
                    ></i>
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4">
              {/* Search */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    placeholder="Search by customer, email, or ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Status Filter
                </label>
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateProductInCache,
    updateBulkProductsInCache,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        router.push("/");
        return;
      }
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization
  const analytics = useMemo(() => {
    const validOrders = orders.filter(
      (order) =>
        order.status !== ORDER_STATUS.CANCELLED &&
        order.status !== ORDER_STATUS.RETURNED,
    );

    const totalProducts = products.length;
    const activeProducts = products.filter(
      (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
    ).length;

    const totalOrders = orders.length;
    const pendingOrders = orders.filter(
      (o) => o.status === ORDER_STATUS.PENDING,
    ).length;

    const totalRevenue = validOrders.reduce(
      (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
      0,
    );

    const now = new Date();
    const thisMonthValidOrders = validOrders.filter((order) => {
      const orderDate = new Date(order.createdAt);
      return (
        orderDate.getMonth() === now.getMonth() &&
        orderDate.getFullYear() === now.getFullYear()
      );
    });

    const averageOrderValue =
      validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

    const recentOrders = orders.slice(0, 5).map((order) => ({
      _id: order._id,
      id: order.id,
      customerName: order.customerName,
      total: order.total || order.farmerSubtotal,
      createdAt: order.createdAt,
      status: order.status,
      items: order.items?.length || 0,
    }));

    return {
      totalProducts,
      activeProducts,
      totalOrders,
      pendingOrders,
      totalRevenue,
      thisMonthOrders: thisMonthValidOrders.length,
      averageOrderValue,
      lowStockProducts: products.filter((p) => p.stock <= 10).length,
      recentOrders,
    };
  }, [products.length, orders.length]);

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = [...products];

    if (filters.search) {
      const searchRegex = new RegExp(filters.search.trim(), "i");
      filtered = filtered.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description || "") ||
          searchRegex.test(product.category || ""),
      );
    }

    if (filters.category) {
      filtered = filtered.filter(
        (product) =>
          product.category?.toLowerCase() === filters.category.toLowerCase(),
      );
    }

    if (filters.status) {
      switch (filters.status) {
        case PRODUCT_STATUS.ACTIVE:
          filtered = filtered.filter(
            (product) =>
              product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.INACTIVE:
          filtered = filtered.filter(
            (product) => product.status === PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.OUT_OF_STOCK:
          filtered = filtered.filter((product) => product.stock === 0);
          break;
        case PRODUCT_STATUS.LOW_STOCK:
          filtered = filtered.filter(
            (product) => product.stock > 0 && product.stock <= 5,
          );
          break;
      }
    }

    if (filters.sort) {
      const [key, order] = filters.sort.split("-");
      filtered.sort((a, b) => {
        let valueA, valueB;

        switch (key) {
          case "price":
            valueA = a.price || 0;
            valueB = b.price || 0;
            break;
          case "name":
            valueA = a.name || "";
            valueB = b.name || "";
            return order === "asc"
              ? valueA.localeCompare(valueB)
              : valueB.localeCompare(valueA);
          case "stock":
            valueA = a.stock || 0;
            valueB = b.stock || 0;
            break;
          case "date":
            valueA = new Date(a.createdAt || 0);
            valueB = new Date(b.createdAt || 0);
            break;
          default:
            return 0;
        }

        return order === "asc" ? valueA - valueB : valueB - valueA;
      });
    }

    return filtered;
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      console.log("handleStatusToggle called with:", {
        productId,
        currentStatus,
        type: typeof currentStatus,
      });

      // Normalize the current status to lowercase for comparison
      const normalizedCurrentStatus = currentStatus?.toLowerCase();
      const normalizedActiveStatus = PRODUCT_STATUS.ACTIVE.toLowerCase();

      const newStatus =
        normalizedCurrentStatus === normalizedActiveStatus
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;

      console.log("Status toggle:", {
        currentStatus,
        normalizedCurrentStatus,
        newStatus,
        comparison: normalizedCurrentStatus === normalizedActiveStatus,
      });

      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        console.log("Sending status update:", { productId, status: newStatus });

        // Use the same API service pattern as bulk update for consistency
        const { apiService } = await import("@/lib/api-service");
        const result = await apiService.bulkUpdateProducts(
          [productId], // Single product as array
          { status: newStatus },
        );

        console.log("API response:", result);

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        // Update the React Query cache using the same pattern as bulk update
        if (updateBulkProductsInCache) {
          updateBulkProductsInCache([productId], { status: newStatus });
        }

        // Dispatch custom event to notify products page of status change
        window.dispatchEvent(
          new CustomEvent("productStatusUpdated", {
            detail: {
              productId: productId,
              newStatus: newStatus,
              timestamp: Date.now(),
            },
          }),
        );

        // Also set localStorage flag for cross-tab communication
        localStorage.setItem(
          "productStatusUpdated",
          JSON.stringify({
            productId: productId,
            newStatus: newStatus,
            timestamp: Date.now(),
          }),
        );

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [updateBulkProductsInCache],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        "⚠️ Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        "• The product listing\n" +
        "• All associated data\n" +
        "• Product from any pending orders";

      if (!confirm(confirmMessage)) return;

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              "❌ Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert("❌ Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        await refetchDashboard();
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(`❌ Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update functions
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-US", {
    return new Intl.NumberFormat("en-US", {
      currency: "USD",
      minimumFractionDigits: 2,
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const commonProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
    updateBulkProductsInCache,
  };

  const productProps = {
    ...commonProps,
    paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
  };

  // Loading state
  // Loading state
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            Loading dashboard...
          </p>
        </div>
      </div>
    );
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            Loading dashboard...
          </p>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            {error.message}
          </p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <DashboardHeader {...commonProps} />

        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...commonProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...commonProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...commonProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...commonProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import ManagePageLoadingSkeleton from "@/components/ManagePageLoadingSkeleton";
import { useDashboardData } from "@/hooks/useDashboardData";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateProductInCache,
    updateBulkProductsInCache,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        router.push("/");
        return;
      }
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization
  const analytics = useMemo(() => {
    const validOrders = orders.filter(
      (order) =>
        order.status !== ORDER_STATUS.CANCELLED &&
        order.status !== ORDER_STATUS.RETURNED,
    );

    const totalProducts = products.length;
    const activeProducts = products.filter(
      (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
    ).length;

    const totalOrders = orders.length;
    const pendingOrders = orders.filter(
      (o) => o.status === ORDER_STATUS.PENDING,
    ).length;

    const totalRevenue = validOrders.reduce(
      (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
      0,
    );

    const now = new Date();
    const thisMonthValidOrders = validOrders.filter((order) => {
      const orderDate = new Date(order.createdAt);
      return (
        orderDate.getMonth() === now.getMonth() &&
        orderDate.getFullYear() === now.getFullYear()
      );
    });

    const averageOrderValue =
      validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

    const recentOrders = orders.slice(0, 5).map((order) => ({
      _id: order._id,
      id: order.id,
      customerName: order.customerName,
      total: order.total || order.farmerSubtotal,
      createdAt: order.createdAt,
      status: order.status,
      items: order.items?.length || 0,
    }));

    return {
      totalProducts,
      activeProducts,
      totalOrders,
      pendingOrders,
      totalRevenue,
      thisMonthOrders: thisMonthValidOrders.length,
      averageOrderValue,
      lowStockProducts: products.filter((p) => p.stock <= 10).length,
      recentOrders,
    };
  }, [products.length, orders.length]);

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = [...products];

    if (filters.search) {
      const searchRegex = new RegExp(filters.search.trim(), "i");
      filtered = filtered.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description || "") ||
          searchRegex.test(product.category || ""),
      );
    }

    if (filters.category) {
      filtered = filtered.filter(
        (product) =>
          product.category?.toLowerCase() === filters.category.toLowerCase(),
      );
    }

    if (filters.status) {
      switch (filters.status) {
        case PRODUCT_STATUS.ACTIVE:
          filtered = filtered.filter(
            (product) =>
              product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.INACTIVE:
          filtered = filtered.filter(
            (product) => product.status === PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.OUT_OF_STOCK:
          filtered = filtered.filter((product) => product.stock === 0);
          break;
        case PRODUCT_STATUS.LOW_STOCK:
          filtered = filtered.filter(
            (product) => product.stock > 0 && product.stock <= 5,
          );
          break;
      }
    }

    if (filters.sort) {
      const [key, order] = filters.sort.split("-");
      filtered.sort((a, b) => {
        let valueA, valueB;

        switch (key) {
          case "price":
            valueA = a.price || 0;
            valueB = b.price || 0;
            break;
          case "name":
            valueA = a.name || "";
            valueB = b.name || "";
            return order === "asc"
              ? valueA.localeCompare(valueB)
              : valueB.localeCompare(valueA);
          case "stock":
            valueA = a.stock || 0;
            valueB = b.stock || 0;
            break;
          case "date":
            valueA = new Date(a.createdAt || 0);
            valueB = new Date(b.createdAt || 0);
            break;
          default:
            return 0;
        }

        return order === "asc" ? valueA - valueB : valueB - valueA;
      });
    }

    return filtered;
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      console.log("handleStatusToggle called with:", {
        productId,
        currentStatus,
        type: typeof currentStatus,
      });

      // Normalize the current status to lowercase for comparison
      const normalizedCurrentStatus = currentStatus?.toLowerCase();
      const normalizedActiveStatus = PRODUCT_STATUS.ACTIVE.toLowerCase();

      const newStatus =
        normalizedCurrentStatus === normalizedActiveStatus
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;

      console.log("Status toggle:", {
        currentStatus,
        normalizedCurrentStatus,
        newStatus,
        comparison: normalizedCurrentStatus === normalizedActiveStatus,
      });

      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        console.log("Sending status update:", { productId, status: newStatus });

        // Use the same API service pattern as bulk update for consistency
        const { apiService } = await import("@/lib/api-service");
        const result = await apiService.bulkUpdateProducts(
          [productId], // Single product as array
          { status: newStatus },
        );

        console.log("API response:", result);

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        // Update the React Query cache using the same pattern as bulk update
        if (updateBulkProductsInCache) {
          updateBulkProductsInCache([productId], { status: newStatus });
        }

        // Dispatch custom event to notify products page of status change
        window.dispatchEvent(
          new CustomEvent("productStatusUpdated", {
            detail: {
              productId: productId,
              newStatus: newStatus,
              timestamp: Date.now(),
            },
          }),
        );

        // Also set localStorage flag for cross-tab communication
        localStorage.setItem(
          "productStatusUpdated",
          JSON.stringify({
            productId: productId,
            newStatus: newStatus,
            timestamp: Date.now(),
          }),
        );

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [updateBulkProductsInCache],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        "⚠️ Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        "• The product listing\n" +
        "• All associated data\n" +
        "• Product from any pending orders";

      if (!confirm(confirmMessage)) return;

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              "❌ Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert("❌ Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        await refetchDashboard();
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(`❌ Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update functions
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const commonProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
    updateBulkProductsInCache,
  };

  const productProps = {
    ...commonProps,
    paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
  };

  // Loading state - Use dedicated manage page skeleton
  if (isLoading || status === "loading") {
    return <ManagePageLoadingSkeleton />;
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            {error.message}
          </p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <DashboardHeader {...commonProps} />

        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...commonProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...commonProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...commonProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...commonProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

// Simple script to check the review-product mismatch
const { MongoClient } = require('mongodb');

async function checkMismatch() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Checking product-review mismatch...\n');

  // Get all products
  const products = await db.collection('products').find({}).toArray();
  console.log(` Total products: ${products.length}`);
  console.log('Product IDs:', products.slice(0, 5).map(p => p._id.toString()));

  // Get all reviews  
  const reviews = await db.collection('reviews').find({}).toArray();
  console.log(`\n Total reviews: ${reviews.length}`);
  console.log('Review product IDs:', reviews.map(r => r.productId));

  // Check for matches
  const productIds = products.map(p => p._id.toString());
  const reviewProductIds = reviews.map(r => r.productId);

  const matches = productIds.filter(pid => reviewProductIds.includes(pid));
  console.log(`\n✅ Matching product-review pairs: ${matches.length}`);

  if (matches.length > 0) {
    console.log('Matched IDs:', matches);

    // Show which products have reviews
    for (const matchId of matches) {
      const product = products.find(p => p._id.toString() === matchId);
      const productReviews = reviews.filter(r => r.productId === matchId);
      console.log(`- ${product.name}: ${productReviews.length} reviews`);
    }
  } else {
    console.log('\n❌ No matches found!');
    console.log('This means the existing reviews belong to products that no longer exist');
    console.log('or have different IDs than the current products in the database');
  }

  await client.close();
}

checkMismatch().catch(console.error);

import { useState, useEffect, useMemo } from "react";
import StarRating from "@/components/StarRating";

export default function EnhancedReviewModal({
  isOpen,
  onClose,
  product,
  user,
  existingReview = null,
  onSubmit,
  isSubmitting = false,
}) {
  const [formData, setFormData] = useState({
    rating: 5,
    title: "",
    comment: "",
    pros: "",
    cons: "",
    wouldRecommend: true,
    isAnonymous: false,
    tags: [],
  });

  const [currentStep, setCurrentStep] = useState(1);
  const [errors, setErrors] = useState({});
  const [selectedTags, setSelectedTags] = useState([]);
  const [isVisible, setIsVisible] = useState(false);

  // Predefined tags for different product categories
  const availableTags = useMemo(() => {
    const categoryTags = {
      vegetables: [
        "Fresh",
        "Organic",
        "Crispy",
        "Sweet",
        "Bitter",
        "Tender",
        "Juicy",
      ],
      fruits: [
        "Sweet",
        "Sour",
        "Juicy",
        "Fresh",
        "Ripe",
        "Organic",
        "Crispy",
        "Soft",
      ],
      dairy: ["Creamy", "Rich", "Fresh", "Smooth", "Thick", "Natural"],
      meat: ["Tender", "Fresh", "Lean", "Juicy", "Well-marbled", "Flavorful"],
      grains: ["Fresh", "Organic", "High-quality", "Clean", "Aromatic"],
    };

    const category = product?.category?.toLowerCase() || "general";
    return (
      categoryTags[category] || [
        "High Quality",
        "Fresh",
        "Good Value",
        "Fast Delivery",
        "Well Packaged",
        "As Described",
        "Organic",
        "Natural",
        "Healthy",
        "Delicious",
      ]
    );
  }, [product?.category]);

  // Animation effect
  useEffect(() => {
    if (isOpen) {
      setIsVisible(true);
      document.body.style.overflow = "hidden";
    } else {
      setIsVisible(false);
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  // Pre-fill form if editing existing review
  useEffect(() => {
    if (existingReview) {
      setFormData({
        rating: existingReview.rating || 5,
        title: existingReview.title || "",
        comment: existingReview.comment || "",
        pros: existingReview.pros || "",
        cons: existingReview.cons || "",
        wouldRecommend: existingReview.wouldRecommend ?? true,
        isAnonymous: existingReview.isAnonymous || false,
        tags: existingReview.tags || [],
      });
      setSelectedTags(existingReview.tags || []);
    } else {
      // Reset form for new review
      setFormData({
        rating: 5,
        title: "",
        comment: "",
        pros: "",
        cons: "",
        wouldRecommend: true,
        isAnonymous: false,
        tags: [],
      });
      setSelectedTags([]);
      setCurrentStep(1);
    }
  }, [existingReview, isOpen]);

  const validateStep = (step) => {
    const newErrors = {};

    switch (step) {
      case 1:
        if (!formData.rating || formData.rating < 1 || formData.rating > 5) {
          newErrors.rating = "Please select a rating between 1 and 5 stars";
        }
        if (!formData.title.trim()) {
          newErrors.title = "Please provide a title for your review";
        } else if (formData.title.length < 5) {
          newErrors.title = "Title must be at least 5 characters long";
        } else if (formData.title.length > 100) {
          newErrors.title = "Title must be less than 100 characters";
        }
        break;

      case 2:
        if (!formData.comment.trim()) {
          newErrors.comment = "Please write your detailed review";
        } else if (formData.comment.length < 20) {
          newErrors.comment = "Review must be at least 20 characters long";
        } else if (formData.comment.length > 1000) {
          newErrors.comment = "Review must be less than 1000 characters";
        }
        break;

      case 3:
        // Optional step - no validation required
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      setCurrentStep((prev) => Math.min(prev + 1, 3));
    }
  };

  const handlePrevious = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  const handleTagToggle = (tag) => {
    const newTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];

    setSelectedTags(newTags);
    setFormData((prev) => ({ ...prev, tags: newTags }));
  };

  const handleSubmit = async () => {
    if (!validateStep(1) || !validateStep(2)) {
      setCurrentStep(1);
      return;
    }

    const reviewData = {
      ...formData,
      tags: selectedTags,
      productId: product?.id || product?._id,
      userId: user?.id || user?._id || user?.userId,
      userName: formData.isAnonymous ? "Anonymous" : user?.name || user?.email,
      userEmail: user?.email,
    };

    await onSubmit(reviewData);
  };

  const getRatingText = (rating) => {
    const texts = {
      1: "Terrible",
      2: "Poor",
      3: "Average",
      4: "Good",
      5: "Excellent",
    };
    return texts[rating] || "Not rated";
  };

  const getRatingColor = (rating) => {
    if (rating <= 2) return "text-red-500";
    if (rating === 3) return "text-yellow-500";
    return "text-green-500";
  };

  if (!isOpen) return null;

  return (
    <div
      className={`fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 transition-all duration-300 ${isVisible ? "opacity-100" : "opacity-0"}`}
    >
      <div
        className={`bg-white dark:bg-gray-900 rounded-3xl w-full max-w-2xl max-h-[95vh] overflow-hidden shadow-2xl border border-gray-200 dark:border-gray-700 transform transition-all duration-300 ${isVisible ? "scale-100 translate-y-0" : "scale-95 translate-y-4"}`}
      >
        {/* Enhanced Header */}
        <div className="relative bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700 text-white p-8">
          <div className="absolute inset-0 bg-black/10"></div>
          <div className="relative z-10">
            <div className="flex justify-between items-start">
              <div className="space-y-2">
                <div className="flex items-center space-x-3">
                  <div className="p-3 bg-white/20 rounded-2xl backdrop-blur-sm">
                    <i className="fas fa-star text-2xl"></i>
                  </div>
                  <div>
                    <h3 className="text-3xl font-bold tracking-tight">
                      {existingReview ? "Edit Review" : "Write a Review"}
                    </h3>
                    <p className="text-blue-100 text-lg">
                      Share your experience with {product?.name}
                    </p>
                  </div>
                </div>

                {/* Progress Steps */}
                <div className="flex items-center space-x-4 mt-6">
                  {[1, 2, 3].map((step) => (
                    <div key={step} className="flex items-center">
                      <div
                        className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold transition-all duration-300 ${
                          currentStep >= step
                            ? "bg-white text-blue-600 shadow-lg"
                            : "bg-white/20 text-white border-2 border-white/30"
                        }`}
                      >
                        {step}
                      </div>
                      {step < 3 && (
                        <div
                          className={`w-12 h-1 mx-2 rounded transition-all duration-300 ${
                            currentStep > step ? "bg-white" : "bg-white/30"
                          }`}
                        ></div>
                      )}
                    </div>
                  ))}
                </div>

                <div className="text-sm text-blue-100 mt-2">
                  Step {currentStep} of 3:{" "}
                  {currentStep === 1
                    ? "Rating & Title"
                    : currentStep === 2
                      ? "Detailed Review"
                      : "Additional Details"}
                </div>
              </div>

              <button
                onClick={onClose}
                className="p-3 hover:bg-white/20 rounded-2xl transition-all duration-200 group"
              >
                <i className="fas fa-times text-xl group-hover:rotate-90 transition-transform duration-200"></i>
              </button>
            </div>
          </div>
        </div>

        {/* Modal Content */}
        <div className="p-8 overflow-y-auto max-h-[calc(95vh-280px)]">
          {/* Step 1: Rating & Title */}
          {currentStep === 1 && (
            <div className="space-y-8">
              {/* Rating Section */}
              <div className="text-center">
                <h4 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
                  How would you rate this product?
                </h4>

                <div className="flex justify-center items-center space-x-4 mb-4">
                  <StarRating
                    rating={formData.rating}
                    onRatingChange={(rating) => {
                      setFormData((prev) => ({ ...prev, rating }));
                      setErrors((prev) => ({ ...prev, rating: null }));
                    }}
                    size="large"
                    interactive={true}
                  />
                  <div className="text-left">
                    <div
                      className={`text-2xl font-bold ${getRatingColor(formData.rating)}`}
                    >
                      {getRatingText(formData.rating)}
                    </div>
                    <div className="text-sm text-gray-500 dark:text-gray-400">
                      {formData.rating}/5 stars
                    </div>
                  </div>
                </div>

                {errors.rating && (
                  <p className="text-red-500 text-sm mt-2">{errors.rating}</p>
                )}
              </div>

              {/* Title Section */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                  Give your review a title
                </label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => {
                    setFormData((prev) => ({ ...prev, title: e.target.value }));
                    setErrors((prev) => ({ ...prev, title: null }));
                  }}
                  placeholder="e.g., Great quality vegetables, fresh and crispy!"
                  className={`w-full px-4 py-3 border-2 rounded-xl text-lg focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-200 dark:bg-gray-800 dark:text-white dark:border-gray-600 ${
                    errors.title ? "border-red-500" : "border-gray-300"
                  }`}
                  maxLength={100}
                />
                <div className="flex justify-between items-center mt-2">
                  {errors.title && (
                    <p className="text-red-500 text-sm">{errors.title}</p>
                  )}
                  <div className="text-sm text-gray-500 dark:text-gray-400 ml-auto">
                    {formData.title.length}/100
                  </div>
                </div>
              </div>

              {/* Recommendation */}
              <div className="bg-gray-50 dark:bg-gray-800 rounded-xl p-6">
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Would you recommend this product?
                </label>
                <div className="flex space-x-4">
                  <button
                    type="button"
                    onClick={() =>
                      setFormData((prev) => ({ ...prev, wouldRecommend: true }))
                    }
                    className={`flex-1 py-3 px-4 rounded-xl font-medium transition-all duration-200 ${
                      formData.wouldRecommend
                        ? "bg-green-600 text-white shadow-lg"
                        : "bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border border-gray-300 dark:border-gray-600"
                    }`}
                  >
                    <i className="fas fa-thumbs-up mr-2"></i>
                    Yes, I recommend this
                  </button>
                  <button
                    type="button"
                    onClick={() =>
                      setFormData((prev) => ({
                        ...prev,
                        wouldRecommend: false,
                      }))
                    }
                    className={`flex-1 py-3 px-4 rounded-xl font-medium transition-all duration-200 ${
                      !formData.wouldRecommend
                        ? "bg-red-600 text-white shadow-lg"
                        : "bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border border-gray-300 dark:border-gray-600"
                    }`}
                  >
                    <i className="fas fa-thumbs-down mr-2"></i>
                    No, I don&apos;t recommend
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Detailed Review */}
          {currentStep === 2 && (
            <div className="space-y-6">
              {/* Detailed Comment */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                  Tell us about your experience
                </label>
                <textarea
                  value={formData.comment}
                  onChange={(e) => {
                    setFormData((prev) => ({
                      ...prev,
                      comment: e.target.value,
                    }));
                    setErrors((prev) => ({ ...prev, comment: null }));
                  }}
                  placeholder="Share your detailed experience with this product. What did you like? How was the quality, freshness, taste, etc.?"
                  rows={6}
                  className={`w-full px-4 py-3 border-2 rounded-xl resize-none focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-200 dark:bg-gray-800 dark:text-white dark:border-gray-600 ${
                    errors.comment ? "border-red-500" : "border-gray-300"
                  }`}
                  maxLength={1000}
                />
                <div className="flex justify-between items-center mt-2">
                  {errors.comment && (
                    <p className="text-red-500 text-sm">{errors.comment}</p>
                  )}
                  <div className="text-sm text-gray-500 dark:text-gray-400 ml-auto">
                    {formData.comment.length}/1000
                  </div>
                </div>
              </div>

              {/* Pros and Cons */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                    <i className="fas fa-plus-circle text-green-500 mr-2"></i>
                    What did you like? (Optional)
                  </label>
                  <textarea
                    value={formData.pros}
                    onChange={(e) =>
                      setFormData((prev) => ({ ...prev, pros: e.target.value }))
                    }
                    placeholder="e.g., Very fresh, great taste, good packaging..."
                    rows={4}
                    className="w-full px-4 py-3 border-2 border-gray-300 dark:border-gray-600 rounded-xl resize-none focus:ring-4 focus:ring-green-100 focus:border-green-500 transition-all duration-200 dark:bg-gray-800 dark:text-white"
                    maxLength={300}
                  />
                  <div className="text-sm text-gray-500 dark:text-gray-400 mt-1 text-right">
                    {formData.pros.length}/300
                  </div>
                </div>

                <div>
                  <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                    <i className="fas fa-minus-circle text-red-500 mr-2"></i>
                    What could be improved? (Optional)
                  </label>
                  <textarea
                    value={formData.cons}
                    onChange={(e) =>
                      setFormData((prev) => ({ ...prev, cons: e.target.value }))
                    }
                    placeholder="e.g., Packaging could be better, slightly expensive..."
                    rows={4}
                    className="w-full px-4 py-3 border-2 border-gray-300 dark:border-gray-600 rounded-xl resize-none focus:ring-4 focus:ring-red-100 focus:border-red-500 transition-all duration-200 dark:bg-gray-800 dark:text-white"
                    maxLength={300}
                  />
                  <div className="text-sm text-gray-500 dark:text-gray-400 mt-1 text-right">
                    {formData.cons.length}/300
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Additional Details */}
          {currentStep === 3 && (
            <div className="space-y-6">
              {/* Tags */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Add tags to help others (Optional)
                </label>
                <div className="flex flex-wrap gap-2">
                  {availableTags.map((tag) => (
                    <button
                      key={tag}
                      type="button"
                      onClick={() => handleTagToggle(tag)}
                      className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                        selectedTags.includes(tag)
                          ? "bg-blue-600 text-white shadow-lg transform scale-105"
                          : "bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600"
                      }`}
                    >
                      {tag}
                    </button>
                  ))}
                </div>
                {selectedTags.length > 0 && (
                  <div className="mt-3 text-sm text-gray-600 dark:text-gray-400">
                    Selected: {selectedTags.join(", ")}
                  </div>
                )}
              </div>

              {/* Privacy Settings */}
              <div className="bg-gray-50 dark:bg-gray-800 rounded-xl p-6">
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Privacy Settings
                </label>
                <div className="space-y-3">
                  <label className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={formData.isAnonymous}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          isAnonymous: e.target.checked,
                        }))
                      }
                      className="w-5 h-5 text-blue-600 border-2 border-gray-300 rounded focus:ring-blue-500"
                    />
                    <div>
                      <div className="font-medium text-gray-900 dark:text-white">
                        Post anonymously
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        Your name will be shown as &quot;Anonymous&quot; instead
                        of your actual name
                      </div>
                    </div>
                  </label>
                </div>
              </div>

              {/* Review Preview */}
              <div className="bg-blue-50 dark:bg-blue-900/20 rounded-xl p-6 border border-blue-200 dark:border-blue-800">
                <h5 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                  <i className="fas fa-eye mr-2 text-blue-600"></i>
                  Review Preview
                </h5>
                <div className="space-y-3">
                  <div className="flex items-center space-x-2">
                    <StarRating rating={formData.rating} size="small" />
                    <span className="font-semibold text-gray-900 dark:text-white">
                      {formData.title || "Review title"}
                    </span>
                  </div>
                  <p className="text-gray-700 dark:text-gray-300 text-sm">
                    {formData.comment ||
                      "Your detailed review will appear here..."}
                  </p>
                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    By{" "}
                    {formData.isAnonymous
                      ? "Anonymous"
                      : user?.name || "Your Name"}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Enhanced Footer */}
        <div className="bg-gray-50 dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 p-6">
          <div className="flex flex-col sm:flex-row justify-between items-stretch sm:items-center gap-4 sm:gap-0">
            <div className="flex items-center justify-start">
              {currentStep > 1 && (
                <button
                  onClick={handlePrevious}
                  className="px-6 py-3 border-2 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-600 rounded-xl font-semibold transition-all duration-200 flex items-center min-w-fit"
                >
                  <i className="fas fa-arrow-left mr-2"></i>
                  Previous
                </button>
              )}
            </div>

            <div className="flex items-center justify-end gap-4 flex-wrap">
              <button
                onClick={onClose}
                className="px-6 py-3 border-2 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-600 rounded-xl font-semibold transition-all duration-200 min-w-fit"
              >
                Cancel
              </button>

              {currentStep < 3 ? (
                <button
                  onClick={handleNext}
                  className="px-8 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-xl font-semibold transition-all duration-200 flex items-center shadow-lg hover:shadow-xl min-w-fit"
                >
                  Next
                  <i className="fas fa-arrow-right ml-2"></i>
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  disabled={isSubmitting}
                  className="px-8 py-3 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white rounded-xl font-semibold transition-all duration-200 disabled:opacity-50 flex items-center shadow-lg hover:shadow-xl min-w-fit whitespace-nowrap"
                >
                  {isSubmitting && (
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                  )}
                  <i className="fas fa-paper-plane mr-2"></i>
                  {existingReview ? "Update Review" : "Submit Review"}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

import { useState, useEffect, useMemo } from "react";
import StarRating from "@/components/StarRating";

export default function EnhancedReviewModal({
  isOpen,
  onClose,
  product,
  user,
  existingReview = null,
  onSubmit,
  isSubmitting = false,
}) {
  const [formData, setFormData] = useState({
    rating: 5,
    title: "",
    comment: "",
    pros: "",
    cons: "",
    wouldRecommend: true,
    isAnonymous: false,
    tags: [],
  });

  const [currentStep, setCurrentStep] = useState(1);
  const [errors, setErrors] = useState({});
  const [selectedTags, setSelectedTags] = useState([]);
  const [isVisible, setIsVisible] = useState(false);

  // Predefined tags for different product categories
  const availableTags = useMemo(() => {
    const categoryTags = {
      vegetables: [
        "Fresh",
        "Organic",
        "Crispy",
        "Sweet",
        "Bitter",
        "Tender",
        "Juicy",
      ],
      fruits: [
        "Sweet",
        "Sour",
        "Juicy",
        "Fresh",
        "Ripe",
        "Organic",
        "Crispy",
        "Soft",
      ],
      dairy: ["Creamy", "Rich", "Fresh", "Smooth", "Thick", "Natural"],
      meat: ["Tender", "Fresh", "Lean", "Juicy", "Well-marbled", "Flavorful"],
      grains: ["Fresh", "Organic", "High-quality", "Clean", "Aromatic"],
    };

    const category = product?.category?.toLowerCase() || "general";
    return (
      categoryTags[category] || [
        "High Quality",
        "Fresh",
        "Good Value",
        "Fast Delivery",
        "Well Packaged",
        "As Described",
        "Organic",
        "Natural",
        "Healthy",
        "Delicious",
      ]
    );
  }, [product?.category]);

  // Animation effect
  useEffect(() => {
    if (isOpen) {
      setIsVisible(true);
      document.body.style.overflow = "hidden";
    } else {
      setIsVisible(false);
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  // Pre-fill form if editing existing review
  useEffect(() => {
    if (existingReview) {
      setFormData({
        rating: existingReview.rating || 5,
        title: existingReview.title || "",
        comment: existingReview.comment || "",
        pros: existingReview.pros || "",
        cons: existingReview.cons || "",
        wouldRecommend: existingReview.wouldRecommend ?? true,
        isAnonymous: existingReview.isAnonymous || false,
        tags: existingReview.tags || [],
      });
      setSelectedTags(existingReview.tags || []);
    } else {
      // Reset form for new review
      setFormData({
        rating: 5,
        title: "",
        comment: "",
        pros: "",
        cons: "",
        wouldRecommend: true,
        isAnonymous: false,
        tags: [],
      });
      setSelectedTags([]);
      setCurrentStep(1);
    }
  }, [existingReview, isOpen]);

  const validateStep = (step) => {
    const newErrors = {};

    switch (step) {
      case 1:
        if (!formData.rating || formData.rating < 1 || formData.rating > 5) {
          newErrors.rating = "Please select a rating between 1 and 5 stars";
        }
        if (!formData.title.trim()) {
          newErrors.title = "Please provide a title for your review";
        } else if (formData.title.length < 5) {
          newErrors.title = "Title must be at least 5 characters long";
        } else if (formData.title.length > 100) {
          newErrors.title = "Title must be less than 100 characters";
        }
        break;

      case 2:
        if (!formData.comment.trim()) {
          newErrors.comment = "Please write your detailed review";
        } else if (formData.comment.length < 20) {
          newErrors.comment = "Review must be at least 20 characters long";
        } else if (formData.comment.length > 1000) {
          newErrors.comment = "Review must be less than 1000 characters";
        }
        break;

      case 3:
        // Optional step - no validation required
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      setCurrentStep((prev) => Math.min(prev + 1, 3));
    }
  };

  const handlePrevious = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  const handleTagToggle = (tag) => {
    const newTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];

    setSelectedTags(newTags);
    setFormData((prev) => ({ ...prev, tags: newTags }));
  };

  const handleSubmit = async () => {
    if (!validateStep(1) || !validateStep(2)) {
      setCurrentStep(1);
      return;
    }

    const reviewData = {
      ...formData,
      tags: selectedTags,
      productId: product?.id || product?._id,
      userId: user?.id || user?._id || user?.userId,
      userName: formData.isAnonymous ? "Anonymous" : user?.name || user?.email,
      userEmail: user?.email,
    };

    await onSubmit(reviewData);
  };

  const getRatingText = (rating) => {
    const texts = {
      1: "Terrible",
      2: "Poor",
      3: "Average",
      4: "Good",
      5: "Excellent",
    };
    return texts[rating] || "Not rated";
  };

  const getRatingColor = (rating) => {
    if (rating <= 2) return "text-red-500";
    if (rating === 3) return "text-yellow-500";
    return "text-green-500";
  };

  if (!isOpen) return null;

  return (
    <div
      className={`fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 transition-all duration-300 ${isVisible ? "opacity-100" : "opacity-0"}`}
    >
      <div
        className={`bg-white dark:bg-gray-900 rounded-3xl w-full max-w-2xl max-h-[95vh] overflow-hidden shadow-2xl border border-gray-200 dark:border-gray-700 transform transition-all duration-300 ${isVisible ? "scale-100 translate-y-0" : "scale-95 translate-y-4"}`}
      >
        {/* Enhanced Header */}
        <div className="relative bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700 text-white p-8">
          <div className="absolute inset-0 bg-black/10"></div>
          <div className="relative z-10">
            <div className="flex justify-between items-start">
              <div className="space-y-2">
                <div className="flex items-center space-x-3">
                  <div className="p-3 bg-white/20 rounded-2xl backdrop-blur-sm">
                    <i className="fas fa-star text-2xl"></i>
                  </div>
                  <div>
                    <h3 className="text-3xl font-bold tracking-tight">
                      {existingReview ? "Edit Review" : "Write a Review"}
                    </h3>
                    <p className="text-blue-100 text-lg">
                      Share your experience with {product?.name}
                    </p>
                  </div>
                </div>

                {/* Progress Steps */}
                <div className="flex items-center space-x-4 mt-6">
                  {[1, 2, 3].map((step) => (
                    <div key={step} className="flex items-center">
                      <div
                        className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold transition-all duration-300 ${
                          currentStep >= step
                            ? "bg-white text-blue-600 shadow-lg"
                            : "bg-white/20 text-white border-2 border-white/30"
                        }`}
                      >
                        {step}
                      </div>
                      {step < 3 && (
                        <div
                          className={`w-12 h-1 mx-2 rounded transition-all duration-300 ${
                            currentStep > step ? "bg-white" : "bg-white/30"
                          }`}
                        ></div>
                      )}
                    </div>
                  ))}
                </div>

                <div className="text-sm text-blue-100 mt-2">
                  Step {currentStep} of 3:{" "}
                  {currentStep === 1
                    ? "Rating & Title"
                    : currentStep === 2
                      ? "Detailed Review"
                      : "Additional Details"}
                </div>
              </div>

              <button
                onClick={onClose}
                className="p-3 hover:bg-white/20 rounded-2xl transition-all duration-200 group"
              >
                <i className="fas fa-times text-xl group-hover:rotate-90 transition-transform duration-200"></i>
              </button>
            </div>
          </div>
        </div>

        {/* Modal Content */}
        <div className="p-8 overflow-y-auto max-h-[calc(95vh-280px)]">
          {/* Step 1: Rating & Title */}
          {currentStep === 1 && (
            <div className="space-y-8">
              {/* Rating Section */}
              <div className="text-center">
                <h4 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
                  How would you rate this product?
                </h4>

                <div className="flex justify-center items-center space-x-4 mb-4">
                  <StarRating
                    rating={formData.rating}
                    onRatingChange={(rating) => {
                      setFormData((prev) => ({ ...prev, rating }));
                      setErrors((prev) => ({ ...prev, rating: null }));
                    }}
                    size="large"
                    interactive={true}
                  />
                  <div className="text-left">
                    <div
                      className={`text-2xl font-bold ${getRatingColor(formData.rating)}`}
                    >
                      {getRatingText(formData.rating)}
                    </div>
                    <div className="text-sm text-gray-500 dark:text-gray-400">
                      {formData.rating}/5 stars
                    </div>
                  </div>
                </div>

                {errors.rating && (
                  <p className="text-red-500 text-sm mt-2">{errors.rating}</p>
                )}
              </div>

              {/* Title Section */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                  Give your review a title
                </label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => {
                    setFormData((prev) => ({ ...prev, title: e.target.value }));
                    setErrors((prev) => ({ ...prev, title: null }));
                  }}
                  placeholder="e.g., Great quality vegetables, fresh and crispy!"
                  className={`w-full px-4 py-3 border-2 rounded-xl text-lg focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-200 dark:bg-gray-800 dark:text-white dark:border-gray-600 ${
                    errors.title ? "border-red-500" : "border-gray-300"
                  }`}
                  maxLength={100}
                />
                <div className="flex justify-between items-center mt-2">
                  {errors.title && (
                    <p className="text-red-500 text-sm">{errors.title}</p>
                  )}
                  <div className="text-sm text-gray-500 dark:text-gray-400 ml-auto">
                    {formData.title.length}/100
                  </div>
                </div>
              </div>

              {/* Recommendation */}
              <div className="bg-gray-50 dark:bg-gray-800 rounded-xl p-6">
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Would you recommend this product?
                </label>
                <div className="flex space-x-4">
                  <button
                    type="button"
                    onClick={() =>
                      setFormData((prev) => ({ ...prev, wouldRecommend: true }))
                    }
                    className={`flex-1 py-3 px-4 rounded-xl font-medium transition-all duration-200 ${
                      formData.wouldRecommend
                        ? "bg-green-600 text-white shadow-lg"
                        : "bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border border-gray-300 dark:border-gray-600"
                    }`}
                  >
                    <i className="fas fa-thumbs-up mr-2"></i>
                    Yes, I recommend this
                  </button>
                  <button
                    type="button"
                    onClick={() =>
                      setFormData((prev) => ({
                        ...prev,
                        wouldRecommend: false,
                      }))
                    }
                    className={`flex-1 py-3 px-4 rounded-xl font-medium transition-all duration-200 ${
                      !formData.wouldRecommend
                        ? "bg-red-600 text-white shadow-lg"
                        : "bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border border-gray-300 dark:border-gray-600"
                    }`}
                  >
                    <i className="fas fa-thumbs-down mr-2"></i>
                    No, I don&apos;t recommend
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Detailed Review */}
          {currentStep === 2 && (
            <div className="space-y-6">
              {/* Detailed Comment */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                  Tell us about your experience
                </label>
                <textarea
                  value={formData.comment}
                  onChange={(e) => {
                    setFormData((prev) => ({
                      ...prev,
                      comment: e.target.value,
                    }));
                    setErrors((prev) => ({ ...prev, comment: null }));
                  }}
                  placeholder="Share your detailed experience with this product. What did you like? How was the quality, freshness, taste, etc.?"
                  rows={6}
                  className={`w-full px-4 py-3 border-2 rounded-xl resize-none focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-200 dark:bg-gray-800 dark:text-white dark:border-gray-600 ${
                    errors.comment ? "border-red-500" : "border-gray-300"
                  }`}
                  maxLength={1000}
                />
                <div className="flex justify-between items-center mt-2">
                  {errors.comment && (
                    <p className="text-red-500 text-sm">{errors.comment}</p>
                  )}
                  <div className="text-sm text-gray-500 dark:text-gray-400 ml-auto">
                    {formData.comment.length}/1000
                  </div>
                </div>
              </div>

              {/* Pros and Cons */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                    <i className="fas fa-plus-circle text-green-500 mr-2"></i>
                    What did you like? (Optional)
                  </label>
                  <textarea
                    value={formData.pros}
                    onChange={(e) =>
                      setFormData((prev) => ({ ...prev, pros: e.target.value }))
                    }
                    placeholder="e.g., Very fresh, great taste, good packaging..."
                    rows={4}
                    className="w-full px-4 py-3 border-2 border-gray-300 dark:border-gray-600 rounded-xl resize-none focus:ring-4 focus:ring-green-100 focus:border-green-500 transition-all duration-200 dark:bg-gray-800 dark:text-white"
                    maxLength={300}
                  />
                  <div className="text-sm text-gray-500 dark:text-gray-400 mt-1 text-right">
                    {formData.pros.length}/300
                  </div>
                </div>

                <div>
                  <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-3">
                    <i className="fas fa-minus-circle text-red-500 mr-2"></i>
                    What could be improved? (Optional)
                  </label>
                  <textarea
                    value={formData.cons}
                    onChange={(e) =>
                      setFormData((prev) => ({ ...prev, cons: e.target.value }))
                    }
                    placeholder="e.g., Packaging could be better, slightly expensive..."
                    rows={4}
                    className="w-full px-4 py-3 border-2 border-gray-300 dark:border-gray-600 rounded-xl resize-none focus:ring-4 focus:ring-red-100 focus:border-red-500 transition-all duration-200 dark:bg-gray-800 dark:text-white"
                    maxLength={300}
                  />
                  <div className="text-sm text-gray-500 dark:text-gray-400 mt-1 text-right">
                    {formData.cons.length}/300
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Additional Details */}
          {currentStep === 3 && (
            <div className="space-y-6">
              {/* Tags */}
              <div>
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Add tags to help others (Optional)
                </label>
                <div className="flex flex-wrap gap-2">
                  {availableTags.map((tag) => (
                    <button
                      key={tag}
                      type="button"
                      onClick={() => handleTagToggle(tag)}
                      className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                        selectedTags.includes(tag)
                          ? "bg-blue-600 text-white shadow-lg transform scale-105"
                          : "bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600"
                      }`}
                    >
                      {tag}
                    </button>
                  ))}
                </div>
                {selectedTags.length > 0 && (
                  <div className="mt-3 text-sm text-gray-600 dark:text-gray-400">
                    Selected: {selectedTags.join(", ")}
                  </div>
                )}
              </div>

              {/* Privacy Settings */}
              <div className="bg-gray-50 dark:bg-gray-800 rounded-xl p-6">
                <label className="block text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  Privacy Settings
                </label>
                <div className="space-y-3">
                  <label className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={formData.isAnonymous}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          isAnonymous: e.target.checked,
                        }))
                      }
                      className="w-5 h-5 text-blue-600 border-2 border-gray-300 rounded focus:ring-blue-500"
                    />
                    <div>
                      <div className="font-medium text-gray-900 dark:text-white">
                        Post anonymously
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        Your name will be shown as &quot;Anonymous&quot; instead
                        of your actual name
                      </div>
                    </div>
                  </label>
                </div>
              </div>

              {/* Review Preview */}
              <div className="bg-blue-50 dark:bg-blue-900/20 rounded-xl p-6 border border-blue-200 dark:border-blue-800">
                <h5 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                  <i className="fas fa-eye mr-2 text-blue-600"></i>
                  Review Preview
                </h5>
                <div className="space-y-3">
                  <div className="flex items-center space-x-2">
                    <StarRating rating={formData.rating} size="small" />
                    <span className="font-semibold text-gray-900 dark:text-white">
                      {formData.title || "Review title"}
                    </span>
                  </div>
                  <p className="text-gray-700 dark:text-gray-300 text-sm">
                    {formData.comment ||
                      "Your detailed review will appear here..."}
                  </p>
                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    By{" "}
                    {formData.isAnonymous
                      ? "Anonymous"
                      : user?.name || "Your Name"}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Enhanced Footer */}
        <div className="bg-gray-50 dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 p-6">
          <div className="flex flex-col sm:flex-row justify-between items-stretch sm:items-center gap-4 sm:gap-0 -mt-2">
            <div className="flex items-center justify-start">
              {currentStep > 1 && (
                <button
                  onClick={handlePrevious}
                  className="px-6 py-3 border-2 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-600 rounded-xl font-semibold transition-all duration-200 flex items-center min-w-fit"
                >
                  <i className="fas fa-arrow-left mr-2"></i>
                  Previous
                </button>
              )}
            </div>

            <div className="flex items-center justify-end gap-4 flex-wrap">
              <button
                onClick={onClose}
                className="px-6 py-3 border-2 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-600 rounded-xl font-semibold transition-all duration-200 min-w-fit"
              >
                Cancel
              </button>

              {currentStep < 3 ? (
                <button
                  onClick={handleNext}
                  className="px-8 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-xl font-semibold transition-all duration-200 flex items-center shadow-lg hover:shadow-xl min-w-fit"
                >
                  Next
                  <i className="fas fa-arrow-right ml-2"></i>
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  disabled={isSubmitting}
                  className="px-8 py-3 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white rounded-xl font-semibold transition-all duration-200 disabled:opacity-50 flex items-center shadow-lg hover:shadow-xl min-w-fit whitespace-nowrap"
                >
                  {isSubmitting && (
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                  )}
                  <i className="fas fa-paper-plane mr-2"></i>
                  {existingReview ? "Update Review" : "Submit Review"}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

