    ]);

    // Calculate actual total count for large datasets
    const actualTotalCount = limit < 1000 ? totalCount : products.length;

    const queryTime = Date.now() - startTime;
    console.log(
      `Atlas query executed in ${queryTime}ms for ${products.length} products`,
    );

    // Enhance with ratings and review counts from the products collection
    const enhancedProducts = await enhanceProductsWithRatings(products, cachedDb);

    // Prepare response data
    const responseData = {
      products: enhancedProducts,
      pagination: {
        page,
        limit,
        total: actualTotalCount,
        totalPages: Math.ceil(actualTotalCount / limit),
        hasNext: page * limit < actualTotalCount,
        hasPrev: page > 1,
      },
      meta: {
        query: {
          search,
          category,
          featured,
          sortBy,
          minPrice,
          maxPrice,
          minRating,
        },
        performance: {
          queryTime,
          cached: false,
        },
      },
    };

    // Cache the response
    setCachedResponse(cacheKey, responseData);

    return NextResponse.json(responseData);
  } catch (error) {
    console.error("Error fetching products:", error);
    return NextResponse.json(
      {
        error: "Failed to fetch products",
        message: error.message,
        products: [],
        pagination: {
          page: 1,
          limit: 12,
          total: 0,
          totalPages: 0,
          hasNext: false,
          hasPrev: false,
        },
      },
      { status: 500 }
    );
  }
}

export async function POST(request) {
  try {
    const body = await request.json();
    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Initialize indexes for the first POST operation
    await initializeProductIndexes(db);

    const result = await db.collection("products").insertOne({
      ...body,
      createdAt: new Date(),
      updatedAt: new Date(),
      status: "active",
      averageRating: 0,
      totalReviews: 0,
      reviewCount: 0,
      purchaseCount: 0,
    });

    return NextResponse.json({
      success: true,
      productId: result.insertedId,
    });
  } catch (error) {
    console.error("Error creating product:", error);
    return NextResponse.json(
      { error: "Failed to create product", message: error.message },
      { status: 500 }
    );
  }
}

"use client";

import { useState, useEffect, useMemo, useCallback, useRef } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Footer from "@/components/Footer";

// Constants for better maintainability
const ORDER_STATUSES = {
  ALL: "All Orders",
  PENDING: "pending",
  CONFIRMED: "confirmed",
  SHIPPED: "shipped",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
};

const DATE_FILTERS = {
  ALL: "all",
  TODAY: "today",
  WEEK: "week",
  MONTH: "month",
};

const SORT_OPTIONS = {
  NEWEST: "newest",
  OLDEST: "oldest",
  HIGHEST: "highest",
  LOWEST: "lowest",
};

const VIEW_MODES = {
  CARDS: "cards",
  LIST: "list",
};

// Custom hooks for better code organization
const useOrdersData = (session, status) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const abortControllerRef = useRef(null);

  const fetchOrders = useCallback(
    async (showLoading = true) => {
      if (status !== "authenticated" || !session?.user) return;

      // Cancel previous request if still pending
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      abortControllerRef.current = new AbortController();

      try {
        if (showLoading) setLoading(true);
        setError(null);

        const userId =
          session.user.userId ||
          session.user.id ||
          session.user._id ||
          session.user.email;
        const userRole =
          session.user.userType || session.user.role || "customer";

        let apiUrl;
        if (userRole === "farmer") {
          const farmerId =
            session.user.userId || session.user.id || session.user._id;
          const farmerEmail = session.user.email;

          if (farmerId) {
            apiUrl = `/api/orders?farmerId=${encodeURIComponent(farmerId)}`;
          } else if (farmerEmail) {
            apiUrl = `/api/orders?farmerEmail=${encodeURIComponent(farmerEmail)}`;
          } else {
            throw new Error("No farmer identifier found");
          }
        } else {
          apiUrl = `/api/orders?userId=${encodeURIComponent(userId)}`;
        }

        const response = await fetch(apiUrl, {
          signal: abortControllerRef.current.signal,
          headers: {
            "Cache-Control": "no-cache",
          },
        });

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();

        // Validate response structure
        if (!data || !Array.isArray(data.orders)) {
          throw new Error("Invalid API response format");
        }

        setOrders(data.orders);
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Request was cancelled");
          return;
        }

        console.error("Error fetching orders:", error);
        setError(error.message || "Failed to fetch orders");
        setOrders([]);
      } finally {
        if (showLoading) setLoading(false);
      }
    },
    [session, status],
  );

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return { orders, loading, error, fetchOrders };
};

const useOrderFilters = (orders) => {
  const [statusFilter, setStatusFilter] = useState(ORDER_STATUSES.ALL);
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFilter, setDateFilter] = useState(DATE_FILTERS.ALL);
  const [sortOrder, setSortOrder] = useState(SORT_OPTIONS.NEWEST);

  const filteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Status filter
    if (statusFilter !== ORDER_STATUSES.ALL) {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Search filter
    if (searchTerm.trim()) {
      const searchText = searchTerm.toLowerCase().trim();
      filtered = filtered.filter((order) => {
        return (
          order._id?.toLowerCase().includes(searchText) ||
          order.customerName?.toLowerCase().includes(searchText) ||
          order.items?.some(
            (item) =>
              item.productName?.toLowerCase().includes(searchText) ||
              item.farmerName?.toLowerCase().includes(searchText),
          )
        );
      });
    }

    // Date filter
    if (dateFilter !== DATE_FILTERS.ALL) {
      const now = new Date();
      filtered = filtered.filter((order) => {
        const orderDate = new Date(order.createdAt);
        switch (dateFilter) {
          case DATE_FILTERS.TODAY:
            return orderDate.toDateString() === now.toDateString();
          case DATE_FILTERS.WEEK:
            const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
            return orderDate >= weekAgo;
          case DATE_FILTERS.MONTH:
            const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
            return orderDate >= monthAgo;
          default:
            return true;
        }
      });
    }

    // Sort orders
    filtered.sort((a, b) => {
      switch (sortOrder) {
        case SORT_OPTIONS.NEWEST:
          return new Date(b.createdAt) - new Date(a.createdAt);
        case SORT_OPTIONS.OLDEST:
          return new Date(a.createdAt) - new Date(b.createdAt);
        case SORT_OPTIONS.HIGHEST:
          return (b.total || 0) - (a.total || 0);
        case SORT_OPTIONS.LOWEST:
          return (a.total || 0) - (b.total || 0);
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateFilter, sortOrder]);

  return {
    filteredOrders,
    statusFilter,
    setStatusFilter,
    searchTerm,
    setSearchTerm,
    dateFilter,
    setDateFilter,
    sortOrder,
    setSortOrder,
  };
};

const useOrderStats = (orders) => {
  return useMemo(() => {
    if (!orders.length) {
      return {
        total: 0,
        pending: 0,
        confirmed: 0,
        shipped: 0,
        delivered: 0,
        cancelled: 0,
        totalSpent: 0,
        averageOrderValue: 0,
      };
    }

    const stats = orders.reduce(
      (acc, order) => {
        const status = order.status?.toLowerCase();
        const total = order.total || 0;

        acc.total++;
        acc.totalSpent += total;

        if (status in acc) {
          acc[status]++;
        }

        return acc;
      },
      {
        total: 0,
        pending: 0,
        confirmed: 0,
        shipped: 0,
        delivered: 0,
        cancelled: 0,
        totalSpent: 0,
      },
    );

    stats.averageOrderValue =
      stats.total > 0 ? stats.totalSpent / stats.total : 0;

    return stats;
  }, [orders]);
};

