export default function Bookings() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Custom hooks
  const { orders, loading, error, fetchOrders } = useOrdersData(
    session,
    status,
  );
  const orderStats = useOrderStats(orders);
  const {
    filteredOrders,
    statusFilter,
    setStatusFilter,
    searchTerm,
    setSearchTerm,
    dateFilter,
    setDateFilter,
    sortOrder,
    setSortOrder,
  } = useOrderFilters(orders);

  // UI state
  const [currentPage, setCurrentPage] = useState(1);
  const [viewMode, setViewMode] = useState(VIEW_MODES.CARDS);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showOrderDetails, setShowOrderDetails] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [actionLoading, setActionLoading] = useState(new Set());
  const [navigationLoading, setNavigationLoading] = useState(true); // Add navigation loading state

  const ordersPerPage = viewMode === VIEW_MODES.LIST ? 10 : 6;

  // Authentication check
  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
    }
  }, [status, router]);

  // Initial data fetch
  useEffect(() => {
    if (status === "authenticated" && session?.user) {
      fetchOrders();
    }
  }, [session, status, fetchOrders]);

  // Handle navigation loading state
  useEffect(() => {
    if (status === "authenticated" && session?.user) {
      // Show loading for a brief moment to display skeleton when navigating
      const timer = setTimeout(() => {
        setNavigationLoading(false);
      }, 800); // Adjust timing as needed

      return () => clearTimeout(timer);
    } else if (status === "loading") {
      setNavigationLoading(true);
    }
  }, [status, session]);

  // Reset pagination when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [statusFilter, searchTerm, dateFilter, sortOrder]);

  // Optimized handlers with error handling
  const handleRefresh = useCallback(async () => {
    setRefreshing(true);
    try {
      await fetchOrders(false);
    } finally {
      setRefreshing(false);
    }
  }, [fetchOrders]);

  const handleCancelOrder = useCallback(
    async (orderId) => {
      if (!window.confirm("Are you sure you want to cancel this order?"))
        return;

      setActionLoading((prev) => new Set(prev).add(orderId));

      try {
        const response = await fetch(`/api/orders/${orderId}`, {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json",
            "Cache-Control": "no-cache",
          },
          body: JSON.stringify({ status: ORDER_STATUSES.CANCELLED }),
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          throw new Error(
            errorData.message ||
              `HTTP ${response.status}: ${response.statusText}`,
          );
        }

        // Show success feedback
        alert("Order cancelled successfully");

        // Refresh orders
        await fetchOrders(false);
      } catch (error) {
        console.error("Error cancelling order:", error);
        alert(`Error cancelling order: ${error.message}`);
      } finally {
        setActionLoading((prev) => {
          const newSet = new Set(prev);
          newSet.delete(orderId);
          return newSet;
        });
      }
    },
    [fetchOrders],
  );

  const handleReorder = useCallback(
    (order) => {
      // Store order items in session storage for reorder functionality
      try {
        sessionStorage.setItem("reorderItems", JSON.stringify(order.items));
        router.push("/products?reorder=true");
      } catch (error) {
        console.error("Failed to store reorder data:", error);
        router.push("/products");
      }
    },
    [router],
  );

  // Utility functions
  const formatPrice = useCallback((price) => {
    const numericPrice =
      typeof price === "number" ? price : parseFloat(price) || 0;
    return `${numericPrice.toFixed(0)}`;
  }, []);

  const formatDate = useCallback((dateString) => {
    try {
      return new Date(dateString).toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      });
    } catch (error) {
      return "Invalid Date";
    }
  }, []);

  const getStatusBadge = (status) => {
    const statusConfig = {
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200 border-blue-200",
        icon: "fas fa-check",
        pulse: false,
      },
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200 border-yellow-200",
        icon: "fas fa-clock",
        pulse: true,
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200 border-green-200",
        icon: "fas fa-check-circle",
        pulse: false,
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200 border-red-200",
        icon: "fas fa-times-circle",
        pulse: false,
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200 border-purple-200",
        icon: "fas fa-truck",
        pulse: true,
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1.5 rounded-full text-sm font-medium border ${config.bg} ${
          config.pulse ? "animate-pulse" : ""
        }`}
      >
        <i className={`${config.icon} mr-1.5 text-xs`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getStatusProgress = (status) => {
    const statuses = ["pending", "confirmed", "shipped", "delivered"];
    const currentIndex = statuses.indexOf(status?.toLowerCase());
    const progress =
      currentIndex >= 0 ? ((currentIndex + 1) / statuses.length) * 100 : 0;

    return (
      <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2 mb-2">
        <div
          className="bg-gradient-to-r from-green-500 to-blue-500 h-2 rounded-full transition-all duration-500 ease-out"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
    );
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  // Show loading screen if still loading authentication, data, or navigation
  if (status === "loading" || loading || navigationLoading) {
    return <InitialLoadingScreen />;
  }

  return (
    <>
      <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-indigo-100 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900">
        {/* Enhanced Header Section */}
        <div className="relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-blue-600/10 to-purple-600/10 dark:from-blue-400/10 dark:to-purple-400/10"></div>
          <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            {/* Breadcrumb */}
            <nav className="flex mb-8" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-blue-600 transition-colors duration-200 flex items-center"
                  >
                    <i className="fas fa-home mr-1"></i>
                    Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  My Orders
                </li>
              </ol>
            </nav>

            {/* Page Header with Stats */}
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
              <div className="mb-6 lg:mb-0">
                <h1 className="text-4xl font-bold bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-gray-300 bg-clip-text text-transparent mb-3">
                  My Orders
                </h1>
                <p className="text-gray-600 dark:text-gray-400 text-lg">
                  Track and manage your orders  {orderStats.total} total orders
                </p>
              </div>

              {/* Quick Stats Cards */}
              <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 w-full lg:w-auto">
                <div className="bg-white dark:bg-gray-800 rounded-xl p-4 shadow-lg border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-all duration-300">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                      {orderStats.total}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400">
                      Total Orders
                    </div>
                  </div>
                </div>
                <div className="bg-white dark:bg-gray-800 rounded-xl p-4 shadow-lg border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-all duration-300">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600 dark:text-green-400">
                      {orderStats.delivered}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400">
                      Delivered
                    </div>
                  </div>
                </div>
                <div className="bg-white dark:bg-gray-800 rounded-xl p-4 shadow-lg border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-all duration-300">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">
                      {orderStats.pending}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400">
                      Pending
                    </div>
                  </div>
                </div>
                <div className="bg-white dark:bg-gray-800 rounded-xl p-4 shadow-lg border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-all duration-300">
                  <div className="text-center">
                    <div className="text-xl font-bold text-purple-600 dark:text-purple-400">
                      {formatPrice(orderStats.totalSpent)}
                    </div>
                    <div className="text-xs text-gray-600 dark:text-gray-400">
                      Total Spent
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Advanced Filters and Controls */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pb-8">
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl border border-gray-200 dark:border-gray-700 p-6 mb-8">
            <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
              {/* Search */}
              <div className="flex-1 max-w-md">
                <div className="relative">
                  <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"></i>
                  <input
                    type="text"
                    placeholder="Search orders, products, or farmers..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 dark:border-gray-600 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white transition-all duration-200"
                  />
                </div>
              </div>

              {/* Filters */}
              <div className="flex flex-wrap gap-3">
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white transition-all duration-200"
                >
                  <option value={ORDER_STATUSES.ALL}>
                    {ORDER_STATUSES.ALL}
                  </option>
                  <option value={ORDER_STATUSES.PENDING}>Pending</option>
                  <option value={ORDER_STATUSES.CONFIRMED}>Confirmed</option>
                  <option value={ORDER_STATUSES.SHIPPED}>Shipped</option>
                  <option value={ORDER_STATUSES.DELIVERED}>Delivered</option>
                  <option value={ORDER_STATUSES.CANCELLED}>Cancelled</option>
                </select>

                <select
                  value={dateFilter}
                  onChange={(e) => setDateFilter(e.target.value)}
                  className="px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white transition-all duration-200"
                >
                  <option value={DATE_FILTERS.ALL}>All Time</option>
                  <option value={DATE_FILTERS.TODAY}>Today</option>
                  <option value={DATE_FILTERS.WEEK}>Last Week</option>
                  <option value={DATE_FILTERS.MONTH}>Last Month</option>
                </select>

                <select
                  value={sortOrder}
                  onChange={(e) => setSortOrder(e.target.value)}
                  className="px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white transition-all duration-200"
                >
                  <option value={SORT_OPTIONS.NEWEST}>Newest First</option>
                  <option value={SORT_OPTIONS.OLDEST}>Oldest First</option>
                  <option value={SORT_OPTIONS.HIGHEST}>Highest Value</option>
                  <option value={SORT_OPTIONS.LOWEST}>Lowest Value</option>
                </select>

                {/* View Mode Toggle */}
                <div className="flex bg-gray-100 dark:bg-gray-700 rounded-xl p-1">
                  <button
                    onClick={() => setViewMode(VIEW_MODES.CARDS)}
                    className={`px-4 py-2 rounded-lg transition-all duration-200 ${
                      viewMode === VIEW_MODES.CARDS
                        ? "bg-white dark:bg-gray-600 text-blue-600 shadow-sm"
                        : "text-gray-600 dark:text-gray-400"
                    }`}
                  >
                    <i className="fas fa-th-large"></i>
                  </button>
                  <button
                    onClick={() => setViewMode(VIEW_MODES.LIST)}
                    className={`px-4 py-2 rounded-lg transition-all duration-200 ${
                      viewMode === VIEW_MODES.LIST
                        ? "bg-white dark:bg-gray-600 text-blue-600 shadow-sm"
                        : "text-gray-600 dark:text-gray-400"
                    }`}
                  >
                    <i className="fas fa-list"></i>
                  </button>
                </div>

                {/* Refresh Button */}
                <button
                  onClick={handleRefresh}
                  disabled={refreshing}
                  className="px-4 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl transition-all duration-200 disabled:opacity-50 flex items-center"
                >
                  <i
                    className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                  ></i>
                  Refresh
                </button>
              </div>
            </div>
          </div>

          {/* Orders Display - Full Width Cards */}
          <div className="space-y-6">
            {currentOrders.map((order, index) => (
              <div
                key={order._id}
                className="group bg-white dark:bg-gray-800 rounded-2xl shadow-lg hover:shadow-2xl border border-gray-200 dark:border-gray-700 overflow-hidden transition-all duration-300 transform hover:-translate-y-1"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                {/* Order Header */}
                <div className="bg-gradient-to-r from-gray-50 via-blue-50 to-indigo-50 dark:from-gray-700 dark:via-gray-600 dark:to-gray-700 p-6 border-b border-gray-200 dark:border-gray-600">
                  <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
                    <div className="flex-1">
                      <div className="flex flex-col sm:flex-row sm:items-center gap-4 mb-4">
                        <div>
                          <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-1">
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400 flex items-center">
                            <i className="fas fa-calendar mr-2"></i>
                            {formatDate(order.createdAt)}
                          </p>
                        </div>

                        <div className="flex items-center gap-4">
                          {getStatusBadge(order.status)}
                          <div className="text-right">
                            <div className="text-2xl font-bold text-gray-900 dark:text-white">
                              {formatPrice(order.total)}
                            </div>
                            <div className="text-sm text-gray-500 dark:text-gray-400">
                              {order.items?.length} items
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Enhanced Progress Timeline */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-4 border border-gray-200 dark:border-gray-600">
                        <div className="flex items-center justify-between mb-3">
                          <h4 className="text-sm font-semibold text-gray-900 dark:text-white flex items-center">
                            <i className="fas fa-route mr-2 text-blue-600"></i>
                            Order Progress
                          </h4>
                          <span className="text-xs text-gray-500 dark:text-gray-400">
                            {(() => {
                              const statuses = [
                                "pending",
                                "confirmed",
                                "shipped",
                                "delivered",
                              ];
                              const currentIndex = statuses.indexOf(
                                order.status?.toLowerCase(),
                              );
                              return `${currentIndex + 1}/${statuses.length}`;
                            })()}
                          </span>
                        </div>

                        {/* Progress Steps */}
                        <div className="relative">
                          <div className="flex items-center justify-between">
                            {(() => {
                              const steps = [
                                {
                                  status: "pending",
                                  icon: "fas fa-clock",
                                  label: "Order Placed",
                                  color: "yellow",
                                },
                                {
                                  status: "confirmed",
                                  icon: "fas fa-check",
                                  label: "Confirmed",
                                  color: "blue",
                                },
                                {
                                  status: "shipped",
                                  icon: "fas fa-truck",
                                  label: "Shipped",
                                  color: "purple",
                                },
                                {
                                  status: "delivered",
                                  icon: "fas fa-check-circle",
                                  label: "Delivered",
                                  color: "green",
                                },
                              ];

                              const currentIndex = steps.findIndex(
                                (step) =>
                                  step.status === order.status?.toLowerCase(),
                              );

                              return steps.map((step, index) => {
                                const isCompleted = index <= currentIndex;
                                const isActive = index === currentIndex;
                                const isCancelled =
                                  order.status?.toLowerCase() === "cancelled";

                                return (
                                  <div
                                    key={step.status}
                                    className="flex flex-col items-center relative z-10"
                                  >
                                    {/* Step Icon */}
                                    <div
                                      className={`
                                      w-12 h-12 rounded-full flex items-center justify-center border-2 transition-all duration-300
                                      ${
                                        isCancelled
                                          ? "bg-red-100 border-red-300 text-red-600"
                                          : isCompleted
                                            ? `bg-${step.color}-100 border-${step.color}-500 text-${step.color}-600`
                                            : isActive
                                              ? `bg-${step.color}-50 border-${step.color}-300 text-${step.color}-500 animate-pulse`
                                              : "bg-gray-100 border-gray-300 text-gray-400"
                                      }
                                    `}
                                    >
                                      <i
                                        className={`${isCancelled ? "fas fa-times" : step.icon} text-lg`}
                                      ></i>
                                    </div>

                                    {/* Step Label */}
                                    <span
                                      className={`
                                      mt-2 text-xs font-medium text-center
                                      ${
                                        isCancelled
                                          ? "text-red-600"
                                          : isCompleted
                                            ? "text-gray-900 dark:text-white"
                                            : "text-gray-500 dark:text-gray-400"
                                      }
                                    `}
                                    >
                                      {isCancelled && index === currentIndex
                                        ? "Cancelled"
                                        : step.label}
                                    </span>

                                    {/* Date/Time for completed steps */}
                                    {isCompleted && !isCancelled && (
                                      <span className="text-xs text-gray-400 mt-1">
                                        {index === 0
                                          ? new Date(
                                              order.createdAt,
                                            ).toLocaleDateString()
                                          : "TBD"}
                                      </span>
                                    )}
                                  </div>
                                );
                              });
                            })()}
                          </div>

                          {/* Progress Line */}
                          <div className="absolute top-6 left-6 right-6 h-0.5 bg-gray-200 dark:bg-gray-600 -z-0">
                            <div
                              className={`h-full transition-all duration-700 ease-out ${
                                order.status?.toLowerCase() === "cancelled"
                                  ? "bg-red-400"
                                  : "bg-gradient-to-r from-yellow-400 via-blue-400 via-purple-400 to-green-400"
                              }`}
                              style={{
                                width: (() => {
                                  const statuses = [
                                    "pending",
                                    "confirmed",
                                    "shipped",
                                    "delivered",
                                  ];
                                  const currentIndex = statuses.indexOf(
                                    order.status?.toLowerCase(),
                                  );
                                  if (
                                    order.status?.toLowerCase() === "cancelled"
                                  )
                                    return "25%";
                                  return `${((currentIndex + 1) / statuses.length) * 100}%`;
                                })(),
                              }}
                            ></div>
                          </div>
                        </div>

                        {/* Status Message */}
                        <div className="mt-4 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            {order.status === "pending" && (
                              <>
                                <i className="fas fa-hourglass-half mr-2 text-yellow-500"></i>
                                Waiting for farmer confirmation. We'll notify
                                you once your order is confirmed.
                              </>
                            )}
                            {order.status === "confirmed" && (
                              <>
                                <i className="fas fa-thumbs-up mr-2 text-blue-500"></i>
                                Your order has been confirmed! The farmer is
                                preparing your items.
                              </>
                            )}
                            {order.status === "shipped" && (
                              <>
                                <i className="fas fa-shipping-fast mr-2 text-purple-500"></i>
                                Your order is on the way! Track your delivery
                                for real-time updates.
                              </>
                            )}
                            {order.status === "delivered" && (
                              <>
                                <i className="fas fa-box-open mr-2 text-green-500"></i>
                                Order delivered successfully! Enjoy your fresh
                                products.
                              </>
                            )}
                            {order.status === "cancelled" && (
                              <>
                                <i className="fas fa-ban mr-2 text-red-500"></i>
                                This order has been cancelled. If you have
                                questions, please contact support.
                              </>
                            )}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Order Content */}
                <div className="p-6">
                  {/* Order Items Grid */}
                  <div className="mb-6">
                    <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                      <i className="fas fa-box mr-2 text-blue-600"></i>
                      Order Items ({order.items?.length || 0})
                    </h4>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 mb-4">
                      {order.items?.slice(0, 8).map((item, index) => (
                        <div
                          key={index}
                          className="bg-gray-50 dark:bg-gray-700 rounded-xl p-4 hover:bg-gray-100 dark:hover:bg-gray-600 transition-colors duration-200 border border-gray-200 dark:border-gray-600"
                        >
                          <div className="flex items-start space-x-3">
                            <div className="relative flex-shrink-0">
                              <img
                                src={
                                  item.image ||
                                  item.productImage ||
                                  item.product?.image ||
                                  item.product?.images?.[0] ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=60&h=60&fit=crop"
                                }
                                alt={item.productName || item.name || "Product"}
                                className="w-16 h-16 rounded-lg object-cover border-2 border-white dark:border-gray-600 shadow-sm"
                                onError={(e) => {
                                  e.target.src =
                                    "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=60&h=60&fit=crop";
                                }}
                              />
                              <div className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center font-bold">
                                {item.quantity}
                              </div>
                            </div>
                            <div className="flex-1 min-w-0">
                              <h5 className="font-medium text-gray-900 dark:text-white text-sm mb-1 truncate">
                                {item.productName}
                              </h5>
                              <p className="text-xs text-gray-600 dark:text-gray-400 mb-1 flex items-center">
                                <i className="fas fa-user-tie mr-1"></i>
                                {item.farmerName || "Local Farmer"}
                              </p>
                              <div className="flex justify-between items-center">
                                <span className="text-xs text-gray-500 dark:text-gray-400">
                                  {formatPrice(item.price)}/unit
                                </span>
                                <span className="font-semibold text-gray-900 dark:text-white text-sm">
                                  {formatPrice(item.price * item.quantity)}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>

                    {order.items?.length > 8 && (
                      <div className="text-center">
                        <button
                          onClick={() => {
                            setSelectedOrder(order);
                            setShowOrderDetails(true);
                          }}
                          className="inline-flex items-center px-4 py-2 text-blue-600 hover:text-blue-700 font-medium text-sm rounded-lg hover:bg-blue-50 dark:hover:bg-blue-900 transition-colors duration-200"
                        >
                          <i className="fas fa-plus mr-2"></i>
                          View {order.items.length - 8} more items
                        </button>
                      </div>
                    )}
                  </div>

                  {/* Order Summary & Actions */}
                  <div className="border-t border-gray-200 dark:border-gray-600 pt-6">
                    <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
                      {/* Order Summary */}
                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 flex-1">
                        <div className="text-center p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-700">
                          <div className="text-lg font-bold text-blue-600 dark:text-blue-400">
                            {order.items?.length || 0}
                          </div>
                          <div className="text-xs text-blue-600 dark:text-blue-400">
                            Items
                          </div>
                        </div>
                        <div className="text-center p-3 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-700">
                          <div className="text-lg font-bold text-green-600 dark:text-green-400">
                            {formatPrice(order.subtotal || order.total || 0)}
                          </div>
                          <div className="text-xs text-green-600 dark:text-green-400">
                            Subtotal
                          </div>
                        </div>
                        <div className="text-center p-3 bg-purple-50 dark:bg-purple-900/20 rounded-lg border border-purple-200 dark:border-purple-700">
                          <div className="text-lg font-bold text-purple-600 dark:text-purple-400">
                            {formatPrice(order.deliveryFee || 50)}
                          </div>
                          <div className="text-xs text-purple-600 dark:text-purple-400">
                            Delivery
                          </div>
                        </div>
                        <div className="text-center p-3 bg-gray-50 dark:bg-gray-700 rounded-lg border border-gray-200 dark:border-gray-600">
                          <div className="text-lg font-bold text-gray-900 dark:text-white">
                            {formatPrice(order.total)}
                          </div>
                          <div className="text-xs text-gray-600 dark:text-gray-400">
                            Total
                          </div>
                        </div>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex flex-wrap gap-3">
                        <button
                          onClick={() => {
                            setSelectedOrder(order);
                            setShowOrderDetails(true);
                          }}
                          className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-all duration-200 transform hover:scale-105 shadow-lg"
                        >
                          <i className="fas fa-eye mr-2"></i>
                          View Details
                        </button>

                        {order.status === "delivered" && (
                          <>
                            <button className="flex items-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition-all duration-200 shadow-lg">
                              <i className="fas fa-download mr-2"></i>
                              Receipt
                            </button>
                            <Link
                              href={`/review?orderId=${order._id}`}
                              className="flex items-center px-4 py-2 border border-orange-300 text-orange-600 hover:bg-orange-50 dark:hover:bg-orange-900 rounded-lg font-medium transition-all duration-200"
                            >
                              <i className="fas fa-star mr-2"></i>
                              Review
                            </Link>
                            <button
                              onClick={() => handleReorder(order)}
                              className="flex items-center px-4 py-2 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-lg font-medium transition-all duration-200"
                            >
                              <i className="fas fa-redo mr-2"></i>
                              Reorder
                            </button>
                          </>
                        )}

                        {(order.status === "pending" ||
                          order.status === "confirmed") && (
                          <button
                            onClick={() => handleCancelOrder(order._id)}
                            className="flex items-center px-4 py-2 border border-red-300 text-red-600 hover:bg-red-50 dark:hover:bg-red-900 rounded-lg font-medium transition-all duration-200"
                          >
                            <i className="fas fa-times mr-2"></i>
                            Cancel Order
                          </button>
                        )}

                        {order.status === "shipped" && (
                          <button className="flex items-center px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-medium transition-all duration-200 shadow-lg">
                            <i className="fas fa-map-marker-alt mr-2"></i>
                            Track Order
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Enhanced Pagination */}
          {totalPages > 1 && (
            <div className="flex flex-col sm:flex-row justify-between items-center mt-12 bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-lg border border-gray-200 dark:border-gray-700">
              <div className="text-sm text-gray-600 dark:text-gray-400 mb-4 sm:mb-0">
                Showing {indexOfFirstOrder + 1} to{" "}
                {Math.min(indexOfLastOrder, filteredOrders.length)} of{" "}
                {filteredOrders.length} orders
              </div>

              <nav
                aria-label="Pagination"
                className="flex items-center space-x-2"
              >
                <button
                  onClick={() =>
                    setCurrentPage((prev) => Math.max(prev - 1, 1))
                  }
                  disabled={currentPage === 1}
                  className="px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
                >
                  <i className="fas fa-chevron-left"></i>
                </button>

                {[...Array(totalPages)].map((_, index) => {
                  const page = index + 1;
                  if (
                    page === currentPage ||
                    page === 1 ||
                    page === totalPages ||
                    (page >= currentPage - 1 && page <= currentPage + 1)
                  ) {
                    return (
                      <button
                        key={page}
                        onClick={() => setCurrentPage(page)}
                        className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                          currentPage === page
                            ? "bg-blue-600 text-white shadow-lg transform scale-110"
                            : "text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 border border-gray-300 dark:border-gray-600"
                        }`}
                      >
                        {page}
                      </button>
                    );
                  } else if (
                    page === currentPage - 2 ||
                    page === currentPage + 2
                  ) {
                    return (
                      <span key={page} className="px-2 text-gray-400">
                        ...
                      </span>
                    );
                  }
                  return null;
                })}

                <button
                  onClick={() =>
                    setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                  }
                  disabled={currentPage === totalPages}
                  className="px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
                >
                  <i className="fas fa-chevron-right"></i>
                </button>
              </nav>
            </div>
          )}
        </div>
      </div>

      {/* Enhanced Order Details Modal */}
      {showOrderDetails && selectedOrder && (
        <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-gray-800 rounded-2xl w-full max-w-4xl max-h-[90vh] overflow-hidden shadow-2xl">
            {/* Modal Header */}
            <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6">
              <div className="flex justify-between items-center">
                <div>
                  <h3 className="text-2xl font-bold mb-1">
                    Order #{selectedOrder._id?.slice(-8)?.toUpperCase()}
                  </h3>
                  <p className="text-blue-100">
                    Placed on {formatDate(selectedOrder.createdAt)}
                  </p>
                </div>
                <button
                  onClick={() => setShowOrderDetails(false)}
                  className="p-2 hover:bg-white/20 rounded-lg transition-colors duration-200"
                >
                  <i className="fas fa-times text-xl"></i>
                </button>
              </div>
            </div>

            {/* Modal Body */}
            <div className="p-6 overflow-y-auto max-h-[calc(90vh-200px)]">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Order Summary */}
                <div className="space-y-6">
                  <div>
                    <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                      <i className="fas fa-clipboard-list mr-2 text-blue-600"></i>
                      Order Summary
                    </h4>
                    <div className="bg-gray-50 dark:bg-gray-700 rounded-xl p-4 space-y-3">
                      <div className="flex justify-between">
                        <span className="text-gray-600 dark:text-gray-400">
                          Status:
                        </span>
                        {getStatusBadge(selectedOrder.status)}
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600 dark:text-gray-400">
                          Total Amount:
                        </span>
                        <span className="text-xl font-bold text-gray-900 dark:text-white">
                          {formatPrice(selectedOrder.total)}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600 dark:text-gray-400">
                          Items:
                        </span>
                        <span className="font-medium text-gray-900 dark:text-white">
                          {selectedOrder.items?.length} items
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Delivery Information */}
                  {selectedOrder.deliveryAddress && (
                    <div>
                      <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                        <i className="fas fa-map-marker-alt mr-2 text-green-600"></i>
                        Delivery Information
                      </h4>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-xl p-4 space-y-2">
                        <p className="text-gray-900 dark:text-white font-medium">
                          {selectedOrder.deliveryAddress.name}
                        </p>
                        <p className="text-gray-600 dark:text-gray-400">
                          {selectedOrder.deliveryAddress.address}
                        </p>
                        <p className="text-gray-600 dark:text-gray-400">
                          {selectedOrder.deliveryAddress.city}
                        </p>
                        <p className="text-gray-600 dark:text-gray-400">
                          <i className="fas fa-phone mr-2"></i>
                          {selectedOrder.deliveryAddress.phone}
                        </p>
                      </div>
                    </div>
                  )}
                </div>

                {/* Order Items */}
                <div>
                  <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center">
                    <i className="fas fa-box mr-2 text-purple-600"></i>
                    Items Ordered
                  </h4>
                  <div className="space-y-3 max-h-96 overflow-y-auto">
                    {selectedOrder.items?.map((item, index) => (
                      <div
                        key={index}
                        className="flex items-center space-x-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-xl"
                      >
                        <img
                          src={
                            item.image ||
                            item.productImage ||
                            item.product?.image ||
                            item.product?.images?.[0] ||
                            "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=60&h=60&fit=crop"
                          }
                          alt={item.productName}
                          className="w-16 h-16 rounded-lg object-cover"
                        />
                        <div className="flex-1">
                          <h5 className="font-medium text-gray-900 dark:text-white">
                            {item.productName}
                          </h5>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            By {item.farmerName || "Local Farmer"}
                          </p>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Quantity: {item.quantity} {" "}
                            {formatPrice(item.price)}/unit
                          </p>
                        </div>
                        <div className="text-right">
                          <div className="font-semibold text-gray-900 dark:text-white">
                            {formatPrice(item.price * item.quantity)}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Modal Footer */}
            <div className="border-t border-gray-200 dark:border-gray-600 p-6 bg-gray-50 dark:bg-gray-700">
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowOrderDetails(false)}
                  className="px-6 py-3 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-600 rounded-xl font-medium transition-all duration-200"
                >
                  Close
                </button>
                {selectedOrder.status === "delivered" && (
                  <button className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl font-medium transition-all duration-200">
                    <i className="fas fa-download mr-2"></i>
                    Download Receipt
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useRouter, useSearchParams } from "next/navigation";
import Link from "next/link";
import Image from "next/image";
import Footer from "@/components/Footer";

export default function CreateProduct() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const searchParams = useSearchParams();
  const [loading, setLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const [editProductId, setEditProductId] = useState(null);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
    price: "",
    stock: "",
    unit: "",
    features: [],
    images: [],
    farmLocation: "",
    harvestDate: "",
  });

  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      // Check if user is a farmer
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }

      // Check if we're in edit mode
      const editId = searchParams.get("edit");
      if (editId) {
        setIsEditMode(true);
        setEditProductId(editId);
        fetchProductForEdit(editId);
      }
    }
  }, [session, status, router, searchParams]);

  const fetchProductForEdit = async (productId) => {
    try {
      setLoading(true);
      const response = await fetch(`/api/products/${productId}`);

      if (response.ok) {
        const data = await response.json();
        const product = data.product;

        // Pre-populate form with existing product data
        setFormData({
          name: product.name || "",
          category: product.category || "",
          description: product.description || "",
          price: product.price?.toString() || "",
          stock: product.stock?.toString() || "",
          unit: product.unit || "",
          features: product.features || [],
          images: product.images || (product.image ? [product.image] : []),
          farmLocation: product.farmLocation || "",
          harvestDate: product.harvestDate
            ? product.harvestDate.split("T")[0]
            : "",
        });

        // Set image previews if product has images
        if (product.images && product.images.length > 0) {
          setImagePreviews(product.images);
        } else if (product.image) {
          setImagePreviews([product.image]);
        }
      } else {
        console.error("Failed to fetch product for editing");
        alert(
          "Failed to load product data. Redirecting to create new product.",
        );
        // Reset to create mode
        setIsEditMode(false);
        setEditProductId(null);
      }
    } catch (error) {
      console.error("Error fetching product for edit:", error);
      alert("Error loading product data. Redirecting to create new product.");
      setIsEditMode(false);
      setEditProductId(null);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      features: checked
        ? [...prev.features, value]
        : prev.features.filter((feature) => feature !== value),
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);

    // Validate number of files
    if (files.length > 5) {
      alert("You can upload a maximum of 5 images");
      return;
    }

    // Validate each file
    const validFiles = [];
    const previews = [];
    const base64Images = [];

    files.forEach((file) => {
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert(`Image ${file.name} is too large. Maximum size is 5MB`);
        return;
      }

      // Validate file type
      if (!file.type.startsWith("image/")) {
        alert(`${file.name} is not a valid image file`);
        return;
      }

      validFiles.push(file);
      previews.push(URL.createObjectURL(file));

      // Convert image to base64 for storage
      const reader = new FileReader();
      reader.onload = (event) => {
        base64Images.push(event.target.result);

        // When all files are processed, update state
        if (base64Images.length === validFiles.length) {
          setFormData((prev) => ({
            ...prev,
            images: base64Images,
          }));
        }
      };
      reader.readAsDataURL(file);
    });

    setImageFiles(validFiles);
    setImagePreviews(previews);
  };

  const removeImage = (index) => {
    const newPreviews = imagePreviews.filter((_, i) => i !== index);
    const newFiles = imageFiles.filter((_, i) => i !== index);
    const newImages = formData.images.filter((_, i) => i !== index);

    setImagePreviews(newPreviews);
    setImageFiles(newFiles);
    setFormData((prev) => ({
      ...prev,
      images: newImages,
    }));
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      alert("Product name is required");
      return false;
    }
    if (!formData.category) {
      alert("Category is required");
      return false;
    }
    if (!formData.description.trim()) {
      alert("Description is required");
      return false;
    }
    if (!formData.price || parseFloat(formData.price) <= 0) {
      alert("Valid price is required");
      return false;
    }
    if (!formData.stock || parseInt(formData.stock) < 0) {
      alert("Valid stock quantity is required");
      return false;
    }
    if (!formData.unit) {
      alert("Unit is required");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      // Use the same user ID logic as in manage page
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        farmerId: userId,
        farmerEmail: userEmail,
        farmer: {
          id: userId,
          email: userEmail,
          name: session.user.name,
        },
        status: "active",
        updatedAt: new Date().toISOString(),
        // Keep backward compatibility with single image
        image: formData.images[0] || "",
      };

      // Only add createdAt for new products
      if (!isEditMode) {
        productData.createdAt = new Date().toISOString();
      }

      console.log(
        isEditMode ? "Updating product:" : "Creating product:",
        productData,
      );

      const url = isEditMode
        ? `/api/products/${editProductId}`
        : "/api/products";
      const method = isEditMode ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const result = await response.json();
        console.log(
          isEditMode
            ? "Product updated successfully:"
            : "Product created successfully:",
          result,
        );

        alert(
          isEditMode
            ? "Product updated successfully!"
            : "Product added successfully!",
        );

        // Reset form only for new products
        if (!isEditMode) {
          setFormData({
            name: "",
            category: "",
            description: "",
            price: "",
            stock: "",
            unit: "",
            features: [],
            images: [],
          });
          setImagePreviews([]);
          setImageFiles([]);
        }

        // Redirect to manage products page
        router.push("/manage");
      } else {
        const errorData = await response.json();
        console.error(
          isEditMode
            ? "Failed to update product:"
            : "Failed to create product:",
          errorData,
        );
        alert(
          `Failed to ${isEditMode ? "update" : "add"} product: ${
            errorData.error || "Unknown error"
          }`,
        );
      }
    } catch (error) {
      console.error(
        isEditMode ? "Error updating product:" : "Error creating product:",
        error,
      );
      alert(
        `Failed to ${isEditMode ? "update" : "add"} product. Please try again.`,
      );
    } finally {
      setLoading(false);
    }
  };

  if (status === "loading") {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-primary-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      {/* Breadcrumb */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
        <nav className="flex" aria-label="Breadcrumb">
          <ol className="flex items-center space-x-2 text-sm">
            <li>
              <Link href="/" className="text-gray-500 hover:text-primary-600">
                Home
              </Link>
            </li>
            <li>
              <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
            </li>
            <li>
              <Link
                href="/manage"
                className="text-gray-500 hover:text-primary-600"
              >
                Manage Products
              </Link>
            </li>
            <li>
              <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
            </li>
            <li className="text-gray-900 dark:text-white">
              {isEditMode ? "Edit Product" : "Add Product"}
            </li>
          </ol>
        </nav>
      </div>

      {/* Add Product Form */}
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-primary-600 text-white px-8 py-6">
            <h1 className="text-3xl font-bold">
              {isEditMode ? "Edit Product" : "Add New Product"}
            </h1>
            <p className="text-primary-100 mt-2">
              {isEditMode
                ? "Update your product information"
                : "Share your fresh produce with customers"}
            </p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="p-8 space-y-8">
            {/* Basic Information */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Basic Information
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="name"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Product Name *
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="e.g., Organic Tomatoes"
                  />
                </div>

                <div>
                  <label
                    htmlFor="category"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Category *
                  </label>
                  <select
                    id="category"
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  >
                    <option value="">Select Category</option>
                    <option value="Vegetables">Vegetables</option>
                    <option value="Fruits">Fruits</option>
                    <option value="Grains">Grains</option>
                    <option value="Dairy">Dairy</option>
                    <option value="Herbs">Herbs</option>
                    <option value="Honey">Honey</option>
                  </select>
                </div>

                <div className="md:col-span-2">
                  <label
                    htmlFor="description"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Description *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    value={formData.description}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="Describe your product, growing methods, quality, etc."
                  />
                </div>
              </div>
            </div>

            {/* Pricing & Inventory */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Pricing & Inventory
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <label
                    htmlFor="price"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Price per Unit () *
                  </label>
                  <input
                    type="number"
                    id="price"
                    name="price"
                    min="0"
                    step="0.01"
                    value={formData.price}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="45.00"
                  />
                </div>

                <div>
                  <label
                    htmlFor="unit"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Unit *
                  </label>
                  <select
                    id="unit"
                    name="unit"
                    value={formData.unit}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  >
                    <option value="">Select Unit</option>
                    <option value="kg">Kilogram (kg)</option>
                    <option value="g">Gram (g)</option>
                    <option value="piece">Piece</option>
                    <option value="bunch">Bunch</option>
                    <option value="liter">Liter</option>
                    <option value="ml">Milliliter (ml)</option>
                    <option value="dozen">Dozen</option>
                    <option value="bundle">Bundle</option>
                  </select>
                </div>

                <div>
                  <label
                    htmlFor="stock"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Available Stock *
                  </label>
                  <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    value={formData.stock}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="100"
                  />
                </div>
              </div>
            </div>

            {/* Product Images */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Product Images
              </h2>
              <div className="space-y-4">
                <div>
                  <label
                    htmlFor="images"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Upload Images (Max 5 images) *
                  </label>
                  <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center hover:border-primary-500 transition">
                    <input
                      type="file"
                      id="images"
                      name="images"
                      multiple
                      accept="image/*"
                      onChange={handleImageChange}
                      className="hidden"
                    />
                    <label htmlFor="images" className="cursor-pointer">
                      <i className="fas fa-cloud-upload-alt text-4xl text-gray-400 mb-4"></i>
                      <p className="text-lg font-medium text-gray-900 dark:text-white">
                        Click to upload images
                      </p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        PNG, JPG, WebP up to 5MB each
                      </p>
                    </label>
                  </div>

                  {/* Image Previews */}
                  {imagePreviews.length > 0 && (
                    <div className="mt-4">
                      <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                        Selected Images ({imagePreviews.length}/5)
                      </p>
                      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                        {imagePreviews.map((preview, index) => (
                          <div
                            key={index}
                            className="relative group rounded-lg overflow-hidden bg-gray-100 dark:bg-gray-700"
                          >
                            <Image
                              src={preview}
                              alt={`Product Image ${index + 1}`}
                              width={200}
                              height={150}
                              className="w-full h-24 object-cover group-hover:scale-105 transition-transform duration-200"
                            />
                            <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-opacity duration-200 flex items-center justify-center">
                              <button
                                type="button"
                                onClick={() => removeImage(index)}
                                className="opacity-0 group-hover:opacity-100 bg-red-500 hover:bg-red-600 text-white rounded-full w-8 h-8 flex items-center justify-center transition-all duration-200 shadow-lg"
                                title="Remove image"
                              >
                                <i className="fas fa-times text-sm"></i>
                              </button>
                            </div>
                            <div className="absolute bottom-1 left-1 bg-black bg-opacity-70 text-white text-xs px-1 py-0.5 rounded">
                              {index + 1}
                            </div>
                          </div>
                        ))}

                        {/* Add more images placeholder */}
                        {imagePreviews.length < 5 && (
                          <div
                            className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg h-24 flex items-center justify-center cursor-pointer hover:border-primary-500 transition-colors"
                            onClick={() =>
                              document.getElementById("images").click()
                            }
                          >
                            <div className="text-center">
                              <i className="fas fa-plus text-gray-400 text-xl mb-1"></i>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                Add More
                              </p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Farm Information */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Farm Information
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="farmLocation"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Farm Location
                  </label>
                  <input
                    type="text"
                    id="farmLocation"
                    name="farmLocation"
                    value={formData.farmLocation}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="e.g., Sylhet, Bangladesh"
                  />
                </div>

                <div>
                  <label
                    htmlFor="harvestDate"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Harvest Date
                  </label>
                  <input
                    type="date"
                    id="harvestDate"
                    name="harvestDate"
                    value={formData.harvestDate}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                </div>
              </div>
            </div>

            {/* Product Features */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Product Features
              </h2>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {[
                  "organic",
                  "pesticide-free",
                  "fresh",
                  "non-gmo",
                  "local",
                  "sustainable",
                  "fair-trade",
                  "gluten-free",
                ].map((feature) => (
                  <label
                    key={feature}
                    className="flex items-center p-3 border border-gray-300 dark:border-gray-600 rounded-lg cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-700"
                  >
                    <input
                      type="checkbox"
                      name="features"
                      value={feature}
                      checked={formData.features.includes(feature)}
                      onChange={handleFeatureChange}
                      className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                    />
                    <span className="ml-2 text-sm capitalize">
                      {feature.replace("-", " ")}
                    </span>
                  </label>
                ))}
              </div>
            </div>

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className={`w-full py-3 rounded-lg font-semibold focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 transition ${
                  loading
                    ? "bg-gray-400 cursor-not-allowed text-white"
                    : "bg-primary-600 text-white hover:bg-primary-700"
                }`}
              >
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    {isEditMode ? "Updating Product..." : "Adding Product..."}
                  </>
                ) : (
                  <>
                    <i className="fas fa-plus mr-2"></i>
                    {isEditMode ? "Update Product" : "Add Product"}
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Image from "next/image";
import Footer from "@/components/Footer";

export default function CreateProduct() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const [loading, setLoading] = useState(false);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
    price: "",
    stock: "",
    unit: "",
    features: [],
    images: [],
    farmLocation: "",
    harvestDate: "",
  });

  useEffect(() => {
    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      // Check if user is a farmer
      const userType = session.user.userType || session.user.role || "user";
      if (userType !== "farmer") {
        router.push("/");
        return;
      }
    }
  }, [session, status, router]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      features: checked
        ? [...prev.features, value]
        : prev.features.filter((feature) => feature !== value),
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);

    // Validate number of files
    if (files.length > 5) {
      alert("You can upload a maximum of 5 images");
      return;
    }

    // Validate each file
    const validFiles = [];
    const previews = [];
    const base64Images = [];

    files.forEach((file) => {
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert(`Image ${file.name} is too large. Maximum size is 5MB`);
        return;
      }

      // Validate file type
      if (!file.type.startsWith("image/")) {
        alert(`${file.name} is not a valid image file`);
        return;
      }

      validFiles.push(file);
      previews.push(URL.createObjectURL(file));

      // Convert image to base64 for storage
      const reader = new FileReader();
      reader.onload = (event) => {
        base64Images.push(event.target.result);

        // When all files are processed, update state
        if (base64Images.length === validFiles.length) {
          setFormData((prev) => ({
            ...prev,
            images: base64Images,
          }));
        }
      };
      reader.readAsDataURL(file);
    });

    setImageFiles(validFiles);
    setImagePreviews(previews);
  };

  const removeImage = (index) => {
    const newPreviews = imagePreviews.filter((_, i) => i !== index);
    const newFiles = imageFiles.filter((_, i) => i !== index);
    const newImages = formData.images.filter((_, i) => i !== index);

    setImagePreviews(newPreviews);
    setImageFiles(newFiles);
    setFormData((prev) => ({
      ...prev,
      images: newImages,
    }));
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      alert("Product name is required");
      return false;
    }
    if (!formData.category) {
      alert("Category is required");
      return false;
    }
    if (!formData.description.trim()) {
      alert("Description is required");
      return false;
    }
    if (!formData.price || parseFloat(formData.price) <= 0) {
      alert("Valid price is required");
      return false;
    }
    if (!formData.stock || parseInt(formData.stock) < 0) {
      alert("Valid stock quantity is required");
      return false;
    }
    if (!formData.unit) {
      alert("Unit is required");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      // Use the same user ID logic as in manage page
      const userId = session.user.userId || session.user.id || session.user._id;
      const userEmail = session.user.email;

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        farmerId: userId,
        farmerEmail: userEmail,
        farmer: {
          id: userId,
          email: userEmail,
          name: session.user.name,
        },
        status: "active",
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
        // Keep backward compatibility with single image
        image: formData.images[0] || "",
      };

      console.log("Creating product:", productData);

      const response = await fetch("/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const result = await response.json();
        console.log("Product created successfully:", result);

        alert("Product added successfully!");

        // Reset form
        setFormData({
          name: "",
          category: "",
          description: "",
          price: "",
          stock: "",
          unit: "",
          features: [],
          images: [],
          farmLocation: "",
          harvestDate: "",
        });
        setImagePreviews([]);
        setImageFiles([]);

        // Redirect to manage products page
        router.push("/manage");
      } else {
        const errorData = await response.json();
        console.error("Failed to create product:", errorData);
        alert(`Failed to add product: ${errorData.error || "Unknown error"}`);
      }
    } catch (error) {
      console.error("Error creating product:", error);
      alert("Failed to add product. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (status === "loading") {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-4xl text-primary-600 mb-4"></i>
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      {/* Breadcrumb */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
        <nav className="flex" aria-label="Breadcrumb">
          <ol className="flex items-center space-x-2 text-sm">
            <li>
              <Link href="/" className="text-gray-500 hover:text-primary-600">
                Home
              </Link>
            </li>
            <li>
              <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
            </li>
            <li>
              <Link
                href="/manage"
                className="text-gray-500 hover:text-primary-600"
              >
                Manage Products
              </Link>
            </li>
            <li>
              <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
            </li>
            <li className="text-gray-900 dark:text-white">Add Product</li>
          </ol>
        </nav>
      </div>

      {/* Add Product Form */}
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-primary-600 text-white px-8 py-6">
            <h1 className="text-3xl font-bold">Add New Product</h1>
            <p className="text-primary-100 mt-2">
              Share your fresh produce with customers
            </p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="p-8 space-y-8">
            {/* Basic Information */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Basic Information
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="name"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Product Name *
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="e.g., Fresh Tomatoes"
                  />
                </div>

                <div>
                  <label
                    htmlFor="category"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Category *
                  </label>
                  <select
                    id="category"
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  >
                    <option value="">Select Category</option>
                    <option value="Vegetables">Vegetables</option>
                    <option value="Fruits">Fruits</option>
                    <option value="Grains">Grains</option>
                    <option value="Dairy">Dairy</option>
                    <option value="Herbs">Herbs</option>
                    <option value="Honey">Honey</option>
                  </select>
                </div>

                <div className="md:col-span-2">
                  <label
                    htmlFor="description"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Description *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    value={formData.description}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="Describe your product, growing methods, quality, etc."
                  />
                </div>
              </div>
            </div>

            {/* Pricing & Inventory */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Pricing & Inventory
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <label
                    htmlFor="price"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Price per Unit () *
                  </label>
                  <input
                    type="number"
                    id="price"
                    name="price"
                    min="0"
                    step="0.01"
                    value={formData.price}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="45.00"
                  />
                </div>

                <div>
                  <label
                    htmlFor="unit"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Unit *
                  </label>
                  <select
                    id="unit"
                    name="unit"
                    value={formData.unit}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  >
                    <option value="">Select Unit</option>
                    <option value="kg">Kilogram (kg)</option>
                    <option value="g">Gram (g)</option>
                    <option value="piece">Piece</option>
                    <option value="bunch">Bunch</option>
                    <option value="liter">Liter</option>
                    <option value="ml">Milliliter (ml)</option>
                    <option value="dozen">Dozen</option>
                    <option value="bundle">Bundle</option>
                  </select>
                </div>

                <div>
                  <label
                    htmlFor="stock"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Available Stock *
                  </label>
                  <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    value={formData.stock}
                    onChange={handleInputChange}
                    required
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="100"
                  />
                </div>
              </div>
            </div>

            {/* Product Images */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Product Images
              </h2>
              <div className="space-y-4">
                <div>
                  <label
                    htmlFor="images"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Upload Images (Max 5 images) *
                  </label>
                  <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center hover:border-primary-500 transition">
                    <input
                      type="file"
                      id="images"
                      name="images"
                      multiple
                      accept="image/*"
                      onChange={handleImageChange}
                      className="hidden"
                    />
                    <label htmlFor="images" className="cursor-pointer">
                      <i className="fas fa-cloud-upload-alt text-4xl text-gray-400 mb-4"></i>
                      <p className="text-lg font-medium text-gray-900 dark:text-white">
                        Click to upload images
                      </p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        PNG, JPG, WebP up to 5MB each
                      </p>
                    </label>
                  </div>

                  {/* Image Previews */}
                  {imagePreviews.length > 0 && (
                    <div className="mt-4">
                      <p className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                        Selected Images ({imagePreviews.length}/5)
                      </p>
                      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                        {imagePreviews.map((preview, index) => (
                          <div
                            key={index}
                            className="relative group rounded-lg overflow-hidden bg-gray-100 dark:bg-gray-700"
                          >
                            <Image
                              src={preview}
                              alt={`Product Image ${index + 1}`}
                              width={200}
                              height={150}
                              className="w-full h-24 object-cover group-hover:scale-105 transition-transform duration-200"
                            />
                            <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-opacity duration-200 flex items-center justify-center">
                              <button
                                type="button"
                                onClick={() => removeImage(index)}
                                className="opacity-0 group-hover:opacity-100 bg-red-500 hover:bg-red-600 text-white rounded-full w-8 h-8 flex items-center justify-center transition-all duration-200 shadow-lg"
                                title="Remove image"
                              >
                                <i className="fas fa-times text-sm"></i>
                              </button>
                            </div>
                            <div className="absolute bottom-1 left-1 bg-black bg-opacity-70 text-white text-xs px-1 py-0.5 rounded">
                              {index + 1}
                            </div>
                          </div>
                        ))}

                        {/* Add more images placeholder */}
                        {imagePreviews.length < 5 && (
                          <div
                            className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg h-24 flex items-center justify-center cursor-pointer hover:border-primary-500 transition-colors"
                            onClick={() =>
                              document.getElementById("images").click()
                            }
                          >
                            <div className="text-center">
                              <i className="fas fa-plus text-gray-400 text-xl mb-1"></i>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                Add More
                              </p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Farm Information */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Farm Information
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label
                    htmlFor="farmLocation"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Farm Location
                  </label>
                  <input
                    type="text"
                    id="farmLocation"
                    name="farmLocation"
                    value={formData.farmLocation}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    placeholder="e.g., Sylhet, Bangladesh"
                  />
                </div>

                <div>
                  <label
                    htmlFor="harvestDate"
                    className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
                  >
                    Harvest Date
                  </label>
                  <input
                    type="date"
                    id="harvestDate"
                    name="harvestDate"
                    value={formData.harvestDate}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                </div>
              </div>
            </div>

            {/* Product Features */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                Product Features
              </h2>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {[
                  "organic",
                  "pesticide-free",
                  "fresh",
                  "non-gmo",
                  "local",
                  "sustainable",
                  "fair-trade",
                  "gluten-free",
                ].map((feature) => (
                  <label
                    key={feature}
                    className="flex items-center p-3 border border-gray-300 dark:border-gray-600 rounded-lg cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-700"
                  >
                    <input
                      type="checkbox"
                      name="features"
                      value={feature}
                      checked={formData.features.includes(feature)}
                      onChange={handleFeatureChange}
                      className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                    />
                    <span className="ml-2 text-sm capitalize">
                      {feature.replace("-", " ")}
                    </span>
                  </label>
                ))}
              </div>
            </div>

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className={`w-full py-3 rounded-lg font-semibold focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 transition ${
                  loading
                    ? "bg-gray-400 cursor-not-allowed text-white"
                    : "bg-primary-600 text-white hover:bg-primary-700"
                }`}
              >
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Adding Product...
                  </>
                ) : (
                  <>
                    <i className="fas fa-plus mr-2"></i>
                    Add Product
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </>
  );
}

// hooks/useDashboardData.js
"use client";

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useSession } from "next-auth/react";
import { apiService } from "@/lib/api-service";

export function useDashboardData() {
  const { data: session } = useSession();
  const queryClient = useQueryClient();

  const getUserIdentifiers = () => {
    if (!session?.user) return null;
    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  };

  const userIds = getUserIdentifiers();

  const { data, isLoading, error, refetch, isRefetching } = useQuery({
    queryKey: ["dashboard", userIds?.userId, userIds?.userEmail],
    queryFn: async () => {
      if (!userIds?.userId && !userIds?.userEmail) {
        throw new Error("No user identification found");
      }

      const dashboardData = await apiService.getDashboardData(
        userIds.userId,
        userIds.userEmail,
        { forceRefresh: true },
      );

      return {
        products: dashboardData.products || [],
        orders: dashboardData.orders || [],
        analytics: dashboardData.analytics || {},
        meta: dashboardData.meta || {},
      };
    },
    enabled: !!session?.user && (!!userIds?.userId || !!userIds?.userEmail),
    staleTime: 2 * 60 * 1000, // 2 minutes for dashboard data
    cacheTime: 5 * 60 * 1000, // 5 minutes cache
  });

  // Function to invalidate and refetch dashboard data
  const refreshDashboard = () => {
    return queryClient.invalidateQueries({
      queryKey: ["dashboard", userIds?.userId, userIds?.userEmail],
    });
  };

  // Function to manually refetch
  const refetchDashboard = () => {
    return refetch();
  };

  // Function to update specific order in cache without full refetch
  const updateOrderInCache = (orderId, newStatus, updatedOrder) => {
    queryClient.setQueryData(
      ["dashboard", userIds?.userId, userIds?.userEmail],
      (oldData) => {
        if (!oldData) return oldData;

        const updatedOrders = oldData.orders.map((order) =>
          order._id === orderId || order.id === orderId
            ? { ...order, status: newStatus, ...updatedOrder }
            : order,
        );

        return {
          ...oldData,
          orders: updatedOrders,
        };
      },
    );
  };

// hooks/useDashboardData.js
"use client";

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useSession } from "next-auth/react";
import { apiService } from "@/lib/api-service";

export function useDashboardData() {
  const { data: session } = useSession();
  const queryClient = useQueryClient();

  const getUserIdentifiers = () => {
    if (!session?.user) return null;
    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  };

  const userIds = getUserIdentifiers();

  const { data, isLoading, error, refetch, isRefetching } = useQuery({
    queryKey: ["dashboard", userIds?.userId, userIds?.userEmail],
    queryFn: async () => {
      if (!userIds?.userId && !userIds?.userEmail) {
        throw new Error("No user identification found");
      }

      const dashboardData = await apiService.getDashboardData(
        userIds.userId,
        userIds.userEmail,
        { forceRefresh: true },
      );

      return {
        products: dashboardData.products || [],
        orders: dashboardData.orders || [],
        analytics: dashboardData.analytics || {},
        meta: dashboardData.meta || {},
      };
    },
    enabled: !!session?.user && (!!userIds?.userId || !!userIds?.userEmail),
    staleTime: 2 * 60 * 1000, // 2 minutes for dashboard data
    cacheTime: 5 * 60 * 1000, // 5 minutes cache
  });

  // Function to invalidate and refetch dashboard data
  const refreshDashboard = () => {
    return queryClient.invalidateQueries({
      queryKey: ["dashboard", userIds?.userId, userIds?.userEmail],
    });
  };

  // Function to manually refetch
  const refetchDashboard = () => {
    return refetch();
  };

  // Function to update specific order in cache without full refetch
  const updateOrderInCache = (orderId, newStatus, updatedOrder) => {
    queryClient.setQueryData(
      ["dashboard", userIds?.userId, userIds?.userEmail],
      (oldData) => {
        if (!oldData) return oldData;

        const updatedOrders = oldData.orders.map((order) =>
          order._id === orderId || order.id === orderId
            ? { ...order, status: newStatus, ...updatedOrder }
            : order,
        );

        return {
          ...oldData,
          orders: updatedOrders,
        };
      },
    );
  };

  // Function to update specific product in cache without full refetch
  const updateProductInCache = (productId, updatedProduct) => {
    queryClient.setQueryData(
      ["dashboard", userIds?.userId, userIds?.userEmail],
      (oldData) => {
        if (!oldData) return oldData;

        const updatedProducts = oldData.products.map((product) =>
          product._id === productId || product.id === productId
            ? { ...product, ...updatedProduct }
            : product,
        );

        return {
          ...oldData,
          products: updatedProducts,
        };
      },
    );
  };

  return {
    products: data?.products || [],
    orders: data?.orders || [],
    analytics: data?.analytics || {},
    meta: data?.meta || {},
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateOrderInCache,
    updateProductInCache,
  };
}

// Cache utility for client-side data caching
class CacheManager {
  constructor() {
    this.cache = new Map();
    this.timestamps = new Map();
    this.defaultTTL = 5 * 60 * 1000; // 5 minutes default TTL
  }

  // Generate cache key from parameters
  generateKey(baseKey, params = {}) {
    const sortedParams = Object.keys(params)
      .sort()
      .reduce((result, key) => {
        result[key] = params[key];
        return result;
      }, {});
    return `${baseKey}_${JSON.stringify(sortedParams)}`;
  }

  // Set cache entry with TTL
  set(key, data, ttl = this.defaultTTL) {
    this.cache.set(key, data);
    this.timestamps.set(key, Date.now() + ttl);

    // Clean up expired entries periodically
    this.cleanup();
  }

  // Get cache entry if not expired
  get(key) {
    const timestamp = this.timestamps.get(key);
    if (!timestamp || Date.now() > timestamp) {
      this.cache.delete(key);
      this.timestamps.delete(key);
      return null;
    }
    return this.cache.get(key);
  }

  // Check if cache has valid entry
  has(key) {
    const timestamp = this.timestamps.get(key);
    if (!timestamp || Date.now() > timestamp) {
      this.cache.delete(key);
      this.timestamps.delete(key);
      return false;
    }
    return this.cache.has(key);
  }

  // Clear specific entry
  delete(key) {
    this.cache.delete(key);
    this.timestamps.delete(key);
  }

  // Clear all cache
  clear() {
    this.cache.clear();
    this.timestamps.clear();
  }

  // Clean up expired entries
  cleanup() {
    const now = Date.now();
    for (const [key, timestamp] of this.timestamps.entries()) {
      if (now > timestamp) {
        this.cache.delete(key);
        this.timestamps.delete(key);
      }
    }
  }

  // Get cache stats
  getStats() {
    return {
      size: this.cache.size,
      keys: Array.from(this.cache.keys()),
    };
  }
}

// Global cache instance
const globalCache = new CacheManager();

export default globalCache;

// Session storage cache for persistence across page reloads
export class SessionCache {
  constructor(prefix = "farmfresh_") {
    this.prefix = prefix;
    this.memoryCache = new Map(); // In-memory cache for large items
  }

  generateKey(baseKey, params = {}) {
    const sortedParams = Object.keys(params)
      .sort()
      .reduce((result, key) => {
        result[key] = params[key];
        return result;
      }, {});
    return `${this.prefix}${baseKey}_${JSON.stringify(sortedParams)}`;
  }

  set(key, data, ttl = 5 * 60 * 1000) {
    if (typeof window === "undefined") return;

    const item = {
      data,
      timestamp: Date.now() + ttl,
    };

    // Check data size and use appropriate storage strategy
    const serializedItem = JSON.stringify(item);
    const sizeInMB = new Blob([serializedItem]).size / (1024 * 1024);

    try {
      // If data is too large (>2MB), use memory cache only
      if (sizeInMB > 2) {
        console.warn(
          `Data too large for sessionStorage (${sizeInMB.toFixed(2)}MB), using memory cache`,
        );
        this.memoryCache.set(key, item);
        return;
      }

      sessionStorage.setItem(key, serializedItem);
    } catch (e) {
      if (e.name === "QuotaExceededError") {
        console.warn(
          "SessionStorage quota exceeded, using memory cache and clearing old entries",
        );
        // Clear old entries
        this.cleanup();

        // Store in memory cache instead
        this.memoryCache.set(key, item);

        // Try to store smaller items in sessionStorage after cleanup
        try {
          if (sizeInMB < 1) {
            sessionStorage.setItem(key, serializedItem);
          }
        } catch (e2) {
          console.warn("Still cannot store in sessionStorage after cleanup");
        }
      } else {
        console.error("Failed to store in sessionStorage:", e);
      }
    }
  }

  get(key) {
    if (typeof window === "undefined") return null;

    // Check memory cache first
    if (this.memoryCache.has(key)) {
      const item = this.memoryCache.get(key);
      if (item.timestamp > Date.now()) {
        return item.data;
      } else {
        this.memoryCache.delete(key);
      }
    }

    // Then check sessionStorage
    try {
      const item = sessionStorage.getItem(key);
      if (!item) return null;

      const parsed = JSON.parse(item);
      if (parsed.timestamp > Date.now()) {
        return parsed.data;
      } else {
        sessionStorage.removeItem(key);
        return null;
      }
    } catch (e) {
      console.error("Failed to retrieve from sessionStorage:", e);
      sessionStorage.removeItem(key);
      return null;
    }
  }

  has(key) {
    return this.get(key) !== null;
  }

  delete(key) {
    if (typeof window === "undefined") return;
    sessionStorage.removeItem(key);
  }

  cleanup() {
    if (typeof window === "undefined") return;

    const keysToRemove = [];
    for (let i = 0; i < sessionStorage.length; i++) {
      const key = sessionStorage.key(i);
      if (key && key.startsWith(this.prefix)) {
        try {
          const item = JSON.parse(sessionStorage.getItem(key));
          if (Date.now() > item.timestamp) {
            keysToRemove.push(key);
          }
        } catch (e) {
          keysToRemove.push(key);
        }
      }
    }

    keysToRemove.forEach((key) => sessionStorage.removeItem(key));
  }
}

export const sessionCache = new SessionCache();

