/**
 * Calculate average rating and review count from reviews array
 * @param {Array} reviews - Array of review objects with rating property
 * @returns {Object} - Object containing averageRating and reviewCount
 */
export function calculateRatingStats(reviews) {
  if (!reviews || !Array.isArray(reviews) || reviews.length === 0) {
    return {
      averageRating: 0,
      reviewCount: 0,
    };
  }

  const totalRating = reviews.reduce((sum, review) => {
    const rating = Number(review.rating);
    return sum + (isNaN(rating) ? 0 : rating);
  }, 0);

  const averageRating = Math.round((totalRating / reviews.length) * 10) / 10;

  return {
    averageRating: averageRating,
    reviewCount: reviews.length,
  };
}

/**
 * Enhance product data with calculated rating statistics
 * @param {Object} product - Product object
 * @returns {Object} - Enhanced product object with calculated ratings
 */
export function enhanceProductWithRatings(product) {
  const { averageRating, reviewCount } = calculateRatingStats(product.reviews);

  return {
    ...product,
    averageRating,
    reviewCount: reviewCount,
  };
}

/**
 * Enhance multiple products with calculated rating statistics from database
 * @param {Array} products - Array of product objects
 * @param {Object} db - MongoDB database instance
 * @returns {Array} - Array of enhanced product objects
 */
export async function enhanceProductsWithRatings(products, db) {
  if (!Array.isArray(products) || products.length === 0) {
    return [];
  }

  try {
    // Get all reviews from the separate reviews collection
    const separateReviews = await db.collection("reviews").find({}).toArray();

    // Group separate reviews by product ID
    const reviewsByProduct = {};
    separateReviews.forEach((review) => {
      const productId = review.productId;
      if (!reviewsByProduct[productId]) {
        reviewsByProduct[productId] = [];
      }
      reviewsByProduct[productId].push(review);
    });

    // Enhance each product with its review data
    const enhancedProducts = products.map((product) => {
      const productId = product._id.toString();

      // Check for reviews in two places:
      // 1. Reviews stored inside the product document (old products)
      // 2. Reviews in the separate reviews collection (new products)
      let productReviews = [];

      // First, check if reviews are stored in the product document itself
      if (product.reviews && Array.isArray(product.reviews)) {
        productReviews = product.reviews;
      }
      // Second, check the separate reviews collection
      else if (reviewsByProduct[productId]) {
        productReviews = reviewsByProduct[productId];
      }

      const { averageRating, reviewCount } =
        calculateRatingStats(productReviews);

      return {
        ...product,
        averageRating,
        reviewCount,
        totalRatings: reviewCount, // Add totalRatings for compatibility with existing code
        reviews: productReviews, // Include reviews if needed
      };
    });

    return enhancedProducts;
  } catch (error) {
    console.error("Error enhancing products with ratings:", error);
    // Return products with default rating values if enhancement fails
    return products.map((product) => ({
      ...product,
      averageRating: 0,
      reviewCount: 0,
      totalRatings: 0,
    }));
  }
}

/**
 * Calculate average rating and review count from reviews array
 * @param {Array} reviews - Array of review objects with rating property
 * @returns {Object} - Object containing averageRating and reviewCount
 */
export function calculateRatingStats(reviews) {
  if (!reviews || !Array.isArray(reviews) || reviews.length === 0) {
    return {
      averageRating: 0,
      reviewCount: 0,
    };
  }

  const totalRating = reviews.reduce((sum, review) => {
    const rating = Number(review.rating);
    return sum + (isNaN(rating) ? 0 : rating);
  }, 0);

  const averageRating = Math.round((totalRating / reviews.length) * 10) / 10;

  return {
    averageRating: averageRating,
    reviewCount: reviews.length,
  };
}

/**
 * Enhance product data with calculated rating statistics
 * @param {Object} product - Product object
 * @returns {Object} - Enhanced product object with calculated ratings
 */
export function enhanceProductWithRatings(product) {
  const { averageRating, reviewCount } = calculateRatingStats(product.reviews);

  return {
    ...product,
    averageRating,
    reviewCount: reviewCount,
  };
}

/**
 * Enhance multiple products with calculated rating statistics from database
 * @param {Array} products - Array of product objects
 * @param {Object} db - MongoDB database instance
 * @returns {Array} - Array of enhanced product objects
 */
export async function enhanceProductsWithRatings(products, db) {
  if (!Array.isArray(products) || products.length === 0) {
    return [];
  }

  try {
    // Get product IDs to fetch full product data with reviews
    const productIds = products.map((product) => product._id);

    // Fetch full product documents including reviews from the products collection
    const fullProducts = await db
      .collection("products")
      .find({
        _id: { $in: productIds },
        status: { $ne: "deleted" },
      })
      .toArray();

    // Create a map of full product data for easy lookup
    const fullProductMap = {};
    fullProducts.forEach((product) => {
      fullProductMap[product._id.toString()] = product;
    });

    // Enhance each product with its review data
    const enhancedProducts = products.map((product) => {
      const productId = product._id.toString();
      const fullProduct = fullProductMap[productId];

      // Check for reviews in the full product document (this is where reviews are stored)
      let productReviews = [];

      // First, check the full product document from database
      if (
        fullProduct &&
        fullProduct.reviews &&
        Array.isArray(fullProduct.reviews)
      ) {
        productReviews = fullProduct.reviews;
      }
      // Fallback: check if reviews are already in the current product object
      else if (product.reviews && Array.isArray(product.reviews)) {
        productReviews = product.reviews;
      }

      const { averageRating, reviewCount } = calculateRatingStats(productReviews);

      return {
        ...product,
        averageRating,
        reviewCount,
        totalRatings: reviewCount, // Add totalRatings for compatibility with existing code
        totalReviews: reviewCount, // Add totalReviews for compatibility
        // Don't include the full reviews array in product cards to keep response size manageable
        // reviews: productReviews, // Uncomment if you need the full reviews in the response
      };
    });

    return enhancedProducts;
  } catch (error) {
    console.error("Error enhancing products with ratings:", error);
    // Return products with default rating values if enhancement fails
    return products.map((product) => ({
      ...product,
      averageRating: 0,
      reviewCount: 0,
      totalRatings: 0,
      totalReviews: 0,
    }));
  }
}

// Add reviews to ALL products that don't have any reviews
const { MongoClient, ObjectId } = require('mongodb');

async function addReviewsToAllProducts() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  // Get ALL products
  const allProducts = await db.collection('products').find({}).toArray();
  console.log(`Found ${allProducts.length} total products`);

  // Get all existing reviews
  const existingReviews = await db.collection('reviews').find({}).toArray();
  const reviewedProductIds = [...new Set(existingReviews.map(r => r.productId))];
  console.log(`Found ${existingReviews.length} existing reviews for ${reviewedProductIds.length} products`);

  // Find products that have NO reviews
  const productsWithoutReviews = allProducts.filter(product => 
    !reviewedProductIds.includes(product._id.toString())
  );

  console.log(`Found ${productsWithoutReviews.length} products WITHOUT reviews`);
  console.log('Products without reviews:');
  productsWithoutReviews.forEach(p => console.log(`- ${p.name} (ID: ${p._id})`));

  // Add sample reviews to products without reviews
  for (const product of productsWithoutReviews) {
    const ratings = [4, 4.5, 5, 3.5, 4.5]; // Random good ratings
    const numReviews = Math.floor(Math.random() * 3) + 2; // 2-4 reviews per product

    const sampleReviews = [];
    for (let i = 0; i < numReviews; i++) {
      const rating = ratings[Math.floor(Math.random() * ratings.length)];
      sampleReviews.push({
        productId: product._id.toString(),
        userId: `sample-user-${i + 1}`,
        reviewer: ['John Doe', 'Jane Smith', 'Mike Johnson', 'Sarah Wilson', 'David Brown'][i] || 'Anonymous',
        rating: rating,
        comment: [
          `Great quality ${product.name}! Fresh and delicious.`,
          `Excellent ${product.name}. Highly recommended!`,
          `Good quality ${product.name}. Will buy again.`,
          `Amazing ${product.name}! Worth every penny.`,
          `Perfect ${product.name}. Fast delivery too!`
        ][i] || `Nice ${product.name}!`,
        createdAt: new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000), // Random date within last 30 days
        updatedAt: new Date()
      });
    }

    await db.collection('reviews').insertMany(sampleReviews);
    console.log(` Added ${numReviews} reviews for ${product.name}`);
  }

  console.log(` Successfully added reviews to ${productsWithoutReviews.length} products!`);
  await client.close();
}

addReviewsToAllProducts().catch(console.error);

