// Add sample reviews to existing products
const { MongoClient, ObjectId } = require('mongodb');

async function addSampleReviews() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  // Get the first 3 existing products
  const products = await db.collection('products').find({}).limit(3).toArray();

  console.log('Adding sample reviews to existing products...');

  for (const product of products) {
    // Add 2-3 sample reviews for each product
