    const salesData = await db
      .collection("orders")
      .aggregate([
        {
          $match: {
            status: { $in: ["completed", "delivered", "shipped"] },
          },
        },
        { $unwind: "$items" },
        {
          $match: {
            $or: [
              { "items.productId": productId },
              { "items.productId": productIdQuery },
              { "items.productId": productId.toString() },
            ],
          },
        },
        {
          $group: {
            _id: null,
            totalSales: { $sum: "$items.quantity" },
            totalRevenue: {
              $sum: { $multiply: ["$items.quantity", "$items.price"] },
            },
            totalOrders: { $sum: 1 },
          },
        },
      ])
      .toArray();

    // Calculate review metrics
    const reviewsData = await db
      .collection("reviews")
      .aggregate([
        {
          $match: {
            $or: [
              { productId: productId },
              { productId: productIdQuery },
              { productId: productId.toString() },
            ],
          },
        },
        {
          $group: {
            _id: null,
            averageRating: { $avg: "$rating" },
            totalReviews: { $sum: 1 },
          },
        },
      ])
      .toArray();

    // Get recent orders count (last 30 days)
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const recentOrdersData = await db
      .collection("orders")
      .aggregate([
        {
          $match: {
            $or: [
              { createdAt: { $gte: thirtyDaysAgo.toISOString() } },
              { createdAt: { $gte: thirtyDaysAgo } },
            ],
            status: { $in: ["completed", "shipped", "delivered"] },
          },
        },
        { $unwind: "$items" },
        {
          $match: {
            $or: [
              { "items.productId": productId },
              { "items.productId": productIdQuery },
              { "items.productId": productId.toString() },
            ],
          },
        },
        {
          $group: {
            _id: null,
            recentSales: { $sum: "$items.quantity" },
            recentRevenue: {
              $sum: { $multiply: ["$items.quantity", "$items.price"] },
            },
          },
        },
      ])
      .toArray();

    const salesMetrics = salesData[0] || {};
    const reviewsMetrics = reviewsData[0] || {};
    const recentMetrics = recentOrdersData[0] || {};

    return {
      totalSales: salesMetrics.totalSales || 0,
      totalRevenue: salesMetrics.totalRevenue || 0,
      totalOrders: salesMetrics.totalOrders || 0,
      averageRating: reviewsMetrics.averageRating || 0,
      totalReviews: reviewsMetrics.totalReviews || 0,
      recentSales: recentMetrics.recentSales || 0,
      recentRevenue: recentMetrics.recentRevenue || 0,
      // Add some calculated metrics
      averageOrderValue:
        salesMetrics.totalOrders > 0
          ? salesMetrics.totalRevenue / salesMetrics.totalOrders
          : 0,
      salesTrend: recentMetrics.recentSales > 0 ? "up" : "stable",
    };
  } catch (error) {
    console.error("Error calculating product performance:", error);
    return {
      totalSales: 0,
      totalRevenue: 0,
      totalOrders: 0,
      averageRating: 0,
      totalReviews: 0,
      recentSales: 0,
      recentRevenue: 0,
      averageOrderValue: 0,
      salesTrend: "stable",
    };
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { enhanceProductsWithRatings } from "@/lib/reviewUtils";

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);
    const search = searchParams.get("search");
    const category = searchParams.get("category");
    const featured = searchParams.get("featured");
    const sortBy = searchParams.get("sortBy");
    const limit = searchParams.get("limit")
      ? parseInt(searchParams.get("limit"))
      : null;
    const page = parseInt(searchParams.get("page")) || 1;

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Try to get products directly from the collection first
    let allProducts = await db.collection("products").find({}).toArray();

    // If no direct products found, try the nested structure
    if (allProducts.length === 0) {
      const productDocuments = await db
        .collection("products")
        .find({})
        .toArray();
      productDocuments.forEach((doc) => {
        if (doc.products && Array.isArray(doc.products)) {
          allProducts = allProducts.concat(doc.products);
        }
      });
    }

    // Apply search filter
    if (search) {
      const searchRegex = new RegExp(search, "i");
      allProducts = allProducts.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description) ||
          searchRegex.test(product.category),
      );
    }

    // Apply category filter
    if (category && category !== "All Categories") {
      allProducts = allProducts.filter(
        (product) => product.category === category,
      );
    }

    // Apply featured filter
    if (featured === "true") {
      allProducts = allProducts.filter((product) => product.featured);
    }

    // Sort products based on sortBy parameter
    if (sortBy) {
      switch (sortBy) {
        case "purchases":
          allProducts.sort(
            (a, b) => (b.purchaseCount || 0) - (a.purchaseCount || 0),
          );
          break;
        case "newest":
          allProducts.sort(
            (a, b) =>
              new Date(b.createdAt || b.dateAdded) -
              new Date(a.createdAt || a.dateAdded),
          );
          break;
        case "price-low":
          allProducts.sort((a, b) => (a.price || 0) - (b.price || 0));
          break;
        case "price-high":
          allProducts.sort((a, b) => (b.price || 0) - (a.price || 0));
          break;
        case "rating":
          allProducts.sort(
            (a, b) => (b.averageRating || 0) - (a.averageRating || 0),
          );
          break;
        default:
          // Default to newest
          allProducts.sort(
            (a, b) =>
              new Date(b.createdAt || b.dateAdded) -
              new Date(a.createdAt || a.dateAdded),
          );
      }
    }

    // Enhance products with ratings
    console.log(`Enhancing ${allProducts.length} products with ratings...`);
    allProducts = await enhanceProductsWithRatings(allProducts, db);

    // Debug: Log a sample of enhanced products
    console.log("Sample enhanced products:");
    allProducts.slice(0, 3).forEach((p) => {
      console.log(
        `- ${p.name}: ${p.averageRating} rating, ${p.reviewCount} reviews (ID: ${p._id})`,
      );
    });

    // Apply pagination
    const itemsPerPage = 12;
    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = limit
      ? Math.min(startIndex + (limit || itemsPerPage), allProducts.length)
      : startIndex + itemsPerPage;

    const paginatedProducts = limit
      ? allProducts.slice(0, limit)
      : allProducts.slice(startIndex, endIndex);

    const totalPages = Math.ceil(allProducts.length / itemsPerPage);

    return NextResponse.json({
      products: paginatedProducts,
      pagination: {
        currentPage: page,
        totalPages,
        totalProducts: allProducts.length,
        hasNext: endIndex < allProducts.length,
        hasPrev: page > 1,
      },
    });
  } catch (error) {
    console.error("Error fetching products:", error);
    return NextResponse.json(
      { error: "Failed to fetch products" },
      { status: 500 },
    );
  }
}

export async function POST(request) {
  try {
    const productData = await request.json();

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Add timestamps and default values
    const newProduct = {
      ...productData,
      createdAt: new Date(),
      updatedAt: new Date(),
      purchaseCount: 0,
      featured: false,
      status: "active",
      stock: productData.stock || 0,
      averageRating: 0,
      totalRatings: 0,
    };

    const result = await db.collection("products").insertOne(newProduct);

    return NextResponse.json({
      success: true,
      productId: result.insertedId,
      product: newProduct,
    });
  } catch (error) {
    console.error("Error creating product:", error);
    return NextResponse.json(
      { error: "Failed to create product" },
      { status: 500 },
    );
  }
}

