"use client";

import Link from "next/link";
import Image from "next/image";
import { useState, useEffect } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import { useSession } from "next-auth/react";
import { useCart } from "@/contexts/CartContext";
import { useFavorites } from "@/contexts/FavoritesContext";
import ProductCard from "@/components/ProductCard";
import StarRating from "@/components/StarRating";
import Footer from "@/components/Footer";
import RecentOrdersSection from "@/components/RecentOrdersSection";
import FarmerProfileView from "@/components/FarmerProfileView";
import useProductData from "@/hooks/useProductData";
import useOwnership from "@/hooks/useOwnership";
import useReviews from "@/hooks/useReviews";

import Loading from "@/components/Loading";
import NotFound from "@/components/NotFound";

export default function ProductDetails() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const productId = searchParams.get("id");
  const viewMode = searchParams.get("view");
  const { data: session } = useSession();
  const { addToCart } = useCart();
  const { addToFavorites, removeFromFavorites, isProductFavorited } =
    useFavorites();

  // Custom hooks
  const {
    product,
    farmer,
    farmerProducts,
    responseType,
    relatedProducts,
    loading,
    fetchProductDetails,
  } = useProductData(productId);

  const { reviews, hasMoreReviews, fetchReviews, reviewsPage } = useReviews(
    productId,
    responseType,
    session?.user?.id, // Pass userId to prioritize user's review
  );
  const isOwner = useOwnership(product, session, viewMode);

  // State management
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState("description");
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  // Review form state
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({ rating: 5, comment: "" });
  const [isSubmittingReview, setIsSubmittingReview] = useState(false);

  // Review edit/delete state
  const [editingReview, setEditingReview] = useState(null);
  const [isUpdatingReview, setIsUpdatingReview] = useState(false);
  const [isDeletingReview, setIsDeletingReview] = useState(false);

  // Purchase verification state
  const [hasPurchasedProduct, setHasPurchasedProduct] = useState(false);
  const [checkingPurchase, setCheckingPurchase] = useState(false);

  // Farmer-specific states
  const [stockUpdate, setStockUpdate] = useState("");
  const [priceUpdate, setPriceUpdate] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [recentOrders, setRecentOrders] = useState([]);
  const [loadingOrders, setLoadingOrders] = useState(false);

  // Effects
  useEffect(() => {
    if (productId) {
      fetchProductDetails();
    }
  }, [productId]);

  useEffect(() => {
    if (productId && isOwner && viewMode !== "customer") {
      const interval = setInterval(() => {
        fetchProductDetails();
      }, 30000);
      return () => clearInterval(interval);
    }
  }, [productId, isOwner, viewMode]);

  useEffect(() => {
    if (productId && isOwner && viewMode !== "customer") {
      fetchRecentOrders();
    }
  }, [productId, isOwner, viewMode]);

  // Check if product is favorited when productId changes
  useEffect(() => {
    if (productId) {
      setIsFavorite(isProductFavorited(productId));
    }
  }, [productId, isProductFavorited]);

  // Check if user has purchased this product
  useEffect(() => {
    if (session?.user?.id && productId) {
      checkUserPurchase();
    }
  }, [session?.user?.id, productId]);

  const checkUserPurchase = async () => {
    if (!session?.user?.id) return;

    setCheckingPurchase(true);
    try {
      const response = await fetch(
        `/api/orders?userId=${session.user.id}&productId=${productId}`,
      );
      if (response.ok) {
        const data = await response.json();
        // Check if user has any order with this product that's confirmed/delivered/pending
        const hasPurchased = data.orders?.some(
          (order) =>
            order.items?.some((item) => item.productId === productId) &&
            ["delivered", "confirmed", "pending"].includes(order.status),
        );
        setHasPurchasedProduct(hasPurchased);
      }
    } catch (error) {
      console.error("Error checking purchase history:", error);
      setHasPurchasedProduct(false);
    } finally {
      setCheckingPurchase(false);
    }
  };

  // API calls
  const fetchRecentOrders = async () => {
    try {
      setLoadingOrders(true);
      const response = await fetch(
        `/api/orders?productId=${productId}&limit=5`,
      );

      if (response.ok) {
        const data = await response.json();
        setRecentOrders(data.orders || []);
      }
    } catch (error) {
      console.error("Error fetching recent orders:", error);
    } finally {
      setLoadingOrders(false);
    }
  };

  // Event handlers
  const handleAddToCart = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      const item = {
        productId: productId,
        id: productId, // Add id field for cart context
        name: product.name,
        price: product.price,
        quantity: quantity,
        stock: product.stock, // Include stock information
        image:
          product.image ||
          (product.images && product.images[0]) ||
          "/placeholder-image.jpg",
        unit: product.unit || "kg",
        farmerId: product.farmerId,
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer",
      };

      await addToCart(item, quantity);
      alert("Product added to cart successfully!");
    } catch (error) {
      console.error("Error adding to cart:", error);
      // Show user-friendly error message for stock issues
      if (
        error.message.includes("Only") &&
        error.message.includes("available in stock")
      ) {
        alert(error.message);
      } else {
        alert("Failed to add product to cart. Please try again.");
      }
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleBuyNow = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Create a product object that matches the CartContext expectations
      const productForCart = {
        id: productId,
        name: product.name,
        price: product.price,
        image:
          product.image ||
          (product.images && product.images[0]) ||
          "/placeholder-image.jpg",
        unit: product.unit || "kg",
        farmerId: product.farmerId,
        farmer: {
          id: product.farmerId,
          _id: product.farmerId,
          email: product.farmer?.email,
          name:
            product.farmer?.name ||
            product.farmer?.farmName ||
            "Unknown Farmer",
        },
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer",
        stock: product.stock || 0,
      };

      // Use CartContext's addToCart function
      await addToCart(productForCart, quantity);

      // Immediately redirect to payment - let the payment page handle the cart state
      // The payment page should wait for cart loading to complete before checking if empty
      router.push("/payment");
    } catch (error) {
      console.error("Error processing buy now:", error);
      alert(error.message || "Failed to process order. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleFavoriteToggle = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    try {
      if (isFavorite) {
        // Remove from favorites
        const success = await removeFromFavorites(productId);
        if (success) {
          setIsFavorite(false);
          alert("Product removed from favorites!");
        } else {
          alert("Failed to remove from favorites. Please try again.");
        }
      } else {
        // Add to favorites
        const success = await addToFavorites(productId);
        if (success) {
          setIsFavorite(true);
          alert("Product added to favorites!");
        } else {
          alert("Failed to add to favorites. Please try again.");
        }
      }
    } catch (error) {
      console.error("Error toggling favorite:", error);
      alert("Failed to update favorites. Please try again.");
    }
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (!session) {
      alert("Please login to submit a review");
      return;
    }

    setIsSubmittingReview(true);
    try {
      // Include userId in the review submission
      const reviewData = {
        ...reviewForm,
        userId:
          session.user.id ||
          session.user._id ||
          session.user.userId ||
          session.user.email,
      };

      const response = await fetch(`/api/products/${productId}/reviews`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(reviewData),
      });

      if (response.ok) {
        setShowReviewForm(false);
        setReviewForm({ rating: 5, comment: "" });
        fetchReviews();
        fetchProductDetails();
        alert("Review submitted successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to submit review");
      }
    } catch (error) {
      console.error("Error submitting review:", error);
      alert("Failed to submit review");
    } finally {
      setIsSubmittingReview(false);
    }
  };

  const handleUpdateReview = async () => {
    if (!editingReview) return;

    setIsUpdatingReview(true);
    try {
      const response = await fetch(`/api/reviews/${editingReview._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          rating: reviewForm.rating,
          comment: reviewForm.comment,
          userId:
            session.user.userId ||
            session.user.id ||
            session.user._id ||
            session.user.email,
        }),
      });

      if (response.ok) {
        setEditingReview(null);
        setReviewForm({ rating: 5, comment: "" });
        setShowReviewForm(false);
        fetchReviews();
        fetchProductDetails();
        alert("Review updated successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update review");
      }
    } catch (error) {
      console.error("Error updating review:", error);
      alert("Failed to update review");
    } finally {
      setIsUpdatingReview(false);
    }
  };

  const handleDeleteReview = async (reviewId) => {
    if (!confirm("Are you sure you want to delete this review?")) {
      return;
    }

    setIsDeletingReview(true);
    try {
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;
      const response = await fetch(
        `/api/reviews/${reviewId}?userId=${encodeURIComponent(userId)}`,
        {
          method: "DELETE",
        },
      );

      if (response.ok) {
        fetchReviews();
        fetchProductDetails();
        alert("Review deleted successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to delete review");
      }
    } catch (error) {
      console.error("Error deleting review:", error);
      alert("Failed to delete review");
    } finally {
      setIsDeletingReview(false);
    }
  };

  const loadMoreReviews = () => {
    fetchReviews(reviewsPage + 1, true);
  };

  const handleUpdateProduct = async () => {
    if (!isOwner) return;

    if (!stockUpdate && !priceUpdate) {
      alert("Please enter a value to update");
      return;
    }

    setIsUpdating(true);
    try {
      const updateData = {};

      if (stockUpdate && stockUpdate.trim() !== "") {
        const stockValue = parseInt(stockUpdate);
        if (isNaN(stockValue) || stockValue < 0) {
          alert("Please enter a valid stock number");
          setIsUpdating(false);
          return;
        }
        updateData.stock = stockValue;
      }

      if (priceUpdate && priceUpdate.trim() !== "") {
        const priceValue = parseFloat(priceUpdate);
        if (isNaN(priceValue) || priceValue <= 0) {
          alert("Please enter a valid price");
          setIsUpdating(false);
          return;
        }
        updateData.price = priceValue;
      }

      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        alert("Product updated successfully!");
        fetchProductDetails();
        setStockUpdate("");
        setPriceUpdate("");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update product");
      }
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleToggleStatus = async () => {
    if (!isOwner) return;

    const newStatus = product.status === "active" ? "inactive" : "active";
    const actionText = newStatus === "active" ? "activate" : "deactivate";

    if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        alert(`Product ${actionText}d successfully!`);
        fetchProductDetails();
      } else {
        const error = await response.json();
        alert(error.error || `Failed to ${actionText} product`);
      }
    } catch (error) {
      console.error(`Error ${actionText}ing product:`, error);
      alert(`Failed to ${actionText} product. Please try again.`);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleDeleteProduct = async () => {
    if (!isOwner) return;

    if (
      !confirm(
        " Are you sure you want to delete this product?\n\nThis action cannot be undone and will remove:\n The product listing\n All associated data\n Product from any pending orders",
      )
    ) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Product deleted successfully!");
        window.location.href = "/manage";
      } else {
        const error = await response.json();
        if (response.status === 409) {
          alert(
            " Cannot Delete Product\n\nThis product has pending orders and cannot be deleted.\nPlease wait for all orders to be completed or cancelled before deleting this product.\n\nYou can temporarily deactivate the product instead.",
          );
        } else {
          alert(error.error || "Failed to delete product");
        }
      }
    } catch (error) {
      console.error("Error deleting product:", error);
      alert("Failed to delete product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAddImages = () => {
    router.push(`/create?edit=${productId}`);
  };

  // Helper function to get all images
  const getAllImages = () => {
    const allImages = [];
    if (product?.image) {
      allImages.push(product.image);
    }
    if (product?.images && product.images.length > 0) {
      allImages.push(...product.images);
    }
    return allImages;
  };

  // Calculate rating distribution from reviews
  const calculateRatingDistribution = () => {
    if (!reviews || reviews.length === 0) {
      return { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
    }

    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };

    reviews.forEach((review) => {
      const rating = Math.floor(review.rating); // Round down to nearest integer
      if (rating >= 1 && rating <= 5) {
        distribution[rating]++;
      }
    });

    return distribution;
  };

  const getRatingPercentage = (rating, distribution) => {
    const totalReviews = reviews?.length || 0;
    if (totalReviews === 0) return 0;
    return (distribution[rating] / totalReviews) * 100;
  };

  // Render components based on state
  if (loading) {
    return <Loading />;
  }

  if (responseType === "farmer" && farmer) {
    return (
      <FarmerProfileView farmer={farmer} farmerProducts={farmerProducts} />
    );
  }

  if (!product && !farmer) {
    return <NotFound responseType={responseType} />;
  }

  // Main product details render
  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Farmer Dashboard View */}
          {isOwner && viewMode !== "customer" ? (
            <>
              {/* Farmer Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/manage" className="hover:text-primary-600">
                      <i className="fas fa-tachometer-alt mr-1"></i>
                      Dashboard
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name} - Management
                    </span>
                  </li>
                </ol>
              </nav>

              {/* Customer View Notice */}
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <i className="fas fa-info-circle text-blue-600 dark:text-blue-400 mr-2"></i>
                    <span className="text-blue-800 dark:text-blue-200">
                      You are viewing this as the product owner.
                    </span>
                  </div>
                  <Link
                    href={`/details?id=${productId}&view=customer`}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                  >
                    <i className="fas fa-eye mr-1"></i>
                    View as Customer
                  </Link>
                </div>
              </div>

              {/* Farmer Product Management Header */}
              <div className="bg-gradient-to-r from-green-600 to-emerald-600 rounded-2xl shadow-lg p-8 mb-8 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-3xl font-bold mb-2">
                      Product Management
                    </h1>
                    <p className="text-green-100">
                      Manage your product listing and inventory
                    </p>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-green-100 mb-1">
                      Product Status
                    </div>
                    <div
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                        product.status === "active"
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      <i
                        className={`fas ${product.status === "active" ? "fa-check-circle" : "fa-times-circle"} mr-1`}
                      ></i>
                      {product.status === "active" ? "Active" : "Inactive"}
                    </div>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Product Images & Info */}
                <div className="lg:col-span-2 space-y-6">
                  {/* Image Management */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Images
                      </h3>
                      <button
                        onClick={handleAddImages}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-plus mr-1"></i>
                        Add Images
                      </button>
                    </div>

                    {/* Current Images Display */}
                    {(() => {
                      const allImages = getAllImages();

                      return allImages.length > 0 ? (
                        <div className="space-y-4">
                          <div className="aspect-video max-w-md bg-gray-100 dark:bg-gray-700 rounded-lg overflow-hidden">
                            <Image
                              src={allImages[selectedImage]}
                              alt={product.name}
                              width={400}
                              height={225}
                              className="w-full h-full object-cover"
                            />
                          </div>
                          {allImages.length > 1 && (
                            <div className="grid grid-cols-5 gap-2 max-w-md">
                              {allImages.map((image, index) => (
                                <button
                                  key={index}
                                  onClick={() => setSelectedImage(index)}
                                  className={`aspect-square rounded-lg overflow-hidden border-2 ${
                                    selectedImage === index
                                      ? "border-primary-500"
                                      : "border-gray-300 dark:border-gray-600"
                                  }`}
                                >
                                  <Image
                                    src={image}
                                    alt={`${product.name} ${index + 1}`}
                                    width={80}
                                    height={80}
                                    className="w-full h-full object-cover"
                                  />
                                </button>
                              ))}
                            </div>
                          )}
                        </div>
                      ) : (
                        <div className="text-center py-12 bg-gray-50 dark:bg-gray-700 rounded-lg">
                          <i className="fas fa-image text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            No images uploaded
                          </p>
                        </div>
                      );
                    })()}
                  </div>

                  {/* Product Information */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Information
                      </h3>
                      <Link
                        href={`/create?edit=${productId}`}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-edit mr-1"></i>
                        Edit Details
                      </Link>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          {product.name}
                        </h2>
                        <p className="text-gray-600 dark:text-gray-400">
                          {product.category}
                        </p>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Price
                          </span>
                          <p className="text-lg font-semibold text-gray-900 dark:text-white">
                            ${product.price?.toFixed(2)} per{" "}
                            {product.unit || "kg"}
                          </p>
                        </div>
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Stock
                          </span>
                          <p className="text-lg font-semibold text-gray-900 dark:text-white">
                            {product.stock} {product.unit || "kg"}
                          </p>
                        </div>
                      </div>

                      <div>
                        <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                          Description
                        </span>
                        <p className="text-gray-700 dark:text-gray-300 mt-1">
                          {product.description || "No description provided."}
                        </p>
                      </div>

                      {product.features && (
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Features
                          </span>
                          <ul className="list-disc list-inside text-gray-700 dark:text-gray-300 mt-1">
                            {product.features.map((feature, index) => (
                              <li key={index}>{feature}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Quick Actions */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
                      Quick Updates
                    </h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      {/* Stock Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Stock
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={stockUpdate}
                            onChange={(e) => setStockUpdate(e.target.value)}
                            placeholder="New stock amount"
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={isUpdating || !stockUpdate}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition disabled:bg-gray-400"
                          >
                            Update
                          </button>
                        </div>
                      </div>

                      {/* Price Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Price
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            step="0.01"
                            value={priceUpdate}
                            onChange={(e) => setPriceUpdate(e.target.value)}
                            placeholder="New price"
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={isUpdating || !priceUpdate}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition disabled:bg-gray-400"
                          >
                            Update
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Recent Orders */}
                  <RecentOrdersSection
                    recentOrders={recentOrders}
                    loadingOrders={loadingOrders}
                    product={product}
                  />
                </div>

                {/* Sidebar - Analytics & Actions */}
                <div className="space-y-6">
                  {/* Performance Stats */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                        Performance
                      </h3>
                      <div className="text-xs text-gray-500 dark:text-gray-400">
                        Real-time data
                      </div>
                    </div>

                    <div className="space-y-4">
                      {/* Total Sales */}
                      <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                        <div className="flex items-center justify-center gap-2 mb-1">
                          <i className="fas fa-chart-line text-blue-600"></i>
                          <span className="text-2xl font-bold text-blue-600">
                            {product.performanceMetrics?.totalSales || 0}
                          </span>
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Sales
                        </div>
                      </div>

                      {/* Total Revenue */}
                      <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-green-600">
                          $
                          {(
                            product.performanceMetrics?.totalRevenue || 0
                          ).toFixed(2)}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Revenue
                        </div>
                      </div>

                      {/* Average Rating */}
                      <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-yellow-600">
                          {(product.averageRating || 0).toFixed(1)}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Average Rating
                        </div>
                        <div className="flex justify-center mt-1">
                          <StarRating
                            rating={product.averageRating || 0}
                            size="sm"
                          />
                        </div>
                      </div>

                      {/* Total Reviews */}
                      <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-purple-600">
                          {product.reviewCount || product.totalReviews || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Reviews
                        </div>
                      </div>

                      {/* Average Order Value */}
                      {product.performanceMetrics?.averageOrderValue > 0 && (
                        <div className="text-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-orange-600">
                            $
                            {product.performanceMetrics.averageOrderValue.toFixed(
                              2,
                            )}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Avg. Order Value
                          </div>
                        </div>
                      )}

                      {/* Total Orders */}
                      {product.performanceMetrics?.totalOrders > 0 && (
                        <div className="text-center p-4 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-indigo-600">
                            {product.performanceMetrics.totalOrders}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Total Orders
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Performance Summary */}
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                      <div className="text-xs text-gray-500 dark:text-gray-400 text-center">
                        {product.performanceMetrics?.totalSales > 0
                          ? "Your product is performing well!"
                          : "Start promoting your product to get your first sale!"}
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Actions
                    </h3>

                    <div className="space-y-3">
                      <Link
                        href={`/create?edit=${productId}`}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-edit mr-2"></i>
                        Edit Product
                      </Link>

                      <Link
                        href={`/details?id=${productId}&view=customer`}
                        className="w-full bg-gray-600 hover:bg-gray-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-eye mr-2"></i>
                        View as Customer
                      </Link>

                      <button
                        onClick={handleToggleStatus}
                        disabled={isUpdating}
                        className={`w-full py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          product.status === "active"
                            ? "bg-orange-600 hover:bg-orange-700 text-white"
                            : "bg-green-600 hover:bg-green-700 text-white"
                        } ${isUpdating ? "opacity-50 cursor-not-allowed" : ""}`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i
                            className={`fas ${product.status === "active" ? "fa-pause" : "fa-play"} mr-2`}
                          ></i>
                        )}
                        {product.status === "active"
                          ? "Deactivate"
                          : "Activate"}
                      </button>

                      <button
                        onClick={handleDeleteProduct}
                        disabled={isUpdating}
                        className={`w-full bg-red-600 hover:bg-red-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          isUpdating ? "opacity-50 cursor-not-allowed" : ""
                        }`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i className="fas fa-trash mr-2"></i>
                        )}
                        Delete Product
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            /* Regular Customer View */
            <>
              {/* Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/" className="hover:text-primary-600">
                      Home
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <Link href="/products" className="hover:text-primary-600">
                      Products
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name}
                    </span>
                  </li>
                </ol>
              </nav>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                {/* Product Images */}
                <div className="space-y-4">
                  <div className="aspect-square bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg">
                    {(() => {
                      const allImages = getAllImages();

                      return (
                        <Image
                          src={
                            allImages[selectedImage] || "/placeholder-image.jpg"
                          }
                          alt={product.name}
                          width={600}
                          height={600}
                          className="w-full h-full object-cover"
                        />
                      );
                    })()}
                  </div>

                  {/* Thumbnail Images */}
                  {(() => {
                    const allImages = getAllImages();

                    return allImages.length > 1 ? (
                      <div className="grid grid-cols-5 gap-2">
                        {allImages.map((image, index) => (
                          <button
                            key={index}
                            onClick={() => setSelectedImage(index)}
                            className={`aspect-square rounded-lg overflow-hidden border-2 ${
                              selectedImage === index
                                ? "border-primary-500"
                                : "border-gray-300 dark:border-gray-600"
                            }`}
                          >
                            <Image
                              src={image}
                              alt={`${product.name} ${index + 1}`}
                              width={100}
                              height={100}
                              className="w-full h-full object-cover"
                            />
                          </button>
                        ))}
                      </div>
                    ) : null;
                  })()}
                </div>

                {/* Product Info */}
                <div className="space-y-6">
                  {/* Category and Features */}
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-3 py-1 rounded-full text-sm font-medium">
                      {product.category}
                    </span>
                    {product.isOrganic && (
                      <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                        Organic
                      </span>
                    )}
                    {product.isFresh && (
                      <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                        Fresh
                      </span>
                    )}
                  </div>

                  {/* Product Name and Farmer */}
                  <div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                      {product.name}
                    </h1>
                    <p className="text-lg text-gray-600 dark:text-gray-400">
                      Produced by{" "}
                      <span className="font-semibold text-primary-600 dark:text-primary-400">
                        {product.farmer?.farmName ||
                          product.farmer?.name ||
                          "Unknown Farmer"}
                      </span>
                    </p>
                  </div>

                  {/* Rating and Reviews */}
                  <div className="flex items-center space-x-4">
                    <StarRating
                      rating={product.averageRating || 0}
                      showValue={true}
                    />
                    <span className="text-gray-500 dark:text-gray-400">
                      ({product.reviewCount || product.totalReviews || 0}{" "}
                      reviews)
                    </span>
                    <button
                      onClick={() => setActiveTab("reviews")}
                      className="text-primary-600 dark:text-primary-400 hover:underline"
                    >
                      Write a review
                    </button>
                  </div>

                  {/* Price and Stock */}
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-6">
                    <div className="flex items-center justify-between mb-4">
                      <div>
                        <span className="text-3xl font-bold text-primary-600 dark:text-primary-400">
                          ${product.price?.toFixed(2)}
                        </span>
                        <span className="text-lg text-gray-500 dark:text-gray-400">
                          /{product.unit || "kg"}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500 dark:text-gray-400">
                          Available Stock
                        </p>
                        <p className="text-lg font-semibold text-gray-900 dark:text-white">
                          {product.stock} {product.unit || "kg"}
                        </p>
                      </div>
                    </div>

                    {/* Location */}
                    <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                      <i className="fas fa-map-marker-alt mr-2"></i>
                      <span>
                        {product.farmer?.location || "Location not specified"}
                      </span>
                    </div>
                  </div>

                  {/* Quantity Selection */}
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Quantity ({product.unit || "kg"})
                      </label>
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={() => setQuantity(Math.max(1, quantity - 1))}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-minus"></i>
                        </button>
                        <input
                          type="number"
                          min="1"
                          max={product.stock}
                          value={quantity}
                          onChange={(e) =>
                            setQuantity(
                              Math.max(1, parseInt(e.target.value) || 1),
                            )
                          }
                          className="w-20 text-center py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                        />
                        <button
                          onClick={() =>
                            setQuantity(Math.min(product.stock, quantity + 1))
                          }
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-plus"></i>
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    <button
                      onClick={handleBuyNow}
                      disabled={product.stock <= 0}
                      className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                    >
                      <i className="fas fa-bolt mr-2"></i>
                      Buy Now
                    </button>
                    <button
                      onClick={handleAddToCart}
                      disabled={isAddingToCart || product.stock <= 0}
                      className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                    >
                      <i className="fas fa-shopping-cart mr-2"></i>
                      {isAddingToCart ? "Adding..." : "Add to Cart"}
                    </button>
                    <button
                      onClick={handleFavoriteToggle}
                      className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                    >
                      <i
                        className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                      ></i>
                      Add to Favorite
                    </button>
                  </div>

                  {/* Farmer Contact */}
                  <div className="bg-primary-50 dark:bg-primary-900 rounded-xl p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-800 flex items-center justify-center">
                        <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                      </div>
                      <div>
                        <h4 className="font-semibold text-gray-900 dark:text-white">
                          {product.farmer?.name || "Farmer"}
                        </h4>
                        <p className="text-sm text-gray-600 dark:text-gray-400">
                          {product.farmer?.email ||
                            "Contact information not available"}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Tabs Section */}
              <div className="mt-16">
                <div className="border-b border-gray-200 dark:border-gray-700">
                  <nav className="flex space-x-8">
                    {[
                      "description",
                      "nutrition",
                      "storage",
                      "reviews",
                      "farmer",
                    ].map((tab) => (
                      <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`py-4 px-1 border-b-2 font-medium text-sm ${
                          activeTab === tab
                            ? "border-primary-500 text-primary-600 dark:text-primary-400"
                            : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
                        }`}
                      >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    ))}
                  </nav>
                </div>

                <div className="py-8">
                  {/* Description Tab */}
                  {activeTab === "description" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-4">
                        Product Description
                      </h3>
                      <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                        {product.description ||
                          "No description available for this product."}
                      </p>

                      {product.features && product.features.length > 0 && (
                        <div className="mt-6">
                          <h4 className="text-lg font-medium mb-3">
                            Key Features
                          </h4>
                          <ul className="list-disc list-inside space-y-1">
                            {product.features.map((feature, index) => (
                              <li
                                key={index}
                                className="text-gray-600 dark:text-gray-400"
                              >
                                {feature}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Nutrition Tab */}
                  {activeTab === "nutrition" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Nutritional Information
                      </h3>

                      {product.nutritionalInformation ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          <p className="text-gray-600 dark:text-gray-400">
                            {product.nutritionalInformation}
                          </p>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-apple-alt text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Nutritional information is not available for this
                            product.
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Storage Instructions Tab */}
                  {activeTab === "storage" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Storage Instructions
                      </h3>

                      {product.storageInstructions ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          <p className="text-gray-600 dark:text-gray-400">
                            {product.storageInstructions}
                          </p>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-warehouse text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Storage instructions are not available for this
                            product.
                          </p>
                        </div>
                      )}

                      {/* General storage tips */}
                      <div className="mt-8">
                        <h4 className="text-lg font-medium mb-4">
                          General Storage Tips
                        </h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
                            <h5 className="font-medium text-blue-900 dark:text-blue-100 mb-2">
                              <i className="fas fa-thermometer-half mr-2"></i>
                              Temperature
                            </h5>
                            <p className="text-sm text-blue-700 dark:text-blue-300">
                              Store in a cool, dry place away from direct
                              sunlight
                            </p>
                          </div>
                          <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
                            <h5 className="font-medium text-green-900 dark:text-green-100 mb-2">
                              <i className="fas fa-tint mr-2"></i>
                              Humidity
                            </h5>
                            <p className="text-sm text-green-700 dark:text-green-300">
                              Keep in low humidity environment to prevent
                              spoilage
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Reviews Tab */}
                  {activeTab === "reviews" && (
                    <div>
                      <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          Customer Reviews (
                          {product.reviewCount || product.totalReviews || 0})
                        </h2>
                        {session && hasPurchasedProduct && (
                          <button
                            onClick={() => setShowReviewForm(true)}
                            className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            <i className="fas fa-plus mr-2"></i>
                            Write Review
                          </button>
                        )}
                        {session &&
                          !hasPurchasedProduct &&
                          !checkingPurchase && (
                            <div className="text-sm text-gray-500 dark:text-gray-400 italic">
                              You need to purchase this product to write a
                              review
                            </div>
                          )}
                        {checkingPurchase && (
                          <div className="text-sm text-gray-500 dark:text-gray-400">
                            <i className="fas fa-spinner fa-spin mr-2"></i>
                            Checking purchase history...
                          </div>
                        )}
                      </div>

                      {/* Review Summary */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-6 mb-8 shadow-lg border border-gray-100 dark:border-gray-700">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                          {/* Overall Rating Section */}
                          <div className="text-center">
                            <div className="mb-4">
                              {(() => {
                                // Calculate actual average rating from reviews if available
                                const actualReviewCount = reviews?.length || 0;
                                let displayRating = product.averageRating || 0;

                                // If we have reviews but no product rating, calculate from reviews
                                if (
                                  actualReviewCount > 0 &&
                                  (!product.averageRating ||
                                    product.averageRating === 0)
                                ) {
                                  const totalRating = reviews.reduce(
                                    (sum, review) => sum + (review.rating || 0),
                                    0,
                                  );
                                  displayRating =
                                    totalRating / actualReviewCount;
                                }

                                return (
                                  <>
                                    <div className="text-5xl font-bold text-primary-600 dark:text-primary-400 mb-2">
                                      {displayRating.toFixed(1)}
                                    </div>
                                    <StarRating
                                      rating={displayRating}
                                      size="lg"
                                    />
                                    <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg font-medium">
                                      Based on{" "}
                                      <span className="text-primary-600 dark:text-primary-400 font-bold">
                                        {actualReviewCount}
                                      </span>{" "}
                                      {actualReviewCount === 1
                                        ? "review"
                                        : "reviews"}
                                    </p>
                                  </>
                                );
                              })()}
                            </div>

                            {/* Review Quality Indicator */}
                            <div className="mt-4 p-3 bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-900/20 dark:to-blue-900/20 rounded-lg">
                              <div className="flex items-center justify-center space-x-2 text-sm">
                                <i className="fas fa-shield-alt text-green-600"></i>
                                <span className="font-medium text-gray-700 dark:text-gray-300">
                                  {(reviews?.length || 0) > 0
                                    ? `${reviews.length} verified review${reviews.length === 1 ? "" : "s"}`
                                    : "No reviews yet"}
                                </span>
                              </div>
                            </div>
                          </div>

                          {/* Rating Distribution */}
                          <div className="space-y-3">
                            <h4 className="font-semibold text-gray-900 dark:text-white mb-4">
                              Rating Breakdown
                            </h4>
                            {(() => {
                              const ratingDistribution =
                                calculateRatingDistribution();
                              const totalReviews = reviews?.length || 0;

                              return [5, 4, 3, 2, 1].map((rating) => {
                                const count = ratingDistribution[rating];
                                const percentage =
                                  totalReviews > 0
                                    ? (count / totalReviews) * 100
                                    : 0;

                                return (
                                  <div
                                    key={rating}
                                    className="flex items-center space-x-3"
                                  >
                                    <span className="text-sm font-medium text-gray-600 dark:text-gray-400 w-12">
                                      {rating} star{rating === 1 ? "" : "s"}
                                    </span>
                                    <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-3 overflow-hidden">
                                      <div
                                        className="bg-gradient-to-r from-yellow-400 to-yellow-500 h-3 rounded-full transition-all duration-500 ease-out"
                                        style={{ width: `${percentage}%` }}
                                      ></div>
                                    </div>
                                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300 w-8 text-right">
                                      {count}
                                    </span>
                                    <span className="text-xs text-gray-500 dark:text-gray-400 w-12 text-right">
                                      {percentage.toFixed(0)}%
                                    </span>
                                  </div>
                                );
                              });
                            })()}

                            {/* Total Reviews Summary */}
                            <div className="mt-4 pt-3 border-t border-gray-200 dark:border-gray-600">
                              <div className="text-center text-sm text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  {(reviews?.length || 0) === 0 &&
                                    "Be the first to review this product!"}
                                  {(reviews?.length || 0) === 1 &&
                                    "1 customer has reviewed this product"}
                                  {(reviews?.length || 0) > 1 &&
                                    `${reviews.length} customers have reviewed this product`}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Review Form Modal */}
                      {showReviewForm && (
                        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 w-full max-w-md">
                            <div className="flex items-center justify-between mb-6">
                              <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                                Write a Review
                              </h3>
                              <button
                                onClick={() => setShowReviewForm(false)}
                                className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                              >
                                <i className="fas fa-times"></i>
                              </button>
                            </div>

                            <form
                              onSubmit={
                                editingReview
                                  ? (e) => {
                                      e.preventDefault();
                                      handleUpdateReview();
                                    }
                                  : handleSubmitReview
                              }
                              className="space-y-4"
                            >
                              <div>
                                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                                  Rating
                                </label>
                                <div className="flex items-center space-x-1">
                                  {[1, 2, 3, 4, 5].map((star) => (
                                    <button
                                      key={star}
                                      type="button"
                                      onClick={() =>
                                        setReviewForm({
                                          ...reviewForm,
                                          rating: star,
                                        })
                                      }
                                      className={`text-2xl ${
                                        star <= reviewForm.rating
                                          ? "text-yellow-400"
                                          : "text-gray-300 dark:text-gray-600"
                                      }`}
                                    >

                                    </button>
                                  ))}
                                </div>
                              </div>

                              <div>
                                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                                  Comment
                                </label>
                                <textarea
                                  value={reviewForm.comment}
                                  onChange={(e) =>
                                    setReviewForm({
                                      ...reviewForm,
                                      comment: e.target.value,
                                    })
                                  }
                                  rows={4}
                                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                                  placeholder="Share your experience with this product..."
                                  required
                                />
                              </div>

                              <div className="flex space-x-3">
                                <button
                                  type="button"
                                  onClick={() => {
                                    setShowReviewForm(false);
                                    setEditingReview(null);
                                    setReviewForm({ rating: 5, comment: "" });
                                  }}
                                  className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
                                >
                                  Cancel
                                </button>
                                <button
                                  type="submit"
                                  disabled={
                                    editingReview
                                      ? isUpdatingReview
                                      : isSubmittingReview
                                  }
                                  className="flex-1 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50"
                                >
                                  {editingReview
                                    ? isUpdatingReview
                                      ? "Updating..."
                                      : "Update Review"
                                    : isSubmittingReview
                                      ? "Submitting..."
                                      : "Submit Review"}
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      )}

                      {/* Individual Reviews */}
                      <div className="space-y-8">
                        {reviews && reviews.length > 0 ? (
                          reviews.map((review, index) => (
                            <div
                              key={review._id}
                              className="group bg-gradient-to-br from-white via-gray-50 to-white dark:from-gray-800 dark:via-gray-850 dark:to-gray-800 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-800"
                              style={{ animationDelay: `${index * 100}ms` }}
                            >
                              <div className="flex items-start justify-between mb-4">
                                <div className="flex items-center space-x-4">
                                  <div className="relative">
                                    <div className="w-12 h-12 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 dark:from-primary-500 dark:to-primary-700 flex items-center justify-center shadow-lg">
                                      <span className="text-white font-bold text-lg">
                                        {(review.reviewer || "Anonymous")
                                          .charAt(0)
                                          .toUpperCase()}
                                      </span>
                                    </div>
                                    <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white dark:border-gray-800 flex items-center justify-center">
                                      <i className="fas fa-check text-white text-xs"></i>
                                    </div>
                                  </div>
                                  <div className="flex-1">
                                    <div className="flex items-center gap-3 mb-1">
                                      <h4 className="font-semibold text-gray-900 dark:text-white text-lg">
                                        {review.reviewer || "Anonymous"}
                                      </h4>
                                      <span className="px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-xs font-medium rounded-full">
                                        Verified Buyer
                                      </span>
                                    </div>
                                    <div className="flex items-center space-x-3">
                                      <div className="flex items-center space-x-1">
                                        {[1, 2, 3, 4, 5].map((star) => (
                                          <span
                                            key={star}
                                            className={`text-lg transition-all duration-200 ${
                                              star <= review.rating
                                                ? "text-yellow-400 drop-shadow-sm"
                                                : "text-gray-300 dark:text-gray-600"
                                            }`}
                                          >

                                          </span>
                                        ))}
                                      </div>
                                      <span className="text-sm font-medium text-yellow-600 dark:text-yellow-400">
                                        {review.rating}/5
                                      </span>
                                      <span className="text-gray-400"></span>
                                      <span className="text-sm text-gray-500 dark:text-gray-400 font-medium">
                                        {new Date(
                                          review.createdAt || review.date,
                                        ).toLocaleDateString("en-US", {
                                          year: "numeric",
                                          month: "short",
                                          day: "numeric",
                                        })}
                                      </span>
                                    </div>
                                  </div>
                                </div>
                                <div className="flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                  {/* Debug information */}
                                  {console.log("DEBUG Review comparison:", {
                                    sessionUserId: session?.user?.userId,
                                    reviewUserId: review.userId,
                                    areEqual:
                                      session?.user?.userId === review.userId,
                                    sessionUser: session?.user,
                                  })}

                                  {/* Show edit/delete buttons only for user's own review */}
                                  {session?.user?.userId === review.userId ? (
                                    <>
                                      <button
                                        onClick={() => {
                                          setEditingReview(review);
                                          setReviewForm({
                                            rating: review.rating,
                                            comment: review.comment,
                                          });
                                          setShowReviewForm(true);
                                        }}
                                        className="p-2 hover:bg-blue-100 dark:hover:bg-blue-900/20 rounded-lg transition-colors"
                                        title="Edit Review"
                                      >
                                        <i className="fas fa-edit text-blue-500 hover:text-blue-600"></i>
                                      </button>
                                      <button
                                        onClick={() =>
                                          handleDeleteReview(review._id)
                                        }
                                        disabled={isDeletingReview}
                                        className="p-2 hover:bg-red-100 dark:hover:bg-red-900/20 rounded-lg transition-colors"
                                        title="Delete Review"
                                      >
                                        <i className="fas fa-trash text-red-500 hover:text-red-600"></i>
                                      </button>
                                    </>
                                  ) : (
                                    <>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-thumbs-up text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-share text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                    </>
                                  )}
                                </div>
                              </div>

                              <div className="relative">
                                <div className="absolute left-0 top-0 w-1 h-full bg-gradient-to-b from-primary-400 to-primary-600 rounded-full opacity-20"></div>
                                <p className="text-gray-700 dark:text-gray-300 leading-relaxed pl-6 text-base">
                                  "{review.comment}"
                                </p>
                              </div>

                              {/* Review actions footer */}
                              <div className="mt-4 pt-4 border-t border-gray-100 dark:border-gray-700 flex items-center justify-between">
                                <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-heart text-red-400"></i>
                                    <span>Helpful</span>
                                  </span>
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-comment text-blue-400"></i>
                                    <span>Reply</span>
                                  </span>
                                </div>
                                <div className="flex items-center space-x-2">
                                  <div className="flex -space-x-1">
                                    {[1, 2, 3].map((i) => (
                                      <div
                                        key={i}
                                        className="w-6 h-6 rounded-full bg-gray-300 dark:bg-gray-600 border-2 border-white dark:border-gray-800"
                                      ></div>
                                    ))}
                                  </div>
                                  <span className="text-xs text-gray-500">
                                    +2 found helpful
                                  </span>
                                </div>
                              </div>
                            </div>
                          ))
                        ) : (
                          <div className="text-center py-16 bg-gradient-to-br from-gray-50 via-white to-gray-50 dark:from-gray-800 dark:via-gray-900 dark:to-gray-800 rounded-2xl border-2 border-dashed border-gray-200 dark:border-gray-700">
                            <div className="relative inline-block mb-6">
                              <div className="w-20 h-20 bg-gradient-to-br from-primary-100 to-primary-200 dark:from-primary-800 dark:to-primary-900 rounded-full flex items-center justify-center mx-auto shadow-lg">
                                <i className="fas fa-star text-3xl text-primary-500 dark:text-primary-400"></i>
                              </div>
                              <div className="absolute -top-2 -right-2 w-8 h-8 bg-yellow-400 rounded-full flex items-center justify-center animate-bounce">
                                <i className="fas fa-plus text-white text-sm"></i>
                              </div>
                            </div>
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-3">
                              No Reviews Yet
                            </h3>
                            <p className="text-gray-600 dark:text-gray-400 mb-6 max-w-md mx-auto">
                              Be the first to share your experience with this
                              amazing product! Your review helps other customers
                              make informed decisions.
                            </p>
                            {session && (
                              <button
                                onClick={() => setShowReviewForm(true)}
                                className="inline-flex items-center bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                              >
                                <i className="fas fa-edit mr-2"></i>
                                Write First Review
                              </button>
                            )}
                          </div>
                        )}

                        {/* Load More Reviews Button */}
                        {hasMoreReviews && (
                          <div className="text-center pt-8">
                            <button
                              onClick={loadMoreReviews}
                              className="group inline-flex items-center bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 text-gray-900 dark:text-white px-8 py-4 rounded-xl font-semibold border-2 border-gray-200 dark:border-gray-700 hover:border-primary-300 dark:hover:border-primary-600 shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                            >
                              <i className="fas fa-chevron-down mr-3 group-hover:animate-bounce"></i>
                              Load More Reviews
                              <span className="ml-3 px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-sm rounded-full">
                                +{Math.min(5, reviews?.length || 0)}
                              </span>
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Farmer Tab */}
                  {activeTab === "farmer" && (
                    <div>
                      <h3 className="text-xl font-semibold mb-6">
                        About the Farmer
                      </h3>
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow-lg">
                        <div className="flex items-start gap-4">
                          <div className="w-16 h-16 rounded-full bg-primary-100 dark:bg-primary-900 flex items-center justify-center flex-shrink-0">
                            <i className="fas fa-user text-2xl text-primary-600 dark:text-primary-400"></i>
                          </div>
                          <div className="flex-1">
                            <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                              {product.farmer?.farmName ||
                                product.farmer?.name ||
                                "Farm Name"}
                            </h4>
                            <p className="text-gray-600 dark:text-gray-400 mb-3">
                              {product.farmer?.email ||
                                "Farmer contact not available"}
                            </p>

                            {product.farmer?.location && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-3">
                                <i className="fas fa-map-marker-alt mr-2"></i>
                                <span>{product.farmer.location}</span>
                              </div>
                            )}

                            {product.farmer?.phone && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                                <i className="fas fa-phone mr-2"></i>
                                <span>{product.farmer.phone}</span>
                              </div>
                            )}

                            <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                              {product.farmer?.description ||
                                "A dedicated farmer committed to providing fresh, quality produce to the community."}
                            </p>

                            <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                              <Link
                                href={`/details?id=${product.farmerId || product.farmer?.id}`}
                                className="inline-flex items-center text-primary-600 hover:text-primary-700 font-medium"
                              >
                                <i className="fas fa-external-link-alt mr-2"></i>
                                View All Products from this Farmer
                              </Link>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Related Products Section - Enhanced */}
                  {relatedProducts.length > 0 && (
                    <div className="mt-16 bg-gradient-to-br from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 rounded-2xl p-8 shadow-lg border border-gray-100 dark:border-gray-700">
                      {/* Section Header */}
                      <div className="text-center mb-10">
                        <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full mb-4 shadow-lg">
                          <i className="fas fa-box-open text-2xl text-white"></i>
                        </div>
                        <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-3">
                          Related Products
                        </h3>
                        <p className="text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
                          Discover similar high-quality products from our
                          trusted farmers. Each item is carefully selected to
                          meet our quality standards.
                        </p>
                        <div className="mt-4 flex items-center justify-center space-x-2 text-sm text-gray-500 dark:text-gray-400">
                          <i className="fas fa-leaf text-green-500"></i>
                          <span>{relatedProducts.length} products found</span>
                          <span></span>
                          <i className="fas fa-truck text-blue-500"></i>
                          <span>Fast delivery available</span>
                        </div>
                      </div>

                      {/* Enhanced Product Grid */}
                      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                        {relatedProducts.map((relatedProduct, index) => (
                          <div
                            key={relatedProduct._id}
                            className="group transform transition-all duration-300 hover:-translate-y-2"
                            style={{ animationDelay: `${index * 100}ms` }}
                          >
                            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-600 overflow-hidden">
                              {/* Product Image */}
                              <div className="relative aspect-square overflow-hidden">
                                <Image
                                  src={
                                    relatedProduct.image ||
                                    relatedProduct.images?.[0] ||
                                    "/placeholder-image.jpg"
                                  }
                                  alt={relatedProduct.name}
                                  width={300}
                                  height={300}
                                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                                />

                                {/* Product Badges */}
                                <div className="absolute top-3 left-3 flex flex-col space-y-2">
                                  {relatedProduct.isOrganic && (
                                    <span className="bg-green-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                      Organic
                                    </span>
                                  )}
                                  {relatedProduct.isFresh && (
                                    <span className="bg-blue-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                      Fresh
                                    </span>
                                  )}
                                </div>

                                {/* Quick Actions Overlay */}
                                <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
                                  <Link
                                    href={`/details?id=${relatedProduct._id}`}
                                    className="bg-white text-gray-900 px-6 py-2 rounded-full font-medium hover:bg-gray-100 transition-colors transform hover:scale-105"
                                  >
                                    View Details
                                  </Link>
                                </div>
                              </div>

                              {/* Product Info */}
                              <div className="p-6">
                                <div className="mb-3">
                                  <h4 className="font-bold text-lg text-gray-900 dark:text-white line-clamp-1 group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                                    {relatedProduct.name}
                                  </h4>
                                  <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-1">
                                    by{" "}
                                    {relatedProduct.farmer?.name ||
                                      relatedProduct.farmer?.farmName ||
                                      "Unknown Farmer"}
                                  </p>
                                </div>

                                {/* Rating */}
                                <div className="flex items-center space-x-1 mb-3">
                                  <StarRating
                                    rating={relatedProduct.averageRating || 0}
                                    size="sm"
                                  />
                                  <span className="text-xs text-gray-500 dark:text-gray-400">
                                    (
                                    {relatedProduct.reviewCount ||
                                      relatedProduct.totalReviews ||
                                      0}
                                    )
                                  </span>
                                </div>

                                {/* Price and Stock */}
                                <div className="flex items-center justify-between mb-4">
                                  <div>
                                    <span className="text-xl font-bold text-primary-600 dark:text-primary-400">
                                      ${(relatedProduct.price || 0).toFixed(2)}
                                    </span>
                                    <span className="text-sm text-gray-500 dark:text-gray-400">
                                      /{relatedProduct.unit || "kg"}
                                    </span>
                                  </div>
                                  <div className="text-right">
                                    <div
                                      className={`text-xs font-medium ${
                                        (relatedProduct.stock || 0) > 0
                                          ? "text-green-600 dark:text-green-400"
                                          : "text-red-600 dark:text-red-400"
                                      }`}
                                    >
                                      {(relatedProduct.stock || 0) > 0
                                        ? `${relatedProduct.stock} ${relatedProduct.unit || "kg"} left`
                                        : "Out of stock"}
                                    </div>
                                  </div>
                                </div>

                                {/* Action Button */}
                                <Link
                                  href={`/details?id=${relatedProduct._id}`}
                                  className="w-full bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white py-2.5 px-4 rounded-lg font-medium transition-all duration-200 text-center block group-hover:shadow-lg transform group-hover:scale-[1.02]"
                                >
                                  <i className="fas fa-eye mr-2"></i>
                                  View Product
                                </Link>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>

                      {/* Browse More Section */}
                      <div className="mt-12 text-center">
                        <div className="border-t border-gray-200 dark:border-gray-700 pt-8">
                          <p className="text-gray-600 dark:text-gray-400 mb-4">
                            Looking for more products?
                          </p>
                          <Link
                            href="/products"
                            className="inline-flex items-center bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                          >
                            <i className="fas fa-shopping-bag mr-2"></i>
                            Browse All Products
                            <i className="fas fa-arrow-right ml-2"></i>
                          </Link>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import Link from "next/link";
import Image from "next/image";
import { useState, useEffect } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import { useSession } from "next-auth/react";
import { useCart } from "@/contexts/CartContext";
import { useFavorites } from "@/contexts/FavoritesContext";
import ProductCard from "@/components/ProductCard";
import StarRating from "@/components/StarRating";
import Footer from "@/components/Footer";
import RecentOrdersSection from "@/components/RecentOrdersSection";
import FarmerProfileView from "@/components/FarmerProfileView";
import useProductData from "@/hooks/useProductData";
import useOwnership from "@/hooks/useOwnership";
import useReviews from "@/hooks/useReviews";

import Loading from "@/components/Loading";
import NotFound from "@/components/NotFound";

export default function ProductDetails() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const productId = searchParams.get("id");
  const viewMode = searchParams.get("view");
  const { data: session } = useSession();
  const { addToCart } = useCart();
  const { addToFavorites, removeFromFavorites, isProductFavorited } =
    useFavorites();

  // Custom hooks
  const {
    product,
    farmer,
    farmerProducts,
    responseType,
    relatedProducts,
    loading,
    fetchProductDetails,
  } = useProductData(productId);

  const { reviews, hasMoreReviews, fetchReviews, reviewsPage } = useReviews(
    productId,
    responseType,
    session?.user?.id, // Pass userId to prioritize user's review
  );
  const isOwner = useOwnership(product, session, viewMode);

  // State management
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState("description");
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  // Review form state
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({ rating: 5, comment: "" });
  const [isSubmittingReview, setIsSubmittingReview] = useState(false);

  // Review edit/delete state
  const [editingReview, setEditingReview] = useState(null);
  const [isUpdatingReview, setIsUpdatingReview] = useState(false);
  const [isDeletingReview, setIsDeletingReview] = useState(false);

  // Purchase verification state
  const [hasPurchasedProduct, setHasPurchasedProduct] = useState(false);
  const [checkingPurchase, setCheckingPurchase] = useState(false);

  // Farmer-specific states
  const [stockUpdate, setStockUpdate] = useState("");
  const [priceUpdate, setPriceUpdate] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [recentOrders, setRecentOrders] = useState([]);
  const [loadingOrders, setLoadingOrders] = useState(false);

  // Effects
  useEffect(() => {
    if (productId) {
      fetchProductDetails();
    }
  }, [productId]);

  useEffect(() => {
    if (productId && isOwner && viewMode !== "customer") {
      const interval = setInterval(() => {
        fetchProductDetails();
      }, 30000);
      return () => clearInterval(interval);
    }
  }, [productId, isOwner, viewMode]);

  useEffect(() => {
    if (productId && isOwner && viewMode !== "customer") {
      fetchRecentOrders();
    }
  }, [productId, isOwner, viewMode]);

  // Check if product is favorited when productId changes
  useEffect(() => {
    if (productId) {
      setIsFavorite(isProductFavorited(productId));
    }
  }, [productId, isProductFavorited]);

  // Check if user has purchased this product
  useEffect(() => {
    if (session?.user?.id && productId) {
      checkUserPurchase();
    }
  }, [session?.user?.id, productId]);

  const checkUserPurchase = async () => {
    if (!session?.user?.id) return;

    setCheckingPurchase(true);
    try {
      const response = await fetch(
        `/api/orders?userId=${session.user.id}&productId=${productId}`,
      );
      if (response.ok) {
        const data = await response.json();
        // Check if user has any order with this product that's confirmed/delivered/pending
        const hasPurchased = data.orders?.some(
          (order) =>
            order.items?.some((item) => item.productId === productId) &&
            ["delivered", "confirmed", "pending"].includes(order.status),
        );
        setHasPurchasedProduct(hasPurchased);
      }
    } catch (error) {
      console.error("Error checking purchase history:", error);
      setHasPurchasedProduct(false);
    } finally {
      setCheckingPurchase(false);
    }
  };

  // API calls
  const fetchRecentOrders = async () => {
    try {
      setLoadingOrders(true);
      const response = await fetch(
        `/api/orders?productId=${productId}&limit=5`,
      );

      if (response.ok) {
        const data = await response.json();
        setRecentOrders(data.orders || []);
      }
    } catch (error) {
      console.error("Error fetching recent orders:", error);
    } finally {
      setLoadingOrders(false);
    }
  };

  // Event handlers
  const handleAddToCart = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      const item = {
        productId: productId,
        id: productId, // Add id field for cart context
        name: product.name,
        price: product.price,
        quantity: quantity,
        stock: product.stock, // Include stock information
        image:
          product.image ||
          (product.images && product.images[0]) ||
          "/placeholder-image.jpg",
        unit: product.unit || "kg",
        farmerId: product.farmerId,
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer",
      };

      await addToCart(item, quantity);
      alert("Product added to cart successfully!");
    } catch (error) {
      console.error("Error adding to cart:", error);
      // Show user-friendly error message for stock issues
      if (
        error.message.includes("Only") &&
        error.message.includes("available in stock")
      ) {
        alert(error.message);
      } else {
        alert("Failed to add product to cart. Please try again.");
      }
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleBuyNow = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Create a product object that matches the CartContext expectations
      const productForCart = {
        id: productId,
        name: product.name,
        price: product.price,
        image:
          product.image ||
          (product.images && product.images[0]) ||
          "/placeholder-image.jpg",
        unit: product.unit || "kg",
        farmerId: product.farmerId,
        farmer: {
          id: product.farmerId,
          _id: product.farmerId,
          email: product.farmer?.email,
          name:
            product.farmer?.name ||
            product.farmer?.farmName ||
            "Unknown Farmer",
        },
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer",
        stock: product.stock || 0,
      };

      // Use CartContext's addToCart function
      await addToCart(productForCart, quantity);

      // Immediately redirect to payment - let the payment page handle the cart state
      // The payment page should wait for cart loading to complete before checking if empty
      router.push("/payment");
    } catch (error) {
      console.error("Error processing buy now:", error);
      alert(error.message || "Failed to process order. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const handleFavoriteToggle = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    try {
      if (isFavorite) {
        // Remove from favorites
        const success = await removeFromFavorites(productId);
        if (success) {
          setIsFavorite(false);
          alert("Product removed from favorites!");
        } else {
          alert("Failed to remove from favorites. Please try again.");
        }
      } else {
        // Add to favorites
        const success = await addToFavorites(productId);
        if (success) {
          setIsFavorite(true);
          alert("Product added to favorites!");
        } else {
          alert("Failed to add to favorites. Please try again.");
        }
      }
    } catch (error) {
      console.error("Error toggling favorite:", error);
      alert("Failed to update favorites. Please try again.");
    }
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (!session) {
      alert("Please login to submit a review");
      return;
    }

    setIsSubmittingReview(true);
    try {
      // Include userId in the review submission
      const reviewData = {
        ...reviewForm,
        userId:
          session.user.id ||
          session.user._id ||
          session.user.userId ||
          session.user.email,
      };

      const response = await fetch(`/api/products/${productId}/reviews`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(reviewData),
      });

      if (response.ok) {
        setShowReviewForm(false);
        setReviewForm({ rating: 5, comment: "" });
        fetchReviews();
        fetchProductDetails();
        alert("Review submitted successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to submit review");
      }
    } catch (error) {
      console.error("Error submitting review:", error);
      alert("Failed to submit review");
    } finally {
      setIsSubmittingReview(false);
    }
  };

  const handleUpdateReview = async () => {
    if (!editingReview) return;

    setIsUpdatingReview(true);
    try {
      const response = await fetch(`/api/reviews/${editingReview._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          rating: reviewForm.rating,
          comment: reviewForm.comment,
          userId:
            session.user.userId ||
            session.user.id ||
            session.user._id ||
            session.user.email,
        }),
      });

      if (response.ok) {
        setEditingReview(null);
        setReviewForm({ rating: 5, comment: "" });
        setShowReviewForm(false);
        fetchReviews();
        fetchProductDetails();
        alert("Review updated successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update review");
      }
    } catch (error) {
      console.error("Error updating review:", error);
      alert("Failed to update review");
    } finally {
      setIsUpdatingReview(false);
    }
  };

  const handleDeleteReview = async (reviewId) => {
    if (!confirm("Are you sure you want to delete this review?")) {
      return;
    }

    setIsDeletingReview(true);
    try {
      const userId =
        session.user.userId ||
        session.user.id ||
        session.user._id ||
        session.user.email;
      const response = await fetch(
        `/api/reviews/${reviewId}?userId=${encodeURIComponent(userId)}`,
        {
          method: "DELETE",
        },
      );

      if (response.ok) {
        fetchReviews();
        fetchProductDetails();
        alert("Review deleted successfully!");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to delete review");
      }
    } catch (error) {
      console.error("Error deleting review:", error);
      alert("Failed to delete review");
    } finally {
      setIsDeletingReview(false);
    }
  };

  const loadMoreReviews = () => {
    fetchReviews(reviewsPage + 1, true);
  };

  const handleUpdateProduct = async () => {
    if (!isOwner) return;

    if (!stockUpdate && !priceUpdate) {
      alert("Please enter a value to update");
      return;
    }

    setIsUpdating(true);
    try {
      const updateData = {};

      if (stockUpdate && stockUpdate.trim() !== "") {
        const stockValue = parseInt(stockUpdate);
        if (isNaN(stockValue) || stockValue < 0) {
          alert("Please enter a valid stock number");
          setIsUpdating(false);
          return;
        }
        updateData.stock = stockValue;
      }

      if (priceUpdate && priceUpdate.trim() !== "") {
        const priceValue = parseFloat(priceUpdate);
        if (isNaN(priceValue) || priceValue <= 0) {
          alert("Please enter a valid price");
          setIsUpdating(false);
          return;
        }
        updateData.price = priceValue;
      }

      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        alert("Product updated successfully!");
        fetchProductDetails();
        setStockUpdate("");
        setPriceUpdate("");
      } else {
        const error = await response.json();
        alert(error.error || "Failed to update product");
      }
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleToggleStatus = async () => {
    if (!isOwner) return;

    const newStatus = product.status === "active" ? "inactive" : "active";
    const actionText = newStatus === "active" ? "activate" : "deactivate";

    if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        alert(`Product ${actionText}d successfully!`);
        fetchProductDetails();
      } else {
        const error = await response.json();
        alert(error.error || `Failed to ${actionText} product`);
      }
    } catch (error) {
      console.error(`Error ${actionText}ing product:`, error);
      alert(`Failed to ${actionText} product. Please try again.`);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleDeleteProduct = async () => {
    if (!isOwner) return;

    if (
      !confirm(
        " Are you sure you want to delete this product?\n\nThis action cannot be undone and will remove:\n The product listing\n All associated data\n Product from any pending orders",
      )
    ) {
      return;
    }

    setIsUpdating(true);
    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Product deleted successfully!");
        window.location.href = "/manage";
      } else {
        const error = await response.json();
        if (response.status === 409) {
          alert(
            " Cannot Delete Product\n\nThis product has pending orders and cannot be deleted.\nPlease wait for all orders to be completed or cancelled before deleting this product.\n\nYou can temporarily deactivate the product instead.",
          );
        } else {
          alert(error.error || "Failed to delete product");
        }
      }
    } catch (error) {
      console.error("Error deleting product:", error);
      alert("Failed to delete product. Please try again.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAddImages = () => {
    router.push(`/create?edit=${productId}`);
  };

  // Helper function to get all images
  const getAllImages = () => {
    const allImages = [];
    if (product?.image) {
      allImages.push(product.image);
    }
    if (product?.images && product.images.length > 0) {
      allImages.push(...product.images);
    }
    return allImages;
  };

  // Calculate rating distribution from reviews
  const calculateRatingDistribution = () => {
    if (!reviews || reviews.length === 0) {
      return { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
    }

    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };

    reviews.forEach((review) => {
      const rating = Math.floor(review.rating); // Round down to nearest integer
      if (rating >= 1 && rating <= 5) {
        distribution[rating]++;
      }
    });

    return distribution;
  };

  const getRatingPercentage = (rating, distribution) => {
    const totalReviews = reviews?.length || 0;
    if (totalReviews === 0) return 0;
    return (distribution[rating] / totalReviews) * 100;
  };

  // Render components based on state
  if (loading) {
    return <Loading />;
  }

  if (responseType === "farmer" && farmer) {
    return (
      <FarmerProfileView farmer={farmer} farmerProducts={farmerProducts} />
    );
  }

  if (!product && !farmer) {
    return <NotFound responseType={responseType} />;
  }

  // Main product details render
  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Farmer Dashboard View */}
          {isOwner && viewMode !== "customer" ? (
            <>
              {/* Farmer Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/manage" className="hover:text-primary-600">
                      <i className="fas fa-tachometer-alt mr-1"></i>
                      Dashboard
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name} - Management
                    </span>
                  </li>
                </ol>
              </nav>

              {/* Customer View Notice */}
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <i className="fas fa-info-circle text-blue-600 dark:text-blue-400 mr-2"></i>
                    <span className="text-blue-800 dark:text-blue-200">
                      You are viewing this as the product owner.
                    </span>
                  </div>
                  <Link
                    href={`/details?id=${productId}&view=customer`}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                  >
                    <i className="fas fa-eye mr-1"></i>
                    View as Customer
                  </Link>
                </div>
              </div>

              {/* Farmer Product Management Header */}
              <div className="bg-gradient-to-r from-green-600 to-emerald-600 rounded-2xl shadow-lg p-8 mb-8 text-white">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-3xl font-bold mb-2">
                      Product Management
                    </h1>
                    <p className="text-green-100">
                      Manage your product listing and inventory
                    </p>
                  </div>
                  <div className="text-right">
                    <div className="text-sm text-green-100 mb-1">
                      Product Status
                    </div>
                    <div
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                        product.status === "active"
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      <i
                        className={`fas ${product.status === "active" ? "fa-check-circle" : "fa-times-circle"} mr-1`}
                      ></i>
                      {product.status === "active" ? "Active" : "Inactive"}
                    </div>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Product Images & Info */}
                <div className="lg:col-span-2 space-y-6">
                  {/* Image Management */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Images
                      </h3>
                      <button
                        onClick={handleAddImages}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-plus mr-1"></i>
                        Add Images
                      </button>
                    </div>

                    {/* Current Images Display */}
                    {(() => {
                      const allImages = getAllImages();

                      return allImages.length > 0 ? (
                        <div className="space-y-4">
                          <div className="aspect-video max-w-md bg-gray-100 dark:bg-gray-700 rounded-lg overflow-hidden">
                            <Image
                              src={allImages[selectedImage]}
                              alt={product.name}
                              width={400}
                              height={225}
                              className="w-full h-full object-cover"
                            />
                          </div>
                          {allImages.length > 1 && (
                            <div className="grid grid-cols-5 gap-2 max-w-md">
                              {allImages.map((image, index) => (
                                <button
                                  key={index}
                                  onClick={() => setSelectedImage(index)}
                                  className={`aspect-square rounded-lg overflow-hidden border-2 ${
                                    selectedImage === index
                                      ? "border-primary-500"
                                      : "border-gray-300 dark:border-gray-600"
                                  }`}
                                >
                                  <Image
                                    src={image}
                                    alt={`${product.name} ${index + 1}`}
                                    width={80}
                                    height={80}
                                    className="w-full h-full object-cover"
                                  />
                                </button>
                              ))}
                            </div>
                          )}
                        </div>
                      ) : (
                        <div className="text-center py-12 bg-gray-50 dark:bg-gray-700 rounded-lg">
                          <i className="fas fa-image text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            No images uploaded
                          </p>
                        </div>
                      );
                    })()}
                  </div>

                  {/* Product Information */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-6">
                      <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                        Product Information
                      </h3>
                      <Link
                        href={`/create?edit=${productId}`}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                      >
                        <i className="fas fa-edit mr-1"></i>
                        Edit Details
                      </Link>
                    </div>

                    <div className="space-y-4">
                      <div>
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          {product.name}
                        </h2>
                        <p className="text-gray-600 dark:text-gray-400">
                          {product.category}
                        </p>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Price
                          </span>
                          <p className="text-lg font-semibold text-gray-900 dark:text-white">
                            ${product.price?.toFixed(2)} per{" "}
                            {product.unit || "kg"}
                          </p>
                        </div>
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Stock
                          </span>
                          <p className="text-lg font-semibold text-gray-900 dark:text-white">
                            {product.stock} {product.unit || "kg"}
                          </p>
                        </div>
                      </div>

                      <div>
                        <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                          Description
                        </span>
                        <p className="text-gray-700 dark:text-gray-300 mt-1">
                          {product.description || "No description provided."}
                        </p>
                      </div>

                      {product.features && (
                        <div>
                          <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                            Features
                          </span>
                          <ul className="list-disc list-inside text-gray-700 dark:text-gray-300 mt-1">
                            {product.features.map((feature, index) => (
                              <li key={index}>{feature}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Quick Actions */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
                      Quick Updates
                    </h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      {/* Stock Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Stock
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            value={stockUpdate}
                            onChange={(e) => setStockUpdate(e.target.value)}
                            placeholder="New stock amount"
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={isUpdating || !stockUpdate}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition disabled:bg-gray-400"
                          >
                            Update
                          </button>
                        </div>
                      </div>

                      {/* Price Update */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                          Update Price
                        </label>
                        <div className="flex gap-2">
                          <input
                            type="number"
                            step="0.01"
                            value={priceUpdate}
                            onChange={(e) => setPriceUpdate(e.target.value)}
                            placeholder="New price"
                            className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                          />
                          <button
                            onClick={handleUpdateProduct}
                            disabled={isUpdating || !priceUpdate}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition disabled:bg-gray-400"
                          >
                            Update
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Recent Orders */}
                  <RecentOrdersSection
                    recentOrders={recentOrders}
                    loadingOrders={loadingOrders}
                    product={product}
                  />
                </div>

                {/* Sidebar - Analytics & Actions */}
                <div className="space-y-6">
                  {/* Performance Stats */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                        Performance
                      </h3>
                      <div className="text-xs text-gray-500 dark:text-gray-400">
                        Real-time data
                      </div>
                    </div>

                    <div className="space-y-4">
                      {/* Total Sales */}
                      <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                        <div className="flex items-center justify-center gap-2 mb-1">
                          <i className="fas fa-chart-line text-blue-600"></i>
                          <span className="text-2xl font-bold text-blue-600">
                            {product.performanceMetrics?.totalSales || 0}
                          </span>
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Sales
                        </div>
                      </div>

                      {/* Total Revenue */}
                      <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-green-600">
                          $
                          {(
                            product.performanceMetrics?.totalRevenue || 0
                          ).toFixed(2)}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Revenue
                        </div>
                      </div>

                      {/* Average Rating */}
                      <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-yellow-600">
                          {(product.averageRating || 0).toFixed(1)}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Average Rating
                        </div>
                        <div className="flex justify-center mt-1">
                          <StarRating
                            rating={product.averageRating || 0}
                            size="sm"
                          />
                        </div>
                      </div>

                      {/* Total Reviews */}
                      <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                        <div className="text-2xl font-bold text-purple-600">
                          {product.reviewCount || product.totalReviews || 0}
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          Total Reviews
                        </div>
                      </div>

                      {/* Average Order Value */}
                      {product.performanceMetrics?.averageOrderValue > 0 && (
                        <div className="text-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-orange-600">
                            $
                            {product.performanceMetrics.averageOrderValue.toFixed(
                              2,
                            )}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Avg. Order Value
                          </div>
                        </div>
                      )}

                      {/* Total Orders */}
                      {product.performanceMetrics?.totalOrders > 0 && (
                        <div className="text-center p-4 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg">
                          <div className="text-2xl font-bold text-indigo-600">
                            {product.performanceMetrics.totalOrders}
                          </div>
                          <div className="text-sm text-gray-600 dark:text-gray-400">
                            Total Orders
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Performance Summary */}
                    <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                      <div className="text-xs text-gray-500 dark:text-gray-400 text-center">
                        {product.performanceMetrics?.totalSales > 0
                          ? "Your product is performing well!"
                          : "Start promoting your product to get your first sale!"}
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                      Actions
                    </h3>

                    <div className="space-y-3">
                      <Link
                        href={`/create?edit=${productId}`}
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-edit mr-2"></i>
                        Edit Product
                      </Link>

                      <Link
                        href={`/details?id=${productId}&view=customer`}
                        className="w-full bg-gray-600 hover:bg-gray-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center"
                      >
                        <i className="fas fa-eye mr-2"></i>
                        View as Customer
                      </Link>

                      <button
                        onClick={handleToggleStatus}
                        disabled={isUpdating}
                        className={`w-full py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          product.status === "active"
                            ? "bg-orange-600 hover:bg-orange-700 text-white"
                            : "bg-green-600 hover:bg-green-700 text-white"
                        } ${isUpdating ? "opacity-50 cursor-not-allowed" : ""}`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i
                            className={`fas ${product.status === "active" ? "fa-pause" : "fa-play"} mr-2`}
                          ></i>
                        )}
                        {product.status === "active"
                          ? "Deactivate"
                          : "Activate"}
                      </button>

                      <button
                        onClick={handleDeleteProduct}
                        disabled={isUpdating}
                        className={`w-full bg-red-600 hover:bg-red-700 text-white py-3 px-4 rounded-lg font-medium transition flex items-center justify-center ${
                          isUpdating ? "opacity-50 cursor-not-allowed" : ""
                        }`}
                      >
                        {isUpdating ? (
                          <i className="fas fa-spinner fa-spin mr-2"></i>
                        ) : (
                          <i className="fas fa-trash mr-2"></i>
                        )}
                        Delete Product
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            /* Regular Customer View */
            <>
              {/* Breadcrumb */}
              <nav className="mb-8">
                <ol className="flex items-center space-x-2 text-sm text-gray-600 dark:text-gray-400">
                  <li>
                    <Link href="/" className="hover:text-primary-600">
                      Home
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <Link href="/products" className="hover:text-primary-600">
                      Products
                    </Link>
                  </li>
                  <li>
                    <i className="fas fa-chevron-right text-xs"></i>
                  </li>
                  <li>
                    <span className="text-gray-900 dark:text-white">
                      {product.name}
                    </span>
                  </li>
                </ol>
              </nav>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                {/* Product Images */}
                <div className="space-y-4">
                  <div className="aspect-square bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg">
                    {(() => {
                      const allImages = getAllImages();

                      return (
                        <Image
                          src={
                            allImages[selectedImage] || "/placeholder-image.jpg"
                          }
                          alt={product.name}
                          width={600}
                          height={600}
                          className="w-full h-full object-cover"
                        />
                      );
                    })()}
                  </div>

                  {/* Thumbnail Images */}
                  {(() => {
                    const allImages = getAllImages();

                    return allImages.length > 1 ? (
                      <div className="grid grid-cols-5 gap-2">
                        {allImages.map((image, index) => (
                          <button
                            key={index}
                            onClick={() => setSelectedImage(index)}
                            className={`aspect-square rounded-lg overflow-hidden border-2 ${
                              selectedImage === index
                                ? "border-primary-500"
                                : "border-gray-300 dark:border-gray-600"
                            }`}
                          >
                            <Image
                              src={image}
                              alt={`${product.name} ${index + 1}`}
                              width={100}
                              height={100}
                              className="w-full h-full object-cover"
                            />
                          </button>
                        ))}
                      </div>
                    ) : null;
                  })()}
                </div>

                {/* Product Info */}
                <div className="space-y-6">
                  {/* Category and Features */}
                  <div className="flex items-center gap-2 flex-wrap">
                    <span className="bg-primary-100 dark:bg-primary-900 text-primary-800 dark:text-primary-200 px-3 py-1 rounded-full text-sm font-medium">
                      {product.category}
                    </span>
                    {product.isOrganic && (
                      <span className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200 px-2 py-1 rounded-full text-xs font-medium">
                        Organic
                      </span>
                    )}
                    {product.isFresh && (
                      <span className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded-full text-xs font-medium">
                        Fresh
                      </span>
                    )}
                  </div>

                  {/* Product Name and Farmer */}
                  <div>
                    <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                      {product.name}
                    </h1>
                    <p className="text-lg text-gray-600 dark:text-gray-400">
                      Produced by{" "}
                      <span className="font-semibold text-primary-600 dark:text-primary-400">
                        {product.farmer?.farmName ||
                          product.farmer?.name ||
                          "Unknown Farmer"}
                      </span>
                    </p>
                  </div>

                  {/* Rating and Reviews */}
                  <div className="flex items-center space-x-4">
                    {(() => {
                      // Calculate actual average rating and count from reviews if available
                      const actualReviewCount = reviews?.length || 0;
                      let displayRating = product.averageRating || 0;

                      // If we have reviews but no product rating, calculate from reviews
                      if (
                        actualReviewCount > 0 &&
                        (!product.averageRating || product.averageRating === 0)
                      ) {
                        const totalRating = reviews.reduce(
                          (sum, review) => sum + (review.rating || 0),
                          0,
                        );
                        displayRating = totalRating / actualReviewCount;
                      }

                      return (
                        <>
                          <StarRating
                            rating={displayRating}
                            showValue={true}
                          />
                          <span className="text-gray-500 dark:text-gray-400">
                            ({actualReviewCount} reviews)
                          </span>
                        </>
                      );
                    })()}
                    <button
                      onClick={() => setActiveTab("reviews")}
                      className="text-primary-600 dark:text-primary-400 hover:underline"
                    >
                      Write a review
                    </button>
                  </div>

                  {/* Price and Stock */}
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-6">
                    <div className="flex items-center justify-between mb-4">
                      <div>
                        <span className="text-3xl font-bold text-primary-600 dark:text-primary-400">
                          ${product.price?.toFixed(2)}
                        </span>
                        <span className="text-lg text-gray-500 dark:text-gray-400">
                          /{product.unit || "kg"}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500 dark:text-gray-400">
                          Available Stock
                        </p>
                        <p className="text-lg font-semibold text-gray-900 dark:text-white">
                          {product.stock} {product.unit || "kg"}
                        </p>
                      </div>
                    </div>

                    {/* Location */}
                    <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                      <i className="fas fa-map-marker-alt mr-2"></i>
                      <span>
                        {product.farmer?.location || "Location not specified"}
                      </span>
                    </div>
                  </div>

                  {/* Quantity Selection */}
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                        Quantity ({product.unit || "kg"})
                      </label>
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={() => setQuantity(Math.max(1, quantity - 1))}
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-minus"></i>
                        </button>
                        <input
                          type="number"
                          min="1"
                          max={product.stock}
                          value={quantity}
                          onChange={(e) =>
                            setQuantity(
                              Math.max(1, parseInt(e.target.value) || 1),
                            )
                          }
                          className="w-20 text-center py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                        />
                        <button
                          onClick={() =>
                            setQuantity(Math.min(product.stock, quantity + 1))
                          }
                          className="w-10 h-10 rounded-lg border border-gray-300 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-700"
                        >
                          <i className="fas fa-plus"></i>
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="space-y-3">
                    <button
                      onClick={handleBuyNow}
                      disabled={product.stock <= 0}
                      className="w-full bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white py-3 px-6 rounded-lg font-medium transition-all duration-200 shadow-md hover:shadow-lg disabled:bg-gray-400"
                    >
                      <i className="fas fa-bolt mr-2"></i>
                      Buy Now
                    </button>
                    <button
                      onClick={handleAddToCart}
                      disabled={isAddingToCart || product.stock <= 0}
                      className="w-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition disabled:bg-gray-400"
                    >
                      <i className="fas fa-shopping-cart mr-2"></i>
                      {isAddingToCart ? "Adding..." : "Add to Cart"}
                    </button>
                    <button
                      onClick={handleFavoriteToggle}
                      className="w-full border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-800 text-gray-900 dark:text-white py-3 px-6 rounded-lg font-medium transition"
                    >
                      <i
                        className={`${isFavorite ? "fas" : "far"} fa-heart mr-2 ${isFavorite ? "text-red-500" : ""}`}
                      ></i>
                      Add to Favorite
                    </button>
                  </div>

                  {/* Farmer Contact */}
                  <div className="bg-primary-50 dark:bg-primary-900 rounded-xl p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 rounded-full bg-primary-100 dark:bg-primary-800 flex items-center justify-center">
                        <i className="fas fa-user text-primary-600 dark:text-primary-400"></i>
                      </div>
                      <div>
                        <h4 className="font-semibold text-gray-900 dark:text-white">
                          {product.farmer?.name || "Farmer"}
                        </h4>
                        <p className="text-sm text-gray-600 dark:text-gray-400">
                          {product.farmer?.email ||
                            "Contact information not available"}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Tabs Section */}
              <div className="mt-16">
                <div className="border-b border-gray-200 dark:border-gray-700">
                  <nav className="flex space-x-8">
                    {[
                      "description",
                      "nutrition",
                      "storage",
                      "reviews",
                      "farmer",
                    ].map((tab) => (
                      <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        className={`py-4 px-1 border-b-2 font-medium text-sm ${
                          activeTab === tab
                            ? "border-primary-500 text-primary-600 dark:text-primary-400"
                            : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
                        }`}
                      >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    ))}
                  </nav>
                </div>

                <div className="py-8">
                  {/* Description Tab */}
                  {activeTab === "description" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-4">
                        Product Description
                      </h3>
                      <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                        {product.description ||
                          "No description available for this product."}
                      </p>

                      {product.features && product.features.length > 0 && (
                        <div className="mt-6">
                          <h4 className="text-lg font-medium mb-3">
                            Key Features
                          </h4>
                          <ul className="list-disc list-inside space-y-1">
                            {product.features.map((feature, index) => (
                              <li
                                key={index}
                                className="text-gray-600 dark:text-gray-400"
                              >
                                {feature}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Nutrition Tab */}
                  {activeTab === "nutrition" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Nutritional Information
                      </h3>

                      {product.nutritionalInformation ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          <p className="text-gray-600 dark:text-gray-400">
                            {product.nutritionalInformation}
                          </p>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-apple-alt text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Nutritional information is not available for this
                            product.
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Storage Instructions Tab */}
                  {activeTab === "storage" && (
                    <div className="prose dark:prose-invert max-w-none">
                      <h3 className="text-xl font-semibold mb-6">
                        Storage Instructions
                      </h3>

                      {product.storageInstructions ? (
                        <div className="bg-white dark:bg-gray-800 rounded-lg p-6">
                          <p className="text-gray-600 dark:text-gray-400">
                            {product.storageInstructions}
                          </p>
                        </div>
                      ) : (
                        <div className="text-center py-8">
                          <i className="fas fa-warehouse text-4xl text-gray-400 mb-4"></i>
                          <p className="text-gray-600 dark:text-gray-400">
                            Storage instructions are not available for this
                            product.
                          </p>
                        </div>
                      )}

                      {/* General storage tips */}
                      <div className="mt-8">
                        <h4 className="text-lg font-medium mb-4">
                          General Storage Tips
                        </h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
                            <h5 className="font-medium text-blue-900 dark:text-blue-100 mb-2">
                              <i className="fas fa-thermometer-half mr-2"></i>
                              Temperature
                            </h5>
                            <p className="text-sm text-blue-700 dark:text-blue-300">
                              Store in a cool, dry place away from direct
                              sunlight
                            </p>
                          </div>
                          <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
                            <h5 className="font-medium text-green-900 dark:text-green-100 mb-2">
                              <i className="fas fa-tint mr-2"></i>
                              Humidity
                            </h5>
                            <p className="text-sm text-green-700 dark:text-green-300">
                              Keep in low humidity environment to prevent
                              spoilage
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Reviews Tab */}
                  {activeTab === "reviews" && (
                    <div>
                      <div className="flex items-center justify-between mb-8">
                        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                          Customer Reviews (
                          {product.reviewCount || product.totalReviews || 0})
                        </h2>
                        {session && hasPurchasedProduct && (
                          <button
                            onClick={() => setShowReviewForm(true)}
                            className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg font-medium transition"
                          >
                            <i className="fas fa-plus mr-2"></i>
                            Write Review
                          </button>
                        )}
                        {session &&
                          !hasPurchasedProduct &&
                          !checkingPurchase && (
                            <div className="text-sm text-gray-500 dark:text-gray-400 italic">
                              You need to purchase this product to write a
                              review
                            </div>
                          )}
                        {checkingPurchase && (
                          <div className="text-sm text-gray-500 dark:text-gray-400">
                            <i className="fas fa-spinner fa-spin mr-2"></i>
                            Checking purchase history...
                          </div>
                        )}
                      </div>

                      {/* Review Summary */}
                      <div className="bg-white dark:bg-gray-800 rounded-xl p-6 mb-8 shadow-lg border border-gray-100 dark:border-gray-700">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                          {/* Overall Rating Section */}
                          <div className="text-center">
                            <div className="mb-4">
                              {(() => {
                                // Calculate actual average rating from reviews if available
                                const actualReviewCount = reviews?.length || 0;
                                let displayRating = product.averageRating || 0;

                                // If we have reviews but no product rating, calculate from reviews
                                if (
                                  actualReviewCount > 0 &&
                                  (!product.averageRating ||
                                    product.averageRating === 0)
                                ) {
                                  const totalRating = reviews.reduce(
                                    (sum, review) => sum + (review.rating || 0),
                                    0,
                                  );
                                  displayRating =
                                    totalRating / actualReviewCount;
                                }

                                return (
                                  <>
                                    <div className="text-5xl font-bold text-primary-600 dark:text-primary-400 mb-2">
                                      {displayRating.toFixed(1)}
                                    </div>
                                    <StarRating
                                      rating={displayRating}
                                      size="lg"
                                    />
                                    <p className="text-gray-600 dark:text-gray-400 mt-3 text-lg font-medium">
                                      Based on{" "}
                                      <span className="text-primary-600 dark:text-primary-400 font-bold">
                                        {actualReviewCount}
                                      </span>{" "}
                                      {actualReviewCount === 1
                                        ? "review"
                                        : "reviews"}
                                    </p>
                                  </>
                                );
                              })()}
                            </div>

                            {/* Review Quality Indicator */}
                            <div className="mt-4 p-3 bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-900/20 dark:to-blue-900/20 rounded-lg">
                              <div className="flex items-center justify-center space-x-2 text-sm">
                                <i className="fas fa-shield-alt text-green-600"></i>
                                <span className="font-medium text-gray-700 dark:text-gray-300">
                                  {(reviews?.length || 0) > 0
                                    ? `${reviews.length} verified review${reviews.length === 1 ? "" : "s"}`
                                    : "No reviews yet"}
                                </span>
                              </div>
                            </div>
                          </div>

                          {/* Rating Distribution */}
                          <div className="space-y-3">
                            <h4 className="font-semibold text-gray-900 dark:text-white mb-4">
                              Rating Breakdown
                            </h4>
                            {(() => {
                              const ratingDistribution =
                                calculateRatingDistribution();
                              const totalReviews = reviews?.length || 0;

                              return [5, 4, 3, 2, 1].map((rating) => {
                                const count = ratingDistribution[rating];
                                const percentage =
                                  totalReviews > 0
                                    ? (count / totalReviews) * 100
                                    : 0;

                                return (
                                  <div
                                    key={rating}
                                    className="flex items-center space-x-3"
                                  >
                                    <span className="text-sm font-medium text-gray-600 dark:text-gray-400 w-12">
                                      {rating} star{rating === 1 ? "" : "s"}
                                    </span>
                                    <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-3 overflow-hidden">
                                      <div
                                        className="bg-gradient-to-r from-yellow-400 to-yellow-500 h-3 rounded-full transition-all duration-500 ease-out"
                                        style={{ width: `${percentage}%` }}
                                      ></div>
                                    </div>
                                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300 w-8 text-right">
                                      {count}
                                    </span>
                                    <span className="text-xs text-gray-500 dark:text-gray-400 w-12 text-right">
                                      {percentage.toFixed(0)}%
                                    </span>
                                  </div>
                                );
                              });
                            })()}

                            {/* Total Reviews Summary */}
                            <div className="mt-4 pt-3 border-t border-gray-200 dark:border-gray-600">
                              <div className="text-center text-sm text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  {(reviews?.length || 0) === 0 &&
                                    "Be the first to review this product!"}
                                  {(reviews?.length || 0) === 1 &&
                                    "1 customer has reviewed this product"}
                                  {(reviews?.length || 0) > 1 &&
                                    `${reviews.length} customers have reviewed this product`}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Review Form Modal */}
                      {showReviewForm && (
                        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 w-full max-w-md">
                            <div className="flex items-center justify-between mb-6">
                              <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                                Write a Review
                              </h3>
                              <button
                                onClick={() => setShowReviewForm(false)}
                                className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                              >
                                <i className="fas fa-times"></i>
                              </button>
                            </div>

                            <form
                              onSubmit={
                                editingReview
                                  ? (e) => {
                                      e.preventDefault();
                                      handleUpdateReview();
                                    }
                                  : handleSubmitReview
                              }
                              className="space-y-4"
                            >
                              <div>
                                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                                  Rating
                                </label>
                                <div className="flex items-center space-x-1">
                                  {[1, 2, 3, 4, 5].map((star) => (
                                    <button
                                      key={star}
                                      type="button"
                                      onClick={() =>
                                        setReviewForm({
                                          ...reviewForm,
                                          rating: star,
                                        })
                                      }
                                      className={`text-2xl ${
                                        star <= reviewForm.rating
                                          ? "text-yellow-400"
                                          : "text-gray-300 dark:text-gray-600"
                                      }`}
                                    >

                                    </button>
                                  ))}
                                </div>
                              </div>

                              <div>
                                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                                  Comment
                                </label>
                                <textarea
                                  value={reviewForm.comment}
                                  onChange={(e) =>
                                    setReviewForm({
                                      ...reviewForm,
                                      comment: e.target.value,
                                    })
                                  }
                                  rows={4}
                                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                                  placeholder="Share your experience with this product..."
                                  required
                                />
                              </div>

                              <div className="flex space-x-3">
                                <button
                                  type="button"
                                  onClick={() => {
                                    setShowReviewForm(false);
                                    setEditingReview(null);
                                    setReviewForm({ rating: 5, comment: "" });
                                  }}
                                  className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
                                >
                                  Cancel
                                </button>
                                <button
                                  type="submit"
                                  disabled={
                                    editingReview
                                      ? isUpdatingReview
                                      : isSubmittingReview
                                  }
                                  className="flex-1 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50"
                                >
                                  {editingReview
                                    ? isUpdatingReview
                                      ? "Updating..."
                                      : "Update Review"
                                    : isSubmittingReview
                                      ? "Submitting..."
                                      : "Submit Review"}
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      )}

                      {/* Individual Reviews */}
                      <div className="space-y-8">
                        {reviews && reviews.length > 0 ? (
                          reviews.map((review, index) => (
                            <div
                              key={review._id}
                              className="group bg-gradient-to-br from-white via-gray-50 to-white dark:from-gray-800 dark:via-gray-850 dark:to-gray-800 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-800"
                              style={{ animationDelay: `${index * 100}ms` }}
                            >
                              <div className="flex items-start justify-between mb-4">
                                <div className="flex items-center space-x-4">
                                  <div className="relative">
                                    <div className="w-12 h-12 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 dark:from-primary-500 dark:to-primary-700 flex items-center justify-center shadow-lg">
                                      <span className="text-white font-bold text-lg">
                                        {(review.reviewer || "Anonymous")
                                          .charAt(0)
                                          .toUpperCase()}
                                      </span>
                                    </div>
                                    <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white dark:border-gray-800 flex items-center justify-center">
                                      <i className="fas fa-check text-white text-xs"></i>
                                    </div>
                                  </div>
                                  <div className="flex-1">
                                    <div className="flex items-center gap-3 mb-1">
                                      <h4 className="font-semibold text-gray-900 dark:text-white text-lg">
                                        {review.reviewer || "Anonymous"}
                                      </h4>
                                      <span className="px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-xs font-medium rounded-full">
                                        Verified Buyer
                                      </span>
                                    </div>
                                    <div className="flex items-center space-x-3">
                                      <div className="flex items-center space-x-1">
                                        {[1, 2, 3, 4, 5].map((star) => (
                                          <span
                                            key={star}
                                            className={`text-lg transition-all duration-200 ${
                                              star <= review.rating
                                                ? "text-yellow-400 drop-shadow-sm"
                                                : "text-gray-300 dark:text-gray-600"
                                            }`}
                                          >

                                          </span>
                                        ))}
                                      </div>
                                      <span className="text-sm font-medium text-yellow-600 dark:text-yellow-400">
                                        {review.rating}/5
                                      </span>
                                      <span className="text-gray-400"></span>
                                      <span className="text-sm text-gray-500 dark:text-gray-400 font-medium">
                                        {new Date(
                                          review.createdAt || review.date,
                                        ).toLocaleDateString("en-US", {
                                          year: "numeric",
                                          month: "short",
                                          day: "numeric",
                                        })}
                                      </span>
                                    </div>
                                  </div>
                                </div>
                                <div className="flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                  {/* Debug information */}
                                  {console.log("DEBUG Review comparison:", {
                                    sessionUserId: session?.user?.userId,
                                    reviewUserId: review.userId,
                                    areEqual:
                                      session?.user?.userId === review.userId,
                                    sessionUser: session?.user,
                                  })}

                                  {/* Show edit/delete buttons only for user's own review */}
                                  {session?.user?.userId === review.userId ? (
                                    <>
                                      <button
                                        onClick={() => {
                                          setEditingReview(review);
                                          setReviewForm({
                                            rating: review.rating,
                                            comment: review.comment,
                                          });
                                          setShowReviewForm(true);
                                        }}
                                        className="p-2 hover:bg-blue-100 dark:hover:bg-blue-900/20 rounded-lg transition-colors"
                                        title="Edit Review"
                                      >
                                        <i className="fas fa-edit text-blue-500 hover:text-blue-600"></i>
                                      </button>
                                      <button
                                        onClick={() =>
                                          handleDeleteReview(review._id)
                                        }
                                        disabled={isDeletingReview}
                                        className="p-2 hover:bg-red-100 dark:hover:bg-red-900/20 rounded-lg transition-colors"
                                        title="Delete Review"
                                      >
                                        <i className="fas fa-trash text-red-500 hover:text-red-600"></i>
                                      </button>
                                    </>
                                  ) : (
                                    <>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-thumbs-up text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                      <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
                                        <i className="fas fa-share text-gray-400 hover:text-primary-500"></i>
                                      </button>
                                    </>
                                  )}
                                </div>
                              </div>

                              <div className="relative">
                                <div className="absolute left-0 top-0 w-1 h-full bg-gradient-to-b from-primary-400 to-primary-600 rounded-full opacity-20"></div>
                                <p className="text-gray-700 dark:text-gray-300 leading-relaxed pl-6 text-base">
                                  "{review.comment}"
                                </p>
                              </div>

                              {/* Review actions footer */}
                              <div className="mt-4 pt-4 border-t border-gray-100 dark:border-gray-700 flex items-center justify-between">
                                <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-heart text-red-400"></i>
                                    <span>Helpful</span>
                                  </span>
                                  <span className="flex items-center space-x-1">
                                    <i className="fas fa-comment text-blue-400"></i>
                                    <span>Reply</span>
                                  </span>
                                </div>
                                <div className="flex items-center space-x-2">
                                  <div className="flex -space-x-1">
                                    {[1, 2, 3].map((i) => (
                                      <div
                                        key={i}
                                        className="w-6 h-6 rounded-full bg-gray-300 dark:bg-gray-600 border-2 border-white dark:border-gray-800"
                                      ></div>
                                    ))}
                                  </div>
                                  <span className="text-xs text-gray-500">
                                    +2 found helpful
                                  </span>
                                </div>
                              </div>
                            </div>
                          ))
                        ) : (
                          <div className="text-center py-16 bg-gradient-to-br from-gray-50 via-white to-gray-50 dark:from-gray-800 dark:via-gray-900 dark:to-gray-800 rounded-2xl border-2 border-dashed border-gray-200 dark:border-gray-700">
                            <div className="relative inline-block mb-6">
                              <div className="w-20 h-20 bg-gradient-to-br from-primary-100 to-primary-200 dark:from-primary-800 dark:to-primary-900 rounded-full flex items-center justify-center mx-auto shadow-lg">
                                <i className="fas fa-star text-3xl text-primary-500 dark:text-primary-400"></i>
                              </div>
                              <div className="absolute -top-2 -right-2 w-8 h-8 bg-yellow-400 rounded-full flex items-center justify-center animate-bounce">
                                <i className="fas fa-plus text-white text-sm"></i>
                              </div>
                            </div>
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-3">
                              No Reviews Yet
                            </h3>
                            <p className="text-gray-600 dark:text-gray-400 mb-6 max-w-md mx-auto">
                              Be the first to share your experience with this
                              amazing product! Your review helps other customers
                              make informed decisions.
                            </p>
                            {session && (
                              <button
                                onClick={() => setShowReviewForm(true)}
                                className="inline-flex items-center bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                              >
                                <i className="fas fa-edit mr-2"></i>
                                Write First Review
                              </button>
                            )}
                          </div>
                        )}

                        {/* Load More Reviews Button */}
                        {hasMoreReviews && (
                          <div className="text-center pt-8">
                            <button
                              onClick={loadMoreReviews}
                              className="group inline-flex items-center bg-white dark:bg-gray-800 hover:bg-gray-50 dark:hover:bg-gray-700 text-gray-900 dark:text-white px-8 py-4 rounded-xl font-semibold border-2 border-gray-200 dark:border-gray-700 hover:border-primary-300 dark:hover:border-primary-600 shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                            >
                              <i className="fas fa-chevron-down mr-3 group-hover:animate-bounce"></i>
                              Load More Reviews
                              <span className="ml-3 px-2 py-1 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 text-sm rounded-full">
                                +{Math.min(5, reviews?.length || 0)}
                              </span>
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Farmer Tab */}
                  {activeTab === "farmer" && (
                    <div>
                      <h3 className="text-xl font-semibold mb-6">
                        About the Farmer
                      </h3>
                      <div className="bg-white dark:bg-gray-800 rounded-lg p-6 shadow-lg">
                        <div className="flex items-start gap-4">
                          <div className="w-16 h-16 rounded-full bg-primary-100 dark:bg-primary-900 flex items-center justify-center flex-shrink-0">
                            <i className="fas fa-user text-2xl text-primary-600 dark:text-primary-400"></i>
                          </div>
                          <div className="flex-1">
                            <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                              {product.farmer?.farmName ||
                                product.farmer?.name ||
                                "Farm Name"}
                            </h4>
                            <p className="text-gray-600 dark:text-gray-400 mb-3">
                              {product.farmer?.email ||
                                "Farmer contact not available"}
                            </p>

                            {product.farmer?.location && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-3">
                                <i className="fas fa-map-marker-alt mr-2"></i>
                                <span>{product.farmer.location}</span>
                              </div>
                            )}

                            {product.farmer?.phone && (
                              <div className="flex items-center text-gray-600 dark:text-gray-400 mb-4">
                                <i className="fas fa-phone mr-2"></i>
                                <span>{product.farmer.phone}</span>
                              </div>
                            )}

                            <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                              {product.farmer?.description ||
                                "A dedicated farmer committed to providing fresh, quality produce to the community."}
                            </p>

                            <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                              <Link
                                href={`/details?id=${product.farmerId || product.farmer?.id}`}
                                className="inline-flex items-center text-primary-600 hover:text-primary-700 font-medium"
                              >
                                <i className="fas fa-external-link-alt mr-2"></i>
                                View All Products from this Farmer
                              </Link>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Related Products Section - Enhanced */}
                  {relatedProducts.length > 0 && (
                    <div className="mt-16 bg-gradient-to-br from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 rounded-2xl p-8 shadow-lg border border-gray-100 dark:border-gray-700">
                      {/* Section Header */}
                      <div className="text-center mb-10">
                        <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full mb-4 shadow-lg">
                          <i className="fas fa-box-open text-2xl text-white"></i>
                        </div>
                        <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-3">
                          Related Products
                        </h3>
                        <p className="text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
                          Discover similar high-quality products from our
                          trusted farmers. Each item is carefully selected to
                          meet our quality standards.
                        </p>
                        <div className="mt-4 flex items-center justify-center space-x-2 text-sm text-gray-500 dark:text-gray-400">
                          <i className="fas fa-leaf text-green-500"></i>
                          <span>{relatedProducts.length} products found</span>
                          <span></span>
                          <i className="fas fa-truck text-blue-500"></i>
                          <span>Fast delivery available</span>
                        </div>
                      </div>

                      {/* Enhanced Product Grid */}
                      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                        {relatedProducts.map((relatedProduct, index) => (
                          <div
                            key={relatedProduct._id}
                            className="group transform transition-all duration-300 hover:-translate-y-2"
                            style={{ animationDelay: `${index * 100}ms` }}
                          >
                            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100 dark:border-gray-700 hover:border-primary-200 dark:hover:border-primary-600 overflow-hidden">
                              {/* Product Image */}
                              <div className="relative aspect-square overflow-hidden">
                                <Image
                                  src={
                                    relatedProduct.image ||
                                    relatedProduct.images?.[0] ||
                                    "/placeholder-image.jpg"
                                  }
                                  alt={relatedProduct.name}
                                  width={300}
                                  height={300}
                                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                                />

                                {/* Product Badges */}
                                <div className="absolute top-3 left-3 flex flex-col space-y-2">
                                  {relatedProduct.isOrganic && (
                                    <span className="bg-green-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                      Organic
                                    </span>
                                  )}
                                  {relatedProduct.isFresh && (
                                    <span className="bg-blue-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                                      Fresh
                                    </span>
                                  )}
                                </div>

                                {/* Quick Actions Overlay */}
                                <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
                                  <Link
                                    href={`/details?id=${relatedProduct._id}`}
                                    className="bg-white text-gray-900 px-6 py-2 rounded-full font-medium hover:bg-gray-100 transition-colors transform hover:scale-105"
                                  >
                                    View Details
                                  </Link>
                                </div>
                              </div>

                              {/* Product Info */}
                              <div className="p-6">
                                <div className="mb-3">
                                  <h4 className="font-bold text-lg text-gray-900 dark:text-white line-clamp-1 group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                                    {relatedProduct.name}
                                  </h4>
                                  <p className="text-sm text-gray-500 dark:text-gray-400 line-clamp-1">
                                    by{" "}
                                    {relatedProduct.farmer?.name ||
                                      relatedProduct.farmer?.farmName ||
                                      "Unknown Farmer"}
                                  </p>
                                </div>

                                {/* Rating */}
                                <div className="flex items-center space-x-1 mb-3">
                                  <StarRating
                                    rating={relatedProduct.averageRating || 0}
                                    size="sm"
                                  />
                                  <span className="text-xs text-gray-500 dark:text-gray-400">
                                    (
                                    {relatedProduct.reviewCount ||
                                      relatedProduct.totalReviews ||
                                      0}
                                    )
                                  </span>
                                </div>

                                {/* Price and Stock */}
                                <div className="flex items-center justify-between mb-4">
                                  <div>
                                    <span className="text-xl font-bold text-primary-600 dark:text-primary-400">
                                      ${(relatedProduct.price || 0).toFixed(2)}
                                    </span>
                                    <span className="text-sm text-gray-500 dark:text-gray-400">
                                      /{relatedProduct.unit || "kg"}
                                    </span>
                                  </div>
                                  <div className="text-right">
                                    <div
                                      className={`text-xs font-medium ${
                                        (relatedProduct.stock || 0) > 0
                                          ? "text-green-600 dark:text-green-400"
                                          : "text-red-600 dark:text-red-400"
                                      }`}
                                    >
                                      {(relatedProduct.stock || 0) > 0
                                        ? `${relatedProduct.stock} ${relatedProduct.unit || "kg"} left`
                                        : "Out of stock"}
                                    </div>
                                  </div>
                                </div>

                                {/* Action Button */}
                                <Link
                                  href={`/details?id=${relatedProduct._id}`}
                                  className="w-full bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white py-2.5 px-4 rounded-lg font-medium transition-all duration-200 text-center block group-hover:shadow-lg transform group-hover:scale-[1.02]"
                                >
                                  <i className="fas fa-eye mr-2"></i>
                                  View Product
                                </Link>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>

                      {/* Browse More Section */}
                      <div className="mt-12 text-center">
                        <div className="border-t border-gray-200 dark:border-gray-700 pt-8">
                          <p className="text-gray-600 dark:text-gray-400 mb-4">
                            Looking for more products?
                          </p>
                          <Link
                            href="/products"
                            className="inline-flex items-center bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
                          >
                            <i className="fas fa-shopping-bag mr-2"></i>
                            Browse All Products
                            <i className="fas fa-arrow-right ml-2"></i>
                          </Link>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useRef, useMemo } from "react";
import { useSession } from "next-auth/react";
import Link from "next/link";
import Footer from "@/components/Footer";
import { debounce } from "@/utils/debounce";
import { useOrderStatusUpdate } from "@/hooks/useOrderStatusUpdate";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const { updateOrderStatus, updating } = useOrderStatusUpdate();
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);

  // Enhanced state for new features
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed"); // 'detailed', 'compact'
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Performance optimizations
  const [requestInProgress, setRequestInProgress] = useState(false);

  const intervalRef = useRef(null);
  const abortControllerRef = useRef(null);
  const cacheRef = useRef(new Map());

  const ordersPerPage = viewMode === "compact" ? 20 : 10;
  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes
  const MAX_RETRY_ATTEMPTS = 3;
  const RETRY_DELAY = 1000; // 1 second

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Optimized cache management
  const getCacheKey = useCallback((userId, userEmail) => {
    return `farmer-orders-${userId || userEmail}`;
  }, []);

  const getCachedData = useCallback(
    (cacheKey) => {
      const cached = cacheRef.current.get(cacheKey);
      if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
        return cached.data;
      }
      return null;
    },
    [CACHE_DURATION],
  );

  const setCachedData = useCallback(
    (cacheKey, data) => {
      cacheRef.current.set(cacheKey, {
        data,
        timestamp: Date.now(),
      });
      // Clean up old cache entries
      for (const [key, value] of cacheRef.current.entries()) {
        if (Date.now() - value.timestamp > CACHE_DURATION) {
          cacheRef.current.delete(key);
        }
      }
    },
    [CACHE_DURATION],
  );

  // Enhanced file download with better error handling
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion with better data handling
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email",
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Enhanced fetch function with retry logic
  const fetchOrdersWithRetry = useCallback(
    async (showLoading = true, retryAttempt = 0) => {
      if (!session?.user || requestInProgress) return;

      try {
        if (showLoading) setLoading(true);
        setRequestInProgress(true);

        const userId =
          session.user.userId || session.user.id || session.user._id;
        const userEmail = session.user.email;
        const cacheKey = getCacheKey(userId, userEmail);

        // Check cache first
        if (!showLoading && retryAttempt === 0) {
          const cachedData = getCachedData(cacheKey);
          if (cachedData) {
            setOrders(cachedData);
            setRequestInProgress(false);
            return;
          }
        }

        // Cancel previous request
        if (abortControllerRef.current) {
          abortControllerRef.current.abort();
        }
        abortControllerRef.current = new AbortController();

        const params = new URLSearchParams();
        if (userId) params.append("farmerId", userId);
        if (userEmail) params.append("farmerEmail", userEmail);

        const response = await fetch(`/api/orders?${params.toString()}`, {
          cache: "no-store",
          signal: abortControllerRef.current.signal,
          headers: {
            "Cache-Control": "no-cache",
            Pragma: "no-cache",
            "X-Requested-With": "XMLHttpRequest",
          },
        });

        if (!response.ok) {
          const error = new Error(
            `HTTP ${response.status}: ${response.statusText}`,
          );
          console.error("Response error:", error);
          throw error;
        }

        const data = await response.json();
        const ordersData = data.orders || [];

        // Check for new orders for notifications (only if not initial load)
        if (
          !showLoading &&
          orders.length > 0 &&
          ordersData.length > orders.length
        ) {
          const newOrdersCount = ordersData.length - orders.length;
          addNotification(
            `${newOrdersCount} new order(s) received!`,
            "success",
          );
        }

        setOrders(ordersData);
        setCachedData(cacheKey, ordersData);
      } catch (error) {
        if (error.name === "AbortError") {
          return; // Request was cancelled, don't treat as error
        }

        console.error("Error fetching orders:", error);

        // Retry logic
        if (retryAttempt < MAX_RETRY_ATTEMPTS) {
          addNotification(
            `Retrying to fetch orders... (${retryAttempt + 1}/${MAX_RETRY_ATTEMPTS})`,
            "warning",
          );

          setTimeout(
            () => {
              fetchOrdersWithRetry(showLoading, retryAttempt + 1);
            },
            RETRY_DELAY * Math.pow(2, retryAttempt),
          ); // Exponential backoff

          return;
        }

        setOrders([]);
        addNotification("Failed to fetch orders. Please try again.", "error");
      } finally {
        if (showLoading) setLoading(false);
        setRequestInProgress(false);
      }
    },
    [
      session,
      orders.length,
      getCacheKey,
      getCachedData,
      setCachedData,
      requestInProgress,
      addNotification,
      MAX_RETRY_ATTEMPTS,
      RETRY_DELAY,
    ],
  );

  // Memoized filtered orders with performance optimization
  const memoizedFilteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter with improved regex
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999); // Include entire end date
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting with improved performance
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Initial fetch
  useEffect(() => {
    if (session?.user && !requestInProgress) {
      fetchOrdersWithRetry();
    }
  }, [session?.user]); // Removed fetchOrdersWithRetry dependency

  // Update filtered orders when memoized value changes
  useEffect(() => {
    setFilteredOrders(memoizedFilteredOrders);
    setCurrentPage(1);
  }, [memoizedFilteredOrders]);

  // Auto-refresh functionality with better management
  useEffect(() => {
    if (autoRefresh && !loading && !requestInProgress) {
      intervalRef.current = setInterval(() => {
        fetchOrdersWithRetry(false);
      }, 30000); // Refresh every 30 seconds
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    }
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [autoRefresh, loading, requestInProgress]); // Removed fetchOrdersWithRetry dependency

  // Optimized bulk operations with better error handling
  const handleBulkStatusUpdate = async (newStatus) => {
    if (selectedOrders.length === 0) {
      addNotification("Please select orders to update", "warning");
      return;
    }

    if (
      !confirm(
        `Are you sure you want to mark ${selectedOrders.length} orders as ${newStatus}?`,
      )
    ) {
      return;
    }

    try {
      setRequestInProgress(true);

      // Process in batches for better performance
      const batchSize = 5;
      const batches = [];
      for (let i = 0; i < selectedOrders.length; i += batchSize) {
        batches.push(selectedOrders.slice(i, i + batchSize));
      }

      let successCount = 0;
      let errorCount = 0;

      for (const batch of batches) {
        const updatePromises = batch.map(async (orderId) => {
          try {
            const response = await fetch(`/api/orders/${orderId}`, {
              method: "PATCH",
              headers: {
                "Content-Type": "application/json",
                "X-Requested-With": "XMLHttpRequest",
              },
              body: JSON.stringify({
                status: newStatus,
                statusHistory: {
                  status: newStatus,
                  timestamp: new Date().toISOString(),
                  updatedBy: session.user.email || session.user.name,
                },
              }),
            });

            if (!response.ok) {
              const error = new Error(`Failed to update order ${orderId}`);
              console.error("Bulk update error:", error);
              throw error;
            }

            return { success: true, orderId };
          } catch (error) {
            console.error(`Error updating order ${orderId}:`, error);
            return { success: false, orderId, error: error.message };
          }
        });

        const results = await Promise.allSettled(updatePromises);

        results.forEach((result) => {
          if (result.status === "fulfilled" && result.value.success) {
            successCount++;
          } else {
            errorCount++;
          }
        });
      }

      if (successCount > 0) {
        // Update local state optimistically
        setOrders((prev) =>
          prev.map((order) =>
            selectedOrders.includes(order._id)
              ? { ...order, status: newStatus }
              : order,
          ),
        );
        setSelectedOrders([]);

        // Clear cache to force refresh
        cacheRef.current.clear();
      }

      if (errorCount === 0) {
        addNotification(
          `${successCount} orders updated successfully!`,
          "success",
        );
      } else {
        addNotification(
          `${successCount} orders updated, ${errorCount} failed`,
          "warning",
        );
      }
    } catch (error) {
      console.error("Bulk update error:", error);
      addNotification("Failed to update orders", "error");
    } finally {
      setRequestInProgress(false);
    }
  };

  // Enhanced single order status update using the hook
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      // Use the hook for updating status with real-time broadcasting
      const result = await updateOrderStatus(orderId, newStatus, {
        estimatedDeliveryDate:
          newStatus === "shipped"
            ? (() => {
                const date = new Date();
                date.setDate(date.getDate() + 3);
                return date.toISOString();
              })()
            : undefined,
      });

      // Optimistic update of local state
      setOrders((prev) =>
        prev.map((order) =>
          order._id === orderId
            ? {
                ...order,
                status: newStatus,
                estimatedDeliveryDate:
                  result.order?.estimatedDeliveryDate ||
                  order.estimatedDeliveryDate,
                statusHistory: result.order?.statusHistory || [
                  ...(Array.isArray(order.statusHistory)
                    ? order.statusHistory
                    : []),
                  {
                    status: newStatus,
                    timestamp: new Date().toISOString(),
                    updatedBy: session.user.email || session.user.name,
                  },
                ],
              }
            : order,
        ),
      );

      // Clear cache to force refresh
      cacheRef.current.clear();

      const successMessages = {
        confirmed: "Order confirmed! Dashboard updated automatically.",
        shipped: "Order marked as shipped! Dashboard updated automatically.",
        delivered: "Order completed! Dashboard updated automatically.",
        cancelled: "Order cancelled! Dashboard updated automatically.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully! Dashboard updated automatically.`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    }
  };

  // Enhanced refresh with better UX
  const handleRefresh = async () => {
    setRefreshing(true);
    cacheRef.current.clear(); // Clear cache to force fresh data
    await fetchOrdersWithRetry(false);
    setRefreshing(false);
    addNotification("Orders refreshed successfully!", "success");
  };

  // Optimized search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Enhanced status filter handler
  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value);
  };

  // Optimized export functionality with better performance
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400 text-lg">
            Loading your enhanced orders dashboard...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      {/* Notification System */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`px-6 py-4 rounded-lg shadow-lg text-white transform transition-all duration-500 ${
              notification.type === "success"
                ? "bg-green-600"
                : notification.type === "error"
                  ? "bg-red-600"
                  : notification.type === "warning"
                    ? "bg-yellow-600"
                    : "bg-blue-600"
            }`}
          >
            <div className="flex items-center">
              <i
                className={`fas ${
                  notification.type === "success"
                    ? "fa-check-circle"
                    : notification.type === "error"
                      ? "fa-exclamation-circle"
                      : notification.type === "warning"
                        ? "fa-exclamation-triangle"
                        : "fa-info-circle"
                } mr-2`}
              ></i>
              {notification.message}
            </div>
          </div>
        ))}
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center">
          <div className="bg-white dark:bg-gray-800 rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Export Orders
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              {selectedOrders.length > 0
                ? `Export ${selectedOrders.length} selected orders`
                : `Export all ${filteredOrders.length} filtered orders`}
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => exportOrders("csv")}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-csv mr-2"></i>
                Export as CSV
              </button>
              <button
                onClick={() => exportOrders("json")}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-code mr-2"></i>
                Export as JSON
              </button>
            </div>
            <button
              onClick={() => setShowExportModal(false)}
              className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg font-medium transition"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Enhanced Breadcrumb with Real-time Indicators */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <nav className="flex" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-home mr-1"></i>Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li>
                  <Link
                    href="/manage"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-cog mr-1"></i>Manage
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  <i className="fas fa-chart-line mr-1"></i>Advanced Order
                  Management
                </li>
              </ol>
            </nav>

            {/* Real-time Status Indicator */}
            <div className="flex items-center space-x-2">
              <div
                className={`w-3 h-3 rounded-full ${autoRefresh ? "bg-green-500 animate-pulse" : "bg-gray-400"}`}
              ></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {autoRefresh ? "Live" : "Static"}
              </span>
            </div>
          </div>
        </div>

        {/* Enhanced Page Header with Quick Actions */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-green-600 to-blue-600 p-4 rounded-2xl">
                <i className="fas fa-chart-line text-white text-2xl"></i>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Advanced Order Management
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Comprehensive order tracking and analytics dashboard
                </p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className="text-sm text-gray-500">
                    Last updated: {new Date().toLocaleTimeString()}
                  </span>
                  {autoRefresh && (
                    <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                      Auto-refresh enabled
                    </span>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6 lg:mt-0 flex flex-wrap gap-3">
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`inline-flex items-center px-4 py-3 rounded-lg font-medium transition ${
                  autoRefresh
                    ? "bg-green-600 hover:bg-green-700 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600"
                }`}
              >
                <i
                  className={`fas ${autoRefresh ? "fa-pause" : "fa-play"} mr-2`}
                ></i>
                {autoRefresh ? "Pause" : "Auto"} Refresh
              </button>

              <button
                onClick={() => setShowExportModal(true)}
                className="inline-flex items-center px-4 py-3 bg-orange-600 hover:bg-orange-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-download mr-2"></i>
                Export
              </button>

              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                ></i>
                Refresh
              </button>

              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Enhanced Order Summary Cards with Animations */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            {Object.entries(orderSummary).map(([key, value], index) => {
              const config = {
                total: {
                  icon: "fa-shopping-cart",
                  color: "gray",
                  label: "Total",
                },
                pending: {
                  icon: "fa-clock",
                  color: "yellow",
                  label: "Pending",
                },
                confirmed: {
                  icon: "fa-check",
                  color: "blue",
                  label: "Confirmed",
                },
                shipped: {
                  icon: "fa-truck",
                  color: "purple",
                  label: "Shipped",
                },
                delivered: {
                  icon: "fa-check-circle",
                  color: "green",
                  label: "Delivered",
                },
                cancelled: {
                  icon: "fa-times-circle",
                  color: "red",
                  label: "Cancelled",
                },
              }[key];

              return (
                <div
                  key={key}
                  className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 transform hover:scale-105 transition-transform duration-200 cursor-pointer"
                  onClick={() =>
                    setStatusFilter(
                      key === "total"
                        ? "All Orders"
                        : config.label.toLowerCase(),
                    )
                  }
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <div className="flex items-center">
                    <div
                      className={`p-2 bg-${config.color}-100 dark:bg-${config.color}-900 rounded-lg`}
                    >
                      <i
                        className={`fas ${config.icon} text-${config.color}-600 dark:text-${config.color}-300`}
                      ></i>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                        {config.label}
                      </p>
                      <p
                        className={`text-2xl font-bold text-${config.color}-600 dark:text-${config.color}-400`}
                      >
                        {value}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Enhanced Filters and Controls */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 lg:mb-0">
                <i className="fas fa-filter mr-2"></i>
                Advanced Filters & Controls
              </h3>

              {/* View Mode Toggle */}
              <div className="flex space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {["detailed", "compact"].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-4 py-2 rounded-md text-sm font-medium transition ${
                      viewMode === mode
                        ? "bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow"
                        : "text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white"
                    }`}
                  >
                    <i
                      className={`fas ${
                        mode === "detailed"
                          ? "fa-list"
                          : mode === "compact"
                            ? "fa-th-list"
                            : ""
                      } mr-1`}
                    ></i>
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4">
              {/* Search */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    placeholder="Search by customer, email, or ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Status Filter
                </label>
                <select
                  value={statusFilter}
                  onChange={handleStatusChange}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import Link from "next/link";
import Footer from "@/components/Footer";
import { debounce } from "@/utils/debounce";
import { useOrderStatusUpdate } from "@/hooks/useOrderStatusUpdate";
import { useFarmerOrders } from "@/hooks/useFarmerOrders";

export default function FarmerOrders() {
  const { data: session, status } = useSession();
  const { updateOrderStatus, updating } = useOrderStatusUpdate();

  // Use React Query hook for orders data
  const {
    orders,
    isLoading: loading,
    error,
    isRefetching: refreshing,
    refetch: refetchOrders,
    refreshOrders,
  } = useFarmerOrders();

  // Local UI state
  const [statusFilter, setStatusFilter] = useState("All Orders");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [viewMode, setViewMode] = useState("detailed");
  const [sortBy, setSortBy] = useState("newest");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });
  const [showExportModal, setShowExportModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [autoRefresh, setAutoRefresh] = useState(false);

  const ordersPerPage = viewMode === "compact" ? 20 : 10;

  // Enhanced notification system
  const addNotification = useCallback((message, type = "info") => {
    const id = Date.now() + Math.random();
    const notification = { id, message, type, timestamp: new Date() };

    setNotifications((prev) => [notification, ...prev.slice(0, 4)]);

    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 5000);
  }, []);

  // Optimized debounced search
  const debouncedSearch = useMemo(
    () =>
      debounce((searchValue) => {
        setSearchTerm(searchValue);
      }, 300),
    [],
  );

  // Enhanced file download
  const downloadFile = useCallback(
    (content, filename, mimeType) => {
      try {
        const blob = new Blob([content], { type: mimeType });
        const url = URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.download = filename;
        link.style.display = "none";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      } catch (error) {
        console.error("Download error:", error);
        addNotification("Failed to download file", "error");
      }
    },
    [addNotification],
  );

  // Enhanced CSV conversion
  const convertToCSV = useCallback((data) => {
    const headers = [
      "Order ID",
      "Customer Name",
      "Customer Email", 
      "Status",
      "Total Amount",
      "Order Date",
      "Items Count",
      "Payment Method",
      "Delivery Address",
    ];

    const csvData = data.map((order) => [
      order._id?.slice(-8)?.toUpperCase() || "N/A",
      (order.customerName || order.userName || "").replace(/,/g, ";"),
      order.customerEmail || order.userEmail || "",
      order.status || "pending",
      order.farmerSubtotal || order.total || 0,
      new Date(order.createdAt).toLocaleDateString(),
      order.items?.length || 0,
      (order.paymentMethod || "Cash on Delivery").replace(/,/g, ";"),
      typeof order.deliveryAddress === "object"
        ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`.replace(
            /,/g,
            ";",
          )
        : (order.deliveryAddress || "Not provided").replace(/,/g, ";"),
    ]);

    return [headers, ...csvData]
      .map((row) => row.map((field) => `"${field}"`).join(","))
      .join("\n");
  }, []);

  // Memoized filtered orders
  const filteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Apply status filter
    if (statusFilter !== "All Orders") {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Apply search filter
    if (searchTerm.trim()) {
      const searchRegex = new RegExp(
        searchTerm.trim().replace(/[.*+?^${}()|[\]\\]/g, "\\$&"),
        "i",
      );
      filtered = filtered.filter((order) => {
        const orderItemsMatch = order.items?.some(
          (item) =>
            searchRegex.test(item.name) ||
            searchRegex.test(item.productName) ||
            searchRegex.test(item.category),
        );
        const customerMatch =
          searchRegex.test(order.customerName) ||
          searchRegex.test(order.customerEmail) ||
          searchRegex.test(order.userEmail);
        const orderIdMatch = searchRegex.test(order._id);

        return orderItemsMatch || customerMatch || orderIdMatch;
      });
    }

    // Apply date range filter
    if (dateRange.start) {
      const startDate = new Date(dateRange.start);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) >= startDate,
      );
    }
    if (dateRange.end) {
      const endDate = new Date(dateRange.end);
      endDate.setHours(23, 59, 59, 999);
      filtered = filtered.filter(
        (order) => new Date(order.createdAt) <= endDate,
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "newest":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "oldest":
          return new Date(a.createdAt) - new Date(b.createdAt);
        case "highest-value":
          return (
            (b.farmerSubtotal || b.total || 0) -
            (a.farmerSubtotal || a.total || 0)
          );
        case "lowest-value":
          return (
            (a.farmerSubtotal || a.total || 0) -
            (b.farmerSubtotal || b.total || 0)
          );
        case "customer-name":
          return (a.customerName || a.userName || "").localeCompare(
            b.customerName || b.userName || "",
          );
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateRange, sortBy]);

  // Reset page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [filteredOrders]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshOrders();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshOrders]);

  // Enhanced single order status update using the hook
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    const statusMessages = {
      confirmed:
        "confirm this order? This will notify the customer that their order has been accepted.",
      shipped:
        "mark this order as shipped? This will notify the customer that their order is on the way.",
      delivered:
        "mark this order as delivered? This will complete the order and notify the customer.",
      cancelled:
        "cancel this order? This action cannot be undone and will notify the customer.",
    };

    const confirmMessage =
      statusMessages[newStatus] || `mark this order as ${newStatus}?`;

    if (!confirm(`Are you sure you want to ${confirmMessage}`)) {
      return;
    }

    try {
      await updateOrderStatus(orderId, newStatus, {
        estimatedDeliveryDate:
          newStatus === "shipped"
            ? (() => {
                const date = new Date();
                date.setDate(date.getDate() + 3);
                return date.toISOString();
              })()
            : undefined,
      });

      const successMessages = {
        confirmed: "Order confirmed! Dashboard updated automatically.",
        shipped: "Order marked as shipped! Dashboard updated automatically.",
        delivered: "Order completed! Dashboard updated automatically.",
        cancelled: "Order cancelled! Dashboard updated automatically.",
      };

      addNotification(
        successMessages[newStatus] ||
          `Order status updated to ${newStatus} successfully! Dashboard updated automatically.`,
        "success",
      );
    } catch (error) {
      console.error("Error updating order status:", error);
      addNotification(
        `Failed to update order status: ${error.message}`,
        "error",
      );
    }
  };

  // Enhanced refresh handler
  const handleRefresh = async () => {
    await refetchOrders();
    addNotification("Orders refreshed successfully!", "success");
  };

  // Search handler
  const handleSearchChange = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  // Export functionality
  const exportOrders = useCallback(
    (format) => {
      const dataToExport =
        selectedOrders.length > 0
          ? filteredOrders.filter((order) => selectedOrders.includes(order._id))
          : filteredOrders;

      try {
        if (format === "csv") {
          const csv = convertToCSV(dataToExport);
          downloadFile(
            csv,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.csv`,
            "text/csv",
          );
        } else if (format === "json") {
          const json = JSON.stringify(dataToExport, null, 2);
          downloadFile(
            json,
            `farmer-orders-${new Date().toISOString().split("T")[0]}.json`,
            "application/json",
          );
        }

        addNotification(
          `${dataToExport.length} orders exported successfully!`,
          "success",
        );
      } catch (error) {
        console.error("Export error:", error);
        addNotification("Failed to export orders", "error");
      } finally {
        setShowExportModal(false);
      }
    },
    [
      filteredOrders,
      selectedOrders,
      convertToCSV,
      downloadFile,
      addNotification,
    ],
  );

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: {
        bg: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
        icon: "fas fa-clock",
      },
      confirmed: {
        bg: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
        icon: "fas fa-check",
      },
      shipped: {
        bg: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
        icon: "fas fa-truck",
      },
      delivered: {
        bg: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
        icon: "fas fa-check-circle",
      },
      cancelled: {
        bg: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
        icon: "fas fa-times-circle",
      },
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;

    return (
      <span
        className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${config.bg}`}
      >
        <i className={`${config.icon} mr-1`}></i>
        {status?.charAt(0).toUpperCase() + status?.slice(1) || "Pending"}
      </span>
    );
  };

  const getOrderActions = (order) => {
    const actions = [];

    switch (order.status.toLowerCase()) {
      case "pending":
        actions.push(
          <button
            key="confirm"
            onClick={() => handleUpdateOrderStatus(order._id, "confirmed")}
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check mr-1"></i>
            Confirm Order
          </button>,
        );
        actions.push(
          <button
            key="cancel"
            onClick={() => handleUpdateOrderStatus(order._id, "cancelled")}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-times mr-1"></i>
            Cancel Order
          </button>,
        );
        break;
      case "confirmed":
        actions.push(
          <button
            key="ship"
            onClick={() => handleUpdateOrderStatus(order._id, "shipped")}
            className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-truck mr-1"></i>
            Mark as Shipped
          </button>,
        );
        break;
      case "shipped":
        actions.push(
          <button
            key="deliver"
            onClick={() => handleUpdateOrderStatus(order._id, "delivered")}
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
          >
            <i className="fas fa-check-circle mr-1"></i>
            Mark as Delivered
          </button>,
        );
        break;
    }

    return actions;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(
    indexOfFirstOrder,
    indexOfLastOrder,
  );
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const getOrderSummary = () => {
    const summary = {
      total: orders.length,
      pending: orders.filter((o) => o.status === "pending").length,
      confirmed: orders.filter((o) => o.status === "confirmed").length,
      shipped: orders.filter((o) => o.status === "shipped").length,
      delivered: orders.filter((o) => o.status === "delivered").length,
      cancelled: orders.filter((o) => o.status === "cancelled").length,
    };
    return summary;
  };

  if (status === "loading" || loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400 text-lg">
            Loading your enhanced orders dashboard...
          </p>
        </div>
      </div>
    );
  }

  const orderSummary = getOrderSummary();

  return (
    <>
      {/* Notification System */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`px-6 py-4 rounded-lg shadow-lg text-white transform transition-all duration-500 ${
              notification.type === "success"
                ? "bg-green-600"
                : notification.type === "error"
                  ? "bg-red-600"
                  : notification.type === "warning"
                    ? "bg-yellow-600"
                    : "bg-blue-600"
            }`}
          >
            <div className="flex items-center">
              <i
                className={`fas ${
                  notification.type === "success"
                    ? "fa-check-circle"
                    : notification.type === "error"
                      ? "fa-exclamation-circle"
                      : notification.type === "warning"
                        ? "fa-exclamation-triangle"
                        : "fa-info-circle"
                } mr-2`}
              ></i>
              {notification.message}
            </div>
          </div>
        ))}
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 flex items-center justify-center">
          <div className="bg-white dark:bg-gray-800 rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Export Orders
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              {selectedOrders.length > 0
                ? `Export ${selectedOrders.length} selected orders`
                : `Export all ${filteredOrders.length} filtered orders`}
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => exportOrders("csv")}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-csv mr-2"></i>
                Export as CSV
              </button>
              <button
                onClick={() => exportOrders("json")}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition"
              >
                <i className="fas fa-file-code mr-2"></i>
                Export as JSON
              </button>
            </div>
            <button
              onClick={() => setShowExportModal(false)}
              className="w-full mt-4 bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg font-medium transition"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
        {/* Enhanced Breadcrumb with Real-time Indicators */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex justify-between items-center">
            <nav className="flex" aria-label="Breadcrumb">
              <ol className="flex items-center space-x-2 text-sm">
                <li>
                  <Link
                    href="/"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-home mr-1"></i>Home
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li>
                  <Link
                    href="/manage"
                    className="text-gray-500 hover:text-green-600 transition"
                  >
                    <i className="fas fa-cog mr-1"></i>Manage
                  </Link>
                </li>
                <li>
                  <i className="fas fa-chevron-right text-gray-400 text-xs"></i>
                </li>
                <li className="text-gray-900 dark:text-white font-medium">
                  <i className="fas fa-chart-line mr-1"></i>Advanced Order
                  Management
                </li>
              </ol>
            </nav>

            {/* Real-time Status Indicator */}
            <div className="flex items-center space-x-2">
              <div
                className={`w-3 h-3 rounded-full ${autoRefresh ? "bg-green-500 animate-pulse" : "bg-gray-400"}`}
              ></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">
                {autoRefresh ? "Live" : "Static"}
              </span>
            </div>
          </div>
        </div>

        {/* Enhanced Page Header with Quick Actions */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-8">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-green-600 to-blue-600 p-4 rounded-2xl">
                <i className="fas fa-chart-line text-white text-2xl"></i>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Advanced Order Management
                </h1>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Comprehensive order tracking and analytics dashboard
                </p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className="text-sm text-gray-500">
                    Last updated: {new Date().toLocaleTimeString()}
                  </span>
                  {autoRefresh && (
                    <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                      Auto-refresh enabled
                    </span>
                  )}
                </div>
              </div>
            </div>

            <div className="mt-6 lg:mt-0 flex flex-wrap gap-3">
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`inline-flex items-center px-4 py-3 rounded-lg font-medium transition ${
                  autoRefresh
                    ? "bg-green-600 hover:bg-green-700 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600"
                }`}
              >
                <i
                  className={`fas ${autoRefresh ? "fa-pause" : "fa-play"} mr-2`}
                ></i>
                {autoRefresh ? "Pause" : "Auto"} Refresh
              </button>

              <button
                onClick={() => setShowExportModal(true)}
                className="inline-flex items-center px-4 py-3 bg-orange-600 hover:bg-orange-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-download mr-2"></i>
                Export
              </button>

              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="inline-flex items-center px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg font-medium transition"
              >
                <i
                  className={`fas fa-sync-alt mr-2 ${refreshing ? "animate-spin" : ""}`}
                ></i>
                Refresh
              </button>

              <Link
                href="/manage"
                className="inline-flex items-center px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
              >
                <i className="fas fa-arrow-left mr-2"></i>
                Back to Manage
              </Link>
            </div>
          </div>

          {/* Enhanced Order Summary Cards with Animations */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4 mb-8">
            {Object.entries(orderSummary).map(([key, value], index) => {
              const config = {
                total: {
                  icon: "fa-shopping-cart",
                  color: "gray",
                  label: "Total",
                },
                pending: {
                  icon: "fa-clock",
                  color: "yellow",
                  label: "Pending",
                },
                confirmed: {
                  icon: "fa-check",
                  color: "blue",
                  label: "Confirmed",
                },
                shipped: {
                  icon: "fa-truck",
                  color: "purple",
                  label: "Shipped",
                },
                delivered: {
                  icon: "fa-check-circle",
                  color: "green",
                  label: "Delivered",
                },
                cancelled: {
                  icon: "fa-times-circle",
                  color: "red",
                  label: "Cancelled",
                },
              }[key];

              return (
                <div
                  key={key}
                  className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 transform hover:scale-105 transition-transform duration-200 cursor-pointer"
                  onClick={() =>
                    setStatusFilter(
                      key === "total"
                        ? "All Orders"
                        : config.label.toLowerCase(),
                    )
                  }
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <div className="flex items-center">
                    <div
                      className={`p-2 bg-${config.color}-100 dark:bg-${config.color}-900 rounded-lg`}
                    >
                      <i
                        className={`fas ${config.icon} text-${config.color}-600 dark:text-${config.color}-300`}
                      ></i>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                        {config.label}
                      </p>
                      <p
                        className={`text-2xl font-bold text-${config.color}-600 dark:text-${config.color}-400`}
                      >
                        {value}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Enhanced Filters and Controls */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 mb-8">
            <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 lg:mb-0">
                <i className="fas fa-filter mr-2"></i>
                Advanced Filters & Controls
              </h3>

              {/* View Mode Toggle */}
              <div className="flex space-x-2 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
                {["detailed", "compact"].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-4 py-2 rounded-md text-sm font-medium transition ${
                      viewMode === mode
                        ? "bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow"
                        : "text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white"
                    }`}
                  >
                    <i
                      className={`fas ${
                        mode === "detailed"
                          ? "fa-list"
                          : mode === "compact"
                            ? "fa-th-list"
                            : ""
                      } mr-1`}
                    ></i>
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4">
              {/* Search */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Search Orders
                </label>
                <div className="relative">
                  <input
                    type="text"
                    placeholder="Search by customer, email, or ID..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                  />
                  <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Status Filter
                </label>
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="All Orders">All Orders</option>
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="shipped">Shipped</option>
                  <option value="delivered">Delivered</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              {/* Sort By */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Sort By
                </label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="highest-value">Highest Value</option>
                  <option value="lowest-value">Lowest Value</option>
                  <option value="customer-name">Customer Name</option>
                </select>
              </div>

              {/* Date Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date From
                </label>
                <input
                  type="date"
                  value={dateRange.start}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, start: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Date To
                </label>
                <input
                  type="date"
                  value={dateRange.end}
                  onChange={(e) =>
                    setDateRange((prev) => ({ ...prev, end: e.target.value }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
              </div>

              {/* Clear Filters */}
              <div className="flex items-end">
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 rounded-lg font-medium transition"
                >
                  <i className="fas fa-times mr-1"></i>
                  Clear All
                </button>
              </div>
            </div>

            {/* Bulk Operations */}
            {selectedOrders.length > 0 && (
              <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
                  <div className="mb-3 sm:mb-0">
                    <p className="font-medium text-blue-900 dark:text-blue-100">
                      {selectedOrders.length} order(s) selected
                    </p>
                    <p className="text-sm text-blue-700 dark:text-blue-300">
                      Choose a bulk action to apply to selected orders
                    </p>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <button
                      onClick={() => handleBulkStatusUpdate("confirmed")}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-check mr-1"></i>
                      Confirm All
                    </button>
                    <button
                      onClick={() => handleBulkStatusUpdate("shipped")}
                      className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-truck mr-1"></i>
                      Ship All
                    </button>
                    <button
                      onClick={() => setSelectedOrders([])}
                      className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      <i className="fas fa-times mr-1"></i>
                      Clear Selection
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Orders List - Enhanced with different view modes */}
          {currentOrders.length === 0 ? (
            <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-12 text-center">
              <div className="max-w-md mx-auto">
                <i className="fas fa-search text-6xl text-gray-400 mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                  No orders found
                </h3>
                <p className="text-gray-600 dark:text-gray-400 mb-6">
                  {statusFilter === "All Orders"
                    ? "You haven't received any orders yet, or no orders match your current filters."
                    : `No ${statusFilter.toLowerCase()} orders found matching your criteria.`}
                </p>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setStatusFilter("All Orders");
                    setSortBy("newest");
                    setDateRange({ start: "", end: "" });
                    setSelectedOrders([]);
                  }}
                  className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-lg font-medium transition"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          ) : (
            <div className={`space-y-${viewMode === "compact" ? "3" : "6"}`}>
              {/* Select All Checkbox */}
              <div className="bg-white dark:bg-gray-800 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={selectedOrders.length === currentOrders.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedOrders(
                          currentOrders.map((order) => order._id),
                        );
                      } else {
                        setSelectedOrders([]);
                      }
                    }}
                    className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                  />
                  <span className="ml-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                    Select all visible orders ({currentOrders.length})
                  </span>
                </label>
              </div>

              {/* Orders */}
              {currentOrders.map((order) => (
                <div
                  key={order._id}
                  className={`bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden transform hover:scale-[1.01] transition-all duration-200 ${
                    selectedOrders.includes(order._id)
                      ? "ring-2 ring-green-500"
                      : ""
                  }`}
                >
                  <div className={`p-${viewMode === "compact" ? "4" : "6"}`}>
                    {/* Order Header with Checkbox */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <input
                          type="checkbox"
                          checked={selectedOrders.includes(order._id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedOrders((prev) => [...prev, order._id]);
                            } else {
                              setSelectedOrders((prev) =>
                                prev.filter((id) => id !== order._id),
                              );
                            }
                          }}
                          className="rounded border-gray-300 text-green-600 focus:ring-green-500"
                        />
                        <div>
                          <h3
                            className={`${viewMode === "compact" ? "text-base" : "text-lg"} font-semibold text-gray-900 dark:text-white`}
                          >
                            Order #
                            {order._id?.slice(-8)?.toUpperCase() || "N/A"}
                          </h3>
                          <p className="text-sm text-gray-600 dark:text-gray-400">
                            Placed on {formatDate(order.createdAt)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        {getStatusBadge(order.status)}
                        <span className="text-lg font-bold text-gray-900 dark:text-white">
                          {formatPrice(
                            order.farmerSubtotal || order.total || 0,
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Information */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Customer Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Name:</span>{" "}
                            {order.customerName || order.userName || "Customer"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Email:</span>{" "}
                            {order.customerEmail ||
                              order.userEmail ||
                              "Not available"}
                          </p>
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Phone:</span>{" "}
                            {order.customerPhone ||
                              order.userPhone ||
                              "Not available"}
                          </p>
                        </div>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                          Delivery Information
                        </h4>
                        <div className="space-y-2 text-sm">
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Address:</span>{" "}
                            {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress
                              ? `${order.deliveryAddress.address || ""}, ${order.deliveryAddress.city || ""} ${order.deliveryAddress.postalCode || ""}`
                                  .replace(/^,\s*/, "")
                                  .replace(/,\s*$/, "") || "Not provided"
                              : order.deliveryAddress || "Not provided"}
                          </p>
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.name && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Recipient:</span>{" "}
                                {order.deliveryAddress.name}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.phone && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">Contact:</span>{" "}
                                {order.deliveryAddress.phone}
                              </p>
                            )}
                          {typeof order.deliveryAddress === "object" &&
                            order.deliveryAddress?.instructions && (
                              <p className="text-gray-600 dark:text-gray-400">
                                <span className="font-medium">
                                  Instructions:
                                </span>{" "}
                                {order.deliveryAddress.instructions}
                              </p>
                            )}
                          <p className="text-gray-600 dark:text-gray-400">
                            <span className="font-medium">Payment Method:</span>{" "}
                            {order.paymentMethod || "Cash on Delivery"}
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* Order Items */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-6 mb-6">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-4">
                        Order Items ({order.items?.length || 0})
                      </h4>
                      <div className="space-y-3">
                        {order.items?.map((item, index) => (
                          <div
                            key={index}
                            className="flex items-center justify-between py-3 px-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                          >
                            <div className="flex items-center space-x-4">
                              <img
                                src={
                                  item.image ||
                                  "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=80&h=80&fit=crop"
                                }
                                alt={item.name || item.productName}
                                className="w-16 h-16 rounded-lg object-cover"
                              />
                              <div>
                                <h5 className="font-medium text-gray-900 dark:text-white">
                                  {item.name || item.productName}
                                </h5>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Quantity: {item.quantity} {item.unit || "kg"}
                                </p>
                                <p className="text-sm text-gray-600 dark:text-gray-400">
                                  Unit Price: {formatPrice(item.price)}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-medium text-gray-900 dark:text-white">
                                {formatPrice(item.price * item.quantity)}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                      <div className="flex flex-wrap gap-3">
                        {getOrderActions(order)}
                        <Link
                          href={`/farmer-orders/${order._id}`}
                          className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition"
                        >
                          <i className="fas fa-eye mr-1"></i>
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <nav aria-label="Pagination">
                <ul className="inline-flex items-center -space-x-px text-gray-600 dark:text-gray-300">
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.max(prev - 1, 1))
                      }
                      disabled={currentPage === 1}
                      className="block px-3 py-2 ml-0 leading-tight text-gray-500 bg-white border border-gray-300 rounded-l-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-left"></i>
                    </button>
                  </li>
                  {[...Array(totalPages)].map((_, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setCurrentPage(index + 1)}
                        className={`px-3 py-2 leading-tight border ${
                          currentPage === index + 1
                            ? "text-white bg-green-600 border-green-600 hover:bg-green-700"
                            : "text-gray-500 bg-white border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                        }`}
                      >
                        {index + 1}
                      </button>
                    </li>
                  ))}
                  <li>
                    <button
                      onClick={() =>
                        setCurrentPage((prev) => Math.min(prev + 1, totalPages))
                      }
                      disabled={currentPage === totalPages}
                      className="block px-3 py-2 leading-tight text-gray-500 bg-white border border-gray-300 rounded-r-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white disabled:opacity-50"
                    >
                      <i className="fas fa-chevron-right"></i>
                    </button>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import { useDashboardData } from "@/hooks/useDashboardData";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    analytics: queryAnalytics,
    meta,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    console.log(
      "Auth check - Status:",
      status,
      "User:",
      session?.user?.email,
      "UserType:",
      session?.user?.userType || session?.user?.role,
    );

    if (status === "loading") return;

    if (status === "unauthenticated") {
      console.log("User not authenticated, redirecting to login");
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      console.log("User authenticated with type:", userType);

      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        console.log("User is not a farmer, redirecting to home");
        router.push("/");
        return;
      }

      console.log(
        "Farmer authenticated, React Query will handle data fetching",
      );
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    console.log("Manual refresh triggered");
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      console.log("Auto-refresh triggered");
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization - Fixed to prevent infinite re-renders
  const analytics = useMemo(() => {
    try {
      const validOrders = orders.filter(
        (order) =>
          order.status !== ORDER_STATUS.CANCELLED &&
          order.status !== ORDER_STATUS.RETURNED,
      );

      const totalProducts = products.length;
      const activeProducts = products.filter(
        (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
      ).length;

      const totalOrders = orders.length;
      const pendingOrders = orders.filter(
        (o) => o.status === ORDER_STATUS.PENDING,
      ).length;

      const totalRevenue = validOrders.reduce(
        (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
        0,
      );

      const now = new Date();
      const thisMonthValidOrders = validOrders.filter((order) => {
        const orderDate = new Date(order.createdAt);
        return (
          orderDate.getMonth() === now.getMonth() &&
          orderDate.getFullYear() === now.getFullYear()
        );
      });

      const averageOrderValue =
        validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

      // Create a stable reference for recent orders by mapping to simple objects
      const recentOrders = orders.slice(0, 5).map((order) => ({
        _id: order._id,
        id: order.id,
        customerName: order.customerName,
        total: order.total || order.farmerSubtotal,
        createdAt: order.createdAt,
        status: order.status,
        items: order.items?.length || 0,
      }));

      return {
        totalProducts,
        activeProducts,
        totalOrders,
        pendingOrders,
        totalRevenue,
        thisMonthOrders: thisMonthValidOrders.length,
        averageOrderValue,
        lowStockProducts: products.filter((p) => p.stock <= 10).length,
        recentOrders,
      };
    } catch (error) {
      console.error("Error calculating analytics:", error);
      return {
        totalProducts: 0,
        activeProducts: 0,
        totalOrders: 0,
        pendingOrders: 0,
        totalRevenue: 0,
        thisMonthOrders: 0,
        averageOrderValue: 0,
        lowStockProducts: 0,
        recentOrders: [],
      };
    }
  }, [products.length, orders.length]); // Simplified dependencies to prevent infinite re-renders

  // Filter and sort products with better performance
  const filteredAndSortedProducts = useMemo(() => {
    try {
      let filtered = [...products];

      if (filters.search) {
        const searchRegex = new RegExp(filters.search.trim(), "i");
        filtered = filtered.filter(
          (product) =>
            searchRegex.test(product.name) ||
            searchRegex.test(product.description || "") ||
            searchRegex.test(product.category || ""),
        );
      }

      if (filters.category) {
        filtered = filtered.filter(
          (product) =>
            product.category?.toLowerCase() === filters.category.toLowerCase(),
        );
      }

      if (filters.status) {
        switch (filters.status) {
          case PRODUCT_STATUS.ACTIVE:
            filtered = filtered.filter(
              (product) =>
                product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.INACTIVE:
            filtered = filtered.filter(
              (product) => product.status === PRODUCT_STATUS.INACTIVE,
            );
            break;
          case PRODUCT_STATUS.OUT_OF_STOCK:
            filtered = filtered.filter((product) => product.stock === 0);
            break;
          case PRODUCT_STATUS.LOW_STOCK:
            filtered = filtered.filter(
              (product) => product.stock > 0 && product.stock <= 5,
            );
            break;
        }
      }

      if (filters.sort) {
        const [key, order] = filters.sort.split("-");
        filtered.sort((a, b) => {
          let valueA, valueB;

          switch (key) {
            case "price":
              valueA = a.price || 0;
              valueB = b.price || 0;
              break;
            case "name":
              valueA = a.name || "";
              valueB = b.name || "";
              return order === "asc"
                ? valueA.localeCompare(valueB)
                : valueB.localeCompare(valueA);
            case "stock":
              valueA = a.stock || 0;
              valueB = b.stock || 0;
              break;
            case "date":
              valueA = new Date(a.createdAt || 0);
              valueB = new Date(b.createdAt || 0);
              break;
            default:
              return 0;
          }

          return order === "asc" ? valueA - valueB : valueB - valueA;
        });
      }

      return filtered;
    } catch (error) {
      console.error("Error filtering products:", error);
      return products;
    }
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      const newStatus =
        currentStatus === PRODUCT_STATUS.ACTIVE
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;
      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ status: newStatus }),
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || `Failed to ${actionText} product`);
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        // Refresh dashboard data to get updated products
        await refetchDashboard();

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        " Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        " The product listing\n" +
        " All associated data\n" +
        " Product from any pending orders";

      if (!confirm(confirmMessage)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              " Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert(" Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        // Refresh dashboard data to get updated products
        await refetchDashboard();

        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(` Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update filters
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  // Update pagination
  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const dashboardProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
  };

  const productProps = {
    products: products,
    paginatedProducts: paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
  };

  const orderProps = {
    orders,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
  };

  const analyticsProps = {
    analytics,
    products,
    orders,
    formatPrice,
    loading: isLoading,
    error,
  };

  const settingsProps = {
    session,
    handleRefresh,
    refreshing: isRefetching,
    loading: isLoading,
    error,
  };

  // Loading state
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            Loading dashboard...
          </p>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            {error.message}
          </p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Dashboard Header */}
        <DashboardHeader {...dashboardProps} />

        {/* Error Alert */}
        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Navigation Tabs */}
        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Tab Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...dashboardProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...orderProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...analyticsProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...settingsProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

"use client";

import { useState, useEffect, useCallback, useMemo } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Footer from "@/components/Footer";
import { useDashboardData } from "@/hooks/useDashboardData";

// Component imports
import DashboardHeader from "@/components/dashboard/DashboardHeader";
import NavigationTabs from "@/components/dashboard/NavigationTabs";
import DashboardTab from "@/components/dashboard/tabs/DashboardTab";
import ProductsTab from "@/components/dashboard/tabs/ProductsTab";
import OrdersTab from "@/components/dashboard/tabs/OrdersTab";
import AnalyticsTab from "@/components/dashboard/tabs/AnalyticsTab";
import SettingsTab from "@/components/dashboard/tabs/SettingsTab";

// Constants
const TABS = {
  DASHBOARD: "dashboard",
  PRODUCTS: "products",
  ORDERS: "orders",
  ANALYTICS: "analytics",
  SETTINGS: "settings",
};

const PRODUCT_STATUS = {
  ACTIVE: "active",
  INACTIVE: "inactive",
  OUT_OF_STOCK: "out-of-stock",
  LOW_STOCK: "low-stock",
};

const ORDER_STATUS = {
  PENDING: "pending",
  CONFIRMED: "confirmed",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
  RETURNED: "returned",
};

const DEFAULT_PAGINATION = {
  page: 1,
  limit: 12,
};

export default function FarmerDashboard() {
  const { data: session, status } = useSession();
  const router = useRouter();

  // Use React Query hook for dashboard data
  const {
    products,
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
  } = useDashboardData();

  // UI state
  const [activeTab, setActiveTab] = useState(TABS.DASHBOARD);
  const [actionLoading, setActionLoading] = useState({});
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Filter and search state
  const [filters, setFilters] = useState({
    search: "",
    category: "",
    status: "",
    sort: "",
  });

  // Pagination state
  const [pagination, setPagination] = useState(DEFAULT_PAGINATION);
  const [viewMode, setViewMode] = useState("grid");

  // Authentication and authorization check
  useEffect(() => {
    if (status === "loading") return;

    if (status === "unauthenticated") {
      router.push("/login");
      return;
    }

    if (status === "authenticated" && session?.user) {
      const userType = session.user.userType || session.user.role || "user";
      const isFarmer =
        userType === "farmer" ||
        userType === "Farmer" ||
        session.user.type === "farmer" ||
        session.user.accountType === "farmer";

      if (!isFarmer) {
        router.push("/");
        return;
      }
    }
  }, [session?.user?.id, session?.user?.email, status, router]);

  // Manual refresh handler
  const handleRefresh = useCallback(async () => {
    await refetchDashboard();
  }, [refetchDashboard]);

  // Auto-refresh functionality
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      refreshDashboard();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, refreshDashboard]);

  // Calculate analytics with memoization
  const analytics = useMemo(() => {
    const validOrders = orders.filter(
      (order) =>
        order.status !== ORDER_STATUS.CANCELLED &&
        order.status !== ORDER_STATUS.RETURNED,
    );

    const totalProducts = products.length;
    const activeProducts = products.filter(
      (p) => p.stock > 0 && p.status !== PRODUCT_STATUS.INACTIVE,
    ).length;

    const totalOrders = orders.length;
    const pendingOrders = orders.filter(
      (o) => o.status === ORDER_STATUS.PENDING,
    ).length;

    const totalRevenue = validOrders.reduce(
      (sum, order) => sum + (order.farmerSubtotal || order.total || 0),
      0,
    );

    const now = new Date();
    const thisMonthValidOrders = validOrders.filter((order) => {
      const orderDate = new Date(order.createdAt);
      return (
        orderDate.getMonth() === now.getMonth() &&
        orderDate.getFullYear() === now.getFullYear()
      );
    });

    const averageOrderValue =
      validOrders.length > 0 ? totalRevenue / validOrders.length : 0;

    const recentOrders = orders.slice(0, 5).map((order) => ({
      _id: order._id,
      id: order.id,
      customerName: order.customerName,
      total: order.total || order.farmerSubtotal,
      createdAt: order.createdAt,
      status: order.status,
      items: order.items?.length || 0,
    }));

    return {
      totalProducts,
      activeProducts,
      totalOrders,
      pendingOrders,
      totalRevenue,
      thisMonthOrders: thisMonthValidOrders.length,
      averageOrderValue,
      lowStockProducts: products.filter((p) => p.stock <= 10).length,
      recentOrders,
    };
  }, [products.length, orders.length]);

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = [...products];

    if (filters.search) {
      const searchRegex = new RegExp(filters.search.trim(), "i");
      filtered = filtered.filter(
        (product) =>
          searchRegex.test(product.name) ||
          searchRegex.test(product.description || "") ||
          searchRegex.test(product.category || ""),
      );
    }

    if (filters.category) {
      filtered = filtered.filter(
        (product) =>
          product.category?.toLowerCase() === filters.category.toLowerCase(),
      );
    }

    if (filters.status) {
      switch (filters.status) {
        case PRODUCT_STATUS.ACTIVE:
          filtered = filtered.filter(
            (product) =>
              product.stock > 0 && product.status !== PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.INACTIVE:
          filtered = filtered.filter(
            (product) => product.status === PRODUCT_STATUS.INACTIVE,
          );
          break;
        case PRODUCT_STATUS.OUT_OF_STOCK:
          filtered = filtered.filter((product) => product.stock === 0);
          break;
        case PRODUCT_STATUS.LOW_STOCK:
          filtered = filtered.filter(
            (product) => product.stock > 0 && product.stock <= 5,
          );
          break;
      }
    }

    if (filters.sort) {
      const [key, order] = filters.sort.split("-");
      filtered.sort((a, b) => {
        let valueA, valueB;

        switch (key) {
          case "price":
            valueA = a.price || 0;
            valueB = b.price || 0;
            break;
          case "name":
            valueA = a.name || "";
            valueB = b.name || "";
            return order === "asc"
              ? valueA.localeCompare(valueB)
              : valueB.localeCompare(valueA);
          case "stock":
            valueA = a.stock || 0;
            valueB = b.stock || 0;
            break;
          case "date":
            valueA = new Date(a.createdAt || 0);
            valueB = new Date(b.createdAt || 0);
            break;
          default:
            return 0;
        }

        return order === "asc" ? valueA - valueB : valueB - valueA;
      });
    }

    return filtered;
  }, [products, filters]);

  // Reset pagination when filters change
  useEffect(() => {
    setPagination((prev) => ({ ...prev, page: 1 }));
  }, [filters]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (pagination.page - 1) * pagination.limit;
    const endIndex = startIndex + pagination.limit;
    return filteredAndSortedProducts.slice(startIndex, endIndex);
  }, [filteredAndSortedProducts, pagination]);

  // Total pages calculation
  const totalPages = Math.ceil(
    filteredAndSortedProducts.length / pagination.limit,
  );

  // Product management functions
  const handleStatusToggle = useCallback(
    async (productId, currentStatus) => {
      const newStatus =
        currentStatus === PRODUCT_STATUS.ACTIVE
          ? PRODUCT_STATUS.INACTIVE
          : PRODUCT_STATUS.ACTIVE;
      const actionText =
        newStatus === PRODUCT_STATUS.ACTIVE ? "activate" : "deactivate";

      if (!confirm(`Are you sure you want to ${actionText} this product?`)) {
        return;
      }

      setActionLoading((prev) => ({ ...prev, [productId]: "status" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ status: newStatus }),
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || `Failed to ${actionText} product`);
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || `Failed to ${actionText} product`);
        }

        await refetchDashboard();

        const successMsg =
          newStatus === PRODUCT_STATUS.ACTIVE
            ? "Product activated successfully!"
            : "Product deactivated successfully!";

        alert(successMsg);
      } catch (error) {
        console.error("Error updating product status:", error);
        alert(`Failed to ${actionText} product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  const handleDeleteProduct = useCallback(
    async (productId) => {
      const confirmMessage =
        " Are you sure you want to delete this product?\n\n" +
        "This action cannot be undone and will remove:\n" +
        " The product listing\n" +
        " All associated data\n" +
        " Product from any pending orders";

      if (!confirm(confirmMessage)) return;

      setActionLoading((prev) => ({ ...prev, [productId]: "delete" }));

      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          const errorData = await response.json();

          if (response.status === 409) {
            alert(
              " Cannot Delete Product\n\n" +
                "This product has pending orders and cannot be deleted.\n" +
                "Please wait for all orders to be completed or cancelled before deleting this product.\n\n" +
                "You can temporarily deactivate the product instead by clicking the pause button.",
            );
            return;
          } else if (response.status === 404) {
            alert(" Product not found. It may have already been deleted.");
            return;
          } else {
            throw new Error(errorData.error || "Failed to delete product");
          }
        }

        const result = await response.json();

        if (!result.success) {
          throw new Error(result.error || "Failed to delete product");
        }

        await refetchDashboard();
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(` Failed to delete product: ${error.message}`);
      } finally {
        setActionLoading((prev) => ({ ...prev, [productId]: null }));
      }
    },
    [refetchDashboard],
  );

  // Update functions
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
  }, []);

  const updatePagination = useCallback((newPagination) => {
    setPagination((prev) => ({ ...prev, ...newPagination }));
  }, []);

  // Utility functions
  const formatPrice = useCallback((price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price || 0);
  }, []);

  const formatDate = useCallback((dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  }, []);

  const getProductStatusBadge = useCallback((product) => {
    if (product.status === PRODUCT_STATUS.INACTIVE) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200">
          <i className="fas fa-pause mr-1"></i>
          Inactive
        </span>
      );
    }
    if (product.stock === 0) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200">
          <i className="fas fa-exclamation-triangle mr-1"></i>
          Out of Stock
        </span>
      );
    }
    if (product.stock <= 5) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
          <i className="fas fa-exclamation-circle mr-1"></i>
          Low Stock
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
        <i className="fas fa-check-circle mr-1"></i>
        Active
      </span>
    );
  }, []);

  // Component props
  const commonProps = {
    session,
    products,
    orders,
    analytics,
    handleRefresh,
    refreshing: isRefetching,
    formatPrice,
    formatDate,
    loading: isLoading,
    error,
  };

  const productProps = {
    ...commonProps,
    paginatedProducts,
    filteredProducts: filteredAndSortedProducts,
    totalProducts: products.length,
    filters,
    updateFilters,
    pagination: { ...pagination, totalPages },
    updatePagination,
    viewMode,
    setViewMode,
    handleStatusToggle,
    handleDeleteProduct,
    actionLoading,
    getProductStatusBadge,
  };

  // Loading state
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !products.length && !orders.length) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="text-red-500 text-6xl mb-4">
            <i className="fas fa-exclamation-triangle"></i>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Dashboard Error
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mb-6">{error.message}</p>
          <button
            onClick={handleRefresh}
            disabled={isRefetching}
            className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition disabled:opacity-50"
          >
            {isRefetching ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Retrying...
              </>
            ) : (
              <>
                <i className="fas fa-redo mr-2"></i>
                Try Again
              </>
            )}
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <DashboardHeader {...commonProps} />

        {error && (
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
            <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
              <div className="flex items-center">
                <i className="fas fa-exclamation-triangle text-red-500 mr-2"></i>
                <span className="text-red-700 dark:text-red-300">
                  {error.message}
                </span>
                <button
                  onClick={handleRefresh}
                  className="ml-auto text-primary-600 hover:text-primary-800"
                >
                  <i className="fas fa-redo"></i>
                </button>
              </div>
            </div>
          </div>
        )}

        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === TABS.DASHBOARD && <DashboardTab {...commonProps} />}
          {activeTab === TABS.PRODUCTS && <ProductsTab {...productProps} />}
          {activeTab === TABS.ORDERS && <OrdersTab {...commonProps} />}
          {activeTab === TABS.ANALYTICS && <AnalyticsTab {...commonProps} />}
          {activeTab === TABS.SETTINGS && <SettingsTab {...commonProps} />}
        </div>
      </div>
      <Footer />
    </>
  );
}

// Simple script to check the review-product mismatch
const { MongoClient } = require('mongodb');

async function checkMismatch() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Checking product-review mismatch...\n');

  // Get all products
  const products = await db.collection('products').find({}).toArray();
  console.log(` Total products: ${products.length}`);
  console.log('Product IDs:', products.slice(0, 5).map(p => p._id.toString()));

  // Get all reviews  
  const reviews = await db.collection('reviews').find({}).toArray();
  console.log(`\n Total reviews: ${reviews.length}`);
  console.log('Review product IDs:', reviews.map(r => r.productId));

  // Check for matches
  const productIds = products.map(p => p._id.toString());
  const reviewProductIds = reviews.map(r => r.productId);

  const matches = productIds.filter(pid => reviewProductIds.includes(pid));
  console.log(`\n Matching product-review pairs: ${matches.length}`);

  if (matches.length > 0) {
    console.log('Matched IDs:', matches);

    // Show which products have reviews
    for (const matchId of matches) {
      const product = products.find(p => p._id.toString() === matchId);
      const productReviews = reviews.filter(r => r.productId === matchId);
      console.log(`- ${product.name}: ${productReviews.length} reviews`);
    }
  } else {
    console.log('\n No matches found!');
    console.log('This means the existing reviews belong to products that no longer exist');
    console.log('or have different IDs than the current products in the database');
  }

  await client.close();
}

checkMismatch().catch(console.error);

"use client";

import Image from "next/image";
import Link from "next/link";
import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useFavorites } from "../contexts/FavoritesContext";
import { useCart } from "../contexts/CartContext";
import StarRating from "./StarRating";

export default function ProductCard({ product, showAddToCart = true }) {
  const { data: session } = useSession();
  const { addToFavorites, removeFromFavorites, isProductFavorited } =
    useFavorites();
  const { addToCart } = useCart();
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isLoadingFavorite, setIsLoadingFavorite] = useState(false);

  // Check if this product is favorited
  const isFavorite = isProductFavorited(product._id);

  const handleFavoriteToggle = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsLoadingFavorite(true);
    try {
      if (isFavorite) {
        await removeFromFavorites(product._id);
      } else {
        await addToFavorites(product._id);
      }
    } catch (error) {
      console.error("Error updating favorite:", error);
    } finally {
      setIsLoadingFavorite(false);
    }
  };

  const handleAddToCart = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Pass the complete product object with normalized id field
      const productForCart = {
        id: product._id, // Normalize _id to id for cart
        _id: product._id,
        name: product.name,
        price: product.price,
        image:
          product.images?.[0] ||
          product.image ||
          "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=400&h=300&fit=crop", // Use first image from images array
        farmer: product.farmer,
        category: product.category,
        unit: product.unit || "kg",
        stock: product.stock || 0, // Add missing stock field
        farmerId: product.farmerId, // Add farmerId for consistency
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer", // Add farmerName
      };

      const success = await addToCart(productForCart, 1);
      if (success !== false) {
        alert("Product added to cart successfully!");
      } else {
        alert("Failed to add product to cart. Please try again.");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add product to cart. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const isOutOfStock = product.stock === 0;

  return (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden group hover:shadow-xl transition-all duration-300">
      <div className="relative">
        {/* Product Image */}
        <Link href={`/details?id=${product._id}`}>
          <Image
            src={
              product.images?.[0] ||
              product.image ||
              "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=400&h=300&fit=crop"
            }
            alt={product.name}
            width={400}
            height={300}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300 cursor-pointer"
          />
        </Link>

        {/* Favorite Button */}
        <button
          onClick={handleFavoriteToggle}
          disabled={isLoadingFavorite}
          className={`absolute top-3 right-3 w-8 h-8 rounded-full flex items-center justify-center transition-all duration-200 ${
            isFavorite
              ? "bg-red-500 text-white"
              : "bg-white/80 text-gray-600 hover:bg-white hover:text-red-500"
          } ${isLoadingFavorite ? "opacity-50 cursor-not-allowed" : ""}`}
        >
          <i
            className={`${
              isFavorite ? "fas" : "far"
            } fa-heart text-sm ${isLoadingFavorite ? "fa-spin fa-spinner" : ""}`}
          ></i>
        </button>

        {/* Stock Status Badge */}
        {isOutOfStock && (
          <div className="absolute top-3 left-3 bg-red-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            Out of Stock
          </div>
        )}

        {/* Featured Badge */}
        {product.featured && (
          <div className="absolute top-3 left-3 bg-primary-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            Featured
          </div>
        )}

        {/* Discount Badge */}
        {product.originalPrice && product.originalPrice > product.price && (
          <div className="absolute top-3 left-3 bg-green-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            {Math.round(
              ((product.originalPrice - product.price) /
                product.originalPrice) *
                100,
            )}
            % OFF
          </div>
        )}
      </div>

      <div className="p-6">
        {/* Category */}
        <div className="flex items-center justify-between mb-2">
          <span className="text-xs font-medium text-primary-600 dark:text-primary-400 bg-primary-100 dark:bg-primary-900 px-2 py-1 rounded-full">
            {product.category}
          </span>
          {product.tags && product.tags.includes("Organic") && (
            <span className="text-xs font-medium text-green-600 dark:text-green-400 bg-green-100 dark:bg-green-900 px-2 py-1 rounded-full">
              Organic
            </span>
          )}
        </div>

        {/* Product Name */}
        <Link href={`/details?id=${product._id}`}>
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2 hover:text-primary-600 dark:hover:text-primary-400 transition-colors cursor-pointer line-clamp-2">
            {product.name}
          </h3>
        </Link>

        {/* Farmer Name */}
        <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
          <i className="fas fa-user-tie mr-1"></i>
          by{" "}
          {typeof product.farmer === "object" && product.farmer?.name
            ? product.farmer.name
            : typeof product.farmer === "string"
              ? product.farmer
              : product.farmerName || "Unknown Farmer"}
        </p>

        {/* Rating */}
        <div className="flex items-center mb-3">
          <StarRating
            rating={product.averageRating || 0}
            size="sm"
            showCount={false}
          />
          <span className="text-sm text-gray-600 dark:text-gray-400 ml-2">
            ({product.totalRatings || 0} reviews)
          </span>
        </div>

        {/* Price */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-xl font-bold text-gray-900 dark:text-white">
              {formatPrice(product.price)}
            </span>
            {product.originalPrice && product.originalPrice > product.price && (
              <span className="text-sm text-gray-500 line-through">
                {formatPrice(product.originalPrice)}
              </span>
            )}
          </div>
          <span className="text-sm text-gray-600 dark:text-gray-400">
            per {product.unit || "kg"}
          </span>
        </div>

        {/* Stock Info */}
        <div className="mb-4">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600 dark:text-gray-400">Stock:</span>
            <span
              className={`font-medium ${
                product.stock > 50
                  ? "text-green-600"
                  : product.stock > 10
                    ? "text-yellow-600"
                    : "text-red-600"
              }`}
            >
              {product.stock} {product.unit || "kg"} available
            </span>
          </div>
          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2 mt-1">
            <div
              className={`h-2 rounded-full transition-all duration-300 ${
                product.stock > 50
                  ? "bg-green-500"
                  : product.stock > 10
                    ? "bg-yellow-500"
                    : "bg-red-500"
              }`}
              style={{
                width: `${Math.min((product.stock / 100) * 100, 100)}%`,
              }}
            ></div>
          </div>
        </div>

        {/* Action Buttons */}
        {showAddToCart && (
          <div className="flex gap-2">
            <button
              onClick={handleAddToCart}
              disabled={isAddingToCart || isOutOfStock}
              className={`flex-1 py-2 px-4 rounded-lg font-medium text-sm transition-all duration-200 ${
                isOutOfStock
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-primary-600 hover:bg-primary-700 text-white"
              } ${isAddingToCart ? "opacity-50 cursor-not-allowed" : ""}`}
            >
              {isAddingToCart ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Adding...
                </>
              ) : isOutOfStock ? (
                "Out of Stock"
              ) : (
                <>
                  <i className="fas fa-cart-plus mr-2"></i>
                  Add to Cart
                </>
              )}
            </button>

            <Link
              href={`/details?id=${product._id}`}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 transition-all duration-200 flex items-center justify-center"
            >
              <i className="fas fa-eye"></i>
            </Link>
          </div>
        )}

        {/* Purchase Count (for featured products) */}
        {product.purchaseCount > 0 && (
          <div className="mt-2 text-xs text-gray-500 dark:text-gray-400 text-center">
            <i className="fas fa-shopping-bag mr-1"></i>
            {product.purchaseCount} purchases
          </div>
        )}
      </div>
    </div>
  );
}

"use client";

import Image from "next/image";
import Link from "next/link";
import { useState, useEffect } from "react";
import { useSession } from "next-auth/react";
import { useFavorites } from "../contexts/FavoritesContext";
import { useCart } from "../contexts/CartContext";
import StarRating from "./StarRating";

export default function ProductCard({ product, showAddToCart = true }) {
  const { data: session } = useSession();
  const { addToFavorites, removeFromFavorites, isProductFavorited } =
    useFavorites();
  const { addToCart } = useCart();
  const [isAddingToCart, setIsAddingToCart] = useState(false);
  const [isLoadingFavorite, setIsLoadingFavorite] = useState(false);

  // Check if this product is favorited
  const isFavorite = isProductFavorited(product._id);

  const handleFavoriteToggle = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsLoadingFavorite(true);
    try {
      if (isFavorite) {
        await removeFromFavorites(product._id);
      } else {
        await addToFavorites(product._id);
      }
    } catch (error) {
      console.error("Error updating favorite:", error);
    } finally {
      setIsLoadingFavorite(false);
    }
  };

  const handleAddToCart = async () => {
    if (!session?.user) {
      window.location.href = "/login";
      return;
    }

    setIsAddingToCart(true);
    try {
      // Pass the complete product object with normalized id field
      const productForCart = {
        id: product._id, // Normalize _id to id for cart
        _id: product._id,
        name: product.name,
        price: product.price,
        image:
          product.images?.[0] ||
          product.image ||
          "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=400&h=300&fit=crop", // Use first image from images array
        farmer: product.farmer,
        category: product.category,
        unit: product.unit || "kg",
        stock: product.stock || 0, // Add missing stock field
        farmerId: product.farmerId, // Add farmerId for consistency
        farmerName:
          product.farmer?.name || product.farmer?.farmName || "Unknown Farmer", // Add farmerName
      };

      const success = await addToCart(productForCart, 1);
      if (success !== false) {
        alert("Product added to cart successfully!");
      } else {
        alert("Failed to add product to cart. Please try again.");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add product to cart. Please try again.");
    } finally {
      setIsAddingToCart(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-BD", {
      style: "currency",
      currency: "BDT",
      minimumFractionDigits: 0,
    }).format(price);
  };

  const isOutOfStock = product.stock === 0;

  return (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg overflow-hidden group hover:shadow-xl transition-all duration-300">
      <div className="relative">
        {/* Product Image */}
        <Link href={`/details?id=${product._id}`}>
          <Image
            src={
              product.images?.[0] ||
              product.image ||
              "https://images.unsplash.com/photo-1592924357228-91a4daadcfea?w=400&h=300&fit=crop"
            }
            alt={product.name}
            width={400}
            height={300}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300 cursor-pointer"
          />
        </Link>

        {/* Favorite Button */}
        <button
          onClick={handleFavoriteToggle}
          disabled={isLoadingFavorite}
          className={`absolute top-3 right-3 w-8 h-8 rounded-full flex items-center justify-center transition-all duration-200 ${
            isFavorite
              ? "bg-red-500 text-white"
              : "bg-white/80 text-gray-600 hover:bg-white hover:text-red-500"
          } ${isLoadingFavorite ? "opacity-50 cursor-not-allowed" : ""}`}
        >
          <i
            className={`${
              isFavorite ? "fas" : "far"
            } fa-heart text-sm ${isLoadingFavorite ? "fa-spin fa-spinner" : ""}`}
          ></i>
        </button>

        {/* Stock Status Badge */}
        {isOutOfStock && (
          <div className="absolute top-3 left-3 bg-red-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            Out of Stock
          </div>
        )}

        {/* Featured Badge */}
        {product.featured && (
          <div className="absolute top-3 left-3 bg-primary-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            Featured
          </div>
        )}

        {/* Discount Badge */}
        {product.originalPrice && product.originalPrice > product.price && (
          <div className="absolute top-3 left-3 bg-green-500 text-white px-2 py-1 rounded-full text-xs font-medium">
            {Math.round(
              ((product.originalPrice - product.price) /
                product.originalPrice) *
                100,
            )}
            % OFF
          </div>
        )}
      </div>

      <div className="p-6">
        {/* Category */}
        <div className="flex items-center justify-between mb-2">
          <span className="text-xs font-medium text-primary-600 dark:text-primary-400 bg-primary-100 dark:bg-primary-900 px-2 py-1 rounded-full">
            {product.category}
          </span>
          {product.tags && product.tags.includes("Organic") && (
            <span className="text-xs font-medium text-green-600 dark:text-green-400 bg-green-100 dark:bg-green-900 px-2 py-1 rounded-full">
              Organic
            </span>
          )}
        </div>

        {/* Product Name */}
        <Link href={`/details?id=${product._id}`}>
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2 hover:text-primary-600 dark:hover:text-primary-400 transition-colors cursor-pointer line-clamp-2">
            {product.name}
          </h3>
        </Link>

        {/* Farmer Name */}
        <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
          <i className="fas fa-user-tie mr-1"></i>
          by{" "}
          {typeof product.farmer === "object" && product.farmer?.name
            ? product.farmer.name
            : typeof product.farmer === "string"
              ? product.farmer
              : product.farmerName || "Unknown Farmer"}
        </p>

        {/* Rating */}
        <div className="flex items-center mb-3">
          <StarRating
            rating={product.averageRating || 0}
            size="sm"
            showCount={false}
          />
          <span className="text-sm text-gray-600 dark:text-gray-400 ml-2">
            ({product.reviewCount || product.totalRatings || 0} reviews)
          </span>
        </div>

        {/* Price */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-xl font-bold text-gray-900 dark:text-white">
              {formatPrice(product.price)}
            </span>
            {product.originalPrice && product.originalPrice > product.price && (
              <span className="text-sm text-gray-500 line-through">
                {formatPrice(product.originalPrice)}
              </span>
            )}
          </div>
          <span className="text-sm text-gray-600 dark:text-gray-400">
            per {product.unit || "kg"}
          </span>
        </div>

        {/* Stock Info */}
        <div className="mb-4">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600 dark:text-gray-400">Stock:</span>
            <span
              className={`font-medium ${
                product.stock > 50
                  ? "text-green-600"
                  : product.stock > 10
                    ? "text-yellow-600"
                    : "text-red-600"
              }`}
            >
              {product.stock} {product.unit || "kg"} available
            </span>
          </div>
          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2 mt-1">
            <div
              className={`h-2 rounded-full transition-all duration-300 ${
                product.stock > 50
                  ? "bg-green-500"
                  : product.stock > 10
                    ? "bg-yellow-500"
                    : "bg-red-500"
              }`}
              style={{
                width: `${Math.min((product.stock / 100) * 100, 100)}%`,
              }}
            ></div>
          </div>
        </div>

        {/* Action Buttons */}
        {showAddToCart && (
          <div className="flex gap-2">
            <button
              onClick={handleAddToCart}
              disabled={isAddingToCart || isOutOfStock}
              className={`flex-1 py-2 px-4 rounded-lg font-medium text-sm transition-all duration-200 ${
                isOutOfStock
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-primary-600 hover:bg-primary-700 text-white"
              } ${isAddingToCart ? "opacity-50 cursor-not-allowed" : ""}`}
            >
              {isAddingToCart ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Adding...
                </>
              ) : isOutOfStock ? (
                "Out of Stock"
              ) : (
                <>
                  <i className="fas fa-cart-plus mr-2"></i>
                  Add to Cart
                </>
              )}
            </button>

            <Link
              href={`/details?id=${product._id}`}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 transition-all duration-200 flex items-center justify-center"
            >
              <i className="fas fa-eye"></i>
            </Link>
          </div>
        )}

        {/* Purchase Count (for featured products) */}
        {product.purchaseCount > 0 && (
          <div className="mt-2 text-xs text-gray-500 dark:text-gray-400 text-center">
            <i className="fas fa-shopping-bag mr-1"></i>
            {product.purchaseCount} purchases
          </div>
        )}
      </div>
    </div>
  );
}

