  reviews.forEach((review, index) => {
    console.log(
      `${index + 1}. ${review.createdAt.toISOString()} - ${review.reviewer} - ${review.comment?.substring(0, 50)}...`,
    );
  });

  await client.close();
}

// hooks/useDashboardData.js
"use client";

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useSession } from "next-auth/react";
import { apiService } from "@/lib/api-service";

export function useDashboardData() {
  const { data: session } = useSession();
  const queryClient = useQueryClient();

  const getUserIdentifiers = () => {
    if (!session?.user) return null;
    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  };

  const userIds = getUserIdentifiers();

  const {
    data,
    isLoading,
    error,
    refetch,
    isRefetching
  } = useQuery({
    queryKey: ['dashboard', userIds?.userId, userIds?.userEmail],
    queryFn: async () => {
      if (!userIds?.userId && !userIds?.userEmail) {
        throw new Error("No user identification found");
      }

      const dashboardData = await apiService.getDashboardData(
        userIds.userId,
        userIds.userEmail,
        { forceRefresh: true }
      );

      return {
        products: dashboardData.products || [],
        orders: dashboardData.orders || [],
        analytics: dashboardData.analytics || {},
        meta: dashboardData.meta || {}
      };
    },
    enabled: !!session?.user && (!!userIds?.userId || !!userIds?.userEmail),
    staleTime: 2 * 60 * 1000, // 2 minutes for dashboard data
    cacheTime: 5 * 60 * 1000, // 5 minutes cache
  });

  // Function to invalidate and refetch dashboard data
  const refreshDashboard = () => {
    return queryClient.invalidateQueries({
      queryKey: ['dashboard', userIds?.userId, userIds?.userEmail]
    });
  };

  // Function to manually refetch
  const refetchDashboard = () => {
    return refetch();
  };

  // Function to update specific order in cache without full refetch
  const updateOrderInCache = (orderId, newStatus, updatedOrder) => {
    queryClient.setQueryData(
      ['dashboard', userIds?.userId, userIds?.userEmail],
      (oldData) => {
        if (!oldData) return oldData;

        const updatedOrders = oldData.orders.map(order => 
          order._id === orderId || order.id === orderId
            ? { ...order, status: newStatus, ...updatedOrder }
            : order
        );

        return {
          ...oldData,
          orders: updatedOrders
        };
      }
    );
  };

  return {
    products: data?.products || [],
    orders: data?.orders || [],
    analytics: data?.analytics || {},
    meta: data?.meta || {},
    isLoading,
    error,
    isRefetching,
    refetch: refetchDashboard,
    refreshDashboard,
    updateOrderInCache
  };
}

// hooks/useFarmerOrders.js
"use client";

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useSession } from "next-auth/react";
import { useCallback } from "react";

export function useFarmerOrders() {
  const { data: session } = useSession();
  const queryClient = useQueryClient();

  const getUserIdentifiers = () => {
    if (!session?.user) return null;
    const user = session.user;
    return {
      userId: user.userId || user.id || user._id,
      userEmail: user.email,
    };
  };

  const userIds = getUserIdentifiers();

  const {
    data: orders = [],
    isLoading,
    error,
    refetch,
    isRefetching
  } = useQuery({
    queryKey: ['farmer-orders', userIds?.userId, userIds?.userEmail],
    queryFn: async () => {
      if (!userIds?.userId && !userIds?.userEmail) {
        throw new Error("No user identification found");
      }

      const params = new URLSearchParams();
      if (userIds.userId) params.append("farmerId", userIds.userId);
      if (userIds.userEmail) params.append("farmerEmail", userIds.userEmail);

      const response = await fetch(`/api/orders?${params.toString()}`, {
        cache: "no-store",
        headers: {
          "Cache-Control": "no-cache",
          Pragma: "no-cache",
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      return data.orders || [];
    },
    enabled: !!session?.user && (!!userIds?.userId || !!userIds?.userEmail),
    staleTime: 30 * 1000, // 30 seconds
    cacheTime: 5 * 60 * 1000, // 5 minutes
  });

  // Function to invalidate and refetch orders
  const refreshOrders = () => {
    return queryClient.invalidateQueries({
      queryKey: ['farmer-orders', userIds?.userId, userIds?.userEmail]
    });
  };

  // Function to manually refetch
  const refetchOrders = () => {
    return refetch();
  };

  // Function to update specific order in cache
  const updateOrderInCache = useCallback((orderId, newStatus, updatedOrder) => {
    queryClient.setQueryData(
      ['farmer-orders', userIds?.userId, userIds?.userEmail],
      (oldOrders) => {
        if (!oldOrders) return oldOrders;

        return oldOrders.map(order => 
          order._id === orderId || order.id === orderId
            ? { ...order, status: newStatus, ...updatedOrder }
            : order
        );
      }
    );
  }, [queryClient, userIds?.userId, userIds?.userEmail]);

  return {
    orders,
    isLoading,
    error,
    isRefetching,
    refetch: refetchOrders,
    refreshOrders,
    updateOrderInCache
  };
}

// hooks/useOrderStatusUpdate.js
"use client";

import { useCallback, useState } from "react";
import { useQueryClient } from "@tanstack/react-query";
import { useSession } from "next-auth/react";

export function useOrderStatusUpdate() {
  const [updating, setUpdating] = useState(false);
  const queryClient = useQueryClient();
  const { data: session } = useSession();

  const updateOrderStatus = useCallback(
    async (orderId, newStatus, additionalData = {}) => {
      if (!orderId || !newStatus) {
        throw new Error("Order ID and status are required");
      }

      setUpdating(true);

      try {
        // Prepare the update payload
        const updatePayload = {
          status: newStatus,
          statusHistory: {
            status: newStatus,
            timestamp: new Date().toISOString(),
            updatedBy: "farmer", // Could be dynamic based on user role
          },
          ...additionalData,
        };

        // Call the API to update the order
        const response = await fetch(`/api/orders/${orderId}`, {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatePayload),
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(
            errorData.error ||
              `HTTP ${response.status}: ${response.statusText}`,
          );
        }

        const result = await response.json();

        // Get user identifiers for cache invalidation
        const user = session?.user;
        if (user) {
          const userId = user.userId || user.id || user._id;
          const userEmail = user.email;

          // Invalidate dashboard queries to trigger refetch
          console.log(
            `Invalidating dashboard cache for order update: ${orderId} -> ${newStatus}`,
          );

          await queryClient.invalidateQueries({
            queryKey: ["dashboard", userId, userEmail],
          });

          // Also update the specific order in cache optimistically
          queryClient.setQueryData(
            ["dashboard", userId, userEmail],
            (oldData) => {
              if (!oldData) return oldData;

              const updatedOrders = oldData.orders.map((order) =>
                order._id === orderId || order.id === orderId
                  ? { ...order, status: newStatus, ...result.order }
                  : order,
              );

              return {
                ...oldData,
                orders: updatedOrders,
              };
            },
          );

          console.log("Dashboard cache invalidated and updated successfully");
        }

        return result;
      } catch (error) {
        console.error("Failed to update order status:", error);
        throw error;
      } finally {
        setUpdating(false);
      }
    },
    [queryClient, session?.user],
  );

  return {
    updateOrderStatus,
    updating,
  };
}

