// Add reviews to ALL products that don't have any reviews
const { MongoClient, ObjectId } = require('mongodb');

async function addReviewsToAllProducts() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  // Get ALL products
  const allProducts = await db.collection('products').find({}).toArray();
  console.log(`Found ${allProducts.length} total products`);

  // Get all existing reviews
  const existingReviews = await db.collection('reviews').find({}).toArray();
  const reviewedProductIds = [...new Set(existingReviews.map(r => r.productId))];
  console.log(`Found ${existingReviews.length} existing reviews for ${reviewedProductIds.length} products`);

  // Find products that have NO reviews
  const productsWithoutReviews = allProducts.filter(product => 
    !reviewedProductIds.includes(product._id.toString())
  );

  console.log(`Found ${productsWithoutReviews.length} products WITHOUT reviews`);
  console.log('Products without reviews:');
  productsWithoutReviews.forEach(p => console.log(`- ${p.name} (ID: ${p._id})`));

  // Add sample reviews to products without reviews
  for (const product of productsWithoutReviews) {
    const ratings = [4, 4.5, 5, 3.5, 4.5]; // Random good ratings
    const numReviews = Math.floor(Math.random() * 3) + 2; // 2-4 reviews per product

    const sampleReviews = [];
    for (let i = 0; i < numReviews; i++) {
      const rating = ratings[Math.floor(Math.random() * ratings.length)];
      sampleReviews.push({
        productId: product._id.toString(),
        userId: `sample-user-${i + 1}`,
        reviewer: ['John Doe', 'Jane Smith', 'Mike Johnson', 'Sarah Wilson', 'David Brown'][i] || 'Anonymous',
        rating: rating,
        comment: [
          `Great quality ${product.name}! Fresh and delicious.`,
          `Excellent ${product.name}. Highly recommended!`,
          `Good quality ${product.name}. Will buy again.`,
          `Amazing ${product.name}! Worth every penny.`,
          `Perfect ${product.name}. Fast delivery too!`
        ][i] || `Nice ${product.name}!`,
        createdAt: new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000), // Random date within last 30 days
        updatedAt: new Date()
      });
    }

    await db.collection('reviews').insertMany(sampleReviews);
    console.log(`✅ Added ${numReviews} reviews for ${product.name}`);
  }

  console.log(`✅ Successfully added reviews to ${productsWithoutReviews.length} products!`);
  await client.close();
}

addReviewsToAllProducts().catch(console.error);

// Add sample reviews to some old products that currently have no reviews
const { MongoClient, ObjectId } = require('mongodb');

async function addReviewsToOldProducts() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  // Get old products that have no reviews (excluding mango and top grain)
  const oldProducts = await db.collection('products').find({
    _id: { 
      $nin: [
        new ObjectId('6889cb7ab6d22dd651140bee'), // mango
        new ObjectId('688affa63c35c3c0ff9c4925')  // top grain
      ]
    }
  }).limit(5).toArray(); // Just add reviews to first 5 old products

  console.log(`Adding sample reviews to ${oldProducts.length} old products...\n`);

  const sampleReviewers = [
    'Alice Johnson', 'Bob Smith', 'Carol Davis', 'David Wilson', 'Emma Brown',
    'Frank Miller', 'Grace Lee', 'Henry Taylor', 'Ivy Chen', 'Jack Anderson'
  ];

  const sampleComments = [
    'Excellent quality! Fresh and delicious.',
    'Great product, will buy again.',
    'Very satisfied with the freshness.',
    'Good value for money.',
    'Highly recommended!',
    'Perfect quality, as expected.',
    'Fresh and tasty!',
    'Amazing product quality.',
    'Will definitely order again.',
    'Best quality I\'ve found!'
  ];

  for (const product of oldProducts) {
    const numReviews = Math.floor(Math.random() * 4) + 2; // 2-5 reviews per product
    const reviews = [];

    for (let i = 0; i < numReviews; i++) {
      const rating = [4, 4.5, 5, 3.5, 4.5][Math.floor(Math.random() * 5)]; // Good ratings
      const reviewer = sampleReviewers[Math.floor(Math.random() * sampleReviewers.length)];
      const comment = sampleComments[Math.floor(Math.random() * sampleComments.length)];

      reviews.push({
        productId: product._id.toString(),
        userId: `sample-user-${i + 1}`,
        reviewer: reviewer,
        rating: rating,
        comment: `${comment} ${product.name} exceeded my expectations!`,
        createdAt: new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000), // Random date within last 30 days
        updatedAt: new Date()
      });
    }

    await db.collection('reviews').insertMany(reviews);
    console.log(`✅ Added ${numReviews} reviews to ${product.name}`);
  }

  console.log(`\n Successfully added reviews to ${oldProducts.length} old products!`);
  console.log('Now refresh your products page - these products should show star ratings!');

  await client.close();
}

addReviewsToOldProducts().catch(console.error);

