    console.log("Farmer-profile page - isAuthenticated:", isAuthenticated);

    if (!isAuthenticated) {
      router.push("/login");
      return;
    }

    // Check if user is a farmer, if not redirect to regular profile (checking userType instead of role)
    if (user && user.userType !== "farmer") {
      console.log("Non-farmer user, redirecting to regular profile");
      router.push("/profile");
      return;
    }

    // Initialize profile data with user data
    if (user) {
      console.log("Initializing farmer profile data");
      setProfileData((prev) => ({
        ...prev,
        name: user.name || "",
        email: user.email || "",
        phone: user.phone || "",
        address: user.address || prev.address,
        farmInfo: user.farmInfo || prev.farmInfo,
        businessInfo: user.businessInfo || prev.businessInfo,
        preferences: user.preferences || prev.preferences,
      }));
    }
  }, [user, isAuthenticated, router]);

  const handleInputChange = (field, value) => {
    setProfileData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleNestedChange = (section, subsection, field, value) => {
    if (subsection) {
      setProfileData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [subsection]: {
            ...prev[section][subsection],
            [field]: value,
          },
        },
      }));
    } else {
      setProfileData((prev) => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: value,
        },
      }));
    }
  };

  const handleArrayChange = (section, field, value) => {
    const arrayValue = value
      .split(",")
      .map((item) => item.trim())
      .filter((item) => item);
    setProfileData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: arrayValue,
      },
    }));
  };

  const handlePersonalInfoUpdate = async () => {
    try {
      setLoading(true);
      setMessage({ type: "", text: "" });

      // Use the farmers API endpoint for farmer profile updates
      const response = await fetch("/api/farmers", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: profileData.name,
          phone: profileData.phone,
          address: profileData.address,
          farmInfo: profileData.farmInfo,
          businessInfo: profileData.businessInfo,
          preferences: profileData.preferences,
        }),
      });

      if (response.ok) {
        const result = await response.json();
        // Update the user context with the updated farmer data
        updateUser({
          ...user,
          ...result.farmer,
        });
        setMessage({ type: "success", text: "Profile updated successfully!" });

        // Invalidate both farmers and products queries to refresh the data
        queryClient.invalidateQueries({ queryKey: FARMERS_QUERY_KEY });
        queryClient.invalidateQueries({ queryKey: ["products"] });

        // Also update localStorage to persist data
        localStorage.setItem(
          "farmfresh_user",
          JSON.stringify({
            ...user,
            ...result.farmer,
          }),
        );
      } else {
        const errorData = await response.json();
        setMessage({
          type: "error",
          text: errorData.error || "Failed to update profile",
        });
      }
    } catch (error) {
      setMessage({
        type: "error",
        text: "An error occurred while updating profile",
      });
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordChange = async () => {
    try {
      setLoading(true);
      setMessage({ type: "", text: "" });

      if (
        profileData.security.newPassword !==
        profileData.security.confirmPassword
      ) {
        setMessage({ type: "error", text: "New passwords don't match" });
        return;
      }

      if (profileData.security.newPassword.length < 6) {
        setMessage({
          type: "error",
          text: "Password must be at least 6 characters long",
        });
        return;
      }

      const response = await fetch("/api/auth/users", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          currentPassword: profileData.security.currentPassword,
          newPassword: profileData.security.newPassword,
        }),
      });

      if (response.ok) {
        setMessage({ type: "success", text: "Password updated successfully!" });
        setProfileData((prev) => ({
          ...prev,
          security: {
            currentPassword: "",
            newPassword: "",
            confirmPassword: "",
          },
        }));
      } else {
        const errorData = await response.json();
        setMessage({
          type: "error",
          text: errorData.error || "Failed to update password",
        });
      }
    } catch (error) {
      setMessage({
        type: "error",
        text: "An error occurred while updating password",
      });
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { id: "personal", label: "Personal Info", icon: "fas fa-user" },
    { id: "farm", label: "Farm Info", icon: "fas fa-seedling" },
    { id: "business", label: "Business", icon: "fas fa-building" },
    { id: "address", label: "Address", icon: "fas fa-map-marker-alt" },
    { id: "preferences", label: "Preferences", icon: "fas fa-cog" },
    { id: "security", label: "Security", icon: "fas fa-shield-alt" },
  ];

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            Farmer Profile Settings
          </h1>
          <p className="mt-2 text-gray-600 dark:text-gray-400">
            Manage your farmer account settings and farm information
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm">
          {/* Tab Navigation */}
          <div className="border-b border-gray-200 dark:border-gray-700">
            <nav className="flex space-x-8 px-6 overflow-x-auto">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 whitespace-nowrap ${
                    activeTab === tab.id
                      ? "border-primary-500 text-primary-600 dark:text-primary-400"
                      : "border-transparent text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300"
                  }`}
                >
                  <i className={tab.icon}></i>
                  <span>{tab.label}</span>
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="p-6">
            {message.text && (
              <div
                className={`mb-4 p-4 rounded-md ${
                  message.type === "success"
                    ? "bg-green-50 dark:bg-green-900 text-green-700 dark:text-green-300 border border-green-200 dark:border-green-800"
                    : "bg-red-50 dark:bg-red-900 text-red-700 dark:text-red-300 border border-red-200 dark:border-red-800"
                }`}
              >
                {message.text}
              </div>
            )}

            {/* Personal Information Tab */}
            {activeTab === "personal" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Personal Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Full Name
                    </label>
                    <input
                      type="text"
                      value={profileData.name}
                      onChange={(e) =>
                        handleInputChange("name", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Email Address
                    </label>
                    <input
                      type="email"
                      value={profileData.email}
                      disabled
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm bg-gray-50 dark:bg-gray-600 text-gray-500 dark:text-gray-400"
                    />
                    <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">
                      Email cannot be changed
                    </p>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      value={profileData.phone}
                      onChange={(e) =>
                        handleInputChange("phone", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Changes"}
                </button>
              </div>
            )}

            {/* Farm Information Tab */}
            {activeTab === "farm" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Farm Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Name *
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.farmName}
                      onChange={(e) =>
                        handleNestedChange(
                          "farmInfo",
                          "",
                          "farmName",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Green Valley Farm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Type
                    </label>
                    <select
                      value={profileData.farmInfo.farmType}
                      onChange={(e) =>
                        handleNestedChange(
                          "farmInfo",
                          "",
                          "farmType",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    >
                      <option value="">Select Farm Type</option>
                      <option value="organic">Organic Farm</option>
                      <option value="conventional">Conventional Farm</option>
                      <option value="hydroponic">Hydroponic Farm</option>
                      <option value="greenhouse">Greenhouse Farm</option>
                      <option value="mixed">Mixed Farm</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Size (acres)
                    </label>
                    <input
                      type="number"
                      value={profileData.farmInfo.farmSize}
                      onChange={(e) =>
                        handleNestedChange(
                          "farmInfo",
                          "",
                          "farmSize",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="10"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Established Year
                    </label>
                    <input
                      type="number"
                      value={profileData.farmInfo.establishedYear}
                      onChange={(e) =>
                        handleNestedChange(
                          "farmInfo",
                          "",
                          "establishedYear",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="2020"
                    />
                  </div>

                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farm Description
                    </label>
                    <textarea
                      value={profileData.farmInfo.farmDescription}
                      onChange={(e) =>
                        handleNestedChange(
                          "farmInfo",
                          "",
                          "farmDescription",
                          e.target.value,
                        )
                      }
                      rows={4}
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Tell customers about your farm, your farming philosophy, and what makes your products special..."
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Certifications (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.certifications.join(", ")}
                      onChange={(e) =>
                        handleArrayChange(
                          "farmInfo",
                          "certifications",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Organic Certified, Non-GMO, USDA Organic"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Farming Methods (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={profileData.farmInfo.farmingMethods.join(", ")}
                      onChange={(e) =>
                        handleArrayChange(
                          "farmInfo",
                          "farmingMethods",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="Sustainable, Pesticide-free, Crop Rotation"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Farm Information"}
                </button>
              </div>
            )}

            {/* Business Information Tab */}
            {activeTab === "business" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Business Information
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Business License
                    </label>
                    <input
                      type="text"
                      value={profileData.businessInfo.businessLicense}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "",
                          "businessLicense",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Tax ID
                    </label>
                    <input
                      type="text"
                      value={profileData.businessInfo.taxId}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "",
                          "taxId",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Delivery Radius (miles)
                    </label>
                    <input
                      type="number"
                      value={profileData.businessInfo.deliveryRadius}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "",
                          "deliveryRadius",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="25"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Minimum Order ($)
                    </label>
                    <input
                      type="number"
                      value={profileData.businessInfo.minimumOrder}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "",
                          "minimumOrder",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="25"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Website
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.website}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "",
                          "website",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://yourfarm.com"
                    />
                  </div>

                  {/* Social Media */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Facebook
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.facebook}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "socialMedia",
                          "facebook",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://facebook.com/yourfarm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Instagram
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.instagram}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "socialMedia",
                          "instagram",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://instagram.com/yourfarm"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Twitter
                    </label>
                    <input
                      type="url"
                      value={profileData.businessInfo.socialMedia.twitter}
                      onChange={(e) =>
                        handleNestedChange(
                          "businessInfo",
                          "socialMedia",
                          "twitter",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                      placeholder="https://twitter.com/yourfarm"
                    />
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
                  disabled={loading}
                  className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Saving..." : "Save Business Information"}
                </button>
              </div>
            )}

            {/* Address Tab */}
            {activeTab === "address" && (
              <div className="space-y-6">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Farm Address
                </h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Street Address
                    </label>
                    <input
                      type="text"
                      value={profileData.address.street}
                      onChange={(e) =>
                        handleNestedChange(
                          "address",
                          "",
                          "street",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      City
                    </label>
                    <input
                      type="text"
                      value={profileData.address.city}
                      onChange={(e) =>
                        handleNestedChange(
                          "address",
                          "",
                          "city",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      State/Province
                    </label>
                    <input
                      type="text"
                      value={profileData.address.state}
                      onChange={(e) =>
                        handleNestedChange(
                          "address",
                          "",
                          "state",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      ZIP/Postal Code
                    </label>
                    <input
                      type="text"
                      value={profileData.address.zipCode}
                      onChange={(e) =>
                        handleNestedChange(
                          "address",
                          "",
                          "zipCode",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Country
                    </label>
                    <select
                      value={profileData.address.country}
                      onChange={(e) =>
                        handleNestedChange(
                          "address",
                          "",
                          "country",
                          e.target.value,
                        )
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    >
                      <option value="">Select Country</option>
                      {countries.map((country) => (
                        <option key={country.code} value={country.code}>
                          {country.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <button
                  onClick={handlePersonalInfoUpdate}
