async function showReviewTimeline() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const reviews = await db
    .collection("reviews")
    .find({})
    .sort({ createdAt: 1 })
    .toArray();

  console.log("\n Review timeline (oldest to newest):");
  reviews.forEach((review, index) => {
    console.log(
      `${index + 1}. ${review.createdAt.toISOString()} - ${review.reviewer} - ${review.comment?.substring(0, 50)}...`,
    );
  });

  await client.close();
}

// Check and fix review mess - remove duplicate reviews added by mistake
const { MongoClient, ObjectId } = require("mongodb");

async function fixReviewMess() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  console.log(" Analyzing review situation...");

  // Get all reviews
  const allReviews = await db.collection("reviews").find({}).toArray();
  console.log(`Total reviews in database: ${allReviews.length}`);

  // Group reviews by product ID
  const reviewsByProduct = {};
  allReviews.forEach((review) => {
    if (!reviewsByProduct[review.productId]) {
      reviewsByProduct[review.productId] = [];
    }
    reviewsByProduct[review.productId].push(review);
  });

  console.log("\n Reviews per product:");
  Object.entries(reviewsByProduct).forEach(([productId, reviews]) => {
    const product = reviews[0]; // Just to get some context
    console.log(`Product ID ${productId}: ${reviews.length} reviews`);

    // Show reviewers to identify duplicates
    const reviewers = reviews.map((r) => r.reviewer).join(", ");
    console.log(`  Reviewers: ${reviewers}`);

    // Check for suspicious patterns (our sample review names)
    const sampleReviewers = [
      "John Doe",
      "Jane Smith",
      "Mike Johnson",
      "Sarah Wilson",
      "David Brown",
    ];
    const hasSampleReviews = reviews.some((r) =>
      sampleReviewers.includes(r.reviewer),
    );
    if (hasSampleReviews) {
      console.log(
        `  ⚠️ This product has sample reviews that might need removal`,
      );
    }
    console.log("");
  });

  // Ask what to do
  console.log("\n What would you like to do?");
  console.log(
    "1. Remove all reviews with sample reviewer names (John Doe, Jane Smith, etc.)",
  );
  console.log("2. Remove reviews created in the last hour (recent additions)");
  console.log(
    "3. Show me the oldest vs newest reviews to identify which are original",
  );
  console.log("\nRun with parameter: node fix-reviews.js [1|2|3]");

  await client.close();
}

// Handle command line arguments
const action = process.argv[2];

if (action === "1") {
  removeSampleReviews();
} else if (action === "2") {
  removeRecentReviews();
} else if (action === "3") {
  showReviewTimeline();
} else {
  fixReviewMess().catch(console.error);
}

async function removeSampleReviews() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const sampleReviewers = [
    "John Doe",
    "Jane Smith",
    "Mike Johnson",
    "Sarah Wilson",
    "David Brown",
  ];

  console.log("️ Removing sample reviews...");
  const result = await db.collection("reviews").deleteMany({
    reviewer: { $in: sampleReviewers },
  });

  console.log(`✅ Removed ${result.deletedCount} sample reviews`);

  // Check what's left
  const remainingReviews = await db.collection("reviews").find({}).toArray();
  console.log(` Remaining reviews: ${remainingReviews.length}`);
  console.log("Your original reviews:");
  remainingReviews.forEach((r) =>
    console.log(`- ${r.reviewer || "Anonymous"}: ${r.comment?.substring(0, 50)}...`),
  );

  await client.close();
}

async function removeRecentReviews() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const oneHourAgo = new Date(Date.now() - 60 * 60 * 1000);

  console.log("️ Removing reviews created in the last hour...");
  const result = await db.collection("reviews").deleteMany({
    createdAt: { $gte: oneHourAgo },
  });

  console.log(`✅ Removed ${result.deletedCount} recent reviews`);
  await client.close();
}

async function showReviewTimeline() {
  const client = new MongoClient("mongodb://localhost:27017");
  await client.connect();
  const db = client.db("farmfresh");

  const reviews = await db
    .collection("reviews")
    .find({})
    .sort({ createdAt: 1 })
    .toArray();

  console.log("\n Review timeline (oldest to newest):");
  reviews.forEach((review, index) => {
    console.log(
      `${index + 1}. ${review.createdAt.toISOString()} - ${review.reviewer} - ${review.comment?.substring(0, 50)}...`,
    );
  });

  await client.close();
}

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { apiService } from "@/lib/api-service";

// Query keys for React Query
export const FARMERS_QUERY_KEY = ["farmers"];

// Custom hook for farmers data
export function useFarmersQuery(options = {}) {
  return useQuery({
    queryKey: FARMERS_QUERY_KEY,
    queryFn: async () => {
      const data = await apiService.getFarmers();
      return data;
    },
    staleTime: 15 * 60 * 1000, // 15 minutes
    gcTime: 30 * 60 * 1000, // 30 minutes
    refetchOnWindowFocus: false,
    retry: 2,
    ...options,
  });
}

// Utility functions for farmers cache management
export function useFarmersCache() {
  const queryClient = useQueryClient();

  return {
    // Invalidate farmers cache to trigger refetch
    invalidateFarmers: () => {
      console.log(" Invalidating farmers query");
      queryClient.invalidateQueries({
        queryKey: FARMERS_QUERY_KEY,
        exact: false,
      });
    },

    // Refresh farmers data
    refetchFarmers: () => {
      queryClient.refetchQueries({ queryKey: FARMERS_QUERY_KEY });
    },

    // Clear farmers cache
    removeFarmers: () => {
      queryClient.removeQueries({ queryKey: FARMERS_QUERY_KEY });
    },

    // Update farmer data in cache
    updateFarmerInCache: (farmerId, updatedData) => {
      queryClient.setQueryData(FARMERS_QUERY_KEY, (oldData) => {
        if (!oldData?.farmers) return oldData;

        return {
          ...oldData,
          farmers: oldData.farmers.map(farmer =>
            farmer._id === farmerId ? { ...farmer, ...updatedData } : farmer
          ),
        };
      });
    },
  };
}

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { apiService } from "@/lib/api-service";

// Query keys for React Query
export const PRODUCTS_QUERY_KEY = ["products"];
export const FARMERS_QUERY_KEY = ["farmers"];

// Custom hook for products with React Query
export function useProductsQuery(filters = {}, options = {}) {
  const { searchTerm, selectedCategory } = filters;

  // Create query key based on filters for proper caching
  const queryKey = [
    ...PRODUCTS_QUERY_KEY,
    {
      search: searchTerm || undefined,
      category:
        selectedCategory !== "All Categories" ? selectedCategory : undefined,
    },
  ];

  return useQuery({
    queryKey,
    queryFn: async () => {
      const params = {
        limit: 1000, // Fetch more products for client-side filtering
      };

      // Add search/category filters for server-side optimization
      if (searchTerm) params.search = searchTerm;
      if (selectedCategory !== "All Categories") {
        params.category = selectedCategory;
      }

      const data = await apiService.getProducts(params, {
        skipCache: true, // Let React Query handle caching
        useSessionCache: false,
      });

      return data;
    },
    staleTime: 3 * 60 * 1000, // 3 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes (replaces cacheTime in newer versions)
    refetchOnWindowFocus: false,
    retry: 2,
    ...options,
  });
}

// Custom hook for farmers data
export function useFarmersQuery(options = {}) {
  return useQuery({
    queryKey: FARMERS_QUERY_KEY,
    queryFn: async () => {
      const data = await apiService.getFarmers();
      return data;
    },
    staleTime: 15 * 60 * 1000, // 15 minutes
    gcTime: 30 * 60 * 1000, // 30 minutes
    refetchOnWindowFocus: false,
    retry: 2,
    ...options,
  });
}

// Utility functions for cache management
export function useProductsCache() {
  const queryClient = useQueryClient();

  return {
    // Invalidate products cache to trigger refetch
    invalidateProducts: () => {
      console.log(" Invalidating all product queries");
      queryClient.invalidateQueries({
        queryKey: PRODUCTS_QUERY_KEY,
        exact: false,
      });
    },

    // Refresh products data
    refetchProducts: () => {
      queryClient.refetchQueries({ queryKey: PRODUCTS_QUERY_KEY });
    },

    // Clear products cache
    removeProducts: () => {
      queryClient.removeQueries({ queryKey: PRODUCTS_QUERY_KEY });
    },
  };
}

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { apiService } from "@/lib/api-service";

// Query keys for React Query
export const PRODUCTS_QUERY_KEY = ["products"];

// Custom hook for products with React Query
export function useProductsQuery(filters = {}, options = {}) {
  const { searchTerm, selectedCategory } = filters;

  // Create query key based on filters for proper caching
  const queryKey = [
    ...PRODUCTS_QUERY_KEY,
    {
      search: searchTerm || undefined,
      category:
        selectedCategory !== "All Categories" ? selectedCategory : undefined,
    },
  ];

  return useQuery({
    queryKey,
    queryFn: async () => {
      const params = {
        limit: 1000, // Fetch more products for client-side filtering
      };

      // Add search/category filters for server-side optimization
      if (searchTerm) params.search = searchTerm;
      if (selectedCategory !== "All Categories") {
        params.category = selectedCategory;
      }

      const data = await apiService.getProducts(params, {
        skipCache: true, // Let React Query handle caching
        useSessionCache: false,
      });

      return data;
    },
    staleTime: 3 * 60 * 1000, // 3 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes (replaces cacheTime in newer versions)
    refetchOnWindowFocus: false,
    retry: 2,
    ...options,
