import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";

export async function POST(request) {
  try {
    const client = await clientPromise;
    const db = client.db("farmfresh");
    const usersCollection = db.collection("users");
    const farmersCollection = db.collection("farmers");

    // Find all users with userType "farmer"
    const farmerUsers = await usersCollection.find({ userType: "farmer" }).toArray();

    console.log(`Found ${farmerUsers.length} farmer users to migrate`);

    let migratedCount = 0;
    let skippedCount = 0;

    for (const user of farmerUsers) {
      // Check if farmer already exists in farmers collection
      const existingFarmer = await farmersCollection.findOne({ _id: user._id });

      if (!existingFarmer) {
        // Create farmer document
        const farmerData = {
          _id: user._id,
          name: user.name || `${user.firstName} ${user.lastName}`,
          email: user.email,
          phone: user.phone || "",
          location: user.address || "",
          farmName: user.farmDetails?.farmName || `${user.name}'s Farm`,
          specializations: user.farmDetails?.specialization ? 
            Array.isArray(user.farmDetails.specialization) ? 
              user.farmDetails.specialization : 
              [user.farmDetails.specialization] : 
            ["General Farming"],
          farmSize: user.farmDetails?.farmSize || null,
          farmSizeUnit: user.farmDetails?.farmSizeUnit || "acres",
          bio: user.bio || "",
          profilePicture: user.profilePicture || null,
          rating: 0,
          totalReviews: 0,
          verified: false,
          joinedDate: user.createdAt || new Date(),
          products: [],
          orders: [],
          availability: {
            status: "available",
            schedule: {
              monday: { start: "09:00", end: "17:00", available: true },
              tuesday: { start: "09:00", end: "17:00", available: true },
              wednesday: { start: "09:00", end: "17:00", available: true },
              thursday: { start: "09:00", end: "17:00", available: true },
              friday: { start: "09:00", end: "17:00", available: true },
              saturday: { start: "09:00", end: "15:00", available: true },
              sunday: { start: "10:00", end: "14:00", available: false }
            }
          },
          socialMedia: {
            facebook: "",
            instagram: "",
            twitter: "",
            website: ""
          },
          certifications: [],
          deliveryOptions: {
            farmPickup: true,
            localDelivery: false,
            shipping: false,
            deliveryRadius: 0
          },
          createdAt: user.createdAt || new Date(),
          updatedAt: new Date()
        };

        // Insert farmer
        await farmersCollection.insertOne(farmerData);
        console.log(`✅ Migrated farmer: ${farmerData.name}`);
        migratedCount++;
      } else {
        console.log(`⚠️  Farmer ${user.name} already exists in farmers collection`);
        skippedCount++;
      }
    }

    // Get final count
    const totalFarmers = await farmersCollection.countDocuments();

    return NextResponse.json({
      success: true,
      message: "Farmer migration completed",
      results: {
        farmersFound: farmerUsers.length,
        migrated: migratedCount,
        skipped: skippedCount,
        totalFarmersInCollection: totalFarmers
      }
    });

  } catch (error) {
    console.error("Migration error:", error);
    return NextResponse.json(
      { 
        success: false, 
        error: "Failed to migrate farmers",
        message: error.message 
      },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

export async function GET(request, { params }) {
  try {
    const { orderId } = params;

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");
    const ordersCollection = db.collection("orders");

    // Find the order
    let order = null;

    // Try to find by ObjectId first
    if (ObjectId.isValid(orderId)) {
      order = await ordersCollection.findOne({ _id: new ObjectId(orderId) });
    }

    // If not found, try to find by orderNumber string
    if (!order) {
      order = await ordersCollection.findOne({ orderNumber: orderId });
    }

    if (!order) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Ensure orderNumber exists, fallback to orderId or generate one
    const orderNumber =
      order.orderNumber ||
      order._id?.toString() ||
      orderId ||
      `ORDER-${Date.now()}`;

    // Generate PDF invoice data (we'll generate PDF on client side)
    const invoiceData = {
      order: { ...order, orderNumber },
      company: {
        name: "FarmFresh Ltd.",
        address: "123 Agriculture Street, Gulshan",
        city: "Dhaka-1212, Bangladesh",
        phone: "+880-1234-567890",
        email: "info@farmfresh.com",
        website: "www.farmfresh.com",
      },
    };

    // Return JSON data for client-side PDF generation
    return NextResponse.json(
      {
        success: true,
        invoiceData,
        orderNumber,
      },
      {
        headers: {
          "X-Order-Number": orderNumber,
        },
      },
    );
  } catch (error) {
    console.error("Error generating invoice data:", error);
    return NextResponse.json(
      { error: "Failed to generate invoice data" },
      { status: 500 },
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

// Track if indexes have been initialized to avoid repeated calls
let indexesInitialized = false;

// Initialize indexes optimized for invoice queries
async function initializeInvoiceIndexes(db) {
  if (indexesInitialized) {
    return;
  }

  try {
    const ordersCollection = db.collection("orders");
    const existingIndexes = await ordersCollection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Invoice-specific indexes for fast lookups
    const indexesToCreate = [
      {
        key: { _id: 1, status: 1 },
        name: "invoice_id_status_idx",
        options: { background: true },
      },
      {
        key: { orderNumber: 1 },
        name: "invoice_orderNumber_idx",
        options: { background: true, sparse: true },
      },
      {
        key: { userId: 1, _id: 1 },
        name: "invoice_userId_id_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await ordersCollection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    indexesInitialized = true;
    console.log("Invoice indexes initialized successfully");
  } catch (error) {
    console.log("Invoice index initialization note:", error.message);
  }
}

// Cache for database connection and collections
let cachedDb = null;
let cachedOrdersCollection = null;

// Response cache for invoice data (10 minutes TTL)
const invoiceCache = new Map();
const CACHE_TTL = 10 * 60 * 1000;

// Generate cache key for invoice request
function generateCacheKey(orderId) {
  return `invoice:${orderId}`;
}

// Get cached invoice response if available and not expired
function getCachedInvoice(cacheKey) {
  const cached = invoiceCache.get(cacheKey);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }
  invoiceCache.delete(cacheKey);
  return null;
}

// Set invoice response in cache
function setCachedInvoice(cacheKey, data) {
  invoiceCache.set(cacheKey, {
    data,
    timestamp: Date.now(),
  });

  // Clean up expired entries
  if (invoiceCache.size > 100) {
    const now = Date.now();
    for (const [key, value] of invoiceCache.entries()) {
      if (now - value.timestamp >= CACHE_TTL) {
        invoiceCache.delete(key);
      }
    }
  }
}

// Export cache clearing function for order updates
export function clearInvoiceCache(orderId) {
  if (orderId) {
    invoiceCache.delete(generateCacheKey(orderId));
  } else {
    invoiceCache.clear();
  }
  console.log(`Invoice cache cleared${orderId ? ` for order ${orderId}` : ""}`);
}

export async function GET(request, { params }) {
  try {
    const { orderId } = params;

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    // Check cache first
    const cacheKey = generateCacheKey(orderId);
    const cachedResponse = getCachedInvoice(cacheKey);
    if (cachedResponse) {
      return NextResponse.json({
        ...cachedResponse,
        meta: {
          ...cachedResponse.meta,
          cached: true,
        },
      });
    }

    // Reuse database connection
    if (!cachedDb) {
      const client = await clientPromise;
      cachedDb = client.db("farmfresh");
      cachedOrdersCollection = cachedDb.collection("orders");
    }

    // Initialize indexes
    await initializeInvoiceIndexes(cachedDb);

    // Optimized aggregation pipeline for invoice data
    const pipeline = [
      // Match stage with optimized query conditions
      {
        $match: {
          $or: [
            // Try ObjectId match first (most common case)
            ...(ObjectId.isValid(orderId) 
              ? [{ _id: new ObjectId(orderId) }] 
              : []
            ),
            // Fallback to orderNumber match
            { orderNumber: orderId },
          ],
        },
      },

      // Project only necessary fields for invoice
      {
        $project: {
          _id: 1,
          userId: 1,
          orderNumber: 1,
          customerName: 1,
          customerEmail: 1,
          customerPhone: 1,
          customerInfo: 1,
          status: 1,
          total: 1,
          subtotal: 1,
          deliveryFee: 1,
          serviceFee: 1,
          deliveryAddress: 1,
          shippingAddress: 1,
          paymentMethod: 1,
          createdAt: 1,
          updatedAt: 1,
          // Include essential item fields for invoice
          "items._id": 1,
          "items.productId": 1,
          "items.productName": 1,
          "items.name": 1,
          "items.price": 1,
          "items.quantity": 1,
          "items.subtotal": 1,
          "items.farmerId": 1,
          "items.farmerName": 1,
          "items.farmerEmail": 1,
          "items.farmer": 1,
          "items.image": 1,
          "items.productImage": 1,
          "items.images": 1,
        },
      },

      // Add computed fields for invoice
      {
        $addFields: {
          // Ensure orderNumber exists
          orderNumber: {
            $ifNull: [
              "$orderNumber",
              {
                $concat: [
                  "ORDER-",
                  { $toString: "$_id" }
                ]
              }
            ]
          },
          // Calculate totals if missing
          calculatedSubtotal: {
            $ifNull: [
              "$subtotal",
              {
                $sum: {
                  $map: {
                    input: "$items",
                    as: "item",
                    in: {
                      $multiply: [
                        { $ifNull: ["$$item.price", 0] },
                        { $ifNull: ["$$item.quantity", 1] }
                      ]
                    }
                  }
                }
              }
            ]
          },
          // Ensure delivery address is properly structured
          invoiceDeliveryAddress: {
            $ifNull: [
              "$deliveryAddress",
              "$shippingAddress"
            ]
          }
        },
      },

      // Limit to single result for performance
      { $limit: 1 },
    ];

    const startTime = Date.now();
    const results = await cachedOrdersCollection.aggregate(pipeline).toArray();
    const queryTime = Date.now() - startTime;

    if (!results || results.length === 0) {
      return NextResponse.json(
        { 
          error: "Order not found",
          details: `No order found with ID: ${orderId}` 
        }, 
        { status: 404 }
      );
    }

    const order = results[0];

    // Ensure orderNumber exists with fallback
    const orderNumber = order.orderNumber || `ORDER-${Date.now()}`;

    // Prepare optimized invoice data
    const invoiceData = {
      order: {
        ...order,
        orderNumber,
        // Use calculated subtotal if original is missing
        subtotal: order.subtotal || order.calculatedSubtotal || 0,
        // Ensure delivery address is available
        deliveryAddress: order.invoiceDeliveryAddress || {
          name: order.customerName || "Valued Customer",
          address: "Address not provided",
          city: "N/A",
          phone: order.customerPhone || "N/A",
        },
        // Enrich items with fallback data
        items: (order.items || []).map(item => ({
          ...item,
          productName: item.productName || item.name || "Product",
          farmerName: item.farmerName || item.farmer?.name || "Local Farmer",
          image: item.image || item.productImage || item.images?.[0],
          subtotal: item.subtotal || (item.price * item.quantity) || 0,
        })),
      },
      company: {
        name: "FarmFresh Ltd.",
        address: "123 Agriculture Street, Gulshan",
        city: "Dhaka-1212, Bangladesh",
        phone: "+880-1234-567890",
        email: "info@farmfresh.com",
        website: "www.farmfresh.com",
        logo: "/logo.png", // Add logo path for future use
      },
      meta: {
        generatedAt: new Date().toISOString(),
        queryTime,
        cached: false,
        version: "2.0",
      },
    };

    // Cache the response
    const response = {
      success: true,
      invoiceData,
      orderNumber,
      meta: invoiceData.meta,
    };

    setCachedInvoice(cacheKey, response);

    console.log(
      `Invoice data generated for order ${orderId} in ${queryTime}ms`
    );

    return NextResponse.json(response, {
      headers: {
        "X-Order-Number": orderNumber,
        "X-Query-Time": queryTime.toString(),
        "Cache-Control": "private, max-age=600", // 10 minutes client cache
      },
    });

  } catch (error) {
    console.error("Error generating invoice data:", error);

    return NextResponse.json(
      { 
        error: "Failed to generate invoice data",
        details: error.message,
        timestamp: new Date().toISOString(),
      },
      { status: 500 }
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

// Track if indexes have been initialized to avoid repeated calls
let indexesInitialized = false;

// Initialize indexes optimized for MongoDB Atlas performance
async function initializeOrderIndexes(db) {
  // Only initialize once per application lifecycle
  if (indexesInitialized) {
    return;
  }

  try {
    const ordersCollection = db.collection("orders");
    const productsCollection = db.collection("products");

    // Check if indexes already exist before creating them
    const existingIndexes = await ordersCollection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Atlas-optimized compound indexes for better performance
    const indexesToCreate = [
      // Core user queries
      {
        key: { userId: 1, createdAt: -1 },
        name: "userId_createdAt_idx",
        options: { background: true },
      },
      // Farmer queries - optimized for Atlas
      {
        key: { "items.farmerId": 1, status: 1, createdAt: -1 },
        name: "items_farmerId_status_date_idx",
        options: { background: true },
      },
      {
        key: { "items.farmerEmail": 1, status: 1, createdAt: -1 },
        name: "items_farmerEmail_status_date_idx",
        options: { background: true },
      },
      // Alternative farmer fields
      {
        key: { farmerIds: 1, createdAt: -1 },
        name: "farmerIds_createdAt_idx",
        options: { background: true },
      },
      {
        key: { farmerEmails: 1, createdAt: -1 },
        name: "farmerEmails_createdAt_idx",
        options: { background: true },
      },
      // Product queries
      {
        key: { "items.productId": 1, createdAt: -1 },
        name: "items_productId_createdAt_idx",
        options: { background: true },
      },
      // Status and date queries
      {
        key: { status: 1, createdAt: -1 },
        name: "status_createdAt_idx",
        options: { background: true },
      },
      // General date sorting
      {
        key: { createdAt: -1 },
        name: "createdAt_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await ordersCollection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    // Products collection indexes for order operations
    const productIndexes = await productsCollection.listIndexes().toArray();
    const productIndexNames = productIndexes.map((index) => index.name);

    if (!productIndexNames.includes("stock_status_idx")) {
      await productsCollection.createIndex(
        { stock: 1, status: 1 },
        { name: "stock_status_idx", background: true },
      );
    }

    indexesInitialized = true;
    console.log("Atlas-optimized order indexes initialized successfully");
  } catch (error) {
    console.log("Order index initialization note:", error.message);
  }
}

// Cache for database connection and collections
let cachedDb = null;
let cachedOrdersCollection = null;

// Response cache for identical requests (3 minutes for orders - shorter than products)
const responseCache = new Map();
const CACHE_TTL = 3 * 60 * 1000;

// Export cache clearing function for use by individual order updates
export function clearOrdersCache() {
  responseCache.clear();
  console.log("Orders cache cleared");
}

// Generate cache key for request
function generateCacheKey(searchParams) {
  const params = {};
  searchParams.forEach((value, key) => {
    params[key] = value;
  });
  return JSON.stringify(params);
}

// Get cached response if available and not expired
function getCachedResponse(cacheKey) {
  const cached = responseCache.get(cacheKey);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }
  responseCache.delete(cacheKey);
  return null;
}

// Set response in cache
function setCachedResponse(cacheKey, data) {
  responseCache.set(cacheKey, {
    data,
    timestamp: Date.now(),
  });

  // Clean up expired entries
  if (responseCache.size > 50) {
    const now = Date.now();
    for (const [key, value] of responseCache.entries()) {
      if (now - value.timestamp >= CACHE_TTL) {
        responseCache.delete(key);
      }
    }
  }
}

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);

    // Check cache first
    const cacheKey = generateCacheKey(searchParams);
    const cachedResponse = getCachedResponse(cacheKey);
    if (cachedResponse) {
      return NextResponse.json(cachedResponse);
    }

    const userId = searchParams.get("userId");
    const farmerId = searchParams.get("farmerId");
    const farmerEmail = searchParams.get("farmerEmail");
    const productId = searchParams.get("productId"); // Add productId parameter
    const status = searchParams.get("status");
    const limit = parseInt(searchParams.get("limit")) || 50;
    const page = parseInt(searchParams.get("page")) || 1;

    // Reuse database connection
    if (!cachedDb) {
      const client = await clientPromise;
      cachedDb = client.db("farmfresh");
      cachedOrdersCollection = cachedDb.collection("orders");
    }

    // Initialize indexes
    await initializeOrderIndexes(cachedDb);

    // Build optimized query
    const query = {};

    if (userId) {
      query.userId = userId;
    }

    if (status) {
      query.status = status;
    }

    // Add productId filtering - only show orders containing this specific product
    if (productId) {
      query["items.productId"] = productId;
    }

    // Optimized farmer filtering for better Atlas performance
    if (farmerId || farmerEmail) {
      const farmerConditions = [];

      if (farmerId) {
        farmerConditions.push(
          { "items.farmerId": farmerId },
          { "items.farmer.id": farmerId },
          { "items.farmer._id": farmerId },
          { farmerIds: farmerId },
        );
      }

      if (farmerEmail) {
        farmerConditions.push(
          { "items.farmerEmail": farmerEmail },
          { "items.farmer.email": farmerEmail },
          { farmerEmails: farmerEmail },
        );
      }

      query.$or = farmerConditions;
    }

    // Optimized projection - reduce data transfer
    const projection = {
      _id: 1,
      userId: 1,
      customerName: 1,
      customerEmail: 1,
      customerPhone: 1,
      customerInfo: 1,
      status: 1,
      total: 1,
      farmerSubtotal: 1,
      shippingAddress: 1,
      paymentMethod: 1,
      createdAt: 1,
      updatedAt: 1,
      // Include essential item fields including images
      "items._id": 1,
      "items.productId": 1,
      "items.name": 1,
      "items.productName": 1,
      "items.price": 1,
      "items.quantity": 1,
      "items.subtotal": 1,
      "items.farmerId": 1,
      "items.farmerEmail": 1,
      "items.farmerName": 1,
      "items.farmer": 1,
      "items.image": 1, // Include item image
      "items.productImage": 1, // Include product image
      "items.images": 1, // Include images array
    };

    // Use aggregation pipeline for better Atlas performance
    const pipeline = [
      { $match: query },
      { $project: projection },
      { $sort: { createdAt: -1 } },
    ];

    // Add pagination
    if (limit < 1000) {
      pipeline.push({ $skip: (page - 1) * limit }, { $limit: limit });
    }

    // Execute optimized query
    const startTime = Date.now();
    const [orders, totalCount] = await Promise.all([
      cachedOrdersCollection.aggregate(pipeline).toArray(),
      // Only count if we need pagination
      limit < 1000
        ? cachedOrdersCollection.countDocuments(query)
        : Promise.resolve(orders.length),
    ]);

    const queryTime = Date.now() - startTime;
    console.log(
      `Atlas orders query executed in ${queryTime}ms for ${orders.length} orders`,
    );

    // Build response
    const response = {
      orders,
      pagination: {
        page,
        limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / limit),
        hasNext: page * limit < totalCount,
        hasPrev: page > 1,
      },
      meta: {
        queryTime,
        cached: false,
      },
    };

    // Cache the response
    setCachedResponse(cacheKey, response);

    return NextResponse.json(response);
  } catch (error) {
    console.error("Orders API Error:", error);
    return NextResponse.json(
      {
        error: "Failed to fetch orders",
        details: error.message,
        orders: [],
        pagination: {
          page: 1,
          limit: 50,
          total: 0,
          totalPages: 0,
          hasNext: false,
          hasPrev: false,
        },
      },
      { status: 500 },
    );
  }
}

export async function POST(request) {
  try {
    const orderData = await request.json();

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Initialize indexes for optimal performance
    await initializeOrderIndexes(db);

    // Optimize stock validation using aggregation pipeline
    const productIds = orderData.items.map(
      (item) => new ObjectId(item.productId),
    );

    const stockValidationPipeline = [
      { $match: { _id: { $in: productIds } } },
      {
        $project: {
          _id: 1,
          stock: 1,
          name: 1,
          image: 1, // Include main product image
          images: 1, // Include product images array
          price: 1, // Include current price
          farmer: 1, // Include farmer info
        },
      },
    ];

    const products = await db
      .collection("products")
      .aggregate(stockValidationPipeline)
      .toArray();

    // Create lookup map for faster validation
    const productMap = new Map(products.map((p) => [p._id.toString(), p]));

    // Validate stock availability
    const stockUpdates = [];
    for (const item of orderData.items) {
      const product = productMap.get(item.productId);

      if (!product) {
        throw new Error(`Product ${item.name} not found`);
      }

      if (product.stock < item.quantity) {
        throw new Error(
          `Insufficient stock for ${item.name}. Available: ${product.stock}, Requested: ${item.quantity}`,
        );
      }

      stockUpdates.push({
        updateOne: {
          filter: { _id: new ObjectId(item.productId) },
          update: {
            $inc: { stock: -item.quantity },
            $set: { updatedAt: new Date() },
          },
        },
      });
    }

    // Perform bulk stock updates for better performance
    if (stockUpdates.length > 0) {
      await db.collection("products").bulkWrite(stockUpdates);
    }

    // Enrich order items with product data including images
    const enrichedItems = orderData.items.map((item) => {
      const product = productMap.get(item.productId);
      return {
        ...item,
        // Add product images to order item
        image: product?.image || item.image,
        productImage: product?.image || product?.images?.[0],
        images: product?.images || [],
        // Store current product name in case it changes later
        productName: item.productName || item.name || product?.name,
        // Store farmer info
        farmerName: item.farmerName || product?.farmer?.name || "Local Farmer",
        farmerEmail: item.farmerEmail || product?.farmer?.email,
        farmerId: item.farmerId || product?.farmer?.id || product?.farmerId,
      };
    });

    // Enrich order data with customer information
    let customerInfo = {};
    if (orderData.userId) {
      try {
        const user = await db.collection("users").findOne({
          $or: [
            { _id: new ObjectId(orderData.userId) },
            { _id: orderData.userId },
            { email: orderData.userId },
          ],
        });

        if (user) {
          customerInfo = {
            customerName:
              user.name || user.username || user.email || "Customer",
            customerEmail: user.email,
            customerPhone: user.phone,
            customerInfo: {
              name: user.name || user.username || "Customer",
              email: user.email,
              phone: user.phone,
            },
          };
        }
      } catch (error) {
        console.log("Could not fetch user details:", error.message);
      }
    }

    // Add timestamps and customer info to order
    const newOrder = {
      ...orderData,
      ...customerInfo,
      items: enrichedItems, // Use enriched items
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    // Create the order
    const result = await db.collection("orders").insertOne(newOrder);

    // Clear cache after creating new order to ensure fresh data
    responseCache.clear();

    return NextResponse.json({
      message: "Order created successfully",
      orderId: result.insertedId,
      order: { ...newOrder, _id: result.insertedId },
    });
  } catch (error) {
    console.error("Create order error:", error);
    return NextResponse.json(
      { error: "Failed to create order", details: error.message },
      { status: 500 },
    );
  }
}

export async function PATCH(request) {
  try {
    const { orderId, ...updateData } = await request.json();

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Get the current order to check status changes (without transactions)
    const currentOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    if (!currentOrder) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Check if order status is being changed to cancelled/returned
    const isBeingCancelled =
      updateData.status &&
      (updateData.status === "cancelled" || updateData.status === "returned") &&
      currentOrder.status !== "cancelled" &&
      currentOrder.status !== "returned";

    // If order is being cancelled, restore stock
    if (isBeingCancelled && currentOrder.items) {
      console.log(
        `Order ${orderId} is being cancelled/returned, restoring stock...`,
      );

      for (const item of currentOrder.items) {
        const productId = item.productId;
        const orderQuantity = item.quantity;

        // Get current product
        const product = await db
          .collection("products")
          .findOne({ _id: new ObjectId(productId) });

        if (product) {
          // Restore product stock
          const newStock = product.stock + orderQuantity;
          await db.collection("products").updateOne(
            { _id: new ObjectId(productId) },
            {
              $set: {
                stock: newStock,
                updatedAt: new Date(),
              },
            },
          );
        } else {
          console.warn(`Product ${productId} not found when restoring stock`);
        }
      }
    }

    // Update the order
    const result = await db.collection("orders").updateOne(
      { _id: new ObjectId(orderId) },
      {
        $set: {
          ...updateData,
          updatedAt: new Date(),
        },
      },
    );

    if (result.matchedCount === 0) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Get the updated order
    const updatedOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    return NextResponse.json({
      message: "Order updated successfully",
      order: updatedOrder,
    });
  } catch (error) {
    console.error("Update order error:", error);
    return NextResponse.json(
      { error: "Failed to update order", details: error.message },
      { status: 500 },
    );
  }
}

import { NextResponse } from "next/server";
import clientPromise from "@/lib/mongodb";
import { ObjectId } from "mongodb";

// Track if indexes have been initialized to avoid repeated calls
let indexesInitialized = false;

// Initialize indexes optimized for MongoDB Atlas performance
async function initializeOrderIndexes(db) {
  // Only initialize once per application lifecycle
  if (indexesInitialized) {
    return;
  }

  try {
    const ordersCollection = db.collection("orders");
    const productsCollection = db.collection("products");

    // Check if indexes already exist before creating them
    const existingIndexes = await ordersCollection.listIndexes().toArray();
    const indexNames = existingIndexes.map((index) => index.name);

    // Atlas-optimized compound indexes for better performance
    const indexesToCreate = [
      // Core user queries
      {
        key: { userId: 1, createdAt: -1 },
        name: "userId_createdAt_idx",
        options: { background: true },
      },
      // Farmer queries - optimized for Atlas
      {
        key: { "items.farmerId": 1, status: 1, createdAt: -1 },
        name: "items_farmerId_status_date_idx",
        options: { background: true },
      },
      {
        key: { "items.farmerEmail": 1, status: 1, createdAt: -1 },
        name: "items_farmerEmail_status_date_idx",
        options: { background: true },
      },
      // Alternative farmer fields
      {
        key: { farmerIds: 1, createdAt: -1 },
        name: "farmerIds_createdAt_idx",
        options: { background: true },
      },
      {
        key: { farmerEmails: 1, createdAt: -1 },
        name: "farmerEmails_createdAt_idx",
        options: { background: true },
      },
      // Product queries
      {
        key: { "items.productId": 1, createdAt: -1 },
        name: "items_productId_createdAt_idx",
        options: { background: true },
      },
      // Status and date queries
      {
        key: { status: 1, createdAt: -1 },
        name: "status_createdAt_idx",
        options: { background: true },
      },
      // General date sorting
      {
        key: { createdAt: -1 },
        name: "createdAt_idx",
        options: { background: true },
      },
    ];

    for (const indexSpec of indexesToCreate) {
      if (!indexNames.includes(indexSpec.name)) {
        await ordersCollection.createIndex(indexSpec.key, {
          name: indexSpec.name,
          ...indexSpec.options,
        });
      }
    }

    // Products collection indexes for order operations
    const productIndexes = await productsCollection.listIndexes().toArray();
    const productIndexNames = productIndexes.map((index) => index.name);

    if (!productIndexNames.includes("stock_status_idx")) {
      await productsCollection.createIndex(
        { stock: 1, status: 1 },
        { name: "stock_status_idx", background: true },
      );
    }

    indexesInitialized = true;
    console.log("Atlas-optimized order indexes initialized successfully");
  } catch (error) {
    console.log("Order index initialization note:", error.message);
  }
}

// Cache for database connection and collections
let cachedDb = null;
let cachedOrdersCollection = null;

// Response cache for identical requests (3 minutes for orders - shorter than products)
const responseCache = new Map();
const CACHE_TTL = 3 * 60 * 1000;

// Export cache clearing function for use by individual order updates
export function clearOrdersCache() {
  responseCache.clear();
  console.log("Orders cache cleared");
}

// Generate cache key for request
function generateCacheKey(searchParams) {
  const params = {};
  searchParams.forEach((value, key) => {
    params[key] = value;
  });
  return JSON.stringify(params);
}

// Get cached response if available and not expired
function getCachedResponse(cacheKey) {
  const cached = responseCache.get(cacheKey);
  if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
    return cached.data;
  }
  responseCache.delete(cacheKey);
  return null;
}

// Set response in cache
function setCachedResponse(cacheKey, data) {
  responseCache.set(cacheKey, {
    data,
    timestamp: Date.now(),
  });

  // Clean up expired entries
  if (responseCache.size > 50) {
    const now = Date.now();
    for (const [key, value] of responseCache.entries()) {
      if (now - value.timestamp >= CACHE_TTL) {
        responseCache.delete(key);
      }
    }
  }
}

export async function GET(request) {
  try {
    const { searchParams } = new URL(request.url);

    // Check cache first
    const cacheKey = generateCacheKey(searchParams);
    const cachedResponse = getCachedResponse(cacheKey);
    if (cachedResponse) {
      return NextResponse.json(cachedResponse);
    }

    const userId = searchParams.get("userId");
    const farmerId = searchParams.get("farmerId");
    const farmerEmail = searchParams.get("farmerEmail");
    const productId = searchParams.get("productId"); // Add productId parameter
    const status = searchParams.get("status");
    const limit = parseInt(searchParams.get("limit")) || 50;
    const page = parseInt(searchParams.get("page")) || 1;

    // Reuse database connection
    if (!cachedDb) {
      const client = await clientPromise;
      cachedDb = client.db("farmfresh");
      cachedOrdersCollection = cachedDb.collection("orders");
    }

    // Initialize indexes
    await initializeOrderIndexes(cachedDb);

    // Build optimized query
    const query = {};

    if (userId) {
      query.userId = userId;
    }

    if (status) {
      query.status = status;
    }

    // Add productId filtering - only show orders containing this specific product
    if (productId) {
      query["items.productId"] = productId;
    }

    // Optimized farmer filtering for better Atlas performance
    if (farmerId || farmerEmail) {
      const farmerConditions = [];

      if (farmerId) {
        farmerConditions.push(
          { "items.farmerId": farmerId },
          { "items.farmer.id": farmerId },
          { "items.farmer._id": farmerId },
          { farmerIds: farmerId },
        );
      }

      if (farmerEmail) {
        farmerConditions.push(
          { "items.farmerEmail": farmerEmail },
          { "items.farmer.email": farmerEmail },
          { farmerEmails: farmerEmail },
        );
      }

      query.$or = farmerConditions;
    }

    // Optimized projection - reduce data transfer
    const projection = {
      _id: 1,
      userId: 1,
      customerName: 1,
      customerEmail: 1,
      customerPhone: 1,
      customerInfo: 1,
      status: 1,
      total: 1,
      subtotal: 1,
      deliveryFee: 1,
      serviceFee: 1,
      farmerSubtotal: 1,
      shippingAddress: 1,
      deliveryAddress: 1, // Add delivery address
      paymentMethod: 1,
      createdAt: 1,
      updatedAt: 1,
      // Include essential item fields including images
      "items._id": 1,
      "items.productId": 1,
      "items.name": 1,
      "items.productName": 1,
      "items.price": 1,
      "items.quantity": 1,
      "items.subtotal": 1,
      "items.farmerId": 1,
      "items.farmerEmail": 1,
      "items.farmerName": 1,
      "items.farmer": 1,
      "items.image": 1, // Include item image
      "items.productImage": 1, // Include product image
      "items.images": 1, // Include images array
    };

    // Use aggregation pipeline for better Atlas performance
    const pipeline = [
      { $match: query },
      { $project: projection },
      { $sort: { createdAt: -1 } },
    ];

    // Add pagination
    if (limit < 1000) {
      pipeline.push({ $skip: (page - 1) * limit }, { $limit: limit });
    }

    // Execute optimized query
    const startTime = Date.now();
    const [orders, totalCount] = await Promise.all([
      cachedOrdersCollection.aggregate(pipeline).toArray(),
      // Only count if we need pagination
      limit < 1000
        ? cachedOrdersCollection.countDocuments(query)
        : Promise.resolve(orders.length),
    ]);

    const queryTime = Date.now() - startTime;
    console.log(
      `Atlas orders query executed in ${queryTime}ms for ${orders.length} orders`,
    );

    // Build response
    const response = {
      orders,
      pagination: {
        page,
        limit,
        total: totalCount,
        totalPages: Math.ceil(totalCount / limit),
        hasNext: page * limit < totalCount,
        hasPrev: page > 1,
      },
      meta: {
        queryTime,
        cached: false,
      },
    };

    // Cache the response
    setCachedResponse(cacheKey, response);

    return NextResponse.json(response);
  } catch (error) {
    console.error("Orders API Error:", error);
    return NextResponse.json(
      {
        error: "Failed to fetch orders",
        details: error.message,
        orders: [],
        pagination: {
          page: 1,
          limit: 50,
          total: 0,
          totalPages: 0,
          hasNext: false,
          hasPrev: false,
        },
      },
      { status: 500 },
    );
  }
}

