export async function PATCH(request) {
  try {
    const { orderId, ...updateData } = await request.json();

    if (!orderId) {
      return NextResponse.json(
        { error: "Order ID is required" },
        { status: 400 },
      );
    }

    const client = await clientPromise;
    const db = client.db("farmfresh");

    // Get the current order to check status changes (without transactions)
    const currentOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    if (!currentOrder) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Check if order status is being changed to cancelled/returned
    const isBeingCancelled =
      updateData.status &&
      (updateData.status === "cancelled" || updateData.status === "returned") &&
      currentOrder.status !== "cancelled" &&
      currentOrder.status !== "returned";

    // If order is being cancelled, restore stock
    if (isBeingCancelled && currentOrder.items) {
      console.log(
        `Order ${orderId} is being cancelled/returned, restoring stock...`,
      );

      for (const item of currentOrder.items) {
        const productId = item.productId;
        const orderQuantity = item.quantity;

        // Get current product
        const product = await db
          .collection("products")
          .findOne({ _id: new ObjectId(productId) });

        if (product) {
          // Restore product stock
          const newStock = product.stock + orderQuantity;
          await db.collection("products").updateOne(
            { _id: new ObjectId(productId) },
            {
              $set: {
                stock: newStock,
                updatedAt: new Date(),
              },
            },
          );
        } else {
          console.warn(`Product ${productId} not found when restoring stock`);
        }
      }
    }

    // Update the order
    const result = await db.collection("orders").updateOne(
      { _id: new ObjectId(orderId) },
      {
        $set: {
          ...updateData,
          updatedAt: new Date(),
        },
      },
    );

    if (result.matchedCount === 0) {
      return NextResponse.json({ error: "Order not found" }, { status: 404 });
    }

    // Get the updated order
    const updatedOrder = await db
      .collection("orders")
      .findOne({ _id: new ObjectId(orderId) });

    return NextResponse.json({
      message: "Order updated successfully",
      order: updatedOrder,
    });
  } catch (error) {
    console.error("Update order error:", error);
    return NextResponse.json(
      { error: "Failed to update order", details: error.message },
      { status: 500 },
    );
  }
}

"use client";

import { useState, useEffect, useMemo, useCallback, useRef } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Footer from "@/components/Footer";

// Constants for better maintainability
const ORDER_STATUSES = {
  ALL: "All Orders",
  PENDING: "pending",
  CONFIRMED: "confirmed",
  SHIPPED: "shipped",
  DELIVERED: "delivered",
  CANCELLED: "cancelled",
};

const DATE_FILTERS = {
  ALL: "all",
  TODAY: "today",
  WEEK: "week",
  MONTH: "month",
};

const SORT_OPTIONS = {
  NEWEST: "newest",
  OLDEST: "oldest",
  HIGHEST: "highest",
  LOWEST: "lowest",
};

const VIEW_MODES = {
  CARDS: "cards",
  LIST: "list",
};

// Custom hooks for better code organization
const useOrdersData = (session, status) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const abortControllerRef = useRef(null);

  const fetchOrders = useCallback(
    async (showLoading = true) => {
      if (status !== "authenticated" || !session?.user) return;

      // Only allow customers to fetch orders here
      const userType = session.user.userType || session.user.role || "customer";
      if (userType !== "customer") {
        setOrders([]);
        setLoading(false);
        return;
      }

      // Cancel previous request if still pending
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      abortControllerRef.current = new AbortController();

      try {
        if (showLoading) setLoading(true);
        setError(null);

        // Fetch ONLY customer orders (not farmer orders)
        const response = await fetch(
          `/api/orders?userId=${session.user.userId || session.user.id}`,
          {
            signal: abortControllerRef.current.signal,
            headers: {
              "Content-Type": "application/json",
            },
          },
        );

        if (!response.ok) {
          throw new Error(`Failed to fetch orders: ${response.status}`);
        }

        const data = await response.json();

        // Filter to ensure we only get customer orders
        const customerOrders = (data.orders || []).filter((order) => {
          // Make sure this order belongs to the current customer
          const orderUserId = order.userId || order.customerId;
          const sessionUserId = session.user.userId || session.user.id;
          return orderUserId === sessionUserId;
        });

        setOrders(customerOrders);
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Request was cancelled");
          return;
        }

        console.error("Error fetching customer orders:", error);
        setError(error.message || "Failed to fetch orders");
        setOrders([]);
      } finally {
        if (showLoading) setLoading(false);
      }
    },
    [session, status],
  );

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return { orders, loading, error, fetchOrders };
};

const useOrderFilters = (orders) => {
  const [statusFilter, setStatusFilter] = useState(ORDER_STATUSES.ALL);
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFilter, setDateFilter] = useState(DATE_FILTERS.ALL);
  const [sortOrder, setSortOrder] = useState(SORT_OPTIONS.NEWEST);

  const filteredOrders = useMemo(() => {
    let filtered = [...orders];

    // Status filter
    if (statusFilter !== ORDER_STATUSES.ALL) {
      filtered = filtered.filter(
        (order) => order.status?.toLowerCase() === statusFilter.toLowerCase(),
      );
    }

    // Search filter
    if (searchTerm.trim()) {
      const searchText = searchTerm.toLowerCase().trim();
      filtered = filtered.filter((order) => {
        return (
          order._id?.toLowerCase().includes(searchText) ||
          order.customerName?.toLowerCase().includes(searchText) ||
          order.items?.some(
            (item) =>
              item.productName?.toLowerCase().includes(searchText) ||
              item.farmerName?.toLowerCase().includes(searchText),
          )
        );
      });
    }

    // Date filter
    if (dateFilter !== DATE_FILTERS.ALL) {
      const now = new Date();
      filtered = filtered.filter((order) => {
        const orderDate = new Date(order.createdAt);
        switch (dateFilter) {
          case DATE_FILTERS.TODAY:
            return orderDate.toDateString() === now.toDateString();
          case DATE_FILTERS.WEEK:
            const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
            return orderDate >= weekAgo;
          case DATE_FILTERS.MONTH:
            const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
            return orderDate >= monthAgo;
          default:
            return true;
        }
      });
    }

    // Sort orders
    filtered.sort((a, b) => {
      switch (sortOrder) {
        case SORT_OPTIONS.NEWEST:
          return new Date(b.createdAt) - new Date(a.createdAt);
        case SORT_OPTIONS.OLDEST:
          return new Date(a.createdAt) - new Date(b.createdAt);
        case SORT_OPTIONS.HIGHEST:
          return (b.total || 0) - (a.total || 0);
        case SORT_OPTIONS.LOWEST:
          return (a.total || 0) - (b.total || 0);
        default:
          return 0;
      }
    });

    return filtered;
  }, [orders, statusFilter, searchTerm, dateFilter, sortOrder]);

  return {
    filteredOrders,
    statusFilter,
    setStatusFilter,
    searchTerm,
    setSearchTerm,
    dateFilter,
    setDateFilter,
    sortOrder,
    setSortOrder,
  };
};

const useOrderStats = (orders) => {
  return useMemo(() => {
    if (!orders.length) {
      return {
        total: 0,
        pending: 0,
        confirmed: 0,
        shipped: 0,
        delivered: 0,
        cancelled: 0,
        totalSpent: 0,
        averageOrderValue: 0,
      };
    }

    const stats = orders.reduce(
      (acc, order) => {
        const status = order.status?.toLowerCase();
        const total = order.total || 0;

        acc.total++;
        acc.totalSpent += total;

        if (status in acc) {
          acc[status]++;
        }

        return acc;
      },
      {
        total: 0,
        pending: 0,
        confirmed: 0,
        shipped: 0,
        delivered: 0,
        cancelled: 0,
        totalSpent: 0,
      },
    );

    stats.averageOrderValue =
      stats.total > 0 ? stats.totalSpent / stats.total : 0;

    return stats;
  }, [orders]);
};

