    {
      $match: {
        $or: [
          { farmerId: id },
          { farmerId: farmer._id },
          { "farmer.id": id },
          { "farmer._id": id },
          { farmerEmail: farmer.email },
          { "farmer.name": { $regex: new RegExp(`^${farmer.name}$`, "i") } },
          ...(farmer.farmName
            ? [
                {
                  "farmer.farmName": {
                    $regex: new RegExp(`^${farmer.farmName}$`, "i"),
                  },
                },
              ]
            : []),
        ],
