                          {[...Array(5)].map((_, i) => (
                            <i
                              key={i}
                              className={`fas fa-star text-lg ${
                                i < Math.floor(stats.averageRating || 0)
                                  ? "text-amber-400"
                                  : "text-gray-300 dark:text-gray-600"
                              }`}
                            ></i>
                          ))}
