                <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                  Security Settings
                </h3>

                <div className="max-w-md space-y-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Current Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.currentPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "currentPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      New Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.newPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "newPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Confirm New Password
                    </label>
                    <input
                      type="password"
                      value={profileData.security.confirmPassword}
                      onChange={(e) =>
                        handleNestedChange("security", "", "confirmPassword", e.target.value)
                      }
                      className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 dark:bg-gray-700 dark:text-white"
                    />
                  </div>

                  <button
                    onClick={handlePasswordChange}
                    disabled={loading}
                    className="bg-primary-600 text-white px-6 py-2 rounded-md hover:bg-primary-700 focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? "Updating..." : "Update Password"}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Simple script to check the review-product mismatch
const { MongoClient } = require('mongodb');

async function checkMismatch() {
  const client = new MongoClient('mongodb://localhost:27017');
  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Checking product-review mismatch...\n');

  // Get all products
  const products = await db.collection('products').find({}).toArray();
  console.log(` Total products: ${products.length}`);
  console.log('Product IDs:', products.slice(0, 5).map(p => p._id.toString()));

  // Get all reviews  
  const reviews = await db.collection('reviews').find({}).toArray();
  console.log(`\n Total reviews: ${reviews.length}`);
  console.log('Review product IDs:', reviews.map(r => r.productId));

  // Check for matches
  const productIds = products.map(p => p._id.toString());
  const reviewProductIds = reviews.map(r => r.productId);

  const matches = productIds.filter(pid => reviewProductIds.includes(pid));
  console.log(`\n✅ Matching product-review pairs: ${matches.length}`);

  if (matches.length > 0) {
    console.log('Matched IDs:', matches);

    // Show which products have reviews
    for (const matchId of matches) {
      const product = products.find(p => p._id.toString() === matchId);
      const productReviews = reviews.filter(r => r.productId === matchId);
      console.log(`- ${product.name}: ${productReviews.length} reviews`);
    }
  } else {
    console.log('\n❌ No matches found!');
    console.log('This means the existing reviews belong to products that no longer exist');
    console.log('or have different IDs than the current products in the database');
  }

  await client.close();
}

checkMismatch().catch(console.error);

"use client";

import Link from "next/link";
import { useAuth } from "../contexts/AuthContext";
import { useTheme } from "../contexts/ThemeContext";
import { useFavorites } from "../contexts/FavoritesContext";
import { useCart } from "../contexts/CartContext";
import { signOut } from "next-auth/react";
import { useState } from "react";
import { usePathname, useRouter } from "next/navigation";

export default function Navigation() {
  const { user, isAuthenticated, logout } = useAuth();
  const { isDarkMode, toggleDarkMode } = useTheme();
  const { favorites } = useFavorites();
  const { cartItems, cartCount } = useCart();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const pathname = usePathname();
  const router = useRouter();

  // Pages that should have simplified navigation (no search/cart)
  const simplifiedPages = [
    "/login",
    "/register",
    "/forgot-password",
    "/reset-password",
    "/cart",
    "/create",
    "/manage",
    "/payment",
    "/bookings",
    "/farmer-orders",
    "/review",
  ];

  // Details page gets minimal navigation (only logo + dark mode)
  const isDetailsPage = pathname === "/details";
  const isFarmersPage = pathname === "/farmers";
  const shouldShowSearchAndCart =
    !simplifiedPages.includes(pathname) && !isDetailsPage;
  const shouldShowNavLinks = !isDetailsPage;
  const shouldShowUserAuth = !isDetailsPage;

  // Different search placeholders based on page
  const getSearchPlaceholder = () => {
    if (isFarmersPage) {
      return "Search farmers...";
    }
    return "Search products...";
  };

  const handleLogout = async () => {
    try {
      // Close the user menu first
      setShowUserMenu(false);

      // Clear any existing modals or intercepted routes by going to home first
      router.replace("/");

      // Small delay to ensure navigation completes before sign out
      await new Promise((resolve) => setTimeout(resolve, 100));

      // Sign out from NextAuth and clear context
      await signOut({ redirect: false });
      logout();

      // Ensure we stay on home page and refresh
      router.replace("/");

      // Force a hard refresh to clear any modal states
      window.location.href = "/";
    } catch (error) {
      console.error("Logout error:", error);
      // Even if there's an error, still redirect to home
      window.location.href = "/";
    }
  };

  return (
    <nav className="bg-white dark:bg-gray-800 shadow-lg sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link href="/" className="flex items-center space-x-3">
            <div className="bg-primary-500 p-2 rounded-lg">
              <i className="fas fa-seedling text-white text-xl"></i>
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                FarmFresh
              </h1>
              <p className="text-xs text-gray-600 dark:text-gray-400">
                Local Farmer Booking
              </p>
            </div>
          </Link>

          {/* Search Bar (conditional) */}
          {shouldShowSearchAndCart && (
            <div className="hidden md:flex flex-1 max-w-lg mx-8">
              <div className="relative w-full">
                <input
                  type="text"
                  placeholder={getSearchPlaceholder()}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <i className="fas fa-search text-gray-400"></i>
                </div>
              </div>
            </div>
          )}

          {/* Navigation Links (conditional) */}
          {shouldShowNavLinks && (
            <div className="hidden lg:flex items-center space-x-8">
              <Link
                href="/"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Home
              </Link>
              <Link
                href="/products"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/products"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Products
              </Link>
              <Link
                href="/farmers"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/farmers"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Farmers
              </Link>

              {/* Show different menu items based on user type */}
              {isAuthenticated && user?.userType === "farmer" && (
                <>
                  <Link
                    href="/create"
                    className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                      pathname === "/create"
                        ? "text-primary-600 dark:text-primary-400"
                        : ""
                    }`}
                  >
                    Add Product
                  </Link>
                  <Link
                    href="/manage"
                    className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                      pathname === "/manage"
                        ? "text-primary-600 dark:text-primary-400"
                        : ""
                    }`}
                  >
                    Manage Products
                  </Link>
                </>
              )}

              {/* Show My Orders for regular users */}
              {isAuthenticated && user?.userType !== "farmer" && (
                <Link
                  href="/bookings"
                  className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                    pathname === "/bookings"
                      ? "text-primary-600 dark:text-primary-400"
                      : ""
                  }`}
                >
                  My Orders
                </Link>
              )}

              <Link
                href="/about"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/about"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                About
              </Link>
            </div>
          )}

          {/* Right side icons and user menu */}
          <div className="flex items-center space-x-4">
            {/* Cart and Favorites (only for authenticated users and not on simplified pages) */}
            {shouldShowSearchAndCart && isAuthenticated && (
              <>
                <Link
                  href="/favorites"
                  className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition relative"
                >
                  <i className="far fa-heart text-xl"></i>
                  {favorites.length > 0 && (
                    <span className="absolute -top-2 -right-2 bg-primary-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {favorites.length}
                    </span>
                  )}
                </Link>
                <Link
                  href="/cart"
                  className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition relative"
                >
                  <i className="fas fa-shopping-cart text-xl"></i>
                  {cartCount > 0 && (
                    <span className="absolute -top-2 -right-2 bg-primary-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {cartCount}
                    </span>
                  )}
                </Link>
              </>
            )}

            {/* Dark mode toggle */}
            <button
              onClick={toggleDarkMode}
              className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
              title={
                isDarkMode ? "Switch to light mode" : "Switch to dark mode"
              }
            >
              {isDarkMode ? (
                <i className="fas fa-sun text-xl"></i>
              ) : (
                <i className="fas fa-moon text-xl"></i>
              )}
            </button>

            {/* User Authentication (conditional) */}
            {shouldShowUserAuth && (
              <>
                {isAuthenticated ? (
                  <div className="relative">
                    <button
                      onClick={() => setShowUserMenu(!showUserMenu)}
                      className="flex items-center space-x-2 text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
                    >
                      <div className="w-8 h-8 bg-primary-500 rounded-full flex items-center justify-center">
                        <span className="text-white text-sm font-medium">
                          {user?.name?.charAt(0)?.toUpperCase() || "U"}
                        </span>
                      </div>
                      <span className="hidden md:block">{user?.name}</span>
                      <i className="fas fa-chevron-down text-sm"></i>
                    </button>

                    {/* User Dropdown Menu */}
                    {showUserMenu && (
                      <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 py-2">
                        <div className="px-4 py-2 border-b border-gray-200 dark:border-gray-700">
                          <p className="text-sm font-medium text-gray-900 dark:text-white">
                            {user?.name}
                          </p>
                          <p className="text-xs text-gray-600 dark:text-gray-400">
                            {user?.email}
                          </p>
                          <p className="text-xs text-primary-600 dark:text-primary-400 capitalize">
                            {user?.userType || "User"}
                          </p>
                        </div>

                        {/* Farmer-specific menu items */}
                        {user?.userType === "farmer" && (
                          <>
                            <Link
                              href="/farmer-orders"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-clipboard-list mr-2"></i>
                              Order Management
                            </Link>
                            <Link
                              href="/create"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-plus mr-2"></i>
                              Add Product
                            </Link>
                            <Link
                              href="/manage"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-cog mr-2"></i>
                              Manage Products
                            </Link>
                          </>
                        )}

                        {/* Regular user menu items */}
                        {user?.userType !== "farmer" && (
                          <>
                            <Link
                              href="/bookings"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-list mr-2"></i>
                              My Orders
                            </Link>
                            <Link
                              href="/favorites"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-heart mr-2"></i>
                              Favorites
                            </Link>
                            <Link
                              href="/cart"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-shopping-cart mr-2"></i>
                              Cart
                            </Link>
                          </>
                        )}

                        {/* Common menu items */}
                        <div className="border-t border-gray-200 dark:border-gray-700 mt-2 pt-2">
                          <Link
                            href="/profile"
                            className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                            onClick={() => setShowUserMenu(false)}
                          >
                            <i className="fas fa-user mr-2"></i>
                            Profile Settings
                          </Link>
                          <button
                            onClick={handleLogout}
                            className="w-full text-left px-4 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-gray-100 dark:hover:bg-gray-700"
                          >
                            <i className="fas fa-sign-out-alt mr-2"></i>
                            Logout
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="flex items-center space-x-3">
                    <Link
                      href="/login"
                      className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
                    >
                      Login
                    </Link>
                    <Link
                      href="/register"
                      className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg transition"
                    >
                      Sign Up
                    </Link>
                  </div>
                )}
              </>
            )}

            {/* Mobile menu button */}
            <button className="lg:hidden text-gray-700 dark:text-gray-300">
              <i className="fas fa-bars text-xl"></i>
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
}

"use client";

import Link from "next/link";
import { useAuth } from "../contexts/AuthContext";
import { useTheme } from "../contexts/ThemeContext";
import { useFavorites } from "../contexts/FavoritesContext";
import { useCart } from "../contexts/CartContext";
import { signOut } from "next-auth/react";
import { useState, useEffect } from "react";
import { usePathname, useRouter } from "next/navigation";

export default function Navigation() {
  const { user, isAuthenticated, logout, updateUser } = useAuth();
  const { isDarkMode, toggleDarkMode } = useTheme();
  const { favorites } = useFavorites();
  const { cartItems, cartCount } = useCart();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [updatedUserName, setUpdatedUserName] = useState(user?.name || "");
  const pathname = usePathname();
  const router = useRouter();

  // Fetch updated name from the database for farmers
  useEffect(() => {
    async function fetchUpdatedUserName() {
      if (!user?.email || user?.userType !== "farmer") return;

      try {
        console.log("Navigation: Fetching updated name for:", user.email);

        // Fetch the latest farmer data with cache busting
        const response = await fetch(
          `/api/farmers?email=${encodeURIComponent(user.email)}&exactMatch=true&_t=${Date.now()}`,
          {
            headers: {
              "Cache-Control": "no-cache, no-store, must-revalidate",
              Pragma: "no-cache",
            },
            cache: "no-store",
          }
        );

        if (response.ok) {
          const data = await response.json();

          if (data && data.farmers && data.farmers.length > 0) {
            // Find the farmer with the matching email
            const farmer = data.farmers.find((f) => f.email === user.email);
            if (farmer && farmer.name !== user.name) {
              console.log(
                "Navigation: Updating user name from",
                user.name,
                "to",
                farmer.name
              );
              setUpdatedUserName(farmer.name);

              // Also update the Auth context
              updateUser({
                ...user,
                name: farmer.name,
              });
            }
          }
        }
      } catch (error) {
        console.error("Navigation: Error fetching updated name:", error);
      }
    }

    fetchUpdatedUserName();
  }, [user?.email, user?.userType]);

  // Pages that should have simplified navigation (no search/cart)
  const simplifiedPages = [
    "/login",
    "/register",
    "/forgot-password",
    "/reset-password",
    "/cart",
    "/create",
    "/manage",
    "/payment",
    "/bookings",
    "/farmer-orders",
    "/review",
  ];

  // Details page gets minimal navigation (only logo + dark mode)
  const isDetailsPage = pathname === "/details";
  const isFarmersPage = pathname === "/farmers";
  const shouldShowSearchAndCart =
    !simplifiedPages.includes(pathname) && !isDetailsPage;
  const shouldShowNavLinks = !isDetailsPage;
  const shouldShowUserAuth = !isDetailsPage;

  // Different search placeholders based on page
  const getSearchPlaceholder = () => {
    if (isFarmersPage) {
      return "Search farmers...";
    }
    return "Search products...";
  };

  const handleLogout = async () => {
    try {
      // Close the user menu first
      setShowUserMenu(false);

      // Clear any existing modals or intercepted routes by going to home first
      router.replace("/");

      // Small delay to ensure navigation completes before sign out
      await new Promise((resolve) => setTimeout(resolve, 100));

      // Sign out from NextAuth and clear context
      await signOut({ redirect: false });
      logout();

      // Ensure we stay on home page and refresh
      router.replace("/");

      // Force a hard refresh to clear any modal states
      window.location.href = "/";
    } catch (error) {
      console.error("Logout error:", error);
      // Even if there's an error, still redirect to home
      window.location.href = "/";
    }
  };

  return (
    <nav className="bg-white dark:bg-gray-800 shadow-lg sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link href="/" className="flex items-center space-x-3">
            <div className="bg-primary-500 p-2 rounded-lg">
              <i className="fas fa-seedling text-white text-xl"></i>
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                FarmFresh
              </h1>
              <p className="text-xs text-gray-600 dark:text-gray-400">
                Local Farmer Booking
              </p>
            </div>
          </Link>

          {/* Search Bar (conditional) */}
          {shouldShowSearchAndCart && (
            <div className="hidden md:flex flex-1 max-w-lg mx-8">
              <div className="relative w-full">
                <input
                  type="text"
                  placeholder={getSearchPlaceholder()}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                />
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <i className="fas fa-search text-gray-400"></i>
                </div>
              </div>
            </div>
          )}

          {/* Navigation Links (conditional) */}
          {shouldShowNavLinks && (
            <div className="hidden lg:flex items-center space-x-8">
              <Link
                href="/"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Home
              </Link>
              <Link
                href="/products"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/products"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Products
              </Link>
              <Link
                href="/farmers"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/farmers"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                Farmers
              </Link>

              {/* Show different menu items based on user type */}
              {isAuthenticated && user?.userType === "farmer" && (
                <>
                  <Link
                    href="/create"
                    className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                      pathname === "/create"
                        ? "text-primary-600 dark:text-primary-400"
                        : ""
                    }`}
                  >
                    Add Product
                  </Link>
                  <Link
                    href="/manage"
                    className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                      pathname === "/manage"
                        ? "text-primary-600 dark:text-primary-400"
                        : ""
                    }`}
                  >
                    Manage Products
                  </Link>
                </>
              )}

              {/* Show My Orders for regular users */}
              {isAuthenticated && user?.userType !== "farmer" && (
                <Link
                  href="/bookings"
                  className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                    pathname === "/bookings"
                      ? "text-primary-600 dark:text-primary-400"
                      : ""
                  }`}
                >
                  My Orders
                </Link>
              )}

              <Link
                href="/about"
                className={`text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition ${
                  pathname === "/about"
                    ? "text-primary-600 dark:text-primary-400"
                    : ""
                }`}
              >
                About
              </Link>
            </div>
          )}

          {/* Right side icons and user menu */}
          <div className="flex items-center space-x-4">
            {/* Cart and Favorites (only for authenticated users and not on simplified pages) */}
            {shouldShowSearchAndCart && isAuthenticated && (
              <>
                <Link
                  href="/favorites"
                  className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition relative"
                >
                  <i className="far fa-heart text-xl"></i>
                  {favorites.length > 0 && (
                    <span className="absolute -top-2 -right-2 bg-primary-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {favorites.length}
                    </span>
                  )}
                </Link>
                <Link
                  href="/cart"
                  className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition relative"
                >
                  <i className="fas fa-shopping-cart text-xl"></i>
                  {cartCount > 0 && (
                    <span className="absolute -top-2 -right-2 bg-primary-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {cartCount}
                    </span>
                  )}
                </Link>
              </>
            )}

            {/* Dark mode toggle */}
            <button
              onClick={toggleDarkMode}
              className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
              title={
                isDarkMode ? "Switch to light mode" : "Switch to dark mode"
              }
            >
              {isDarkMode ? (
                <i className="fas fa-sun text-xl"></i>
              ) : (
                <i className="fas fa-moon text-xl"></i>
              )}
            </button>

            {/* User Authentication (conditional) */}
            {shouldShowUserAuth && (
              <>
                {isAuthenticated ? (
                  <div className="relative">
                    <button
                      onClick={() => setShowUserMenu(!showUserMenu)}
                      className="flex items-center space-x-2 text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
                    >
                      <div className="w-8 h-8 bg-primary-500 rounded-full flex items-center justify-center">
                        <span className="text-white text-sm font-medium">
                          {user?.name?.charAt(0)?.toUpperCase() || "U"}
                        </span>
                      </div>
                      <span className="hidden md:block">{user?.name}</span>
                      <i className="fas fa-chevron-down text-sm"></i>
                    </button>

                    {/* User Dropdown Menu */}
                    {showUserMenu && (
                      <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 py-2">
                        <div className="px-4 py-2 border-b border-gray-200 dark:border-gray-700">
                          <p className="text-sm font-medium text-gray-900 dark:text-white">
                            {user?.name}
                          </p>
                          <p className="text-xs text-gray-600 dark:text-gray-400">
                            {user?.email}
                          </p>
                          <p className="text-xs text-primary-600 dark:text-primary-400 capitalize">
                            {user?.userType || "User"}
                          </p>
                        </div>

                        {/* Farmer-specific menu items */}
                        {user?.userType === "farmer" && (
                          <>
                            <Link
                              href="/farmer-orders"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-clipboard-list mr-2"></i>
                              Order Management
                            </Link>
                            <Link
                              href="/create"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-plus mr-2"></i>
                              Add Product
                            </Link>
                            <Link
                              href="/manage"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-cog mr-2"></i>
                              Manage Products
                            </Link>
                          </>
                        )}

                        {/* Regular user menu items */}
                        {user?.userType !== "farmer" && (
                          <>
                            <Link
                              href="/bookings"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-list mr-2"></i>
                              My Orders
                            </Link>
                            <Link
                              href="/favorites"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-heart mr-2"></i>
                              Favorites
                            </Link>
                            <Link
                              href="/cart"
                              className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                              onClick={() => setShowUserMenu(false)}
                            >
                              <i className="fas fa-shopping-cart mr-2"></i>
                              Cart
                            </Link>
                          </>
                        )}

                        {/* Common menu items */}
                        <div className="border-t border-gray-200 dark:border-gray-700 mt-2 pt-2">
                          <Link
                            href="/profile"
                            className="block px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                            onClick={() => setShowUserMenu(false)}
                          >
                            <i className="fas fa-user mr-2"></i>
                            Profile Settings
                          </Link>
                          <button
                            onClick={handleLogout}
                            className="w-full text-left px-4 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-gray-100 dark:hover:bg-gray-700"
                          >
                            <i className="fas fa-sign-out-alt mr-2"></i>
                            Logout
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="flex items-center space-x-3">
                    <Link
                      href="/login"
                      className="text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition"
                    >
                      Login
                    </Link>
                    <Link
                      href="/register"
                      className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg transition"
                    >
                      Sign Up
                    </Link>
                  </div>
                )}
              </>
            )}

            {/* Mobile menu button */}
            <button className="lg:hidden text-gray-700 dark:text-gray-300">
              <i className="fas fa-bars text-xl"></i>
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
}

// components/dashboard/DashboardHeader.js
import { useState, useEffect } from "react";
import Link from "next/link";

export default function DashboardHeader({
  session,
  handleRefresh,
  refreshing,
}) {
  const [currentFarmerName, setCurrentFarmerName] = useState(
    session?.user?.name || "Farmer",
  );

  // Fetch the current farmer name from database when component mounts or refreshes
  useEffect(() => {
    async function fetchCurrentFarmerName() {
      if (!session?.user?.email) return;

      try {
        console.log(
          "Fetching updated farmer name for email:",
          session.user.email,
        );

        // Use query params that match how other pages fetch the farmer data
        // Adding additional params to ensure we get fresh data
        const response = await fetch(
          `/api/farmers?email=${encodeURIComponent(
            session.user.email,
          )}&single=true&_t=${Date.now()}`,
          {
            headers: {
              "Cache-Control": "no-cache, no-store, must-revalidate",
              Pragma: "no-cache",
            },
          },
        );

        if (response.ok) {
          const data = await response.json();
          console.log("Farmer data received:", data);

          // Check if we have farmer data and extract the name
          if (data && data.farmers && data.farmers.length > 0) {
            const name = data.farmers[0].name;
            console.log("Setting farmer name to:", name);
            setCurrentFarmerName(name);
          }
        } else {
          console.error("Failed to fetch farmer data:", response.status);
        }
      } catch (error) {
        console.error("Error fetching current farmer name:", error);
      }
    }

    fetchCurrentFarmerName();
  }, [session?.user?.email, refreshing]);

  // Log when the component renders with the current name
  useEffect(() => {
    console.log(
      "DashboardHeader rendering with farmer name:",
      currentFarmerName,
    );
  }, [currentFarmerName]);

  return (
    <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              Farmer Dashboard
            </h1>
            <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
              Welcome back, {currentFarmerName}! Manage your farm business.
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={handleRefresh}
              disabled={refreshing}
              className="inline-flex items-center px-4 py-2 bg-gray-600 hover:bg-gray-700 disabled:bg-gray-400 text-white rounded-lg font-medium transition"
            >
              <i
                className={`fas fa-sync-alt mr-2 ${refreshing ? "fa-spin" : ""}`}
              ></i>
              Refresh
            </button>
            <Link
              href="/create"
              className="inline-flex items-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
            >
              <i className="fas fa-plus mr-2"></i>
              Add Product
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

// components/dashboard/DashboardHeader.js
import { useState, useEffect } from "react";
import Link from "next/link";

export default function DashboardHeader({
  session,
  handleRefresh,
  refreshing,
}) {
  const [currentFarmerName, setCurrentFarmerName] = useState(
    session?.user?.name || "Farmer",
  );

  // Fetch the current farmer name from database when component mounts or refreshes
  useEffect(() => {
    async function fetchCurrentFarmerName() {
      if (!session?.user?.email) return;

      try {
        console.log(
          "Fetching updated farmer name for email:",
          session.user.email,
        );

        // Use query params that match how other pages fetch the farmer data
        // Adding additional params to ensure we get fresh data
        const response = await fetch(
          `/api/farmers?email=${encodeURIComponent(
            session.user.email,
          )}&exactMatch=true&_t=${Date.now()}`,
          {
            headers: {
              "Cache-Control": "no-cache, no-store, must-revalidate",
              Pragma: "no-cache",
            },
            cache: "no-store",
          },
        );

        if (response.ok) {
          const data = await response.json();
          console.log("Farmer data received:", data);

          // Check if we have farmer data and extract the name
          if (data && data.farmers && data.farmers.length > 0) {
            // Find the farmer with the matching email
            const farmer = data.farmers.find(
              (f) => f.email === session.user.email,
            );
            if (farmer) {
              console.log("Found matching farmer:", farmer.name);
              setCurrentFarmerName(farmer.name);
            } else {
              console.log(
                "No matching farmer found for email:",
                session.user.email,
              );
            }
          }
        } else {
          console.error("Failed to fetch farmer data:", response.status);
        }
      } catch (error) {
        console.error("Error fetching current farmer name:", error);
      }
    }

    fetchCurrentFarmerName();
  }, [session?.user?.email, refreshing]);

  // Log when the component renders with the current name
  useEffect(() => {
    console.log(
      "DashboardHeader rendering with farmer name:",
      currentFarmerName,
    );
  }, [currentFarmerName]);

  return (
    <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              Farmer Dashboard
            </h1>
            <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
              Welcome back, {currentFarmerName}! Manage your farm business.
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={handleRefresh}
              disabled={refreshing}
              className="inline-flex items-center px-4 py-2 bg-gray-600 hover:bg-gray-700 disabled:bg-gray-400 text-white rounded-lg font-medium transition"
            >
              <i
                className={`fas fa-sync-alt mr-2 ${refreshing ? "fa-spin" : ""}`}
              ></i>
              Refresh
            </button>
            <Link
              href="/create"
              className="inline-flex items-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition"
            >
              <i className="fas fa-plus mr-2"></i>
              Add Product
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { createContext, useContext, useEffect, useState } from "react";
import { useSession } from "next-auth/react";
import { useQueryClient } from "@tanstack/react-query";

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const { data: session, status } = useSession();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [tokens, setTokens] = useState(null);
  const queryClient = useQueryClient();

  useEffect(() => {
    if (status === "loading") return;

    const previousUser = user;
    const hadSession = !!previousUser;
    const hasSession = !!session;

    if (session) {
      // Check if we have stored extended user data
      const storedUserData = localStorage.getItem("farmfresh_user");

      if (storedUserData) {
        try {
          const parsedUserData = JSON.parse(storedUserData);
          // Use stored extended data if available, fallback to session data
          setUser(parsedUserData);
        } catch (error) {
          console.error("Error parsing stored user data:", error);
          setUser(session.user);
        }
      } else {
        setUser(session.user);
      }

      setTokens({
        accessToken: session.accessToken,
        refreshToken: session.refreshToken,
      });

      // If this is a new login (no previous session), invalidate all caches
      if (!hadSession && hasSession) {
        console.log(" User logged in, clearing all caches for fresh data");
        queryClient.invalidateQueries();
        // Also clear API service cache
        if (typeof window !== "undefined" && window.apiService) {
          window.apiService.clearAllCache();
        }
      }
    } else {
      // User logged out - clear all caches and stored data
      if (hadSession && !hasSession) {
        console.log(" User logged out, clearing all caches");
        queryClient.clear(); // Clear all React Query cache
        // Also clear API service cache
        if (typeof window !== "undefined" && window.apiService) {
          window.apiService.clearAllCache();
        }
      }

      // Check for stored tokens
      const storedTokens = localStorage.getItem("farmfresh_tokens");
      if (storedTokens) {
        setTokens(JSON.parse(storedTokens));
      }

      // Clear stored user data if no session
      localStorage.removeItem("farmfresh_user");
    }
    setLoading(false);
  }, [session, status]);

  const refreshTokens = async () => {
    try {
      if (!tokens?.refreshToken) return null;

      const response = await fetch("/api/auth/refresh", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ refreshToken: tokens.refreshToken }),
      });

      if (response.ok) {
        const data = await response.json();
        setTokens(data.tokens);
        localStorage.setItem("farmfresh_tokens", JSON.stringify(data.tokens));
        return data.tokens;
      }
    } catch (error) {
      console.error("Token refresh failed:", error);
      logout();
    }
    return null;
  };

  const login = (userData, userTokens) => {
    setUser(userData);
    setTokens(userTokens);
    localStorage.setItem("farmfresh_tokens", JSON.stringify(userTokens));
    localStorage.setItem("farmfresh_user", JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    setTokens(null);
    localStorage.removeItem("farmfresh_tokens");
    localStorage.removeItem("farmfresh_user");
  };

  const updateUser = (updatedUserData) => {
    setUser(updatedUserData);
    // Persist updated user data to localStorage
    localStorage.setItem("farmfresh_user", JSON.stringify(updatedUserData));
  };

  const value = {
    user,
    tokens,
    loading,
    login,
    logout,
    updateUser,
    refreshTokens,
    isAuthenticated: !!user,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

"use client";

import { createContext, useContext, useEffect, useState } from "react";
import { useSession } from "next-auth/react";
import { useQueryClient } from "@tanstack/react-query";
import { invalidateAllFarmerRelatedData } from "@/lib/cache-invalidation";

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const { data: session, status } = useSession();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [tokens, setTokens] = useState(null);
  const queryClient = useQueryClient();

  useEffect(() => {
    if (status === "loading") return;

    const previousUser = user;
    const hadSession = !!previousUser;
    const hasSession = !!session;

    if (session) {
      // Check if we have stored extended user data
      const storedUserData = localStorage.getItem("farmfresh_user");

      if (storedUserData) {
        try {
          const parsedUserData = JSON.parse(storedUserData);
          // Use stored extended data if available, fallback to session data
          setUser(parsedUserData);
        } catch (error) {
          console.error("Error parsing stored user data:", error);
          setUser(session.user);
        }
      } else {
        setUser(session.user);
      }

      setTokens({
        accessToken: session.accessToken,
        refreshToken: session.refreshToken,
      });

      // If this is a new login (no previous session), invalidate all caches
      if (!hadSession && hasSession) {
        console.log(" User logged in, clearing all caches for fresh data");

        // Use our enhanced cache invalidation with the user's email
        if (session.user?.email) {
          invalidateAllFarmerRelatedData(queryClient, session.user.email);
        } else {
          invalidateAllFarmerRelatedData(queryClient);
        }

        // Also clear API service cache
        if (typeof window !== "undefined" && window.apiService) {
          window.apiService.clearAllCache();
        }
      }
    } else {
      // User logged out - clear all caches and stored data
      if (hadSession && !hasSession) {
        console.log(" User logged out, clearing all caches");
        queryClient.clear(); // Clear all React Query cache
        // Also clear API service cache
        if (typeof window !== "undefined" && window.apiService) {
          window.apiService.clearAllCache();
        }
      }

      // Check for stored tokens
      const storedTokens = localStorage.getItem("farmfresh_tokens");
      if (storedTokens) {
        setTokens(JSON.parse(storedTokens));
      }

      // Clear stored user data if no session
      localStorage.removeItem("farmfresh_user");
    }
    setLoading(false);
  }, [session, status]);

  const refreshTokens = async () => {
    try {
      if (!tokens?.refreshToken) return null;

      const response = await fetch("/api/auth/refresh", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ refreshToken: tokens.refreshToken }),
      });

      if (response.ok) {
        const data = await response.json();
        setTokens(data.tokens);
        localStorage.setItem("farmfresh_tokens", JSON.stringify(data.tokens));
        return data.tokens;
      }
    } catch (error) {
      console.error("Token refresh failed:", error);
      logout();
    }
    return null;
  };

  const login = (userData, userTokens) => {
    setUser(userData);
    setTokens(userTokens);
    localStorage.setItem("farmfresh_tokens", JSON.stringify(userTokens));
    localStorage.setItem("farmfresh_user", JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    setTokens(null);
    localStorage.removeItem("farmfresh_tokens");
    localStorage.removeItem("farmfresh_user");
  };

  const updateUser = (updatedUserData) => {
    setUser(updatedUserData);
    // Persist updated user data to localStorage
    localStorage.setItem("farmfresh_user", JSON.stringify(updatedUserData));
  };

  const value = {
    user,
    tokens,
    loading,
    login,
    logout,
    updateUser,
    refreshTokens,
    isAuthenticated: !!user,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

