  await client.connect();
  const db = client.db('farmfresh');

  console.log(' Debugging old product reviews...\n');

  // Get all reviews
  const allReviews = await db.collection('reviews').find({}).toArray();
  console.log(` Total reviews in database: ${allReviews.length}`);

  // Get sample of old products (first 5)
  const oldProducts = await db.collection('products').find({}).limit(5).toArray();
  console.log(` Checking first 5 products:\n`);

  for (const product of oldProducts) {
    const productId = product._id.toString();
    const productReviews = allReviews.filter(review => review.productId === productId);

    console.log(`Product: ${product.name} (ID: ${productId})`);
    console.log(`  Reviews found: ${productReviews.length}`);
    if (productReviews.length > 0) {
      console.log(`  Sample review: ${productReviews[0].comment?.substring(0, 50)}...`);
      console.log(`  Review rating: ${productReviews[0].rating}`);
    }
    console.log('');
  }

  // Check the structure of all reviews
  console.log(' All review product IDs:');
  const reviewProductIds = [...new Set(allReviews.map(r => r.productId))];
  reviewProductIds.forEach(id => {
    const count = allReviews.filter(r => r.productId === id).length;
    console.log(`  ${id}: ${count} reviews`);
  });

  await client.close();
}

