    });
  }

  async getProduct(id) {
    return this.fetchWithCache(
      `/api/products/${id}`,
      {},
      {
        ttl: 10 * 60 * 1000, // 10 minutes for individual products
        useSessionCache: true,
      },
    );
  }

  // Categories API
  async getCategories() {
    return this.fetchWithCache(
      "/api/categories",
      {},
      {
        ttl: 30 * 60 * 1000, // 30 minutes for categories (rarely change)
        useSessionCache: true,
      },
